INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(1, 114, 2, 107, 'Bakara', 'Medeni', '.Göklerin ve yerin Hükümdarlığının Allah\'a aid olduğunu bilmez misin? Allah\'tan başka dost ve yardımcınız yoktur.'),
(2, 115, 2, 108, 'Bakara', 'Medeni', 'Yoksa, daha önce Musa\'nın sorguya çekildiği gibi, siz de peygamberinizi sorguya mı çekmek istiyorsunuz? İmanı inkarla değiştiren, şüphesiz doğru yoldan sapmış olur.'),
(3, 116, 2, 109, 'Bakara', 'Medeni', 'Kitap ehlinin çoğu, hak kendilerine apaçık belli olduktan sonra, içlerindeki çekememezlikten ötürü, sizi, inandıktan sonra küfre döndürmeyi isterler. Allah\'ın emri gelene kadar onları affedin, geçin. Allah muhakkak her şeye Kadir\'dir.'),
(4, 117, 2, 110, 'Bakara', 'Medeni', 'Namazı kılın, zekatı verin, kendiniz için önden gönderdiğiniz her hayrı Allah katında bulacaksınız. Allah yaptıklarınızı şüphesiz görür.'),
(5, 118, 2, 111, 'Bakara', 'Medeni', '\"Yahudi veya Hıristiyan olmayan kimse elbette cennete girmeyecek\" dediler; bu onların kuruntularıdır. De ki: \"Sözünüz doğru ise delillerinizi getirin\".'),
(6, 119, 2, 112, 'Bakara', 'Medeni', 'Hayır, öyle değil; iyilik yaparak kendini Allah\'a veren kimsenin ecri Rabbi\'nin katındadır. Onlara korku yoktur, onlar üzülmeyeceklerdir.'),
(7, 120, 2, 113, 'Bakara', 'Medeni', 'Yahudiler \"Hıristiyanlar bir temel üzerinde değil\" dediler, Hıristiyanlar da \"Yahudiler bir temel üzerinde değil\" dediler; oysa onlar Kitaplarını da okuyorlar. Bilgisizler de tıpkı onların söylediklerini söylemiştir. Allah, kıyamet günü, anlaşmazlığa düştükleri şeylerde onların arasında hüküm verecektir.'),
(8, 121, 2, 114, 'Bakara', 'Medeni', 'Allah\'ın mescidlerinde O\'nun isminin anılmasını yasak eden ve oraların yıkılmasına çalışan kimseden daha zalim kim vardır? Onların oralara korkmadan girememeleri gerekir. Dünyada rezillik onlaradır, ahirette büyük azab da onlaradır.'),
(9, 122, 2, 115, 'Bakara', 'Medeni', 'Doğu da batı da Allah\'ındır, nereye dönerseniz Allah\'ın yönü orasıdır. Doğrusu Allah her yeri kaplar ve her şeyi bilir.'),
(10, 123, 2, 116, 'Bakara', 'Medeni', '\"Allah oğul edindi\" dediler; haşa, oysa, göklerde ve yerde olanlar O\'nundur. Hepsi O\'na boyun eğmişlerdir.'),
(11, 124, 2, 117, 'Bakara', 'Medeni', 'Gökleri ve yeri yoktan var eden Allah\'tır. O, bir işin olmasını dilerse, ona ancak \"ol\" der ve olur.'),
(12, 125, 2, 118, 'Bakara', 'Medeni', 'Bilmeyenler: \"Allah bizimle konuşmalı veya bize bir ayet gelmeli değil miydi?\" dediler. Onlardan öncekiler de onların söylediklerinin tıpkısını söylemişlerdi. Kalbleri birbirine benzedi. Kesinlikle inanan kimseler için ayetleri açıklamışızdır.'),
(13, 126, 2, 119, 'Bakara', 'Medeni', 'Doğrusu Biz, seni hak ile, müjdeci ve uyarıcı olarak göndermişizdir. Sen, cehennemliklerden sorumlu tutulmayacaksın.'),
(14, 127, 2, 120, 'Bakara', 'Medeni', 'Kendi dinlerine uymadıkça, Yahudi ve Hıristiyanlar senden asla hoşnud olmayacaklardır. De ki: \"Doğru yol, ancak Allah\'ın yoludur\". Sana gelen ilimden sonra onların heveslerine uyarsan, and olsun ki, Allah\'tan sana ne bir dost ve ne de bir yardımcı olur.'),
(15, 128, 2, 121, 'Bakara', 'Medeni', 'Kendilerine verdiğimiz Kitabı gereğince okuyanlar var ya, işte ona ancak onlar inanırlar. Onu inkar edenler ise kaybedenlerdir.'),
(16, 129, 2, 122, 'Bakara', 'Medeni', 'Ey İsrailoğulları! Size verdiğim nimeti ve sizi bir zamanlar alemlere üstün tuttuğumu hatırlayın.'),
(17, 130, 2, 123, 'Bakara', 'Medeni', 'Kimsenin kimse namına bir şey ödemeyeceği, hiç kimseden fidye alınmayacağı, kimseye şefaatin yarar sağlamayacağı ve onların yardım görmeyeceği günden korunun.'),
(18, 131, 2, 124, 'Bakara', 'Medeni', 'Rabbi İbrahim\'i bir takım emirlerle denemiş, o da onları yerine getirmişti. Allah, \"seni insanlara önder kılacağım\" demişti. O \"soyumdan da\" deyince, \"zalimler benim ahdime erişemez\" buyurmuştu.'),
(19, 132, 2, 125, 'Bakara', 'Medeni', 'Kabeyi, insanlar için toplanma ve güven yeri kılmıştık. İbrahim\'in makamını namaz yeri edinin, dedik. Evimi ziyaret edenler, kendini ibadete verenler, rüku ve secde edenler için temiz tutun diye İbrahim ve İsmail\'e ahd verdik.'),
(20, 133, 2, 126, 'Bakara', 'Medeni', 'İbrahim: \"Rabbim! Burasını emin bir şehir kıl, halkından, Allah\'a ve ahiret gününe inananları ürünlerle rızıklandır\" demişti. Allah da: \"İnkar edeni de az bir müddet geçindirir, sonra da onu ateşin azabına uğramak zorunda bırakırım, ne kötü sonuç\" buyurmuştu.'),
(21, 134, 2, 127, 'Bakara', 'Medeni', 'İbrahim ve İsmail, Kabenin temellerini yükseltiyordu: \"Rabbimiz! Yaptığımızı kabul buyur. Şüphesiz ki, Sen hem işitir hem bilirsin\"'),
(22, 135, 2, 128, 'Bakara', 'Medeni', '\"Rabbimiz! İkimizi Sana teslim olanlardan kıl, soyumuzdan da Sana teslim olanlardan bir ümmet yetiştir. Bize ibadet yollarımızı göster, tevbemizi kabul buyur, çünkü tevbeleri daima kabul eden, merhametli olan ancak Sensin\".'),
(23, 136, 2, 129, 'Bakara', 'Medeni', '\"Rabbimiz! İçlerinden onlara Senin ayetlerini okuyan, Kitabı ve hikmeti öğreten, onları her kötülükten arıtan bir peygamber gönder. Doğrusu güçlü ve Hakim olan ancak Sensin\".'),
(24, 137, 2, 130, 'Bakara', 'Medeni', 'Kendini bilmezden başkası İbrahim\'in dininden yüz çevirmez. And olsun ki, dünyada onu seçtik, şüphesiz o, ahirette de iyilerdendir.'),
(25, 138, 2, 131, 'Bakara', 'Medeni', 'Rabbi ona: \"Teslim ol\" buyurduğunda, \"Alemlerin Rabbine teslim oldum\" demişti.'),
(26, 139, 2, 132, 'Bakara', 'Medeni', 'İbrahim bunu oğullarına vasiyet etti. Yakub da: \"Oğullarım! Allah dini size seçti, siz de ancak O\'na teslim olmuş olarak can verin\" dedi.'),
(27, 140, 2, 133, 'Bakara', 'Medeni', 'Yoksa Yakub can verirken sizler yanında mı idiniz? O, oğullarına: \"Benden sonra kime kulluk edeceksiniz?\" diye sormuştu; Onlar da: \"Senin Tanrına ve ataların İbrahim, İsmail, İshak\'ın Tanrısı olan tek Tanrıya kulluk edeceğiz, bizler O\'na teslim olmuşuzdur\" demişlerdi.'),
(28, 141, 2, 134, 'Bakara', 'Medeni', 'Onlar geçmiş birer ümmettir. Kazandıkları kendilerine, sizin kazandıklarınız da sizedir. Onların yapmış olduklarından sorumlu değilsiniz.'),
(29, 142, 2, 135, 'Bakara', 'Medeni', '\"Yahudi veya Hıristiyan olun ki doğru yolu bulasınız\" dediler. \"Doğruya yönelmiş olan ve Allah\'a eş koşanlardan olmayan İbrahim\'in dinine uyarız\" de.'),
(30, 143, 2, 136, 'Bakara', 'Medeni', '\"Allah\'a, bize gönderilene, İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a ve torunlarına gönderilene, Musa ve İsa\'ya verilene, Rableri tarafından peygamberlere verilene, onları birbirinden ayırt etmeyerek inandık, biz O\'na teslim olanlarız\" deyin.'),
(31, 144, 2, 137, 'Bakara', 'Medeni', 'Sizin inandığınız gibi inanmış olsalar, doğru yolu bulmuş olurlar. Yüz çevirirlerse, şüphesiz onlar çıkmazdadırlar. Onlara karşı sana Allah yetecektir. O, işitir ve bilir.'),
(32, 145, 2, 138, 'Bakara', 'Medeni', 'Allah\'ın verdiği renge uyun; rengi Allah\'ınkinden daha güzel olan kim vardır? \"Biz O\'na kulluk edenleriz\" deyin.'),
(33, 146, 2, 139, 'Bakara', 'Medeni', 'De ki: \"Bizim ve sizin Rabbiniz olan Allah hakkında bize karşı hüccet mi gösteriyorsunuz? Bizim yaptıklarımız kendimize, sizin yaptıklarınız de kendinize aittir. Biz O\'na karşı samimiyiz\".'),
(34, 147, 2, 140, 'Bakara', 'Medeni', 'Yoksa İbrahim, İsmail, İshak, Yakub ve torunlarının Yahudi veya Hıristiyan olduklarını mı söylüyorsunuz? Peki, siz mi yoksa Allah mı daha iyi bilir? de. Allah tarafından kendisine bildirilen bir gerçeği gizleyenden daha zalim kim vardır? Allah yaptıklarınızdan gafil değildir.'),
(35, 148, 2, 141, 'Bakara', 'Medeni', 'Onlar geçmiş birer ümmettir. Kazandıkları kendilerine, sizin kazandıklarınız da sizedir. Onların yapmış olduklarından sorumlu değilsiniz.'),
(36, 149, 2, 142, 'Bakara', 'Medeni', 'İnsanların beyinsizleri, \"Yöneldikleri kıbleden onları çeviren nedir?\" diyecekler; de ki: \"Doğu ve batı Allah\'ındır. O, dilediğini doğru yola eriştirir\".'),
(37, 150, 2, 143, 'Bakara', 'Medeni', 'Böylece sizi insanlara şahid ve örnek olmanız için tam ortada bulunan bir ümmet kıldık. Peygamber de size şahid ve örnektir. Senin yöneldiğin yönü, Peygambere uyanları, cayacaklardan ayırdetmek için kıble yaptık. Doğrusu Allah\'ın yola koyduğu kimselerden başkasına bu ağır bir şeydir. Allah ibadetlerinizi boşa çıkaracak değildir. Doğrusu Allah insanlara şefkat gösterir, merhamet eder.'),
(38, 151, 2, 144, 'Bakara', 'Medeni', 'Yüzünü göğe çevirip durduğunu görüyoruz. Hoşnud olacağın kıbleye seni elbette çevireceğiz. Artık yüzünü Mescid-i Haram semtine çevir; bulunduğunuz yerde yüzlerinizi o yöne çevirin. Doğrusu Kitap verilenler, bunun Rab\'lerinden bir gerçek olduğunu bilirler. Allah onların yaptıklarından gafil değildir.'),
(39, 152, 2, 145, 'Bakara', 'Medeni', 'Sen, Kitap verilenlere her türlü delili getirsen, yine de kıblene uymazlar; sen de onların kıblesine uyacak değilsin. Onlar birbirlerinin kıblesine de uymazlar. And olsun ki, eğer sana gelen ilimden sonra onların heveslerine uyarsan, şüphesiz o zaman zulmedenlerden olursun.'),
(40, 153, 2, 146, 'Bakara', 'Medeni', 'Kendilerine Kitap verdiklerimiz, onu (peygamberi) oğullarını tanıdıkları gibi tanırlar. Onlardan bir takımı, doğrusu bile bile hakkı gizlerler.'),
(41, 154, 2, 147, 'Bakara', 'Medeni', 'Gerçek Rabb\'indendir, sakın şüphelenenlerden olma.'),
(42, 155, 2, 148, 'Bakara', 'Medeni', 'Herkesin yöneldiği bir yön vardır. Hayırlı işlerde birbirinizle yarışın. Nerede olursanız olun Allah sizi bir araya toplar, Allah şüphesiz her şeye Kadir\'dir.'),
(43, 156, 2, 149, 'Bakara', 'Medeni', 'Her nereden yola çıkarsan, yüzünü Mescid-i Haram semtine çevir, şüphesiz bu Rabbinden bir haktır. Allah, yaptıklarınızdan gafil değildir.'),
(44, 157, 2, 150, 'Bakara', 'Medeni', 'Her nereden çıkarsan, yüzünü Mescid-i Haram semtine çevir. İnsanların zulmedenlerinden başkalarının size karşı gösterecekleri bir hüccet olmaması için, her nerede olursanız, yüzlerinizi oranın semtine çevirin, bu hususta onlardan korkmayın. Benden korkun da size olan nimetimi tamamlayayım. Böylece doğru yolu bulursunuz.'),
(45, 158, 2, 151, 'Bakara', 'Medeni', 'Nitekim Biz size, ayetlerimizi okuyacak, sizi her kötülükten arıtacak, size kitabı ve hikmeti öğretecek ve bilmediklerinizi bildirecek aranızdan, bir Peygamber gönderdik.'),
(46, 159, 2, 152, 'Bakara', 'Medeni', 'Artık Beni anın, Ben de sizi anayım; Bana şükredin, nankörlük etmeyin.'),
(47, 160, 2, 153, 'Bakara', 'Medeni', 'Ey İnananlar! Sabır ve namazla yardım dileyin. Allah, muhakkak ki sabredenlerle beraberdir.'),
(48, 161, 2, 154, 'Bakara', 'Medeni', 'Allah yolunda öldürülenlere \"Ölüler\" demeyin, zira onlar diridirler, fakat siz farkında değilsiniz.'),
(49, 162, 2, 155, 'Bakara', 'Medeni', 'Muhakkak sizi biraz korku, biraz açlık ve mallardan, canlardan, ürünlerden biraz eksiltmekle deneriz, sabredenleri müjdele.'),
(50, 163, 2, 156, 'Bakara', 'Medeni', 'Onlara bir musibet geldiğinde: \"Biz Allah\'ınız ve elbette O\'na döneceğiz\" derler.'),
(51, 164, 2, 157, 'Bakara', 'Medeni', 'Rablerinin mağfiret ve rahmeti onlaradır. O\'nun yolunda olanlar da onlardır.'),
(52, 165, 2, 158, 'Bakara', 'Medeni', 'Şüphesiz Safa ile Merve Allah\'ın nişanelerindendir. Kim Kabe\'yi hacceder veya umre yaparsa, bu ikisini de tavaf etmesinde bir beis yoktur. Kim gönülden iyilik yaparsa, karşılığını görür. Doğrusu Allah şükrün karşılığını verendir ve bilendir.'),
(53, 166, 2, 159, 'Bakara', 'Medeni', 'İndirdiğimiz belgeleri ve doğru yolu Kitab\'da insanlara açıkladıktan sonra, gizleyen kimseler var ya, onlara hem Allah lanet eder, hem lanetçiler lanet eder, ancak tevbe edenler, ıslah olanlar ve gerçeği ortaya koyanlar müstesna; işte onların tevbesini kabul ederim. Ben, tevbeleri daima kabul ve merhamet edenim.'),
(54, 167, 2, 160, 'Bakara', 'Medeni', 'İndirdiğimiz belgeleri ve doğru yolu Kitab\'da insanlara açıkladıktan sonra, gizleyen kimseler var ya, onlara hem Allah lanet eder, hem lanetçiler lanet eder, ancak tevbe edenler, ıslah olanlar ve gerçeği ortaya koyanlar müstesna; işte onların tevbesini kabul ederim. Ben, tevbeleri daima kabul ve merhamet edenim.'),
(55, 168, 2, 161, 'Bakara', 'Medeni', 'İnkar edip de o halde ölenler var ya, işte, Allah\'ın, meleklerin, insanların hepsinin laneti onlaradır.'),
(56, 169, 2, 162, 'Bakara', 'Medeni', 'Lanette temellidirler, onlardan azab hafifletilmez ve onların azabı geciktirilmez.'),
(57, 170, 2, 163, 'Bakara', 'Medeni', 'Tanrınız bir tek Tanrıdır. O, merhamet eden, merhametli olandan başka Tanrı yoktur.'),
(58, 171, 2, 164, 'Bakara', 'Medeni', 'Göklerin ve yerin yaratılmasında, gece ile gündüzün birbiri ardınca gelmesinde, insanlara yararlı şeylerle denizde süzülen gemilerde, Allah\'ın gökten indirip yeri ölümünden sonra dirilttiği suda, her türlü canlıyı orada yaymasında, rüzgarları ve yerle gök arasında emre amade duran bulutları döndürmesinde, düşünen kimseler için deliller vardır.'),
(59, 172, 2, 165, 'Bakara', 'Medeni', 'İnsanlar arasında, Allah\'ı bırakıp, O\'na koştukları eşleri tanrı olarak benimseyenler ve onları, Allah\'ı severcesine sevenler vardır. Müminlerin Allah\'ı sevmesi ise hepsinden kuvvetlidir. Zalimler azabı gördükleri zaman, bütün kuvvetin Allah\'a aid bulunacağını ve Allah\'ın azabının şiddetli olduğunu keşke bilselerdi!'),
(60, 173, 2, 166, 'Bakara', 'Medeni', 'Nitekim, kendilerine uyulanlar, azabı görünce uyanlardan uzaklaşacaklar ve aralarındaki bağlar kopacaktır.'),
(61, 174, 2, 167, 'Bakara', 'Medeni', 'Uyanlar: \"Keşke bizim için dünyaya bir dönüş olsa da, bizden uzaklaştıkları gibi biz de onlardan uzaklaşsak\" derler. Böylece Allah onlara, hasretini çekecekleri işlerini gösterir. Onlar cehennemden çıkmayacaklardır.'),
(62, 175, 2, 168, 'Bakara', 'Medeni', 'Ey İnsanlar! Yeryüzündeki temiz ve helal şeylerden yiyin, şeytana ayak uydurmayın, zira o sizin için apaçık bir düşmandır.'),
(63, 176, 2, 169, 'Bakara', 'Medeni', 'Muhakkak size, kötülüğü, hayasızlığı, Allah\'a karşı da bilmediğiniz şeyi söylemenizi emreder.'),
(64, 177, 2, 170, 'Bakara', 'Medeni', 'Onlara: \"Allah\'ın indirdiğine uyun\" denilince, \"Hayır, atalarımızı yapar bulduğumuz şeye uyarız\" derler; ya ataları bir şey akledemeyen ve doğru olmayan kimseler idiyseler?'),
(65, 178, 2, 171, 'Bakara', 'Medeni', 'İnkar edenlerin durumu, çağırma ve bağırmadan başkasını duymayarak haykıran gibidir. Sağırdırlar, dilsizdirler, kördürler, bu yüzden akledemezler.'),
(66, 179, 2, 172, 'Bakara', 'Medeni', 'Ey İnananlar! Sizi rızıklandırdığımızın temizlerinden yiyin; yalnız Allah\'a kulluk ediyorsanız, O\'na şükredin.'),
(67, 180, 2, 173, 'Bakara', 'Medeni', 'Şüphesiz size ölü hayvan etini, kanı, domuz etini, Allah\'tan başkası için kesilen hayvanı haram kılmıştır; fakat, darda kalana, başkasının payına el uzatmamak ve zaruret miktarını aşmamak üzere günah sayılmaz. Çünkü Allah bağışlayandır, merhamet edendir.'),
(68, 181, 2, 174, 'Bakara', 'Medeni', 'Gerçekten, Allah\'ın indirdiği Kitap\'tan bir şeyi gizlemede bulunup onu az bir değere değişenler var ya, onların karınlarına tıkındıkları ancak ateştir. Allah kıyamet günü onlarla konuşmaz ve onları günahlardan arıtmaz. Onlara elem verici azab vardır.'),
(69, 182, 2, 175, 'Bakara', 'Medeni', 'Onlar doğruluk yerine sapıklığı, mağfiret yerine azabı alanlardır.'),
(70, 183, 2, 176, 'Bakara', 'Medeni', 'Bu da, Allah\'ın Kitab\'ı doğru olarak indirmesinden ileri geliyor. Kitap hakkında ayrılığa düşenler doğrusu derin bir çıkmazdadırlar.'),
(71, 184, 2, 177, 'Bakara', 'Medeni', 'Yüzlerinizi doğudan yana ve batıdan yana çevirmeniz iyi olmak demek değildir; Lakin iyi olan, Allah\'a, ahiret gününe, meleklere, Kitap\'a, peygamberlere inanan, O\'nun sevgisiyle, yakınlarına, yetimlere, düşkünlere, yolculara, yoksullara ve köleler uğrunda mal veren, namaz kılan, zekat veren ve ahidleştiklerinde ahidlerine vefa gösterenler, zorda, darda ve savaş alanında sabredenlerdir. İşte onlar doğru olanlardır ve sakınanlar ancak onlardır.'),
(72, 185, 2, 178, 'Bakara', 'Medeni', 'Ey İnananlar! Öldürülenler hakkında size kısas farz kılındı: Hür ile hür insan, köle ile köle ve kadın ile kadın. Öldüren, ölenin kardeşi tarafından bağışlanmışsa, kendisine örfe uymak ve bağışlayana güzellikle diyet ödemek gerekir. Bu, Rabbiniz\'den bir hafifletme ve rahmettir. Bundan sonra tecavüzde bulunana elem verici azab vardır.'),
(73, 186, 2, 179, 'Bakara', 'Medeni', 'Ey akıl sahibleri! Kısasta sizin için hayat vardır. Artık, Allah\'a karşı gelmekten sakınırsınız.'),
(74, 187, 2, 180, 'Bakara', 'Medeni', 'Birinize ölüm geldiği zaman, eğer mal bırakıyorsa, ana babaya, yakınlara, uygun bir tarzda vasiyet etmesi Allah\'a karşı gelmekten sakınanlara bir borç olarak size farz kılındı.'),
(75, 188, 2, 181, 'Bakara', 'Medeni', 'Vasiyeti işittikten sonra değiştiren olursa, bunun günahı değiştirenin üzerinedir. Allah şüphesiz işitir ve bilir.'),
(76, 189, 2, 182, 'Bakara', 'Medeni', 'Vasiyet edenin yanılacağından veya günaha gireceğinden endişe duyan kimse, ilgililerin arasını düzeltirse ona günah yoktur. Allah şüphesiz bağışlar ve merhamet eder.'),
(77, 190, 2, 183, 'Bakara', 'Medeni', 'Ey İnananlar! Oruç, sizden öncekilere farz kılındığı gibi, Allah\'a karşı gelmekten sakınasınız diye, size sayılı günlerde farz kılındı. İçinizden hasta olan veya yolculukta bulunan, tutamadığı günlerin sayısınca diğer günlerde tutar. Oruca dayanamıyanlar, bir düşkünü doyuracak kadar fidye verir. Kim gönülden iyilik yaparsa o iyilik kendisinedir. Oruç tutmanız eğer bilirseniz sizin için hayırlıdır.'),
(78, 191, 2, 184, 'Bakara', 'Medeni', 'Ey İnananlar! Oruç, sizden öncekilere farz kılındığı gibi, Allah\'a karşı gelmekten sakınasınız diye, size sayılı günlerde farz kılındı. İçinizden hasta olan veya yolculukta bulunan, tutamadığı günlerin sayısınca diğer günlerde tutar. Oruca dayanamıyanlar, bir düşkünü doyuracak kadar fidye verir. Kim gönülden iyilik yaparsa o iyilik kendisinedir. Oruç tutmanız eğer bilirseniz sizin için hayırlıdır.'),
(79, 192, 2, 185, 'Bakara', 'Medeni', 'Ramazan ayı, ki onda Kuran, insanlara yol gösterici ve doğruyu yanlıştan ayırıcı belgeler olarak indirildi. Sizden bu ayı idrak eden, onda oruç tutsun; hasta veya yolculukta olan, tutamadığı günlerin sayısınca diğer günlerde tutsun. Allah size kolaylık ister, zorluk istemez. Bu kolaylıkları, sayıyı tamamlamanız ve size yol gösterdiğine karşılık O\'nu ululamanız için meşru kılmıştır; ola ki şükredersiniz.'),
(80, 193, 2, 186, 'Bakara', 'Medeni', 'Kullarım sana Beni sorarlarsa, bilsinler ki Ben, şüphesiz onlara yakınım. Benden isteyenin, dua ettiğinde duasını kabul ederim. Artık onlar da davetimi kabul edip Bana inansınlar ki doğru yolda yürüyenlerden olsunlar.'),
(81, 194, 2, 187, 'Bakara', 'Medeni', 'Oruç tuttuğunuz günlerin gecesi kadınlarınıza yaklaşmanız size helal kılındı, onlar sizin örtünüz, siz de onların örtülerisiniz. Allah, nefsinize güvenemiyeceğinizi biliyordu, bu sebeple tevbenizi kabul edip sizi affetti; artık onlara yaklaşabilirsiniz. Allah\'ın sizin için takdir ettiğini dileyin. Tan yerinde, beyaz iplik siyah iplikten sizce ayırdedilinceye kadar, yiyin için, sonra orucu geceye kadar tamamlayın. Mescidlerde itikafa çekildiğinizde kadınlarınıza yaklaşmayın. Allah insanlara yasaklardan sakınsınlar diye ayetlerini böylece apaçık bildirir.'),
(82, 195, 2, 188, 'Bakara', 'Medeni', 'Aranızda mallarınızı haksızlıkla yemeyin; bildiğiniz halde günaha girerek insanların mallarından bir kısmını yemek için onu hakimlere aktarmayın.'),
(83, 196, 2, 189, 'Bakara', 'Medeni', 'Sana hilal halindeki ayları sorarlar. De ki: \"Onlar, insanların ve hac vakitlerinin ölçüsüdür\". Evlere arkalarından girmeniz iyilik değildir; iyi kimse kötülükten sakınan kimsedir. Evlere kapılarından girin; Allah\'tan sakının ki muradınıza erersiniz.'),
(84, 197, 2, 190, 'Bakara', 'Medeni', 'Sizinle savaşanlarla Allah yolunda savaşın, aşırı gitmeyin; doğrusu Allah aşırı gidenleri sevmez.'),
(85, 198, 2, 191, 'Bakara', 'Medeni', 'Onları bulduğunuz yerde öldürün. Sizi çıkardıkları yerden siz de onları çıkarın. Fitne çıkarmak, adam öldürmekten daha kötüdür. Mescidi Haram\'ın yanında, onlar savaşmadıkça siz de onlarla savaşmayın. Sizinle savaşırlarsa onları öldürün. İnkar edenlerin cezası böyledir.'),
(86, 199, 2, 192, 'Bakara', 'Medeni', 'Vazgeçerlerse onları bağışlayın; şüphesiz Allah bağışlar ve merhamet eder.'),
(87, 200, 2, 193, 'Bakara', 'Medeni', 'Fitne kalmayıp, yalnız Allah\'ın dini ortada kalana kadar onlarla savaşın. Eğer vazgeçerlerse sataşmayın. Zulmedenlerden başkasına düşmanlık yoktur.'),
(88, 201, 2, 194, 'Bakara', 'Medeni', 'Hürmetli ay, hürmetli aya mukabildir, hürmetler karşılıklıdır; o halde, size tecavüz edene (saldırana), size saldırdıkları gibi saldırın. Allah\'tan sakının ve Allah\'ın sakınanlarla beraber olduğunu bilin.'),
(89, 202, 2, 195, 'Bakara', 'Medeni', 'Allah yolunda sarf edin, kendinizi kendi elinizle tehlikeye atmayın, işlerinizi iyi yapın. Şüphesiz Allah iyi iş yapanları sever.'),
(90, 203, 2, 196, 'Bakara', 'Medeni', 'Başladığınız hac ve umreyi Allah için tamamlayın. Alıkonursanız, kolayınıza gelen bir kurban gönderin. Kurban, yerine ulaşıncaya kadar, başlarınızı tıraş etmeyin. İçinizde hasta olan veya başından rahatsız bulunan varsa fidye olarak ya oruç tutması, ya sadaka vermesi ya da kurban kesmesi gerekir. Güven içinde olursanız, hacca kadar umreden faydalanabilen kimseye kolayına gelen bir kurban kesmek, bulamayana, hac esnasında üç gün ve döndüğünüzde yedi gün, ki o tam on gündür oruç tutmak gerekir. Bu, ailesi Mescidi Haram\'da oturmayan kimseler içindir. Allah\'tan sakının ve Allah\'ın cezasının şiddetli olacağını bilin.'),
(91, 204, 2, 197, 'Bakara', 'Medeni', 'Hac bilinen aylardadır. O aylarda hacca girişen kimse bilmelidir ki, hacda kadına yaklaşmak, sövüşmek, dövüşmek yoktur. Ne iyilik yaparsanız Allah onu bilir. Kendinize azık edinin, şüphe yok ki azığın en iyisi Allah korkusudur. Ey akıl sahibleri! Benden korkun.'),
(92, 205, 2, 198, 'Bakara', 'Medeni', 'Rabbiniz\'den refah istemenizde bir engel yoktur. Arafat\'tan indiğinizde, Allah\'ı Meşari Haram\'da anın; O\'nu, size gösterdiği şekilde zikredin. Nitekim siz önceleri hiç şüphesiz sapıklardandınız.'),
(93, 206, 2, 199, 'Bakara', 'Medeni', 'Sonra, insanların toplu olarak akın ettiği yerden, siz de akın edin. Allah\'tan mağfiret dileyin. Allah bağışlar ve merhamet eder.'),
(94, 207, 2, 200, 'Bakara', 'Medeni', 'Hac ibadetinizi bitirdiğinizde, babalarınızı andığınız gibi, hatta ondan daha kuvvetli bir anışla Allah\'ı anın. \"Rabbimiz! Bize sadece dünyada ver\" diyen insanlar vardır, öylesine, ahirette bir pay yoktur.'),
(95, 208, 2, 201, 'Bakara', 'Medeni', '\"Rabbimiz! Bize dünyada iyiyi, ahirette de iyiyi ver, bizi ateşin azabından koru\" diyenler vardır.'),
(96, 209, 2, 202, 'Bakara', 'Medeni', 'İşte onlara, kazançlarından ötürü karşılık vardır. Allah hesabı çabuk görür.'),
(97, 210, 2, 203, 'Bakara', 'Medeni', 'Allah\'ı sayılı günlerde anın. Günahtan sakınan kimseye, acele edip, Mina\'daki ibadeti iki günde bitirirse günah yoktur, geri kalsa da günah yoktur. Allah\'tan sakının. O\'nun katında toplanacağınızı bilin.'),
(98, 211, 2, 204, 'Bakara', 'Medeni', 'Dünya hayatına dair konuşması senin hoşuna giden, pek azılı düşman iken, kalbinde olana Allah\'ı şahid tutan, işbaşına geçince, yeryüzünde bozgunculuk yapmaya, ekin ve nesli yok etmeğe çabalayan insanlar vardır. Allah bozgunculuğu sevmez.'),
(99, 212, 2, 205, 'Bakara', 'Medeni', 'Dünya hayatına dair konuşması senin hoşuna giden, pek azılı düşman iken, kalbinde olana Allah\'ı şahid tutan, işbaşına geçince, yeryüzünde bozgunculuk yapmaya, ekin ve nesli yok etmeğe çabalayan insanlar vardır. Allah bozgunculuğu sevmez.'),
(100, 213, 2, 206, 'Bakara', 'Medeni', 'Ona: \"Allah\'tan sakın\" denince, gururu kendisine günah işletir, artık ona cehennem yetişir, ne kötü yataktır!..'),
(101, 214, 2, 207, 'Bakara', 'Medeni', 'İnsanlar arasında, Allah\'ın rızasını kazanmak için canını verenler vardır. Allah kullarına karşı şefkatlidir.'),
(102, 215, 2, 208, 'Bakara', 'Medeni', 'Ey İnananlar! Hep birden barışa girin, şeytana ayak uydurmayın, o sizin apaçık düşmanınızdır.'),
(103, 216, 2, 209, 'Bakara', 'Medeni', 'Size belgeler geldikten sonra kayarsanız, biliniz ki Allah güçlüdür, Hakim\'dir.'),
(104, 217, 2, 210, 'Bakara', 'Medeni', 'Onlar, bulut gölgeleri içinde, Allah\'ın azabının ve meleklerin tepelerine inip işin bitmesini mi bekliyorlar? Bütün işler Allah\'a dönecektir.'),
(105, 218, 2, 211, 'Bakara', 'Medeni', 'İsrailoğullarına sor; onlara apaçık nice ayetler verdik, Allah\'ın nimetini, kendisine geldikten sonra kim değiştirirse, bilsin ki, Allah\'ın cezası şüphesiz şiddetlidir.'),
(106, 219, 2, 212, 'Bakara', 'Medeni', 'İnkar edenlere, dünya hayatı güzel görünür, onlar, inananlarla alay ederler, oysa, Allah\'a karşı gelmekten sakınanlar kıyamet günü onların üstünde olacaklardır. Allah dilediğini hesabsız şekilde rızıklandırır.'),
(107, 220, 2, 213, 'Bakara', 'Medeni', 'İnsanlar bir tek ümmetti. Allah peygamberleri müjdeci ve uyarıcı olarak gönderdi; insanların ayrılığa düşecekleri hususlarda aralarında hüküm vermek için onlarla birlikte hak Kitaplar indirdi. Ancak Kitap verilenler, kendilerine belgeler geldikten sonra, aralarındaki ihtiras yüzünden onda ayrılığa düştüler. Allah, inananları, ayrılığa düştükleri gerçeğe kendi izni ile eriştirdi. Allah dilediğini doğru yola eriştirir.'),
(108, 221, 2, 214, 'Bakara', 'Medeni', 'Sizden önce gelenlerin durumu sizin başınıza gelmeden cennete gireceğinizi mi zannettiniz? Peygamber ve onunla beraber müminler: \"Allah\'ın yardımı ne zaman?\" diyecek kadar darlığa ve zorluğa uğramışlar ve sarsılmışlardı; iyi bilin ki Allah\'ın yardımı şüphesiz yakındır.'),
(109, 222, 2, 215, 'Bakara', 'Medeni', 'Sana, ne sarfedeceklerini sorarlar, de ki: \"Sarfedeceğiniz mal, ana baba, yakınlar, yetimler, düşkünler, yolcular içindir. Yaptığınız her iyiliği Allah şüphesiz bilir\".'),
(110, 223, 2, 216, 'Bakara', 'Medeni', 'Savaş, hoşunuza gitmediği halde size farz kılındı. İhtimal ki hoşlanmadığınız şey sizin iyiliğinizedir ve ihtimal ki sevdiğiniz bir şey sizin kötülüğünüzedir. Siz bilmezsiniz, Allah bilir.'),
(111, 224, 2, 217, 'Bakara', 'Medeni', 'Sana hürmet edilen ayı, o aydaki savaşı sorarlar. De ki: \"O ayda savaşmak büyük suçtur. Allah yolundan alıkoymak, O\'nu inkar etmek, Mescidi Haram\'a engel olmak ve halkını oradan çıkarmak Allah katında daha büyük suçtur. Fitne çıkarmak ise öldürmekten daha büyüktür\". Güçleri yeterse, dininizden döndürünceye kadar sizinle savaşa devam ederler. İçinizden dininden dönüp kafir olarak ölen olursa, bunların işleri dünya ve ahirette boşa gitmiş olur. İşte cehennemlikler onlardır, onlar orada temellidirler.'),
(112, 225, 2, 218, 'Bakara', 'Medeni', 'İnananlar, hicret edenler ve Allah yolunda cihad edenler Allah\'ın rahmetini umarlar. Allah bağışlar ve merhamet eder.'),
(113, 226, 2, 219, 'Bakara', 'Medeni', 'Sana içki ve kumarı sorarlar, de ki: \"İkisinde hem büyük günah ve hem insanlara bazı faydalar vardır. Günahları faydasından daha büyüktür\". Ne sarfedeceklerini sana sorarlar, de ki: \"Artanı\". Böylece Allah, dünya ve ahiret hususunda düşünesiniz diye size ayetleri açıklar.'),
(114, 227, 2, 220, 'Bakara', 'Medeni', 'Sana yetimleri sorarlar, de ki: \"Onların işlerini düzeltmek hayırlıdır\". Eğer onlarla bir arada yaşarsanız, artık onlar sizin kardeşlerinizdir. Allah düzeltenden bozanı ayırdetmesini bilir. Allah dileseydi sizi zora sokardı. Allah şüphesiz güçlüdür, Hakim\'dir.'),
(115, 228, 2, 221, 'Bakara', 'Medeni', 'Allah\'a eş koşan kadınlarla onlar imana gelinceye kadar evlenmeyin. İnanan bir cariye, hoşunuza gitse de ortak koşan bir kadından daha iyidir. İnanmalarına kadar; ortak koşan erkeklerle mümin kadınları evlendirmeyin. İnanan bir köle, hoşunuza gitmiş olsa da, ortak koşan bir erkekten daha iyidir. İşte onlar ateşe çağırırlar, Allah ise izniyle cennete ve mağfirete çağırır ve insanlara ibret alsınlar diye ayetlerini açıklar.'),
(116, 229, 2, 222, 'Bakara', 'Medeni', 'Sana, kadınların aybaşı hali hakkında da sorarlar, de ki: \"O bir ezadır (rahatsızlıktır)\". Aybaşı halinde iken kadınlardan el çekin, temizlenmelerine kadar onlara yaklaşmayın. Temizlendikleri zaman, Allah\'ın size buyurduğu yoldan yaklaşın. Allah şüphesiz daima tevbe edenleri sever, temizlenenleri de sever.'),
(117, 1, 1, 1, 'Fâtiha', 'Mekki', 'Rahman ve Rahim olan Allah\'ın adıyla:'),
(118, 2, 1, 2, 'Fâtiha', 'Mekki', 'Hamd, Alemlerin Rabbi Allah\'a mahsustur.'),
(119, 3, 1, 3, 'Fâtiha', 'Mekki', 'O Rahman ve Rahim\'dir,'),
(120, 4, 1, 4, 'Fâtiha', 'Mekki', 'Din Gününün sahibidir.'),
(121, 5, 1, 5, 'Fâtiha', 'Mekki', 'Ancak Sana kulluk eder ve yalnız Senden yardım dileriz.'),
(122, 6, 1, 6, 'Fâtiha', 'Mekki', 'Bizi doğru yola eriştir.'),
(123, 7, 1, 7, 'Fâtiha', 'Mekki', 'Nimete erdirdiğin kimselerin yoluna; gazaba uğrayanların, ya da sapıtanların yoluna değil.'),
(124, 8, 2, 1, 'Bakara', 'Medeni', 'Elif, Lam, Mim.'),
(125, 9, 2, 2, 'Bakara', 'Medeni', 'Bu, doğruluğu şüphe götürmeyen ve Allah\'a karşı gelmekten sakınanlara yol gösteren Kitap\'dır.'),
(126, 10, 2, 3, 'Bakara', 'Medeni', 'Onlar, gaybe inanırlar, namazı kılarlar, kendilerine verdiğimiz rızıktan yerli yerince sarfederler.'),
(127, 11, 2, 4, 'Bakara', 'Medeni', 'Onlar, sana indirilen Kitap\'a da, senden önce indirilenlere de inanırlar; ahirete de yalnız onlar kesinlikle inanırlar.'),
(128, 12, 2, 5, 'Bakara', 'Medeni', 'İşte Rab\'lerinin yolunda olanlar ve saadete erişenler bunlardır.'),
(129, 13, 2, 6, 'Bakara', 'Medeni', 'Şüphe yok ki, inkar edenleri, başlarına gelecekle uyarsan da uyarmasan da birdir, inanmazlar.'),
(130, 14, 2, 7, 'Bakara', 'Medeni', 'Allah onların kalblerini ve kulaklarını mühürlemiştir, gözlerinde de perde vardır ve büyük azab onlar içindir.'),
(131, 15, 2, 8, 'Bakara', 'Medeni', 'İnsanlardan, inanmadıkları halde, \"Allah\'a ve ahiret gününe inandık\" diyenler vardır.'),
(132, 16, 2, 9, 'Bakara', 'Medeni', 'Bunlar Allah\'ı ve inananları aldatmaya çalışırlar, oysa sadece kendilerini aldatırlar da farkında değildirler.'),
(133, 17, 2, 10, 'Bakara', 'Medeni', 'Kalblerinde hastalık vardır, Allah hastalıklarını artırmıştır. Yalan söyleye geldikleri için onlara elem verici azab vardır.'),
(134, 18, 2, 11, 'Bakara', 'Medeni', 'Kendilerine: \"Yeryüzünde bozgunculuk yapmayın\" dendiği zaman, \"Bizler sadece ıslah edicileriz\" derler.'),
(135, 19, 2, 12, 'Bakara', 'Medeni', 'İyi bilin ki, asıl bozguncular kendileridir, lakin farkında değillerdir.'),
(136, 20, 2, 13, 'Bakara', 'Medeni', 'Onlara \"Müslümanların inandığı gibi siz de inanın\" denilince de, \"Beyinsizlerin inandığı gibi mi inanalım?\" derler; iyi bilin ki asıl beyinsizler kendileridir, fakat bilmezler.'),
(137, 21, 2, 14, 'Bakara', 'Medeni', 'İnananlara rastladıkları zaman, \"İnandık\" derler, elebaşılarıyla baş başa kaldıklarında, \"Biz şüphesiz sizinleyiz, onlarla sadece alay etmekteyiz\" derler.'),
(138, 22, 2, 15, 'Bakara', 'Medeni', 'Onlarla Allah alay eder ve taşkınlıkları içinde bocalar durumda bırakır.'),
(139, 23, 2, 16, 'Bakara', 'Medeni', 'Onlar, doğruluk yerine sapıklığı aldılar da alışverişleri kar getirmedi; doğru yolu bulamamışlardı.'),
(140, 24, 2, 17, 'Bakara', 'Medeni', 'Onlar, çevresini aydınlatmak için ateş yakan kimseye benzerler ki, Allah ışıklarını yok edince, onları karanlıklar içinde görmez bir halde bırakmıştır.'),
(141, 25, 2, 18, 'Bakara', 'Medeni', 'Sağırdırlar, dilsizdirler, kördürler, bu yüzden doğru yola dönmezler.'),
(142, 26, 2, 19, 'Bakara', 'Medeni', 'Bir kısmı da, karanlıklarda, gök gürlemeleri ve şimşek arasında gökten boşanan sağanağa tutulup, yıldırımlardan ölmek korkusu ile parmaklarını kulaklarına tıkayan kimseye benzer.'),
(143, 27, 2, 20, 'Bakara', 'Medeni', 'Şimşeğin çakması neredeyse gözlerini alır; onları aydınlattıkça ışığında yürürler ve üzerlerine karanlık basınca durakalırlar. Allah dileseydi işitme ve görmelerini giderirdi. Doğrusu Allah her şeye Kadir\'dir.'),
(144, 28, 2, 21, 'Bakara', 'Medeni', 'Ey insanlar! Sizi ve sizden öncekileri yaratan Rabbinize kulluk ediniz ki, O\'na karşı gelmekten korunmuş olabilesiniz.'),
(145, 29, 2, 22, 'Bakara', 'Medeni', 'O, yeryüzünü size bir döşek ve göğü de bir bina kıldı. Gökten su indirip onunla size rızık olmak üzere ürünler meydana getirdi; artık Allah\'a, bile bile eş koşmayın.'),
(146, 30, 2, 23, 'Bakara', 'Medeni', 'Kulumuza indirdiğimiz Kuran\'dan şüphe ediyorsanız, siz de onun benzeri bir sure meydana getirin; eğer doğru sözlü iseniz, Allah\'tan başka, güvendiklerinizi de yardıma çağırın.'),
(147, 31, 2, 24, 'Bakara', 'Medeni', 'Yapamazsanız ki yapamayacaksınız o takdirde, inkar edenler için hazırlanan ve yakıtı insanlarla taş olan ateşten sakının.'),
(148, 32, 2, 25, 'Bakara', 'Medeni', 'İnananlar ve yararlı işler yapanlara, kendilerine altlarından ırmaklar akan cennetler olduğunu müjdele. Onlara buranın bir ürünü rızık olarak verildiğinde, \"Bu daha önce de rızıklandığımızdır\" derler. Bunlar, söylediklerinin benzerleri olarak sunulmuştur. Onlara orada tertemiz eşler vardır ve orada temelli kalırlar.'),
(149, 33, 2, 26, 'Bakara', 'Medeni', 'Allah sivrisineği ve onun üstününü misal olarak vermekten çekinmez. İnananlar bunun Rablerinden bir gerçek olduğunu bilirler. İnkar edenler ise \"Allah bu misalle neyi murad etti?\" derler, O, bu misalle birçoğunu saptırır, birçoğunu da yola getirir. Onunla saptırdığı yalnız fasıklardır ki onlar Allah\'la yapılan sözleşmeyi kabulden sonra bozarlar. Allah\'ın birleştirilmesini buyurduğu şeyi ayırırlar ve yeryüzünde bozgunculuk yaparlar; zarara uğrayanlar işte onlardır.'),
(150, 34, 2, 27, 'Bakara', 'Medeni', 'Allah sivrisineği ve onun üstününü misal olarak vermekten çekinmez. İnananlar bunun Rablerinden bir gerçek olduğunu bilirler. İnkar edenler ise \"Allah bu misalle neyi murad etti?\" derler, O, bu misalle birçoğunu saptırır, birçoğunu da yola getirir. Onunla saptırdığı yalnız fasıklardır ki onlar Allah\'la yapılan sözleşmeyi kabulden sonra bozarlar. Allah\'ın birleştirilmesini buyurduğu şeyi ayırırlar ve yeryüzünde bozgunculuk yaparlar; zarara uğrayanlar işte onlardır.'),
(151, 35, 2, 28, 'Bakara', 'Medeni', 'Ölü idiniz sizleri diriltti, sonra öldürecek sonra tekrar diriltecek ve sonunda O\'na döneceksiniz; öyleyken Allah\'ı nasıl inkar edersiniz?'),
(152, 36, 2, 29, 'Bakara', 'Medeni', 'Yerde olanların hepsini; sizin için yaratan O\'dur. Sonra, göğe doğru yönelerek yedi gök olarak onları düzenlemiştir. O her şeyi bilir.'),
(153, 37, 2, 30, 'Bakara', 'Medeni', 'Rabbin meleklere \"Ben yeryüzünde bir halife var edeceğim\" demişti; melekler, \"Orada bozgunculuk yapacak, kanlar akıtacak birini mi var edeceksin? Oysa biz Seni överek yüceltiyor ve Seni devamlı takdis ediyoruz\" dediler; Allah \"Ben şüphesiz sizin bilmediklerinizi bilirim\" dedi.'),
(154, 38, 2, 31, 'Bakara', 'Medeni', 'Ve Adem\'e bütün isimleri öğretti, sonra eşyayı meleklere gösterdi. \"Eğer sözünüzde samimi iseniz bunların isimlerini bana söyleyin\" dedi.'),
(155, 39, 2, 32, 'Bakara', 'Medeni', 'Cevap verdiler: \"Sen münezzehsin, öğrettiğinden başka bizim bir bilgimiz yoktur. Şüphesiz Sen hem bilensin, hem Hakim\'sin\".'),
(156, 40, 2, 33, 'Bakara', 'Medeni', 'Allah \"Ey Adem onlara isimlerini söyle\" dedi. Adem isimlerini söyleyince, Allah \"Ben gökler ve yerde görünmeyeni biliyorum, sizin açıkladığınızı ve gizlemekte olduğunuzu da bilirim, diye size söylememiş miydim?\" dedi.'),
(157, 41, 2, 34, 'Bakara', 'Medeni', 'Meleklere, \"Adem\'e secde edin\" demiştik, İblis müstesna hepsi secde ettiler, o ise kaçındı, büyüklük tasladı ve inkar edenlerden oldu.'),
(158, 42, 2, 35, 'Bakara', 'Medeni', '\"Ey Adem! Eşin ve sen cennette kal, orada olandan istediğiniz yerde bol bol yiyin, yalnız şu ağaca yaklaşmayın; yoksa zalimlerden olursunuz\" dedik.'),
(159, 43, 2, 36, 'Bakara', 'Medeni', 'Şeytan oradan ikisinin de ayağını kaydırttı, onları bulundukları yerden çıkardı, onlara \"Birbirinize düşman olarak inin, yeryüzünde bir müddet için yerleşip geçineceksiniz\" dedik.'),
(160, 44, 2, 37, 'Bakara', 'Medeni', 'Adem, Rabbi\'nden emirler aldı; onları yerine getirdi. Rabb\'i de bunun üzerine tevbesini kabul etti. Şüphesiz o tevbeleri daima kabul edendir, merhametli olandır.'),
(161, 45, 2, 38, 'Bakara', 'Medeni', '\"İnin oradan hepiniz, tarafımdan size bir yol gösteren gelecektir; Benim yoluma uyanlar için artık korku yoktur, onlar üzülmeyeceklerdir\" dedik.'),
(162, 46, 2, 39, 'Bakara', 'Medeni', 'İnkar eden kimseler ve ayetlerimizi yalan sayanlar cehennemlik olanlardır, onlar orada temelli kalacaklardır.'),
(163, 47, 2, 40, 'Bakara', 'Medeni', 'Ey İsrailoğulları! Size verdiğim nimeti hatırlayın ve ahdimi yerine getirin ki Ben de yerine getireyim; yoksa benden korkun.'),
(164, 48, 2, 41, 'Bakara', 'Medeni', 'Yanınızdaki Tevrat\'ı tasdik ederek indirdiğim Kuran\'a, inanın; onu ilk inkar edenler siz olmayın, ayetlerimi hiçbir değere karşılık değiştirmeyin ve bile bile hakkı gizlemeyin.'),
(165, 49, 2, 42, 'Bakara', 'Medeni', 'Hakkı batıla karıştırmayın ve bile bile hakkı gizlemeyin.'),
(166, 50, 2, 43, 'Bakara', 'Medeni', 'Namazı kılın, zekatı verin, rüku edenlerle birlikte rüku edin.'),
(167, 51, 2, 44, 'Bakara', 'Medeni', 'Kitap\'ı okuyup durduğunuz halde kendinizi unutur da başkalarına mı iyilikle emredersiniz? Düşünmez misiniz?'),
(168, 52, 2, 45, 'Bakara', 'Medeni', 'Sabır ve namazla Allah\'a sığınıp yardım isteyin; Rablerine kavuşacaklarını ve Ona döneceklerini umanlar ve huşu duyanlardan başkasına namaz elbette ağır gelir.'),
(169, 53, 2, 46, 'Bakara', 'Medeni', 'Sabır ve namazla Allah\'a sığınıp yardım isteyin; Rablerine kavuşacaklarını ve Ona döneceklerini umanlar ve huşu duyanlardan başkasına namaz elbette ağır gelir.'),
(170, 54, 2, 47, 'Bakara', 'Medeni', 'Ey İsrailoğulları! Size verdiğim nimeti ve sizi bir zamanlar alemlere üstün kıldığımı hatırlayın.'),
(171, 55, 2, 48, 'Bakara', 'Medeni', 'Kimsenin kimseden faydalanamayacağı, kimseden bir şefaat kabul edilmeyeceği, kimseden bir fidye alınmayacağı ve yardım görülmeyeceği günden korunun.'),
(172, 56, 2, 49, 'Bakara', 'Medeni', 'Size işkence eden, kadınlarınızı sağ bırakıp oğullarınızı boğazlayan Firavun ailesinden sizi kurtarmıştık; bu Rabbinizin büyük bir imtihanı idi.'),
(173, 57, 2, 50, 'Bakara', 'Medeni', 'Denizi yarıp sizi kurtarmış ve gözlerinizin önünde Firavun ailesini batırmıştık.'),
(174, 58, 2, 51, 'Bakara', 'Medeni', 'Musa\'ya kırk gece vade vermiştik. Sonra onun arkasından, kendinize yazık ederek, buzağıyı tanrı edinmiştiniz.'),
(175, 59, 2, 52, 'Bakara', 'Medeni', 'Sonra bunun ardından, şükredersiniz diye, sizi bağışlamıştık.'),
(176, 60, 2, 53, 'Bakara', 'Medeni', 'Doğru yola gidesiniz diye Musa\'ya hakkı batıldan ayıran Kitabı vermiştik.'),
(177, 61, 2, 54, 'Bakara', 'Medeni', 'Musa milletine \"Ey milletim! Buzağıyı tanrı olarak benimsemekle kendinize yazık ettiniz. Yaratanınıza tevbe edin ve nefislerinizi öldürün, bu Yaratanınız katında sizin için hayırlı olur; O daima tevbeleri kabul ve merhamet eden olduğu için tevbenizikabul eder\" demişti.'),
(178, 62, 2, 55, 'Bakara', 'Medeni', '\"Ya Musa! Allah\'ı apaçık görmedikçe sana inanmayacağız\" demiştiniz de gözleriniz göre göre sizi yıldırım çarpmıştı.'),
(179, 63, 2, 56, 'Bakara', 'Medeni', 'Ölümünüzden sonra, şükredesiniz diye sizi tekrar diriltmiştik.'),
(180, 64, 2, 57, 'Bakara', 'Medeni', 'Bulutla sizi gölgelendirdik, kudret helvası ve bıldırcın indirdik, \"Verdiğimiz rızıkların iyi ve güzel olanlarından yiyin\" dedik. Onlar Bize değil, fakat kendilerine yazık ediyorlardı.'),
(181, 65, 2, 58, 'Bakara', 'Medeni', '\"Şu şehre girin, orada dilediğiniz gibi, bol bol yiyin, secde ederek kapısından girin, \"bağışla!\" deyin, Biz de yanılmalarınızı bağışlarız, iyilere daha da artırırız\" demiştik.'),
(182, 66, 2, 59, 'Bakara', 'Medeni', 'Ama zulmedenler, kendilerine söylenmiş olan sözü başka sözle değiştirdiler. Biz de, zalimlere, yoldan çıkmalarından dolayı gökten azab indirdik.'),
(183, 67, 2, 60, 'Bakara', 'Medeni', 'Musa, milleti için su aramıştı; \"Asanla taşa vur\" dedik; ondan on iki pınar fışkırdı, herkes içeceği yeri bildi. Allah\'ın rızkından yiyin, için, yalnız yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın.'),
(184, 68, 2, 61, 'Bakara', 'Medeni', '\"Ey Musa! Bir çeşit yemeğe dayanamayacağız, bizim için Rabbine yalvar, bize, yerin bitirdiği sebze, hıyar, sarımsak, mercimek ve soğan yetiştirsin\" demiştiniz de, \"Hayırlı olanı daha düşük şeyle mi değiştirmek istiyorsunuz? Bir şehre inin, şüphesiz orada istediğiniz vardır\" demişti. Onlara yoksulluk ve düşkünlük damgası vuruldu, Allah\'ın gazabına uğradılar. Bu, Allah\'ın ayetlerini inkar etmeleri ve haksız yere peygamberleri öldürmelerindendi; bu, karşı gelmeleri ve taşkınlık yapmalarındandı.'),
(185, 69, 2, 62, 'Bakara', 'Medeni', 'Şüphesiz, inananlar, Yahudi olanlar, Hıristiyanlar ve Sabiilerden Allah\'a ve ahiret gününe inanıp yararlı iş yapanların ecirleri Rablerinin katındadır. Onlar için artık korku yoktur. Onlar üzülmeyeceklerdir.'),
(186, 70, 2, 63, 'Bakara', 'Medeni', 'Sizden kesin söz almıştık. Tur dağını yükselterek tepenize dikmiştik. \"Allah\'a karşı gelmekten sakınanlardan olabilmeniz için, size verdiğimiz Kitab\'a kuvvetle sarılın, onda bulunanları hatırda tutun\" demiştik.'),
(187, 71, 2, 64, 'Bakara', 'Medeni', 'Bundan sonra yine yüz çevirdiniz; eğer Allah\'ın size bol nimeti ve merhameti olmasaydı, muhakkak zarara uğrayanlardan olurdunuz.'),
(188, 72, 2, 65, 'Bakara', 'Medeni', 'İçinizden cumartesi günü azgınlık edenleri elbette biliyorsunuz. Onlara \"Aşağılık birer maymun olunuz\" dedik; bunu, çağdaşlarına ve sonradan geleceklere bir ceza örneği ve Allah\'a karşı gelmekten sakınanlara öğüt olsun diye yaptık.'),
(189, 73, 2, 66, 'Bakara', 'Medeni', 'İçinizden cumartesi günü azgınlık edenleri elbette biliyorsunuz. Onlara \"Aşağılık birer maymun olunuz\" dedik; bunu, çağdaşlarına ve sonradan geleceklere bir ceza örneği ve Allah\'a karşı gelmekten sakınanlara öğüt olsun diye yaptık.'),
(190, 74, 2, 67, 'Bakara', 'Medeni', 'Musa milletine: \"Allah muhakkak bir sığır boğazlamanızı buyuruyor\" demişti; \"Bizi alaya mı alıyorsun?\" dediklerinde de: \"Cahillerden olmaktan Allah\'a sığınırım\" dedi.'),
(191, 75, 2, 68, 'Bakara', 'Medeni', '\"Rabbine bizim adımıza yalvar da onun mahiyetini bize bildirsin\" dediler, \"O, onun ne pek kart, ne pek körpe, ikisi ortası bir sığır olduğunu söylüyor, size emrolunanı yapın\" dedi.'),
(192, 76, 2, 69, 'Bakara', 'Medeni', '\"Rabbine bizim adımıza yalvar da ne renk olduğunu bize bildirsin\" dediler. \"O, onun, bakanların içini açan parlak sarı renkli bir sığır olduğunu söylüyor\" dedi.'),
(193, 77, 2, 70, 'Bakara', 'Medeni', '\"Rabbine bizim adımıza yalvar da, mahiyetini bize bildirsin, çünkü sığırlar, bizce, birbirine benzemektedir. Allah dilerse biz şüphesiz doğruyu bulmuş oluruz\" dediler.'),
(194, 78, 2, 71, 'Bakara', 'Medeni', '\"Yeri sürüp, ekini sulayarak boyunduruk altında ezilmemiş, kusursuz, alacasız bir sığır olduğunu söylüyor\" dedi. \"Şimdi gerçeği bildirdin\" deyip sığırı boğazladılar; az kalsın bunu yapmayacaklardı.'),
(195, 79, 2, 72, 'Bakara', 'Medeni', 'Siz bir kimseyi öldürmüş ve bunu birbirinize atmıştınız; oysa Allah gizlemekte olduğunuzu ortaya çıkaracaktı.'),
(196, 80, 2, 73, 'Bakara', 'Medeni', '\"Sığırın bir parçasıyla ona vurun\" dedik. İşte böylece Allah ölüleri diriltir ve aklınızı kullanasınız diye size ayetlerini gösterir.'),
(197, 81, 2, 74, 'Bakara', 'Medeni', 'Sonra kalbleriniz yine katılaştı, taş gibi, hatta daha da katı oldu. Nitekim taşlar arasında kendisinden ırmaklar fışkıran vardır; yarılıp su çıkan vardır; Allah korkusundan yuvarlananlar vardır. Allah yaptıklarınızı bilmez değildir.'),
(198, 82, 2, 75, 'Bakara', 'Medeni', 'Size inanacaklarını umuyor musunuz? Oysa onlardan bir takımı Allah\'ın sözünü işitiyor, ona akılları yattıktan sonra, bile bile onu tahrif ediyorlardı.'),
(199, 83, 2, 76, 'Bakara', 'Medeni', 'İnananlarla karşılaştıkları zaman, \"İnandık\" derlerdi; birbirleriyle yalnız kaldıklarında, \"Rabbinizin katında size karşı hüccet göstersinler diye mi Allah\'ın size açıkladığını onlara anlatıyorsunuz? Bunu akletmiyor musunuz?\" derlerdi.'),
(200, 84, 2, 77, 'Bakara', 'Medeni', 'Gizlediklerini de, açıkladıklarını da Allah\'ın bildiğini bilmiyorlar mı?'),
(201, 85, 2, 78, 'Bakara', 'Medeni', 'Onların bir kısmının okuyup yazması yoktu. Kitab\'ı bilmezlerdi; bildikleri sadece bir takım yalan ve kuruntulardı. Onlar ancak vehim içindedirler.'),
(202, 86, 2, 79, 'Bakara', 'Medeni', 'Vay, Kitabı elleriyle yazıp, sonra da onu az bir değere satmak için, \"Bu Allah katındandır\" diyenlere! Vay ellerinin yazdıklarına! Vay kazandıklarına!'),
(203, 87, 2, 80, 'Bakara', 'Medeni', '\"Ateş bize sadece sayılı birkaç gün değecektir\", derler; sor, \"Allah katından siz söz mü aldınız?\", eğer öyle ise Allah sözünden caymayacaktır. \"Yoksa Allah\'a karşı bilmediğiniz bir şey mi söylüyorsunuz?\"'),
(204, 88, 2, 81, 'Bakara', 'Medeni', 'Hayır öyle değil; kötülük işleyip suçu kendisini kuşatmış olan kimseler; cehennemlikler işte onlardır. Onlar orada temellidirler.'),
(205, 89, 2, 82, 'Bakara', 'Medeni', 'İnanıp yararlı işler yapan kimseler cennetlik olanlardır, onlar da orada temellidirler.'),
(206, 90, 2, 83, 'Bakara', 'Medeni', 'İsrailoğullarından, \"Allah\'tan başkasına kulluk etmeyin, anne babaya, yakınlara, yetimlere, düşkünlere iyilik edin, insanlarla güzel güzel konuşun, namazı kılın, zekatı verin\" diye söz almıştık. Sonra siz pek azınız müstesna, döndünüz; hala da yüz çevirip duruyorsunuz.'),
(207, 91, 2, 84, 'Bakara', 'Medeni', 'Kanınızı dökmeyin, birbirinizi yurdunuzdan sürmeyin diye sizden söz almıştık, sonra bunu böylece kabul etmiştiniz, buna siz şahidsiniz.'),
(208, 92, 2, 85, 'Bakara', 'Medeni', 'Sonra siz, birbirinizi öldüren, aranızdan bir takımı memleketlerinden süren, onlara karşı günah ve düşmanlıkta birleşen, onları çıkarmak haramken size esir olarak geldiklerinde fidyelerini vermeye kalkan kimselersiniz. Kitabın bir kısmına inanıp, bir kısmını inkar mı ediyorsunuz? Aranızda böyle yapanın cezası ancak dünya hayatında rezil olmaktır. Ahiret gününde de azabın en şiddetlisine onlar uğratılırlar. Allah yaptıklarınızdan gafil değildir.'),
(209, 93, 2, 86, 'Bakara', 'Medeni', 'Onlar ahiret karşılığında dünya hayatını satın alan kimselerdir, bu yüzden azabları hafifletilmez, onlar yardım da görmezler.'),
(210, 94, 2, 87, 'Bakara', 'Medeni', 'And olsun ki, Musa\'ya kitap verdik, ondan sonra ardarda peygamberler gönderdik. Meryem oğlu İsa\'ya belgeler verdik, onu Ruhul Kudüs ile destekledik. Size bir peygamber nefsinizin hoşlanmadığı bir şey getirdikçe, büyüklük taslayarak, bir kısmını yalancı sayıp, bir kısmını öldürür müsünüz?'),
(211, 95, 2, 88, 'Bakara', 'Medeni', '\"Kalplerimiz perdelidir\" dediler, hayır, Allah inkarlarından dolayı onları lanetlemiştir. Onların pek azı inanırlar.'),
(212, 96, 2, 89, 'Bakara', 'Medeni', 'Vaktaki Allah katından onlara, kendilerinde olanı tasdik eden Kitap geldi ki onlar bundan önceleri, inkar edenlere karşı kendilerine yardım gelmesini beklerlerdi, bildikleri gelince onu inkar ettiler. Allah\'ın laneti, inkar edenlerin üzerine olsun.'),
(213, 97, 2, 90, 'Bakara', 'Medeni', 'Allah\'ın kullarından dilediğine, bol ihsanından indirmesini çekemeyerek, Allah\'ın indirdiğini inkar etmekle, kendilerini ne kötü bir şey karşılığında sattılar. Bu yüzden gazab üstüne gazaba uğradılar. Kafirlere alçaltıcı bir azab vardır.'),
(214, 98, 2, 91, 'Bakara', 'Medeni', 'Onlara, \"Allah\'ın indirdiğine inanın\" denildiğinde \"Bize indirilene inanırız\" deyip ondan sonra gelen Kuran\'ı inkar ederler; halbuki o, ellerinde bulunan Tevrat\'ı tasdik eden hak bir Kitap\'dır. Onlara \"Eğer inanıyor idiyseniz niçin daha önce Allah\'ın peygamberlerini öldürüyordunuz?\" diye sor.'),
(215, 99, 2, 92, 'Bakara', 'Medeni', 'And olsun ki, Musa size mucizeler getirdi, sonra ardından kendinize yazık ederek buzağıyı tanrı olarak benimsediniz.'),
(216, 100, 2, 93, 'Bakara', 'Medeni', 'Sizden kesin söz almış ve Tur\'u tepenize dikmiştik, \"Size verdiğimize kuvvetle sarılın ve dinleyin\" demiştik \"İşittik ve karşı geldik\" dediler de inkarları yüzünden buzağı sevgisi kalblerine sindirildi. De ki, \"Eğer inanmışsanız, imanınız size ne kötü şey emrediyor?\"'),
(217, 101, 2, 94, 'Bakara', 'Medeni', 'De ki, \"Eğer ahiret yurdu Allah katında başkalarına değil de yalnız size mahsus ise ve eğer doğru sözlü iseniz, ölümü dilesenize!\"'),
(218, 102, 2, 95, 'Bakara', 'Medeni', 'Bunu, önceden işlediklerinden ötürü, asla dilemeyeceklerdir. Allah zalimleri bilir.'),
(219, 103, 2, 96, 'Bakara', 'Medeni', 'And olsun ki, onların hayata diğer insanlardan ve hatta Allah\'a eş koşanlardan da daha düşkün olduklarını görürsün. Her biri ömrünün bin yıl olmasını ister. Oysa uzun ömürlü olması onu azabdan uzaklaştırmaz. Allah onların yaptıklarını görür.'),
(220, 104, 2, 97, 'Bakara', 'Medeni', 'De ki, \"Cebrail\'e düşman olan kimse Allah\'a düşmandır\", çünkü O, Kuran\'ı Allah\'ın izniyle kendinden öncekini tasdik ederek, yol gösterici ve inananlara müjdeci olarak senin kalbine indirmiştir.'),
(221, 105, 2, 98, 'Bakara', 'Medeni', 'Allah\'a, meleklerine, peygamberlerine, Cebrail\'e ve Mikail\'e düşman olan kimse inkar etmiş olur. Allah şüphesiz, inkar edenlerin düşmanıdır.'),
(222, 106, 2, 99, 'Bakara', 'Medeni', 'And olsun ki, sana apaçık ayetler indirdik. Onları sadece yoldan çıkmışlar inkar eder.'),
(223, 107, 2, 100, 'Bakara', 'Medeni', 'Onlar, her ne zaman bir ahidde bulunmuşlarsa içlerinden bir takımı onu bozmamış mıdır? Zaten onların çoğu inanmazlar.'),
(224, 108, 2, 101, 'Bakara', 'Medeni', 'Yanlarındakini doğrulayan bir Peygamber, Allah katından onlara gelince Kitap verilenlerden bir takımı, bilmiyorlarmış gibi, Allah\'ın Kitabı\'nı arkalarına attılar.'),
(225, 109, 2, 102, 'Bakara', 'Medeni', 'Şeytanların Süleyman\'ın hükümdarlığı hakkında söylediklerine uydular. Oysa Süleyman kafir değildi, ama insanlara sihri öğreten şeytanlar kafir olmuşlardı. Babil\'de, melek denilen Harut ve Marut\'a bir şey indirilmemişti. Bu ikisi \"Biz sadece imtihan ediyoruz, sakın inkar etme\" demedikçe kimseye bir şey öğretmezlerdi. Halbuki bu ikisinden, koca ile karısının arasını ayıracak şeyler öğreniyorlardı. Oysa Allah\'ın izni olmadıkça onlar kimseye zarar veremezlerdi. Kendilerine zarar verecek, faydalı olmayacak şeyler öğreniyorlardı. And olsun ki, onu satın alanın ahiretten bir nasibi olmadığını biliyorlardı. Kendilerini karşılığında sattıkları şeyin ne kötü olduğunu keşke bilselerdi!'),
(226, 110, 2, 103, 'Bakara', 'Medeni', 'Onlar inanıp, Allah\'a karşı gelmekten sakınsalardı, Allah katından olan sevab daha hayırlı olurdu. Keşke bilselerdi!');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(227, 111, 2, 104, 'Bakara', 'Medeni', 'Ey inananlar! Peygamber\'e, \"Bizi de dinle\" (raina; kötü anlama gelebilecek söz) demeyin, \"Bizi gözet\" (unzurna) deyin ve dinleyin, inkar edenlere elem verici azab vardır.'),
(228, 112, 2, 105, 'Bakara', 'Medeni', 'Kitap ehlinden ve Allah\'a eş koşanlardan inkar edenler, Rabbinizden size bir iyilik gelmesini istemezler. Allah, rahmetini dilediğine tahsis eder. Allah büyük nimet sahibidir.'),
(229, 113, 2, 106, 'Bakara', 'Medeni', 'Herhangi bir ayetin hükmünü yürürlükten kaldırır veya unutturursak, onun yerine daha hayırlısını veya onun benzerini getiririz. Allah\'ın her şeye kadir olduğunu bilmez misin?'),
(230, 230, 2, 223, 'Bakara', 'Medeni', 'Kadınlarınız sizin tarlanızdır, tarlanıza istediğiniz gibi gelin. İstikbal için hazırlıklı olun, Allah\'tan sakının. O\'na, hiç şüphesiz kavuşacağınızı bilin, bunu inananlara müjdele.'),
(231, 231, 2, 224, 'Bakara', 'Medeni', 'İnsanların arasını düzeltmeniz, günahtan sakınmanız ve iyi olmanız için, Allah\'a yaptığınız yeminleri engel kılmayın, Allah işitir ve bilir.'),
(232, 232, 2, 225, 'Bakara', 'Medeni', 'Allah sizi rastgele yeminlerinizden dolayı değil, fakat kalblerinizin kasdettiği yeminlerden dolayı sorumlu tutar. Allah bağışlayandır, Halim\'dir.'),
(233, 233, 2, 226, 'Bakara', 'Medeni', 'Kadınlarına yaklaşmamaya yemin edenler, dört ay bekleyebilirler; eğer yeminlerinden dönerlerse, bilsinler ki Allah bağışlar ve merhamet eder.'),
(234, 234, 2, 227, 'Bakara', 'Medeni', 'Şayet boşanmaya kararlı iseler, bilsinler ki Allah şüphesiz işitir ve bilir.'),
(235, 235, 2, 228, 'Bakara', 'Medeni', 'Boşanan kadınlar, kendi kendilerine üç aybaşı hali beklerler, eğer Allah\'a ve ahiret gününe inanmışlarsa, rahimlerinde Allah\'ın yarattığını gizlemeleri kendilerine helal değildir. Kocaları bu arada barışmak isterlerse, karılarını geri almakta daha çok hak sahibidirler. Kadınların hakları, örfe uygun bir şekilde vazifelerine denktir. Erkeklerin onlardan bir üstün derecesi vardır. Allah güçlüdür. Hakim\'dir.'),
(236, 236, 2, 229, 'Bakara', 'Medeni', 'Boşanma iki defadır. Ya iyilikle tutma ya da iyilik yaparak bırakmadır. İkisi Allah\'ın yasalarını koruyamamaktan korkmadıkça kadınlara verdiklerinizden (mehirden) bir şey almanız size helal değildir. Eğer Allah\'ın yasalarını ikisi koruyamıyacaklar diye korkarsanız, o zaman kadının fidye vermesinde (mehrinden vazgeçerse) ikisine de günah yoktur. Bunlar Allah\'ın yasalarıdır, onları bozmayın. Allah\'ın yasalarını bozanlar ancak zalimlerdir.'),
(237, 237, 2, 230, 'Bakara', 'Medeni', 'Bundan sonra kadını boşarsa, kadın başka birisiyle evlenmedikçe bir daha kendisine helal olmaz. Eğer ikinci koca da onu boşarsa, Allah\'ın yasalarını koruyacaklarını sanırlarsa eski karı kocanın birbirlerine dönmelerine bir engel yoktur. Bunlar, bilenkimseler için Allah\'ın açıkladığı yasalardır.'),
(238, 238, 2, 231, 'Bakara', 'Medeni', 'Kadınları boşadığınızda, müddetleri sona ererken, onları güzellikle tutun, ya da güzellikle bırakın, haklarına tecavüz etmek için onlara zararlı olacak şekilde tutmayın; böyle yapan şüphesiz kendisine yazık etmiş olur. Allah\'ın ayetlerini de alaya almayın; Allah\'ın üzerinize olan nimetini, öğüt vermek üzere size indirdiği Kitap ve hikmeti anın, Allah\'tan sakının, Allah\'ın her şeyi bildiğini bilin.'),
(239, 239, 2, 232, 'Bakara', 'Medeni', 'Kadınları boşadığınızda, müddetleri sona ermişse, kocaları ile birbirleriyle güzellikle anlaşmışlarsa evlenmelerine engel olmayın. İçinizden Allah\'a ve ahiret gününe inanan kimse bundan ibret alır. Bu sizin için daha nezih ve daha paktır. Allah bilir, siz bilmezsiniz.'),
(240, 240, 2, 233, 'Bakara', 'Medeni', 'Anneler çocuklarını, emzirmeyi tamamlatmak isteyen baba için, tam iki sene emzirirler. Anaların yiyecek ve giyeceğini uygun bir şekilde sağlamak çocuk kendisinin olan babaya borçtur. Herkese ancak gücü nisbetinde teklifte bulunulur. Ana çocuğundan, çocuk kendisinin olan baba da çocuğundan dolayı zarara sokulmasın. Mirasçıya da aynı şeyi yapmak borçtur. Ana baba aralarında danışarak ve anlaşarak sütten kesmek isterlerse, ikisine de sorumluluk yoktur. Çocuklarınızı sütanneye emzirtmek isterseniz, vereceğinizi örfe uygun bir şekilde öderseniz, size sorumluluk yoktur. Allah\'tan sakının, yaptıklarınızı gördüğünü bilin.'),
(241, 241, 2, 234, 'Bakara', 'Medeni', 'İçinizden ölenlerin bırakmış olduğu eşler kendi kendilerine dört ay on gün beklerler; müddetleri sona erdiğinde, onların kendi haklarında uygun şekilde yaptıklarından dolayı size sorumluluk yoktur. Allah işlediklerinizden haberdardır.'),
(242, 242, 2, 235, 'Bakara', 'Medeni', 'Böyle kadınlara kapalı bir şekilde evlenme teklif etmenizde veya içinizden onlarla evlenmeyi geçirmenizde size sorumluluk yoktur. Allah onları anacağınızı bilir. Sakın meşru sözler dışında onlarla gizlice sözleşmeyin, müddet sona erene kadar nikah akdine kalkışmayın. İçinizde olanı Allah\'ın bildiğini bilin de O\'ndan çekinin. Allah\'ın bağışlayan ve Halim olduğunu bilin.'),
(243, 243, 2, 236, 'Bakara', 'Medeni', 'Kadınlara el sürmeden ve mehirlerini biçmeden onları boşarsanız size sorumluluk yoktur. Onları zengin kendi çapına, fakir kendi çapına uygun bir şekilde faydalandırın. Bu iyi davrananların şanına yakışır bir borçtur.'),
(244, 244, 2, 237, 'Bakara', 'Medeni', 'Eğer onlara mehir biçer de el sürmeden onları boşarsanız, kendileri veya nikah akdi elinde olan erkeğin bağışlaması hali müstesna biçtiğinizin yarısını verin, bağışlamanız Allah\'tan sakınmaya daha uygundur. Aranızdaki iyiliği unutmayın. Allah şüphesiz işlediklerinizi görür.'),
(245, 245, 2, 238, 'Bakara', 'Medeni', 'Namazlara ve orta namaza devam edin; gönülden boyun eğerek Allah için namaza durun.'),
(246, 246, 2, 239, 'Bakara', 'Medeni', 'Eğer korkarsanız, yaya yahut binekte iken kılın, güvene erişince, bilmediklerinizi öğrettiği gibi Allah\'ı anın.'),
(247, 247, 2, 240, 'Bakara', 'Medeni', 'İçinizden ölüp, eşler bırakacak olanlar, evlerinden çıkarılmaksızın, senesine kadar eşlerinin geçimini sağlayacak şeyi vasiyet etsinler; eğer çıkarlarsa kendilerinin meşru olarak yaptıklarından dolayı size sorumluluk yoktur. Allah güçlüdür, Hakim\'dir.'),
(248, 248, 2, 241, 'Bakara', 'Medeni', 'Boşanan kadınları, haksızlıktan sakınanlara bir borç olmak üzere, uygun bir surette faydalandırma vardır.'),
(249, 249, 2, 242, 'Bakara', 'Medeni', 'Allah ayetlerini düşünesiniz diye böylece açıklamaktadır.'),
(250, 250, 2, 243, 'Bakara', 'Medeni', 'Binlerce kişinin memleketlerinden ölüm korkusuyla çıktıklarını görmedin mi? Allah onlara \"Ölün\" dedi. Sonra onları diriltti. Allah insanlara bol nimet verir, fakat insanların çoğu şükretmezler.'),
(251, 251, 2, 244, 'Bakara', 'Medeni', 'Allah yolunda savaşın; bilin ki Allah işitir ve bilir.'),
(252, 252, 2, 245, 'Bakara', 'Medeni', 'Allah\'a, kat kat karşılığını arttıracağı güzel bir ödünç takdiminde kim bulunur? Allah hem darlaştırır, hem bollaştırır; O\'na döneceksiniz.'),
(253, 253, 2, 246, 'Bakara', 'Medeni', 'Musa\'dan sonra İsrailoğullarının ileri gelenlerini görmedin mi? Peygamberlerinden birine, \"Bize bir hükümdar gönder de Allah yolunda savaşalım\" demişlerdi. \"Ya savaş size farz kılındığında gitmeyecek olursanız?\" demişti. \"Memleketimizden ve çocuklarımızdan uzaklaştırıldığımıza göre niye Allah yolunda savaşmıyalım?\" demişlerdi. Ama savaş onlara farz kılınınca, az bir kısmı müstesna yüz cevirdiler. Allah zalimleri bilir.'),
(254, 254, 2, 247, 'Bakara', 'Medeni', 'Peygamberleri onlara \"Allah size şüphesiz, Talut\'u hükümdar olarak gönderdi\" dedi. \"Biz hükümdarlığa ondan layık iken ve ona malca da bir bolluk verilmemişken bize hükümdar olmağa o nasıl layık olabilir?\" dediler, \"Doğrusu Allah size onu seçti, bilgice ve vücutça gücünü artırdı\" dedi. Allah mülkü dilediğine verir. Allah her şeyi kaplar ve bilir.'),
(255, 255, 2, 248, 'Bakara', 'Medeni', 'Peygamberleri onlara, \"Onun hükümdarlığının alameti, size sandığın gelmesidir, onda Rabbinizden gelen gönül rahatlığı ve Musa ailesinin ve Harun ailesinin bıraktıklarından kalanlar var; onu melekler taşır, eğer inanmışsanız bunda sizin için delil vardır\" dedi.'),
(256, 256, 2, 249, 'Bakara', 'Medeni', 'Talut orduyla birlikte ayrıldıktan sonra, \"Doğrusu Allah sizi bir ırmakla deneyecektir, ondan içen benden değildir, onu tatmayan eliyle sadece bir avuç avuçlayan müstesna şüphesiz bendendir\" dedi. Onlardan pek azı hariç, sudan içtiler. Kendisi ve kendisiyle olan inananlar ırmağı geçince, \"Bugün Calut ve ordusuna karşı koyacak gücümüz yok\" dediler. Kendilerinin Allah\'a kavuşacağını bilenler ise: \"Nice az topluluk çok topluluğa Allah\'ın izniyle üstün gelmiştir, Allah sabredenlerle beraberdir\" dediler.'),
(257, 257, 2, 250, 'Bakara', 'Medeni', 'Calut ve ordusuna karşı çıktıklarında, \"Rabbimiz! Bize sabır ver, sebatımızı artır, inkar eden millete karşı bize yardım et\" dediler.'),
(258, 258, 2, 251, 'Bakara', 'Medeni', 'Onları Allah\'ın izniyle bozguna uğrattılar; Davud Calut\'u öldürdü, Allah Davud\'a hükümranlık ve hikmet verdi ve ona dilediğinden öğretti. Allah\'ın insanları birbiriyle savması olmasaydı yeryüzünün düzeni bozulurdu. Fakat Allah alemlere lütufkardır.'),
(259, 259, 2, 252, 'Bakara', 'Medeni', 'İşte bunlar Allah\'ın ayetleridir. Biz onları sana doğru olarak okuyoruz. Şüphesiz sen peygamberlerden birisin.'),
(260, 260, 2, 253, 'Bakara', 'Medeni', 'İşte bu peygamberlerden bir kısmını diğerlerinden üstün kıldık. Onlardan Allah\'ın kendilerine hitabettiği, derecelerle yükselttikleri vardır. Meryem oğlu İsa\'ya belgeler verdik, onu Ruhul Kudüs\'le destekledik. Allah dileseydi, belgeler kendilerine geldikten sonra, peygamberlerin ardından birbirlerini öldürmezlerdi. Fakat ayrılığa düştüler, kimi inandı, kimi inkar etti. Allah dileseydi birbirlerini öldürmezlerdi, lakin Allah istediğini yapar.'),
(261, 261, 2, 254, 'Bakara', 'Medeni', 'Ey inananlar! Alışverişin, dostluğun, şefaatin olmayacağı günün gelmesinden önce sizi rızıklandırdığımızdan hayra sarfedin. İnkar edenler ancak yazık edenlerdir.'),
(262, 262, 2, 255, 'Bakara', 'Medeni', 'Allah, O\'ndan başka tanrı olmayan, kendisini uyuklama ve uyku tutmayan, diri, her an yaratıklarını gözetip durandır. Göklerde olan ve yerde olan ancak O\'nundur. O\'nun izni olmadan katında şefaat edecek kimdir? Onların işlediklerini ve işleyeceklerini bilir, dilediğinden başka ilminden hiçbir şeyi kavrayamazlar. Hükümranlığı gökleri ve yeri kaplamıştır, onların gözetilmesi O\'na ağır gelmez. O yücedir, büyüktür.'),
(263, 263, 2, 256, 'Bakara', 'Medeni', 'Dinde zorlama yoktur; Artık hak ile batıl iyice ayrılmıştır. Tağutu (saptırıcıları) inkar edip Allah\'a inanan kimse, kopmak bilmeyen sağlam bir kulpa sarılmıştır. Allah işitendir, bilendir.'),
(264, 264, 2, 257, 'Bakara', 'Medeni', 'Allah inananların dostudur, onları karanlıklardan aydınlığa çıkarır. İnkar edenlerin ise dostları tağuttur. Onları aydınlıktan karanlıklara sürüklerler. İşte onlar cehennemliklerdir, onlar orada temelli kalacaklardır.'),
(265, 265, 2, 258, 'Bakara', 'Medeni', 'Allah kendisine mülk verdi diye İbrahim ile Rabbi hakkında tartışanı görmedin mi? İbrahim: \"Rabbim, dirilten ve öldürendir\" demişti. \"Ben de diriltir ve öldürürüm\" dedi; İbrahim, \"Şüphesiz Allah güneşi doğudan getiriyor, sen de batıdan getirsene\" dedi. İnkar eden şaşırıp kaldı. Allah zulmeden kimseleri doğru yola eriştirmez.'),
(266, 266, 2, 259, 'Bakara', 'Medeni', 'Yahut altı üstüne gelmiş bir kasabaya uğrayan kimseyi görmedin mi? \"Allah burayı ölümünden sonra acaba nasıl diriltecek?\" dedi. Bunun üzerine Allah onu yüz yıl ölü bıraktı, sonra diriltti, \"Ne kadar kaldın?\" dedi, \"Bir gün veya bir günden az kaldım\" dedi, \"Hayır yüz yıl kaldın, yiyeceğine içeceğine bak, bozulmamış; eşeğine bak ve hem seni insanlar için bir ibret kılacağız, kemiklere bak, onları nasıl birleştirip, sonra onlara et giydiriyoruz\" dedi; bu ona apaçık belli olunca, \"Artık Allah\'ın her şeye Kadir olduğuna inanmış bulunuyorum\" dedi.'),
(267, 267, 2, 260, 'Bakara', 'Medeni', 'İbrahim: \"Rabbim! Ölüleri nasıl dirilttiğini bana göster\" dediğinde, \"İnanmıyor musun?\" deyince de, \"Hayır öyle değil, fakat kalbim iyice kansın\" demişti. \"Öyleyse dört çeşit kuş al, onları kendine alıştır, sonra onları parçalayıp her dağın üzerine bir parça koy, sonra onları çağır; koşarak sana gelirler. O halde Allah\'ın güçlü ve Hakim olduğunu bil\" demişti.'),
(268, 268, 2, 261, 'Bakara', 'Medeni', 'Mallarını Allah yolunda sarfedenlerin durumu, her başağında yüz tane olmak üzere yedi başak veren tanenin durumu gibidir. Allah dilediğine kat kat verir. Allah\'ın lütfü geniştir, O her şeyi bilendir.'),
(269, 269, 2, 262, 'Bakara', 'Medeni', 'Mallarını Allah yolunda sarfedip sonra sarfettikleri şeyin ardından başa kakmayan ve eza etmeyenlerin ecirleri Rablerinin katındadır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(270, 270, 2, 263, 'Bakara', 'Medeni', 'Güzel bir söz ve bağışlama, peşinden eza gelen bir sadakadan daha iyidir. Allah müstağnidir, Halim\'dir.'),
(271, 271, 2, 264, 'Bakara', 'Medeni', 'Ey İnananlar! Allah\'a ve ahiret gününe inanmayıp, insanlara gösteriş için malını sarfeden kimse gibi, sadakalarınızı başa kakma ve eza etmekle boşa çıkarmayın. Onun durumu, üzerinde toprak bulunan kayanın durumu gibidir, üzerine bol yağmur yağdığındaonu cascavlak bırakır. Kazandıklarından hiçbir şey elde edemezler. Allah inkar eden kimseleri doğru yola eriştirmez.'),
(272, 272, 2, 265, 'Bakara', 'Medeni', 'Allah\'ın rızasını kazanmak ve kalblerini sağlamlaştırmak için mallarını sarfedenlerin durumu, yüksekçe bir tepede bulunan, bol yağmur aldığında yemişlerini iki kat veren, bol yağmur yağmasa bile çisentisi düşen bir bahçenin durumu gibidir. Allah işlediklerinizi görür.'),
(273, 273, 2, 266, 'Bakara', 'Medeni', 'Hangi biriniz, kendisi ihtiyarlamış ve çocukları da güçsüzken, altlarından ırmaklar akan, hurma, üzüm ve her çeşit meyveleri bulunan bahçesinin, ateşli bir kasırganın kopmasıyla yanmasını ister? Düşünesiniz diye Allah size ayetlerini böylece açıklar.'),
(274, 274, 2, 267, 'Bakara', 'Medeni', 'Ey İnananlar! Kazandıklarınızın temizlerinden ve size yerden çıkardıklarımızdan sarfedin; iğrenmeden alamıyacağınız pis şeyleri vermeye kalkmayın. Allah\'ın müstağni ve övülmeye layık olduğunu bilin.'),
(275, 275, 2, 268, 'Bakara', 'Medeni', 'Şeytan sizi fakirlikle korkutarak cimriliği ve hayasızlığı emreder; Allah ise kendisinden mağfiret ve bol nimet vadeder. Allah\'ın lütfü boldur, O her şeyi bilir.'),
(276, 276, 2, 269, 'Bakara', 'Medeni', 'Hikmeti dilediğine verir. Kime hikmet verilmişse şüphesiz ona çokça hayır verilmiştir. Bundan ancak akıl sahipleri ibret alır.'),
(277, 277, 2, 270, 'Bakara', 'Medeni', 'Sarfettiğiniz harcı ve adadığınız adağı şüphesiz Allah bilir. Zulmedenlerin hiç yardımcıları yoktur.'),
(278, 278, 2, 271, 'Bakara', 'Medeni', 'Sadakaları açıkça verirseniz o ne güzel! Eğer onları yoksullara gizlice verirseniz sizin için daha iyidir. Allah onları kötülüklerinizden bir kısmına karşı tutar. Allah işlediklerinizden haberdardır.'),
(279, 279, 2, 272, 'Bakara', 'Medeni', 'Onların doğru yola iletilmeleri sana düşmez, fakat Allah dilediğini doğru yola eriştirir. Sarfettiğiniz iyi şey kendinizedir, zaten ancak Allah\'ın rızasını kazanmak için sarfedersiniz. Sarfettiğiniz iyi bir şeyin karşılığı haksızlığa uğratılmaksızın size verilir.'),
(280, 280, 2, 273, 'Bakara', 'Medeni', 'Sadakalarınızı, kendilerini Allah yoluna adayıp yeryüzünde dolaşamayanlara, hayalarından dolayı, kendilerini tanımayanların zengin saydıkları yoksullara verin. Onları yüzlerinden tanırsın, insanlardan yüzsüzlük ederek bir şey istemezler. Sarfettiğiniz iyi bir şeyi Allah şüphesiz bilir.'),
(281, 281, 2, 274, 'Bakara', 'Medeni', 'Gece gündüz, açık gizli, mallarını sarfedenlerin mükafatlarını Rab\'leri verecektir. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(282, 282, 2, 275, 'Bakara', 'Medeni', 'Faiz yiyenler mahşerde ancak şeytanın çarptığı kimsenin kalktığı gibi kalkarlar. Bu, onların, \"Zaten alışveriş de faiz gibidir\" demelerindendir. Oysa Allah alışverişi helal, faizi haram kıldı. Kime Rabb\'inden bir öğüt gelir de faizcilikten geri durursa, geçmişi kendisinedir, onun işi Allah\'a aittir. Kim faizciliğe dönerse, işte onlar cehennemliktir, onlar orada temelli kalacaklardır.'),
(283, 283, 2, 276, 'Bakara', 'Medeni', 'Allah faizi eksiltir, sadakaları bereketlendirir. Allah pek nankör olan hiçbir günahkarı sevmez.'),
(284, 284, 2, 277, 'Bakara', 'Medeni', 'İnanıp yararlı işler işleyenlerin, namaz kılıp, zekat verenlerin Rab\'leri katında ecirleri vardır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(285, 285, 2, 278, 'Bakara', 'Medeni', 'Ey İnananlar! Allah\'tan sakının, inanmışsanız, faizden arta kalmış hesabdan vazgeçin.'),
(286, 286, 2, 279, 'Bakara', 'Medeni', 'Böyle yapmazsanız, bunun Allah\'a ve Peygamberine karşı açılmış bir savaş olduğunu bilin. Eğer tevbe ederseniz sermayeniz sizindir. Böylece haksızlık etmemiş ve haksızlığa uğramamış olursunuz.'),
(287, 287, 2, 280, 'Bakara', 'Medeni', 'Borçlu darda ise, eli genişleyinceye kadar ona mühlet verin. Bilmiş olsanız borcu bağışlamanız sizin için daha hayırlıdır.'),
(288, 288, 2, 281, 'Bakara', 'Medeni', 'Allah\'a döneceğiniz ve sonra haksızlığa uğramadan herkesin kazancının kendisine eksiksiz verileceği günden korkunuz.'),
(289, 289, 2, 282, 'Bakara', 'Medeni', 'Ey İnananlar! Birbirinize belirli bir süre için borçlandığınız zaman onu yazınız. İçinizden bir katip doğru olarak yazsın; katip onu Allah\'ın kendisine öğrettiği gibi yazmaktan çekinmesin, yazsın. Borçlu olan da yazdırsın, Rabbi olan Allah\'tan sakınsın, ondan bir şey eksiltmesin. Eğer borçlu, aptal veya aciz, ya da yazdıramıyacak durumda ise, velisi, doğru olarak yazdırsın. Erkeklerinizden iki şahid tutun; eğer iki erkek bulunmazsa, şahidlerden razı olacağınız bir erkek, biri unuttuğunda diğeri ona hatırlatacak iki kadın olabilir. Şahidler çağırıldıklarında çekinmesinler. Borç büyük veya küçük olsun, onu süresiyle beraber yazmaya üşenmeyin; bu, Allah katında en doğru, şahidlik için en sağlam ve şüphelenmenizden en uzak olandır. Ancak aranızdaki alışveriş peşin olursa, onu yazmamanızda size bir sorumluluk yoktur. Alışveriş yaptığınızda şahid tutun. Katibe de şahide de zarar verilmesin; eğer zarar verirseniz, o zaman doğru yoldan çıkmış olursunuz. Allah\'tan sakının, Allah size öğretiyor; Allah her şeyi bilir.'),
(290, 290, 2, 283, 'Bakara', 'Medeni', 'Eğer yolculukta olup katip bulamazsanız alınan rehin yeter. Şayet birbirinize güvenirseniz, güvenilen kimse borcunu ödesin. Rabbi olan Allah\'tan sakınsın. Şahidliği gizlemeyin, onu kim gizlerse şüphesiz kalbi günah işlemiş olur. Allah işlediklerinizi bilir.'),
(291, 291, 2, 284, 'Bakara', 'Medeni', 'Göklerde ve yerde olanlar Allah\'ındır. İçinizdekini açıklasanız da gizleseniz de Allah sizi onunla hesaba çeker ve dilediğini bağışlar, dilediğine azabeder. Allah her şeye Kadir\'dir.'),
(292, 292, 2, 285, 'Bakara', 'Medeni', 'Peygamber ve inananlar, ona Rabb\'inden indirilene inandı. Hepsi Allah\'a, meleklerine, kitaplarına, peygamberlerine inandı. \"Peygamberleri arasından hiçbirini ayırdetmeyiz, işittik, itaat ettik, Rabbimiz! Affını dileriz, dönüş Sanadır\" dediler.'),
(293, 293, 2, 286, 'Bakara', 'Medeni', 'Allah kişiye ancak gücünün yeteceği kadar yükler; kazandığı iyilik lehine, ettiği kötülük de aleyhinedir. Rabbimiz! Eğer unutacak veya yanılacak olursak bizi sorumlu tutma. Rabbimiz bizden öncekilere yüklediğin gibi, bize de ağır yük yükleme. Rabbimiz! Bize gücümüzün yetmeyeceği şeyi taşıtma, bizi affet, bizi bağışla, bize acı. Sen Mevlamızsın, kafirlere karşı bize yardım et.'),
(294, 294, 3, 1, 'Âl-i İmrân', 'Medeni', 'Elif, Lam, Mim.'),
(295, 295, 3, 2, 'Âl-i İmrân', 'Medeni', 'Allah, Ondan başka tanrı olmayan, diri, her an yaratıklarını gözetip durandır.'),
(296, 296, 3, 3, 'Âl-i İmrân', 'Medeni', 'Kendisinden önceki Kitapları tasdik eden Hak Kitap\'ı sana indirdi. Önceden insanlara yol gösterici olarak Tevrat ve İncil\'i de indirmişti. O, doğruyu yanlıştan ayıran Kitap\'ı indirdi. Doğrusu Allah\'ın ayetlerini inkar edenler için şiddetli azab vardır. Allah güçlüdür, mazlumların öcünü alır.'),
(297, 297, 3, 4, 'Âl-i İmrân', 'Medeni', 'Kendisinden önceki Kitapları tasdik eden Hak Kitap\'ı sana indirdi. Önceden insanlara yol gösterici olarak Tevrat ve İncil\'i de indirmişti. O, doğruyu yanlıştan ayıran Kitap\'ı indirdi. Doğrusu Allah\'ın ayetlerini inkar edenler için şiddetli azab vardır. Allah güçlüdür, mazlumların öcünü alır.'),
(298, 298, 3, 5, 'Âl-i İmrân', 'Medeni', 'Şüphesiz gökte ve yerde hiçbir şey Allah\'tan gizli kalmaz.'),
(299, 299, 3, 6, 'Âl-i İmrân', 'Medeni', 'Ana rahminde sizi dilediği gibi şekillendiren O\'dur. O\'ndan başka tanrı yoktur, güçlüdür, Hakim\'dir.'),
(300, 300, 3, 7, 'Âl-i İmrân', 'Medeni', 'Sana Kitap\'ı indiren O\'dur. Onda Kitap\'ın temeli olan kesin anlamlı ayetler vardır, diğerleri de çeşitli anlamlıdırlar. Kalblerinde eğrilik olan kimseler, fitne çıkarmak, kendilerine göre yorumlamak için onların çeşitli anlamlı olanlarına uyarlar. Oysa onların yorumunu ancak Allah bilir. İlimde derinleşmiş olanlar: \"Ona inandık, hepsi Rabbimiz\'in katındandır\" derler. Bunu ancak akıl sahipleri düşünür;'),
(301, 301, 3, 8, 'Âl-i İmrân', 'Medeni', 'Rabbimiz! Bizi doğru yola erdirdikten sonra kalblerimizi eğriltme, katından bize rahmet bağışla; şüphesiz Sen sonsuz bağışta bulunansın.'),
(302, 302, 3, 9, 'Âl-i İmrân', 'Medeni', 'Rabbimiz! Doğrusu geleceği şüphe götürmeyen günde, insanları toplayacak olan Sensin. Şüphesiz ki Allah verdiği sözden caymaz.'),
(303, 303, 3, 10, 'Âl-i İmrân', 'Medeni', 'İnkar edenlerin malları ve çocukları, Allah\'a karşı onlara bir şey sağlamaz. İşte onlar ateşin yakıtlarıdır.'),
(304, 304, 3, 11, 'Âl-i İmrân', 'Medeni', 'Bunların tutumu, Firavun ailesinin ve onlardan öncekilerin tutumu gibi ki, ayetlerimizi yalanladılar da Allah onları günahlarından dolayı yok (helak) etti. Allah\'ın cezalandırması şiddetlidir.'),
(305, 305, 3, 12, 'Âl-i İmrân', 'Medeni', 'İnkar edenlere: \"Yenileceksiniz, toplanıp cehenneme sürüleceksiniz, orası ne kötü döşektir\" de.'),
(306, 306, 3, 13, 'Âl-i İmrân', 'Medeni', 'Karşı karşıya gelen iki topluluğun durumlarında sizin için ibret vardır; biri Allah yolunda savaşanlardır, diğeri inkarcılardır ki, bunlar karşı tarafı gözleriyle kendilerinin iki misli görüyorlardı. Allah dilediğini yardımıyla destekler. Bunda görebilenler için ibret vardır.'),
(307, 307, 3, 14, 'Âl-i İmrân', 'Medeni', 'Kadınlara, oğullara, kantar kantar altın ve gümüşe, nişanlı atlar ve develere, ekinlere karşı aşırı sevgi beslemek insanlara güzel gösterilmiştir. Bunlar dünya hayatının nimetleridir, oysa gidilecek yerin güzeli Allah katındadır.'),
(308, 308, 3, 15, 'Âl-i İmrân', 'Medeni', 'De ki: Bundan daha iyisini size haber vereyim mi? Allah\'a karşı gelmekten sakınanlara, Rab\'lerinin katında, altlarından ırmaklar akan ve orada temelli kalacakları cennetler, tertemiz eşler ve Allah\'ın rızası vardır. Allah kullarını hakkiyle görücüdür.'),
(309, 309, 3, 16, 'Âl-i İmrân', 'Medeni', 'Onlar ki, \"Rabbimiz! Biz şüphesiz inandık, bunun için günahlarımızı bize bağışla ve bizi ateşin azabından koru\" diyen, sabreden, doğru olan, gönülden kulluk eden, hayra sarfeden ve seher vakitlerinde bağışlanma dileyenlerdir.'),
(310, 310, 3, 17, 'Âl-i İmrân', 'Medeni', 'Onlar ki, \"Rabbimiz! Biz şüphesiz inandık, bunun için günahlarımızı bize bağışla ve bizi ateşin azabından koru\" diyen, sabreden, doğru olan, gönülden kulluk eden, hayra sarfeden ve seher vakitlerinde bağışlanma dileyenlerdir.'),
(311, 311, 3, 18, 'Âl-i İmrân', 'Medeni', 'Allah, melekler ve adaleti yerine getiren ilim sahibleri, O\'ndan başka tanrı olmadığına şahidlik etmişlerdir. O\'ndan başka tanrı yoktur, O güçlüdür, Hakim\'dir.'),
(312, 312, 3, 19, 'Âl-i İmrân', 'Medeni', 'Allah katında din, şüphesiz İslam\'dır. Ancak, Kitap verilenler, kendilerine ilim geldikten sonra, aralarındaki ihtiras yüzünden ayrılığa düştüler. Allah\'ın ayetlerini kim inkar ederse bilsin ki, Allah hesabı çabuk görür.'),
(313, 313, 3, 20, 'Âl-i İmrân', 'Medeni', 'Eğer seninle tartışmaya girişirlerse, \"Ben bana uyanlarla birlikte kendimi Allah\'a verdim\" de. Kendilerine Kitap verilenlere ve kitapsızlara: \"Siz de İslam oldunuz mu?\" de, şayet İslam olurlarsa doğru yola girmişlerdir, yüz çevirirlerse, sana yalnız tebliğ etmek düşer. Allah kullarını görür.'),
(314, 314, 3, 21, 'Âl-i İmrân', 'Medeni', 'Allah\'ın ayetlerini inkar edenlere, haksız yere peygamberleri öldürenlere, insanlardan adaleti emredenleri öldürenlere elem verici bir azabı müjdele.'),
(315, 315, 3, 22, 'Âl-i İmrân', 'Medeni', 'Onlar, dünya ve ahirette işleri boşa çıkacak olanlardır. Onların hiç yardımcıları da yoktur.'),
(316, 316, 3, 23, 'Âl-i İmrân', 'Medeni', 'Kendilerine Kitapdan bir pay verilenleri, görmedin mi? Onlar aralarında hüküm vermek için Allah\'ın Kitabına çağırılmışlar, sonra onlardan bir takımı dönmüşlerdir. Onlar temelli yüz çevirenlerdir.'),
(317, 317, 3, 24, 'Âl-i İmrân', 'Medeni', 'Bu, onların: \"Bize ateş sadece sayılı birkaç gün değecektir\" demelerindendir. Uydurup durdukları şeyler, onları dinlerinde yanıltmıştır.'),
(318, 318, 3, 25, 'Âl-i İmrân', 'Medeni', 'Geleceğinden şüphe olmayan günde, onları topladığımız ve haksızlık yapılmayarak herkese kazandığı eksiksiz verildiği zaman, nasıl olacak?'),
(319, 319, 3, 26, 'Âl-i İmrân', 'Medeni', 'De ki: \"Mülkün sahibi olan Allah\'ım! Mülkü dilediğine verirsin; dilediğinden çekip alırsın; dilediğini aziz kılar, dilediğini alçaltırsın; iyilik elindedir. Doğrusu Sen, her şeye Kadir\'sin.'),
(320, 320, 3, 27, 'Âl-i İmrân', 'Medeni', 'Geceyi gündüze, gündüzü geceye geçirirsin; ölüden diri, diriden ölü çıkarırsın; dilediğini hesapsız rızıklandırırsın\".'),
(321, 321, 3, 28, 'Âl-i İmrân', 'Medeni', 'Müminler, müminleri bırakıp kafirleri dost edinmesinler; kim böyle yaparsa Allah katında bir değeri yoktur, ancak, onlardan sakınmanız hali müstesnadır. Allah sizi Kendisiyle korkutur, dönüş Allah\'adır.'),
(322, 322, 3, 29, 'Âl-i İmrân', 'Medeni', 'De ki: \"İçinizde olanı gizleseniz de açıklasanız da Allah onu bilir. Göklerde olanları da, yerde olanları da bilir. Allah her şeye Kadir\'dir\".'),
(323, 323, 3, 30, 'Âl-i İmrân', 'Medeni', 'Her kişinin yaptığı iyiliği ve yaptığı kötülüğü, ki kendisiyle o kötülük arasında uzun bir mesafe olmasını diler, hazır bulacağı günü bir düşünün. Kullarına karşı şefkatli olan Allah size kendinden korkmanızı emreder.'),
(324, 324, 3, 31, 'Âl-i İmrân', 'Medeni', 'De ki: \"Allah\'ı seviyorsanız bana uyun. Allah da sizi sevsin ve günahlarınızı bağışlasın. Allah affeder ve merhamet eder\".'),
(325, 325, 3, 32, 'Âl-i İmrân', 'Medeni', 'De ki: \"Allah\'a ve Peygambere itaat edin\". Yüz çevirirlerse bilsinler ki, Allah inkar edenleri sevmez.'),
(326, 326, 3, 33, 'Âl-i İmrân', 'Medeni', 'Allah, Adem\'i, Nuh\'u, İbrahim ailesini, İmran ailesini birbirinin soyundan olarak alemlere tercih etti. Allah işitendir, bilendir.'),
(327, 327, 3, 34, 'Âl-i İmrân', 'Medeni', 'Allah, Adem\'i, Nuh\'u, İbrahim ailesini, İmran ailesini birbirinin soyundan olarak alemlere tercih etti. Allah işitendir, bilendir.'),
(328, 328, 3, 35, 'Âl-i İmrân', 'Medeni', 'İmran\'ın karısı: \"Ya Rabbi! Karnımda olanı, sadece sana hizmet etmek üzere adadım, benden kabul buyur, doğrusu işiten ve bilen ancak Sensin\" demişti.'),
(329, 329, 3, 36, 'Âl-i İmrân', 'Medeni', 'Onu doğurduğunda, Allah onun ne doğurduğunu bilirken \"Ya Rabbi! Kız doğurdum. Erkek, kız gibi değildir, ben ona Meryem adını verdim, ben onu da soyunu da, kovulmuş şeytandan Sana sığındırırım\" dedi.'),
(330, 330, 3, 37, 'Âl-i İmrân', 'Medeni', 'Rabbi onu güzel bir kabulle karşıladı, güzel bir bitki gibi yetiştirdi; onu Zekeriya\'nın himayesine bıraktı. Zekeriya mabedde onun yanına her girişinde, yanında bir yiyecek bulurdu. \"Ey Meryem! Bu sana nereden geldi?\" demiş, o da: Bu, Allah\'ın katındandır\" cevabını vermişti. Doğrusu Allah dilediğini hesapsız rızıklandırır.'),
(331, 331, 3, 38, 'Âl-i İmrân', 'Medeni', 'Orada Zekeriya Rabbine dua etti: \"Ya Rabbi! Bana kendi katından temiz bir soy bahşet, doğrusu Sen duayı işitirsin\".'),
(332, 332, 3, 39, 'Âl-i İmrân', 'Medeni', 'Mabedde namaz kılarken melekler ona seslendiler: \"Allah sana Allah\'ın emriyle (vücud bulan İsa\'yı) tasdik eden, efendi, iffetli, iyilerden bir peygamber olarak Yahya\'yı müjdeler\".'),
(333, 333, 3, 40, 'Âl-i İmrân', 'Medeni', '\"Ya Rabbi! Ben artık iyice kocamış, karım da kısırken nasıl oğlum olabilir?\" dedi. Allah: \"Böyledir, Allah dilediğini yapar\" dedi.'),
(334, 334, 3, 41, 'Âl-i İmrân', 'Medeni', '\"Ya Rabbi! Bana bir alamet ver\" dedi, \"Alametin, üç gün, işaretle anlaşma dışında insanlarla konuşmamandır; Rabbini çok an, akşam sabah hamd et\" dedi.'),
(335, 335, 3, 42, 'Âl-i İmrân', 'Medeni', 'Melekler şöyle demişti: \"Ey Meryem! Allah seni seçip temizledi. Dünyaların kadınlarından seni üstün tuttu.\"'),
(336, 336, 3, 43, 'Âl-i İmrân', 'Medeni', '\"Ey Meryem! Rabbine gönülden boyun eğ, secde et, rüku edenlerle birlikte rüku et.\"'),
(337, 337, 3, 44, 'Âl-i İmrân', 'Medeni', 'Bu Sana vahyettiğimiz gayb haberlerindendir. Meryem\'e hangisi kefil olacak diye kalemlerini atarlarken sen yanlarında değildin, çekişirlerken de orada bulunmadın.'),
(338, 338, 3, 45, 'Âl-i İmrân', 'Medeni', 'Melekler demişti ki: \"Ey Meryem! Allah sana, Kendinden bir sözü, adı Meryem oğlu İsa olan Mesihi, dünya ve ahirette şerefli ve Allah\'a yakın kılınanlardan olarak müjdeler\".'),
(339, 339, 3, 46, 'Âl-i İmrân', 'Medeni', '\"İnsanlarla, beşikte iken de, yetişkin iken de konuşacaktır ve o, iyilerdendir\".'),
(340, 340, 3, 47, 'Âl-i İmrân', 'Medeni', 'Meryem: \"Rabbim! Bana bir insan dokunmamışken nasıl çocuğum olabilir?\" demişti. Melekler şöyle dediler: \"Allah dilediğini böylece yaratır. Bir işin olmasını dilerse ona ol der ve olur\".'),
(341, 341, 3, 48, 'Âl-i İmrân', 'Medeni', 'Ona Kitabı, hikmeti, Tevrat\'ı ve İncil\'i öğretecek, İsrailoğullarına şöyle diyen bir peygamber kılacak: \"Ben size Rabbinizden bir ayet getirdim. Ben size çamurdan kuş gibi bir şey yapıp ona üfleyeceğim, Allah\'ın izniyle, hemen kuş olacaktır; anadan doğma körleri, alacalıları iyi edeceğim; Allah\'ın izniyle, ölüleri dirilteceğim; yediklerinizi ve evlerinizde sakladıklarınızı da size haber vereceğim. İnanmışsanız bunda size delil vardır\".'),
(342, 342, 3, 49, 'Âl-i İmrân', 'Medeni', 'Ona Kitabı, hikmeti, Tevrat\'ı ve İncil\'i öğretecek, İsrailoğullarına şöyle diyen bir peygamber kılacak: \"Ben size Rabbinizden bir ayet getirdim. Ben size çamurdan kuş gibi bir şey yapıp ona üfleyeceğim, Allah\'ın izniyle, hemen kuş olacaktır; anadan doğma körleri, alacalıları iyi edeceğim; Allah\'ın izniyle, ölüleri dirilteceğim; yediklerinizi ve evlerinizde sakladıklarınızı da size haber vereceğim. İnanmışsanız bunda size delil vardır\".'),
(343, 343, 3, 50, 'Âl-i İmrân', 'Medeni', '\"Benden önce gelen Tevrat\'ı tasdik etmekle beraber size yasak edilenlerin bir kısmını helal kılmak üzere, Rabbinizden size bir ayet getirdim. Allah\'tan sakının ve bana itaat edin; çünkü Allah benim de Rabbim, sizin de Rabbinizdir. O\'na kulluk edin, bu doğru yoldur\".'),
(344, 344, 3, 51, 'Âl-i İmrân', 'Medeni', '\"Benden önce gelen Tevrat\'ı tasdik etmekle beraber size yasak edilenlerin bir kısmını helal kılmak üzere, Rabbinizden size bir ayet getirdim. Allah\'tan sakının ve bana itaat edin; çünkü Allah benim de Rabbim, sizin de Rabbinizdir. O\'na kulluk edin, bu doğru yoldur\".'),
(345, 345, 3, 52, 'Âl-i İmrân', 'Medeni', 'İsa onların inkarlarını hissedince: \"Allah uğrunda yardımcılarım kimlerdir?\" dedi. Havariler şöyle dediler: \"Biz Allah\'ın yardımcılarıyız, Allah\'a inandık, O\'na teslim olduğumuza şahid ol\".'),
(346, 346, 3, 53, 'Âl-i İmrân', 'Medeni', '\"Rabbimiz! İndirdiğine inandık, Peygambere uyduk; bizi sahid olanlarla beraber yaz\".'),
(347, 347, 3, 54, 'Âl-i İmrân', 'Medeni', 'Fakat (inkarcılar) hile yaptılar. Allah da onları cezalandırdı. Allah, hile yapanların cezasını en iyi verendir.'),
(348, 348, 3, 55, 'Âl-i İmrân', 'Medeni', 'Allah demişti ki: \"Ey İsa! Ben seni eceline yetireceğim, seni kendime yükselteceğim, inkar edenlerden seni tertemiz ayıracağım; sana uyanları, kıyamet gününe kadar, inkar edenlerin üstünde tutacağım. Sonra dönüşünüz Banadır. Ayrılığa düştüğünüz hususlarda aranızda hükmedeceğim. İnkar edenleri de dünya ve ahirette şiddetli azaba uğratacağım. Onların hiç yardımcıları olmayacaktır.\"'),
(349, 349, 3, 56, 'Âl-i İmrân', 'Medeni', 'Allah demişti ki: \"Ey İsa! Ben seni eceline yetireceğim, seni kendime yükselteceğim, inkar edenlerden seni tertemiz ayıracağım; sana uyanları, kıyamet gününe kadar, inkar edenlerin üstünde tutacağım. Sonra dönüşünüz Banadır. Ayrılığa düştüğünüz hususlarda aranızda hükmedeceğim. İnkar edenleri de dünya ve ahirette şiddetli azaba uğratacağım. Onların hiç yardımcıları olmayacaktır.\"'),
(350, 350, 3, 57, 'Âl-i İmrân', 'Medeni', 'İnanıp yararlı iş işleyenlerin ecirleri ise tastamam verilecektir. Allah zalimleri sevmez.'),
(351, 351, 3, 58, 'Âl-i İmrân', 'Medeni', 'Sana okuduğumuz bunlar, ayetlerden ve hikmet dolu Kuran\'dandır.'),
(352, 352, 3, 59, 'Âl-i İmrân', 'Medeni', 'Allah\'ın katında İsa\'nın durumu kendisini topraktan yaratıp sonra ol demesiyle olmuş olan Adem\'in durumu gibidir.'),
(353, 353, 3, 60, 'Âl-i İmrân', 'Medeni', 'Gerçek Rabb\'indendir, o halde şüphelenenlerden olma.'),
(354, 354, 3, 61, 'Âl-i İmrân', 'Medeni', 'Sana ilim geldikten sonra, bu hususta seninle kim tartışacak olursa, de ki: \"Gelin, oğullarımızı, oğullarınızı, kadınlarımızı, kadınlarınızı, kendimizi ve kendinizi çağıralım, sonra lanetleşelim de, Allah\'ın lanetinin yalancılara olmasını dileyelim\".'),
(355, 355, 3, 62, 'Âl-i İmrân', 'Medeni', 'Şüphesiz bu anlatılanlar gerçek olaylardır. Allah\'tan başka tanrı yoktur. Doğrusu Allah güçlüdür, Hakim\'dir.'),
(356, 356, 3, 63, 'Âl-i İmrân', 'Medeni', 'Eğer yüz çevirirlerse, şüphesiz Allah bozguncuları bilir.'),
(357, 357, 3, 64, 'Âl-i İmrân', 'Medeni', 'De ki: \"Ey Kitap ehli! Ancak Allah\'a kulluk etmek, O\'na bir şeyi eş koşmamak, Allah\'ı bırakıp birbirimizi rab olarak benimsememek üzere, bizimle sizin aranızda müşterek bir söze gelin\". Eğer yüz çevirirlerse: \"Bizim müslüman olduğumuza şahid olun\" deyin.'),
(358, 358, 3, 65, 'Âl-i İmrân', 'Medeni', 'Ey Kitap ehli! İbrahim hakkında niçin tartışıyorsunuz? Tevrat da, İncil de şüphesiz ondan sonra indirilmiştir. Akletmiyor musunuz?'),
(359, 359, 3, 66, 'Âl-i İmrân', 'Medeni', 'Siz, hadi bilginiz olan şey üzerinde tartışanlarsınız. Ama bilginiz olmayan şey hakkında niçin tartışırsınız? Oysa Allah bilir, sizler bilmezsiniz.'),
(360, 360, 3, 67, 'Âl-i İmrân', 'Medeni', 'İbrahim, yahudi de, hıristiyan da değildi, ama doğruya yönelen bir müslimdi; ortak koşanlardan değildi.'),
(361, 361, 3, 68, 'Âl-i İmrân', 'Medeni', 'Doğrusu İbrahim\'e en yakın olanlar, ona uyanlar, bu Peygamber ve inananlardır. Allah inananların dostudur.'),
(362, 362, 3, 69, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinden bir takımı sizi sapıtmak isterler; oysa kendilerini saptırırlar da farkına varmazlar.'),
(363, 363, 3, 70, 'Âl-i İmrân', 'Medeni', 'Ey Kitap ehli! Sizler göz göre göre Allah\'ın ayetlerini niçin inkar ediyorsunuz?'),
(364, 364, 3, 71, 'Âl-i İmrân', 'Medeni', 'Ey Kitap ehli! Niçin hakkı batıla karıştırıyor ve bile bile hakkı gizliyorsunuz?'),
(365, 365, 3, 72, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinden bir takımı şöyle dedi: \"İnananlara indirilene günün başında inanın, sonunda inkar edin ki, belki dönerler ve dininize uyanlardan başkasına inanmayın\". De ki: \"Doğru yol Allah\'ın yoludur\". Ve yine başkasına da verildiğine veya Rabbinizin katında Müslümanların karşı delil getirip sizi alt edeceğine inanmayın, derler. De ki: \"Doğrusu bol nimet Allah\'ın elindedir, onu dilediğine verir. Allah\'ın fazlı her şeyi kaplar, O her şeyi bilir\".'),
(366, 366, 3, 73, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinden bir takımı şöyle dedi: \"İnananlara indirilene günün başında inanın, sonunda inkar edin ki, belki dönerler ve dininize uyanlardan başkasına inanmayın\". De ki: \"Doğru yol Allah\'ın yoludur\". Ve yine başkasına da verildiğine veya Rabbinizin katında Müslümanların karşı delil getirip sizi alt edeceğine inanmayın, derler. De ki: \"Doğrusu bol nimet Allah\'ın elindedir, onu dilediğine verir. Allah\'ın fazlı her şeyi kaplar, O her şeyi bilir\".'),
(367, 367, 3, 74, 'Âl-i İmrân', 'Medeni', '\"Rahmetini dilediğine tahsis eder, Allah büyük, bol nimet sahibidir\".'),
(368, 368, 3, 75, 'Âl-i İmrân', 'Medeni', 'Kitap ehli arasında kantarla emanet bıraksan onu sana ödeyen ve bir lira emanet etsen, tepesine dikilmedikçe onu sana ödemeyen vardır. Bu, onların: \"Kitapsızlara karşı üzerimize bir sorumluluk yoktur\" demelerindendir. Onlar bile bile Allah\'a karşı yalan söylemektedirler.'),
(369, 369, 3, 76, 'Âl-i İmrân', 'Medeni', 'Hayır, öyle değil; ahdini yerine getiren ve günahtan sakınan bilsin ki, Allah sakınanları şüphesiz sever.'),
(370, 370, 3, 77, 'Âl-i İmrân', 'Medeni', 'Allah\'ın ahdini ve yeminlerini az bir değere değişenlerin, işte onların, ahirette bir payları yoktur. Allah onlara kıyamet günü hitab etmeyecek, onlara bakmayacak, onları temize çıkarmayacaktır. Elem verici azab onlar içindir.'),
(371, 371, 3, 78, 'Âl-i İmrân', 'Medeni', 'Onlardan bir takımı, Kitapta olmadığı halde Kitaptan zannedesiniz diye dillerini eğip bükerler. O, Allah katından olmadığı halde: \"Allah katındandır\" derler, bile bile Allah\'a karşı yalan söylerler.'),
(372, 372, 3, 79, 'Âl-i İmrân', 'Medeni', 'Allah\'ın kendisine Kitap\'ı, hükmü, peygamberliği verdiği insanoğluna: \"Allah\'ı bırakıp bana kulluk edin\" demek yaraşmaz, fakat: \"Kitabı öğrettiğinize, okuduğunuza göre Rabb\'e kul olun\" demek yaraşır.'),
(373, 373, 3, 80, 'Âl-i İmrân', 'Medeni', 'Size melekleri, peygamberleri Rab olarak benimsemenizi emretmesi de yaraşmaz. Siz müslüman olduktan sonra, size inkar etmeyi mi emredecek?'),
(374, 374, 3, 81, 'Âl-i İmrân', 'Medeni', 'Allah peygamberlerden ahid almıştı: \"And olsun ki size Kitap, hikmet verdim; sizde olanı tasdik eden bir peygamber gelecek, ona mutlaka inanacaksınız ve ona mutlaka yardım edeceksiniz, ikrar edip bu ahdi kabul ettiniz mi?\" demişti. \"İkrar ettik\" demişlerdi de: \"Şahid olun, Ben de sizinle beraber şahidlerdenim\" demişti.'),
(375, 375, 3, 82, 'Âl-i İmrân', 'Medeni', 'Bunun ardından yüz çeviren var ya, işte onlar fasık olanlardır.'),
(376, 376, 3, 83, 'Âl-i İmrân', 'Medeni', 'Allah\'ın dininden başka bir din mi arzu ediyorlar? Oysa göklerde ve yerde kim varsa, ister istemez O\'na teslim olmuştur, O\'na döneceklerdir.'),
(377, 377, 3, 84, 'Âl-i İmrân', 'Medeni', '\"Allah\'a, bize indirilene, İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a ve torunlarına indirilene, Rableri tarafından Musa, İsa ve peygamberlere verilene inandık, onları birbirinden ayırt etmeyiz, biz O\'na teslim olanlarız\" de.'),
(378, 378, 3, 85, 'Âl-i İmrân', 'Medeni', 'Kim İslam\'dan başka bir dine yönelirse, onunki kabul edilmeyecektir. O ahirette de kaybedenlerdendir.'),
(379, 379, 3, 86, 'Âl-i İmrân', 'Medeni', 'İnandıktan, peygamberin hak olduğuna şehadet ettikten, kendilerine belgeler geldikten sonra inkar eden bir milleti Allah nasıl doğru yola eriştirir? Allah zalimleri doğru yola eriştirmez.'),
(380, 380, 3, 87, 'Âl-i İmrân', 'Medeni', 'İşte bunların cezası, Allah\'ın, meleklerin, insanların hepsinin lanetine uğramalarıdır.'),
(381, 381, 3, 88, 'Âl-i İmrân', 'Medeni', 'Orada temellidirler; onlardan azab hafifletilmez; onların azabı geciktirilmez.'),
(382, 382, 3, 89, 'Âl-i İmrân', 'Medeni', 'Ancak bunun ardından tevbe edip düzelenler müstesnadır. Doğrusu Allah bağışlar ve merhamet eder.'),
(383, 383, 3, 90, 'Âl-i İmrân', 'Medeni', 'İnandıktan sonra inkar edip, inkarda aşırı gidenler var ya, onların tevbeleri kabul edilmeyecektir. İşte sapıklar onlardır.'),
(384, 384, 3, 91, 'Âl-i İmrân', 'Medeni', 'Doğrusu inkar edip, inkarcı olarak ölenlerin hiçbirinden, yeryüzünü dolduracak kadar altını fidye vermiş olsa bile, bu kabul edilmeyecektir. İşte elem verici azab onlaradır, onların hiç yardımcıları da yoktur.'),
(385, 385, 3, 92, 'Âl-i İmrân', 'Medeni', 'Sevdiğiniz şeylerden sarfetmedikçe iyiliğe erişemezsiniz. Her ne sarfederseniz, şüphesiz Allah onu bilir.'),
(386, 386, 3, 93, 'Âl-i İmrân', 'Medeni', 'Tevrat\'ın indirilmesinden önce İsrail\'in kendisine haram ettiğinden başka bütün yiyecekler İsrailoğullarına helal idi. De ki: \"Doğru sözlü iseniz Tevrat\'ı getirip okuyun\".'),
(387, 387, 3, 94, 'Âl-i İmrân', 'Medeni', 'Bundan sonra Allah\'a karşı kim yalan isnad ederse, işte onlar zalimlerdir.'),
(388, 388, 3, 95, 'Âl-i İmrân', 'Medeni', 'De ki: \"Allah doğru söyledi, doğruya meyleden İbrahim\'in dinine uyun; O, puta tapanlardan değildi\".'),
(389, 389, 3, 96, 'Âl-i İmrân', 'Medeni', 'Doğrusu insanlar için ilk kurulan ev, Mekke\'de, dünyalar için mübarek ve doğru yol gösteren Kabe\'dir.'),
(390, 390, 3, 97, 'Âl-i İmrân', 'Medeni', 'Orada apaçık deliller vardır, İbrahim\'in makamı vardır; kim oraya girerse, güvenlik içinde olur; oraya yol bulabilen insana Allah için Kabe\'yi haccetmesi gereklidir. Kim inkar ederse, bilsin ki; doğrusu Allah alemlerden müstağnidir.'),
(391, 391, 3, 98, 'Âl-i İmrân', 'Medeni', 'De ki: \"Ey Kitap ehli! Allah yaptıklarınızı görüp dururken, niçin Allah\'ın ayetlerini inkar ediyorsunuz?\"'),
(392, 392, 3, 99, 'Âl-i İmrân', 'Medeni', 'De ki: \"Ey Kitap ehli! Siz doğru olduğuna şahidken, niçin inananları Allah\'ın yolunu eğri göstermeğe yeltenerek ondan çeviriyorsunuz? Allah işlediklerinizden gafil değildir\".'),
(393, 393, 3, 100, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! Kitap verilenlerin bir takımına uyarsanız, inanmanızdan sonra sizi kafir olmağa çevirirler.'),
(394, 394, 3, 101, 'Âl-i İmrân', 'Medeni', 'Allah\'ın ayetleri size okunur, aranızda da Peygamberi bulunurken nasıl inkar edersiniz? Kim Allah\'ın Kitabına sarılırsa şüphesiz doğru yola erişir.'),
(395, 395, 3, 102, 'Âl-i İmrân', 'Medeni', 'Ey inananlar! Allah\'tan, sakınılması gerektiği gibi sakının, sizler ancak müslüman olarak can verin.'),
(396, 396, 3, 103, 'Âl-i İmrân', 'Medeni', 'Toptan Allah\'ın ipine sarılın, ayrılmayın. Allah\'ın size olan nimetini anın: Düşmandınız, kalblerinizin arasını uzlaştırdı da onun nimeti sayesinde kardeş oldunuz. Bir ateş çukurunun kenarında idiniz, sizi oradan kurtardı. Allah, doğru yola erişesiniz diye size böylece ayetlerini açıklar.'),
(397, 397, 3, 104, 'Âl-i İmrân', 'Medeni', 'Sizden, iyiye çağıran, doğruluğu emreden ve fenalıktan meneden bir cemaat olsun. İşte başarıya erişenler yalnız onlardır.'),
(398, 398, 3, 105, 'Âl-i İmrân', 'Medeni', 'Kendilerine belgeler geldikten sonra ayrılan ve ayrılığa düşenler gibi olmayın. Bir takım yüzlerin ağaracağı ve bir takım yüzlerin kararacağı günde büyük azab onlaradır. Yüzleri kararanlara: \"İnanmanızdan sonra inkar eder misiniz? İnkar etmenizden dolayı tadın azabı\" denecektir.'),
(399, 399, 3, 106, 'Âl-i İmrân', 'Medeni', 'Kendilerine belgeler geldikten sonra ayrılan ve ayrılığa düşenler gibi olmayın. Bir takım yüzlerin ağaracağı ve bir takım yüzlerin kararacağı günde büyük azab onlaradır. Yüzleri kararanlara: \"İnanmanızdan sonra inkar eder misiniz? İnkar etmenizden dolayı tadın azabı\" denecektir.'),
(400, 400, 3, 107, 'Âl-i İmrân', 'Medeni', 'Yüzleri ağaranlar ise Allah\'ın rahmetindedirler. Onlar orada temellidirler.'),
(401, 401, 3, 108, 'Âl-i İmrân', 'Medeni', 'İşte bunlar, sana doğru olarak okuduğumuz Allah\'ın ayetleridir. Allah hiç kimseye zulmetmek istemez.'),
(402, 402, 3, 109, 'Âl-i İmrân', 'Medeni', 'Göklerde olanlar da, yerde olanlar da Allah\'ındır. İşler Allah\'a varacaktır.'),
(403, 403, 3, 110, 'Âl-i İmrân', 'Medeni', 'Siz, insanlar için ortaya çıkarılan, doğruluğu emreden, fenalıktan alıkoyan, Allah\'a inanan hayırlı bir ümmetsiniz. Kitap ehli inanmış olsalardı, kendileri için daha hayırlı olurdu; içlerinde inananlar olmakla beraber, çoğu yoldan çıkmıştır.'),
(404, 404, 3, 111, 'Âl-i İmrân', 'Medeni', 'Onlar incitmekten başka size bir zarar veremezler. Sizinle savaşa koyulurlarsa, geri dönüp kaçarlar. Sonra kendilerine yardım da edilmez.'),
(405, 405, 3, 112, 'Âl-i İmrân', 'Medeni', 'Nerede bulunsalar Allah\'ın ve inanan insanların himayesinde olanlar müstesna onlara alçaklık damgası vurulmuştur. Allah\'tan bir gazaba uğradılar, onlara aşağılık damgası vuruldu. Bu, Allah\'ın ayetlerini inkar etmeleri ve haksız yere peygamberleri öldürmelerindendir. Bu, karşı gelmeleri ve taşkınlık yapmalarındandır.'),
(406, 406, 3, 113, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinin hepsi bir değildir: Onlardan geceleri secdeye kapanarak Allah\'ın ayetlerini okuyup duranlar vardır; bunlar Allah\'a ve ahiret gününe inanır, kötülükten meneder, iyiliklere koşarlar. İşte onlar iyilerdendir.'),
(407, 407, 3, 114, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinin hepsi bir değildir: Onlardan geceleri secdeye kapanarak Allah\'ın ayetlerini okuyup duranlar vardır; bunlar Allah\'a ve ahiret gününe inanır, kötülükten meneder, iyiliklere koşarlar. İşte onlar iyilerdendir.'),
(408, 408, 3, 115, 'Âl-i İmrân', 'Medeni', 'Ne iyilik yaparlarsa, karşılığını bulacaklardır. Allah sakınanları bilir.'),
(409, 409, 3, 116, 'Âl-i İmrân', 'Medeni', 'İnkar eden kimselerin malları ve çocukları, Allah\'tan yana, onlara bir fayda vermeyecektir. İşte onlar cehennemliklerdir, onlar orada temellidirler.'),
(410, 410, 3, 117, 'Âl-i İmrân', 'Medeni', 'Bu dünya hayatında sarfettiklerinin durumu, kendilerine zulmeden kimselerin ekinlerine isabetle kavurup mahveden soğuk bir rüzgarın durumu gibidir. Allah onlara zulmetmedi, onlar kendilerine yazık ettiler.'),
(411, 411, 3, 118, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! Sizden olmayanı sırdaş edinmeyin, onlar sizi şaşırtmaktan geri durmazlar, sıkıntıya düşmenizi isterler. Onların öfkesi ağızlarından taşmaktadır, kalblerinin gizlediği ise daha büyüktür. Eğer aklediyorsanız, şüphesiz size ayetleri açıkladık.'),
(412, 412, 3, 119, 'Âl-i İmrân', 'Medeni', 'İşte siz, onlar sizi sevmezken onları seven ve Kitapların bütününe inanan kimselersiniz. Size rastladıkları zaman: \"İnandık\" derler, yalnız kaldıklarında da, size öfkelerinden parmaklarını ısırırlar. De ki: \"Öfkenizden çatlayın\". Allah kalblerde olanı bilir.'),
(413, 413, 3, 120, 'Âl-i İmrân', 'Medeni', 'Size bir iyilik gelse, onların fenasına gider; başınıza bir kötülük gelse buna sevinirler. Sabreder ve sakınırsanız, onların hilesi size hiçbir zarar vermez. Allah işlediklerinin hepsini ilmiyle kuşatmıştır.'),
(414, 414, 3, 121, 'Âl-i İmrân', 'Medeni', 'Sen inananları savaş için duracakları yerlere yerleştirmek üzere, erkenden evinden ayrılmıştın. Allah işitir ve bilir.'),
(415, 415, 3, 122, 'Âl-i İmrân', 'Medeni', 'Sizden iki takım bozulup geri çekilmek üzere idi; oysa Allah onların dostu idi, inananlar yalnız Allah\'a güvensinler.'),
(416, 416, 3, 123, 'Âl-i İmrân', 'Medeni', 'And olsun ki, siz düşkün bir durumda iken, Bedir\'de, Allah size yardım etmişti; Allah\'tan sakının ki şükredebilesiniz.'),
(417, 417, 3, 124, 'Âl-i İmrân', 'Medeni', 'İnananlara: \"Rabbinizin size gönderilmiş üç bin melekle yardım etmesi size yetmeyecek mi?\" diyordun. Evet, eğer sabrederseniz, sakınırsanız ve onlar de hemen üzerinize gelirlerse Rabbiniz size, nişanlı beş bin melekle imdat edecektir.'),
(418, 418, 3, 125, 'Âl-i İmrân', 'Medeni', 'İnananlara: \"Rabbinizin size gönderilmiş üç bin melekle yardım etmesi size yetmeyecek mi?\" diyordun. Evet, eğer sabrederseniz, sakınırsanız ve onlar de hemen üzerinize gelirlerse Rabbiniz size, nişanlı beş bin melekle imdat edecektir.'),
(419, 419, 3, 126, 'Âl-i İmrân', 'Medeni', 'Allah bunu, ancak size müjde olsun ve böylece kalbleriniz yatışsın diye yapmıştır. İnkar edenlerin bir kısmını kesmek veya ümidsiz olarak geri dönecek şekilde bozguna uğratmak için gereken yardım, ancak Güçlü ve Hakim olan Allah katından olur.'),
(420, 420, 3, 127, 'Âl-i İmrân', 'Medeni', 'Allah bunu, ancak size müjde olsun ve böylece kalbleriniz yatışsın diye yapmıştır. İnkar edenlerin bir kısmını kesmek veya ümidsiz olarak geri dönecek şekilde bozguna uğratmak için gereken yardım, ancak Güçlü ve Hakim olan Allah katından olur.'),
(421, 421, 3, 128, 'Âl-i İmrân', 'Medeni', 'Allah\'ın, onların tevbelerini kabul veya onlara azab etmesi işiyle senin bir ilişiğin yoktur; çünkü onlar zalimlerdir.'),
(422, 422, 3, 129, 'Âl-i İmrân', 'Medeni', 'Göklerde olanlar da, yerde olanlar da Allah\'ındır. Dilediğini bağışlar, dilediğine azab eder. Allah bağışlayandır, merhamet edendir.'),
(423, 423, 3, 130, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! Faizi kat kat alarak yemeyin. Allah\'tan sakının ki başarıya erişesiniz.'),
(424, 424, 3, 131, 'Âl-i İmrân', 'Medeni', 'İnkar edenler için hazırlanmış ateşten sakının.'),
(425, 425, 3, 132, 'Âl-i İmrân', 'Medeni', 'Size merhamet edilmesi için, Allah\'a ve Peygamber\'e itaat edin.'),
(426, 426, 3, 133, 'Âl-i İmrân', 'Medeni', 'Rabbinizin mağfiretine ve Allah\'a karşı gelmekten sakınanlar için hazırlanmış eni gökler ve yer kadar olan cennete koşuşun.'),
(427, 427, 3, 134, 'Âl-i İmrân', 'Medeni', 'Onlar bollukta ve darlıkta sarfederler, öfkelerini yenerler, insanların kusurlarını affederler. Allah iyilik yapanları sever.'),
(428, 428, 3, 135, 'Âl-i İmrân', 'Medeni', 'Onlar fena bir şey yaptıklarında veya kendilerine zulmettiklerinde Allah\'ı anarlar, günahlarının bağışlanmasını dilerler. Günahları Allah\'tan başka bağışlayan kim vardır? Onlar, yaptıklarında bile bile direnmezler.'),
(429, 429, 3, 136, 'Âl-i İmrân', 'Medeni', 'Onların hareketlerinin karşılığı Rablerinden bağışlanma ve altlarından ırmaklar akan, içinde temelli kalacakları cennetlerdir. İyi davrananların ne güzel ecri vardır!'),
(430, 430, 3, 137, 'Âl-i İmrân', 'Medeni', 'Sizden önce neler gelip geçmiştir. Yeryüzünde gezin de, yalancıların sonunun ne olduğuna bir bakın.'),
(431, 431, 3, 138, 'Âl-i İmrân', 'Medeni', 'Bu Kuran, insanlara bir açıklama, sakınanlara yol gösterme ve bir öğüttür.'),
(432, 432, 3, 139, 'Âl-i İmrân', 'Medeni', 'Gevşemeyin, üzülmeyin, inanmışsanız, mutlaka siz en üstünsünüzdür.'),
(433, 433, 3, 140, 'Âl-i İmrân', 'Medeni', 'Eğer siz (Uhud\'da) bir yara almışsanız, (size düşman olan) o topluluk da (Bedir\'de) benzeri bir yara almıştı. Böylece biz, Allah\'ın gerçek müminleri ortaya çıkarması ve içinizden şahitler edinmesi için, bu günleri bazen lehe, bazen de aleyhe döndürüp duruyoruz. Allah, zulmedenleri sevmez.'),
(434, 434, 3, 141, 'Âl-i İmrân', 'Medeni', 'Bir de Allah, böylece iman edenleri günahlardan arıtmak, inkarcıları ise yok etmek ister.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(435, 435, 3, 142, 'Âl-i İmrân', 'Medeni', 'Yoksa içinizden Allah cihad edenleri ve sabredenleri belirtmeden cennete gireceğinizi mi sanıyordunuz?'),
(436, 436, 3, 143, 'Âl-i İmrân', 'Medeni', 'And olsun ki, ölümle karşılaşmadan önce onu temenni ediyordunuz; işte onu gözlerinizle bakarak gördünüz.'),
(437, 437, 3, 144, 'Âl-i İmrân', 'Medeni', 'Muhammed ancak bir peygamberdir. Ondan önce de peygamberler geçmişti. Ölür veya öldürülürse geriye mi döneceksiniz? Geriye dönen, Allah\'a hiçbir zarar vermez. Allah şükredenlerin mükafatını verecektir.'),
(438, 438, 3, 145, 'Âl-i İmrân', 'Medeni', 'Hiçbir kimse Allah\'ın izni olmadan ölmez; o, belli bir vakte bağlanmıştır. Kim dünya nimetini isterse ona ondan veririz; ve kim ahiret nimetini isterse ona ondan veririz. Şükredenlerin mükafatını vereceğiz.'),
(439, 439, 3, 146, 'Âl-i İmrân', 'Medeni', 'Nice peygamberlerin yanında Rabbe kul olmuş pek çok kimse savaşmıştır. Allah yolunda başlarına gelenlerden ötürü gevşememişler, yılmamışlar ve boyun eğmemişlerdi. Allah, sabredenleri sever.'),
(440, 440, 3, 147, 'Âl-i İmrân', 'Medeni', 'Dedikleri ancak şu idi: \"Rabbimiz! Günahlarımızı, işimizdeki aşırılıklarımızı bize bağışla, sebatımızı arttır, inkarcı topluluğa karşı bize yardım et\".'),
(441, 441, 3, 148, 'Âl-i İmrân', 'Medeni', 'Bu yüzden Allah onlara dünya nimetini de ahiret nimetini de fazlasiyle verdi. Allah işlerini iyi yapanları sever.'),
(442, 442, 3, 149, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! İnkar edenlere itaat ederseniz, sizi geriye döndürürler de kayba uğrarsınız.'),
(443, 443, 3, 150, 'Âl-i İmrân', 'Medeni', 'Halbuki Mevlanız Allah\'tır. O, yardımcıların en iyisidir.'),
(444, 444, 3, 151, 'Âl-i İmrân', 'Medeni', 'Hakkında hiçbir delil indirmediği şeyi Allah\'a ortak koşmalarından ötürü, inkar edenlerin kalbine korku salacağız. Onların varacağı yer cehennemdir. Zalimlerin durağı ne kötüdür!'),
(445, 445, 3, 152, 'Âl-i İmrân', 'Medeni', 'And olsun ki, Allah, size verdiği sözde durdu. Onun izniyle kafirleri kırıp biçiyordunuz, ama Allah size arzuladığınız zaferi gösterdikten sonra gevşeyip bu hususta çekiştiniz ve isyan ettiniz; sizden kimi dünyayı, kimi ahireti istiyordu; derken denemek için Allah sizi geri çevirip bozguna uğrattı. And olsun ki O, sizi bağışladı. Allah\'ın inananlara nimeti boldur.'),
(446, 446, 3, 153, 'Âl-i İmrân', 'Medeni', 'Peygamber arkanızdan sizi çağırırken, kimseye bakmadan kaçıyordunuz; kaybettiğinize ve başınıza gelene üzülmeyesiniz diye, Allah sizi kederden kedere uğrattı. Allah, işlediklerinizden haberdardır.'),
(447, 447, 3, 154, 'Âl-i İmrân', 'Medeni', 'Kederden sonra, bir takımınızı kendinden geçirecek şekilde size huzur ve emniyet indirdi; oysa bir takımınız da kendi derdlerine düşmüşlerdi. Haksız yere Allah hakkında, cahiliye devrinde olduğu gibi inanıyorlar. \"Bu işte bizim bir fikrimiz var mı?\" diyorlardı; De ki: \"Buyruğun hepsi Allah\'ındır\". Sana açmadıklarını içlerinde gizliyorlar. \"Bu işte bizim fikrimiz alınsaydı, burada öldürülmezdik\" diyorlar. De ki: Evlerinizde olsaydınız, haklarında ölüm yazılı olan kimseler, yine de devrilecekleri yere varırlardı. Bu, Allah\'ın içinizde olanı denemesi, kalblerinizde olanı arıtması içindir. Allah gönüllerde olanı bilir.'),
(448, 448, 3, 155, 'Âl-i İmrân', 'Medeni', 'İki topluluğun karşılaştığı gün, içinizden yüz çevirenlerin, yaptıklarının bir kısmından ötürü şeytan ayaklarını kaydırıp yoldan çıkarmak istemişti. Allah, and olsun ki, onları affetti. Allah bağışlayandır. Halim\'dir.'),
(449, 449, 3, 156, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! Yolculuğa çıkan veya savaşa giden kardeşleri hakkında: \"Onlar yanımızda olsalardı ölmezler ve öldürülmezlerdi\" diyen inkarcılar gibi olmayın ki, Allah bunu onların kalblerinde bir hasret olarak bıraksın. Dirilten de öldüren de Allah\'tır. Allah işlediklerinizi görür.'),
(450, 450, 3, 157, 'Âl-i İmrân', 'Medeni', 'Allah yolunda öldürülür veya ölürseniz, size Allah\'tan onların topladıklarından hayırlı bir mağfiret ve rahmet vardır.'),
(451, 451, 3, 158, 'Âl-i İmrân', 'Medeni', 'And olsun ki, ölseniz de, öldürülseniz de Allah katında toplanacaksınız.'),
(452, 452, 3, 159, 'Âl-i İmrân', 'Medeni', 'Allah\'ın rahmetinden dolayı, sen onlara karşı yumuşak davrandın. Eğer kaba ve katı kalbli olsaydın, şüphesiz etrafından dağılır giderlerdi. Onları affet, onlara mağfiret dile, iş hakkında onlara danış, fakat karar verdin mi Allah\'a güven, doğrusu Allah güvenenleri sever.'),
(453, 453, 3, 160, 'Âl-i İmrân', 'Medeni', 'Allah size yardım ederse, sizi yenecek yoktur; eğer sizi yardımsız bırakıverirse, O\'ndan başka size yardım edecek kimdir? İnananlar yalnız Allah\'a güvensinler.'),
(454, 454, 3, 161, 'Âl-i İmrân', 'Medeni', 'Hiçbir peygambere ganimete ve millet malına hiyanet yaraşmaz; haksızlık kim yaparsa, kıyamet günü yaptığı ile gelir, sonra, haksızlık yapılmaksızın herkese kazanmış olduğu ödenir.'),
(455, 455, 3, 162, 'Âl-i İmrân', 'Medeni', 'Allah\'ın rızasına uyan kimse, Allah\'ın hışmına uğrayan gibi midir? Bu kimsenin varacağı yer cehennemdir; o ne kötü varılacak yerdir!'),
(456, 456, 3, 163, 'Âl-i İmrân', 'Medeni', 'Onlar Allah katında derece derecedir. Allah, işlediklerini görmektedir.'),
(457, 457, 3, 164, 'Âl-i İmrân', 'Medeni', 'And olsun ki Allah, inananlara, ayetlerini okuyan, onları arıtan, onlara Kitap ve hikmeti öğreten, kendilerinden bir peygamber göndermekle iyilikte bulunmuştur. Halbuki onlar, önceleri apaçık sapıklıkta idiler.'),
(458, 458, 3, 165, 'Âl-i İmrân', 'Medeni', 'Başkalarını iki misline uğrattığınız bir musibete kendiniz uğrayınca mı: \"Bu nereden?\" dersiniz? De ki: \"O, kendi tarafınızdandır\". Doğrusu Allah her şeye Kadir\'dir.'),
(459, 459, 3, 166, 'Âl-i İmrân', 'Medeni', 'İki topluluğun karşılaştığı günde başınıza gelen, Allah\'ın izniyledir. Bu, inananları da, münafıklık edenleri de belirtmesi içindir. Münafıklık edenlere: \"gelin, Allah yolunda savaşın, veya hiç olmazsa savunmada bulunun\" dendiği zaman: \"Eğer savaşmayı bilseydik, ardınızdan gelirdik\" dediler. O gün, onlar imandan çok inkara yakındılar. Kalblerinde olmayanı ağızlarıyla söylüyorlar. Allah gizlediklerini onlardan iyi bilir.'),
(460, 460, 3, 167, 'Âl-i İmrân', 'Medeni', 'İki topluluğun karşılaştığı günde başınıza gelen, Allah\'ın izniyledir. Bu, inananları da, münafıklık edenleri de belirtmesi içindir. Münafıklık edenlere: \"gelin, Allah yolunda savaşın, veya hiç olmazsa savunmada bulunun\" dendiği zaman: \"Eğer savaşmayı bilseydik, ardınızdan gelirdik\" dediler. O gün, onlar imandan çok inkara yakındılar. Kalblerinde olmayanı ağızlarıyla söylüyorlar. Allah gizlediklerini onlardan iyi bilir.'),
(461, 461, 3, 168, 'Âl-i İmrân', 'Medeni', 'Onlar oturup, kardeşleri için: \"Bize itaat etselerdi öldürülmezlerdi\" dediler. De ki: \"Eğer doğru sözlü iseniz, ölümü kendinizden savın\".'),
(462, 462, 3, 169, 'Âl-i İmrân', 'Medeni', 'Allah yolunda öldürülenleri ölü saymayın, bilakis Rableri katında diridirler. Allah\'ın bol nimetinden onlara verdiği şeylerle sevinç içinde rızıklanırlar, arkalarından kendilerine ulaşamayan kimselere, kendilerine korku olmadığını ve kendilerinin üzülmeyeceklerini müjde etmek isterler.'),
(463, 463, 3, 170, 'Âl-i İmrân', 'Medeni', 'Allah yolunda öldürülenleri ölü saymayın, bilakis Rableri katında diridirler. Allah\'ın bol nimetinden onlara verdiği şeylerle sevinç içinde rızıklanırlar, arkalarından kendilerine ulaşamayan kimselere, kendilerine korku olmadığını ve kendilerinin üzülmeyeceklerini müjde etmek isterler.'),
(464, 464, 3, 171, 'Âl-i İmrân', 'Medeni', 'Onlar Allah\'tan olan bir nimeti, bolluğu ve Allah\'ın, müminlerin ecrini zayi etmeyeceğini müjdelemek isterler.'),
(465, 465, 3, 172, 'Âl-i İmrân', 'Medeni', 'Kendileri savaşta yara aldıktan sonra Allah ve Peygamberin çağrısına koşanlara, hele onlardan iyilik edip sakınanlara büyük ecir vardır.'),
(466, 466, 3, 173, 'Âl-i İmrân', 'Medeni', 'İnsanlar onlara: \"Düşmanınız olan insanlar size karşı bir ordu topladılar, onlardan korkun\" dediler. Bu, onların imanını artırdı da: \"Allah bize yeter. O ne güzel Vekil\'dir\" dediler.'),
(467, 467, 3, 174, 'Âl-i İmrân', 'Medeni', 'Bu yüzden kendilerine bir fenalık dokunmadan, Allah\'tan nimet ve bollukla geri döndüler; Allah\'ın rızasına uydular. Allah büyük, bol nimet sahibidir.'),
(468, 468, 3, 175, 'Âl-i İmrân', 'Medeni', 'İşte o şeytan ancak kendi dostlarını korkutur, inanmışsanız onlardan korkmayın, Benden korkun.'),
(469, 469, 3, 176, 'Âl-i İmrân', 'Medeni', 'Küfürde yarışanlar seni üzmesin; şüphesiz onlar Allah\'a bir zarar veremezler. Allah ahirette onlara bir pay vermemek istiyor; onlara büyük azab vardır.'),
(470, 470, 3, 177, 'Âl-i İmrân', 'Medeni', 'İmanı inkar edenler, kendilerine vermiş olduğumuz mühletin sakın kendileri için hayırlı olduğunu sanmasınlar.'),
(471, 471, 3, 178, 'Âl-i İmrân', 'Medeni', 'Biz onlara ancak, günahları çoğalsın diye mühlet veriyoruz. Küçültücü azab onlaradır.'),
(472, 472, 3, 179, 'Âl-i İmrân', 'Medeni', 'Allah inananları sizin durumunuzda bırakacak değildir, temizi pisten ayıracaktır. Allah size gaybı bildirecek değildir; fakat Allah peygamberlerinden dilediğini seçip, ona gaybı bildirir. Artık Allah\'a ve peygamberlerine inanın; inanır ve sakınırsanız size büyük ecir vardır.'),
(473, 473, 3, 180, 'Âl-i İmrân', 'Medeni', 'Allah\'ın bol nimetinden verdiklerinde cimrilik edenler, sakın bunun kendileri için hayırlı olduğunu sanmasınlar, bilakis bu onların kötülüğünedir. Cimrilik yaptıkları şey, kıyamet günü boyunlarına dolanacaktır. Göklerin ve yerin mirası Allah\'ındır. Allah işlediklerinizden haberdardır.'),
(474, 474, 3, 181, 'Âl-i İmrân', 'Medeni', 'And olsun ki, Allah: \"Allah fakir; biz zenginiz\" diyenlerin sözünü işitmiştir. Dediklerini ve haksız yere peygamberleri öldürdüklerini elbette yazacağız, \"Yakıcı azabı tadın\" diyeceğiz.'),
(475, 475, 3, 182, 'Âl-i İmrân', 'Medeni', '\"Bu, yaptığınızın karşılığıdır\". Yoksa Allah kullara asla zulmetmez.'),
(476, 476, 3, 183, 'Âl-i İmrân', 'Medeni', '\"Doğrusu, ateşin yiyeceği bir kurban getirmedikçe hiçbir peygambere inanmamak üzere Allah bize ahid verdi\" diyenlere sen, de ki: \"Benden önce peygamberler size belgeler ve dediğiniz şeyi getirdi. Doğru sözlü iseniz niçin onları öldürdünüz?\"'),
(477, 477, 3, 184, 'Âl-i İmrân', 'Medeni', 'Seni yalancı saydılarsa, senden önce belgeler, sahifeler ve aydınlatıcı kitap getiren peygamberler de yalanlanmıştı.'),
(478, 478, 3, 185, 'Âl-i İmrân', 'Medeni', 'Her insan ölümü tadacaktır. Kıyamet günü, ecirleriniz size mutlaka ödenecektir. Ateşten uzaklaştırılıp cennete sokulan kimse artık kurtulmuştur. Dünya hayatı, zaten, sadece aldatıcı bir geçinmeden ibarettir.'),
(479, 479, 3, 186, 'Âl-i İmrân', 'Medeni', 'And olsun ki mallarınız ve canlarınızla sınanacaksınız; hiç şüphesiz, sizden önce Kitap verilenlerden ve Allah\'a eş koşanlardan çok üzücü sözler işiteceksiniz. Sabreder ve Allah\'a karşı gelmekten sakınırsanız bilin ki, bu üzerinde sebat edilecek işlerdendir.'),
(480, 480, 3, 187, 'Âl-i İmrân', 'Medeni', 'Allah, Kitap verilenlerden, onu insanlara açıklayacaksınız ve gizlemeyeceksiniz, diye ahid almıştı. Onlar ise, onu arkalarına atıp az bir değere değiştiler. Alış verişleri ne kötüdür!'),
(481, 481, 3, 188, 'Âl-i İmrân', 'Medeni', 'Ettiklerine sevinen ve yapmadıklarıyla övülmekten hoşlananların, sakın onların azabdan kurtulacaklarını sanma; elem verici azab onlaradır.'),
(482, 482, 3, 189, 'Âl-i İmrân', 'Medeni', 'Göklerin ve yerin hükümranlığı Allah\'ındır. Allah her şeye Kadir\'dir.'),
(483, 483, 3, 190, 'Âl-i İmrân', 'Medeni', 'Göklerin ve yerin yaratılışında, gece ile gündüzün birbiri ardınca gelmesinde akıl sahiblerine şüphesiz deliller vardır.'),
(484, 484, 3, 191, 'Âl-i İmrân', 'Medeni', 'Onlar ayakta iken, otururken, yan yatarken Allah\'ı anarlar; göklerin ve yerin yaratılışını düşünürler: \"Rabbimiz! Sen bunu boşuna yaratmadın, Sen münezzehsin. Bizi ateşin azabından koru\"'),
(485, 485, 3, 192, 'Âl-i İmrân', 'Medeni', '\"Rabbimiz! Sen ateşe kimi sokarsan, onu şüphesiz rezil etmiş olursun, zulmedenlerin hiç yardımcıları yoktur\".'),
(486, 486, 3, 193, 'Âl-i İmrân', 'Medeni', '\"Rabbimiz! Doğrusu biz Rabbinize inanın diye inanmaya çağıran bir çağırıcıyı işittik de iman ettik. Rabbimiz! Günahlarımızı bize bağışla, kötülüklerimizi ört, canımızi iyilerle beraber al\".'),
(487, 487, 3, 194, 'Âl-i İmrân', 'Medeni', '\"Rabbimiz! Peygamberlerinle vadettiklerini bize ver, kıyamet günü bizi rezil etme. Sen şüphesiz sözünden caymazsın\".'),
(488, 488, 3, 195, 'Âl-i İmrân', 'Medeni', 'Rableri dualarını kabul etti: \"Birbirinizden meydana gelen sizlerden, erkek olsun, kadın olsun, iş yapanın işini boşa çıkarmam. Hicret edenlerin, memleketlerinden çıkarılanların, yolumda ezaya uğratılanların, savaşan ve öldürülenlerin günahlarını elbette örteceğim. And olsun ki, Allah katından bir nimet olarak, onları içlerinden ırmaklar akan cennetlere koyacağım. Nimetin güzeli Allah katındadır\".'),
(489, 489, 3, 196, 'Âl-i İmrân', 'Medeni', 'İnkar edenlerin diyar diyar gezip refah içinde dolaşması sakın seni aldatmasın; az bir faydalanmadan sonra onların varacakları yer cehennemdir. O ne kötü duraktır!..'),
(490, 490, 3, 197, 'Âl-i İmrân', 'Medeni', 'İnkar edenlerin diyar diyar gezip refah içinde dolaşması sakın seni aldatmasın; az bir faydalanmadan sonra onların varacakları yer cehennemdir. O ne kötü duraktır!..'),
(491, 491, 3, 198, 'Âl-i İmrân', 'Medeni', 'Fakat Rablerinden sakınanlara, Allah katından konukluklar bulunan, içlerinden ırmaklar akan, içinde temelli kalacakları cennetler vardır. Allah katındaki şeyler, iyi olanlar için daha hayırlıdır.'),
(492, 492, 3, 199, 'Âl-i İmrân', 'Medeni', 'Kitap ehlinden Allah\'a huşu duyarak inanıp, Allah\'ın ayetlerini az bir değere değişmeyenler vardır. İşte onların ecirleri Rablerinin katındadır. Şüphesiz Allah\'ın hesabı çabuktur.'),
(493, 493, 3, 200, 'Âl-i İmrân', 'Medeni', 'Ey İnananlar! Sabredin, düşmanlarınızdan daha sabırlı olun, cihada hazır bulunun, Allah\'a karşı gelmekten sakının ki başarıya erişebilesiniz.'),
(494, 494, 4, 1, 'Nisâ', 'Medeni', 'Ey İnsanlar! Sizi bir tek nefisten yaratan, ondan eşini var eden ve ikisinden pek çok erkek ve kadın meydana getiren Rabb\'inize hürmetsizlikten sakının. Kendisi adına birbirinizden dilekte bulunduğunuz Allah\'ın ve akrabanın haklarına riayetsizliktende sakının. Allah şüphesiz hepinizi görüp gözetmektedir.'),
(495, 495, 4, 2, 'Nisâ', 'Medeni', 'Yetimlere mallarını verin. Temizi murdara değişmeyin, onların mallariyle kendi mallarınızı karıştırarak yemeyin, çünkü bu büyük bir suçtur.'),
(496, 496, 4, 3, 'Nisâ', 'Medeni', 'Eğer, velisi olduğunuz mal sahibi yetim kızlarla evlenmekle onlara haksızlık yapmaktan korkarsanız onlarla değil, hoşunuza giden başka kadınlarla iki, üç ve dörde kadar evlenebilirsiniz; şayet, aralarında adaletsizlik yapmaktan korkarsanız bir tane almalısınız veya sahip olduğunuz ile yetinmelisiniz. Doğru yoldan sapmamanız için en uygunu budur.'),
(497, 497, 4, 4, 'Nisâ', 'Medeni', 'Kadınlara mehirlerini cömertçe verin, eğer ondan gönül hoşluğu ile size bir şey bağışlarlarsa onu afiyetle yiyin.'),
(498, 498, 4, 5, 'Nisâ', 'Medeni', 'Allah\'ın sizi koruyucu kılmış olduğu mallarınızı, beyinsizlere vermeyin, kendilerini bunların geliriyle rızıklandırıp giydirin ve onlara güzel söz söyleyin.'),
(499, 499, 4, 6, 'Nisâ', 'Medeni', 'Yetimleri, evlenme çağına gelene kadar deneyin; onlarda olgunlaşma görürseniz mallarını kendilerine verin; büyüyecekler de geri alacaklar diye onları israf ederek ve tez elden yemeyin. Zengin olan, iffetli olmağa çalışsın, yoksul olan uygun bir şekilde yesin. Mallarını kendilerine verdiğiniz zaman, yanlarında şahid bulundurun. Hesap sormak için Allah yeter.'),
(500, 500, 4, 7, 'Nisâ', 'Medeni', 'Ana babanın ve yakınların bıraktıklarından, erkeklere hisse vardır. Ana babanın ve yakınların bıraktıklarından kadınlara da hisse vardır. Bunlar, az veya çok, belirli bir hissedir.'),
(501, 501, 4, 8, 'Nisâ', 'Medeni', 'Taksimde, yakınlar, yetimler ve düşkünler bulunursa, ondan onlara da verin, güzel sözler söyleyin.'),
(502, 502, 4, 9, 'Nisâ', 'Medeni', 'Arkalarında cılız çocuklar bıraktıkları takdirde, bundan endişe edecek olanlar, haksızlık yapmaktan korksunlar; dürüst söz söylesinler.'),
(503, 503, 4, 10, 'Nisâ', 'Medeni', 'Yetimlerin mallarını haksız yere yiyenler, karınlarına ancak ateş tıkınmış olurlar, zaten onlar çılgın aleve atılacaklardır.'),
(504, 504, 4, 11, 'Nisâ', 'Medeni', 'Allah çocuklarınız hakkında, erkeğe iki dişinin hissesi kadar tavsiye eder. Eğer kadınlar ikinin üstünde ise, bırakılanın üçte ikisi onlarındır; şayet bir ise yarısı onundur. Ana babadan her birine, ölenin çocuğu varsa yaptığı vasiyetten veya borcundan arta kalanın altıda biri, çocuğu yoksa, anası babası ona varis olur, anasına üçte bir düşer. Kardeşleri varsa, altıda biri annesinindir; babalarınız ve oğullarınızdan menfaatçe hangisinin size daha yakın olduğunu siz bilmezsiniz. Bunlar Allah tarafından tesbit edilmiştir. Doğrusu Allah bilendir, Hakim olandır.'),
(505, 505, 4, 12, 'Nisâ', 'Medeni', 'Kadınlarınızın çocukları yoksa bıraktıklarının yarısı sizindir, çocukları varsa, bıraktıklarının ettikleri vasiyetten veya borçtan arta kalanın dörtte biri sizindir. Sizin çocuğunuz yoksa ettiğiniz vasiyet veya borç çıktıktan sonra bıraktıklarınızın dörtte biri karılarınızındır; çocuğunuz varsa, bıraktıklarınızın sekizde biri onlarındır. Eğer bir erkek veya kadına kelale yollu (çocuğu ve babası olmadığı halde) varis olunuyor ve bunların ana-bir erkek veya bir kız kardeşi bulunuyorsa, her birine edilen vasiyetten veya borçtan arta kalanın altıda biri düşer; ikiden çoksalar, üçte birine, zarara uğratılmaksızın ortak olurlar. Bunlar Allah tarafından tavsiye edilmiştir. Allah bilendir. Halim\'dir.'),
(506, 506, 4, 13, 'Nisâ', 'Medeni', 'Bunlar Allah\'ın yasalarıdır. Allah\'a ve Peygamberine kim itaat ederse onu içlerinden ırmaklar akan cennetlere koyacaktır, orada temellidirler, büyük kurtuluş budur.'),
(507, 507, 4, 14, 'Nisâ', 'Medeni', 'Kim Allah\'a ve Peygamberine baş kaldırır ve yasalarını aşarsa, onu, temelli kalacağı cehenneme sokar. Alçaltıcı azab onadır.'),
(508, 508, 4, 15, 'Nisâ', 'Medeni', 'Kadınlarınızdan zina edenlere, bunu isbat edecek aranızdan dört şahid getirin, şehadet ederlerse, ölünceye veya Allah onlara bir yol açana kadar evlerde tutun.'),
(509, 509, 4, 16, 'Nisâ', 'Medeni', 'İçinizden zina eden iki kimseye eziyet edin, tevbe edip düzeltirlerse onları bırakın. Doğrusu Allah tevbeleri daima kabul ve merhamet eder.'),
(510, 510, 4, 17, 'Nisâ', 'Medeni', 'Allah kötülüğü bilmeyerek yapıp da, hemen tevbe edenlerin tevbesini kabul etmeyi üzerine almıştır. Allah işte onların tevbesini kabul eder. Allah Bilen\'dir, Hakim olandır.'),
(511, 511, 4, 18, 'Nisâ', 'Medeni', 'Kötülükleri işleyip dururken, ölüm kendisine geldiği zaman; \"şimdi tevbe ettim\" diyenler ile kafir olarak ölenlerin tevbesi makbul değildir. İşte onlara elem verici azab hazırlamışızdır.'),
(512, 512, 4, 19, 'Nisâ', 'Medeni', 'Ey İnananlar! Kadınlara zorla mirasçı olmaya kalkmanız size helal değildir. Apaçık hayasızlık etmedikçe onlara verdiğinizin bir kısmını alıp götürmeniz için onları sıkıştırmayın. Onlarla güzellikle geçinin. Eğer onlardan hoşlanmıyorsanız, sabredin, hoşlanmadığınız bir şeyi Allah çok hayırlı kılmış olabilir.'),
(513, 513, 4, 20, 'Nisâ', 'Medeni', 'Bir eşin yerine başka bir eşi almak isterseniz, birincisine bir yük altın vermiş olsanız bile ondan bir şey almayın. İftira ederek ve apaçık günaha girerek ona verdiğinizi geri alır mısınız?'),
(514, 514, 4, 21, 'Nisâ', 'Medeni', 'Nasıl alırsınız ki siz birbirinize katılmıştınız ve onlar sizden sağlam teminat almışlardı.'),
(515, 515, 4, 22, 'Nisâ', 'Medeni', 'Babalarınızın evlendikleri kadınlarla evlenmeyin, geçmişte olanlar artık geçmiştir çünkü bu bir fuhuş ve igrenç bir şeydi, ne kötü yoldu!'),
(516, 516, 4, 23, 'Nisâ', 'Medeni', 'Sizlere, analarınız, kızlarınız, kızkardeşleriniz, halalarınız, teyzeleriniz, kardeşlerinizin kızları, kızkardeşlerinizin kızları, sizi emziren süt anneleriniz, süt kardeşleriniz, karılarınızın anneleri, kendileriyle gerdeğe girdiğiniz kadınlarınızin yanınızda kalan üvey kızlarınız ki onlarla gerdeğe girmemişseniz size bir engel yoktur, öz oğullarınızın eşleri ve iki kız kardeşi bir arada almak suretiyle evlenmek, -geçmişte olanlar artık geçmiştir- size haram kılındı. Doğrusu Allah bağışlar ve merhamet eder.'),
(517, 517, 4, 24, 'Nisâ', 'Medeni', 'Evli kadınlarla evlenmeniz de haram kılındı. Maliki bulunduğunuz cariyeler müstesna, bunlar, Allah\'ın üzerinize farz kıldığı hükümlerdir. Bunlardan başkasını, zinadan kaçınıp, iffetli olarak, mallarınızla istemeniz size helal kılındı. Onlardan faydalandığınıza mukabil, kararlaştırılmış olan mehirlerini verin; kararlaştırılandan başka, karşılıklı hoşnud olduğunuz hususda size bir sorumluluk yoktur. Allah Bilen\'dir, Hakim\'dir.'),
(518, 518, 4, 25, 'Nisâ', 'Medeni', 'Sizden, hür mümin kadınlarla evlenmeye güç yetiremiyen kimse, ellerinizdeki mümin cariyelerinizden alsın. Allah sizin imanınızı çok iyi bilir. Birbirinizdensiniz, aynı soydansınız. Onlarla, zinadan kaçınmaları, iffetli olmaları ve gizli dost tutmamış olmaları halinde, velilerinin izniyle evlenin ve örfe uygun bir şekilde mehirlerini verin. Evlendiklerinde zina edecek olurlarsa, onlara, hür kadınlara edilen azabın yarısı edilir. Cariye ile evlenmedeki bu izin içinizden, günaha girme korkusu olanlaradır. Sabretmeniz sizin için daha hayırlıdır. Allah bağışlar ve merhamet eder.'),
(519, 519, 4, 26, 'Nisâ', 'Medeni', 'Allah size açıklamak ve sizden öncekilerin yollarını göstermek ve tevbenizi kabul etmek ister. Allah Bilen\'dir, Hakim\'dir.'),
(520, 520, 4, 27, 'Nisâ', 'Medeni', 'Allah sizin tevbenizi kabul etmek ister, şehvetlerine uyanlar ise sizin büyük bir sapıklığa girmenizi isterler.'),
(521, 521, 4, 28, 'Nisâ', 'Medeni', 'İnsan zayıf yaratılmış olduğundan Allah sizden yükü hafifletmek ister.'),
(522, 522, 4, 29, 'Nisâ', 'Medeni', 'Ey İnananlar! Mallarınızı aranızda haksızlıkla değil, karşılıklı rıza ile yapılan ticaretle yeyin, haram ile nefsinizi mahvetmeyin. Allah şüphesiz ki size merhamet eder.'),
(523, 523, 4, 30, 'Nisâ', 'Medeni', 'Bunu kim aşırı giderek haksızlıkla yaparsa, onu ateşe sokacağız. Bu, Allah\'a kolaydır.'),
(524, 524, 4, 31, 'Nisâ', 'Medeni', 'Size yasak edilen büyük günahlardan kaçınırsanız, kusurlarınızı örter ve sizi şerefli bir yere yerleştiririz.'),
(525, 525, 4, 32, 'Nisâ', 'Medeni', 'Allah\'ın sizi birbirinizden üstün kıldığı şeyleri özlemeyin. Erkeklere, kazandıklarından bir pay, kadınlara da kazandıklarından bir pay vardır. Allah\'tan bol nimet isteyin. Doğrusu Allah her şeyi bilir.'),
(526, 526, 4, 33, 'Nisâ', 'Medeni', 'Ana babanın ve yakınların bıraktıklarından her birine varisler kıldık. Kendileriyle yeminleştiğiniz kimselere hisselerini veriniz. Doğrusu Allah her şeye şahiddir.'),
(527, 527, 4, 34, 'Nisâ', 'Medeni', 'Allah\'ın kimini kimine üstün kılmasından ötürü ve erkeklerin, mallarından sarfetmelerinden dolayı erkekler kadınlar üzerine hakimdirler. İyi kadınlar, gönülden boyun eğenler ve Allah\'ın korunmasını emrettiğini, kocasının bulunmadığı zaman da koruyanlardır. Serkeşlik etmelerinden endişelendiğiniz kadınlara öğüt verin, yataklarında onları yalnız bırakın, nihayet dövün. Size itaat ediyorlarsa aleyhlerine yol aramayın. Doğrusu Allah Yüce\'dir, Büyük\'tür.'),
(528, 528, 4, 35, 'Nisâ', 'Medeni', 'Karı kocanın arasının açılmasından endişelenirseniz, erkeğin ailesinden bir hakem ve kadının ailesinden bir hakem gönderin; bunlar düzeltmek isterlerse, Allah onların aralarını buldurur. Doğrusu Allah her şeyi Bilen ve haberdar olandır.'),
(529, 529, 4, 36, 'Nisâ', 'Medeni', 'Allah\'a kulluk edin, O\'na bir şeyi ortak koşmayın. Ana babaya, yakınlara, yetimlere, düşkünlere, yakın komşuya, uzak komşuya, yanınızdaki arkadaşa, yolcuya ve elinizin altında bulunan kimselere iyilik edin. Allah, kendini beğenip öğünenleri elbette sevmez.'),
(530, 530, 4, 37, 'Nisâ', 'Medeni', 'Onlar cimrilik ederler, insanlara cimrilik tavsiyesinde bulunurlar, Allah\'ın bol nimetinden kendilerine verdiğini gizlerler. Kafirlere aşağılık bir azab hazırlamışızdır.'),
(531, 531, 4, 38, 'Nisâ', 'Medeni', 'Mallarını insanlara gösteriş için sarfedip, Allah\'a ve ahiret gününe inanmayanları da Allah sevmez. Şeytanın arkadaş olduğu kimsenin ne fena arkadaşı vardır!'),
(532, 532, 4, 39, 'Nisâ', 'Medeni', 'Bunlar Allah\'a, ahiret gününe inanmış, Allah\'ın verdiği rızıklardan sarfetmiş olsalardı ne zararı olurdu? Oysa Allah onları bilir.'),
(533, 533, 4, 40, 'Nisâ', 'Medeni', 'Allah şüphesiz zerre kadar haksızlık yapmaz, zerre kadar iyilik olsa onu kat kat arttırır ve yapana büyük ecir verir.'),
(534, 534, 4, 41, 'Nisâ', 'Medeni', 'Her ümmete bir şahid getirdiğimiz ve seni de bunlara şahid getirdiğimiz vakit durumları nasıl olacak?'),
(535, 535, 4, 42, 'Nisâ', 'Medeni', 'O gün, inkar edip Peygambere baş kaldırmış olanlar, yerle bir olmayı ne kadar isterler ve Allah\'tan bir söz gizleyemezler.'),
(536, 536, 4, 43, 'Nisâ', 'Medeni', 'Ey İnananlar! Sarhoşken, ne dediğinizi bilene kadar, cünübken, yolcu olan müstesna gusledene kadar namaza yaklaşmayın. Eğer hasta veya yolculukta iseniz yahut biriniz ayak yolundan gelmişseniz veya kadınlara yaklaşmışsanız ve bu durumlarda su bulamamışsanız tertemiz bir toprağa teyemmüm edin, yüzlerinize ve ellerinize sürün. Allah affeder ve bağışlar.'),
(537, 537, 4, 44, 'Nisâ', 'Medeni', 'Kendilerine Kitap\'dan bir pay verilenlerin sapıklığı satın aldıklarını ve sizin yolu sapıtmanızı istediklerini görmüyor musun?'),
(538, 538, 4, 45, 'Nisâ', 'Medeni', 'Allah, düşmanlarınızı çok iyi bilir. Allah size dost olarak da yeter, yardımcı olarak da yeter.'),
(539, 539, 4, 46, 'Nisâ', 'Medeni', 'Yahudilerden, sözleri yerlerinden değiştirip: \"İşittik ve karşı geldik, kulak vermeyerek dinle\" ve dillerini eğip bükerek ve dini yererek: \"Bizi de dinle\" diyenler vardır. Şayet: \"İşittik ve itaat ettik, dinle ve bizi gözet\" demiş olsalardı, onlar için daha iyi daha doğru olurdu. İşte Allah inkarları yüzünden onlara lanet etmiştir. Onların ancak pek azı inanır.'),
(540, 540, 4, 47, 'Nisâ', 'Medeni', 'Ey Kitap verilenler! Yüzleri silip arkaya çevirerek enseler gibi dümdüz yapmadan, yahut cumartesi güncüleri lanetlediğimiz gibi lanetlemeden önce, yanınızdakini tasdik ederek indirdiğimiz Kuran\'a inanın; Allah\'ın emri daima yapılagelmiştir.'),
(541, 541, 4, 48, 'Nisâ', 'Medeni', 'Allah kendisine ortak koşmayı elbette bağışlamaz, bundan başkasını dilediğine bağışlar. Allah\'a ortak koşan kimse, şüphesiz büyük bir günahla iftira etmiş olur.'),
(542, 542, 4, 49, 'Nisâ', 'Medeni', 'Kendilerini temize çıkaranları görmedin mi? Allah dilediğini temize çıkarır ve kendilerine kıl kadar haksızlık yapmaz.'),
(543, 543, 4, 50, 'Nisâ', 'Medeni', 'Allah\'a nasıl yalan yere iftira ettiklerine bir bak. Bu, apaçık bir günah olarak yeter.'),
(544, 544, 4, 51, 'Nisâ', 'Medeni', 'Kendilerine kitap verilmiş olanların, puta ve şeytana kanıp, inkar edenlere: \"Bunlar, inananlardan daha doğru yoldadırlar\" dediklerini görmedin mi?'),
(545, 545, 4, 52, 'Nisâ', 'Medeni', 'İşte, Allah\'ın lanetledikleri onlardır. Allah\'ın lanetlediği kişiye asla yardımcı bulamayacaksın.'),
(546, 546, 4, 53, 'Nisâ', 'Medeni', 'Yoksa onların hükümranlıktan bir payı mı var? O zaman insanlara bir çekirdek parçası bile vermezler.'),
(547, 547, 4, 54, 'Nisâ', 'Medeni', 'Yoksa Allah\'ın bol nimetinden verdiği kimseleri mi çekemiyorlar? Oysa İbrahim ailesine kitap ve hikmet verdik, onlara büyük hükümranlık bahşettik.'),
(548, 548, 4, 55, 'Nisâ', 'Medeni', 'Onlardan ona inananlar ve yüz çevirenler vardı. Çılgın bir alev olarak cehennem yeter. Doğrusu, ayetlerimizi inkar edenleri ateşe sokacağız; derilerinin her yanışında, azabı tatmaları için onları başka derilerle değiştireceğiz. Allah güçlüdür, Hakim\'dir.'),
(549, 549, 4, 56, 'Nisâ', 'Medeni', 'Onlardan ona inananlar ve yüz çevirenler vardı. Çılgın bir alev olarak cehennem yeter. Doğrusu, ayetlerimizi inkar edenleri ateşe sokacağız; derilerinin her yanışında, azabı tatmaları için onları başka derilerle değiştireceğiz. Allah güçlüdür, Hakim\'dir.'),
(550, 550, 4, 57, 'Nisâ', 'Medeni', 'İnanıp yararlı iş işleyenleri içinde temelli ve ebedi kalacakları, içlerinden ırmaklar akan cennetlere koyacağız. Onlara orada tertemiz eşler vardır. Onları en koyu gölgeliklere yerleştireceğiz.'),
(551, 551, 4, 58, 'Nisâ', 'Medeni', 'Hiç şüphesiz Allah size, emanetleri ehline teslim etmenizi ve insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi emreder. Allah size ne güzel öğüt veriyor. Şüphesiz Allah işitir ve görür.'),
(552, 552, 4, 59, 'Nisâ', 'Medeni', 'Ey İnananlar! Allah\'a itaat edin, Peygambere ve sizden buyruk sahibi olanlara itaat edin. Eğer bir şeyde çekişirseniz, Allah\'a ve ahiret gününe inanmışsanız onun halini Allah\'a ve Peygambere bırakın. Bu, hayırlı ve netice itibariyle en güzeldir.'),
(553, 553, 4, 60, 'Nisâ', 'Medeni', 'Sana indirilen Kuran\'a ve senden önce indirilenlere inandıklarını iddia edenleri görmüyor musun? Putlarının önünde muhakeme olunmalarını isterler. Oysa, onları tanımamakla emr olunmuşlardı. Şeytan onları derin bir sapıklığa saptırmak ister.'),
(554, 554, 4, 61, 'Nisâ', 'Medeni', 'Onlara: \"Allah\'ın indirdiğine ve Peygambere gelin\" dendiği zaman, münafıkların senden büsbütün uzaklaştıklarını görürsün.'),
(555, 555, 4, 62, 'Nisâ', 'Medeni', 'Başlarına kendi işlediklerinden ötürü bir musibet çattığında sana gelip: \"Biz, iyilik etmek ve uzlaştırmaktan başka bir şey istemedik\" diye de nasıl Allah\'a yemin ederler?'),
(556, 556, 4, 63, 'Nisâ', 'Medeni', 'İşte bunlarin kalblerinde olanı Allah bilir. Onlardan yüz çevir, onlara öğüt ver, kendilerine tesirli sözler söyle.'),
(557, 557, 4, 64, 'Nisâ', 'Medeni', 'Biz her peygamberi ancak, Allah\'ın izniyle, itaat olunması için gönderdik. Onlar, kendilerine yazık ettiklerinde, sana gelip Allah\'tan mağfiret dileseler ve Peygamber de onlara mağfiret dileseydi, Allah\'ın tevbeleri daima kabul ve merhamet eden olduğunu görürlerdi.'),
(558, 558, 4, 65, 'Nisâ', 'Medeni', 'Hayır; Rabb\'ine and olsun ki, aralarında çekiştikleri şeylerde seni hakem tayin edip, sonra senin verdiğin hükmü içlerinde bir sıkıntı duymadan tamamen kabul etmedikçe inanmış olmazlar.'),
(559, 559, 4, 66, 'Nisâ', 'Medeni', 'Şayet onlara \"Kendinizi öldürün\" yahut \"Memleketinizden çıkın\" diye emretmiş olsaydık, pek azından başkaları bunu yapmazlardı. Kendilerine verilen öğüdü yerine getirmiş olsalardı onlar için daha iyi ve daha sağlam olurdu.'),
(560, 560, 4, 67, 'Nisâ', 'Medeni', 'O zaman onlara kendi katımızdan büyük bir ecir verir ve onları doğru yola eriştirirdik.'),
(561, 561, 4, 68, 'Nisâ', 'Medeni', 'O zaman onlara kendi katımızdan büyük bir ecir verir ve onları doğru yola eriştirirdik.'),
(562, 562, 4, 69, 'Nisâ', 'Medeni', 'Kim Allah\'a ve Peygambere itaat ederse, işte onlar Allah\'ın nimetine eriştirdiği peygamberlerle, dosdoğru olanlar, şehidler ve iyilerle beraberdirler. Onlar ne iyi arkadaştırlar!'),
(563, 563, 4, 70, 'Nisâ', 'Medeni', 'Bu nimet, Allah\'tandır. Bilen olarak Allah yeter.'),
(564, 564, 4, 71, 'Nisâ', 'Medeni', 'Ey İnananlar! İhtiyatlı davranın, bölük bölük veya hep birden savaşa gidin.'),
(565, 565, 4, 72, 'Nisâ', 'Medeni', 'Şüphesiz aranızda pek ağır davrananlar vardır; size bir musibet gelirse: \"Allah bana iyilikte bulundu, çünkü onlarla beraber bulunmadim\" der.'),
(566, 566, 4, 73, 'Nisâ', 'Medeni', 'Allah\'tan size bir nimet erişse, and olsun ki, sizinle kendi arasında bir dostluk yokmuş gibi: \"Keşki onlarla beraber olsaydım da ben de büyük bir başarı kazansaydım\" der.'),
(567, 567, 4, 74, 'Nisâ', 'Medeni', 'O halde, dünya hayatı yerine ahireti alanlar, Allah yolunda savaşsınlar. Kim Allah yolunda savaşır, öldürülür veya galib gelirse, Biz ona büyük bir ecir vereceğiz.'),
(568, 568, 4, 75, 'Nisâ', 'Medeni', 'Size ne oluyor da: \"Rabbimiz! Bizi halkı zalim olan bu şehirden çıkar, katından bize bir sahip çıkan gönder, katından bize bir yardımcı lutfet\" diyen zavallı çocuklar, erkekler ve kadınlar uğrunda ve Allah yolunda savaşmıyorsunuz?'),
(569, 569, 4, 76, 'Nisâ', 'Medeni', 'İnananlar Allah yolunda savaşırlar, inkar edenler ise tağut yolunda harbederler. Şeytanın dostlarıyla savaşın, esasen şeytanın hilesi zayıftır.'),
(570, 570, 4, 77, 'Nisâ', 'Medeni', 'Kendilerine: \"Elinizi savaştan çekin, namaz kılın, zekat verin\" denenleri görmedin mi? Onlara savaş farz kılındığında, içlerinden bir takımı hemen, insanlardan, Allah\'tan korkar gibi, hatta daha çok korkarlar ve \"Rabbimiz! Bize savaşı niçin farz kıldın, bizi yakın bir zamana kadar tehir edemez miydin?\" derler. De ki: \"Dünya geçimliği azdır, ahiret, Allah\'a karşı gelmekten sakınan için hayırlıdır, size zerre kadar zulmedilmez\".'),
(571, 571, 4, 78, 'Nisâ', 'Medeni', 'Nerede olursaniz olun, sağlam kaleler içinde bulunsanız bile, ölüm size yetişecektir. Onlara bir iyilik gelirse: \"Bu Allah\'tandır\" derler, bir kötülüğe uğrarlarsa \"Bu, senin tarafındandır\" derler. De ki: \"Hepsi Allah\'tandır\". Bunlara ne oluyor ki, hiçbir sözü anlamaya yanaşmıyorlar?'),
(572, 572, 4, 79, 'Nisâ', 'Medeni', 'Sana ne iyilik gelirse Allah\'tandır, sana ne kötülük dokunursa kendindendir. Seni insanlara peygamber gönderdik, şahid olarak Allah yeter.'),
(573, 573, 4, 80, 'Nisâ', 'Medeni', 'Peygamber\'e itaat eden, Allah\'a itaat etmiş olur. Kim yüz çevirirse bilsin ki, Biz seni onlara bekçi göndermedik.'),
(574, 574, 4, 81, 'Nisâ', 'Medeni', '\"Peki\" derler, fakat senin yanından çıktıklarında, içlerinden bir takımı, geceleyin senin dediklerinden başka bir şey kurarlar. Allah gece tasarladıklarını yazıyor, onlara aldırış etme. Allah\'a güven, vekil olarak Allah yeter.'),
(575, 575, 4, 82, 'Nisâ', 'Medeni', 'Kuran\'ı durup düşünmüyorlar mı? Eğer o Allah\'tan başkasından gelseydi, onda çok aykırılıklar bulurlardı.'),
(576, 576, 4, 83, 'Nisâ', 'Medeni', 'Kendilerine güven veya korku hususunda bir haber geldiğinde onu yayarlar; halbuki o haberi Peygamber\'e veya kendilerinden buyruk sahibi olanlara götürselerdi, onlardan sonuç çıkarmaya kadir olanlar onu bilirdi. Allah\'ın size bol nimeti ve rahmeti olmasaydı, pek azınız bir yana, şeytana uyardınız.'),
(577, 577, 4, 84, 'Nisâ', 'Medeni', 'Allah yolunda savaş; sen ancak kendinden sorumlusun, inananları teşvik et; umulur ki Allah, inkar edenlerin baskınını önler. Allah\'ın kahrı da, ibret alınacak cezası da pek şiddetlidir.'),
(578, 578, 4, 85, 'Nisâ', 'Medeni', 'Kim iyi bir işte aracılık ederse, ona onun sevabından bir pay vardır; kim de kötü bir şeyde aracılık yaparsa, ona o kötülükten bir hisse vardır. Allah, her şeyin karşılığını verir.'),
(579, 579, 4, 86, 'Nisâ', 'Medeni', 'Size bir selam verildiği zaman, ondan daha iyisiyle selam verin veya ayniyle mukabele edin. Allah her şeyin hesabını gereği gibi yapandır.'),
(580, 580, 4, 87, 'Nisâ', 'Medeni', 'Allah\'tan başka tanrı yoktur, geleceğinde şüphe olmayan kıyamet günü, sizi mutlaka toplayacaktır. Allah\'tan daha doğru sözlü kim olabilir?'),
(581, 581, 4, 88, 'Nisâ', 'Medeni', 'Ey müslümanlar! Münafıklar hakkında iki fırka olmanız da niye? Allah onları, yaptıklarından dolayı başaşağı etmiştir. Allah\'ın saptırdığını siz mi yola getirmek istiyorsunuz? Allah\'ın saptırdığı kimseye sen hiç yol bulamayacaksın.'),
(582, 582, 4, 89, 'Nisâ', 'Medeni', 'Onlar kendileri inkar ettikleri gibi, keşki siz de inkar etseniz de eşit olsanız isterler. Allah yolunda hicret etmedikçe onlardan dost edinmeyin. Eğer yüz çevirirlerse onları tutun, bulduğunuz yerde öldürün. Onlardan dost ve yardımcı edinmeyin.'),
(583, 583, 4, 90, 'Nisâ', 'Medeni', 'Ancak, sizinle kendileri arasında anlaşma olan bir millete sığınanlar yahut sizinle savaştan veya kendi milletleriyle savaşmaktan bıkarak size başvuranlar müstesnadır. Allah dileseydi onları üzerinize çullandırırdı da sizinle savaşırlardı. Eğer sizden uzak durur, sizinle savaşmaz, size barış teklif ederlerse Allah onlara dokunmanıza izin vermez.'),
(584, 584, 4, 91, 'Nisâ', 'Medeni', 'Diğerlerinin de sizden ve kendi milletlerinden güvende olmayı istediklerini göreceksiniz. Ne var ki fitneciliğe her çağırıldıklarında ona can atarlar; eğer sizden uzak durmazlar, barış teklif etmezler ve sizden el çekmezlerse onları yakalayın, bulduğunuz yerde öldürün. İşte onların aleyhlerine size apaçık ferman verdik.'),
(585, 585, 4, 92, 'Nisâ', 'Medeni', 'Bir müminin diğer mümini yanlışlık dışında öldürmesi asla caiz değildir. Bir mümini yanlışlıkla öldürenin, bir mümin köleyi azad etmesi ve öldürülenin ailesi bağışlamadıkça, ona diyet ödemesi gerekir. Eğer o mümin, size düşman bir topluluktan ise mümin bir köleyi azad etmek gerekir. Şayet aranızda anlaşma olan bir millettense, ailesine diyet ödemek ve mümin bir köleyi azat etmek gerekir. Bulamayana, Allah tarafından tevbesinin kabulü için, ard arda iki ay oruç tutmak gerekir. Allah bilendir. Hakim\'dir.'),
(586, 586, 4, 93, 'Nisâ', 'Medeni', 'Kim bir mümini kasden öldürürse cezası, içinde temelli kalacağı cehennemdir. Allah ona gazabetmiş, lanetlemiş ve büyük azab hazırlamıştır.'),
(587, 587, 4, 94, 'Nisâ', 'Medeni', 'Ey İnananlar! Allah yolunda yürüdüğünüz vakit, her şeyi iyice anlayın. Size, müslüman olduğunu bildirene, dünya hayatının geçici menfaatine göz dikerek: \"Sen mümin değilsin\" demeyin. Allah katında birçok ganimetler vardır. Evvelce siz de öyleydiniz. Allah size iyilikte bulundu, iyice araştırıp anlayın, Allah işlediklerinizden şüphesiz haberdardır.'),
(588, 588, 4, 95, 'Nisâ', 'Medeni', 'İnananlardan, özürsüz olarak yerlerinde oturanlar ile, mal ve canlariyle cihad edenleri, mertebece, oturanlardan üstün kılmıştır. Allah hepsine de cenneti vadetmiştir, ama Allah, cihad edenleri oturanlara, büyük ecirler, dereceler, mağfiret ve rahmetle üstün kılmıştır. Allah bağışlar ve merhamet eder.'),
(589, 589, 4, 96, 'Nisâ', 'Medeni', 'İnananlardan, özürsüz olarak yerlerinde oturanlar ile, mal ve canlariyle cihad edenleri, mertebece, oturanlardan üstün kılmıştır. Allah hepsine de cenneti vadetmiştir, ama Allah, cihad edenleri oturanlara, büyük ecirler, dereceler, mağfiret ve rahmetle üstün kılmıştır. Allah bağışlar ve merhamet eder.'),
(590, 590, 4, 97, 'Nisâ', 'Medeni', 'Kendilerine yazık edenlerin melekler canlarını aldıkları zaman onlara: \"Ne yaptınız bakalım?\" deyince, \"Biz yeryüzünde zavallı kimselerdik\" diyecekler, melekler de: \"Allah\'ın arzı geniş değil miydi? Hicret etseydiniz ya!\" cevabını verecekler. Onlarınvaracakları yer cehennemdir. Orası ne kötü dönülecek yerdir!'),
(591, 591, 4, 98, 'Nisâ', 'Medeni', 'Çaresiz kalan, yol bulamayan zavallı erkek, kadın ve çocuklar müstesnadırlar.'),
(592, 592, 4, 99, 'Nisâ', 'Medeni', 'İşte Allah\'ın bunları affetmesi umulur. Allah Affedendir, Bağışlayan\'dır.'),
(593, 593, 4, 100, 'Nisâ', 'Medeni', 'Allah yolunda hicret eden kişi, yeryüzünde çok bereketli yer ve genişlik bulur. Evinden, Allah\'a ve Peygamberine hicret ederek çıkan kimseye ölüm gelirse, onun ecrini vermek Allah\'a düşer. Allah bağışlar ve merhamet eder.'),
(594, 594, 4, 101, 'Nisâ', 'Medeni', 'Yolculuk ettiğinizde, kafirlerin size bir fenalık yapmasından korkarsanız, namazı kısaltmanızda size bir sorumluluk yoktur. Zira kafirler, size apaçık düşmandırlar.'),
(595, 595, 4, 102, 'Nisâ', 'Medeni', 'Sen içlerinde olup da namazlarını kıldırdığın zaman, bir kısmı seninle beraber namaza dursun ve silahlarını da yanlarına alsınlar; secdeyi yaptıktan sonra onlar arkanıza geçsinler; kılmayan öbür kısım gelsin, seninle beraber kılsınlar, tedbirli olsunlar, silahlarını alsınlar. Kafirler, size ansızın bir baskın vermek için, silah ve eşyanızdan ayrılmış bulunmanızı dilerler. Yağmurdan zarar görecekseniz veya hasta olursanız, silahlarınızı bırakmanıza engel yoktur, fakat dikkatli olun. Allah kafirlere şüphesiz ağır bir azab hazırlamıştır.'),
(596, 596, 4, 103, 'Nisâ', 'Medeni', 'Namazı kıldıktan başka, Allah\'ı ayakta iken, otururken, yan yatarken de anın. Emniyete kavuştuğunuzda, namazı gereğince kılın. Namaz şüphesiz, inananlara belirli vakitlerde farz kılınmıştır.'),
(597, 597, 4, 104, 'Nisâ', 'Medeni', 'Düşman milleti kovalamakta gevşeklik göstermeyin. Eğer siz acı çekiyorsanız, şüphesiz onlar da sizin çektiğiniz gibi acı çekiyorlar; oysa siz Allah\'tan onların beklemedikleri şeyleri bekliyorsunuz. Allah Bilendir, Hakim olandır.'),
(598, 598, 4, 105, 'Nisâ', 'Medeni', 'Doğrusu, insanlar arasında Allah\'ın sana gösterdiği gibi hükmedesin diye Kitap\'ı sana hak olarak indirdik; hakkı gözet, hainlerden taraf olma.'),
(599, 599, 4, 106, 'Nisâ', 'Medeni', 'Allah\'tan mağfiret dile. Allah bağışlar ve merhamet eder.'),
(600, 600, 4, 107, 'Nisâ', 'Medeni', 'Kendilerine hainlik edenlerden yana uğraşmaya kalkma. Allah, hainlikte direnen suçluyu sevmez.'),
(601, 601, 4, 108, 'Nisâ', 'Medeni', 'Allah\'ın razı olmadığı sözü gece kurarlarken, onu, insanlardan gizliyorlar da kendileriyle beraber olan Allah\'dan gizlemiyorlar. Allah işlediklerinin hepsini bilmektedir.'),
(602, 602, 4, 109, 'Nisâ', 'Medeni', 'İşte siz dünya hayatında onları savunuyorsunuz ama, kıyamet günü onları Allah\'a karşı kim savunacak? Veya onların vekaletini kim üzerine alacaktır?'),
(603, 603, 4, 110, 'Nisâ', 'Medeni', 'Kim kötülük işler veya kendine yazık eder de sonra Allah\'tan bağışlanma dilerse, Allah\'ı mağfiret ve merhamet sahibi olarak bulur.'),
(604, 604, 4, 111, 'Nisâ', 'Medeni', 'Kim günah işlerse bunu ancak kendi aleyhine yapmış olur. Allah bilendir, Hakim\'dir.'),
(605, 605, 4, 112, 'Nisâ', 'Medeni', 'Kim yanılır veya suç işler de sonra onu bir suçsuzun üzerine atarsa, şüphesiz iftira etmiş, apaçık bir günah yüklenmiş olur.'),
(606, 606, 4, 113, 'Nisâ', 'Medeni', 'Eğer sana Allah\'ın bol nimeti ve rahmeti olmasaydı, onlardan bir takımı seni sapıtmaya çalışırdı. Halbuki onlar kendilerinden başkasını saptıramazlar, sana da bir zarar vermezler. Allah sana Kitap ve hikmet indirmiş, sana bilmediğini öğretmiştir. Allah\'ın sana olan nimeti ne büyüktür.'),
(607, 607, 4, 114, 'Nisâ', 'Medeni', 'Ancak sadaka vermeyi yahut iyilik yapmayı ve insanların arasını düzeltmeyi gözeten kimseler müstesna, onların gizli toplantılarının çoğunda hayır yoktur. Bunları, Allah\'ın rızasını kazanmak için yapana büyük ecir vereceğiz.'),
(608, 608, 4, 115, 'Nisâ', 'Medeni', 'Doğru yol kendisine apaçık belli olduktan sonra, Peygamberden ayrılıp, inananların yolundan başkasına uyan kimseyi, döndüğü yöne döndürür ve onu cehenneme sokarız. Orası ne kötü bir dönüş yeridir!'),
(609, 609, 4, 116, 'Nisâ', 'Medeni', 'Allah, kendisine ortak koşulmasını elbette bağışlamaz, bundan başkasını dilediğine bağışlar. Allah\'a ortak koşan kimse derin bir sapıklığa sapmış olur.'),
(610, 610, 4, 117, 'Nisâ', 'Medeni', 'Onlar Allah\'ı bırakıp tanrıçalara taparlar ve: \"Elbette senin kullarından belli bir takımı alıp onları saptıracağım, onlara kuruntu kurduracağım, develerin kulaklarını yarmalarını emredeceğim, Allah\'ın yarattığını değiştirmelerini emredeceğim\" diyen, Allah\'ın lanet ettiği azgın şeytana taparlar. Allah\'ı bırakıp şeytanı dost edinen şüphesiz açıktan açığa kayba uğramıştır.'),
(611, 611, 4, 118, 'Nisâ', 'Medeni', 'Onlar Allah\'ı bırakıp tanrıçalara taparlar ve: \"Elbette senin kullarından belli bir takımı alıp onları saptıracağım, onlara kuruntu kurduracağım, develerin kulaklarını yarmalarını emredeceğim, Allah\'ın yarattığını değiştirmelerini emredeceğim\" diyen, Allah\'ın lanet ettiği azgın şeytana taparlar. Allah\'ı bırakıp şeytanı dost edinen şüphesiz açıktan açığa kayba uğramıştır.'),
(612, 612, 4, 119, 'Nisâ', 'Medeni', 'Onlar Allah\'ı bırakıp tanrıçalara taparlar ve: \"Elbette senin kullarından belli bir takımı alıp onları saptıracağım, onlara kuruntu kurduracağım, develerin kulaklarını yarmalarını emredeceğim, Allah\'ın yarattığını değiştirmelerini emredeceğim\" diyen, Allah\'ın lanet ettiği azgın şeytana taparlar. Allah\'ı bırakıp şeytanı dost edinen şüphesiz açıktan açığa kayba uğramıştır.'),
(613, 613, 4, 120, 'Nisâ', 'Medeni', 'Şeytan onlara vadediyor, onları kuruntulara düşürüyor, ancak aldatmak için vaadde bulunuyor.'),
(614, 614, 4, 121, 'Nisâ', 'Medeni', 'İşte onların varacağı yer cehennemdir. Oradan kaçacak yer de bulamıyacaklardır.'),
(615, 615, 4, 122, 'Nisâ', 'Medeni', 'İnanıp yararlı işler yapanları, Allah\'ın gerçek bir sözü olarak, içinde temelli ve ebedi kalacakları, içinden ırmaklar akan cennetlere koyacağız. Allah\'tan daha doğru sözlü kim vardır?'),
(616, 616, 4, 123, 'Nisâ', 'Medeni', 'Bu, sizin kuruntularınıza ve Kitap ehlinin kuruntularına göre değildir. Kim fenalık yaparsa cezasını görür, kendisine Allah\'tan başka ne dost ve ne de yardımcı bulur.'),
(617, 617, 4, 124, 'Nisâ', 'Medeni', 'Erkek veya kadın, mümin olarak, kim yararlı işler işlerse, işte onlar cennete girerler, kendilerine zerre kadar zulmedilmez.'),
(618, 618, 4, 125, 'Nisâ', 'Medeni', 'İyilik yaparak kendisini Allah\'a teslim edip, hakka yönelen İbrahim\'in dinine uyandan, din bakımından daha iyi kim olabilir? Allah İbrahim\'i dost edinmişti.'),
(619, 619, 4, 126, 'Nisâ', 'Medeni', 'Göklerde olanlar da, yerde olanlar da Allah\'ındır. Allah her şeyi kuşatır.'),
(620, 620, 4, 127, 'Nisâ', 'Medeni', 'Kadınlar hakkında senden fetva isterler, de ki: \"Onlar hakkında fetvayı size Allah veriyor: \"Bu fetva, kendilerine yazılan şeyi vermeyip kendileriyle evlenmeyi arzuladığınız yetim kadınlara ve bir de zavallı çocuklara ve yetimlere doğrulukla bakmanız hususunda Kitap\'da size okunandır\". Ne iyilik yaparsaniz Allah onu şüphesiz bilir.'),
(621, 621, 4, 128, 'Nisâ', 'Medeni', 'Eğer kadın, kocasının serkeşliğinden veya aldırışsızlığından endişe ederse, aralarında anlaşmaya çalışmalarında kendilerine bir engel yoktur. Anlaşmak daha hayırlıdır. Nefisler kıskançlığa meyyaldir. Eğer iyi davranır ve haksızlıktan sakınırsaniz bilin ki, Allah işlediklerinizden şüphesiz haberdardır.'),
(622, 622, 4, 129, 'Nisâ', 'Medeni', 'Adil hareket etmeye ne kadar uğraşsanız, kadınlar arasında eşitlik yapamayacaksınız, bari bir tarafa kalben tamamen meyletmeyin ki diğerini askıdaymış gibi bırakmış olmayasınız. İşleri düzeltir ve haksızlıktan sakınırsanız bilin ki Allah şüphesiz bağışlar ve merhamet eder.'),
(623, 623, 4, 130, 'Nisâ', 'Medeni', 'Ayrılırlarsa, Allah her birini nimetinin genişliğiyle yoksulluktan kurtarır, Allah her şeyi kaplayandır. Hakim\'dir.'),
(624, 624, 4, 131, 'Nisâ', 'Medeni', 'Göklerde olanlar da, yerde olanlar da Allah\'ındır. And olsun ki, sizden önce Kitap verilenlere ve size, Allah\'tan sakınmanızı tavsiye ettik. İnkar ederseniz bilin ki, göklerde olanlar da yerde olanlar da Allah\'ındır.'),
(625, 625, 4, 132, 'Nisâ', 'Medeni', 'Göklerde ve yerde olanlar Allah\'ındır. Vekil olarak Allah yeter.'),
(626, 626, 4, 133, 'Nisâ', 'Medeni', 'Ey İnsanlar! Allah dilerse sizi yok eder, başkalarını getirir, O, buna Kadir\'dir.'),
(627, 627, 4, 134, 'Nisâ', 'Medeni', 'Dünya nimetini kim isterse, bilsin ki, dünyanın ve ahiretin nimeti Allah\'ın katındadır. Allah işitir ve görür.'),
(628, 628, 4, 135, 'Nisâ', 'Medeni', 'Ey İnananlar! Kendiniz, ana babanız ve yakınlarınız aleyhlerine de olsa, Allah için şahit olarak adaleti gözetin; ister zengin, ister fakir olsun, Allah onlara daha yakındır. Adaletinizde heveslere uymayın. Eğer eğriltirseniz veya yüz çevirirseniz bilin ki, Allah işlediklerinizden şüphesiz haberdardır.'),
(629, 629, 4, 136, 'Nisâ', 'Medeni', 'Ey İnananlar! Allah\'a, Peygamberine, Peygamberine indirdiği Kitap\'a ve daha önce indirdiği Kitap\'a inanmakta sebat gösterin. Kim Allah\'ı, meleklerini, kitaplarını, peygamberlerini ve ahiret gününu inkar ederse, şüphesiz derin bir sapıklığa sapmıştır.'),
(630, 630, 4, 137, 'Nisâ', 'Medeni', 'Doğrusu inanıp sonra inkar edenleri, sonra inanıp tekrar inkar edenleri, sonra da inkarları artmış olanları Allah bağışlamaz; onları doğru yola eriştirmez.'),
(631, 631, 4, 138, 'Nisâ', 'Medeni', 'Münafıklara, kendilerine elem verici bir azab olduğunu müjdele.'),
(632, 632, 4, 139, 'Nisâ', 'Medeni', 'Onlar, inananları bırakıp da kafirleri dost edinirler; onların tarafında bir şeref ve kudret mi arıyorlar? Doğrusu kudret bütün olarak Allah\'ındır.'),
(633, 633, 4, 140, 'Nisâ', 'Medeni', 'O, size Kitap\'da \"Allah\'ın ayetlerinin inkar edildiğini ve alaya alındığını işittiğinizde, başka bir söze geçmedikçe, onlarla bir arada oturmayın, yoksa siz de onlar gibi olursunuz\" diye indirdi. Doğrusu Allah münafıkları ve kafirlerin hepsini cehennemde toplayacaktır.'),
(634, 634, 4, 141, 'Nisâ', 'Medeni', 'Sizi gözleyenler, Allah\'tan size bir zafer gelirse, \"Sizinle beraber değil miydik?\" derler; eğer kafirlere bir pay çıkarsa, onlara: \"Size üstünlük sağlayarak sizi müminlerden korumadık mı?\" derler. Allah kıyamet günü aranızda hüküm verir. Allah inkarcılara, inananlar aleyhinde asla fırsat vermeyecektir.'),
(635, 635, 4, 142, 'Nisâ', 'Medeni', 'Doğrusu münafıklar Allah\'ı aldatmağa çalışırlar, oysa O, onlara aldatmanın ne olduğunu gösterecektir. Onlar namaza tembel tembel kalkarlar, insanlara gösteriş yaparlar, ne onlarla, ne de bunlarla olur, ikisi arasında bocalayarak Allah\'ı pek az anarlar. Allah\'ın saptırdığı kimseye yol bulamayacaksın.'),
(636, 636, 4, 143, 'Nisâ', 'Medeni', 'Doğrusu münafıklar Allah\'ı aldatmağa çalışırlar, oysa O, onlara aldatmanın ne olduğunu gösterecektir. Onlar namaza tembel tembel kalkarlar, insanlara gösteriş yaparlar, ne onlarla, ne de bunlarla olur, ikisi arasında bocalayarak Allah\'ı pek az anarlar. Allah\'ın saptırdığı kimseye yol bulamayacaksın.'),
(637, 637, 4, 144, 'Nisâ', 'Medeni', 'Ey İnananlar! Müminleri bırakıp kafirleri dost edinmeyin. Allah\'ın aleyhinize apaçık bir ferman vermesini mi istersiniz?'),
(638, 638, 4, 145, 'Nisâ', 'Medeni', 'Doğrusu münafıklar cehennemin en alt tabakasındadırlar. Onlara yardımcı bulamayacaksın.'),
(639, 639, 4, 146, 'Nisâ', 'Medeni', 'Ancak tevbe edenler, nefislerini ıslah edenler, Allah\'ın Kitap\'ına sarılanlar ve dinlerine Allah için candan bağlananlar müstesnadır. Onlar inananlarla beraberdirler. Allah müminlere büyük ecir verecektir.'),
(640, 640, 4, 147, 'Nisâ', 'Medeni', 'Şükreder ve inanırsanız, Allah size niçin azabetsin? Allah şükrün karşılığını verir ve bilir.'),
(641, 641, 4, 148, 'Nisâ', 'Medeni', 'Allah, zulme uğrayan kimseden başkasının, kötülüğü sözle bile açıklamasını sevmez. Allah işitir ve bilir.'),
(642, 642, 4, 149, 'Nisâ', 'Medeni', 'Bir iyiliği açığa vurur veya gizler yahut bir kötülüğü affederseniz, bilin ki Allah da Affeden\'dir, Güçlü Olan\'dır.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(643, 643, 4, 150, 'Nisâ', 'Medeni', 'Allah\'ı ve peygamberlerini inkar eden, Allah\'la peygamberleri arasını ayırmak isteyen, \"Bir kısmına inanır bir kısmını inkar ederiz\" diyerek ikisi arasında bir yol tutmak isteyenler, işte onlar gerçekten kafir olanlardır. Kafirlere ağır bir azab hazırlamışızdır.'),
(644, 644, 4, 151, 'Nisâ', 'Medeni', 'Allah\'ı ve peygamberlerini inkar eden, Allah\'la peygamberleri arasını ayırmak isteyen, \"Bir kısmına inanır bir kısmını inkar ederiz\" diyerek ikisi arasında bir yol tutmak isteyenler, işte onlar gerçekten kafir olanlardır. Kafirlere ağır bir azab hazırlamışızdır.'),
(645, 645, 4, 152, 'Nisâ', 'Medeni', 'Allah\'a ve peygamberlerine inanıp, onlardan hiçbirini ayırmayanlara, işte onlara Allah ecirlerini verecektir. O, bağışlar ve merhamet eder.'),
(646, 646, 4, 153, 'Nisâ', 'Medeni', 'Kitap ehli, senin kendilerine gökten bir kitap indirmeni isterler. Musa\'dan bundan daha büyüğünü istemişlerdi ve \"Bize Allah\'ı apaçık göster\" demişlerdi. Zulümlerinden ötürü onları yıldırım çarpmıştı. Belgeler kendilerine geldikten sonra da, buzağıyı tanrı olarak benimsediler, fakat bunları affettik ve Musa\'ya apaçık bir hüccet verdik, söz vermelerine karşılık Tur dağını üzerlerine kaldırdık ve onlara: \"Kapıdan secde ederek girin\" dedik, \"Cumartesileri aşırı gitmeyin\" dedik, onlardan sağlam bir söz aldık.'),
(647, 647, 4, 154, 'Nisâ', 'Medeni', 'Kitap ehli, senin kendilerine gökten bir kitap indirmeni isterler. Musa\'dan bundan daha büyüğünü istemişlerdi ve \"Bize Allah\'ı apaçık göster\" demişlerdi. Zulümlerinden ötürü onları yıldırım çarpmıştı. Belgeler kendilerine geldikten sonra da, buzağıyı tanrı olarak benimsediler, fakat bunları affettik ve Musa\'ya apaçık bir hüccet verdik, söz vermelerine karşılık Tur dağını üzerlerine kaldırdık ve onlara: \"Kapıdan secde ederek girin\" dedik, \"Cumartesileri aşırı gitmeyin\" dedik, onlardan sağlam bir söz aldık.'),
(648, 648, 4, 155, 'Nisâ', 'Medeni', 'Sözleşmelerini bozmaları, Allah\'ın ayetlerini inkar etmeleri, peygamberleri haksız yere öldürmeleri, \"Kalblerimiz perdelidir\" demelerinden ötürü Allah, evet, inkarlarına karşılık onların kalblerini mühürledi, onun için bunların ancak pek azı inanır.'),
(649, 649, 4, 156, 'Nisâ', 'Medeni', 'Bu, bir de inkarlarından, Meryem\'e büyük bir iftirada bulunmalarından ve: \"Meryem oğlu İsa Mesih\'i, Allah\'ın elçisini öldürdük\" demelerinden ötürüdür. Oysa onu öldürmediler ve asmadılar, fakat onlara öyle göründü. Ayrılığa düştükleri şeyde doğrusu şüphededirler, bu husustaki bilgileri ancak sanıya uymaktan ibarettir, kesin olarak onu öldürmediler, bilakis Allah onu kendi katına yükseltti. Allah Güçlü\'dür, Hakim\'dir.'),
(650, 650, 4, 157, 'Nisâ', 'Medeni', 'Bu, bir de inkarlarından, Meryem\'e büyük bir iftirada bulunmalarından ve: \"Meryem oğlu İsa Mesih\'i, Allah\'ın elçisini öldürdük\" demelerinden ötürüdür. Oysa onu öldürmediler ve asmadılar, fakat onlara öyle göründü. Ayrılığa düştükleri şeyde doğrusu şüphededirler, bu husustaki bilgileri ancak sanıya uymaktan ibarettir, kesin olarak onu öldürmediler, bilakis Allah onu kendi katına yükseltti. Allah Güçlü\'dür, Hakim\'dir.'),
(651, 651, 4, 158, 'Nisâ', 'Medeni', 'Bu, bir de inkarlarından, Meryem\'e büyük bir iftirada bulunmalarından ve: \"Meryem oğlu İsa Mesih\'i, Allah\'ın elçisini öldürdük\" demelerinden ötürüdür. Oysa onu öldürmediler ve asmadılar, fakat onlara öyle göründü. Ayrılığa düştükleri şeyde doğrusu şüphededirler, bu husustaki bilgileri ancak sanıya uymaktan ibarettir, kesin olarak onu öldürmediler, bilakis Allah onu kendi katına yükseltti. Allah Güçlü\'dür, Hakim\'dir.'),
(652, 652, 4, 159, 'Nisâ', 'Medeni', 'Kitap ehlinden, ölmeden önce, İsa\'ya inanmayacak yoktur. O, gerektiği gibi inanmadıklarından, kıyamet günü onların aleyhine şahit olur.'),
(653, 653, 4, 160, 'Nisâ', 'Medeni', 'Yahudilerin haksızlıklarından, çoklarını Allah yolundan menetmelerinden, yasak edilmişken faiz almaları ve insanların mallarını haksızlıkla yemelerinden ötürü kendilerine helal kılınan temiz şeyleri onlara haram kıldık. Onlardan inkar edenlere, elem verici azab hazırladık.'),
(654, 654, 4, 161, 'Nisâ', 'Medeni', 'Yahudilerin haksızlıklarından, çoklarını Allah yolundan menetmelerinden, yasak edilmişken faiz almaları ve insanların mallarını haksızlıkla yemelerinden ötürü kendilerine helal kılınan temiz şeyleri onlara haram kıldık. Onlardan inkar edenlere, elem verici azab hazırladık.'),
(655, 655, 4, 162, 'Nisâ', 'Medeni', 'Fakat onlardan ilimde derinleşmiş olanlara, sana indirilen Kitap\'a ve senden önce indirilen Kitap\'a inanan müminlere, namaz kılanlara, zekat verenlere, Allah\'a ve ahiret gününe inananlara, elbette büyük ecir vereceğiz.'),
(656, 656, 4, 163, 'Nisâ', 'Medeni', 'Nuh\'a, ondan sonra gelen peygamberlere vahyettiğimiz, İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a, torunlarına, İsa\'ya, Eyyub\'a, Yunus\'a, Harun\'a ve Süleyman\'a vahyettiğimiz gibi şüphesiz sana da vahyettik. Davud\'a da Zebur verdik.'),
(657, 657, 4, 164, 'Nisâ', 'Medeni', 'Peygamberlerden sonra, insanların Allah\'a karşı bir hüccetleri olmaması için, gönderilen müjdeci ve uyarıcı peygamberlerden bir kısmını daha önce sana anlatmış, bir kısmını da anlatmamıştık. Allah, Musa\'ya hitabetmişti. Allah güçlüdür, Hakim\'dir.'),
(658, 658, 4, 165, 'Nisâ', 'Medeni', 'Peygamberlerden sonra, insanların Allah\'a karşı bir hüccetleri olmaması için, gönderilen müjdeci ve uyarıcı peygamberlerden bir kısmını daha önce sana anlatmış, bir kısmını da anlatmamıştık. Allah, Musa\'ya hitabetmişti. Allah güçlüdür, Hakim\'dir.'),
(659, 659, 4, 166, 'Nisâ', 'Medeni', 'Fakat Allah sana indirdiğine şahidlik eder, onu bilerek indirmiştir, melekler de şahidlik ederler. Şahid olarak Allah yeter.'),
(660, 660, 4, 167, 'Nisâ', 'Medeni', 'İnkar edenler, Allah yolundan alıkoyanlar, şüphesiz derin bir sapıklığa sapmışlardır.'),
(661, 661, 4, 168, 'Nisâ', 'Medeni', 'İnkar edenleri ve zalimleri Allah şüphesiz bağışlamaz, onları içinde temelli ve ebediyyen kalacakları cehennem yolundan başka bir yola eriştirmez. Bu, Allah\'a kolaydır.'),
(662, 662, 4, 169, 'Nisâ', 'Medeni', 'İnkar edenleri ve zalimleri Allah şüphesiz bağışlamaz, onları içinde temelli ve ebediyyen kalacakları cehennem yolundan başka bir yola eriştirmez. Bu, Allah\'a kolaydır.'),
(663, 663, 4, 170, 'Nisâ', 'Medeni', 'Ey İnsanlar! Peygamber Rabbiniz\'den size gerçekle geldi, inanın, bu sizin hayrınızadır. İnkar ederseniz, bilin ki, göklerde ve yerde olanlar Allah\'ındır. Allah bilendir. Hakim\'dir.'),
(664, 664, 4, 171, 'Nisâ', 'Medeni', 'Ey Kitap ehli! Dininizde taşkınlık etmeyin. Allah hakkında ancak gerçeği söyleyin. Meryem oğlu İsa Mesih, Allah\'ın peygamberi, Meryem\'e ulaştırdığı kelimesi ve kendinden bir ruhtur. Allah\'a ve peygamberlerine inanın, \"üçtür\" demeyin, vazgeçin, bu hayrınızadır. Allah ancak bir tek Tanrı\'dır, çocuğu olmaktan münezzehtir, göklerde olanlar da yerde olanlar da O\'nundur. Vekil olarak Allah yeter.'),
(665, 665, 4, 172, 'Nisâ', 'Medeni', 'Mesih de, gözde melekler de Allah\'a kul olmaktan asla çekinmezler. Kim O\'na kulluktan çekinir ve büyüklük taslarsa, bilsin ki, O, hepsini huzuruna toplayacaktır.'),
(666, 666, 4, 173, 'Nisâ', 'Medeni', 'İnananlara ve yararlı iş işleyenlere, ecirlerini ödeyecek, onlara olan bol nimetini daha da artıracaktır. Kulluk etmekten çekinenleri ve büyüklük taslayanları elem verici bir azaba uğratacaktır. Onlar kendilerine Allah\'tan başka bir dost ve yardımcı bulamazlar.'),
(667, 667, 4, 174, 'Nisâ', 'Medeni', 'Ey İnsanlar! Rabbiniz\'den size açık bir delil geldi, size apaçık bir nur, Kuran indirdik.'),
(668, 668, 4, 175, 'Nisâ', 'Medeni', 'Allah kendisine inananları ve Kitabına sarılanları rahmetine ve bol nimetine kavuşturacak, onları Kendisine götüren doğru yola eriştirecektir.'),
(669, 669, 4, 176, 'Nisâ', 'Medeni', 'Senden fetva isterler, de ki: \"Allah size ikinci dereceden mirasçılar hakkında fetva veriyor: \"Şayet çocuğu olmayıp bir kızkardeşi bulunan kimse ölürse, bıraktığının yarısı kızkardeşe kalır. Fakat kızkardeşinin çocuğu yoksa kendisi, ona tamamen varis olur. Eğer iki kızkardeş kalmışsa, bıraktığının üçte ikisi onlaradır. Eğer mirasçılar erkek ve kadın kardeşlerse, erkeğe, iki dişinin hissesi kadar vardır. Doğru yoldan saparsınız diye Allah size açıklıyor.\" Allah her şeyi bilir.'),
(670, 670, 5, 1, 'Mâide', 'Medeni', 'Ey İnananlar! Akidleri yerine getirin. İhramda iken avlanmayı helal görmeksizin, size bildirilecek olanlar dışında, hayvanlar helal kılındı; Allah dilediği hükmü verir.'),
(671, 671, 5, 2, 'Mâide', 'Medeni', 'Ey İnananlar! Allah\'ın nişanelerine, hürmet edilen aya, hediye olan kurbanlığa, gerdanlıklar takılan hayvanlara, Rab\'lerinden bol nimet ve rıza talep ederek Beyt-i Haram\'a gelenlere sakın hürmetsizlik etmeyin. İhramdan çıktığınız zaman avlanabilirsiniz. Sizi Mescid-i Haram\'dan menettiği için bir topluluğa olan kininiz, aşırı gitmenize sebep olmasın; iyilikte ve fenalıktan sakınmakta yardımlaşın, günah işlemek ve aşırı gitmekte yardımlaşmayın. Allah\'tan sakının, Allah\'ın cezası şiddetlidir.'),
(672, 672, 5, 3, 'Mâide', 'Medeni', 'Leş, kan, domuz eti, Allah\'tan başkası adına kesilenler, -canları çıkmadan önce kesmemişseniz, boğulmuş, bir yerine vurularak öldürülmüş, düşüp yuvarlanmış, başka bir hayvan tarafından süsülmüş, yırtıcı hayvan tarafından yenmiş olanları- dikili taşlar üzerine boğazlananlar ile fal oklarıyla kısmet aramanız size haram kılındı; bunlar fasıklıktır. Bugün, inkar edenler sizi dininizden etmekten umutlarını kesmişlerdir, onlardan korkmayın, Benden korkun. Bugün, size dininizi bütünledim, üzerinize olan nimetimi tamamladım, din olarak sizin için İslam\'ı beğendim. Açlıktan darda kalan, günaha kaymaksızın yiyebilir. Doğrusu Allah Bağışlayan\'dır, merhametli olandır.'),
(673, 673, 5, 4, 'Mâide', 'Medeni', 'Sana, kendilerine neyin helal kılındığını soruyorlar, de ki: Size temiz olanlar helal kılındı; Allah\'ın size öğrettiği üzere alıştırıp yetiştirerek öğrettiğiniz avcı hayvanların sizin için tuttuklarını yiyin ve üzerine Allah\'ın adını anın. Allah\'tan sakının, doğrusu Allah hesabı çabuk görür.'),
(674, 674, 5, 5, 'Mâide', 'Medeni', 'Bugün, size temiz olanlar helal kılındı. Kitap verilenlerin yemeği size helal, sizin yemeğiniz de onlara helaldir. İnanan hür ve iffetli kadınlar ve sizden önce kitap verilenlerin hür ve iffetli kadınları -zina etmeksizin, gizli dost tutmaksızın ve mehirlerini verdiğiniz takdirde- size helaldir. Kim imanı inkar ederse, şüphesiz amelleri boşa gider. O, ahirette de kaybedenlerdendir.'),
(675, 675, 5, 6, 'Mâide', 'Medeni', 'Ey İnananlar! Namaza kalktığınızda yüzlerinizi, dirseklere kadar ellerinizi, -başlarınızı meshedip- topuk kemiklerine kadar ayaklarınızı yıkayın. Eğer cünüpseniz yıkanıp temizlenin; şayet hasta veya yolculukta iseniz veya ayak yolundan gelmişseniz yahut kadınlara yaklaşmışsanız ve su bulamamışsanız temiz bir toprağa teyemmüm edin, yüzlerinizi, ellerinizi onunla meshedin. Allah sizi zorlamak istemez, Allah sizi arıtıp üzerinize olan nimetini tamamlamak ister ki şükredesiniz.'),
(676, 676, 5, 7, 'Mâide', 'Medeni', 'Allah\'ın size olan nimetini ve \"İşittik, itaat ettik\" dediğinizde sizi andına bağladığı sözünü anın. Allah\'tan sakının, Allah içinizde olanı elbette bilir.'),
(677, 677, 5, 8, 'Mâide', 'Medeni', 'Ey İnananlar! Allah için adaleti ayakta tutup gözeten şahidler olun. Bir topluluğa olan öfkeniz sizi adaletsizliğe sürüklemesin; adil olun; bu, Allah\'a karşı gelmekten sakınmaya daha yakındır. Allah\'tan sakının, doğrusu Allah işlediklerinizden Haberdar\'dır.'),
(678, 678, 5, 9, 'Mâide', 'Medeni', 'Allah, inananlara ve yararlı işler işleyenlere mağfiret ve büyük ecir olduğunu vadetmiştir.'),
(679, 679, 5, 10, 'Mâide', 'Medeni', 'İnkar edenler ve ayetlerimizi yalanlayanlar, işte onlar cehennemliklerdir.'),
(680, 680, 5, 11, 'Mâide', 'Medeni', 'Ey İnananlar! Allah\'ın üzerinize olan nimetini anın: Hani bir topluluk size tecavüze kalkışmıştı da Allah onlara mani olmuştu. Allah\'tan sakının, inananlar Allah\'a güvensinler.'),
(681, 681, 5, 12, 'Mâide', 'Medeni', 'And olsun ki, Allah, İsrailoğullarından söz almıştı. Onlardan oniki reis seçtik. Allah: \"Ben şüphesiz sizinleyim, namaz kılarsanız, zekat verirseniz, peygamberlerime inanır ve onlara yardım ederseniz, Allah uğrunda güzel bir takdimede bulunursanız, and olsun ki kötülüklerinizi örterim. And olsun ki, sizi içlerinden ırmaklar akan cennetlere koyarım. Bundan sonra sizden kim inkar ederse şüphesiz doğru yoldan sapmış olur\" dedi.'),
(682, 682, 5, 13, 'Mâide', 'Medeni', 'Sözlerini bozdukları için onlara lanet ettik, kalblerini katılaştırdık. Onlar sözleri yerlerinden değiştirirler. Kendilerine belletilenin bir kısmını unuttular. İçlerinden pek azından başkasının daima hainliklerini görürsün, onları affet ve geç. Allah iyilik yapanları şüphesiz sever.'),
(683, 683, 5, 14, 'Mâide', 'Medeni', '\"Biz hıristiyanız\" diyenlerden de söz almıştık; onlar, kendilerine belletilenin bir kısmını unuttular, bu yüzden aralarına kıyamete kadar düşmanlık ve kin saldık. Allah, yapmakta olduklarını kendilerine haber verecektir.'),
(684, 684, 5, 15, 'Mâide', 'Medeni', 'Ey Kitap ehli! Kitap\'dan gizleyip durduğunuzun çoğunu size açıkça anlatan ve çoğundan da geçiveren peygamberimiz gelmiştir. Doğrusu size Allah\'tan bir nur ve apaçık bir Kitap gelmiştir.'),
(685, 685, 5, 16, 'Mâide', 'Medeni', 'Allah, rızasını gözetenleri onunla, selamet yollarına eriştirir ve onları, izni ile, karanlıklardan aydınlığa çıkarır. Onları doğru yola iletir.'),
(686, 686, 5, 17, 'Mâide', 'Medeni', '\"Allah ancak Meryem oğlu Mesih\'tir\" diyenler and olsun ki kafir olmuşlardır. De ki: \"Allah Meryem oğlu Mesih\'i, anasını ve yeryüzünde olanların hepsini yok etmeyi dilerse kim O\'na karşı koyabilir?\" Göklerin, yerin ve arasındakilerin hükümranlığı Allah\'ındır, dilediğini yaratır. Allah her şeye Kadir\'dir.'),
(687, 687, 5, 18, 'Mâide', 'Medeni', 'Yahudiler ve hıristiyanlar, \"Biz Allah\'ın oğulları ve sevgilileriyiz\" dediler. \"Öyleyse günahlarınızdan ötürü size niçin azabediyor? Bilakis siz O\'nun yarattığı insanlarsınız\" de, Allah dilediğini bağışlar, dilediğine azab eder. Göklerin, yerin ve ikisinin arasındakilerin hükümranlığı Allah\'ındır. Dönüş O\'nadır.'),
(688, 688, 5, 19, 'Mâide', 'Medeni', 'Ey Kitap ehli! Peygamberlerin arası kesildiğinde, \"Bize müjdeci ve uyarıcı gelmedi\" dersiniz diye, size açıkça anlatacak peygamberimiz geldi. Şüphesiz O, size müjdeci ve uyarıcı olarak gelmiştir. Allah her şeye Kadir\'dir.'),
(689, 689, 5, 20, 'Mâide', 'Medeni', 'Musa, milletine: \"Ey milletim! Allah\'ın size olan nimetini anın: içinizden peygamberler çıkarmış ve sizi hükümdar yapmıştır, dünyalarda kimseye vermediğini size vermiştir\".'),
(690, 690, 5, 21, 'Mâide', 'Medeni', '\"Ey milletim! Allah\'ın size yazdığı kutsal yere girin, ardınıza dönmeyin, yoksa kaybedenler olarak dönersiniz\" demişti.'),
(691, 691, 5, 22, 'Mâide', 'Medeni', '\"Ey Musa! Orada zorba bir millet vardır, onlar oradan çıkmadıkça biz oraya girmeyeceğiz, eğer çıkarlarsa, biz de gireriz\" demişlerdi.'),
(692, 692, 5, 23, 'Mâide', 'Medeni', 'Korkanlar arasında bulunan, Allah\'ın nimete erdirdiği iki adam: \"Üstlerine kapıdan yürüyün, oradan girerseniz şüphesiz galip gelirsiniz; eğer inanıyorsanız Allah\'a güvenin\" demişlerdi.'),
(693, 693, 5, 24, 'Mâide', 'Medeni', '\"Ey Musa! Onlar orada oldukça biz asla oraya girmeyeceğiz. Sen ve Rabbin gidin savaşın, doğrusu biz burada oturacağız\" demişlerdi.'),
(694, 694, 5, 25, 'Mâide', 'Medeni', 'Musa: \"Rabbim! Ben ancak kendime ve kardeşime söz geçirebiliyorum; artık bizimle bu yoldan çıkmış milletin arasını ayır\" dedi.'),
(695, 695, 5, 26, 'Mâide', 'Medeni', 'Allah: \"Orası onlara kırk yıl haram kılındı; yeryüzünde şaşkın şaşkın dolaşacaklar. Sen, yoldan çıkmış millet için tasalanma\" dedi.'),
(696, 696, 5, 27, 'Mâide', 'Medeni', 'Onlara, Adem\'in iki oğlunun kıssasını doğru olarak anlat: İkisi birer kurban sunmuşlar, birininki kabul edilmiş, diğerininki edilmemişti. Kabul edilmeyen, \"And olsun seni öldüreceğim\" deyince, kardeşi: \"Allah ancak sakınanların takdimesini kabul eder\" demişti.'),
(697, 697, 5, 28, 'Mâide', 'Medeni', '\"Beni öldürmek üzere elini bana uzatırsan, ben seni öldürmek için sana elimi uzatmam, çünkü ben, Alemlerin Rabbi olan Allah\'tan korkarım.'),
(698, 698, 5, 29, 'Mâide', 'Medeni', 'Ben, hem benim hem de kendi günahını yüklenip cehennemliklerden olmanı isterim, zulmedenlerin cezası budur\".'),
(699, 699, 5, 30, 'Mâide', 'Medeni', 'Bunun üzerine, kardeşini öldürmekte nefsine uydu ve onu öldürerek, zarara uğrayanlardan oldu.'),
(700, 700, 5, 31, 'Mâide', 'Medeni', 'Allah, kardeşinin ölüsünü nasıl gömeceğini göstermek üzere, ona yeri eşeleyen bir karga gönderdi. \"Bana yazıklar olsun! Kardeşimin ölüsünü örtmek için bu karga kadar olmaktan aciz kaldım\" dedi de ettiğine yananlardan oldu.'),
(701, 701, 5, 32, 'Mâide', 'Medeni', 'Bunun için İsrailoğullarına şöyle yazdık: \"Kim bir kimseyi bir kimseye veya yeryüzünde bozgunculuğa karşılık olmadan öldürürse, bütün insanları öldürmüş gibi olur. Kim de onu diriltirse (ölümden kurtarırsa) bütün insanları diriltmiş gibi olur\". And olsun ki, onlara belgelerle peygamberlerimiz geldi, sonra buna rağmen, onların çoğu yeryüzünde taşkınlık edenler oldu.'),
(702, 702, 5, 33, 'Mâide', 'Medeni', 'Allah ve Peygamberiyle savaşanların ve yeryüzünde bozgunculuğa uğraşanların cezası öldürülmek veya asılmak yahut çapraz olarak el ve ayakları kesilmek ya da yerlerinden sürülmektir. Bu onlara dünyada bir rezilliktir. Onlara ahirette büyük azab vardır.'),
(703, 703, 5, 34, 'Mâide', 'Medeni', 'Ancak, onları yakalamanızdan önce tevbe edenler bunun dışındadır. Biliniz ki Allah, bağışlar ve merhamet eder.'),
(704, 704, 5, 35, 'Mâide', 'Medeni', 'Ey İnananlar! Allah\'tan sakının, O\'na ulaşmaya yol arayın, yolunda cihad edin ki kurtulasınız.'),
(705, 705, 5, 36, 'Mâide', 'Medeni', 'Doğrusu, yeryüzünde olan bütün şeyler ve onların bir katı daha kafirlerin olsa da, kıyamet gününün azabından kurtulmak için fidye verseler kabul edilmez. Onlara elem verici azab vardır.'),
(706, 706, 5, 37, 'Mâide', 'Medeni', 'Ateşten çıkmak isterler, çıkamazlar. Onlara sürekli azab vardır.'),
(707, 707, 5, 38, 'Mâide', 'Medeni', 'Erkek hırsız ve kadın hırsızın, yaptıklarından ötürü Allah tarafından ibret verici bir ceza olarak, ellerini kesin. Allah Güçlü\'dür, Hakim\'dir.'),
(708, 708, 5, 39, 'Mâide', 'Medeni', 'Ettiği zulümden sonra tevbe edip düzelen kimse, bilsin ki Allah onun tevbesini kabul eder. Allah şüphesiz Bağışlayan\'dır, merhametli olandır.'),
(709, 709, 5, 40, 'Mâide', 'Medeni', 'Göklerin ve yerin hükümranlığının Allah\'ın olduğunu bilmiyor musun? Dilediğine azabeder, dilediğini bağışlar. Allah her şeye Kadir\'dir.'),
(710, 710, 5, 41, 'Mâide', 'Medeni', 'Kalbleri inanmamışken, ağızlarıyla, \"İnandık\" diyenler, yahudilerden yalana kulak verenler ve başka bir topluluk hesabına casusluk edenlerden inkara koşanlar seni üzmesin. Sözleri asıl yerlerinden değiştirirler de, \"Böyle bir fetva size verilirse alın, verilmezse kaçının\" derler. Allah\'ın fitneye düşmesini dilediği kimse için Allah\'a karşı senin elinden bir şey gelmez. İşte onlar Allah\'ın, kalblerini arıtmak istemediği kimselerdir. Dünyada rezillik onlaradır. Onlara ahirette de büyük azab vardır.'),
(711, 711, 5, 42, 'Mâide', 'Medeni', 'Onlar yalana kulak verirler, haram yerler. Eğer sana gelirlerse aralarında hükmet, yahut onlardan yüz çevir; yüz çevirirsen sana bir zarar veremezler. Eğer hükmedersen aralarında adaletle hüküm ver. Allah adil olanları sever.'),
(712, 712, 5, 43, 'Mâide', 'Medeni', 'Allah\'ın hükmünün bulunduğu Tevrat yanlarında iken, ne yüzle seni hakem tayin ediyorlar da sonra bundan yüz çeviriyorlar? İşte onlar inanmış değillerdir.'),
(713, 713, 5, 44, 'Mâide', 'Medeni', 'Doğrusu Biz yol gösterici olarak Tevrat\'ı indirdik. Kendisini Allah\'a teslim etmiş peygamberler, yahudi olanlara onunla ve Rabbe kul olanlar, bilginler de Allah\'ın Kitap\'ından elde mahfuz kalanla hükmederlerdi. Tevrat\'a şahiddiler. O halde insanlardan korkmayın, benden korkun, ayetlerimi hiçbir değerle değiştirmeyin; Allah\'ın indirdiği ile hükmetmeyenler, işte onlar kafirlerdir.'),
(714, 714, 5, 45, 'Mâide', 'Medeni', 'Orada onlara cana can, göze göz, buruna burun, kulağa kulak, dişe dişle ve yaralara karşılıklı ödeşme yazdık. Kim hakkından vazgeçerse bu, onun günahlarına keffaret olur. Allah\'ın indirdiği ile hükmetmeyenler, işte onlar zalimlerdir.'),
(715, 715, 5, 46, 'Mâide', 'Medeni', 'Onların izi üzerine arkalarından Meryem oğlu İsa\'yı, ondan önce gelmiş bulunan Tevrat\'ı doğrulayarak gönderdik. Ona, yol gösterici, aydınlatıcı olan ve önünde bulunan Tevrat\'ı doğrulayan İncil\'i sakınanlara öğüt ve yol gösterici olarak verdik.'),
(716, 716, 5, 47, 'Mâide', 'Medeni', 'İncil sahibleri Allah\'ın onda indirdikleri ile hükmetsinler. Allah\'ın indirdiği ile hükmetmeyenler, işte onlar fasık olanlardır.'),
(717, 717, 5, 48, 'Mâide', 'Medeni', 'Kuran\'ı, önce gelen Kitap\'ı tasdik ederek ve ona şahid olarak gerçekle sana indirdik. Allah\'ın indirdiği ile aralarında hükmet; gerçek olan sana gelmiş bulunduğuna göre, onların heveslerine uyma! Her biriniz için bir yol ve bir yöntem kıldık; eğer Allah dileseydi sizi bir tek ümmet yapardı, fakat bu, verdikleriyle sizi denemesi içindir; o halde iyiliklere koşuşun, hepinizin dönüşü Allah\'adır. O, ayrılığa düştüğünüz şeyleri size bildirir.'),
(718, 718, 5, 49, 'Mâide', 'Medeni', 'O halde, Allah\'ın indirdiği Kitap ile aralarında hükmet, Allah\'ın sana indirdiği Kuran\'ın bir kısmından seni vazgeçirmelerinden sakın, heveslerine uyma; eğer yüz çevirirlerse bil ki, Allah bir kısım günahları yüzünden onları cezalandırmak istiyor. İnsanların çoğu gerçekten fasıktırlar.'),
(719, 719, 5, 50, 'Mâide', 'Medeni', 'Cahiliye devri hükmünü mü istiyorlar? Yakinen bilen bir millet için Allah\'tan daha iyi hüküm veren kim vardır?'),
(720, 720, 5, 51, 'Mâide', 'Medeni', 'Ey İnananlar! Yahudileri ve hıristiyanları dost olarak benimsemeyin, onlar birbirlerinin dostudurlar. Sizden kim onlara dost olursa o da onlardandır. Allah zulmeden kimseleri doğru yola eriştirmez.'),
(721, 721, 5, 52, 'Mâide', 'Medeni', 'Kalblerinde hastalık olanların, \"Bize bir felaket gelmesinden korkuyoruz\" diyerek onlara koştuğunu görürsün. Olur ki Allah bir zafer verir veya katından bir emir getirir de kalblerinde gizlediklerine içleri yananlara dönerler.'),
(722, 722, 5, 53, 'Mâide', 'Medeni', 'İnananlar, \"Sizinle beraber olduklarına bütün güçleriyle Allah\'a yemin edenler bunlar mıdır?\" derler. Onların amelleri boşa gitmiş ve kaybeden kimseler olmuşlardır.'),
(723, 723, 5, 54, 'Mâide', 'Medeni', 'Ey İnananlar! Aranızda dininden kim dönerse bilsin ki, Allah, sevdiği ve onların O\'nu sevdiği, inananlara karşı alçak gönüllü, inkarcılara karşı güçlü, Allah yolunda cihad eden, yerenin yermesinden korkmayan bir millet getirir. Bu, Allah\'ın dilediğine verdiği bol nimetidir. Allah her şeyi kaplar ve bilir.'),
(724, 724, 5, 55, 'Mâide', 'Medeni', 'Sizin dostunuz ancak Allah, O\'nun Peygamberi ve namaz kılan, zekat veren ve rüku eden müminlerdir.'),
(725, 725, 5, 56, 'Mâide', 'Medeni', 'Kim Allah\'ı, Peygamberini ve inananları dost edinirse bilsin ki, şüphesiz Allah\'tan yana olanlar üstün gelirler.'),
(726, 726, 5, 57, 'Mâide', 'Medeni', 'Ey İnananlar! Kendilerine sizden önce kitap verilenlerden, dininizi alaya ve eğlenceye alanları ve inkarcıları dost olarak benimsemeyin. İnanıyorsanız Allah\'tan sakının.'),
(727, 727, 5, 58, 'Mâide', 'Medeni', 'Namaza çağırdığınızda onu alay ve eğlenceye alırlar. Bu, onların akletmeyen bir topluluk olmasındandır.'),
(728, 728, 5, 59, 'Mâide', 'Medeni', 'De ki, \"Ey kitap ehli! Allah\'a, bize indirilene ve daha önce indirilene inanmamızdan ve çoğunuzun fasık olmasından ötürü mü bizden hoşlanmıyorsunuz?\"'),
(729, 729, 5, 60, 'Mâide', 'Medeni', '\"Allah katında bundan daha kötü bir karşılığın bulunduğunu size haber vereyim mi?\" de, Allah kime lanet ve gazab ederse, kimlerden maymunlar, domuzlar ve şeytana kullar kılarsa, işte onlar yeri en kötü ve doğru yoldan en çok sapmış olanlardır.'),
(730, 730, 5, 61, 'Mâide', 'Medeni', 'Size geldiklerinde \"İnandık\" derler, oysa yanınıza inkarcı olarak girmiş ve yine inkarcı olarak çıkmışlardır. Gizlemekte olduklarını Allah daha iyi bilir.'),
(731, 731, 5, 62, 'Mâide', 'Medeni', 'Onlardan çoğunun günaha, haksızlığa ve haram yemeğe koşuştuklarını görürsün. Yaptıkları ne kötüdür!'),
(732, 732, 5, 63, 'Mâide', 'Medeni', 'Rabbe kul olanlar ve bilginlerin onlara günah söz söylemeyi ve haram yemeyi yasak etmeleri gerekmez miydi? Yapmakta oldukları ne kötüdür!'),
(733, 733, 5, 64, 'Mâide', 'Medeni', 'Yahudiler, \"Allah\'ın eli sıkıdır\" dediler; dediklerinden ötürü elleri bağlandı, lanetlendiler. Hayır, O\'nun iki eli de açıktır, nasıl dilerse sarfeder. And olsun ki, sana Rabbinden indirilen sözler onların çoğunun azgınlığını ve inkarını artıracaktır. Onların arasına kıyamete kadar sürecek düşmanlık ve kin saldık. Savaş ateşini ne zaman körükleseler Allah onu söndürmüştür. Yeryüzünde bozgunculuğa koşarlar. Allah bozguncuları sevmez.'),
(734, 734, 5, 65, 'Mâide', 'Medeni', 'Şayet kitap ehli inanıp karşı gelmekten sakınsalardı, kötülüklerini örterdik ve onları nimet cennetlerine koyardık.'),
(735, 735, 5, 66, 'Mâide', 'Medeni', 'Eğer onlar Tevrat\'ı, İncil\'i ve Rablerinden kendilerine indirilen Kuran\'ı gereğince uygulasalardı, her yönden nimete ermiş olurlardı. İçlerinde orta yolu tutan bir zümre vardı, çoğunun işledikleri ise kötü idi.'),
(736, 736, 5, 67, 'Mâide', 'Medeni', 'Ey Peygamber! Rabbinden sana indirileni tebliğ et, eğer bunu yapmazsan O\'nun elçiliğini yapmamış olursun. Allah seni insanlardan korur. Doğrusu Allah kafirlere yol göstermez.'),
(737, 737, 5, 68, 'Mâide', 'Medeni', '\"Ey Kitap ehli! Tevrat\'ı, İncil\'i ve Rabbinizden size indirileni gereğince uygulamadıkça bir temeliniz olmaz\" de. And olsun ki Rabbinden sana indirilen, Kuran, onlardan çoğunun azgınlık ve küfrünü artırır. Öyleyse kafirler için tasalanma.'),
(738, 738, 5, 69, 'Mâide', 'Medeni', 'Doğrusu inananlar, yahudiler, sabiiler ve hıristiyanlardan Allah\'a ve ahiret gününe inanan, yararlı iş yapan kimselere korku yoktur, onlar üzülmeyeceklerdir.'),
(739, 739, 5, 70, 'Mâide', 'Medeni', 'And olsun ki İsrailoğullarından söz aldık ve onlara peygamberler gönderdik. Nefislerinin hoşlanmadığı bir şeyle onlara her peygamber gelişte, bir kısmını yalanlarlar ve bir kısmını da öldürürlerdi.'),
(740, 740, 5, 71, 'Mâide', 'Medeni', 'Bir fitne kopmayacağını sandılar, körleştiler, sağırlaştılar; sonra Allah tevbelerini kabul etti, yine de çoğu körleştiler ve sağırlaştılar. Allah, işlediklerini görür.'),
(741, 741, 5, 72, 'Mâide', 'Medeni', 'And olsun ki, \"Allah ancak Meryem oğlu Mesih\'tir\" diyenler kafir oldular. Oysa Mesih, \"Ey İsrailoğulları! Rabbim ve Rabbiniz olan Allah\'a kulluk edin; kim Allah\'a ortak koşarsa muhakkak Allah ona cenneti haram eder, varacağı yer ateştir, zulmedenlerin yardımcıları yoktur\" dedi.'),
(742, 742, 5, 73, 'Mâide', 'Medeni', 'And olsun ki, \"Allah üçten biridir\" diyenler kafir olmuştur; oysa tanrı ancak bir tek Tanrı\'dır. Dediklerinden vazgeçmezlerse, and olsun onlardan inkar edenler elem verici bir azaba uğrayacaktır.'),
(743, 743, 5, 74, 'Mâide', 'Medeni', 'Allah\'a tevbe etmezler, O\'ndan mağfiret dilemezler mi? Oysa Allah Bağışlayan\'dır, merhamet edendir.'),
(744, 744, 5, 75, 'Mâide', 'Medeni', 'Meryem oğlu Mesih sadece peygamberdir, -ondan önce de peygamberler geçmiştir- onun annesi dosdoğrudur, her ikisi de yemek yerlerdi. Onlara ayetleri nasıl açıkladığımıza bir bak, sonra da bak ki nasıl yüz çeviriyorlar!'),
(745, 745, 5, 76, 'Mâide', 'Medeni', '\"Size zarar da fayda da veremeyecek, Allah\'tan başka birine mi kulluk ediyorsunuz?\" de. Allah hem işitir, hem bilir.'),
(746, 746, 5, 77, 'Mâide', 'Medeni', '\"Ey Kitap ehli! Haksız olarak dininizde taşkınlık etmeyin. Daha önce sapıtan, çoğunu saptıran ve doğru yoldan ayrılan bir milletin heveslerine uymayın\" de.'),
(747, 747, 5, 78, 'Mâide', 'Medeni', 'İsrailoğullarından inkar edenler, Davud\'un ve Meryem oğlu İsa\'nın diliyle lanetlenmişlerdi. Bu, baş kaldırmaları ve aşırı gitmelerindendi.'),
(748, 748, 5, 79, 'Mâide', 'Medeni', 'Birbirlerinin yaptıkları fenalıklara mani olmuyorlardı. Yapmakta oldukları ne kötü idi!'),
(749, 749, 5, 80, 'Mâide', 'Medeni', 'Çoğunun inkar edenleri dost edindiklerini görürsün. Nefislerinin önlerine sürdüğü ne kötüdür! Allah onlara gazabetmiştir, onlar azabta temellidirler.'),
(750, 750, 5, 81, 'Mâide', 'Medeni', 'Eğer Allah\'a, Peygambere ve ona indirilen Kuran\'a inanmış olsalardı, onları dost edinmezlerdi, fakat onların çoğu fasıktır.'),
(751, 751, 5, 82, 'Mâide', 'Medeni', 'İnananlara en şiddetli düşman olarak, insanlardan yahudileri ve Allah\'a eş koşanları bulursun. Onlardan, inananlara sevgice en yakın \"Biz hıristiyanız\" diyenleri bulursun. Bu, onların içinde bilginler ve rahibler bulunmasından ve büyüklük taslamamalarındandır.'),
(752, 752, 5, 83, 'Mâide', 'Medeni', 'Peygambere indirilen Kuran\'ı işittiklerinde, gerçeği öğrenmelerinden gözlerinin yaşla dolarak, \"Rabbimiz! İnandık, bizi de şahidlerden yaz. Rabbimizin bizi iyi milletle birlikte bulundurmasını umarken niçin Allah\'a ve bize gelen gerçeğe inanmayalım?\" dediklerini görürsün.'),
(753, 753, 5, 84, 'Mâide', 'Medeni', 'Peygambere indirilen Kuran\'ı işittiklerinde, gerçeği öğrenmelerinden gözlerinin yaşla dolarak, \"Rabbimiz! İnandık, bizi de şahidlerden yaz. Rabbimizin bizi iyi milletle birlikte bulundurmasını umarken niçin Allah\'a ve bize gelen gerçeğe inanmayalım?\" dediklerini görürsün.'),
(754, 754, 5, 85, 'Mâide', 'Medeni', 'Allah onlara, dediklerine karşılık, temelli kalacakları, altından ırmaklar akan cennetler verdi. Bu, iyi davrananların mükafatıdır.'),
(755, 755, 5, 86, 'Mâide', 'Medeni', 'İnkar edip ayetlerimizi yalanlayanlar, işte onlar cehennemliklerdir.'),
(756, 756, 5, 87, 'Mâide', 'Medeni', 'Ey İnananlar! Allah\'ın size helal ettiği temiz şeyleri haram kılmayın, hududu da aşmayın, doğrusu Allah aşırı gidenleri sevmez.'),
(757, 757, 5, 88, 'Mâide', 'Medeni', 'Allah\'ın size verdiği rızıktan temiz ve helal olarak yiyin. İnandığınız Allah\'tan sakının.'),
(758, 758, 5, 89, 'Mâide', 'Medeni', 'Allah size rasgele yeminlerinizden dolayı değil, bile bile ettiğiniz yeminlerden ötürü hesap sorar. Yeminin keffareti, ailenize yedirdiğinizin ortalamasından on düşkünü yedirmek yahut giydirmek ya da bir köle azad etmektir. Bulamayan üç gün oruç tutmalıdır; yeminlerinizin keffareti budur. Yemin ettiğinizde yeminlerinizi tutun. Şükredesiniz diye Allah size böylece ayetlerini açıklıyor.'),
(759, 759, 5, 90, 'Mâide', 'Medeni', 'Ey İnananlar! İçki, kumar, putlar ve fal okları şüphesiz şeytan işi pisliklerdir, bunlardan kaçının ki saadete eresiniz.'),
(760, 760, 5, 91, 'Mâide', 'Medeni', 'Şeytan şüphesiz içki ve kumar yüzünden aranıza düşmanlık ve kin sokmak ve sizi Allah\'ı anmaktan, namazdan alıkoymak ister. Artık bunlardan vazgeçersiniz değil mi?'),
(761, 761, 5, 92, 'Mâide', 'Medeni', 'Allah\'a itaat edin, Peygambere itaat edin, karşı gelmekten çekinin; eğer yüz çevirirseniz bilin ki, peygamberimize düşen sadece açıkça tebliğ etmektir.'),
(762, 762, 5, 93, 'Mâide', 'Medeni', 'İnananlara ve yararlı iş işleyenlere, -sakınırlar, inanırlar, yararlı işler işlerler, sonra haramdan sakınıp inanırlar ve sonra isyandan sakınıp iyilik yaparlarsa- daha önceleri tatmış olduklarından dolayı bir sorumluluk yoktur. Allah iyi davrananları sever.'),
(763, 763, 5, 94, 'Mâide', 'Medeni', 'Ey İnananlar! Gıyabında Kendisinden, kimin korktuğunu ortaya koymak için, (ihramlıyken) elinizin ve mızraklarınızın ulaştığı avdan bir şeyle Allah and olsun ki sizi dener. Bundan sonra kim haddi aşarsa ona elem verici azab vardır.'),
(764, 764, 5, 95, 'Mâide', 'Medeni', 'Ey İnananlar! İhramlı iken avı öldürmeyin. Sizden bile bile onu öldürene, ehli hayvanlardan öldürdüğü kadar olduğuna içinizden iki adil kimsenin hükmedeceği, Kabe\'ye ulaşacak bir kurbanı ödeme, yahut düşkünlere yemek yedirme şeklinde keffaret ya da yaptığının ağırlığını tatmak üzere bunlara denk oruç tutma vardır. Allah geçmiştekileri affetmiştir, kim tekrar yaparsa Allah ondan öç alır. Allah Güçlü\'dür, Öçalıcı\'dır.'),
(765, 765, 5, 96, 'Mâide', 'Medeni', 'Deniz avı ve onu yemek size de, yolculara da, geçimlik olarak helal kılınmıştır. İhramlı bulunduğunuz sürece kara avı size haram kılınmıştır. Huzuruna toplanacağınız Allah\'tan sakının.'),
(766, 766, 5, 97, 'Mâide', 'Medeni', 'Allah, hürmetli ev Kabe\'yi, hürmetli ayı, kurbanı, boynu tasmalı kurbanlıkları insanların faydası için ortaya koydu. Bu, Allah\'ın göklerde ve yerde olanları bildiğini ve Allah\'ın şüphesiz her şeyi Bilen olduğunu bilmeniz içindir.'),
(767, 767, 5, 98, 'Mâide', 'Medeni', 'Allah\'ın azabının şiddetli olduğunu ve Allah\'ın Bağışlayan, merhamet eden olduğunu bilin.'),
(768, 768, 5, 99, 'Mâide', 'Medeni', 'Peygamberin görevi sadece tebliğ etmektir. Allah, sizin açıkladıklarınızı da gizlediklerinizi de bilir.'),
(769, 769, 5, 100, 'Mâide', 'Medeni', 'De ki: \"Helal ile haram, haram şeylerin çokluğundan hoşlansan bile, eşit değildir\". Ey akıl sahibleri, Allah\'tan sakının ki kurtuluşa eresiniz.'),
(770, 770, 5, 101, 'Mâide', 'Medeni', 'Ey İnananlar! Size açıklanınca hoşunuza gitmeyecek şeyleri sormayın. Kuran indirilirken onları sorarsanız size açıklanır (ama üzülürsünüz). Allah sorduğunuz şeyleri affetmiştir. Allah Bağışlayan\'dır, Halim\'dir.'),
(771, 771, 5, 102, 'Mâide', 'Medeni', 'Sizden önce bir millet onları sormuştu, sonra da onları inkar etmişlerdi.'),
(772, 772, 5, 103, 'Mâide', 'Medeni', 'Allah, kulağı çentilen, salıverilen, erkek dişi ikizler doğuran, on defa yavrulamasından ötürü yük vurulmayan hayvanların adanmasını emretmemiştir; fakat inkar edenler Allah\'a karşı yalan uydururlar ve çoğu da akletmezler.'),
(773, 773, 5, 104, 'Mâide', 'Medeni', 'Onlara, \"Gelin Allah\'ın indirdiği Kitap\'a ve peygambere uyun\" dendiğinde, \"Atalarımızı üzerinde bulduğumuz yol bize yeter\" derler; ya ataları bir şey bilmeyen ve doğru yolda olmayan kimseler idiyseler?'),
(774, 774, 5, 105, 'Mâide', 'Medeni', 'Ey İnananlar! Siz kendinize bakın; doğru yolda iseniz sapıtan kimse size zarar veremez. Hepinizin dönüşü Allah\'adır, işlemekte olduklarınızı size haber verecektir.'),
(775, 775, 5, 106, 'Mâide', 'Medeni', 'Ey İnananlar! Ölüm birinize geldiği zaman vasiyet ederken içinizden iki adil kimseyi; şayet yolculukta olup başınıza da ölüm musibeti gelmişse, namazdan sonra alıkoyacağınız, şüpheleniyorsanız, \"Akraba bile olsa yeminle hiçbir değeri değiştirmeyeceğiz, Allah\'ın şahidliğini gizlemeyeceğiz, yoksa şüphesiz günahkarlardan oluruz\" diye yemin eden sizden olmayan iki kişiyi şahid tutun.'),
(776, 776, 5, 107, 'Mâide', 'Medeni', 'Eğer bu şahidlerin günah işlemiş oldukları ortaya çıkarsa ölene daha yakın hak sahibi diğer iki kişi bunların yerine geçer ve \"Bizim şahidliğimiz ikisininkinden de daha doğrudur, biz aşırı gitmedik, yoksa şüphesiz zulmedenlerden oluruz\" diye Allah\'a yemin ederler.'),
(777, 777, 5, 108, 'Mâide', 'Medeni', 'Bu, şahidliği gerektiği gibi yapmalarını veya yeminlerinden sonra yeminlerin kabul edilmemesinden korkmalarını daha iyi sağlar. Allah\'tan sakının, dinleyin. Allah fasık kimselere yol göstermez.'),
(778, 778, 5, 109, 'Mâide', 'Medeni', 'Allah peygamberleri topladığı gün, \"Size ne cevap verildi?\" der; onlar, \"Bizim bir bildiğimiz yoktur, doğrusu görülmeyenleri bilen ancak Sen\'sin\" derler.'),
(779, 779, 5, 110, 'Mâide', 'Medeni', 'Allah, \"Ey Meryem oğlu İsa! Sana ve anana olan nimetimi an\" demişti, \"Seni Ruhul Kudüs ile desteklemiştim; beşikte ve yetişkin iken insanlarla konuşuyordun; sana Kitap\'ı, hikmeti, Tevrat\'ı ve İncil\'i öğretmiştim. Sen iznimle, çamurdan kuş gibi bir şey yapmış ona üflemiştin de iznimle kuş olmuştu; anadan doğma körü, alacalıyı iznimle iyi etmiştin. Ölüleri iznimle diriltiyordun. İsrailoğullarına belgelerle geldiğinde, onlardan inkar edenler, \'Bu apaçık bir büyüdür\' demişlerdi de Ben onların sana zarar vermelerini önlemiştim.\"'),
(780, 780, 5, 111, 'Mâide', 'Medeni', 'Havarilere, \"Bana ve peygamberime inanın\" diye bildirmiştim, \"İnandık, bizim müslimler olduğumuza şahid ol\" demişlerdi.'),
(781, 781, 5, 112, 'Mâide', 'Medeni', 'Havariler, \"Ey Meryem oğlu İsa! Rabbin bize gökten bir sofra indirebilir mi?\" demişlerdi de, \"İnanıyorsanız Allah\'tan sakının\" demişti.'),
(782, 782, 5, 113, 'Mâide', 'Medeni', '\"Ondan yemeyi, kalblerimizin kanmasını ve senin bize doğru söylediğini bilmeyi, ona şahid olmayı istiyoruz\" dediler.'),
(783, 783, 5, 114, 'Mâide', 'Medeni', 'Meryem oğlu İsa, \"Allahım! Rabbimiz! Bize ve bizden sonra geleceklere bayram ve Sen\'den bir delil olarak gökten bir sofra indir, bizi rızıklandır, Sen rızık verenlerin en hayırlısısın\" dedi.'),
(784, 784, 5, 115, 'Mâide', 'Medeni', 'Allah, \"Ben onu size indireceğim; bundan sonra içinizden kim inkar ederse, dünyalarda kimseye azabetmiyeceğim şekilde ona azabedeceğim\" dedi.'),
(785, 785, 5, 116, 'Mâide', 'Medeni', 'Allah, \"Ey Meryem oğlu İsa! Sen mi insanlara Beni ve annemi Allah\'tan başka iki tanrı olarak benimseyin dedin?\" demişti de, \"Haşa, hak olmayan sözü söylemek bana yaraşmaz; eğer söylemişsem, şüphesiz Sen onu bilirsin; Sen, benim içimde olanı bilirsin; ben Senin içinde olanı bilmem; doğrusu görülmeyeni bilen ancak Sensin\" demişti, \"Ben onlara sadece \'Rabbim ve Rabbiniz olan Allah\'a kulluk edin\' diye bana emrettiğini söyledim. Aralarında bulunduğum müddetce onlar hakkında şahiddim, beni aralarından aldığında onları Sen gözlüyordun. Sen her şeye şahidsin.\"'),
(786, 786, 5, 117, 'Mâide', 'Medeni', 'Allah, \"Ey Meryem oğlu İsa! Sen mi insanlara Beni ve annemi Allah\'tan başka iki tanrı olarak benimseyin dedin?\" demişti de, \"Haşa, hak olmayan sözü söylemek bana yaraşmaz; eğer söylemişsem, şüphesiz Sen onu bilirsin; Sen, benim içimde olanı bilirsin; ben Senin içinde olanı bilmem; doğrusu görülmeyeni bilen ancak Sensin\" demişti, \"Ben onlara sadece \'Rabbim ve Rabbiniz olan Allah\'a kulluk edin\' diye bana emrettiğini söyledim. Aralarında bulunduğum müddetce onlar hakkında şahiddim, beni aralarından aldığında onları Sen gözlüyordun. Sen her şeye şahidsin.\"'),
(787, 787, 5, 118, 'Mâide', 'Medeni', '\"Onlara azabedersen, doğrusu onlar Senin kullarındır; onları bağışlarsan, Güçlü olan, Hakim olan şüphesiz ancak Sensin.\"'),
(788, 788, 5, 119, 'Mâide', 'Medeni', 'Allah, \"Bu, doğrulara doğruluklarının fayda verdiği gündür; ebedi ve temelli kalacakları, altlarından ırmaklar akan cennetler onlarındır. Allah onlardan hoşnud olmuştur, onlar da Allah\'tan hoşnud olmuşlardır, bu büyük kurtuluştur\" dedi.'),
(789, 789, 5, 120, 'Mâide', 'Medeni', 'Göklerin, yerin ve onlarda bulunanların hükümranlığı Allah\'ındır, Allah her şeye Kadir\'dir.'),
(790, 790, 6, 1, 'En’âm', 'Mekki', 'Hamd, gökleri ve yeri yaratan, karanlıkları ve aydınlığı vareden Allah\'a mahsustur. Öyle iken, inkar edenler Rablerine başkalarını eşit tutuyorlar.'),
(791, 791, 6, 2, 'En’âm', 'Mekki', 'O, sizi çamurdan yaratan, sonra size bir ecel tayin edendir. Belirli bir ecel O\'nun katındadır; sonra bir de şüphe edersiniz.'),
(792, 792, 6, 3, 'En’âm', 'Mekki', 'O, göklerin ve yerin Allah\'ı, içinizi dışınızı bilir, kazandıklarınızı da bilir.'),
(793, 793, 6, 4, 'En’âm', 'Mekki', 'Onlara Rablerinin ayetlerinden bir ayet geldikçe ondan yüz çevirirlerdi.'),
(794, 794, 6, 5, 'En’âm', 'Mekki', 'Gerçek kendilerine gelince onu yalanladılar. Alaya aldıkları şeyin haberleri kendilerine gelecektir.'),
(795, 795, 6, 6, 'En’âm', 'Mekki', 'Onlardan önce nice nesilleri yok ettiğimizi görmediler mi? Onları, sizi yerleştirmediğimiz bir şekilde yeryüzüne yerleştirmiş, gökten bol yağmur yağdırmış, altlarından ırmaklar akıtmıştık. Fakat onları günahlarından ötürü yok ettik ve ardlarından başka bir nesil yetiştirdik.'),
(796, 796, 6, 7, 'En’âm', 'Mekki', 'Sana Kitap\'ı kağıtta yazılı olarak indirmiş olsak da, elleriyle ona dokunsalar, inkar edenler yine de, \"Bu apaçık bir büyüdür\" derlerdi.'),
(797, 797, 6, 8, 'En’âm', 'Mekki', '\"Ona bir melek indirilmeli değil miydi?\" dediler. Bir melek indirmiş olsaydık iş bitmiş olurdu da onlara göz bile açtırılmazdı.'),
(798, 798, 6, 9, 'En’âm', 'Mekki', 'Biz onu melek kılsaydık, bir insan şeklinde yapardık da, düştükleri şüpheye onları yine düşürmüş olurduk.'),
(799, 799, 6, 10, 'En’âm', 'Mekki', 'And olsun ki, senden önce birçok peygamberler alaya alınmıştı, onlarla eğlenenleri, alaya aldıkları şey mahvetti.'),
(800, 800, 6, 11, 'En’âm', 'Mekki', 'De ki: \"Yeryüzünde gezip dolaşın, sonra da, yalanlayanların sonunun nasıl olduğuna bir bakın.\"'),
(801, 801, 6, 12, 'En’âm', 'Mekki', 'De ki: \"göklerde ve yerde olanlar kimindir?\", \"Allah\'ındır\" de. O, rahmet etmeyi kendi üzerine almıştır; and olsun ki, sizi vukuu şüphe götürmeyen kıyamet gününde toplayacaktır. Kendilerine yazık ettiler; çünkü onlar inanmazlar.'),
(802, 802, 6, 13, 'En’âm', 'Mekki', 'Gecede ve gündüzde bulunan O\'nundur. O işitendir, Bilen\'dir.'),
(803, 803, 6, 14, 'En’âm', 'Mekki', '\"Gökleri ve yeri yaratan, beslenmeyip besleyen Allah\'tan başka bir dost mu edinirim?\" de. \"Doğrusu ben ilk müslüman olmakla emrolundum\" de; asla ortak koşanlardan olma!'),
(804, 804, 6, 15, 'En’âm', 'Mekki', '\"Ben Rabbime karşı gelirsem, büyük günün azabından korkarım\" de.'),
(805, 805, 6, 16, 'En’âm', 'Mekki', 'O gün kim azabdan alıkonursa, şüphesiz o kimse rahmete erişmiştir. Bu, apaçık bir kurtuluştur.'),
(806, 806, 6, 17, 'En’âm', 'Mekki', 'Allah sana bir sıkıntı verirse, O\'ndan başkası gideremez. Sana bir iyilik verirse başkası onu engelleyemez. O, her şeye Kadir\'dir.'),
(807, 807, 6, 18, 'En’âm', 'Mekki', 'O, kullarının üstünde yegane tasarruf sahibidir. Hakim\'dir, haberdardır.'),
(808, 808, 6, 19, 'En’âm', 'Mekki', '\"Şahit olarak hangi şey daha büyüktür\" de. \"Allah benimle sizin aranızda şahiddir. Bu Kuran bana, sizi ve ulaştığı kimseleri uyarmam için vahyolundu; Allah\'la beraber başka tanrılar bulunduğuna siz mi şahidlik ediyorsunuz?\" de. \"Ben şehadet etmem\" de. \"O ancak tek Tanrıdır, doğrusu ben ortak koşmanızdan uzağım\" de.'),
(809, 809, 6, 20, 'En’âm', 'Mekki', 'Kendilerine Kitap verdiklerimiz, onu (peygamberi) çocuklarını tanıdıkları gibi tanırlar; fakat kendilerine yazık ettiler, çünkü onlar inanmazlar.'),
(810, 810, 6, 21, 'En’âm', 'Mekki', 'Allah\'a karşı yalan uyduran veya ayetlerini yalanlayandan daha zalim kimdir? Zalimler bunun için saadete ulaşamazlar.'),
(811, 811, 6, 22, 'En’âm', 'Mekki', 'Bir gün hepsini toplarız, sonra ortak koşanlara, \"İddia ettiğiniz ortaklarınız nerede?\" deriz.'),
(812, 812, 6, 23, 'En’âm', 'Mekki', 'Sonra, \"Rabbimiz Allah\'a and olsun ki bizler ortak koşanlar değildik\" demekten başka çare bulamazlar.'),
(813, 813, 6, 24, 'En’âm', 'Mekki', 'Kendilerine karşı nasıl yalan söylediklerine bak; uydurdukları şeyler de onlardan uzaklaştı.'),
(814, 814, 6, 25, 'En’âm', 'Mekki', 'Onlardan seni dinleyenler vardır, Kuran\'ı anlarlar diye kalblerine örtüler kulaklarına da ağırlık koyduk. Onlar her türlü mucizeyi görseler bile, yine de ona inanmazlar, nihayet sana geldiklerinde de seninle çekişirler. İnkar edenler, \"Bu, öncekilerin masallarından başka bir şey değildir\" derler.'),
(815, 815, 6, 26, 'En’âm', 'Mekki', 'Onlar Kuran\'dan alıkorlar ve ondan uzaklaşırlar. Böylece yalnız kendilerini mahvederler de farkına varamazlar.'),
(816, 816, 6, 27, 'En’âm', 'Mekki', 'Onların, ateşin kenarına getirilip durdurulduklarında, \"keşke dünyaya tekrar döndürülseydik, Rabbimiz\'in ayetlerini yalanlamasaydık ve inananlardan olsaydık\" dediklerini bir görsen!'),
(817, 817, 6, 28, 'En’âm', 'Mekki', 'Hayır; daha önce gizledikleri onlara göründü. Eğer geri döndürülseler yine kendilerine yasak edilen şeylere dönerler. Doğrusu onlar yalancıdırlar.'),
(818, 818, 6, 29, 'En’âm', 'Mekki', '\"Hayat ancak bu dünyadakinden ibarettir, biz dirilecek değiliz\" dediler.'),
(819, 819, 6, 30, 'En’âm', 'Mekki', 'Onları, Rablerinin huzuruna çıkarıldıkları zaman bir görsen! Allah: \"Bu gerçek değil mi?\" der; onlar, \"Evet, Rabbimiz hakkı için gerçektir\" derler. Allah da \"Öyleyse inkar etmenizden ötürü azabı tadın\" der.'),
(820, 820, 6, 31, 'En’âm', 'Mekki', 'Allah\'a kavuşmayı yalanlayanlar doğrusu kaybedenlerdir ki kıyamet saati onlara ansızın gelince, ağırlıklarını arkalarına yüklenerek, \"Dünyada işlediğimiz büyük kusurlardan ötürü yazıklar olsun bize\" derler. Dikkat edin, yüklendikleri şeyler ne kötüdür!'),
(821, 821, 6, 32, 'En’âm', 'Mekki', 'Dünya hayatı sadece oyun ve oyalanmadır; ahiret yurdu, sakınanlar için daha iyidir. Düşünmüyor musunuz?'),
(822, 822, 6, 33, 'En’âm', 'Mekki', 'Onların söylediklerinin seni üzeceğini elbette biliyoruz; doğrusu onlar seni yalancı saymıyorlar, fakat zalimler Allah\'ın ayetlerini bile bile inkar ediyorlar.'),
(823, 823, 6, 34, 'En’âm', 'Mekki', 'Senden önce nice peygamberler yalanlandı ve kendilerine yardımımız gelene kadar yalanlanmalarına ve sıkıştırılmaya katlandılar. Allah\'ın sözlerini değiştirebilecek yoktur; and olsun ki peygamberlerin haberi sana da geldi.'),
(824, 824, 6, 35, 'En’âm', 'Mekki', 'Onların yüz çevirmesi sana ağır gelince, eğer gücün yeri delmeye veya göğe merdiven dayamağa yetmiş olsaydı, onlara bir mucize göstermek isterdin. Allah dileseydi onları doğru yolda toplardı. Sakın bilmeyenlerden olma.'),
(825, 825, 6, 36, 'En’âm', 'Mekki', 'Ancak kulak verenler daveti kabul ederler. Ölüleri Allah diriltir, sonra O\'na dönerler.'),
(826, 826, 6, 37, 'En’âm', 'Mekki', '\"Rabbinden ona (Muhammed\'e) bir belge indirilseydi ya\" dediler. De ki: \"Doğrusu Allah bir belge indirmeye Kadir\'dir, fakat çoğu bilmezler.\"'),
(827, 827, 6, 38, 'En’âm', 'Mekki', 'Yerde yürüyen hayvanlar ve kanatlarıyla uçan kuşlar da ancak sizin gibi birer toplulukturlar. Kitap\'da Biz hiçbir şeyi eksik bırakmadık; onlar sonra Rablerine toplanacaklardır.'),
(828, 828, 6, 39, 'En’âm', 'Mekki', 'Ayetlerimizi yalanlayanlar karanlıklarda kalmış sağır ve dilsizlerdir. Allah kimi dilerse onu saptırır ve kimi dilerse onu doğru yola koyar.'),
(829, 829, 6, 40, 'En’âm', 'Mekki', 'De ki, \"Üzerinize Allah\'ın azabı gelse veya kıyamet saati size gelse, Allah\'tan başkasına mı yalvarırsınız? Doğru iseniz bana bildirin\".'),
(830, 830, 6, 41, 'En’âm', 'Mekki', 'Hayır; sadece O\'na yalvarırsınız; dilerse yalvardığınız şeyi giderir, siz de O\'na koştuğunuz ortakları unutursunuz.'),
(831, 831, 6, 42, 'En’âm', 'Mekki', 'Şüphesiz ki, senden önce ümmetlere peygamberler göndermiştik; onları yalvarsınlar diye darlık ve sıkıntıya sokmuştuk.'),
(832, 832, 6, 43, 'En’âm', 'Mekki', 'Hiç değilse, onlara şiddetimiz geldiği zaman yalvarıp yakarmalı değil miydiler? Lakin kalbleri katılaştı, şeytan da yaptıklarını onlara güzel gösterdi.'),
(833, 833, 6, 44, 'En’âm', 'Mekki', 'Kendilerine hatırlatılanı unuttuklarında, onlara her şeyin kapısını açtık; kendilerine verilene sevinince ansızın onları yakaladık da umutsuz kalıverdiler.'),
(834, 834, 6, 45, 'En’âm', 'Mekki', 'Zulmeden milletin kökü böylece kesildi. Hamd, Alemlerin Rabbi Allah\'a mahsustur.'),
(835, 835, 6, 46, 'En’âm', 'Mekki', 'De ki: \"Gördünüz mü? Allah, işitmenizi, gözlerinizi alsa, kalblerinizi kapasa, Allah\'tan başka hangi tanrı onu sizlere getirebilir?\" Ayetleri nasıl türlü türlü açıkladığımıza bir baksana, sonra da onlar yüz çevirirler.'),
(836, 836, 6, 47, 'En’âm', 'Mekki', 'De ki: \"Allah\'ın azabı size ansızın veya açıkça gelirse, zalimlerden başkası mı yok olur? Bana bildirin.\"'),
(837, 837, 6, 48, 'En’âm', 'Mekki', 'Peygamberleri ancak müjdeci ve uyarıcı olarak gönderiyoruz. Kim inanır ve nefsini ıslah ederse onlara korku yoktur, onlar üzülmeyeceklerdir.'),
(838, 838, 6, 49, 'En’âm', 'Mekki', 'Ayetlerimizi inkar edenlere yoldan çıkmalarından ötürü azab dokunacaktır.'),
(839, 839, 6, 50, 'En’âm', 'Mekki', 'De ki: \"Size Allah\'ın hazineleri elimdedir, demiyorum; gaybı da bilmiyorum; size, ben meleğim demiyorum, ben ancak bana vahyolunana uyuyorum.\" De ki: \"Görenle görmeyen bir midir? Düşünmüyor musunuz?\"'),
(840, 840, 6, 51, 'En’âm', 'Mekki', 'Rablerine toplanacaklarından korkanları Kuran ile uyar. O\'ndan başka bir dost ve aracıları yoktur. Umulur ki Allah\'tan sakınalar.'),
(841, 841, 6, 52, 'En’âm', 'Mekki', 'Sabah akşam, Rabblerinin rızasını isteyerek O\'na yalvaranları kovma. Onların hesabından sana bir sorumluluk yoktur, senin hesabından da onlara bir sorumluluk yoktur ki onları kovarak zulmedenlerden olasın.'),
(842, 842, 6, 53, 'En’âm', 'Mekki', 'Böylece, \"Aramızdan Allah bunlara mı iyilikte bulundu?\" demeleri için onları birbiriyle denedik. Allah şükredenleri iyi bilen değil midir?'),
(843, 843, 6, 54, 'En’âm', 'Mekki', 'Ayetlerimize inananlar sana gelince: \"Size selam olsun\" de. Rabbiniz, sizden kim bilmeyerek fenalık işler de arkasından tövbe eder ve nefsini düzeltirse, ona rahmet etmeyi kendi üzerine almıştır. O, bağışlar ve merhamet eder.'),
(844, 844, 6, 55, 'En’âm', 'Mekki', 'Suçluların yolu belli olsun diye, böylece ayetleri uzun uzun açıklarız.'),
(845, 845, 6, 56, 'En’âm', 'Mekki', 'De ki: \"Allah\'tan başka, yalvardıklarınıza kulluk etmekten menolundum.\" \"Sizin heveslerinize uymayacağım, yoksa sapıtmış, doğru yolda gidenlerden olmamış olurum\" de.'),
(846, 846, 6, 57, 'En’âm', 'Mekki', 'De ki: \"Ben Rabbim\'den bir belgeye dayanmaktayım, halbuki siz onu yalanladınız; acele istediğiniz de elimde değildir. Hüküm ancak Allah\'ındır. O, hükmedenlerin en iyisi olarak gerçeği anlatır.\"'),
(847, 847, 6, 58, 'En’âm', 'Mekki', 'De ki: \"Acele istediğiniz şey elimde olsaydı, benimle aranızdaki iş bitmiş olurdu.\" Allah zulmedenleri en iyi bilendir.'),
(848, 848, 6, 59, 'En’âm', 'Mekki', 'Gaybın anahtarları O\'nun katındadır, onları ancak O bilir. Karada ve denizde olanı bilir. Düşen yaprağı, yerin karanlıklarında olan taneyi, yaşı kuruyu ki apaçık Kitap\'tadır ancak O bilir.'),
(849, 849, 6, 60, 'En’âm', 'Mekki', 'Geceleyin sizi ölü gibi uyutan, gündüzün yaptıklarınızı bilen, mukadder olan hayat süreniz doluncaya kadar gündüzleri sizi tekrar kaldıran O\'dur. Sonra dönüşünüz O\'nadır, işlediklerinizi size bildirecektir.'),
(850, 850, 6, 61, 'En’âm', 'Mekki', 'O, kulların üstünde yegane Hakim\'dir, size koruyucular gönderir. Artık birinize ölüm gelince elçilerimiz, bir eksiklik yapmaksızın onun canını alırlar, sonra gerçek Mevlalarına döndürürler. Haberiniz olsun, hüküm O\'nundur. O, hesap görenlerin en süratlisidir.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(851, 851, 6, 62, 'En’âm', 'Mekki', 'O, kulların üstünde yegane Hakim\'dir, size koruyucular gönderir. Artık birinize ölüm gelince elçilerimiz, bir eksiklik yapmaksızın onun canını alırlar, sonra gerçek Mevlalarına döndürürler. Haberiniz olsun, hüküm O\'nundur. O, hesap görenlerin en süratlisidir.'),
(852, 852, 6, 63, 'En’âm', 'Mekki', 'De ki: \"Kara ve denizin karanlıklarından sizi kim kurtarır? Bundan bizi kurtarırsan şükredenlerden olacağız diye O\'na gizli gizli yalvarır yakarırsınız.\"'),
(853, 853, 6, 64, 'En’âm', 'Mekki', 'De ki: \"Allah sizi ondan ve her sıkıntıdan kurtarır, sonra da O\'na ortak koşarsınız.\"'),
(854, 854, 6, 65, 'En’âm', 'Mekki', 'De ki: \"Üstünüzden ve altınızdan size azab göndermeğe, sizi fırka fırka yapıp kiminize kiminizin hıncını tattırmağa Kadir olan O\'dur.\" Anlasınlar diye ayetleri nasıl yerli yerince açıkladığımıza bak.'),
(855, 855, 6, 66, 'En’âm', 'Mekki', 'Gerçekten, senin milletin Kuran\'ı yalanladı. \"Cezanızı ben verecek değilim\" de.'),
(856, 856, 6, 67, 'En’âm', 'Mekki', 'Her haberin gerçekleşeceği bir zaman vardır ki siz onu yakında bileceksiniz.'),
(857, 857, 6, 68, 'En’âm', 'Mekki', 'Ayetlerimizi çekişmeye dalanları görünce, başka bir bahse geçmelerine kadar onlardan yüz çevir. Şeytan sana unutturursa hatırladıktan sonra artık zulmedenlerle beraber oturma.'),
(858, 858, 6, 69, 'En’âm', 'Mekki', 'Sakınan kimselere, onların hesaplarından bir sorumluluk yoktur. Fakat bir hatırlatmadır; belki sakınırlar.'),
(859, 859, 6, 70, 'En’âm', 'Mekki', 'Dinlerini oyun ve eğlenceye alanları, dünya hayatının aldattığı kimseleri bırak. Kuran ile öğüt ver ki, bir kimse kazandığıyla helake düşmeye görsün, o takdirde Allah\'dan başka ona ne bir yardımcı, ne de bir kurtarıcı bulunur; her türlü fidyeyi de verse kabul olunmaz. Kazandıklarından ötürü yok olanlar işte bunlardır. İnkar etmelerinden dolayı kızgın içecek ve can yakıcı azab onlaradır.'),
(860, 860, 6, 71, 'En’âm', 'Mekki', 'De ki: \"Arkadaşları bize gel diye doğru yola çağırırken, şeytanların yeryüzünde şaşırttıkları bir kimse gibi geriye mi dönelim. Allah bizi doğru yola eriştirdikten sonra, bize faydası olmayan, zarar da veremeyen Allah\'tan başka şeylere mi yalvaralım?\" De ki, \"Doğru yol ancak Allah\'ın yoludur. Alemlerin Rabbine teslim olarak namaz kılın, Allah\'tan sakının diye emrolunduk.\" Kendisine toplanacağınız O\'dur.'),
(861, 861, 6, 72, 'En’âm', 'Mekki', 'De ki: \"Arkadaşları bize gel diye doğru yola çağırırken, şeytanların yeryüzünde şaşırttıkları bir kimse gibi geriye mi dönelim. Allah bizi doğru yola eriştirdikten sonra, bize faydası olmayan, zarar da veremeyen Allah\'tan başka şeylere mi yalvaralım?\" De ki, \"Doğru yol ancak Allah\'ın yoludur. Alemlerin Rabbine teslim olarak namaz kılın, Allah\'tan sakının diye emrolunduk.\" Kendisine toplanacağınız O\'dur.'),
(862, 862, 6, 73, 'En’âm', 'Mekki', 'Gökleri ve yeri gerçekle yaratan O\'dur ki \"Ol\" dediği gün (an) hemen olur; sözü gerçektir. Sura üfleneceği gün hükümranlık O\'nundur. Görülmeyeni de görüleni de bilir. O Hakim\'dir, haberdardır.'),
(863, 863, 6, 74, 'En’âm', 'Mekki', 'İbrahim, babası Azer\'e, \"Putları tanrı olarak mı benimsiyorsun? Doğrusu ben seni ve milletini açık bir sapıklık içinde görüyorum\" demişti.'),
(864, 864, 6, 75, 'En’âm', 'Mekki', 'Yakinen bilenlerden olması için İbrahim\'e göklerin ve yerin hükümranlığını şöylece gösteriyorduk:'),
(865, 865, 6, 76, 'En’âm', 'Mekki', 'Gece basınca bir yıldız gördü, \"işte bu benim Rabbim!\" dedi; yıldız batınca, \"batanları sevmem\" dedi.'),
(866, 866, 6, 77, 'En’âm', 'Mekki', 'Ayı doğarken görünce, \"işte bu benim Rabbim!\" dedi, batınca, \"Rabbim beni doğruya eriştirmeseydi and olsun ki sapıklardan olurdum\" dedi.'),
(867, 867, 6, 78, 'En’âm', 'Mekki', 'Güneşi doğarken görünce \"işte bu benim Rabbim, bu daha büyük!\" dedi; batınca, \"Ey milletim! Doğrusu ben ortak koştuklarınızdan uzağım\" dedi.'),
(868, 868, 6, 79, 'En’âm', 'Mekki', '\"Doğrusu ben yüzümü, gökleri ve yeri yaratana, doğruya yönelerek çevirdim, ben ortak koşanlardan değilim.\"'),
(869, 869, 6, 80, 'En’âm', 'Mekki', 'Milleti onunla tartışmaya girişti. \"Beni doğru yola eriştirmişken, Allah hakkında benimle mi tartışıyorsunuz? O\'na ortak koştuklarınızdan korkmuyorum, meğer ki Rabbim bir şeyi dilemiş ola. Rabbim ilimce her şeyi kuşatmıştır; hala öğüt kabul etmez misiniz?\" dedi.'),
(870, 870, 6, 81, 'En’âm', 'Mekki', '\"Allah\'a koştuğunuz ortaklardan nasıl korkarım? Oysa siz, Allah\'ın hakkında size bir delil indirmediği bir şeyi O\'na ortak koşmaktan korkmuyorsunuz. İki taraftan hangisine güvenmek daha gereklidir, bir bilseniz.\"'),
(871, 871, 6, 82, 'En’âm', 'Mekki', 'İşte güven; onlara, inanıp haksızlık karıştırmayanlaradır. Onlar doğru yoldadırlar.'),
(872, 872, 6, 83, 'En’âm', 'Mekki', 'Bu, İbrahim\'e, milletine karşı verdiğimiz hüccetimizdir. Dilediğimizi derecelerle yükseltiriz. Doğrusu Rabbin Hakim\'dir, Bilen\'dir.'),
(873, 873, 6, 84, 'En’âm', 'Mekki', 'Ona İshak\'ı, Yakub\'u bağışladık, her birini doğru yola eriştirdik. Daha önce Nuh\'u ve soyundan Davud\'u, Süleyman\'ı, Eyyub\'u, Yusuf\'u, Musa\'yı ve Harun\'u -ki işlerini iyi yapanlara böylece karşılık veririz-, Zekeriya\'yı, Yahya\'yı, İsa\'yı ve İlyas\'ı -ki hepsi iyilerdendir-, İsmail\'i, Elyesa\'ı, Yunus\'u, Lut\'u -ki hepsini dünyalara üstün kıldık- doğru yola eriştirdik.'),
(874, 874, 6, 85, 'En’âm', 'Mekki', 'Ona İshak\'ı, Yakub\'u bağışladık, her birini doğru yola eriştirdik. Daha önce Nuh\'u ve soyundan Davud\'u, Süleyman\'ı, Eyyub\'u, Yusuf\'u, Musa\'yı ve Harun\'u -ki işlerini iyi yapanlara böylece karşılık veririz-, Zekeriya\'yı, Yahya\'yı, İsa\'yı ve İlyas\'ı -ki hepsi iyilerdendir-, İsmail\'i, Elyesa\'ı, Yunus\'u, Lut\'u -ki hepsini dünyalara üstün kıldık- doğru yola eriştirdik.'),
(875, 875, 6, 86, 'En’âm', 'Mekki', 'Ona İshak\'ı, Yakub\'u bağışladık, her birini doğru yola eriştirdik. Daha önce Nuh\'u ve soyundan Davud\'u, Süleyman\'ı, Eyyub\'u, Yusuf\'u, Musa\'yı ve Harun\'u -ki işlerini iyi yapanlara böylece karşılık veririz-, Zekeriya\'yı, Yahya\'yı, İsa\'yı ve İlyas\'ı -ki hepsi iyilerdendir-, İsmail\'i, Elyesa\'ı, Yunus\'u, Lut\'u -ki hepsini dünyalara üstün kıldık- doğru yola eriştirdik.'),
(876, 876, 6, 87, 'En’âm', 'Mekki', 'Babalarından, soylarından, kardeşlerinden bir kısmını seçtik ve doğru yola eriştirdik.'),
(877, 877, 6, 88, 'En’âm', 'Mekki', 'Bu, Allah\'ın kullarından dilediğini eriştirdiği yoludur. Eğer ortak koşsalarda amelleri boşa çıkardı.'),
(878, 878, 6, 89, 'En’âm', 'Mekki', 'Kendilerine kitap, hüküm ve peygamberlik verdiklerimiz işte bunlardır. Kafirler onları inkar ederlerse, inkar etmeyecek bir milleti onlara vekil kılarız.'),
(879, 879, 6, 90, 'En’âm', 'Mekki', 'İşte bunlar Allah\'ın doğru yola eriştirdikleridir, onların yoluna uy, \"Sizden buna karşılık bir ücret istemem, bu sadece herkes için bir hatırlatmadır\" de.'),
(880, 880, 6, 91, 'En’âm', 'Mekki', '\"Allah hiçbir insana bir şey indirmemiştir\" demekle Allah\'ı gereği gibi değerlendiremediler. De ki: \"Musa\'nın insanlara nur ve yol gösterici olarak getirdiği Kitap\'ı kim indirdi? Ki siz onu kağıtlara yazıp bir kısmını gösterip çoğunu gizlersiniz, atalarınızın ve sizin bilmediğiniz size onunla öğretilmiştir.\" \"Allah\" de, sonra da onları daldıkları sapıklıkta bırak, oynasınlar.'),
(881, 881, 6, 92, 'En’âm', 'Mekki', 'Bu indirdiğimiz, kendinden öncekileri doğrulayan, Mekkelileri ve etrafındakileri uyaran mübarek Kitap\'dır. Ahirete inananlar buna inanırlar, namazlarına da devam ederler.'),
(882, 882, 6, 93, 'En’âm', 'Mekki', 'Allah\'a karşı yalan uydurandan veya kendisine bir şey vahyedilmemişken \"Bana vahyolundu, Allah\'ın indirdiği gibi ben de indireceğim\" diyenden daha zalim kim olabilir? Bu zalimleri can çekişirlerken melekler ellerini uzatmış, \"Canlarınızı verin, bugün Allah\'a karşı haksız yere söylediklerinizden, O\'nun ayetlerine büyüklük taslamanızdan ötürü alçaltıcı azabla cezalandırılacaksınız\" derken bir görsen!'),
(883, 883, 6, 94, 'En’âm', 'Mekki', 'Onlara: \"And olsun ki, sizi ilk defa yarattığımız gibi size verdiklerimizi ardınızda bırakarak bize birer birer geldiniz; içinizde Allah\'ın ortakları olduğunu sandığınız şefaatçılarınızı beraber görmüyoruz. And olsun ki aranızdaki bağlar kopmuş, ortak sandıklarınız sizden ayrılmışlardır\" denecek.'),
(884, 884, 6, 95, 'En’âm', 'Mekki', 'Taneyi ve çekirdeği yaran şüphesiz Allah\'tır; ölüyü çıkarır. İşte Allah budur, nasıl yüz çevirirsiniz?'),
(885, 885, 6, 96, 'En’âm', 'Mekki', 'Tanyerini ağartan, geceyi dinlenme zamanı, güneş ve ayı vakit ölçüsü kılandır. Bu, Güçlü olan\'ın, Bilen\'in nizamıdır.'),
(886, 886, 6, 97, 'En’âm', 'Mekki', 'O, yıldızları kara ve denizin karanlıklarında yol bulasınız diye sizin için var edendir. Bilen millet için ayetleri uzun uzadıya açıkladık.'),
(887, 887, 6, 98, 'En’âm', 'Mekki', 'O, sizi bir tek nefisten, babaların sulbünde kararlaşmış ve anaların rahminde kararlaşmakta olarak yaratandır. Anlayan millet için ayetleri uzun uzadıya açıkladık.'),
(888, 888, 6, 99, 'En’âm', 'Mekki', 'O, gökten su indirendir. Her bitkiyi onunla bitirdik, ondan bitirdiğimiz yeşilden, birbirine benzeyen ve benzemeyen yığın yığın taneler, hurmaların tomurcuklarından sarkan salkımlar, üzüm bağları, zeytin ve nar çıkardık. Ürün verdiklerinde ürünlerine, olgunlaşmalarına bir bakın. Bunlarda, inananlar için, şüphesiz, deliller vardır.'),
(889, 889, 6, 100, 'En’âm', 'Mekki', 'Cinleri O yaratmışken kafirler Allah\'a ortak koştular. Körü körüne O\'na oğullar ve kızlar uydurdular. Haşa, O onların vasıflandırmalarından yücedir.'),
(890, 890, 6, 101, 'En’âm', 'Mekki', 'O, gökleri ve yeri yoktan yaratandır. Zevcesi olmadan nasıl çocuğu olabilir? Oysa her şeyi O yaratmıştır, her şeyi bilir.'),
(891, 891, 6, 102, 'En’âm', 'Mekki', 'İşte Rabbiniz, Allah budur. O\'ndan başka tanrı yoktur, her şeyin yaratanıdır. Öyleyse O\'na kulluk edin; O her şeye de vekildir.'),
(892, 892, 6, 103, 'En’âm', 'Mekki', 'Gözler O\'nu görmez, O bütün gözleri görür. O Latif\'tir, haberdardır.'),
(893, 893, 6, 104, 'En’âm', 'Mekki', 'Doğrusu size Rabbiniz\'den açık belgeler gelmiştir; kim görürse kendi lehine ve kim körlük ederse kendi aleyhinedir. Ben sizin bekçiniz değilim.'),
(894, 894, 6, 105, 'En’âm', 'Mekki', 'Sana, \"Sen okumuşsun\" derler; oysa Biz, öğrenecek kimselere ayetleri böylece türlü türlü açıklamaktayız.'),
(895, 895, 6, 106, 'En’âm', 'Mekki', 'Rabbin\'den sana vahyolunana uy, O\'ndan başka tanrı yoktur, puta tapanlardan yüz çevir.'),
(896, 896, 6, 107, 'En’âm', 'Mekki', 'Allah dileseydi puta tapmazlardı. Seni onlara koruyucu yapmadık, onların vekili de değilsin.'),
(897, 897, 6, 108, 'En’âm', 'Mekki', 'Allah\'tan başka yalvardıklarına sövmeyin ki, onlar da cahillikle ileri giderek Allah\'a sövmesinler. Böylece her ümmete işini güzel gösterdik, sonra dönüşleri Rab\'lerinedir. O, işlediklerini haber verir.'),
(898, 898, 6, 109, 'En’âm', 'Mekki', 'Kendilerine bir mucize gösterilirse, mutlaka ona inanacaklarına dair bütün güçleriyle Allah\'a yemin ederler. De ki: \"Mucizeler, ancak Allah katındadır\"; onların, mucize geldiği zaman da inanmayacaklarını anlamıyor musunuz?'),
(899, 899, 6, 110, 'En’âm', 'Mekki', 'Onların kalblerini, gözlerini, ona ilk defa inanmadıkları gibi çeviririz; onları taşkınlıkları içinde şaşkın şaşkın bırakırız.'),
(900, 900, 6, 111, 'En’âm', 'Mekki', 'Eğer biz onlara melekleri indirsek, ölüler onlarla konuşsa ve her şeyi karşılarına toplasaydık, Allah dilemedikçe, yine de inanmazlardı; fakat onların çoğu bunu bilmiyorlar.'),
(901, 901, 6, 112, 'En’âm', 'Mekki', 'Aldatmak için birbirlerine cazip sözler fısıldayan cin ve insan şeytanlarını her peygambere düşman yaptık. Bu şeytanlar ahirete inanmayanların kalblerinin o sözlere yönelmesi, ondan hoşnut olması ve kendilerinin işledikleri suçları işlemeleri için böyle yaparlar. Rabbin dileseydi bunu yapamazlardı, sen onları iftiraları ile başbaşa bırak;'),
(902, 902, 6, 113, 'En’âm', 'Mekki', 'Aldatmak için birbirlerine cazip sözler fısıldayan cin ve insan şeytanlarını her peygambere düşman yaptık. Bu şeytanlar ahirete inanmayanların kalblerinin o sözlere yönelmesi, ondan hoşnut olması ve kendilerinin işledikleri suçları işlemeleri için böyle yaparlar. Rabbin dileseydi bunu yapamazlardı, sen onları iftiraları ile başbaşa bırak;'),
(903, 903, 6, 114, 'En’âm', 'Mekki', '\"Allah size Kitap\'ı açık açık indirmişken O\'ndan başka bir hakem mi isteyeyim?\" Kendilerine Kitap verdiklerimiz, onun gerçekten Rableri katından indirilmiş olduğunu bilirler. Öyleyse, sen şüpheye düşenlerden olma!'),
(904, 904, 6, 115, 'En’âm', 'Mekki', 'Rabbinin sözü, doğruluk ve adaletle tamamlandı. O\'nun sözlerini değiştirebilecek yoktur. O, işitir ve bilir.'),
(905, 905, 6, 116, 'En’âm', 'Mekki', 'Yeryüzündekilerin çoğunluğuna itaat edersen seni Allah yolundan saptırırlar. Onlar ancak zanna uyarlar, sadece tahminde bulunurlar.'),
(906, 906, 6, 117, 'En’âm', 'Mekki', 'Doğrusu Rabbin, yolundan kimin saptığını daha iyi bilir. Doğru yolda olanları da en iyi O bilir.'),
(907, 907, 6, 118, 'En’âm', 'Mekki', 'Allah\'ın ayetlerine inanıyorsanız, üzerine Allah\'ın adı anılmış olan şeyden yiyin.'),
(908, 908, 6, 119, 'En’âm', 'Mekki', 'Size ne oluyor ki, Allah size darda kalmanızın dışında, haram olanları genişçe anlatmışken adının üzerine anıldığı şeyden yemiyorsunuz? Doğrusu çoğunluk, heva ve heveslerine uyarak, bilmeden sapıtıyorlar. Aşırı gidenleri en iyi bilen Rabbindir.'),
(909, 909, 6, 120, 'En’âm', 'Mekki', 'Günahın açığını da gizlisini de bırakın. Günah kazananlar, kazandıklarına karşılık şüphesiz ceza göreceklerdir.'),
(910, 910, 6, 121, 'En’âm', 'Mekki', 'Üzerine Allah\'ın adının anılmadığı kesilmiş hayvanları yemeyin, bunu yapmak Allah\'ın yolundan çıkmaktır. Doğrusu şeytanlar sizinle tartışmaları için dostlarına fısıldarlar, eğer onlara itaat ederseniz şüphesiz siz müşrik olursunuz.'),
(911, 911, 6, 122, 'En’âm', 'Mekki', 'Ölü iken kalbini diriltip, insanlar arasında yürürken önünü aydınlatacak bir nur verdiğimiz kimsenin durumu, karanlıklarda kalıp çıkamayan kimsenin durumu gibi midir? Kafirlere de, işledikleri güzel gösterilmiştir.'),
(912, 912, 6, 123, 'En’âm', 'Mekki', 'Bunun gibi, her kasabanın bir takım ileri gelenlerini orada hile yapan suçlular kıldık. Oysa yalnız kendilerine hile yaparlar da farkına varmazlar.'),
(913, 913, 6, 124, 'En’âm', 'Mekki', 'Onlara bir ayet geldiği zaman, \"Allah\'ın peygamberlerine verilen bize de verilmedikçe inanmayız\" derler. Allah, peygamberliğini vereceği kimseyi daha iyi bilir. Suç işleyenlere Allah katından bir aşağılık ve hilelerinden ötürü de şiddetli bir azab erişecektir.'),
(914, 914, 6, 125, 'En’âm', 'Mekki', 'Allah kimi doğru yola koymak isterse onun kalbini İslamiyet\'e açar, kimi de saptırmak isterse, göğe yükseliyormuş gibi, kalbini dar ve sıkıntılı kılar. Allah böylece, inanmayanları küfür bataklığında bırakır.'),
(915, 915, 6, 126, 'En’âm', 'Mekki', 'Rabbinin, dosdoğru yolu işte budur. İbret alan kimselere ayetleri uzun uzadıya açıkladık.'),
(916, 916, 6, 127, 'En’âm', 'Mekki', 'Rablerinin katında selamet yurdu onlarındır. O, işlediklerinden ötürü onların dostudur.'),
(917, 917, 6, 128, 'En’âm', 'Mekki', 'Allah hepsini toplayacağı gün, \"Ey cin topluluğu! İnsanların çoğunu yoldan çıkardınız\" der, insanlardan onlara uymuş olanlar, \"Rabbimiz! Bir kısmımız bir kısmımızdan faydalandık ve bize tayin ettiğin sürenin sonuna ulaştık\" derler. \"Cehennem, Allah\'ın dilemesine bağlı olarak, temelli kalacağınız durağınızdır\" der. Doğrusu Rabbin hakimdir, bilendir.'),
(918, 918, 6, 129, 'En’âm', 'Mekki', 'Zalimlerin bir kısmını, kazandıklarından ötürü diğer bir kısmına böylece musallat ederiz.'),
(919, 919, 6, 130, 'En’âm', 'Mekki', '\"Ey cin ve insan topluluğu! Size ayetlerimi anlatan, bugünle karşılaşmanızdan sizi uyaran peygamberler gelmedi mi?\" \"Kendi hakkımızda şahidiz\" derler. Dünya hayatı onları aldattı da inkarcı olduklarına, kendi aleyhlerinde şahidlik ettiler.'),
(920, 920, 6, 131, 'En’âm', 'Mekki', 'Bu, haberleri yokken kasabalar halkını Allah\'ın haksız yere yok etmeyeceğinden dolayıdır.'),
(921, 921, 6, 132, 'En’âm', 'Mekki', 'İşlediklerine karşılık her birinin dereceleri vardır. Rabbin onların işlediklerinden habersiz değildir.'),
(922, 922, 6, 133, 'En’âm', 'Mekki', 'Rabbin müstağni ve rahmet sahibidir. Dilerse, sizi başka bir milletin soyundan getirdiği gibi, sizi yok eder, dilediğini yerinize getirir.'),
(923, 923, 6, 134, 'En’âm', 'Mekki', 'Size vadedilen, mutlaka yerine gelecektir; siz O\'nu aciz kılamazsınız.'),
(924, 924, 6, 135, 'En’âm', 'Mekki', 'De ki, \"Ey milletim! Durumunuzun gerektirdiğini yapın, doğrusu ben de yapacağım. Sonucun kimin için hayırlı olacağını bileceksiniz. Zulmedenler şüphesiz kurtulamazlar.\"'),
(925, 925, 6, 136, 'En’âm', 'Mekki', 'Kendi zanlarına göre, \"Bu Allah\'ındır, bu da putlarımızındır\" diyerek, Allah\'ın yarattığı hayvanlar ve ekinlerden pay ayırdılar. Putları için ayırdıkları Allah için verilmez, ama Allah için ayırdıkları putlarına verilirdi; ne kötü hüküm veriyorlardı!'),
(926, 926, 6, 137, 'En’âm', 'Mekki', 'Böylece, putlara hizmet edenler, puta tapanların çoğunu helake sürüklemek, dinlerini karma karışık etmek için çocuklarını öldürmelerini onlara iyi göstermişlerdir. Allah dileseydi bunu yapamazlardı. Sen onları ve iftiralarını bir tarafa bırak.'),
(927, 927, 6, 138, 'En’âm', 'Mekki', '\"Bu hayvanlar ve ekinleri dilediğimizden başkasının yemesi yasaktır; bir kısım hayvanların sırtlarına yük vurmak da haramdır\" iddiasında bulunarak ve bir kısım hayvanları keserken de Allah\'ın adını anmamak suretiyle O\'na iftira ederler. Allah, yaptıkları iftiralara karşı onları cezalandıracaktır.'),
(928, 928, 6, 139, 'En’âm', 'Mekki', '\"Bu hayvanların karınlarında olan yavrular yalnız erkeklerimize mahsus olup, eşlerimize yasaktır. Ölü doğacak olursa hepsi ona ortak olurlar\" dediler. Allah bu türlü sözlerin cezasını verecektir, çünkü O hakimdir, bilendir.'),
(929, 929, 6, 140, 'En’âm', 'Mekki', 'Beyinsizlikleri yüzünden, körü körüne çocuklarını öldürenler ve Allah\'ın kendilerine verdiği nimetleri Allah\'a iftira ederek haram sayanlar mahvolmuşlardır; onlar sapıtmışlardır, zaten doğru yolda da değillerdi.'),
(930, 930, 6, 141, 'En’âm', 'Mekki', 'Çardaklı ve çardaksız bağları inşa eden Allah\'tır. Tadları çeşitli ekin ve hurmaları, zeytin ve narı birbirine benzer ve benzemez şekilde yaratan O\'dur. Ürün verdiği zaman ürününden yiyin, devşirildiği ve biçildiği gün hakkını verin; israf etmeyin, çünkü Allah müsrifleri sevmez.'),
(931, 931, 6, 142, 'En’âm', 'Mekki', 'Hayvanları da yük ve kesim için yaratan Allah\'tır. Allah\'ın size verdiği rızıktan yiyin, şeytana ayak uydurmayın, o size apaçık bir düşmandır.'),
(932, 932, 6, 143, 'En’âm', 'Mekki', 'Sekiz çift: Koyundan iki ve keçiden iki; de ki: \"İki erkeği mi, yoksa iki dişiyi mi veya o iki dişinin rahimlerinde bulunan yavruları mı haram kılmıştır? Doğru sözlü iseniz bana bilgiye dayanarak cevap verin.\"'),
(933, 933, 6, 144, 'En’âm', 'Mekki', 'Deveden iki, sığırdan iki yaratmıştır; de ki: \"İki erkeği mi, yoksa iki dişiyi mi veya o iki dişinin rahimlerinde bulunan yavruları mı haram kılmıştır? Yoksa Allah size bunları buyururken orada mı idiniz?\" İnsanları, bilmediklerinden sapıtmak için Allah\'a karşı yalan uyduranlardan daha zalim kimdir? Allah, zalim milleti doğru yola eriştirmez.'),
(934, 934, 6, 145, 'En’âm', 'Mekki', 'De ki: \"Bana vahyolunanda, leş, akıtılmış kan, domuz eti ki pistir ve günah işlenerek Allah\'tan başkası adına kesilen hayvandan başkasını yemenin haram olduğuna dair bir emir bulamıyorum; fakat darda kalan, başkasının payına el uzatmamak ve zaruret miktarını aşmamak üzere bunlardan da yiyebilir.\" Doğrusu Rabbin bağışlar ve merhamet eder.'),
(935, 935, 6, 146, 'En’âm', 'Mekki', 'Yahudilere tırnaklı her hayvanı haram kıldık. Onlara sığır ve davarın sırt, bağırsak ve kemik yağları hariç, iç yağlarını da haram kıldık. Aşırı gitmelerinden ötürü onları bu şekilde cezalandırdık. Biz şüphesiz doğru sözlüyüzdür.'),
(936, 936, 6, 147, 'En’âm', 'Mekki', 'Seni yalanlarlarsa, \"Rabbinizin rahmeti geniştir; O\'nun azabı suçlu milletten geri çevrilemez\" de.'),
(937, 937, 6, 148, 'En’âm', 'Mekki', 'Puta tapanlar, \"Allah dileseydi babalarımız ve biz puta tapmaz ve hiçbir şeyi haram kılmazdık\" diyecekler; onlardan öncekiler de, Bizim şiddetli azabımızı tadana kadar böyle demişlerdi. Onlara \"Bize karşı çıkarabileceğiniz bir bilginiz var mı? Siz ancak zanna uyuyorsunuz ve sadece tahminde bulunuyorsunuz\" de.'),
(938, 938, 6, 149, 'En’âm', 'Mekki', '\"Üstün delil Allah\'ın delilidir. O dileseydi hepinizi doğru yola eriştirirdi\" de.'),
(939, 939, 6, 150, 'En’âm', 'Mekki', 'De ki: \"Allah\'ın bunu haram kıldığına şahidlik edecek şahidlerinizi getirin\". Şahidlik ederlerse, onlarla beraber olup sözlerini kabullenme; ayetlerimizi yalanlayanların ve ahirete inanmayanların heveslerine uyma; onlar Rablerine başkalarını eşit tutuyorlar.'),
(940, 940, 6, 151, 'En’âm', 'Mekki', 'De ki: \"Gelin size Rabbinizin haram kıldığı şeyleri söyleyeyim: O\'na hiçbir şeyi ortak koşmayın, anaya babaya iyilik yapın, yoksulluk korkusuyla çocuklarınızı öldürmeyin, sizin ve onların rızkını veren Biziz, gizli ve açık kötülüklere yaklaşmayın, Allah\'ın haram kıldığı cana haksız yere kıymayın. Allah bunları size düşünesiniz diye buyurmaktadır.\"'),
(941, 941, 6, 152, 'En’âm', 'Mekki', 'Yetim malına, erginlik çağına erişene kadar en iyi şeklin dışında yaklaşmayın; ölçüyü ve tartıyı doğru yapın. Biz kişiye ancak gücünün yeteceği kadar yükleriz. Konuştuğunuzda, akraba bile olsa sözünüzde adil olun. Allah\'ın ahdini yerine getirin. Allah size bunları öğüt almanız için buyurmaktadır.'),
(942, 942, 6, 153, 'En’âm', 'Mekki', 'Bu, dosdoğru olan yoluma uyun. Sizi Allah yolundan ayrı düşürecek yollara uymayın. Allah size bunları sakınasınız diye buyurmaktadır.'),
(943, 943, 6, 154, 'En’âm', 'Mekki', 'Sonra, iyilik işleyenlere nimeti tamamlamak, her şeyi uzun uzadıya açıklamak, doğruyu göstermek ve rahmet olmak üzere Musa\'ya Kitap\'ı verdik. Rablerine kavuşacaklarına belki artık inanırlar.'),
(944, 944, 6, 155, 'En’âm', 'Mekki', 'Bu, indirdiğimiz kutsal Kitap\'dır, ona uyun. \"Bizden önce iki topluluğa kitap indirildi, bizim onların okuduklarından haberimiz yok\" demekten veya \"Bize kitap indirilseydi onlardan daha doğru yolda olurduk\" demekten sakının ki merhamet olunasınız. Şüphesiz o, size Rabbinizden belge, yol gösteren ve rahmet olarak gelmiştir. Allah\'ın ayetlerini yalanlayandan ve onlardan yüz çevirenden daha zalim kimdir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü, kötü bir azabla cezalandıracağız.'),
(945, 945, 6, 156, 'En’âm', 'Mekki', 'Bu, indirdiğimiz kutsal Kitap\'dır, ona uyun. \"Bizden önce iki topluluğa kitap indirildi, bizim onların okuduklarından haberimiz yok\" demekten veya \"Bize kitap indirilseydi onlardan daha doğru yolda olurduk\" demekten sakının ki merhamet olunasınız. Şüphesiz o, size Rabbinizden belge, yol gösteren ve rahmet olarak gelmiştir. Allah\'ın ayetlerini yalanlayandan ve onlardan yüz çevirenden daha zalim kimdir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü, kötü bir azabla cezalandıracağız.'),
(946, 946, 6, 157, 'En’âm', 'Mekki', 'Bu, indirdiğimiz kutsal Kitap\'dır, ona uyun. \"Bizden önce iki topluluğa kitap indirildi, bizim onların okuduklarından haberimiz yok\" demekten veya \"Bize kitap indirilseydi onlardan daha doğru yolda olurduk\" demekten sakının ki merhamet olunasınız. Şüphesiz o, size Rabbinizden belge, yol gösteren ve rahmet olarak gelmiştir. Allah\'ın ayetlerini yalanlayandan ve onlardan yüz çevirenden daha zalim kimdir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü, kötü bir azabla cezalandıracağız.'),
(947, 947, 6, 158, 'En’âm', 'Mekki', 'Onlar kendilerine meleklerin gelmesini mi, yoksa Rabbinin gelmesini mi, yahut Rablerinden bir takım mucizelerin gelmesini mi bekliyorlar? Rabbinin bir takım mucizeleri geldiği gün, bir kimse daha önce inanmamışsa veya imaniyle bir iyilik kazanmamışsa, imanı ona fayda vermez. Onlara: \"Bekleyin, doğrusu biz de bekliyoruz\" de.'),
(948, 948, 6, 159, 'En’âm', 'Mekki', 'Fırka fırka olup dinlerini parçalayanlarla senin hiçbir ilişiğin olamaz. Onların işi Allah\'a kalmıştır, yaptıklarını onlara sonra bildirecektir.'),
(949, 949, 6, 160, 'En’âm', 'Mekki', 'Kim ortaya bir iyilik koyarsa ona on katı verilir; ortaya bir kötülük koyan ise ancak misliyle cezalandırılır; onlara haksızlık yapılmaz.'),
(950, 950, 6, 161, 'En’âm', 'Mekki', '\"Şüphesiz Rabbim beni doğru yola, gerçek dine, doğruya yönelen ve puta tapanlardan olmayan İbrahim\'in dinine iletmiştir\" de.'),
(951, 951, 6, 162, 'En’âm', 'Mekki', 'De ki: \"Namazım, ibadetlerim, hayatım ve ölümüm, alemlerin Rabbi Allah içindir.'),
(952, 952, 6, 163, 'En’âm', 'Mekki', 'O\'nun hiçbir ortağı yoktur; böyle emrolundum ve ben Müslümanların ilkiyim.\"'),
(953, 953, 6, 164, 'En’âm', 'Mekki', 'De ki: \"Allah her şeyin Rabbi iken O\'ndan başka bir rab mi arayayım? Herkesin kazandığı kendisinedir, kimse başkasının yükünü taşımaz; sonunda dönüşünüz Rabbinizedir, ayrılığa düştüğünüz şeyleri size bildirecektir.\"'),
(954, 954, 6, 165, 'En’âm', 'Mekki', 'Verdikleriyle denemek için sizi yeryüzünün halifeleri kılan ve kiminizi kiminize derecelerle üstün yapan O\'dur. Doğrusu Rabbinin cezalandırması süratlidir. Şüphesiz O bağışlar, merhamet eder.'),
(955, 955, 7, 1, 'A’râf ', 'Mekki', 'Elif, Lam, Mim, Sad.'),
(956, 956, 7, 2, 'A’râf ', 'Mekki', 'Sana bir Kitap indirildi. Onunla insanları uyarman ve inananlara öğüt vermen için kalbine bir darlık gelmesin.'),
(957, 957, 7, 3, 'A’râf ', 'Mekki', 'Rabbinizden size indirilen Kitap\'a uyun, O\'ndan başka dostlar edinerek onlara uymayın. Pek az öğüt dinliyorsunuz.'),
(958, 958, 7, 4, 'A’râf ', 'Mekki', 'Biz nice kentleri yok etmişizdir; geceleyin veya gündüz uykularında iken baskınımıza uğramışlardır.'),
(959, 959, 7, 5, 'A’râf ', 'Mekki', 'Baskınımıza uğradıklarında, sözleri, \"Gerçekten biz haksızdık\" demekten ibaret kalmıştır.'),
(960, 960, 7, 6, 'A’râf ', 'Mekki', 'And olsun ki, kendilerine peygamber gönderilenlere soracağız, peygamberlere de soracağız.'),
(961, 961, 7, 7, 'A’râf ', 'Mekki', 'And olsun ki, yaptıklarını kendilerine bir bir anlatacağız, zira onlardan uzak değildik.'),
(962, 962, 7, 8, 'A’râf ', 'Mekki', 'Gerçek tartı kıyamet günündedir. Tartıları ağır gelenler, işte onlar kurtulanlardır.'),
(963, 963, 7, 9, 'A’râf ', 'Mekki', 'Tartıları hafif gelenler, ayetlerimize yaptıkları haksızlıklardan ötürü kendilerini mahvetmiş olanlardır.'),
(964, 964, 7, 10, 'A’râf ', 'Mekki', 'Sizi yeryüzünde yerleştirdik ve orada size geçimlikler yarattık. Öyleyken pek az şükrediyorsunuz.'),
(965, 965, 7, 11, 'A’râf ', 'Mekki', 'And olsun ki, sizi yarattık, sonra şekil verdik, sonra meleklere, \"Adem\'e secde edin\" dedik; İblis\'ten başka hepsi secde etti, o secde edenlerden olmadı.'),
(966, 966, 7, 12, 'A’râf ', 'Mekki', 'Allah, \"Sana emrettiğim halde, seni secdeden alıkoyan nedir?\" dedi, \"Beni ateşten onu çamurdan yarattın, ben ondan üstünüm\" cevabını verdi.'),
(967, 967, 7, 13, 'A’râf ', 'Mekki', 'Ona, \"İn oradan, orada büyüklenmek sana düşmez, defol, sen alçağın birisin\" dedi.'),
(968, 968, 7, 14, 'A’râf ', 'Mekki', 'Ona, \"İnsanların tekrar dirilecekleri güne kadar beni ertele\" dedi.'),
(969, 969, 7, 15, 'A’râf ', 'Mekki', 'Allah; \"Sen erteye bırakılanlardansın\" dedi.'),
(970, 970, 7, 16, 'A’râf ', 'Mekki', '\"Beni azdırdığın için, and olsun ki, Senin doğru yolun üzerinde onlara karşı duracağım; sonra önlerinden, ardlarından, sağ ve sollarından onlara sokulacağım; çoğunu Sana şükreder bulamayacaksın\" dedi.'),
(971, 971, 7, 17, 'A’râf ', 'Mekki', '\"Beni azdırdığın için, and olsun ki, Senin doğru yolun üzerinde onlara karşı duracağım; sonra önlerinden, ardlarından, sağ ve sollarından onlara sokulacağım; çoğunu Sana şükreder bulamayacaksın\" dedi.'),
(972, 972, 7, 18, 'A’râf ', 'Mekki', 'Allah, \"Yerilmiş ve kovulmuşsun, oradan defol; and olsun ki insanlardan sana kim uyarsa, hepinizi cehenneme dolduracağım\" dedi.'),
(973, 973, 7, 19, 'A’râf ', 'Mekki', '\"Ey Adem! Sen ve eşin cennette kalın ve istediğiniz yerden yiyin, yalnız şu ağaca yaklaşmayın yoksa zalimlerden olursunuz.\"'),
(974, 974, 7, 20, 'A’râf ', 'Mekki', 'Şeytan, ayıp yerlerini kendilerine göstermek için onlara fısıldadı: \"Rabbinizin sizi bu ağaçtan menetmesi melek olmanız veya burada temelli kalmanızı önlemek içindir.\"'),
(975, 975, 7, 21, 'A’râf ', 'Mekki', '\"Doğrusu ben size öğüt verenlerdenim\" diye ikisine yemin etti.'),
(976, 976, 7, 22, 'A’râf ', 'Mekki', 'Böylece onların yanılmalarını sağladı. Ağaçtan meyve tattıklarında kendilerine ayıp yerleri göründü, cennet yapraklarından oralarına örtmeğe koyuldular. Rableri onlara, \"Ben sizi o ağaçtan menetmemiş miydim? Şeytanın size apaçık bir düşman olduğunu söylememiş miydim?\" diye seslendi.'),
(977, 977, 7, 23, 'A’râf ', 'Mekki', 'Her ikisi, \"Rabbimiz! Kendimize yazık ettik; bizi bağışlamaz ve bize merhamet etmezsen biz kaybedenlerden oluruz\" dediler.'),
(978, 978, 7, 24, 'A’râf ', 'Mekki', '\"Birbirinize düşman olarak inin, siz yeryüzünde bir müddet için yerleşip geçineceksiniz.\"'),
(979, 979, 7, 25, 'A’râf ', 'Mekki', '\"Orada yaşar, orada ölür ve oradan dirilip çıkarılırsınız\" dedi.'),
(980, 980, 7, 26, 'A’râf ', 'Mekki', 'Ey İnsanoğulları! Ayıp yerlerinizi örtecek giyimlikle sizi süsleyecek elbiseler gönderdik. Takva örtüsü ise bunlardan daha hayırlıdır. Allah\'ın bu ayetleri öğüt almanız içindir.'),
(981, 981, 7, 27, 'A’râf ', 'Mekki', 'Ey İnsanoğulları! Şeytan, ayıp yerlerini kendilerine göstermek için elbiselerini soyarak ananızı babanızı cennetten çıkardığı gibi sizi de şaşırtmasın. Sizin onları görmediğiniz yerlerden o ve taraftarları sizi görürler. Biz şeytanları, inanmayanlara dost kılarız.'),
(982, 982, 7, 28, 'A’râf ', 'Mekki', 'Onlar bir fenalık yaptıkları zaman, \"Babalarımızı bu yolda bulduk, Allah da bize bunu emretti\" derler. De ki: \"Allah fenalığı emretmez. Bilmediğiniz şeyi Allah\'a karşı mı söylüyorsunuz?\"'),
(983, 983, 7, 29, 'A’râf ', 'Mekki', 'De ki: \"Rabbim adaleti emretti; her secde yerinde yüzünüzü O\'na doğrultun; dinde samimi olarak O\'na yalvarın. Sizi yarattığı gibi yine O\'na döneceksiniz.\"'),
(984, 984, 7, 30, 'A’râf ', 'Mekki', 'Allah insanlardan bir takımını doğru yola eriştirdi, fakat bir takımı da sapıklığı haketti, çünkü bunlar Allah\'ı bırakıp şeytanları dost edinmiş ve kendilerini doğru yolda sanmışlardı.'),
(985, 985, 7, 31, 'A’râf ', 'Mekki', 'Ey Ademoğulları! Her mescide güzel elbiselerinizi giyinerek gidin; yiyin için fakat israf etmeyin, çünkü Allah müsrifleri sevmez.'),
(986, 986, 7, 32, 'A’râf ', 'Mekki', '\"Allah\'ın kulları için yarattığı ziynet ve temiz rızıkları haram kılan kimdir?\" \"Bunlar, dünya hayatında inananlarındır, kıyamet gününde de yalnız onlar içindir\" de. Bilen kimseler için ayetlerimizi böylece uzun uzun açıklıyoruz.'),
(987, 987, 7, 33, 'A’râf ', 'Mekki', 'De ki: \"Rabbim sadece, açık ve gizli fenalıkları, günahı, haksız yere tecavüzü, hakkında hiçbir delil indirmediği şeyi Allah\'a ortak koşmanızı, Allah\'a karşı bilmediğiniz şeyleri söylemenizi haram kılmıştır.\"'),
(988, 988, 7, 34, 'A’râf ', 'Mekki', 'Her ümmet için belirli bir süre vardır; vakitleri dolunca ne bir saat gecikebilir ne de öne geçebilirler.'),
(989, 989, 7, 35, 'A’râf ', 'Mekki', 'Ey Adem oğulları! Size aranızdan ayetlerimizi okuyan peygamberler geldiğinde, onların bildirdiklerine karşı gelmekten sakınan ve gidişini düzeltenlere, işte onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(990, 990, 7, 36, 'A’râf ', 'Mekki', 'Ayetlerimizi yalanlayıp onlara karşı büyüklük taslayanlara gelince, işte onlar cehennemliklerdir, orada temelli kalacaklardır.'),
(991, 991, 7, 37, 'A’râf ', 'Mekki', 'Allah\'a karşı yalan uyduran veya ayetlerini yalan sayandan daha zalim kimdir? Kitap\'daki payları kendilerine erişecek olanlar onlardır. Elçilerimiz canlarını almak üzere geldiklerinde onlara, \"Allah\'tan başka taptıklarınız nerede?\" deyince, \"Bizi koyup kaçtılar\" derler, böylece inkarcı olduklarına kendi aleyhlerine şahidlik ederler.'),
(992, 992, 7, 38, 'A’râf ', 'Mekki', 'Allah, \" Sizden önce geçmiş cin ve insan ümmetleriyle beraber ateşe girin\" der. Her ümmet girdikçe kendi yoldaşına lanet eder. Hepsi birbiri ardından cehennemde toplanınca, sonrakiler öncekiler için, \"Rabbimiz! Bizi sapıtanlar işte bunlardır, onlara ateş azabını kat kat ver\" derler, Allah, \"Hepsinin kat kattır, ama bilmezsiniz\" der.'),
(993, 993, 7, 39, 'A’râf ', 'Mekki', 'Öncekiler sonrakilere, \"Sizin bizden bir üstünlüğünüz yoktu, kazandığınıza karşılık azabı tadın\" derler.'),
(994, 994, 7, 40, 'A’râf ', 'Mekki', 'Doğrusu ayetlerimizi yalan sayıp, onlara karşı büyüklük taslayanlara, göğün kapıları açılmaz; deve iğnenin deliğinden geçmedikçe cennete de giremezler. Suçluları böyle cezalandırırız.'),
(995, 995, 7, 41, 'A’râf ', 'Mekki', 'Onlar için cehennemden bir yatak ve üstlerine de örtüler vardır. Zalimleri böyle cezalandırırız.'),
(996, 996, 7, 42, 'A’râf ', 'Mekki', 'İnanan ve yararlı iş işleyenler ki kişiye ancak gücünün yeteceği kadar yükleriz işte cennetlikler onlardır, orada temelli kalacaklardır.'),
(997, 997, 7, 43, 'A’râf ', 'Mekki', 'Cennette altlarından ırmaklar akarken gönüllerinden kini çıkarıp atarız. \"Bizi buraya eriştiren Allah\'a hamdolsun. Eğer Allah bizi doğru yola iletmeseydi, biz doğru yolu bulamazdık. And olsun ki Rabbimizin peygamberleri bize gerçeği getirmiştir\" derler. Onlara, \"İşlediğinize karşılık işte mirasçısı olduğunuz cennet\" diye seslenilir.'),
(998, 998, 7, 44, 'A’râf ', 'Mekki', 'Cennetlikler, cehennemliklere: \"Biz Rabbimizin bize vadettiğini gerçek bulduk, Rabbinizin size de vadettiğini gerçek buldunuz mu?\" diye seslenirler, \"Evet\" derler. Aralarında bir münadi, \"Allah\'ın laneti Allah yolundan alıkoyan, o yolun eğriliğini isteyen ve ahireti inkar eden zalimleredir\" diye seslenir.'),
(999, 999, 7, 45, 'A’râf ', 'Mekki', 'Cennetlikler, cehennemliklere: \"Biz Rabbimizin bize vadettiğini gerçek bulduk, Rabbinizin size de vadettiğini gerçek buldunuz mu?\" diye seslenirler, \"Evet\" derler. Aralarında bir münadi, \"Allah\'ın laneti Allah yolundan alıkoyan, o yolun eğriliğini isteyen ve ahireti inkar eden zalimleredir\" diye seslenir.'),
(1000, 1000, 7, 46, 'A’râf ', 'Mekki', 'İki taraf arasında bir perde ve burçlar üzerinde her iki tarafı da simalarından tanıyan adamlar vardır; cennetliklere, \"Size selam olsun\" derler. Bunlar henüz girmeyen fakat cenneti uman kimselerdir.'),
(1001, 1001, 7, 47, 'A’râf ', 'Mekki', 'Gözleri cehennemlikler yönüne çevrilince: \"Rabbimiz! Bizi zalimlerle beraber bulundurma\" derler.'),
(1002, 1002, 7, 48, 'A’râf ', 'Mekki', 'Burçlarda olanlar, simalarından tanıdıkları adamlara; Topluluğunuz, topladığınız mal ve büyüklük taslamalarınız size fayda vermedi. Allah\'ın rahmetine erdirmeyeceğine yemin ettikleriniz bunlar mıydı? Oysa Allah onlara şöyle der: \"Cennete girin, size korku yoktur, sizler mahzun da olmayacaksınız.\"'),
(1003, 1003, 7, 49, 'A’râf ', 'Mekki', 'Burçlarda olanlar, simalarından tanıdıkları adamlara; Topluluğunuz, topladığınız mal ve büyüklük taslamalarınız size fayda vermedi. Allah\'ın rahmetine erdirmeyeceğine yemin ettikleriniz bunlar mıydı? Oysa Allah onlara şöyle der: \"Cennete girin, size korku yoktur, sizler mahzun da olmayacaksınız.\"'),
(1004, 1004, 7, 50, 'A’râf ', 'Mekki', 'Cehennemlikler cennetliklere, \"Bize biraz su veya Allah\'ın size verdiği rızıktan gönderin\" diye seslenirler, onlar da, \"Doğrusu Allah dinlerini alay ve eğlenceye alan, dünya hayatına aldanan inkarcılara ikisini de haram etmiştir\" derler. Bugünle karşılaşacaklarını unuttukları, ayetlerimizi bile bile inkar ettikleri gibi biz de onları unutuyoruz.'),
(1005, 1005, 7, 51, 'A’râf ', 'Mekki', 'Cehennemlikler cennetliklere, \"Bize biraz su veya Allah\'ın size verdiği rızıktan gönderin\" diye seslenirler, onlar da, \"Doğrusu Allah dinlerini alay ve eğlenceye alan, dünya hayatına aldanan inkarcılara ikisini de haram etmiştir\" derler. Bugünle karşılaşacaklarını unuttukları, ayetlerimizi bile bile inkar ettikleri gibi biz de onları unutuyoruz.'),
(1006, 1006, 7, 52, 'A’râf ', 'Mekki', 'And olsun ki Biz onlara bir Kitap getirdik, inanan bir millet için yol gösterici ve rahmet olarak onu bilgiyle uzun uzun açıkladık.'),
(1007, 1007, 7, 53, 'A’râf ', 'Mekki', 'Kitap\'ın haber verdiği sonuçtan başka bir şey mi bekliyorlar? Sonuç gelip çattığı gün, önceleri onu unutmuş olanlar, \"Rabbimizin peygamberleri şüphesiz bize gerçeği getirmişti, şimdi bize şefaat etsin, yahut geriye çevrilsek de işlediklerimizin başka türlüsünü işlesek\" derler. Doğrusu kendilerini mahvetmişlerdir, uydurdukları şeyler onları koyup kaçmışlardır.'),
(1008, 1008, 7, 54, 'A’râf ', 'Mekki', 'Rabbiniz, gökleri ve yeri altı günde yaratan ve sonra arşa hükmeden, gündüzü durmadan kovalayan gece ile bürüyen; güneşi, ayı, yıldızları, hepsini buyruğuna baş eğdirerek var eden Allah\'tır. Bilin ki yaratma da emir de O\'nun hakkıdır. Alemlerin Rabbiolan Allah Yüce\'dir.'),
(1009, 1009, 7, 55, 'A’râf ', 'Mekki', 'Rabbinize gönülden ve gizlice yalvarın. Doğrusu O aşırı gidenleri sevmez.'),
(1010, 1010, 7, 56, 'A’râf ', 'Mekki', 'Düzeltilmişken, yeryüzünde bozgunculuk yapmayın. Allah\'a korkarak ve umutla yalvarın. Doğrusu Allah\'ın rahmeti iyi davrananlara yakındır.'),
(1011, 1011, 7, 57, 'A’râf ', 'Mekki', 'Rahmetinin önünde, müjdeci olarak rüzgarları gönderen Allah\'tır. Rüzgarlar, yağmur yüklü bulutları taşıdığında, onu ölü bir memlekete gönderir, su indirir ve onunla her türlü ürünü yetiştiririz; ölüleri de bunun gibi diriltip, çıkarırız; belki bundan ibret alırsınız.'),
(1012, 1012, 7, 58, 'A’râf ', 'Mekki', 'İyi toprak Rabbinin izniyle bitki verir, çorak toprak kavruk bitki çıkarır. Şükredecek millet için böylece ayetleri yerli yerince açıklarız.'),
(1013, 1013, 7, 59, 'A’râf ', 'Mekki', 'And olsun ki Nuh\'u milletine gönderdik. \"Ey milletim! Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur; doğrusu sizin için büyük günün azabından korkuyorum\" dedi.'),
(1014, 1014, 7, 60, 'A’râf ', 'Mekki', 'Milletinin ileri gelenleri: \"Biz senin apaçık sapıklıkta olduğunu görüyoruz\" dediler.'),
(1015, 1015, 7, 61, 'A’râf ', 'Mekki', '\"Ey milletim! Bende bir sapıklık yoktur, ancak ben Alemlerin Rabbinin peygamberiyim, Rabbimin sözlerini size bildiriyor, öğüt veriyorum. Sizin bilmediğinizi Allah katından ben biliyorum. Sakınmanızı ve böylece merhamete uğramanızı sağlamak üzere sizi uyarmak için aranızdan biri vasıtasıyla Rabbinizden size haber gelmesine mi şaşıyorsunuz?\" dedi.'),
(1016, 1016, 7, 62, 'A’râf ', 'Mekki', '\"Ey milletim! Bende bir sapıklık yoktur, ancak ben Alemlerin Rabbinin peygamberiyim, Rabbimin sözlerini size bildiriyor, öğüt veriyorum. Sizin bilmediğinizi Allah katından ben biliyorum. Sakınmanızı ve böylece merhamete uğramanızı sağlamak üzere sizi uyarmak için aranızdan biri vasıtasıyla Rabbinizden size haber gelmesine mi şaşıyorsunuz?\" dedi.'),
(1017, 1017, 7, 63, 'A’râf ', 'Mekki', '\"Ey milletim! Bende bir sapıklık yoktur, ancak ben Alemlerin Rabbinin peygamberiyim, Rabbimin sözlerini size bildiriyor, öğüt veriyorum. Sizin bilmediğinizi Allah katından ben biliyorum. Sakınmanızı ve böylece merhamete uğramanızı sağlamak üzere sizi uyarmak için aranızdan biri vasıtasıyla Rabbinizden size haber gelmesine mi şaşıyorsunuz?\" dedi.'),
(1018, 1018, 7, 64, 'A’râf ', 'Mekki', 'Onu yalanladılar; biz de onu ve gemide beraberinde olanları kurtardık, ayetlerimizi yalan sayanları suda boğduk, çünkü onlar kör bir milletti.'),
(1019, 1019, 7, 65, 'A’râf ', 'Mekki', 'Ad milletine de, kardeşleri Hud\'u gönderdik \"Ey milletim! Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur, karşı gelmekten sakınmaz mısınız?\" dedi.'),
(1020, 1020, 7, 66, 'A’râf ', 'Mekki', 'Milletinin inkarcı ileri gelenleri, \"Biz senin beyinsiz olduğunu görüyor ve seni yalancılardan sanıyoruz\" dediler.'),
(1021, 1021, 7, 67, 'A’râf ', 'Mekki', '\"Ey milletim! Ben beyinsiz değil, Alemlerin Rabbinin peygamberiyim.'),
(1022, 1022, 7, 68, 'A’râf ', 'Mekki', 'Size Rabbimin sözlerini bildiriyorum. Ben sizin için güvenilir bir öğütçüyüm; sizi uyarmak üzere, aranızdan bir adam vasıtasiyle Rabbinizden size bir haber gelmesine mi şaşıyorsunuz? Allah\'ın sizi Nuh\'un milleti yerine getirdiğini ve vücutça da onlardan üstün kıldığını hatırlayın, başarıya erişebilmeniz için Allah\'ın nimetlerini anın\" dedi.'),
(1023, 1023, 7, 69, 'A’râf ', 'Mekki', 'Size Rabbimin sözlerini bildiriyorum. Ben sizin için güvenilir bir öğütçüyüm; sizi uyarmak üzere, aranızdan bir adam vasıtasiyle Rabbinizden size bir haber gelmesine mi şaşıyorsunuz? Allah\'ın sizi Nuh\'un milleti yerine getirdiğini ve vücutça da onlardan üstün kıldığını hatırlayın, başarıya erişebilmeniz için Allah\'ın nimetlerini anın\" dedi.'),
(1024, 1024, 7, 70, 'A’râf ', 'Mekki', '\"Bize yalnız Allah\'a kulluk etmemizi, babalarımızın taptıklarını bırakmamızı söylemek için mi geldin? Doğru sözlülerden isen haydi bizi tehdit ettiğin azaba uğrat\" dediler.'),
(1025, 1025, 7, 71, 'A’râf ', 'Mekki', '\"Hiç şüphesiz artık Rabbinizin azab ve öfkesini hakettiniz. Allah\'ın hiçbir delil indirmediği, isimlerini de siz ve babalarınızın koyduğu putlar hakkında mı benimle tartışıyorsunuz? Bekleyin, doğrusu ben de sizinle beraber bekleyenlerdenim\" dedi.'),
(1026, 1026, 7, 72, 'A’râf ', 'Mekki', 'Biz, rahmetimizle, Hud\'u ve beraberinde bulunanları kurtardık, ayetlerimizi yalan sayarak inanmayanların kökünü kestik.'),
(1027, 1027, 7, 73, 'A’râf ', 'Mekki', 'Semud milletine de kardeşleri Salih\'i gönderdik. \"Ey milletim! Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur. Rabbinizden size bir belge geldi: Allah\'ın bu dişi devesi size bir delildir, onu bırakın, Allah\'ın toprağında otlasın; ona kötülük etmeyin, yoksa can yakıcı azaba uğrarsınız.'),
(1028, 1028, 7, 74, 'A’râf ', 'Mekki', 'Allah\'ın sizi Ad milleti yerine getirdiğini, ovalarında köşkler kurup dağlarında kayadan evler yonttuğunuz yeryüzünde yerleştirdiğini hatırlayın; Allah\'ın nimetlerini anın, yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın\" dedi.'),
(1029, 1029, 7, 75, 'A’râf ', 'Mekki', 'Milletinin büyüklük taslayan ileri gelenleri, aralarından iman eden ve bu sebeple hor gördükleri kimselere, \"Salih\'in Rabbi tarafından gönderildiğini sahiden biliyor musunuz?\" dediler, onlar da, \"Doğrusu biz onunla gönderilene inanıyoruz\" dediler.'),
(1030, 1030, 7, 76, 'A’râf ', 'Mekki', 'Büyüklük taslayanlar, \"Sizin inandığınızı biz inkar ediyoruz\" dediler ve dişi deveyi kesip devirdiler; Rablerinin buyruğuna baş kaldırdılar, \"Ey Salih, eğer sen peygambersen bizi tehdit ettiğin azaba uğrat bakalım\" dediler.'),
(1031, 1031, 7, 77, 'A’râf ', 'Mekki', 'Büyüklük taslayanlar, \"Sizin inandığınızı biz inkar ediyoruz\" dediler ve dişi deveyi kesip devirdiler; Rablerinin buyruğuna baş kaldırdılar, \"Ey Salih, eğer sen peygambersen bizi tehdit ettiğin azaba uğrat bakalım\" dediler.'),
(1032, 1032, 7, 78, 'A’râf ', 'Mekki', 'Bu yüzden onları bir sarsıntı tuttu ve oldukları yerde diz üstü çöküverdiler.'),
(1033, 1033, 7, 79, 'A’râf ', 'Mekki', 'Salih de onlardan yüz çevirdi ve \"Ey milletim! And olsun ki ben size Rabbimin sözünü bildirmiş ve öğüt vermiştim; fakat siz öğüt verenleri sevmiyorsunuz\" dedi.'),
(1034, 1034, 7, 80, 'A’râf ', 'Mekki', 'Lut\'u da gönderdik, milletine \"Dünyalarda hiç kimsenin sizden önce yapmadığı bir hayasızlığı mı yapıyorsunuz? Siz kadınları bırakıp erkeklere yaklaşıyorsunuz, doğrusu çok aşırı giden bir milletsiniz\" dedi.'),
(1035, 1035, 7, 81, 'A’râf ', 'Mekki', 'Lut\'u da gönderdik, milletine \"Dünyalarda hiç kimsenin sizden önce yapmadığı bir hayasızlığı mı yapıyorsunuz? Siz kadınları bırakıp erkeklere yaklaşıyorsunuz, doğrusu çok aşırı giden bir milletsiniz\" dedi.'),
(1036, 1036, 7, 82, 'A’râf ', 'Mekki', 'Milletinin cevabı sadece, \"Onları kasabanızdan çıkarın, güya onlar temiz kalmaya uğraşan insanlarmış\" demek oldu.'),
(1037, 1037, 7, 83, 'A’râf ', 'Mekki', 'Bunun üzerine Lut\'u ve taraftarlarını kurtardık; yalnız karısı, geride kalıp helake uğrayanlardan oldu.'),
(1038, 1038, 7, 84, 'A’râf ', 'Mekki', 'Geriye kalanların üzerine öyle bir yağmur yağdırdık ki! Suçluların sonunun nasıl olduğuna bir bak!'),
(1039, 1039, 7, 85, 'A’râf ', 'Mekki', 'Medyen halkına da kardeşleri Şuayb\'ı gönderdik, onlara şöyle dedi: \"Ey milletim! Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur. Rabbinizden size bir belge geldi. Ölçü ve tartıyı tam yapın, insanların eşyasını eksik vermeyin, düzelttikten sonra yeryüzünde bozgunculuk etmeyin; inanıyorsanız bilin ki, bunlar sizin için hayırlıdır.\"'),
(1040, 1040, 7, 86, 'A’râf ', 'Mekki', '\"Allah\'a inananları yolundan alıkoyup ve o yolun eğriliğini dileyerek tehdit edip her yolda pusu kurup oturmayın. Azken, Allah\'ın sizi çoğalttığını hatırlayın; bozguncuların sonunun nasıl olduğuna bir bakın.\"'),
(1041, 1041, 7, 87, 'A’râf ', 'Mekki', '\"İçinizde mademki benimle gönderilene inanan bir topluluk ve inanmayan bir topluluk var, o halde Allah\'ın aramızda hükmünü bildirmesine kadar sabredin. Allah hükmedenlerin en iyisidir.\"'),
(1042, 1042, 7, 88, 'A’râf ', 'Mekki', 'Milletinin büyüklük taslayan ileri gelenleri, \"Ey Şuayb! Ya dinimize dönersiniz ya da, and olsun ki seni ve inananları seninle beraber kentimizden çıkarırız\" dediler. Şuayb, onlara: \"İstemezsek de mi? Allah bizi dininizden kurtardıktan sonra ona dönecek olursak, doğrusu Allah\'a karşı yalan uydurmuş oluruz. Rabbimizin dilemesi bir yana, dininize dönmek bize yakışmaz. Rabbimizin ilmi her şeyi kuşatmıştır. Biz yalnız Allah\'a güvendik. Rabbimiz! Bizimle milletimiz arasında hak ile Sen hüküm ver, Sen hükmedenlerin en hayırlısısın\" dedi.'),
(1043, 1043, 7, 89, 'A’râf ', 'Mekki', 'Milletinin büyüklük taslayan ileri gelenleri, \"Ey Şuayb! Ya dinimize dönersiniz ya da, and olsun ki seni ve inananları seninle beraber kentimizden çıkarırız\" dediler. Şuayb, onlara: \"İstemezsek de mi? Allah bizi dininizden kurtardıktan sonra ona dönecek olursak, doğrusu Allah\'a karşı yalan uydurmuş oluruz. Rabbimizin dilemesi bir yana, dininize dönmek bize yakışmaz. Rabbimizin ilmi her şeyi kuşatmıştır. Biz yalnız Allah\'a güvendik. Rabbimiz! Bizimle milletimiz arasında hak ile Sen hüküm ver, Sen hükmedenlerin en hayırlısısın\" dedi.'),
(1044, 1044, 7, 90, 'A’râf ', 'Mekki', 'Milletinin inkar eden ileri gelenleri, \"Şuayb\'a uyarsanız, and olsun ki siz kaybedersiniz\" dediler.'),
(1045, 1045, 7, 91, 'A’râf ', 'Mekki', 'Bu yüzden onları bir sarsıntı tuttu ve oldukları yerde diz üstü çöküverdiler.'),
(1046, 1046, 7, 92, 'A’râf ', 'Mekki', 'Şuayb\'ı yalanlayanlar, yurtlarında sanki hiç yaşamamışlar gibi oldular, izleri bile kalmadı. Mahvolanlar, Şuayb\'ı yalanlayanlar oldu.'),
(1047, 1047, 7, 93, 'A’râf ', 'Mekki', 'Şuayb onlardan döndü de, \"Ey milletim! And olsun ki, Rabbimin sözlerini size bildirdim, öğüt verdim; inkarcı millet için niçin üzüleyim?\" dedi.'),
(1048, 1048, 7, 94, 'A’râf ', 'Mekki', 'Biz hangi kente (ülkeye) bir peygamber gönderdikse, ora halkını, yalvarıp yakarsınlar diye, darlık ve sıkıntıya uğratmışızdır.'),
(1049, 1049, 7, 95, 'A’râf ', 'Mekki', 'Sonra kötülüğün yerine iyiliği koyduk, öyle ki, çoğalıp, \"babalarımız da darlığa uğramış, bolluğa kavuşmuşlardı\" dediler. Bu yüzden onları haberleri olmadan, ansızın yakalayıverdik.'),
(1050, 1050, 7, 96, 'A’râf ', 'Mekki', 'Eğer kentlerin halkı inanmış ve Bize karşı gelmekten sakınmış olsalardı, onlara göğün ve yerin bolluklarını verirdik. Ama yalanladılar; bu yüzden onları, yaptıklarına karşılık yakalayıverdik.'),
(1051, 1051, 7, 97, 'A’râf ', 'Mekki', 'Kentlerin halkı, geceleyin uyurlarken azabımızın kendilerine gelmesinden güvende miydiler?'),
(1052, 1052, 7, 98, 'A’râf ', 'Mekki', 'Yahut kentlerin halkı, kuşluk vakti eğlenirlerken azabımızın kendilerine gelmesinden güvende miydiler?'),
(1053, 1053, 7, 99, 'A’râf ', 'Mekki', 'Onlar Allah\'ın düzeninden güvende miydiler? Allah\'ın düzeninden ancak mahvolacak millet güvende olur. Sahiplerinden sonra yeryüzüne mirasçı olan kimselere hala şu açıkça anlaşılmadı mı ki Biz dileseydik onları da suçlarının cezasına uğratırdık.'),
(1054, 1054, 7, 100, 'A’râf ', 'Mekki', 'Kalblerini kapatıp mühürleriz de birşey duymazlar.'),
(1055, 1055, 7, 101, 'A’râf ', 'Mekki', 'İşte o kentlerin haberlerini sana anlatıyoruz. And olsun ki onlara peygamberler belgeler getirdi; önceleri yalanladıklarından ötürü inanamadılar. Allah kafirlerin kalblerini böylece kapatıp mühürler.'),
(1056, 1056, 7, 102, 'A’râf ', 'Mekki', 'Onların çoğunda ahde bağlılık görmedik, çoğunu fasık kimseler olarak bulduk.'),
(1057, 1057, 7, 103, 'A’râf ', 'Mekki', 'Sonra peygamberlerin ardından Musa\'yı ayetlerimizle Firavun ve erkanına gönderdik. Ayetlerimize karşı haksızlık ettiler. Bozguncuların sonunun nasıl olduğuna bir bak.'),
(1058, 1058, 7, 104, 'A’râf ', 'Mekki', 'Musa, \"Ey Firavun! Ben alemlerin Rabbinin peygamberiyim.'),
(1059, 1059, 7, 105, 'A’râf ', 'Mekki', 'Bana Allah\'a karşı ancak gerçeği söylemek yaraşır. Size Rabbinizden bir mucize getirdim, İsrailoğullarını benimle beraber gönder\" dedi.'),
(1060, 1060, 7, 106, 'A’râf ', 'Mekki', 'Firavun: \"Bir mucize getirdiysen ortaya koy bakalım, doğru sözlülerden isen bunu yaparsın\" dedi.'),
(1061, 1061, 7, 107, 'A’râf ', 'Mekki', 'Musa, asasını yere atar atmaz apaçık bir yılan (ejderha) oluverdi; elini çıkardı, bakanlar bembeyaz olduğunu gördüler.'),
(1062, 1062, 7, 108, 'A’râf ', 'Mekki', 'Musa, asasını yere atar atmaz apaçık bir yılan (ejderha) oluverdi; elini çıkardı, bakanlar bembeyaz olduğunu gördüler.'),
(1063, 1063, 7, 109, 'A’râf ', 'Mekki', 'Firavun milletinin ileri gelenleri, \"Doğrusu bu bilgin bir sihirbazdır, sizi memleketinizden çıkarmak istiyor\" dediler. Firavun: \"Ne buyurursunuz?\" dedi.'),
(1064, 1064, 7, 110, 'A’râf ', 'Mekki', 'Firavun milletinin ileri gelenleri, \"Doğrusu bu bilgin bir sihirbazdır, sizi memleketinizden çıkarmak istiyor\" dediler. Firavun: \"Ne buyurursunuz?\" dedi.'),
(1065, 1065, 7, 111, 'A’râf ', 'Mekki', '\"Onu ve kardeşini eğle; şehirlere toplayıcılar gönder, bütün bilgin sihirbazları sana getirsinler\" dediler.'),
(1066, 1066, 7, 112, 'A’râf ', 'Mekki', '\"Onu ve kardeşini eğle; şehirlere toplayıcılar gönder, bütün bilgin sihirbazları sana getirsinler\" dediler.'),
(1067, 1067, 7, 113, 'A’râf ', 'Mekki', 'Sihirbazlar Firavun\'a geldi, \"Yenecek olursak bize şüphesiz bir mükafat var değil mi?\" dediler.'),
(1068, 1068, 7, 114, 'A’râf ', 'Mekki', 'Firavun, \"Evet, yenerseniz gözdelerden olacaksınız\" dedi.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(1069, 1069, 7, 115, 'A’râf ', 'Mekki', 'Sihirbazlar: \"Ey Musa! Marifetini ya sen ortaya koy veya biz koyalım\" dediler.'),
(1070, 1070, 7, 116, 'A’râf ', 'Mekki', 'Musa: \"Siz koyun\" dedi. Sihirbazlar marifetlerini ortaya koyunca insanların gözlerini sihirlediler ve onları ürküttüler, büyük bir sihir yaptılar.'),
(1071, 1071, 7, 117, 'A’râf ', 'Mekki', 'Biz de Musa\'ya, \"Asanı koyuver\" dedik, o da koydu; hemen onların uydurduklarını yutmaya başladı.'),
(1072, 1072, 7, 118, 'A’râf ', 'Mekki', 'Hak tahakkuk etti, onların yaptıkları boşa gitti.'),
(1073, 1073, 7, 119, 'A’râf ', 'Mekki', 'İşte orada yenildiler, küçük düştüler.'),
(1074, 1074, 7, 120, 'A’râf ', 'Mekki', 'Sihirbazlar secdeye kapanıp, \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(1075, 1075, 7, 121, 'A’râf ', 'Mekki', 'Sihirbazlar secdeye kapanıp, \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(1076, 1076, 7, 122, 'A’râf ', 'Mekki', 'Sihirbazlar secdeye kapanıp, \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(1077, 1077, 7, 123, 'A’râf ', 'Mekki', 'Firavun: \"Ben size izin vermeden mi O\'na inandınız? Doğrusu bu, halkı şehirden çıkarmak için düzdüğünüz bir hiledir, fakat siz göreceksiniz. And olsun ki, ellerinizi ayaklarınızı çaprazlama keseceğim, sonra da hepinizi asacağım\" dedi.'),
(1078, 1078, 7, 124, 'A’râf ', 'Mekki', 'Firavun: \"Ben size izin vermeden mi O\'na inandınız? Doğrusu bu, halkı şehirden çıkarmak için düzdüğünüz bir hiledir, fakat siz göreceksiniz. And olsun ki, ellerinizi ayaklarınızı çaprazlama keseceğim, sonra da hepinizi asacağım\" dedi.'),
(1079, 1079, 7, 125, 'A’râf ', 'Mekki', 'Onlar da: \"Doğrusu biz ancak Rabbimize döneriz. Rabbimizin ayetleri gelince, onlara inanmamızdan ötürü bizden öç alıyorsun. Rabbimiz! Bize sabır ver ve canımızı müslim olarak al\" dediler.'),
(1080, 1080, 7, 126, 'A’râf ', 'Mekki', 'Onlar da: \"Doğrusu biz ancak Rabbimize döneriz. Rabbimizin ayetleri gelince, onlara inanmamızdan ötürü bizden öç alıyorsun. Rabbimiz! Bize sabır ver ve canımızı müslim olarak al\" dediler.'),
(1081, 1081, 7, 127, 'A’râf ', 'Mekki', 'Firavun milletinin ileri gelenleri: \"Musa\'yı ve milletini yeryüzünde bozgunculuk yapsınlar, seni ve tanrılarını bıraksınlar diye mi koyveriyorsun?\" dediler. Firavun: \"Onların oğullarını öldüreceğiz, kadınlarını sağ bırakacağız. Elbette biz onları ezecek üstünlükteyiz\" dedi.'),
(1082, 1082, 7, 128, 'A’râf ', 'Mekki', 'Musa milletine: \"Allah\'tan yardım dileyin ve sabredin; yeryüzü şüphesiz Allah\'ındır, kullarından dilediğini ona mirasçı kılar; sonuç Allah\'a karşı gelmekten sakınanlarındır\" dedi.'),
(1083, 1083, 7, 129, 'A’râf ', 'Mekki', 'Milleti: \"Sen bize gelmeden önce de, geldikten sonra da eziyet çektik\" dediler. Musa da: \"Rabbinizin düşmanlarınızı yok etmesi ve yeryüzünde sizi onların yerine geçirmesi umulur. O zaman nasıl davranacağınıza bakar\" dedi.'),
(1084, 1084, 7, 130, 'A’râf ', 'Mekki', 'And olsun ki, Biz de Firavun ailesini, ders alsınlar diye, yıllarca kuraklığa ve ürün kıtlığına uğrattık.'),
(1085, 1085, 7, 131, 'A’râf ', 'Mekki', 'Onlara bir iyilik geldiği zaman; \"Bu bizden ötürüdür\" derler, bir fenalığa uğrarlarsa da, Musa ve onunla beraber olanların uğursuzluğuna verirlerdi. Bilin ki, kendilerinin uğradığı uğursuzluk Allah katındandır, fakat çoğu bunu bilmezler.'),
(1086, 1086, 7, 132, 'A’râf ', 'Mekki', 'Firavun ailesi: \"Bizi sihirlemek için ne mucize gösterirsen göster, sana inanmayacağız\" dediler.'),
(1087, 1087, 7, 133, 'A’râf ', 'Mekki', 'Bunun üzerine su baskınını, çekirgeyi, haşeratı, kurbağaları ve kanı birbirinden ayrı mucizeler olarak onlara musallat kıldık; yine de büyüklük taslayıp suçlu bir millet oldular.'),
(1088, 1088, 7, 134, 'A’râf ', 'Mekki', 'Azab başlarına çökünce, \"Ey Musa! Rabbine, sana verdiği ahde göre bizim için yalvar. Bizden azabı kaldırırsan sana, and olsun ki, inanacağız ve İsrailoğullarını seninle beraber göndereceğiz\"dediler.'),
(1089, 1089, 7, 135, 'A’râf ', 'Mekki', 'Azabı nasıl olsa sonuna gelecekleri bir müddet için üzerlerinden kaldırınca, hemen sözlerinden cayıyorlardı.'),
(1090, 1090, 7, 136, 'A’râf ', 'Mekki', 'Bu sebeple onlardan öç aldık, ayetlerimizi yalan sayıp umursamadıkları için onları denizde boğduk.'),
(1091, 1091, 7, 137, 'A’râf ', 'Mekki', 'Hor görülen yahudileri, bereketlendirdiğimiz yerin doğularına ve batılarına mirasçı kıldık. Rabbinin İsrailoğullarına verdiği güzel söz, sabırlarına karşılık yerine geldi. Firavun ve milletinin yaptığını ve yükselttiklerini yıktık.'),
(1092, 1092, 7, 138, 'A’râf ', 'Mekki', 'İsrailoğullarının denizden geçmelerini sağladık. Puta gönülden tapan bir millete rastladılar. \"Ey Musa! Onların tanrıları gibi bize de bir tanrı yap\" dediler, Musa: \" Doğrusu siz bilgisiz bir milletsiniz, bunlar yok olacaklar ve işledikleri boşa gidecektir\" dedi.'),
(1093, 1093, 7, 139, 'A’râf ', 'Mekki', 'İsrailoğullarının denizden geçmelerini sağladık. Puta gönülden tapan bir millete rastladılar. \"Ey Musa! Onların tanrıları gibi bize de bir tanrı yap\" dediler, Musa: \" Doğrusu siz bilgisiz bir milletsiniz, bunlar yok olacaklar ve işledikleri boşa gidecektir\" dedi.'),
(1094, 1094, 7, 140, 'A’râf ', 'Mekki', '\"Sizi alemlere üstün kılmış olan Allah\'tan başka bir tanrı mı arayacağım?\" dedi.'),
(1095, 1095, 7, 141, 'A’râf ', 'Mekki', 'Sizi kötü azaba sokan, kadınlarınızı sağ bırakıp oğullarınızı öldüren Firavun ailesinden kurtarmıştık. Bunda, size Rabbiniz tarafından büyük bir imtihan vardı.'),
(1096, 1096, 7, 142, 'A’râf ', 'Mekki', 'Musa\'ya otuz gece vade verip sonra buna on gece daha kattık; böylece Rabbinin tayin ettiği müddet kırk geceye tamamlandı. Musa, kardeşi Harun\'a, \"Milletim içinde benim yerime geç, onları ıslah et, bozguncuların yoluna gitme\" dedi.'),
(1097, 1097, 7, 143, 'A’râf ', 'Mekki', 'Musa, tayin ettiğimiz vakitte gelip Rabbi onunla konuşunca, Musa: \"Rabbim! Bana Kendini göster, Sana bakayım\" dedi. Allah: \"Sen Beni göremezsin ama dağa bak, eğer o yerinde kalırsa sen de Beni göreceksin\" buyurdu. Rabbi dağa tecelli edince onu yerlebir etti ve Musa da baygın düştü; ayılınca: \"Yarabbi, münezzehsin, Sana tevbe ettim, ben inananların ilkiyim\" dedi.'),
(1098, 1098, 7, 144, 'A’râf ', 'Mekki', '\"Ey Musa! Verdiklerimle ve seninle konuşmamla seni insanlar arasından seçtim; sana verdiğimi al ve şükret\" dedi.'),
(1099, 1099, 7, 145, 'A’râf ', 'Mekki', 'Ona levhalarda her şeyden bir öğüt yazdık ve her şeyi uzun uzadıya açıkladık; onlara sıkıca sarıl, milletine de emret en güzel şekilde tutsunlar. Size Allah\'a karşı gelenlerin yurdunu göstereceğim.'),
(1100, 1100, 7, 146, 'A’râf ', 'Mekki', 'Yeryüzünde haksız yere büyüklük taslayanları, ayetlerimden yüz çevirteceğim. Onlar bütün ayetleri görseler yine de inanmazlar; doğru yolu görseler, yol olarak benimsemezler; azgınlık yolunu görseler, hemen onu yol edinirler. Bu, onların mucizelerimizi yalan saymaları ve onlardan habersiz görünmelerinden ileri gelir.'),
(1101, 1101, 7, 147, 'A’râf ', 'Mekki', 'Ayetlerimizi ve ahirete kavuşmayı yalan sayan kimselerin işleri boşa gitmiştir. Onlar işlediklerinin karşılığından başka bir şeyle mi cezalanırlar?'),
(1102, 1102, 7, 148, 'A’râf ', 'Mekki', 'Musa\'nın ardından milleti, ziynet takımlarından, canlıymış gibi böğüren bir buzağı heykeli yaparak onu tanrı edindiler. O buzağının kendileriyle konuşmadığını ve yol da göstermediğini görmediler mi? Onu tanrı olarak benimseyip kendilerine yazık ettiler.'),
(1103, 1103, 7, 149, 'A’râf ', 'Mekki', 'Elleri böğründe, çaresiz kalıp, kendilerinin sapıtmış olduklarını görünce: \"Eğer Rabbimiz bize acımaz ve bizi bağışlamazsa, and olsun ki mahvoluruz\" dediler.'),
(1104, 1104, 7, 150, 'A’râf ', 'Mekki', 'Musa, milletine, kızgın ve üzgün olarak dönünce \"Benim arkamdan ne kötü olmuşsunuz! Rabbinizin emrinin çabucak gelmesini mi istiyorsunuz?\" dedi, levhaları attı ve kardeşinin başından tutup kendine doğru çekti. Harun: \"Ey annem oğlu! Bu millet beni küçümsedi; az kalsın öldürüyorlardı. Bana, düşmanları sevindirecek şekilde davranma, beni bu zalim milletle bir sayma\" dedi.'),
(1105, 1105, 7, 151, 'A’râf ', 'Mekki', 'Musa \"Rabbim! Beni ve kardeşimi bağışla, bize acı, Sen merhametlilerin merhametlisisin\" dedi.'),
(1106, 1106, 7, 152, 'A’râf ', 'Mekki', 'Buzağıyı tanrı olarak benimseyenler Rablerinin öfkesine ve dünya hayatında alçaklığa uğrayacaklardır; iftira edenleri böylece cezalandırırız.'),
(1107, 1107, 7, 153, 'A’râf ', 'Mekki', 'Kötülük işleyip ardından tevbe edenler ve inananlar bilsinler ki Rabbin, bu hareketlerinin ardından onları şüphesiz bağışlar ve merhamet eder.'),
(1108, 1108, 7, 154, 'A’râf ', 'Mekki', 'Musa, öfkesi yatışınca, bir nüshasında Rablerinden korkanlar için doğru yol ve rahmet yazılı olan levhaları aldı.'),
(1109, 1109, 7, 155, 'A’râf ', 'Mekki', 'Musa, tayin ettiğimiz müddette milletinden yetmiş kişi seçti; onları sarsıntı tutunca dedi ki: \"Rabbim! Dileseydin daha önce beni ve onları yok ederdin, aramızdaki beyinsizlerin yaptıklarından ötürü bizi yok eder misin? Bu, Senin imtihanından başka birşey değildir, bununla dilediğini saptırır, dilediğini doğru yola iletirsin; bizim dostumuz Sensin; bizi bağışla, bize merhamet et. Sen bağışlayanların en iyisisin.\"'),
(1110, 1110, 7, 156, 'A’râf ', 'Mekki', '\"Bu dünyada ve ahirette bizim için güzel olanı yaz; biz Sana yöneldik\" dedi. Allah: \"Azabıma dilediğim kimseyi uğratırım, rahmetim herşeyi kaplamıştır; bunu Allah\'a karşı gelmekten sakınanlara, zekat verenlere, ayetlerimize inanıp, yanlarındaki Tevrat ve İncil\'de yazılı buldukları, okuyup yazması olmayan peygambere uyanlara yazacağız. O peygamber, onlara, uygun olanı emreder ve fenalıktan meneder, temiz şeyleri helal, murdar şeyleri haram kılar, onların ağır yüklerini indirir, zor tekliflerini hafifletir. Bu peygambere inanan, hürmet eden, yardım eden, onunla gönderilen nura uyanlar yok mu? İşte onlar saadete erenlerdir\" dedi.'),
(1111, 1111, 7, 157, 'A’râf ', 'Mekki', '\"Bu dünyada ve ahirette bizim için güzel olanı yaz; biz Sana yöneldik\" dedi. Allah: \"Azabıma dilediğim kimseyi uğratırım, rahmetim herşeyi kaplamıştır; bunu Allah\'a karşı gelmekten sakınanlara, zekat verenlere, ayetlerimize inanıp, yanlarındaki Tevrat ve İncil\'de yazılı buldukları, okuyup yazması olmayan peygambere uyanlara yazacağız. O peygamber, onlara, uygun olanı emreder ve fenalıktan meneder, temiz şeyleri helal, murdar şeyleri haram kılar, onların ağır yüklerini indirir, zor tekliflerini hafifletir. Bu peygambere inanan, hürmet eden, yardım eden, onunla gönderilen nura uyanlar yok mu? İşte onlar saadete erenlerdir\" dedi.'),
(1112, 1112, 7, 158, 'A’râf ', 'Mekki', 'De ki: \"Ey insanlar! Doğrusu ben, göklerin ve yerin hükümranı, O\'ndan başka tanrı bulunmayan, dirilten ve öldüren Allah\'ın, hepiniz için gönderdiği peygamberiyim. Allah\'a ve okuyup yazması olmayan, haber getiren peygamberine -ki o da Allah\'a ve sözlerine inanmıştır- inanın; ona uyun ki doğru yolu bulasınız.\"'),
(1113, 1113, 7, 159, 'A’râf ', 'Mekki', 'Musa\'nın milletinden bir topluluk hakkı gösterirler ve onunla hükmederlerdi.'),
(1114, 1114, 7, 160, 'A’râf ', 'Mekki', 'Biz İsrailoğullarını oymaklar halinde on iki topluluğa ayırdık. Milleti Musa\'dan su isteyince ona: \"Asanla taşa vur\" diye bildirdik; ondan on iki pınar fışkırdı. Herkes içeceği yeri öğrendi. Bulutla üzerlerine gölge yaptık, onlara kudret helvası ve bıldırcın indirdik, \"Size verdiğimiz rızıkların temiz olanlarından yiyin\" dedik. Onlar, karşı gelmekle, Bize değil kendilerine zulmediyorlardı.'),
(1115, 1115, 7, 161, 'A’râf ', 'Mekki', 'Onlara: \"Şu şehirde oturun, dilediğiniz gibi yiyip için, \"affet!\" deyin ve secde ederek kapısından girin; Biz de yanılmalarınızı bağışlarız. İyi davrananlara daha da artıracağız\" denmişti.'),
(1116, 1116, 7, 162, 'A’râf ', 'Mekki', 'Onların zulmedenleri, kendilerine söylenen sözü başkasiyle değiştirdiler. Biz de, o zalimlere, zulümlerinden ötürü gökten azab indirdik.'),
(1117, 1117, 7, 163, 'A’râf ', 'Mekki', 'Onlara, deniz kıyısındaki kasabanın durumunu sor. Cumartesi yasaklarına tecavüz ediyorlardı. Cumartesileri balıklar sürüyle geliyor, başka günler gelmiyorlardı. Biz onları, yoldan çıkmaları sebebiyle böylece deniyorduk.'),
(1118, 1118, 7, 164, 'A’râf ', 'Mekki', 'Aralarından bir topluluk: \"Allah\'ın yok edeceği veya şiddetli azaba uğratacağı bir millete niçin öğüt veriyorsunuz?\" dediler. Öğüt verenler: \"Rabbinize, hiç değilse bir özür beyan edebilmemiz içindir, belki Allah\'a karşı gelmekten sakınırlar\" dediler.'),
(1119, 1119, 7, 165, 'A’râf ', 'Mekki', 'Kendilerine yapılan öğütleri unutunca, Biz fenalıktan menedenleri kurtardık ve zalimleri, Allah\'a karşı gelmelerinden ötürü şiddetli azaba uğrattık.'),
(1120, 1120, 7, 166, 'A’râf ', 'Mekki', 'Kendilerine edilen yasakları aşınca, onlara: \"Aşağılık birer maymun olun\" dedik.'),
(1121, 1121, 7, 167, 'A’râf ', 'Mekki', 'Rabbin, kıyamet gününe kadar, onları, kötü azaba uğratacak kimseleri üzerlerine göndereceğini bildirmişti. Doğrusu Rabbin, cezayı çabuk verir. Doğrusu O bağışlar ve merhamet eder.'),
(1122, 1122, 7, 168, 'A’râf ', 'Mekki', 'Biz onları yeryüzünde iyiler ve aşağılıklar olarak bölük bölük ayırdık; iyiliğe dönerler diye onları güzellikler ve kötülüklerle sınadık.'),
(1123, 1123, 7, 169, 'A’râf ', 'Mekki', 'Ardlarından yerlerine gelen bir takım kötüler, Kitap\'a mirasçı oldular. \"Biz nasıl olsa affedileceğiz\" diyerek Kitap\'ın hükümlerini değiştirme karşılığı bu değersiz dünyanın mallarını alırlar; yine ona benzer geçici bir şey kendilerine gelince onu da kabul ederlerdi. Onlardan, Allah\'a karşı ancak gerçeği söyleyeceklerine dair Kitap üzerine söz alınmamış mıydı? Kitap\'da olanları okumamışlar mıydı? Allah\'a karşı gelmekten sakınanlar için, ahiret yurdu vardır, düşünmüyor musunuz? Biz, iyiliğe çalışanların ecrini elbette zayi etmeyiz.'),
(1124, 1124, 7, 170, 'A’râf ', 'Mekki', 'Ardlarından yerlerine gelen bir takım kötüler, Kitap\'a mirasçı oldular. \"Biz nasıl olsa affedileceğiz\" diyerek Kitap\'ın hükümlerini değiştirme karşılığı bu değersiz dünyanın mallarını alırlar; yine ona benzer geçici bir şey kendilerine gelince onu da kabul ederlerdi. Onlardan, Allah\'a karşı ancak gerçeği söyleyeceklerine dair Kitap üzerine söz alınmamış mıydı? Kitap\'da olanları okumamışlar mıydı? Allah\'a karşı gelmekten sakınanlar için, ahiret yurdu vardır, düşünmüyor musunuz? Biz, iyiliğe çalışanların ecrini elbette zayi etmeyiz.'),
(1125, 1125, 7, 171, 'A’râf ', 'Mekki', 'Tur dağını, gölgelik gibi onların üzerlerine yükseltmiştik, onlar tepelerine düşeceğini sanmışlardı. Onlara: \"Size verdiğimiz Kitap\'a sıkıca sarılın, içinde olanı düşünün ki sakınanlardan olasınız\" demiştik.'),
(1126, 1126, 7, 172, 'A’râf ', 'Mekki', 'Rabbin, insanoğlunun sulbünden soyunu alıp devam ettirmiş, onlara: \"Ben sizin Rabbiniz değil miyim\" demiş ve buna kendilerini şahit tutmuştu. Onlar da: \"Evet şahidiz\" demişlerdi. Bu, kıyamet günü, \"Bizim bundan haberimiz yoktu\" dersiniz veya \"Daha önce babalarımız Allah\'a ortak koşmuşlardı, biz de onlardan sonra gelen bir soyuz, bizi, boşa çalışanların yaptıklarından ötürü yok eder misin?\" dersiniz diyedir.'),
(1127, 1127, 7, 173, 'A’râf ', 'Mekki', 'Rabbin, insanoğlunun sulbünden soyunu alıp devam ettirmiş, onlara: \"Ben sizin Rabbiniz değil miyim\" demiş ve buna kendilerini şahit tutmuştu. Onlar da: \"Evet şahidiz\" demişlerdi. Bu, kıyamet günü, \"Bizim bundan haberimiz yoktu\" dersiniz veya \"Daha önce babalarımız Allah\'a ortak koşmuşlardı, biz de onlardan sonra gelen bir soyuz, bizi, boşa çalışanların yaptıklarından ötürü yok eder misin?\" dersiniz diyedir.'),
(1128, 1128, 7, 174, 'A’râf ', 'Mekki', 'Belki doğru yola dönerler diye ayetleri böylece uzun uzadıya açıklıyoruz.'),
(1129, 1129, 7, 175, 'A’râf ', 'Mekki', 'Onlara, şeytanın peşine takdığı ve kendisine verdiğimiz ayetlerden sıyrılarak azgınlıklardan olan kişinin olayını anlat.'),
(1130, 1130, 7, 176, 'A’râf ', 'Mekki', 'Dileseydik, onu ayetlerimizle üstün kılardık; fakat o, dünyaya meyletti ve hevesine uydu. Durumu, üstüne varsan da, kendi haline bıraksan da, dilini sarkıtıp soluyan köpeğin durumu gibidir. İşte ayetlerimizi yalan sayan kimselerin hali böyledir. Sen onlara bu kıssayı anlat, belki üzerinde düşünürler.'),
(1131, 1131, 7, 177, 'A’râf ', 'Mekki', 'Ayetlerimizi yalan sayan, kendine zulmeden millet ne kötü bir misaldir!'),
(1132, 1132, 7, 178, 'A’râf ', 'Mekki', 'Allah\'ın doğru yola sevkettiği kimse doğru yolda olur. Saptırdığı kimseler ise, işte onlar mahvolanlardır.'),
(1133, 1133, 7, 179, 'A’râf ', 'Mekki', 'And olsun ki, cehennem için de birçok cin ve insan yarattık; onların kalbleri vardır ama anlamazlar; gözleri vardır ama görmezler; kulakları vardır ama işitmezler. İşte bunlar hayvanlar gibi hatta daha sapıktırlar. İşte bunlar gafillerdir.'),
(1134, 1134, 7, 180, 'A’râf ', 'Mekki', 'En güzel isimler Allah\'ındır, O\'na o isimlerle dua edin, O\'nun isimleri konusunda eğriliğe sapanları bırakın. Onlar yaptıklarının cezasını göreceklerdir.'),
(1135, 1135, 7, 181, 'A’râf ', 'Mekki', 'Yarattıklarımızdan bir topluluk hakkı gösterirler ve onunla hükmederler.'),
(1136, 1136, 7, 182, 'A’râf ', 'Mekki', 'Ayetlerimizi yalan sayanları, bilmedikleri yönden, ağır ağır sonuçlarına yaklaştıracağız.'),
(1137, 1137, 7, 183, 'A’râf ', 'Mekki', 'Onlara mahsustan mühlet veririm, çünkü Benim düzenim çetindir.'),
(1138, 1138, 7, 184, 'A’râf ', 'Mekki', 'Düşünmüyorlar mı ki, arkadaşları olan peygamberde deliliğin eseri yoktur. O ancak açıkça uyaran bir kimsedir.'),
(1139, 1139, 7, 185, 'A’râf ', 'Mekki', 'Göklerin ve yerin hükümranlığını, Allah\'ın yarattığı her şeyi ve ecellerinin yaklaşmış olması ihtimalini düşünmüyorlar mı? Bundan sonra hangi söze inanacaklar?'),
(1140, 1140, 7, 186, 'A’râf ', 'Mekki', 'Allah\'ın saptırdığını yola getirecek yoktur. O, sapanları taşkınlıkları içinde bocalayıp dururlarken bırakır.'),
(1141, 1141, 7, 187, 'A’râf ', 'Mekki', 'Sana, kıyamet saatinin ne zaman gelip çatacağını soruyorlar, de ki: \"Onu ancak Rabbim bilir, onun vaktini, O\'ndan başka belirtecek yoktur. Göklerin ve yerin, ağırlığını kaldıramayacağı o saat, sizlere ansızın gelecektir.\" Sen sanki öğrenmişsin gibi sana soruyorlar, de ki: \"Onu bilmek ancak Allah\'a mahsustur, ama insanların çoğu bu gerçeği bilmezler.\"'),
(1142, 1142, 7, 188, 'A’râf ', 'Mekki', 'De ki: \"Allah\'ın dilemesi dışında ben kendime bir fayda ve zarar verecek durumda değilim. Görülmeyeni bileydim, daha çok iyilik yapardım ve bana kötülük de gelmezdi. Ben sadece, inanan bir milleti uyaran ve müjdeleyen bir peygamberim.\"'),
(1143, 1143, 7, 189, 'A’râf ', 'Mekki', 'Sizi bir nefisten yaratan ve gönlünün huzura kavuşacağı eşini de ondan var eden Allah\'tır. Eşine yaklaşınca, eşi hafif bir yük yüklendi ve bu halde bir müddet taşıdı. Hamileliği ağırlaşınca, karı-koca, Rableri olan Allah\'a: \"Bize kusursuz bir çocuk verirsen, and olsun ki şükredenlerden oluruz\" diye yalvardılar.'),
(1144, 1144, 7, 190, 'A’râf ', 'Mekki', 'Allah onlara kusursuz bir çocuk verince, kendilerine verdiği şey hakkında Allah\'a ortaklar koştular. Allah, onların ortak koştukları şeylerden yücedir.'),
(1145, 1145, 7, 191, 'A’râf ', 'Mekki', 'Kendileri yaratılmışken, bir şey yaratamayan putları mı ortak koşuyorlar?'),
(1146, 1146, 7, 192, 'A’râf ', 'Mekki', 'Oysa putlar ne onlara yardım edebilir ve ne de kendilerine bir yardımları olur.'),
(1147, 1147, 7, 193, 'A’râf ', 'Mekki', 'Onları doğru yola çağırırsanız, size uymazlar; çağırmanız da, susmanız da onlar için birdir.'),
(1148, 1148, 7, 194, 'A’râf ', 'Mekki', 'Allah\'tan başka taptıklarınız putlar da, sizin gibi yaratıklardır. Eğer doğru sözlü iseniz, onları çağırın da size cevap versinler bakalım.'),
(1149, 1149, 7, 195, 'A’râf ', 'Mekki', 'Onların yürüyecek ayakları mı var, yoksa tutacak elleri mi var, ya da görecek gözleri mi var, veya işitecek kulakları mı var? De ki: \"Ortaklarınızı çağırın elinizden gelirse bana tuzak kurun, göz açtırmayın.\"'),
(1150, 1150, 7, 196, 'A’râf ', 'Mekki', '\"Çünkü benim dostum, Kitap\'ı indiren Allah\'tır. O, iyileri dost edinir.\"'),
(1151, 1151, 7, 197, 'A’râf ', 'Mekki', '\"O\'nu bırakıp da taptıklarınız, kendilerine yardım edemezler ki size yardım etsinler.\"'),
(1152, 1152, 7, 198, 'A’râf ', 'Mekki', 'Onları doğru yola çağırırsanız duymazlar. Sana baktıklarını görürsün, oysa görmezler.'),
(1153, 1153, 7, 199, 'A’râf ', 'Mekki', 'Sen af yolunu tut, bağışla, uygun olanı emret, bilgisizlere aldırış etme.'),
(1154, 1154, 7, 200, 'A’râf ', 'Mekki', 'Şeytan seni dürtecek olursa Allah\'a sığın, doğrusu O işitir ve bilir.'),
(1155, 1155, 7, 201, 'A’râf ', 'Mekki', 'Allah\'a karşı gelmekten sakınanlar, şeytan tarafından bir vesveseye uğrayınca, Allah\'ı anarlar ve hemen gerçeği görürler.'),
(1156, 1156, 7, 202, 'A’râf ', 'Mekki', 'Şeytanın kardeşleri onları azgınlığa sürüklerler ve bundan hiç geri durmazlar.'),
(1157, 1157, 7, 203, 'A’râf ', 'Mekki', 'Onlara bir ayet getirmediğin zaman, \"Sen bir tane yapsaydın ya\" derler. De ki: \"Ben ancak Rabbim tarafından bana vahyolunana uyarım. Bu Kitap inanan millete Rabbinizden açık belgeler, yol gösterme ve rahmettir.\"'),
(1158, 1158, 7, 204, 'A’râf ', 'Mekki', 'Kuran okunduğu zaman ona kulak verin, dinleyin ki merhamet olunasınız.'),
(1159, 1159, 7, 205, 'A’râf ', 'Mekki', 'Rabbini gönülden ve korkarak içinden hafif bir sesle sabah akşam an, gafillerden olma.'),
(1160, 1160, 7, 206, 'A’râf ', 'Mekki', 'Doğrusu Rabbinin katında olanlar, O\'na kulluk etmekten büyüklenmezler, O\'nu tenzih ederler ve yalnız O\'na secde ederler.'),
(1161, 1161, 8, 1, 'Enfâl ', 'Medeni', 'Sana, ganimetlere dair soru sorarlar, de ki: Ganimetler Allah\'ın ve Peygamberindir. İnanıyorsanız Allah\'tan sakının, aranızdaki münasebetleri düzeltin, Allah\'a ve Peygamberine itaat edin.'),
(1162, 1162, 8, 2, 'Enfâl ', 'Medeni', 'İnananlar ancak, o kimselerdir ki Allah anıldığı zaman kalbleri titrer, ayetleri okunduğu zaman bu onların imanlarını artırır. Ve Rablerine güvenirler; namaz kılarlar; kendilerine verdiğimiz rızıktan yerli yerince sarf ederler.'),
(1163, 1163, 8, 3, 'Enfâl ', 'Medeni', 'İnananlar ancak, o kimselerdir ki Allah anıldığı zaman kalbleri titrer, ayetleri okunduğu zaman bu onların imanlarını artırır. Ve Rablerine güvenirler; namaz kılarlar; kendilerine verdiğimiz rızıktan yerli yerince sarf ederler.'),
(1164, 1164, 8, 4, 'Enfâl ', 'Medeni', 'İşte gerçekten inanmış olanlar bunlardır. Onlara Rablerinin katında mertebeler, mağfiret ve cömertçe verilmiş rızıklar vardır.'),
(1165, 1165, 8, 5, 'Enfâl ', 'Medeni', 'Nitekim, Rabbin seni hak uğrunda evinden savaş için çıkarmıştı, oysa müslümanların bir takımı bundan hoşlanmamıştı.'),
(1166, 1166, 8, 6, 'Enfâl ', 'Medeni', 'Sanki göz göre göre ölüme sürükleniyorlarmış gibi, gerçek ortaya çıktıktan sonra bile seninle tartışıyorlardı.'),
(1167, 1167, 8, 7, 'Enfâl ', 'Medeni', 'Allah bu iki taifeden birini size vadetmişti; siz, kuvvetsiz olanın size düşmesini istiyordunuz. Oysa, suçluların hoşuna gitmese de, hakkı ortaya çıkarmak ve batılı tepelemek için, Allah sözleriyle hakkı ortaya koymak ve inkarcıların kökünü kesmek istiyordu.'),
(1168, 1168, 8, 8, 'Enfâl ', 'Medeni', 'Allah bu iki taifeden birini size vadetmişti; siz, kuvvetsiz olanın size düşmesini istiyordunuz. Oysa, suçluların hoşuna gitmese de, hakkı ortaya çıkarmak ve batılı tepelemek için, Allah sözleriyle hakkı ortaya koymak ve inkarcıların kökünü kesmek istiyordu.'),
(1169, 1169, 8, 9, 'Enfâl ', 'Medeni', 'Rabbinizin yardımına sığınıyordunuz. O, \"Ben size, birbiri peşinden bin melekle yardım ederim\" diye cevap vermişti.'),
(1170, 1170, 8, 10, 'Enfâl ', 'Medeni', 'Allah bunu ancak bir müjde olması ve kalblerinizin yatışması için yapmıştı. Yardım ancak Allah katındandır. Doğrusu Allah güçlüdür, hakimdir.'),
(1171, 1171, 8, 11, 'Enfâl ', 'Medeni', 'Allah kendi katından bir güven işareti olarak sizi hafif bir uykuya daldırmıştı. Sizi arıtmak, sizden şeytan vesvesesini gidermek, kalblerinizi pekiştirmek ve sebatınızı artırmak için gökten size su indirmişti.'),
(1172, 1172, 8, 12, 'Enfâl ', 'Medeni', 'Rabbin meleklere, \"Ben sizinleyim, inananları destekleyin\" diye vahyetti. \"Ben inkar edenlerin kalblerine korku salacağım, artık vurun onların boyunları üstüne, vurun her parmağına\" dedi.'),
(1173, 1173, 8, 13, 'Enfâl ', 'Medeni', 'Bu, onların Allah\'a ve Peygamberine karşı koymalarındandır. Kim Allah\'a ve peygamberine karşı koyarsa, bilsin ki, Allah\'ın cezası şiddetlidir.'),
(1174, 1174, 8, 14, 'Enfâl ', 'Medeni', 'İşte bunu tadın, inkar edenlere cehennem azabı da vardır.'),
(1175, 1175, 8, 15, 'Enfâl ', 'Medeni', 'Ey İnananlar! Savaş için ilerlerken, inkar edenlerle toplu halde karşılaştığınızda onlara arkanızı dönmeyin.'),
(1176, 1176, 8, 16, 'Enfâl ', 'Medeni', 'Tekrar savaşmak için bir tarafa çekilmek veya bir başka topluluğa katılmak maksadı dışında, o gün arkasını düşmana dönen kimse Allah\'dan bir gazaba uğramış olur. Onun varacağı yer cehennemdir. Ne kötü bir dönüştür!'),
(1177, 1177, 8, 17, 'Enfâl ', 'Medeni', 'Onları siz öldürmediniz fakat Allah öldürdü. Attığın zaman da sen atmamıştın, fakat Allah atmıştı. Allah bunu, inananları güzel bir imtihana tabi tutmak için yapmıştı. Doğrusu O işitir ve bilir.'),
(1178, 1178, 8, 18, 'Enfâl ', 'Medeni', 'İşte bu, Allah\'ın inkarcıların düzenini zayıflatıp yok etmesidir.'),
(1179, 1179, 8, 19, 'Enfâl ', 'Medeni', 'Ey inkarcılar! Zafer istiyorsanız, işte zafer geldi (aleyhinize çıktı). Peygambere karşı gelmekten vazgeçerseniz sizin iyiliğinize olur, yok tekrar dönerseniz biz de döneriz; topluluğunuz çok da olsa size hiçbir fayda vermez. Allah inananlarla beraberdir.'),
(1180, 1180, 8, 20, 'Enfâl ', 'Medeni', 'Ey inananlar! Allah\'a ve Peygamberine itaat edin, Kuran\'ı dinleyip dururken yüz çevirmeyin, dinlemedikleri halde \"dinledik\" diyenler gibi olmayın.'),
(1181, 1181, 8, 21, 'Enfâl ', 'Medeni', 'Ey inananlar! Allah\'a ve Peygamberine itaat edin, Kuran\'ı dinleyip dururken yüz çevirmeyin, dinlemedikleri halde \"dinledik\" diyenler gibi olmayın.'),
(1182, 1182, 8, 22, 'Enfâl ', 'Medeni', 'Allah katında, yeryüzündeki canlıların en kötüsü gerçeği akletmeyen sağırlar ve dilsizlerdir.'),
(1183, 1183, 8, 23, 'Enfâl ', 'Medeni', 'Allah onlarda bir iyilik görseydi onlara işittirirdi. Onlara işittirmiş olsaydı yine de yüz çevirirlerdi, zaten dönektirler.'),
(1184, 1184, 8, 24, 'Enfâl ', 'Medeni', 'Ey inananlar! Allah ve Peygamber, sizi, hayat verecek şeye çağırdığı zaman icabet edin. Allah\'ın kişi ile kalbi arasına girdiğini ve sonunda O\'nun katında toplanacağınızı bilin.'),
(1185, 1185, 8, 25, 'Enfâl ', 'Medeni', 'Aranızdan yalnız zalimlere erişmekle kalmayacak fitneden sakının, Allah\'ın azabının şiddetli olduğunu bilin.'),
(1186, 1186, 8, 26, 'Enfâl ', 'Medeni', 'Yeryüzünde az sayıda olduğunuz ve zayıf sayıldığınız için insanların sizi esir olarak alıp götürmesinden korktuğunuz zamanları, hatırlayın. Allah, şükredesiniz diye sizi barındırmış, yardımıyla desteklemiş, temiz şeylerle rızıklandırmıştır.'),
(1187, 1187, 8, 27, 'Enfâl ', 'Medeni', 'Ey inananlar! Allah\'a ve Peygambere karşı hainlik etmeyin, size güvenilen şeylere bile bile hıyanet etmiş olursunuz.'),
(1188, 1188, 8, 28, 'Enfâl ', 'Medeni', 'Mallarınızın ve çocuklarınızın, aslında bir sınama olduğunu ve büyük ecrin Allah katında bulunduğunu bilin.'),
(1189, 1189, 8, 29, 'Enfâl ', 'Medeni', 'Ey inananlar! Allah\'tan sakınırsanız, O size iyiyi kötüden ayırdedecek bir anlayış verir, kötülüklerinizi örter, sizi bağışlar. Allah büyük, bol nimet sahibidir.'),
(1190, 1190, 8, 30, 'Enfâl ', 'Medeni', 'İnkar edenler, seni bağlayıp bir yere kapamak veya öldürmek, ya da sürmek için düzen kuruyorlardı. Onlar düzen kurarken, Allah da düzenlerini bozuyordu. Allah düzen yapanların en iyisidir.'),
(1191, 1191, 8, 31, 'Enfâl ', 'Medeni', 'Ayetlerimiz onlara okunduğu zaman, \"İşittik, işittik! İstesek biz de aynını söyleyebiliriz; bu sadece eskilerin masallarıdır\" derlerdi.'),
(1192, 1192, 8, 32, 'Enfâl ', 'Medeni', '\"Allah\'ımız! Eğer bu Kitap, gerçekten Senin katından ise bize gökten taş yağdır veya can yakıcı bir azab ver\" demişlerdi.'),
(1193, 1193, 8, 33, 'Enfâl ', 'Medeni', 'Oysa, sen içlerinde iken Allah onlara azabetmez. Onlar bağışlanma dilerlerken de elbette Allah azab edecek değildir.'),
(1194, 1194, 8, 34, 'Enfâl ', 'Medeni', 'Yoksa Mescidi Haram\'a girmekten menederlerken Allah onlara niçin azab etmesin? Hem de O\'nun dostu değiller; O\'nun dostları ancak karşı gelmekten sakınanlardır. Fakat çoğu bunu bilmiyorlar.'),
(1195, 1195, 8, 35, 'Enfâl ', 'Medeni', 'Kabe\'deki tapınmaları sadece ıslık çalmak ve el çırpmaktan başka bir şey değildir. İnkarınıza karşılık artık azabı tadın.'),
(1196, 1196, 8, 36, 'Enfâl ', 'Medeni', 'Doğrusu inkar edenler mallarını Allah\'ın yolundan insanları alıkoymak için sarfederler ve daha da sarfedeceklerdir; ama sonra içleri yanacak, hem de mağlup olacaklardır. Bu, Allah\'ın, temizi murdardan ayırması ve murdarları üstüste koyup hepsini yığarak cehenneme yerleştirmesi içindir; inkar edenler cehenneme toplanacaklardır. İşte onlar mahvolanlardır.'),
(1197, 1197, 8, 37, 'Enfâl ', 'Medeni', 'Doğrusu inkar edenler mallarını Allah\'ın yolundan insanları alıkoymak için sarfederler ve daha da sarfedeceklerdir; ama sonra içleri yanacak, hem de mağlup olacaklardır. Bu, Allah\'ın, temizi murdardan ayırması ve murdarları üstüste koyup hepsini yığarak cehenneme yerleştirmesi içindir; inkar edenler cehenneme toplanacaklardır. İşte onlar mahvolanlardır.'),
(1198, 1198, 8, 38, 'Enfâl ', 'Medeni', 'İnkar edenlere, eğer savaştan vazgeçerlerse, geçmişlerinin bağışlanacağını ve tekrar başlarlarsa evvelkilerin hükmünün uygulanacağını söyle.'),
(1199, 1199, 8, 39, 'Enfâl ', 'Medeni', 'Fitne kalmayıp, yalnız Allah\'ın dini kalana kadar onlarla savaşın. Eğer vazgeçerlerse bilsinler ki Allah onların işlediklerini şüphesiz görür.'),
(1200, 1200, 8, 40, 'Enfâl ', 'Medeni', 'Eğer yüz çevirirlerse Allah\'ın sizin dostunuz olduğunu bilin; O ne güzel dost, ne güzel yardımcıdır!'),
(1201, 1201, 8, 41, 'Enfâl ', 'Medeni', 'Eğer Allah\'a ve hakkı batıldan ayıran o günde, iki topluluğun karşılaştığı günde kulumuza indirdiğimize inanıyorsanız, bilin ki, ele geçirdiğiniz ganimetin beşte biri Allah\'ın, Peygamber\'in ve yakınlarının, yetimlerin, düşkünlerin ve yolcularındır. Allah her şeye Kadir\'dir.'),
(1202, 1202, 8, 42, 'Enfâl ', 'Medeni', 'Siz vadiye en yakın ve onlar da en uzak yamaçta idiler; kervanın süvarileri sizden daha aşağıdaydı. Savaş için buluşmak üzere sözleşmeye kalksaydınız, vaktini tayinde anlaşmazlığa düşerdiniz; fakat Allah mahvolan, apaçık belgeden ötürü mahvolsun, yaşayan da apaçık belgeden ötürü yaşasın diye olacak işi yaptı. Doğrusu Allah işitir ve bilir.'),
(1203, 1203, 8, 43, 'Enfâl ', 'Medeni', 'Allah onları uykunda sana az gösteriyordu. Çok göstermiş olsaydı, yılacak ve bu hususta çekişmeye başlıyacaktınız, fakat Allah sizi kurtardı; çünkü O kalblerde olanı bilir.'),
(1204, 1204, 8, 44, 'Enfâl ', 'Medeni', 'Karşılaştığınızda, olacak işi oldurmak için, onları gözlerinize az gösteriyor ve sizi de onların gözünde azaltıyordu. Bütün işler dönüp Allah\'a varır.'),
(1205, 1205, 8, 45, 'Enfâl ', 'Medeni', 'Ey inananlar! Bir toplulukla karşılaşırsanız dayanın; başarıya erişebilmeniz için Allah\'ı çok anın.'),
(1206, 1206, 8, 46, 'Enfâl ', 'Medeni', 'Allah\'a ve Peygamberine itaat edin; çekişmeyin, yoksa korkar başarısızlığa düşersiniz ve kuvvetiniz gider. Sabredin, doğrusu Allah sabredenlerle beraberdir.'),
(1207, 1207, 8, 47, 'Enfâl ', 'Medeni', 'Yurtlarından böbürlenerek, insanlara gösteriş yaparak çıkan ve Allah yolundan men edenler gibi olmayın. Allah onların işlediklerini her yönüyle bilendir.'),
(1208, 1208, 8, 48, 'Enfâl ', 'Medeni', 'Şeytan onlara işlediklerini güzel gösterdi ve \"Bugün insanlardan sizi yenecek kimse yoktur; doğrusu ben de size yardımcıyım\" dedi. İki ordu karşılaşınca da, geri dönüp, \"Benim sizinle ilgim yok; doğrusu sizin görmediğinizi ben görüyorum ve şüphesiz Allah\'tan korkuyorum, Allah\'ın azabı şiddetlidir\" dedi.'),
(1209, 1209, 8, 49, 'Enfâl ', 'Medeni', 'İkiyüzlüler ve kalblerinde hastalık bulunanlar \"Müslümanları dinleri aldattı\" diyorlardı; oysa, kim Allah\'a güvenirse bilmelidir ki Allah güçlüdür, hakimdir.'),
(1210, 1210, 8, 50, 'Enfâl ', 'Medeni', 'Melekler, inkar edenlerin yüzlerine ve sırtlarına vurarak, \"Yakıcı azabı tadın, bu, kendi ellerinizle yaptığınızın karşılığıdır\" diyerek canlarını alırken bir görseydin! Yoksa Allah kullara asla zulmetmez.'),
(1211, 1211, 8, 51, 'Enfâl ', 'Medeni', 'Melekler, inkar edenlerin yüzlerine ve sırtlarına vurarak, \"Yakıcı azabı tadın, bu, kendi ellerinizle yaptığınızın karşılığıdır\" diyerek canlarını alırken bir görseydin! Yoksa Allah kullara asla zulmetmez.'),
(1212, 1212, 8, 52, 'Enfâl ', 'Medeni', 'Firavun taifesi ve onlardan öncekilerin gidişi gibi, Allah\'ın ayetlerini yalanladılar da Allah onları günahlarından ötürü yoketti. Allah kuvvetlidir, cezalandırması şiddetlidir.'),
(1213, 1213, 8, 53, 'Enfâl ', 'Medeni', 'Bu, bir topluluk iyi gidişini değiştirmedikçe Allah\'ın da verdiği nimeti değiştirmeyeceğinden ve Allah\'ın işiten, bilen olmasındandır.'),
(1214, 1214, 8, 54, 'Enfâl ', 'Medeni', 'Firavun taifesi ve onlardan öncekilerin gidişi gibi, Rablerinin ayetlerini yalanladılar da onları günahlarından ötürü yok ettik. Firavun taifesini suda boğduk, hepsi zalimlerdi.'),
(1215, 1215, 8, 55, 'Enfâl ', 'Medeni', 'Allah katında yeryüzünde yaşayanların en kötüsü, inkar edenlerdir. Onlar artık inanmazlar.'),
(1216, 1216, 8, 56, 'Enfâl ', 'Medeni', 'Anlaşma yaptığın kimseler, sonucundan sakınmayarak anlaşmalarını her defasında bozarlar. Savaşta onları yakalarsan, arkalarındakilere ibret olacak şekilde, darmadağın et.'),
(1217, 1217, 8, 57, 'Enfâl ', 'Medeni', 'Anlaşma yaptığın kimseler, sonucundan sakınmayarak anlaşmalarını her defasında bozarlar. Savaşta onları yakalarsan, arkalarındakilere ibret olacak şekilde, darmadağın et.'),
(1218, 1218, 8, 58, 'Enfâl ', 'Medeni', 'Eğer bir topluluğun anlaşmaya hıyanet etmesinden korkarsan, sen de onlara karşı anlaşmayı bozarak aynı şekilde davran. Doğrusu Allah hainleri sevmez.'),
(1219, 1219, 8, 59, 'Enfâl ', 'Medeni', 'İnkar edenler, asla öne geçtiklerini sanmasınlar, çünkü onlar bizi aciz bırakamıyacaklardır.'),
(1220, 1220, 8, 60, 'Enfâl ', 'Medeni', 'Ey inananlar! Onlara karşı gücünüzün yettiği kadar Allah\'ın düşmanı ve sizin düşmanlarınızı ve bunların dışında Allah\'ın bilip sizin bilmediklerinizi yıldırmak üzere kuvvet ve savaş atları hazırlayın. Allah yolunda sarfettiğiniz her şey size haksızlık yapılmadan, tamamen ödenecektir.'),
(1221, 1221, 8, 61, 'Enfâl ', 'Medeni', 'Eğer onlar barışa yanaşırlarsa, sen de yanaş ve Allah\'a güven. O, şüphesiz işitir ve bilir.'),
(1222, 1222, 8, 62, 'Enfâl ', 'Medeni', 'Seni aldatmak isterlerse, bil ki şüphesiz Allah sana kafidir. Seni ve inananları yardımıyla destekleyen, kalblerini uzlaştıran O\'dur. Eğer yeryüzünde olan her şeyi sarfetsen bile, sen onların kalblerini uzlaştıramazdın, ama Allah onları uzlaştırdı. Doğrusu O Güçlü\'dür, Hakim\'dir.'),
(1223, 1223, 8, 63, 'Enfâl ', 'Medeni', 'Seni aldatmak isterlerse, bil ki şüphesiz Allah sana kafidir. Seni ve inananları yardımıyla destekleyen, kalblerini uzlaştıran O\'dur. Eğer yeryüzünde olan her şeyi sarfetsen bile, sen onların kalblerini uzlaştıramazdın, ama Allah onları uzlaştırdı. Doğrusu O Güçlü\'dür, Hakim\'dir.'),
(1224, 1224, 8, 64, 'Enfâl ', 'Medeni', 'Allah\'ın yardımı sana ve sana uyan müminlere yeter.'),
(1225, 1225, 8, 65, 'Enfâl ', 'Medeni', 'Müminleri savaş için coştur. Sizin sabırlı yirmi kişiniz onlardan ikiyüz kişiyi yener. Sizin yüz kişiniz, inkar edenlerden bin kişiyi yener; çünkü onlar anlayışsız bir güruhtur. Şimdi Allah yükünüzü hafifletti, zira içinizde zaaf bulunduğunu biliyordu. Sizin sabırlı yüz kişiniz onlardan ikiyüz kişiyi yener; sizin bin kişiniz, Allah\'ın izniyle, ikibin kişiyi yener. Allah sabredenlerle beraberdir.'),
(1226, 1226, 8, 66, 'Enfâl ', 'Medeni', 'Müminleri savaş için coştur. Sizin sabırlı yirmi kişiniz onlardan ikiyüz kişiyi yener. Sizin yüz kişiniz, inkar edenlerden bin kişiyi yener; çünkü onlar anlayışsız bir güruhtur. Şimdi Allah yükünüzü hafifletti, zira içinizde zaaf bulunduğunu biliyordu. Sizin sabırlı yüz kişiniz onlardan ikiyüz kişiyi yener; sizin bin kişiniz, Allah\'ın izniyle, ikibin kişiyi yener. Allah sabredenlerle beraberdir.'),
(1227, 1227, 8, 67, 'Enfâl ', 'Medeni', 'Yeryüzünde savaşırken, düşmanı yere sermeden esir almak hiçbir peygambere yaraşmaz. Geçici dünya malını istiyorsunuz, oysa Allah ahireti kazanmanızı ister. Allah Güçlü\'dür, Hakim\'dir.'),
(1228, 1228, 8, 68, 'Enfâl ', 'Medeni', 'Daha önceden Allah\'tan verilmiş bir hüküm olmasaydı, aldıklarınızdan ötürü size büyük bir azab erişirdi.'),
(1229, 1229, 8, 69, 'Enfâl ', 'Medeni', 'Elde ettiğiniz ganimetleri temiz ve helal olarak yiyin; Allah\'tan sakının, doğrusu Allah bağışlar ve merhamet eder.'),
(1230, 1230, 8, 70, 'Enfâl ', 'Medeni', 'Elinizde bulunan esirlere, \"Allah kalblerinizde bir iyilik bulursa, size sizden alınanın daha hayırlısını verir, sizi bağışlar, Allah bağışlayandır, merhamet edendir\" de.'),
(1231, 1231, 8, 71, 'Enfâl ', 'Medeni', 'Esirler sana hıyanet etmek isterlerse, bilsinler ki esasen daha önce de Allah\'a hıyanet etmişlerdi, Allah bundan ötürü onları yenmen için sana imkan verdi. Allah Bilen\'dir, Hakim\'dir.'),
(1232, 1232, 8, 72, 'Enfâl ', 'Medeni', 'Doğrusu inanıp hicret edenler, Allah yolunda mallarıyla canlarıyla cihat edenler ve muhacirleri barındırıp onlara yardım edenler, işte bunlar birbirinin dostudurlar. İnanıp hicret etmeyenlerle, hicret edene kadar sizin dostluğunuz yoktur. Fakat din uğrunda yardım isterlerse, aranızda anlaşma olmayan topluluktan başkasına karşı onlara yardım etmeniz gerekir. Allah işlediklerinizi görür.'),
(1233, 1233, 8, 73, 'Enfâl ', 'Medeni', 'İnkar edenler birbirlerinin dostlarıdır. Eğer siz aranızda dost olmazsanız yeryüzünde kargaşalık, fitne ve büyük bozgun çıkar.'),
(1234, 1234, 8, 74, 'Enfâl ', 'Medeni', 'İnanıp hicret eden, Allah yolunda savaşanlar ve muhacirleri barındırıp onlara yardım edenler, işte onlar gerçekten inanmış olanlardır. Onlara mağfiret ve cömertçe verilmiş rızıklar vardır.'),
(1235, 1235, 8, 75, 'Enfâl ', 'Medeni', 'Sonra inanıp hicret eden ve sizinle birlikte savaşanlar, işte onlar sizdendir. Birbirinin mirasçısı olan akraba, Allah\'ın Kitap\'ına göre birbirine daha yakındır. Doğrusu Allah her şeyi bilir.'),
(1236, 1236, 9, 1, 'Tevbe ', 'Medeni', 'Allah\'tan ve Peygamberinden, kendileriyle andlaşma yaptığınız müşriklere ihtardır: Yeryüzünde dört ay daha dolaşabilirsiniz. Allah\'ı aciz bırakamayacağınızı, Allah\'ın inkarcıları rezil edeceğini bilin.'),
(1237, 1237, 9, 2, 'Tevbe ', 'Medeni', 'Allah\'tan ve Peygamberinden, kendileriyle andlaşma yaptığınız müşriklere ihtardır: Yeryüzünde dört ay daha dolaşabilirsiniz. Allah\'ı aciz bırakamayacağınızı, Allah\'ın inkarcıları rezil edeceğini bilin.'),
(1238, 1238, 9, 3, 'Tevbe ', 'Medeni', 'Allah\'ın ve Peygamberinin, ortak koşanlardan uzak olduğunu, büyük hac günü, Allah ve peygamberi insanlara ilan eder. Eğer tevbe ederseniz, bu sizin için daha hayırlı olur, yüz çevirirseniz, bilin ki siz Allah\'ı aciz bırakamazsınız. İnkar edenlere can yakıcı azabı müjdele.'),
(1239, 1239, 9, 4, 'Tevbe ', 'Medeni', 'Yalnız, andlaşma hükümlerinde size karşı bir eksiklik yapmayan ve aleyhinizde kimseye yardım etmeyen müşriklerle yaptığınız andlaşmaya sonuna kadar riayet edin. Allah sakınanları sever.'),
(1240, 1240, 9, 5, 'Tevbe ', 'Medeni', 'Hürmetli aylar çıkınca, puta tapanları bulduğunuz yerde öldürün; onları yakalayıp hapsedin; her gözetleme yerinde onları bekleyin. Eğer tevbe eder, namaz kılar ve zekat verirlerse yollarını serbest bırakın. Doğrusu Allah bağışlar ve merhamet eder.'),
(1241, 1241, 9, 6, 'Tevbe ', 'Medeni', 'Puta tapanlardan biri sana sığınırsa, onu güvene al; ta ki Allah\'ın sözünü dinlesin. Sonra onu güven içinde olacağı yere ulaştır. Çünkü onlar bilgisiz bir topluluktur.'),
(1242, 1242, 9, 7, 'Tevbe ', 'Medeni', 'Mescidi Haram\'ın yanında andlaştıklarınızın dışında, puta tapanların Allah katında ve Peygamberi önünde nasıl bir andlaşmaları olabilir. Size doğru davrandıkça siz de onlara doğru davranın. Allah, sözleşmelerini bozmaktan sakınanları sever.'),
(1243, 1243, 9, 8, 'Tevbe ', 'Medeni', 'Nasıl olabilir ki, size üstün gelselerdi ne bir yakınlık, ne de bir ahd gözetirlerdi. Kalpleriyle istemezlerken sizi ağızlarıyla hoşnut etmeye uğraşırlar; çokları fasıktırlar.'),
(1244, 1244, 9, 9, 'Tevbe ', 'Medeni', 'Allah\'ın ayetlerini az bir değere değişip, O\'nun yolundan alıkoydular. Onların işledikleri gerçekten ne kötüdür!'),
(1245, 1245, 9, 10, 'Tevbe ', 'Medeni', 'Onlar hiçbir müminin yakınlık veya ahdini gözetmezler. İşte aşırı gidenler bunlardır.'),
(1246, 1246, 9, 11, 'Tevbe ', 'Medeni', 'Eğer tevbe eder, namaz kılar ve zekat verirlerse, sizin din kardeşiniz olurlar. Bilen kimseler için ayetleri uzun uzadıya açıklıyoruz.'),
(1247, 1247, 9, 12, 'Tevbe ', 'Medeni', 'Eğer andlaşmalarından sonra, yeminlerini bozarlar, dininize dil uzatırlarsa, inkarda önde gidenlerle savaşın, çünkü onların yeminleri sayılmaz, belki vazgeçerler.'),
(1248, 1248, 9, 13, 'Tevbe ', 'Medeni', 'Yeminlerini bozan, Peygamberi sürgüne göndermeye azmeden bir toplumla savaşmanız gerekmez mi ki, önce onlar başlamışlardır? Onlardan korkar mısınız? Eğer inanıyorsanız bilin ki asıl korkmanız gereken Allah\'tır.'),
(1249, 1249, 9, 14, 'Tevbe ', 'Medeni', 'Onlarla savaşın ki Allah sizin elinizle onları azablandırsın, rezil etsin ve sizi üstün getirsin de müminlerin gönüllerini ferahlandırsın, kalblerindeki öfkeyi gidersin. Allah dilediğinin tevbesini kabul eder. Allah Bilendir, Hakimdir.'),
(1250, 1250, 9, 15, 'Tevbe ', 'Medeni', 'Onlarla savaşın ki Allah sizin elinizle onları azablandırsın, rezil etsin ve sizi üstün getirsin de müminlerin gönüllerini ferahlandırsın, kalblerindeki öfkeyi gidersin. Allah dilediğinin tevbesini kabul eder. Allah Bilendir, Hakimdir.'),
(1251, 1251, 9, 16, 'Tevbe ', 'Medeni', 'Allah, içinizden cihat edenleri; Allah\'tan, peygamberinden ve inananlardan başka sırdaş edinmeyenleri ortaya çıkarmadan sizi kendi halinize bırakacak mı zannediyorsunuz? Allah işlediklerinizden haberdardır.'),
(1252, 1252, 9, 17, 'Tevbe ', 'Medeni', 'Puta tapanların kendilerinin inkarcı olduklarını itiraf edip dururken Allah\'ın mescidlerini onarmaları gerekmez. Onların işledikleri boşa gitmiştir, cehennemde temelli kalacaklardır.'),
(1253, 1253, 9, 18, 'Tevbe ', 'Medeni', 'Allah\'ın mescidlerini sadece, Allah\'a ve ahiret gününe inanan, namaz kılan, zekat veren ve ancak Allah\'tan korkan kimseler onarır. İşte onlar doğru yolda bulunanlardan olabilirler.'),
(1254, 1254, 9, 19, 'Tevbe ', 'Medeni', 'Hacca gelenlere su vermeyi, Mescidi Haramı onarmayı, Allah\'a ve ahiret gününe inananla, Allah yolunda cihat edenle bir mi tuttunuz? Allah katında bir olmazlar; Allah zulmeden milleti doğru yola eriştirmez.'),
(1255, 1255, 9, 20, 'Tevbe ', 'Medeni', 'İnanan, hicret eden ve Allah yolunda mallarıyla, canlarıyla cihat eden kimselere Allah katında en büyük dereceler vardır. İşte kurtulanlar onlardır.'),
(1256, 1256, 9, 21, 'Tevbe ', 'Medeni', 'Rableri onlara katından bir rahmet, hoşnutluk ve içinde tükenmez nimetler bulunan cennetleri müjdeler. Doğrusu büyük ecir Allah katındadır.'),
(1257, 1257, 9, 22, 'Tevbe ', 'Medeni', 'Rableri onlara katından bir rahmet, hoşnutluk ve içinde tükenmez nimetler bulunan cennetleri müjdeler. Doğrusu büyük ecir Allah katındadır.'),
(1258, 1258, 9, 23, 'Tevbe ', 'Medeni', 'Ey inananlar! Babalarınızı, kardeşlerinizi küfrü imana tercih ediyorlarsa dost edinmeyin. Sizden onları kim dost edinirse doğrusu kendine yazık etmiş olurlar.'),
(1259, 1259, 9, 24, 'Tevbe ', 'Medeni', 'De ki: \"Babalarınız, oğullarınız, kardeşleriniz, eşleriniz, akrabanız, elde ettiğiniz mallar, durgun gitmesinden korktuğunuz ticaret, hoşunuza giden evler sizce Allah\'tan, Peygamberinden ve Allah yolunda savaşmaktan daha sevgili ise, Allah\'ın buyruğu gelene kadar bekleyin. Allah fasık kimseleri doğru yola eriştirmez.\"'),
(1260, 1260, 9, 25, 'Tevbe ', 'Medeni', 'And olsun ki Allah size birçok yerlerde, ve çokluğunuzun sizi böbürlendirdiği fakat bir faydası da olmadığı, yeryüzünün geniş olmasına rağmen size dar gelip de bozularak arkanıza döndüğünüz Huneyn gününde yardım etmişti.'),
(1261, 1261, 9, 26, 'Tevbe ', 'Medeni', 'Bozgundan sonra Allah, Peygamberine, müminlere güvenlik verdi ve görmediğiniz askerler indirdi; inkar edenleri azaba uğrattı. İnkarcıların cezası budur.'),
(1262, 1262, 9, 27, 'Tevbe ', 'Medeni', 'Allah bundan sonra da dilediğinin tevbesini kabul eder. Allah bağışlar ve merhamet eder.'),
(1263, 1263, 9, 28, 'Tevbe ', 'Medeni', 'Ey inananlar! Doğrusu puta tapanlar pistirler, bu sebeple, bu yıllardan sonra Mescidi Haram\'a yaklaşmasınlar. Eğer fakirlikten korkarsanız, bilin ki Allah dilerse sizi bol nimetiyle zenginleştirecektir. Allah şüphesiz bilendir, hakimdir.'),
(1264, 1264, 9, 29, 'Tevbe ', 'Medeni', 'Kitap verilenlerden, Allah\'a, ahiret gününe inanmayan, Allah\'ın ve Peygamberinin haram kıldığını haram saymayan, hak dinini din edinmeyenlerle, boyunlarını büküp kendi elleriyle cizye verene kadar savaşın.'),
(1265, 1265, 9, 30, 'Tevbe ', 'Medeni', 'Yahudiler, \"Üzeyr Allah\'ın oğludur\" dediler; Hıristiyanlar, \"Mesih Allah\'ın oğludur\" dediler. Bu, daha önce inkar edenlerin sözlerine benzeterek ağızlarında geveledikleri sözdür. Allah onları yok etsin, nasıl da uyduruyorlar!'),
(1266, 1266, 9, 31, 'Tevbe ', 'Medeni', 'Onlar Allah\'ı bırakıp hahamlarını, papazlarını ve Meryem oğlu Mesih\'i rableri olarak kabul ettiler. Oysa tek Tanrı\'dan başkasına kulluk etmemekle emrolunmuşlardı. Ondan başka tanrı yoktur. Allah, koştukları eşlerden münezzehtir.'),
(1267, 1267, 9, 32, 'Tevbe ', 'Medeni', 'Allah\'ın nurunu ağızlarıyla söndürmek isterler. Kafirler istemese de Allah nurunu mutlaka tamamlayacaktır.'),
(1268, 1268, 9, 33, 'Tevbe ', 'Medeni', 'Puta tapanlar hoşlanmasa da, dinini bütün dinlerden üstün kılmak üzere, Peygamberini doğru yol ve hak dinle gönderen Allah\'tır.'),
(1269, 1269, 9, 34, 'Tevbe ', 'Medeni', 'Ey inananlar! Hahamlar ve rahiplerin çoğu, insanların mallarını haksızlıkla yerler. Allah yolundan alıkoyarlar. Altın ve gümüşü biriktirip Allah yolunda sarf etmeyenlere can yakıcı bir azabı müjdele.'),
(1270, 1270, 9, 35, 'Tevbe ', 'Medeni', 'Bunlar cehennem ateşinde kızdırıldığı gün, alınları, böğürleri ve sırtları onlarla dağlanacak, \"Bu, kendiniz için biriktirdiğinizdir; biriktirdiğinizi tadın\" denecek.'),
(1271, 1271, 9, 36, 'Tevbe ', 'Medeni', 'Allah\'ın gökleri ve yeri yarattığı günkü yazısında, Allah\'a göre ayların sayısı onikidir. Bunlardan dördü hürmetli aydır. Bu dosdoğru bir nizamdır. Öyleyse o aylar içinde kendinize yazık etmeyin, topyekun sizinle savaşan putperestlerle siz de topyekun savaşın, Allah\'ın sakınanlarla beraber olduğunu bilin.'),
(1272, 1272, 9, 37, 'Tevbe ', 'Medeni', 'Sapıtmak için hürmetli ayların yerlerini değiştirip geciktirmek, küfürde gerçekten ileri gitmekdir. İnkar edenler Allah\'ın haram kıldığı ayların sayısına uydurmak için, onu bir yıl haram, bir yıl helal sayıyor, böylece Allah\'ın haram kıldığını helalkılıyorlar. Kötü işleri kendilerine güzel göründü. Allah inkar eden toplumu doğru yola eriştirmez.'),
(1273, 1273, 9, 38, 'Tevbe ', 'Medeni', 'Ey inananlar! Size ne oldu ki, \"Allah yolunda, savaşa çıkın\" dendiği zaman yere çöküp kaldınız? Oysa dünya hayatının geçimi ahirete göre pek az bir şeydir.'),
(1274, 1738, 13, 31, 'Ra’d ', 'Mekki', 'Eğer Kuran ile dağlar yürütülmüş veya yeryüzü parçalanmış yahut ölüler konuşturulmuş olsaydı, kafirler yine de inanmazlardı. Oysa bütün işler Allah\'a aittir. İnananların, \"Allah dilese bütün insanları doğru yola eriştirebilir\" gerçeğini akılları kesmedi mi? Allah\'ın sözü yerine gelinceye kadar, yaptıkları işler sebebiyle inkar edenlere bir belanın dokunması veya evlerinin yakınına inmesi devam eder durur. Allah, verdiği sözden şüphesiz caymaz.'),
(1275, 1739, 13, 32, 'Ra’d ', 'Mekki', 'And olsun ki, senden önce de nice peygamberler alaya alınmıştı. İnkar edenleri önce erteledim, sonra cezalarını verdim. Cezalandırmam nasıldı?'),
(1276, 1740, 13, 33, 'Ra’d ', 'Mekki', 'Herkesin yaptığını gözeten Allah, bunu yapamayan putlarla bir olur mu? Onlar Allah\'a ortak koştular. De ki: \"Onlara bir ad bulun bakalım; yeryüzünde bilmediği bir şeyi mi Allah\'a haber veriyorsunuz? Yoksa kuru sözlere mi aldanıyorsunuz? Fakat inkar edenlere, kurdukları düzenler güzel gösterildi ve doğru yoldan alıkonuldular. Zaten Allah\'ın saptırdığına yol gösteren bulunmaz.'),
(1277, 1741, 13, 34, 'Ra’d ', 'Mekki', 'Onlara, dünya hayatında azap vardır, ahiret azabı ise daha çetindir. Allah\'a karşı onları bir koruyan da yoktur.'),
(1278, 1742, 13, 35, 'Ra’d ', 'Mekki', 'Allah\'a karşı gelmekten sakınanlara vadedilen cennetin altından ırmaklar akar; oranın yiyecekleri ve gölgeleri devamlıdır. Bu, sakınanların elde edeceği sonuçtur, inkarcıların varacağı sonuç ise ateştir.'),
(1279, 1743, 13, 36, 'Ra’d ', 'Mekki', 'Kendilerine kitap verdiklerimiz, sana indirilenden memnun olurlar. Karşı guruplar içinde ise, onun bir kısmını inkar edenler vardır. De ki: \"Ben ancak Allah\'a kulluk etmekle ve O\'na asla ortak koşmamakla emrolundum. Hepinizi ancak O\'na çağırıyorum vedönüşüm O\'nadır.\"'),
(1280, 1744, 13, 37, 'Ra’d ', 'Mekki', 'Böylece Biz Kuran\'ı Arapça bir hüküm ve hikmet olarak indirdik. Sana ilim geldikten sonra onların heveslerine uyarsan, and olsun ki, Allah katında sana bir dost ve seni koruyan çıkmaz.'),
(1281, 1745, 13, 38, 'Ra’d ', 'Mekki', 'And olsun ki, senden önce nice peygamberler gönderdik; onlara eşler ve çocuklar verdik. Allah\'ın izni olmadan hiçbir peygamber bir ayet getiremez. Her şeyin vakti ve süresi yazılıdır.'),
(1282, 1746, 13, 39, 'Ra’d ', 'Mekki', 'Allah dilediğini siler, dilediğini bırakır; Ana Kitap O\'nun katındadır.'),
(1283, 1747, 13, 40, 'Ra’d ', 'Mekki', 'Onlara vadettiğimiz azabın bir kısmını sana göstersek de senin canını alsak da, vazifen sadece tebliğ etmektir. Hesap görmek Bize düşer.'),
(1284, 1748, 13, 41, 'Ra’d ', 'Mekki', 'Görmüyorlar mı ki, Biz yeryüzünü etrafından gitgide eksiltmekteyiz. Hüküm Allah\'ındır, O\'nun hükmünü takip edip bozacak yoktur. O, hesabı çabuk görür.'),
(1285, 1749, 13, 42, 'Ra’d ', 'Mekki', 'Onlardan öncekiler de tuzak kurdular, oysa bütün tuzaklar(ın cezası) Allah\'ındır, Herkesin yaptığını bilir. İnkarcılar da, neticenin kimin olduğunu göreceklerdir.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(1286, 1750, 13, 43, 'Ra’d ', 'Mekki', 'İnkar edenler: \"Sen peygamber değilsin\" derler; de ki: \"Benimle sizin aranızda şahit olarak Allah ve Kitap\'ı bilenler yeter.\"'),
(1287, 1751, 14, 1, 'İbrahim ', 'Mekki', 'Elif, Lam, Ra; Bu, Allah\'ın izniyle, insanları karanlıklardan aydınlığa, güçlü ve övülmeğe layık, göklerde ve yerde olanların sahibi Allah\'ın yoluna çıkarman için, sana indirdiğimiz Kitaptır. Uğrayacakları çetin azabdan dolayı vay kafirlerin haline!'),
(1288, 1752, 14, 2, 'İbrahim ', 'Mekki', 'Elif, Lam, Ra; Bu, Allah\'ın izniyle, insanları karanlıklardan aydınlığa, güçlü ve övülmeğe layık, göklerde ve yerde olanların sahibi Allah\'ın yoluna çıkarman için, sana indirdiğimiz Kitaptır. Uğrayacakları çetin azabdan dolayı vay kafirlerin haline!'),
(1289, 1753, 14, 3, 'İbrahim ', 'Mekki', 'Onlar dünya hayatını ahirete tercih ederler, Allah\'ın yolundan alıkoyup onun eğriliğini isterler. İşte onlar uzak bir sapıklık içindedirler.'),
(1290, 1754, 14, 4, 'İbrahim ', 'Mekki', 'Kendilerine apaçık anlatabilsin diye, her peygamberi kendi milletinin diliyle gönderdik. Allah dilediğini saptırır ve dilediğini de doğru yola eriştirir; güçlü olan, Hakim olan O\'dur.'),
(1291, 1755, 14, 5, 'İbrahim ', 'Mekki', 'And olsun ki Musa\'yı ayetlerimizle, \"Milletini karanlıklardan aydınlığa çıkar ve Allah\'ın günlerini onlara hatırlat\" diye göndermiştik. Bunlarda, çokça sabreden ve şükreden herkes için dersler vardır.'),
(1292, 1756, 14, 6, 'İbrahim ', 'Mekki', 'Musa, milletine dedi ki: \"Allah\'ın size olan nimetlerini anın; size işkence eden, kadınlarınızı sağ bırakıp oğullarınızı boğazlayan Firavun ailesinden sizi kurtardı; bütün bunlarda Rabbinizden size büyük bir imtihan vardır.\"'),
(1293, 1757, 14, 7, 'İbrahim ', 'Mekki', 'Rabbiniz: \"Şükrederseniz and olsun ki, size karşılığını artıracağım; nankörlük ederseniz bilin ki azabım pek çetindir\" diye bildirmişti.'),
(1294, 1758, 14, 8, 'İbrahim ', 'Mekki', 'Musa: \"Siz ve yeryüzünde olanlar, hepiniz nankörlük etseniz, Allah yine de müstağni ve övülmeğe layık olandır\" demişti.'),
(1295, 1759, 14, 9, 'İbrahim ', 'Mekki', 'Sizden önce gecen Nuh, Ad, Semud milletlerinin ve onlardan sonra gelenlerin haberleri ki onları Allah\'tan başkası bilmez size ulaşmadı mı? Onlara peygamberleri belgelerle geldiler, fakat ellerini ağızlarına götürüp: \"Biz sizinle gönderilene inanmıyoruz. Bizi çağırdığınız şeyden de şüphe ve endişe içindeyiz\" dediler.'),
(1296, 1760, 14, 10, 'İbrahim ', 'Mekki', 'Onların peygamberleri: \"Gökleri ve yeri yaratan, günahlarınızı bağışlamaya çağıran ve bir süreye kadar sizi erteleyen Allah\'tan mı şüphe ediyorsunuz?\" dediler. Onlar da: \"Siz de sadece bizim gibi birer insansınız; bizi babalarımızın taptıklarından alıkoymak istiyorsunuz. Öyleyse bize apaçık bir delil getirmelisiniz\" dediler.'),
(1297, 1761, 14, 11, 'İbrahim ', 'Mekki', 'Peygamberleri onlara şöyle dedi: \"Biz ancak sizin gibi birer insanız ama, Allah, kullarından dilediğine iyilikte bulunur. Allah\'ın izni olmadıkça biz size delil getiremeyiz. İnananlar sadece Allah\'a güvensin.\"'),
(1298, 1762, 14, 12, 'İbrahim ', 'Mekki', '\"Bize yollarımızı gösteren Allah\'a niçin güvenmeyelim? Bize ettiğiniz eziyete elbette katlanacağız. Güvenenler ancak Allah\'a güvensinler.\"'),
(1299, 1763, 14, 13, 'İbrahim ', 'Mekki', 'İnkar edenler, peygamberlerine: \"Ya bizim dinimize dönersiniz ya da sizi memleketimizden çıkarırız\" dediler. Rableri peygamberlere: \"Biz, haksızlık edenleri yok edeceğiz, onlardan sonra yeryüzüne sizi yerleştireceğiz. Bu, makamımdan ve tehdidimden korkanlar içindir.\" diye vahyetti.'),
(1300, 1764, 14, 14, 'İbrahim ', 'Mekki', 'İnkar edenler, peygamberlerine: \"Ya bizim dinimize dönersiniz ya da sizi memleketimizden çıkarırız\" dediler. Rableri peygamberlere: \"Biz, haksızlık edenleri yok edeceğiz, onlardan sonra yeryüzüne sizi yerleştireceğiz. Bu, makamımdan ve tehdidimden korkanlar içindir.\" diye vahyetti.'),
(1301, 1765, 14, 15, 'İbrahim ', 'Mekki', 'Peygamberler yardım istediler ve her inatçı zorba hüsrana uğradı.'),
(1302, 1766, 14, 16, 'İbrahim ', 'Mekki', 'Ardında cehennem vardır; orada kendisine irinli su içirilecektir.'),
(1303, 1767, 14, 17, 'İbrahim ', 'Mekki', 'Onu yudum yudum alacak fakat yutamıyacaktır. Ölüm ona her taraftan geldiği halde, ölemiyecek, arkasından da çetin bir azap gelecektir.'),
(1304, 1768, 14, 18, 'İbrahim ', 'Mekki', 'Rablerini inkar edenlerin işleri, fırtınalı bir günde, rüzgarın şiddetle savurduğu küle benzer; yaptıklarından hiçbir şey elde edemezler. İşte bu uzak sapıklıktır.'),
(1305, 1769, 14, 19, 'İbrahim ', 'Mekki', 'Gökleri ve yeri gerçekten Allah\'ın yarattığını bilmiyor musun? Dilerse sizi yok edip yeni bir topluluk var eder.'),
(1306, 1770, 14, 20, 'İbrahim ', 'Mekki', 'Bu, Allah için güç değildir.'),
(1307, 1771, 14, 21, 'İbrahim ', 'Mekki', 'İnsanların hepsi Allah\'ın huzuruna çıkarlar; güçsüzler, büyüklük taslayanlara: \"Doğrusu biz size uymuştuk, Allah\'ın azabından bizi koruyabilecek misiniz?\" derler. Cevap olarak: \"Allah bizi doğru yola eriştirseydi biz de sizi eriştirirdik. Artık sızlansak da sabretsek de birdir, çünkü kaçacak yerimiz yoktur\" derler.'),
(1308, 1772, 14, 22, 'İbrahim ', 'Mekki', 'İş olup bitince, şeytan: \"Doğrusu Allah size gerçeği söz vermişti. Ben de size söz verdim ama, sonra caydım; esasen sizi zorlayacak bir nüfuzum yoktu; sadece çağırdım, siz de geldiniz. O halde, beni değil kendinizi kınayın. Artık ben sizi kurtaramam, siz de beni kurtaramazsınız. Beni Allah\'a ortak koşmanızı daha önce kabul etmemiştim; doğrusu zalimlere can yakan bir azap vardır\" der.'),
(1309, 1773, 14, 23, 'İbrahim ', 'Mekki', 'İnanan ve yararlı işleri yapanlar, içlerinden ırmaklar akan cennetlere konulurlar, Rablerinin izniyle orada temelli kalırlar. Oradaki dirlik temennileri: \"Selam!\"dır.'),
(1310, 1774, 14, 24, 'İbrahim ', 'Mekki', 'Allah\'ın, hoş bir sözü; kökü sağlam, dalları göğe doğru olan, Rabbinin izniyle her zaman meyve veren hoş bir ağaca benzeterek nasıl misal verdiğini görmüyor musun? İnsanlar ibret alsın diye Allah onlara misal gösteriyor.'),
(1311, 1775, 14, 25, 'İbrahim ', 'Mekki', 'Allah\'ın, hoş bir sözü; kökü sağlam, dalları göğe doğru olan, Rabbinin izniyle her zaman meyve veren hoş bir ağaca benzeterek nasıl misal verdiğini görmüyor musun? İnsanlar ibret alsın diye Allah onlara misal gösteriyor.'),
(1312, 1776, 14, 26, 'İbrahim ', 'Mekki', 'Çirkin bir söz de, yerden koparılmış, kökü olmayan kötü bir ağaca benzer.'),
(1313, 1777, 14, 27, 'İbrahim ', 'Mekki', 'Allah inananları, dünya hayatında ve ahirette sağlam bir söz üzerinde tutar; zalimleri de saptırır. Allah dilediğini yapar.'),
(1314, 1778, 14, 28, 'İbrahim ', 'Mekki', 'Allah\'ın verdiği nimeti nankörlükle karşılayanları ve milletlerini helak olacakları yere, yaslanacakları cehenneme götürenleri görmüyor musun?'),
(1315, 1779, 14, 29, 'İbrahim ', 'Mekki', 'Allah\'ın verdiği nimeti nankörlükle karşılayanları ve milletlerini helak olacakları yere, yaslanacakları cehenneme götürenleri görmüyor musun?'),
(1316, 1780, 14, 30, 'İbrahim ', 'Mekki', 'Allah\'ın yolundan sapıtmak için O\'na eşler koşmuşlardı. De ki: \"Yaşayın bakalım, hiç şüphesiz varacağınız yer ateş olacaktır.\"'),
(1317, 1781, 14, 31, 'İbrahim ', 'Mekki', 'İnanan kullarıma söyle, namazı kılsınlar; alışveriş ve dostluğun olmayacağı günün gelmesinden önce, kendilerine verdiğimiz rızıktan açık ve gizli sarfetsinler.'),
(1318, 1782, 14, 32, 'İbrahim ', 'Mekki', 'Gökleri ve yeri yaratan, yukardan indirdiği su ile rızık olarak ürünler yetiştiren, emri gereğince denizde yüzmek üzere gemileri, nehirleri, belli yörüngelerinde yürüyen ay ve güneşi, geceyle gündüzü sizin buyruğunuza veren Allah\'tır.'),
(1319, 1783, 14, 33, 'İbrahim ', 'Mekki', 'Gökleri ve yeri yaratan, yukardan indirdiği su ile rızık olarak ürünler yetiştiren, emri gereğince denizde yüzmek üzere gemileri, nehirleri, belli yörüngelerinde yürüyen ay ve güneşi, geceyle gündüzü sizin buyruğunuza veren Allah\'tır.'),
(1320, 1784, 14, 34, 'İbrahim ', 'Mekki', 'Kendisinden isteyebileceğiniz her şeyi size vermiştir. Allah\'ın nimetini sayacak olsanız bitiremezsiniz. Doğrusu insan pek zalim ve çok nankördür.'),
(1321, 1785, 14, 35, 'İbrahim ', 'Mekki', 'İbrahim şöyle demişti: \"Rabbim! Bu şehri güvenli kıl; beni ve oğullarımı putlara tapmaktan uzak tut.\"'),
(1322, 1786, 14, 36, 'İbrahim ', 'Mekki', '\"Rabbim! O putlar çok insanları saptırdı; bana uyan bendendir, bana karşı gelen kimseyi Sana bırakırım; Sen bağışlarsın, merhamet edersin.\"'),
(1323, 1787, 14, 37, 'İbrahim ', 'Mekki', '\"Rabbimiz! Ben çocuklarımdan kimini, namaz kılabilmeleri için Senin kutsal evinin yanında, ziraata elverişsiz bir vadiye yerleştirdim. Rabbimiz! İnsanların gönüllerini onlara meylettir, şükretmeleri için onları ürünlerle rızıklandır.\"'),
(1324, 1788, 14, 38, 'İbrahim ', 'Mekki', '\"Rabbimiz! Doğrusu Sen gizlediğimizi de, açığa vurduğumuzu da bilirsin. Yerde ve gökte hiçbir şey Allah\'tan gizli kalmaz.\"'),
(1325, 1789, 14, 39, 'İbrahim ', 'Mekki', '\"Kocamışken, bana İsmail ve İshak\'ı veren Allah\'a hamdolsun. Doğrusu Rabbim duaları işitendir.\"'),
(1326, 1790, 14, 40, 'İbrahim ', 'Mekki', '\"Rabbim! Beni ve çocuklarımı namaz kılanlardan eyle. Rabbimiz! Duamı kabul buyur.\"'),
(1327, 1791, 14, 41, 'İbrahim ', 'Mekki', '\"Rabbimiz! Hesap görülecek günde, beni, anamı babamı ve inananları bağışla.\"'),
(1328, 1792, 14, 42, 'İbrahim ', 'Mekki', 'Sakın Allah\'ı, zalimlerin yaptıklarından habersiz sanma; gözlerin dışarı fırlayacağı bir güne kadar onları ertelemektedir.'),
(1329, 1793, 14, 43, 'İbrahim ', 'Mekki', 'O gün başları kalkmış, gözleri kendilerine dönemeyecek şekilde sabit kalmış, gönülleri bomboş halde koşup duracaklardır.'),
(1330, 1794, 14, 44, 'İbrahim ', 'Mekki', 'İnsanları, kendilerine azabın geleceği gün ile uyar. Haksızlık edenler: \"Rabbimiz! Bizi yakın bir süreye kadar ertele de çağrına gelelim, peygamberlere uyalım\" derler. Siz daha önce, sonunuzun gelmeyeceğine yemin etmemiş miydiniz! Üstelik kendilerine yazık edenlerin yerlerinde oturdunuz. Onlara, yaptıklarımız da sizlere açıklanmıştı. Size misaller de vermiştik.'),
(1331, 1795, 14, 45, 'İbrahim ', 'Mekki', 'İnsanları, kendilerine azabın geleceği gün ile uyar. Haksızlık edenler: \"Rabbimiz! Bizi yakın bir süreye kadar ertele de çağrına gelelim, peygamberlere uyalım\" derler. Siz daha önce, sonunuzun gelmeyeceğine yemin etmemiş miydiniz! Üstelik kendilerine yazık edenlerin yerlerinde oturdunuz. Onlara, yaptıklarımız da sizlere açıklanmıştı. Size misaller de vermiştik.'),
(1332, 1796, 14, 46, 'İbrahim ', 'Mekki', 'Şüphesiz onlar düzenlerini kurdular; oysa dağları yerinden oynatacak olsa bile, bu düzenleri hep Allah\'ın elindeydi.'),
(1333, 1797, 14, 47, 'İbrahim ', 'Mekki', 'Yerin başka bir yerle, göklerin de başka göklerle değiştirildiği, her şeye üstün gelen tek Allah\'ın huzuruna çıktıkları günde, sakın Allah\'ın peygamberlerine verdiği sözden cayacağını sanma; doğrusu Allah güçlüdür, öç alandır.'),
(1334, 1798, 14, 48, 'İbrahim ', 'Mekki', 'Yerin başka bir yerle, göklerin de başka göklerle değiştirildiği, her şeye üstün gelen tek Allah\'ın huzuruna çıktıkları günde, sakın Allah\'ın peygamberlerine verdiği sözden cayacağını sanma; doğrusu Allah güçlüdür, öç alandır.'),
(1335, 1799, 14, 49, 'İbrahim ', 'Mekki', 'O gün, suçluları zincirlere vurulmuş olarak görürsün.'),
(1336, 1800, 14, 50, 'İbrahim ', 'Mekki', 'Gömlekleri katrandan olacak, yüzlerini ateş bürüyecektir.'),
(1337, 1801, 14, 51, 'İbrahim ', 'Mekki', 'Bu, Allah herkese yaptığının karşılığını vereceği için böyledir. Doğrusu Allah hesabı çabuk görür.'),
(1338, 1802, 14, 52, 'İbrahim ', 'Mekki', 'Bu Kuran, onunla uyarılsınlar ve tek bir Tanrı bulunduğunu bilsinler ve akıl sahipleri öğüt alsınlar diye insanlara tebliğ edilmiştir.'),
(1339, 1803, 15, 1, 'Hicr ', 'Mekki', 'Elif, Lam, Ra. Bunlar Kitap\'ın ve apaçık olan Kuran\'ın ayetleridir.'),
(1340, 1804, 15, 2, 'Hicr ', 'Mekki', 'İnkar edenler, keşke müslüman olsaydık temennisinde bulunacaklardır.'),
(1341, 1805, 15, 3, 'Hicr ', 'Mekki', 'Bırak onları yesinler, zevk alsınlar; ümit onları avundursun; ilerde öğrenecekler.'),
(1342, 1806, 15, 4, 'Hicr ', 'Mekki', 'Yok ettiğimiz herhangi bir kasabanın elbette belli bir yazısı vardır.'),
(1343, 1807, 15, 5, 'Hicr ', 'Mekki', 'Hiçbir ümmet kendi süresini öne alamaz, geciktiremez de.'),
(1344, 1808, 15, 6, 'Hicr ', 'Mekki', 'Onlar: \"Ey kendisine Kitap indirilen kimse! Sen mutlaka delisin. Doğrulardan isen melekleri bize getirsene\" dediler.'),
(1345, 1809, 15, 7, 'Hicr ', 'Mekki', 'Onlar: \"Ey kendisine Kitap indirilen kimse! Sen mutlaka delisin. Doğrulardan isen melekleri bize getirsene\" dediler.'),
(1346, 1810, 15, 8, 'Hicr ', 'Mekki', 'Biz melekleri ancak gerekince indiririz. O takdirde de ceza görecekler asla geri bırakılmazlar.'),
(1347, 1811, 15, 9, 'Hicr ', 'Mekki', 'Doğrusu Kitap\'ı Biz indirdik, onun koruyucusu elbette Biziz.'),
(1348, 1812, 15, 10, 'Hicr ', 'Mekki', 'And olsun ki, senden önce çeşitli ümmetlere peygamber göndermiştik.'),
(1349, 1813, 15, 11, 'Hicr ', 'Mekki', 'Onlara gelen her peygamberi alaya alıyorlardı.'),
(1350, 1814, 15, 12, 'Hicr ', 'Mekki', 'Aynı şekilde biz de Kitap\'ı suçluların kalblerine sokarız, ama ona yine de inanmazlar. Oysa kendilerinden öncekilerin uğradıkları meydandadır.'),
(1351, 1815, 15, 13, 'Hicr ', 'Mekki', 'Aynı şekilde biz de Kitap\'ı suçluların kalblerine sokarız, ama ona yine de inanmazlar. Oysa kendilerinden öncekilerin uğradıkları meydandadır.'),
(1352, 1816, 15, 14, 'Hicr ', 'Mekki', 'Onlara gökten bir kapı açsak da, oradan çıkmağa koyulsalar: \"Gözlerimiz döndü, biz herhalde büyülendik\" derler.'),
(1353, 1817, 15, 15, 'Hicr ', 'Mekki', 'Onlara gökten bir kapı açsak da, oradan çıkmağa koyulsalar: \"Gözlerimiz döndü, biz herhalde büyülendik\" derler.'),
(1354, 1818, 15, 16, 'Hicr ', 'Mekki', 'And olsun ki, gökte burçlar meydana getirdik, onları bakanlar için donattık.'),
(1355, 1819, 15, 17, 'Hicr ', 'Mekki', 'Onları, kovulmuş her şeytandan koruduk.'),
(1356, 1820, 15, 18, 'Hicr ', 'Mekki', 'Fakat kulak hırsızlığı yapan olursa, parlak bir ateş onu kovalar.'),
(1357, 1821, 15, 19, 'Hicr ', 'Mekki', 'Yeri yaydık, oraya sabit dağlar yerleştirdik, orada her şeyi bir ölçüye göre bitirdik.'),
(1358, 1822, 15, 20, 'Hicr ', 'Mekki', 'Orada sizin ve rızık veremeyeceğiniz kimseler için geçimlikler meydana getirdik.'),
(1359, 1823, 15, 21, 'Hicr ', 'Mekki', 'Hazinesi Bizim katımızda olmayan hiçbir şey yoktur. Biz onu ancak belli bir ölçüye göre indiririz.'),
(1360, 1824, 15, 22, 'Hicr ', 'Mekki', 'Rüzgarları aşılayıcı olarak gönderdik; yukarıdan su indirdik de sizi onunla suladık. Yoksa siz onu toplayamazdınız.'),
(1361, 1825, 15, 23, 'Hicr ', 'Mekki', 'Doğrusu dirilten ve öldüren Biziz; hepsinin gerisinde de Biz kalırız.'),
(1362, 1826, 15, 24, 'Hicr ', 'Mekki', 'And olsun ki, sizden önce geçenleri biliriz; and olsun ki, geri kalanları da biliriz.'),
(1363, 1827, 15, 25, 'Hicr ', 'Mekki', 'Doğrusu Rabbin onları diriltip bir araya getirecektir. Şüphesiz O Hakim\'dir, Herşeyi Bilen\'dir.'),
(1364, 1828, 15, 26, 'Hicr ', 'Mekki', 'And olsun ki, insanı kuru balçıktan, işlenebilen kara topraktan yarattık.'),
(1365, 1829, 15, 27, 'Hicr ', 'Mekki', 'Cinleri de, daha önce, dumansız ateşten yarattık.'),
(1366, 1830, 15, 28, 'Hicr ', 'Mekki', '\'Rabbin meleklere: \"Ben, balçıktan, işlenebilen kara topraktan bir insan yaratacağım. Onu yapıp ruhumdan üflediğimde ona secdeye kapanın\" demişti.'),
(1367, 1831, 15, 29, 'Hicr ', 'Mekki', '\'Rabbin meleklere: \"Ben, balçıktan, işlenebilen kara topraktan bir insan yaratacağım. Onu yapıp ruhumdan üflediğimde ona secdeye kapanın\" demişti.'),
(1368, 1832, 15, 30, 'Hicr ', 'Mekki', 'Bunun üzerine, İblis\'in dışında bütün melekler hemen secde ettiler. O, secde edenlerle beraber olmaktan çekindi.'),
(1369, 1833, 15, 31, 'Hicr ', 'Mekki', 'Bunun üzerine, İblis\'in dışında bütün melekler hemen secde ettiler. O, secde edenlerle beraber olmaktan çekindi.'),
(1370, 1834, 15, 32, 'Hicr ', 'Mekki', 'Allah: \"Ey İblis! Secde edenlerle beraber olmaktan seni alıkoyan nedir?\" dedi.'),
(1371, 1835, 15, 33, 'Hicr ', 'Mekki', 'O: \"Balçıktan, işlenebilen kara topraktan yarattığın insana secde edemem\" dedi.'),
(1372, 1836, 15, 34, 'Hicr ', 'Mekki', '\"Öyleyse defol oradan, sen artık kovulmuş birisin. Doğrusu hesap gününe kadar lanet sanadır\" dedi.'),
(1373, 1837, 15, 35, 'Hicr ', 'Mekki', '\"Öyleyse defol oradan, sen artık kovulmuş birisin. Doğrusu hesap gününe kadar lanet sanadır\" dedi.'),
(1374, 1838, 15, 36, 'Hicr ', 'Mekki', '\"Rabbim! Beni hiç olmazsa, tekrar dirilecekleri güne kadar ertele\" dedi.'),
(1375, 1839, 15, 37, 'Hicr ', 'Mekki', 'Allah: \"Sen, bilinen gün gelene kadar bırakılanlardansın\" dedi.'),
(1376, 1840, 15, 38, 'Hicr ', 'Mekki', 'Allah: \"Sen, bilinen gün gelene kadar bırakılanlardansın\" dedi.'),
(1377, 1841, 15, 39, 'Hicr ', 'Mekki', '\"Rabbim! Beni saptırdığın için, and olsun ki yeryüzünde fenalıkları onlara güzel göstereceğim; halis kıldığın kulların bir yana, onların hepsini saptıracağım\" dedi.'),
(1378, 1842, 15, 40, 'Hicr ', 'Mekki', '\"Rabbim! Beni saptırdığın için, and olsun ki yeryüzünde fenalıkları onlara güzel göstereceğim; halis kıldığın kulların bir yana, onların hepsini saptıracağım\" dedi.'),
(1379, 1843, 15, 41, 'Hicr ', 'Mekki', '\'Allah şöyle dedi: \"Benim gerekli kıldığım dosdoğru yol budur; kullarımın üzerinde senin bir nüfuzun olamaz. Ancak sana uyan sapıklar bunun dışındadır.\"'),
(1380, 1844, 15, 42, 'Hicr ', 'Mekki', '\'Allah şöyle dedi: \"Benim gerekli kıldığım dosdoğru yol budur; kullarımın üzerinde senin bir nüfuzun olamaz. Ancak sana uyan sapıklar bunun dışındadır.\"'),
(1381, 1845, 15, 43, 'Hicr ', 'Mekki', '\"Ve Cehennem onların hepsinin toplanacağı yerdir.\"'),
(1382, 1846, 15, 44, 'Hicr ', 'Mekki', 'O cehennemin yedi kapısı olup, her kapıdan onların girecekleri ayrılmış bir kısım vardır.'),
(1383, 1847, 15, 45, 'Hicr ', 'Mekki', 'Allah\'a karşı gelmekten sakınanlar ise, cennetlerde, pınar başlarındadırlar.'),
(1384, 1848, 15, 46, 'Hicr ', 'Mekki', '\"Oraya güven içinde, esenlikle girin\" denilir.'),
(1385, 1849, 15, 47, 'Hicr ', 'Mekki', 'Biz onların gönüllerinde olan kini çıkardık, artık onlar sedirler üzerinde karşılıklı oturan kardeşlerdir.'),
(1386, 1850, 15, 48, 'Hicr ', 'Mekki', 'Onlar orada bir yorgunluk hissetmezler. Oradan çıkarılacak da değillerdir.'),
(1387, 1851, 15, 49, 'Hicr ', 'Mekki', 'Kullarıma Benim bağışlayan, merhamet eden olduğumu, azabımın can yakıcı bir azap olduğunu haber ver.'),
(1388, 1852, 15, 50, 'Hicr ', 'Mekki', 'Kullarıma Benim bağışlayan, merhamet eden olduğumu, azabımın can yakıcı bir azap olduğunu haber ver.'),
(1389, 4485, 45, 12, 'Câsiye ', 'Mekki', 'Emri gereğince denizde yüzmek üzere gemileri, lütfedip verdiği rızkı aramanız için denizi buyruğunuz altına veren Allah\'tır, belki artık şükredersiniz.'),
(1390, 4486, 45, 13, 'Câsiye ', 'Mekki', 'Göklerde olanları, yerde olanları, hepsini sizin buyruğunuz altına vermiştir. Doğrusu bunlarda, düşünen kimseler için dersler vardır.'),
(1391, 4487, 45, 14, 'Câsiye ', 'Mekki', 'İnanmışlara de ki: Allah\'ın bir milleti yaptıklarına karşılık cezalandıracağı günlerin geleceğini ummayanları şimdilik bağışlasınlar.'),
(1392, 4488, 45, 15, 'Câsiye ', 'Mekki', 'Kim yararlı iş işlerse kendinedir; kim kötülük yaparsa kendi aleyhinedir. Sonra Rabbinize döndürülürsünüz.'),
(1393, 4489, 45, 16, 'Câsiye ', 'Mekki', 'And olsun ki Biz, İsrailoğullarına Kitap, hüküm ve peygamberlik verdik; onları temiz şeylerle rızıklandırdık; onları dünyalara üstün kıldık.'),
(1394, 4490, 45, 17, 'Câsiye ', 'Mekki', 'Din konusunda, onlara belgeler verdik; ancak, kendilerine ilim geldikten sonra birbirini çekememezlikten ayrılığa düştüler. Rabbin kıyamet günü, ayrılığa düştükleri şeyler hakkında şüphesiz aralarında hükmedecektir.'),
(1395, 4491, 45, 18, 'Câsiye ', 'Mekki', 'Sonra seni de din konusunda bir şeriat sahibi kıldık, ona uy; bilmeyenlerin heveslerine uyma.'),
(1396, 4492, 45, 19, 'Câsiye ', 'Mekki', 'Şüphesiz onlar, seni Allah\'tan müstağni kılamazlar. Doğrusu zalimler birbirlerinin dostudurlar. Sakınanların dostu ise Allah\'tır.'),
(1397, 4493, 45, 20, 'Câsiye ', 'Mekki', 'Bu Kuran, insanlar için açık belgeler; kesin olarak inanan millet için doğruluk rehberi ve rahmettir.'),
(1398, 4494, 45, 21, 'Câsiye ', 'Mekki', 'Yoksa, kötülük işleyen kimseler, ölümlerinde ve diriliklerinde kendilerini, inanıp yararlı iş işleyen kimseler ile bir mi tutacağımızı sandılar? Ne kötü hüküm veriyorlar!'),
(1399, 4495, 45, 22, 'Câsiye ', 'Mekki', 'Allah gökleri ve yeri gerçekle yaratmıştır; her cana, kazandığının karşılığı verilir, onlara zulmedilmez.'),
(1400, 4496, 45, 23, 'Câsiye ', 'Mekki', 'Heva ve hevesini tanrı edinen, bilgisi olduğu halde Allah\'ın şaşırttığı, kulağını ve kalbini mühürlediği, gözünü perdelediği kimseyi gördün mü? Onu Allah\'tan başka kim doğru yola eriştirebilir? Ey insanlar! Anlamaz mısınız?'),
(1401, 4497, 45, 24, 'Câsiye ', 'Mekki', '\"Hayat, ancak bu dünyadaki hayatımızdır. Ölürüz ve yaşarız; bizi ancak zamanın geçişi yokluğa sürükler\" derler. Onların bu hususta bir bilgisi yoktur, sadece böyle sanırlar.'),
(1402, 4498, 45, 25, 'Câsiye ', 'Mekki', 'Ayetlerimiz onlara açıkça okunduğu zaman, delilleri yalnızca: \"Doğru sözlü iseniz babalarımızı getirin bakalım\" demek olur.'),
(1403, 4499, 45, 26, 'Câsiye ', 'Mekki', 'De ki: \"Sizi Allah diriltir, sonra öldürür, sonra sizi şüphe götürmeyen kıyamet gününde toplar. Ama insanların çoğu bilmezler.\"'),
(1404, 4500, 45, 27, 'Câsiye ', 'Mekki', 'Göklerin ve yerin hükümranlığı Allah\'ındır. Kıyamet kopacağı gün, işte o gün, batıl sözlere uymuş olanlar hüsranda kalırlar.'),
(1405, 4501, 45, 28, 'Câsiye ', 'Mekki', 'Her ümmeti diz üstü çökmüş olarak görürsün. Her ümmet kitabına çağrılır. Onlara denir ki: \"Bugün, size işlediğinizin karşılığı verilecektir.\"'),
(1406, 4502, 45, 29, 'Câsiye ', 'Mekki', '\"Bu kitabımız gerçekten sizin aleyhinize konuşur. Biz yaptıklarınızı şüphesiz bir bir kaydediyorduk.\"'),
(1407, 4503, 45, 30, 'Câsiye ', 'Mekki', 'İnanıp, yararlı iş işleyenlere gelince, Rableri onları rahmetine garkeder. İşte bu, apaçık kurtuluştur.'),
(1408, 4504, 45, 31, 'Câsiye ', 'Mekki', 'Ama, inkar eden kimselere denir ki: \"Ayetlerim size okunmuş, siz de büyüklenip suçlu bir millet olmuştunuz değil mi?\"'),
(1409, 4505, 45, 32, 'Câsiye ', 'Mekki', '\"Doğrusu Allah\'ın verdiği söz gerçektir, kıyamet saati şüphe götürmez\" dendiği zaman: \"Kıyametin ne olduğunu bilmiyoruz, yalnız yoktur sanıyoruz, buna dair kesin bir bilgi elde etmiş değiliz\" derdiniz.'),
(1410, 4506, 45, 33, 'Câsiye ', 'Mekki', 'İşledikleri kötülükler kendilerine belli oldu ve onları, alaya aldıkları şeyler kuşatıp mahvetti.'),
(1411, 4507, 45, 34, 'Câsiye ', 'Mekki', 'Onlara denir ki: \"Bugüne kavuşacağınızı unuttuğunuz gibi Biz de sizi unuttuk; varacağınız yer ateştir, yardımcılarınız da yoktur.\"'),
(1412, 4508, 45, 35, 'Câsiye ', 'Mekki', '\"Bu, Allah\'ın ayetlerini alaya almanızdan ve dünya hayatının sizi aldatmış olmasından ötürüdür.\" O gün, ne oradan çıkarılırlar ve ne de özürleri dinlenir.'),
(1413, 4509, 45, 36, 'Câsiye ', 'Mekki', 'Övülmek, göklerin Rabbi, yerin Rabbi ve alemlerin Rabbi olan Allah içindir.'),
(1414, 4510, 45, 37, 'Câsiye ', 'Mekki', 'Göklerde ve yerde azamet O\'nundur, O, güçlüdür, Hakim\'dir.'),
(1415, 4511, 46, 1, 'Ahkâf ', 'Mekki', 'Ha, Mim.'),
(1416, 4512, 46, 2, 'Ahkâf ', 'Mekki', 'Bu Kitap\'ın indirilmesi güçlü olan, Hakim olan Allah katındandır.'),
(1417, 4513, 46, 3, 'Ahkâf ', 'Mekki', 'Biz, gökleri, yeri ve ikisinin arasında bulunanları, ancak gerçek üzere ve belirli bir süre için yarattık; inkar edenler, uyarıldıkları şeylerden yüz çevirmektedirler.'),
(1418, 4514, 46, 4, 'Ahkâf ', 'Mekki', 'De ki: \"Allah\'ı bırakıp taptığınız şeyleri görüyor musunuz? Yeryüzünde ne yaratmışlar bana göstersenize! Yoksa Allah\'la ortaklıkları göklerde midir? Eğer doğru sözlü iseniz, size indirilmiş bir kitap veya intikal etmiş bir bilgi kalıntısı varsa bana getirin.\"'),
(1419, 4515, 46, 5, 'Ahkâf ', 'Mekki', 'Allah\'ı bırakıp da, kıyamet gününe kadar cevap veremeyecek şeylere yalvarandan daha sapık kimdir? Çünkü, yalvardıkları şeyler yalvarışlarından habersizdirler.'),
(1420, 4516, 46, 6, 'Ahkâf ', 'Mekki', 'Ama, insanlar kıyamet günü toplatılınca, putları onlara düşman olurlar ve tapınmalarını inkar ederler.'),
(1421, 4517, 46, 7, 'Ahkâf ', 'Mekki', 'Ayetlerimiz onlara açıkça okunduğu zaman inkar edenler, kendilerine gelen gerçek için: \"Bu, apaçık bir büyüdür\" derler.'),
(1422, 4518, 46, 8, 'Ahkâf ', 'Mekki', 'Veya, \"onu uydurdu\" derler. De ki: \"Eğer onu uydurdumsa, beni Allah\'a karşı hiçbir şekilde savunamazsınız; O, Kuran için yaptığınız taşkınlıkları daha iyi bilir. Benimle sizin aranızda şahit olarak O yeter. O, bağışlayandır, merhamet edendir.\"'),
(1423, 4519, 46, 9, 'Ahkâf ', 'Mekki', 'De ki: \"Ben peygamberlerin ilki değilim; benim ve sizin başınıza gelecekleri bilmem; ben ancak bana vahyolunana uymaktayım; ben sadece apaçık bir uyarıcıyım.\"'),
(1424, 4520, 46, 10, 'Ahkâf ', 'Mekki', 'De ki: \"Eğer bu Kitap Allah katından ise ve siz de onu inkar etmişseniz; İsrailoğullarından bir şahit de bunun böyle olduğuna şehadet edip de inanmışken, siz yine de büyüklük taslarsınız, bana söyleyin kendinize yazık etmiş olmaz mısınız?\" Doğrusu Allah zalim milleti doğru yola eriştirmez.'),
(1425, 4521, 46, 11, 'Ahkâf ', 'Mekki', 'İnkar edenler, inananlar için: \"Eğer İslamiyet\'te bir hayır olsaydı, bu hususta bizden öne geçemezlerdi\" derler. Bununla doğru yola girmedikleri için de, \"Bu, eski bir uydurmadır\" derler.'),
(1426, 4522, 46, 12, 'Ahkâf ', 'Mekki', 'Kuran\'dan önce, Musa\'nın kitabı (Tevrat), bir rahmet ve rehberdi. Bu Kuran da, zulmedenleri uyarmak ve iyi davrananlara müjde olmak üzere Arap diliyle indirilmiş, kendinden öncekileri doğrulayan bir Kitap\'dır.'),
(1427, 4523, 46, 13, 'Ahkâf ', 'Mekki', 'Doğrusu, \"Rabbimiz Allah\'tır\" deyip, sonra da dosdoğru gidenlere korku yoktur, onlar üzülmeyeceklerdir.'),
(1428, 4524, 46, 14, 'Ahkâf ', 'Mekki', 'İşte onlar, cennetliklerdir; işlediklerine karşılık olarak, içinde temelli kalacaklardır.'),
(1429, 4525, 46, 15, 'Ahkâf ', 'Mekki', 'Biz insana, anne ve babasına karşı iyi davranmasını tavsiye etmişizdir; zira annesi, onu, karnında, zorluğa uğrayarak taşımış; onu güçlükle doğurmuştur. Taşınması ve sütten kesilmesi otuz ay sürer. Sonunda erginlik çağına erince ve kırk yaşına varınca: \"Rabbim! Bana ve anne babama verdiğin nimete şükretmemi ve benim hoşnut olacağın yararlı bir işi yapmamı sağla; bana verdiğin gibi soyuma da salah ver; doğrusu Sana yöneldim, ben, kendini Sana verenlerdenim\" demesi gerekir.'),
(1430, 4526, 46, 16, 'Ahkâf ', 'Mekki', 'İşte, işlediklerini en güzel şekilde kabul ettiğimiz ve kötülüklerini geçtiğimiz bu kimseler, cennetlikler içindedirler. Bu, verilen doğru bir sözdür.'),
(1431, 4527, 46, 17, 'Ahkâf ', 'Mekki', 'Annesine babasına: \"Of ikinizden; benden önce nice nesiller gelip geçmişken beni tekrar diriltilmemle mi tehdit ediyorsunuz?\" diyen kimseye, anne babası Allah\'a sığınarak: \"Sana yazıklar olsun! İnan; doğrusu Allah\'ın sözü gerçektir\" dedikleri halde: \"Bu, Kuran öncekilerin masallarından başka bir şey değildir\" diye cevap verenler işte onlar kendilerinden önce cinlerden ve insanlardan gelip geçmiş ümmetler içinde, Allah\'ın azap vadinin aleyhlerinde gerçekleştiği kimselerdir. Doğrusu onlar hüsranda olanlardır.'),
(1432, 4528, 46, 18, 'Ahkâf ', 'Mekki', 'Annesine babasına: \"Of ikinizden; benden önce nice nesiller gelip geçmişken beni tekrar diriltilmemle mi tehdit ediyorsunuz?\" diyen kimseye, anne babası Allah\'a sığınarak: \"Sana yazıklar olsun! İnan; doğrusu Allah\'ın sözü gerçektir\" dedikleri halde: \"Bu, Kuran öncekilerin masallarından başka bir şey değildir\" diye cevap verenler işte onlar kendilerinden önce cinlerden ve insanlardan gelip geçmiş ümmetler içinde, Allah\'ın azap vadinin aleyhlerinde gerçekleştiği kimselerdir. Doğrusu onlar hüsranda olanlardır.'),
(1433, 4529, 46, 19, 'Ahkâf ', 'Mekki', 'İşlediklerinden ötürü herkesin bir derecesi vardır. Herkese işlediklerinin karşılığı ödenir. Kendilerine haksızlık yapılmaz.'),
(1434, 4530, 46, 20, 'Ahkâf ', 'Mekki', 'İnkar edenler, ateşe sunuldukları gün, onlara: \"Dünyadaki hayatınızda sizin için güzel olan her şeyi harcadınız, onların zevkini sürdünüz; ama bugün, yeryüzünde haksız yere büyüklük taslamanızın ve yoldan çıkmanızın karşılığında alçaltıcı bir azap göreceksiniz\"'),
(1435, 4531, 46, 21, 'Ahkâf ', 'Mekki', 'Ad milletinin kardeşi Hud\'u an; ondan önce ve sonra, \"Allah\'tan başkasına kulluk etmeyin\" diyen nice uyarıcılar gelip geçmişken, Ahkaf bölgesindeki milletini uyarmış \"Doğrusu sizin için, büyük günün azabından korkuyorum\" demişti.'),
(1436, 4532, 46, 22, 'Ahkâf ', 'Mekki', '\"Bize, bizi tanrılarımızdan alıkoymak için mi geldin? Doğru sözlülerden isen, bizi tehdit ettiğin şeyi başımıza getir\" dediler.'),
(1437, 4533, 46, 23, 'Ahkâf ', 'Mekki', '\"Doğrusu bunun ne zaman geleceğini Allah bilir; ben size benimle gönderileni tebliğ ediyorum; fakat sizin cahil bir millet olduğunuzu görüyorum.\" dedi.'),
(1438, 4534, 46, 24, 'Ahkâf ', 'Mekki', 'O azabın, yayılarak vadilerine doğru yöneldiğini gördüklerinde: \"Bu yaygın bulut bize yağmur yağdıracaktır\" dediler. Hud: \"Hayır, o, acele beklediğiniz şeydir; can yakıcı azap veren bir rüzgardır; Rabbinin buyruğu ile her şeyi yok eder\" dedi. Bunun üzerine evlerinin harabelerinden başka bir şey görünmez oldu. Biz, suçlu milleti işte böyle cezalandırırız.'),
(1439, 4535, 46, 25, 'Ahkâf ', 'Mekki', 'O azabın, yayılarak vadilerine doğru yöneldiğini gördüklerinde: \"Bu yaygın bulut bize yağmur yağdıracaktır\" dediler. Hud: \"Hayır, o, acele beklediğiniz şeydir; can yakıcı azap veren bir rüzgardır; Rabbinin buyruğu ile her şeyi yok eder\" dedi. Bunun üzerine evlerinin harabelerinden başka bir şey görünmez oldu. Biz, suçlu milleti işte böyle cezalandırırız.'),
(1440, 4536, 46, 26, 'Ahkâf ', 'Mekki', 'And olsun ki onlara, size vermediğimiz servet ve imkanı vermiştik. Onlara kulaklar, gözler ve kalbler vermiştik; ama kulakları, gözleri ve kalbleri onlara bir fayda sağlamadı, zira, Allah\'ın ayetlerini bile bile inkar ediyorlardı, alaya aldıkları şeyler onları kuşatıp yokediverdi.'),
(1441, 4537, 46, 27, 'Ahkâf ', 'Mekki', 'And olsun ki, çevrenizde bulunan birçok kentleri yok etmişizdir. Belki doğru yola dönerler diye ayetleri türlü türlü anlatmışızdır.'),
(1442, 4538, 46, 28, 'Ahkâf ', 'Mekki', 'O zamanlar, Allah\'ı bırakıp da O\'na yakınlık peyda etmek için edindikleri tanrılar kendilerine yardım etmeli değil miydi? Ama tanrıları onlardan uzaklaştılar. Bu, onların yalanı ve uydurup durdukları şeydir.'),
(1443, 4539, 46, 29, 'Ahkâf ', 'Mekki', 'Kuran\'ı dinleyecek cinlerden bir takımını sana yöneltmiştik. Onlar Kuran\'ı dinlemeğe hazır olunca birbirlerine: \"Susun\" dediler. Kuran\'ın okunması bitince, her biri birer uyarıcı olarak milletlerine döndüler.'),
(1444, 4540, 46, 30, 'Ahkâf ', 'Mekki', 'Şöyle dediler: \"Ey milletimiz! Doğrusu biz, Musa\'dan sonra indirilen, kendinden öncekileri doğrulayan, gerçeği ve doğru yolu gösteren bir kitap dinledik.\"'),
(1445, 4541, 46, 31, 'Ahkâf ', 'Mekki', '\"Ey milletimiz! Allah\'a çağırana (Muhammed\'e) uyun ve O\'na inanın da Allah da sizin günahlarınızı bağışlasın ve sizi can yakıcı azabdan korusun.\"'),
(1446, 4542, 46, 32, 'Ahkâf ', 'Mekki', 'Allah\'a çağırana uymayan kimse bilsin ki, Allah\'ı yeryüzünde aciz bırakamaz; onların O\'ndan başka dostları da bulunmaz; işte onlar apaçık sapıklıktadırlar.'),
(1447, 4543, 46, 33, 'Ahkâf ', 'Mekki', 'Gökleri, yeri yaratan ve onları yaratmaktan yorulmayan Allah\'ın, ölüleri diriltmeye de kadir olduğunu görmezler mi? Evet; O her şeye Kadir\'dir.'),
(1448, 4544, 46, 34, 'Ahkâf ', 'Mekki', 'İnkar edenler, ateşe sunuldukları gün onlara: \"Bu, gerçek değil miydi?\" denir, onlar: \"Rabbimize and olsun ki evet gerçekti\" derler. Allah: \"İnkar etmenizden ötürü azabı tadın\" der.'),
(1449, 4545, 46, 35, 'Ahkâf ', 'Mekki', 'Peygamberlerden azim sahibi olanların sabrettiği gibi sen de sabret; inkarcılar için acele etme; onlar, kendilerine söz verileni gördükleri gün dünyada sadece gündüzün bir müddeti eğlendiklerini sanırlar. Bu bir bildiridir; yoldan çıkmış olanlardan başkası mı yok edilir?'),
(1450, 4546, 47, 1, 'Muhammed ', 'Medeni', 'Allah, inkar edenlerin ve kendi yolundan alıkoyanların işlerini boşa çıkarır.'),
(1451, 4547, 47, 2, 'Muhammed ', 'Medeni', 'İnanıp yararlı iş işleyenlerin ve Muhammed\'e, Rablerinden bir gerçek olarak indirilene inananların kötülüklerini Allah örter ve durumlarını düzeltir.'),
(1452, 4548, 47, 3, 'Muhammed ', 'Medeni', 'Bu, inkar edenlerin batıla uymaları ve inananların Rablerinden gelen gerçeğe uymalarından ötürü böyledir. Allah böylece insanlara kendilerinin misallerini anlatır.'),
(1453, 4549, 47, 4, 'Muhammed ', 'Medeni', 'Savaşta inkar edenlerle karşılaştığınızda boyunlarını vurun; sonunda onlara üstün geldiğinizde onları esir alın; savaş sona erince onları ya karşılıksız, ya da fidye ile salıverin; Allah dilemiş olsaydı, onlardan başka türlü öç alabilirdi, bunun böyle olması, kiminizi kiminizle denemek içindir. Allah, kendi yolunda öldürülenlerin işlerini boşa çıkarmaz.'),
(1454, 4550, 47, 5, 'Muhammed ', 'Medeni', 'Onları doğru yola eriştirir, durumlarını düzeltir.'),
(1455, 4551, 47, 6, 'Muhammed ', 'Medeni', 'Onları, kendilerine anlattığı cennete koyar.'),
(1456, 4552, 47, 7, 'Muhammed ', 'Medeni', 'Ey inananlar! Siz Allah\'ın dinine yardım ederseniz, O da size yardım eder, ayaklarınızı savaşta sabit kılar.'),
(1457, 4553, 47, 8, 'Muhammed ', 'Medeni', 'İnkar edenlere ise, yıkım ve yokluk olsun! Allah onların işlerini boşa çıkarır.'),
(1458, 4554, 47, 9, 'Muhammed ', 'Medeni', 'Bu, Allah\'ın indirdiğini beğenmediklerinden ötürüdür. İşlerini Allah bunun için boşa çıkarmıştır.'),
(1459, 4555, 47, 10, 'Muhammed ', 'Medeni', 'Yeryüzünde dolaşıp kendilerinden öncekilerin sonlarının nasıl olduğuna bakmazlar mı? Allah onları yere geçirmiştir; inkarcılara da onların başına gelenin benzerleri vardır.'),
(1460, 4556, 47, 11, 'Muhammed ', 'Medeni', 'Çünkü Allah inananların sahibidir. Kafirlerin ise sahibi yoktur.'),
(1461, 4557, 47, 12, 'Muhammed ', 'Medeni', 'Doğrusu Allah, inanıp yararlı işler işleyenleri içlerinden ırmaklar akan cennetlere koyar. Durakları ateş olduğu halde kafirler, zevklenirler ve hayvanlar gibi yerler.'),
(1462, 4558, 47, 13, 'Muhammed ', 'Medeni', 'Seni sürüp çıkaran şehirden daha kuvvetli olan nice şehirler yok ettik. Yardım edenleri bulunmadı.'),
(1463, 4559, 47, 14, 'Muhammed ', 'Medeni', 'Rabbinin katından bir belgesi olan kimse, kötü işi kendisine güzel gösterilen kimseye benzer mi? Bunlar heveslerine uymuşlardır.'),
(1464, 4560, 47, 15, 'Muhammed ', 'Medeni', 'Allah\'a karşı gelmekten sakınanlara söz verilen cennet şöyledir: Orada temiz su ırmakları, tadı bozulmayan süt ırmakları, içenlere zevk veren şarap ırmakları, süzme bal ırmakları vardır. Onlara orada her türlü ürün ve Rablerinden mağfiret vardır. Bunların durumu, ateşte temelli kalan ve bağırsaklarını parça parça edecek kaynar su içirilen kimselerin durumu gibi olur mu?'),
(1465, 4561, 47, 16, 'Muhammed ', 'Medeni', 'Onların içinde seni dinleyenler vardır; sonra senin yanından çıkınca, bilgili kimselere \"Az önce ne demişti?\" diye sorarlar. İşte bunlar, Allah\'ın kalblerini mühürlemiş olduğu, kendi heveslerine uyan kimselerdir.'),
(1466, 4562, 47, 17, 'Muhammed ', 'Medeni', 'Doğru yolu bulanların ise Allah doğruluklarını artırır, onların karşı gelmekten sakınmalarını sağlar.'),
(1467, 4563, 47, 18, 'Muhammed ', 'Medeni', 'Onlar kıyamet gününün kendilerine ansızın gelmesini mi bekliyorlar. Şüphesiz onun alametleri belirmiştir. Kendilerine gelip çatınca ibret almaları neye yarar?'),
(1468, 4564, 47, 19, 'Muhammed ', 'Medeni', 'Bil ki, Allah\'tan başka tanrı yoktur; kendinin, inanmış erkek ve kadınların günahlarının bağışlanmasını dile. Allah, gezip dolaştığınız ve duracağınız yerleri bilir.'),
(1469, 4565, 47, 20, 'Muhammed ', 'Medeni', 'İnananlar: \"Keşke bir süre indirilse de cihada çıksak\" derlerdi. Fakat hükmü açık bir süre inip, orada savaş zikredilince, kalblerinde hastalık olanların, ölüm korkusuyla bayılmış kimselerin bakışları gibi, sana baktıklarını gördün. Oysa onlara itaat etmek ve uygun olanı söylemek yaraşırdı. İş ciddileşince Allah\'a verdikleri yeminde doğruluk gösterselerdi, onların iyiliğine olurdu.'),
(1470, 4566, 47, 21, 'Muhammed ', 'Medeni', 'İnananlar: \"Keşke bir süre indirilse de cihada çıksak\" derlerdi. Fakat hükmü açık bir süre inip, orada savaş zikredilince, kalblerinde hastalık olanların, ölüm korkusuyla bayılmış kimselerin bakışları gibi, sana baktıklarını gördün. Oysa onlara itaat etmek ve uygun olanı söylemek yaraşırdı. İş ciddileşince Allah\'a verdikleri yeminde doğruluk gösterselerdi, onların iyiliğine olurdu.'),
(1471, 4567, 47, 22, 'Muhammed ', 'Medeni', 'Geri dönerseniz yeryüzünde bozgunculuk yapmanız ve akrabalık bağlarını kesmeniz beklenmez mi sizden?'),
(1472, 4568, 47, 23, 'Muhammed ', 'Medeni', 'İşte, Allah\'ın lanetlediği, sağır kıldığı ve gözlerini kör ettiği bunlardır.'),
(1473, 4569, 47, 24, 'Muhammed ', 'Medeni', 'Bunlar Kuran\'ı düşünmezler mi? Yoksa kalbleri kilitli midir?'),
(1474, 4570, 47, 25, 'Muhammed ', 'Medeni', 'Kendileri için doğru yol belli olduktan sonra ardlarına dönenleri, bu işi yapmaya şeytan sürüklemiş, onlara ümit vermiştir.'),
(1475, 4571, 47, 26, 'Muhammed ', 'Medeni', 'Bu, Allah\'ın indirdiğini beğenmeyen kimselerin: \"Biz bazı işlerde size itaat edeceğiz\" demelerindendir. Allah onların gizlediklerini bilir.'),
(1476, 4572, 47, 27, 'Muhammed ', 'Medeni', 'Melekler, onların yüzlerine ve sırtlarına vurarak canlarını alırken durumları nice olur?'),
(1477, 4573, 47, 28, 'Muhammed ', 'Medeni', 'Bu, Allah\'ı gazablandıran şeye uymaları ve O\'nun rızasından hoşnut olmamalarından ötürüdür. Allah da onların işlerini boşa çıkarmıştır.'),
(1478, 4574, 47, 29, 'Muhammed ', 'Medeni', 'Yoksa, kalblerinde hastalık olanlar, Allah\'ın onların kinlerini dışarı vurmayacağını mı sandılar?'),
(1479, 4575, 47, 30, 'Muhammed ', 'Medeni', 'Eğer dileseydik, Biz onları sana gösterirdik; sen de onları yüzlerinden tanırdın. And olsun ki sen, onları konuşmalarından da tanırsın; Allah işlediklerinizi bilir.'),
(1480, 4576, 47, 31, 'Muhammed ', 'Medeni', 'And olsun ki sizi, içinizden cihada çıkanları ve sabredenleri meydana çıkarana ve haberlerinizi açıklayana kadar deneyeceğiz.'),
(1481, 4577, 47, 32, 'Muhammed ', 'Medeni', 'Şüphesiz, inkar edenler, Allah yolundan alıkoyanlar ve kendilerine doğru yol belli olduktan sonra Peygambere karşı gelenler Allah\'a hiçbir zarar veremezler. O, onların işlerini boşa çıkaracaktır.'),
(1482, 4578, 47, 33, 'Muhammed ', 'Medeni', 'Ey inananlar! Allah\'a itaat edin, Peygambere itaat edin; işlerinizi boşa çıkarmayın.'),
(1483, 4579, 47, 34, 'Muhammed ', 'Medeni', 'İnkar edip Allah yolundan alıkoyanları, sonra da inkarcı olarak ölenleri Allah şüphesiz ki bağışlamayacaktır.'),
(1484, 4580, 47, 35, 'Muhammed ', 'Medeni', 'Ey inananlar! Sizler daha üstün olduğunuz halde düşman karşısında gevşemeyin ki barış istemek zorunda kalmayasınız; Allah sizinle beraberdir; sizin işlerinizi eksiltmeyecektir.'),
(1485, 4581, 47, 36, 'Muhammed ', 'Medeni', 'Doğrusu dünya hayatı oyun ve oyalanmadır. Eğer inanır ve Allah\'a karşı gelmekten sakınırsanız, O, size ecirlerinizi verir; O, sizin mallarınızı tamamen sarfetmenizi istemez.'),
(1486, 4582, 47, 37, 'Muhammed ', 'Medeni', 'Eğer sizden onları isteyip de sizi zorlarsa, cimrilik edecektiniz, O da kinlerinizi ortaya çıkaracaktı.'),
(1487, 4583, 47, 38, 'Muhammed ', 'Medeni', 'İşte sizler, Allah yolunda sarfetmeye çağırılan kimselersiniz. Kiminiz cimrilik yapıyor ama, cimrilik yapan bilsin ki, ancak kendine karşı cimrilik etmiş olur. Allah zengindir, siz ise fakirsiniz. Eğer O\'ndan yüz çevirirseniz sizi ortadan kaldırır, sizin gibi olmayacak bir milleti yerinize getirir.'),
(1488, 4584, 48, 1, 'Fetih ', 'Medeni', 'Doğrusu Biz sana apaçık bir zafer sağlamışızdır.'),
(1489, 4585, 48, 2, 'Fetih ', 'Medeni', 'Allah böylece, senin geçmiş ve gelecek günahlarını bağışlar, sana olan nimetini tamamlar, seni doğru yola eriştirir.'),
(1490, 4586, 48, 3, 'Fetih ', 'Medeni', 'Böylece sana, kimsenin güç yetiremeyeceği bir şekilde yardım eder.'),
(1491, 4587, 48, 4, 'Fetih ', 'Medeni', 'İnananların, imanlarını kat kat artırmaları için, kalblerine güven indiren O\'dur. Göklerdeki ve yerdeki ordular Allah\'ındır. Allah bilendir, Hakim olandır.'),
(1492, 4588, 48, 5, 'Fetih ', 'Medeni', 'İnanan erkek ve kadınları, içinde temelli kalacakları, içlerinden ırmaklar akan cennetlere koyar, onların kötülüklerini örter. Allah katında büyük kurtuluş işte budur.'),
(1493, 4589, 48, 6, 'Fetih ', 'Medeni', 'İnananlara yardım etmez diye Allah\'a kötü sanıda bulunan ikiyüzlü erkek ve kadınlara, puta tapan erek ve kadınlara Allah azabetsin; kötü sanıları kendi baslarına gelsin! Allah onlara gazabetmiş, onları lanetlemiş ve cehennemi kendilerine hazırlamıştır. Ne kötü dönüş yeridir!'),
(1494, 4590, 48, 7, 'Fetih ', 'Medeni', 'Göklerdeki ve yerdeki ordular Allah\'ındır. Allah güçlü olandır. Hakim olandır.'),
(1495, 4591, 48, 8, 'Fetih ', 'Medeni', 'Doğrusu seni şahit, müjdeci ve uyarıcı olarak gönderdik. Ey insanlar, siz de Allah\'a ve Peygamberine inanasınız, ona yardım edesiniz, O\'na saygı gösteresiniz ve O\'nu sabah akşam tesbih edesiniz.'),
(1496, 4592, 48, 9, 'Fetih ', 'Medeni', 'Doğrusu seni şahit, müjdeci ve uyarıcı olarak gönderdik. Ey insanlar, siz de Allah\'a ve Peygamberine inanasınız, ona yardım edesiniz, O\'na saygı gösteresiniz ve O\'nu sabah akşam tesbih edesiniz.'),
(1497, 4593, 48, 10, 'Fetih ', 'Medeni', 'Şüphesiz sana baş eğerek ellerini verenler (biat edenler), Allah\'a baş eğip el vermiş sayılırlar. Allah\'ın eli onların ellerinin üstündedir. Verdiği bu sözden dönen, ancak kendi aleyhine dönmüş olur. Allah\'a verdiği sözü yerine getirene, Allah büyük ecir verecektir.'),
(1498, 4594, 48, 11, 'Fetih ', 'Medeni', 'Bedevilerin savaştan geri kalmış olanları, sana: \"Bizi mallarımız ve ailelerimiz alıkoydu. Allah\'tan bizim bağışlanmamızı dile\" diyecekler. Dilleriyle, gönüllerinde bulunmayanı söylerler; de ki: \"Allah size bir zarar gelmesini dilerse, yahut bir fayda elde etmenizi dilerse, O\'na karşı kimin gücü bir şeye yeter? Kaldı ki, Allah yaptıklarınızdan haberdardır.\"'),
(1499, 4595, 48, 12, 'Fetih ', 'Medeni', 'Aslında siz, Peygamberin ve inananların, ailelerine bir daha dönmeyeceklerini sanmıştınız. Bu, gönüllerinize güzel görünmüştü de kötü sanıda bulunmuştunuz. Hayırsız bir topluluk oldunuz.'),
(1500, 4596, 48, 13, 'Fetih ', 'Medeni', 'Allah\'a ve Peygamberine kim inanmamışsa bilsin ki, şüphesiz Biz, inkarcılar için çılgın alevli cehennemi hazırlamışızdır.'),
(1501, 4597, 48, 14, 'Fetih ', 'Medeni', 'Göklerin ve yerin hükümranlığı Allah\'ındır. O, dilediğini bağışlar, dilediğine azabeder. Allah bağışlayandır, merhamet edendir.'),
(1502, 4598, 48, 15, 'Fetih ', 'Medeni', 'Savaştan geri kalmış olanlar, siz ganimetleri almaya giderken: \"Bırakın, biz de sizinle gelelim\" diyeceklerdir. Onlar Allah\'ın sözünü değiştirmek isterler. De ki: \"Bize uymayacaksınız; Allah sizin için önceden böyle buyurmuştur.\" Size: \"Hayır, bizi çekemiyorsunuz\" diyecekler. Aksine, kendileri ancak pek az söz anlayan kimselerdir.'),
(1503, 4599, 48, 16, 'Fetih ', 'Medeni', 'Bedevilerden geri kalmış olanlara de ki: \"güçlü kuvvetli bir millete karşı, onlar müslüman olana kadar savaşmaya çağrılacaksanız; eğer itaat ederseniz Allah size güzel ecir verir, ama daha önce döndüğünüz gibi yine dönecek olursanız sizi can yakan bir azaba uğratır.\"'),
(1504, 4600, 48, 17, 'Fetih ', 'Medeni', 'Ama, gözleri görmeyen kimse savaşa gelmezse ona bir sorumluluk yoktur; topala ve hastaya da sorumluluk yoktur. Kim Allah\'a ve peygamberine itaat ederse, Allah onu, içlerinden ırmaklar akan cennetlere koyar. Kim yüz çevirirse, onu can yakıcı azaba uğratır.'),
(1505, 1274, 9, 39, 'Tevbe ', 'Medeni', 'Çıkmazsanız Allah size can yakıcı azabla azabeder ve yerinize başka bir millet getirir. O\'na bir şey de yapamazsınız. Allah her şeye kadirdir.'),
(1506, 1275, 9, 40, 'Tevbe ', 'Medeni', 'Ona (Muhammed\'e) yardım etmezseniz, bilin ki, inkar edenler onu Mekke\'den çıkardıklarında mağarada bulunan iki kişiden biri olarak Allah ona yardım etmişti. Arkadaşına (Ebu Bekir\'e) \"Üzülme, Allah bizimledir\" diyordu; Allah da ona güven vermiş, görmediğiniz askerlerle onu desteklemiş, inkar edenlerin sözünü alçaltmıştı. Ancak Allah\'ın sözü yücedir. Allah güçlüdür, hakimdir.'),
(1507, 1276, 9, 41, 'Tevbe ', 'Medeni', 'İsteyen, istemeyen, hepiniz savaşa çıkın. Allah yolunda mallarınızla, canlarınızla cihat edin. Bilirseniz bu sizin için hayırlıdır.'),
(1508, 1277, 9, 42, 'Tevbe ', 'Medeni', 'Kolay bir kazanç, normal bir yolculuk olsaydı sana uyarlardı, fakat çıkılacak yol onlara uzak geldi, kendilerini helak ederek, \"Gücümüz yetseydi sizinle beraber çıkardık\" diye Allah\'a yemin edeceklerdir. Allah, onların yalancı olduğunu elbette biliyor.'),
(1509, 1278, 9, 43, 'Tevbe ', 'Medeni', 'Allah seni affetsin; doğrular sana belli olup, yalancıları bilmeden önce, niçin onlara izin verdin?'),
(1510, 1279, 9, 44, 'Tevbe ', 'Medeni', 'Allah\'a ve ahiret gününe inananlar, mallariyle, canlariyle savaşmak istediklerinden ötürü geri kalmak için senden izin istemezler. Allah sakınanları bilir.'),
(1511, 1280, 9, 45, 'Tevbe ', 'Medeni', 'Ancak Allah\'a ve ahiret gününe inanmayan, kalbleri şüpheye düşüp şüphelerinde bocalayan kimseler senden izin isterler.'),
(1512, 1281, 9, 46, 'Tevbe ', 'Medeni', 'Eğer savaşa çıkmak isteselerdi bir hazırlık yaparlardı. Ama Allah davranışlarını beğenmedi de onları alıkoydu. \"Acizlerle beraber oturun\" denildi.'),
(1513, 1282, 9, 47, 'Tevbe ', 'Medeni', 'Aranızda savaşa çıkmış olsalardı, ancak sizi bozmağa çalışırlar ve fitneye düşürmek için aranıza sokulurlardı. İçinizde onlara kulak verenler var. Allah kendilerine yazık edenleri bilir.'),
(1514, 1283, 9, 48, 'Tevbe ', 'Medeni', 'And olsun ki, daha önce de fitne koparmak istemişlerdi. Sana karşı bir takım işler çeviriyorlardı, sonunda onlar istemedikleri halde hak ortaya çıktı, Allah\'ın emri üstün geldi.'),
(1515, 1284, 9, 49, 'Tevbe ', 'Medeni', 'Onlardan, \"Bana izin ver, beni fitneye düşürme\" diyen vardır. Bilin ki onlar zaten fitneye düşmüşlerdi. Cehennem, inkar edenleri şüphesiz kuşatacaktır.'),
(1516, 1285, 9, 50, 'Tevbe ', 'Medeni', 'Sana bir iyilik gelince onların fenasına gider; bir kötülük gelse, \"Biz önceden ihtiyatlı davrandık\" derler, sevinerek dönüp giderler.'),
(1517, 1286, 9, 51, 'Tevbe ', 'Medeni', 'De ki: \"Allah\'ın bize yazdığından başkası başımıza gelmez. O bizim Mevlamızdır, inananlar Allah\'a güvensin.\"'),
(1518, 1287, 9, 52, 'Tevbe ', 'Medeni', 'De ki: \"Bize iki iyiden, gazilik ve şehidlikten başka bir şeyin gelmesini mi bekliyorsunuz? Oysa biz Allah\'ın kendi katından veya elimizle, sizi bir azaba uğratmasını bekliyoruz. Bekleyiniz, doğrusu biz de sizinle birlikte beklemekteyiz.\"'),
(1519, 1288, 9, 53, 'Tevbe ', 'Medeni', 'De ki: \"İstekli yahut isteksiz olarak verin, nasıl olsa kabul edilmeyecektir. Siz şüphesiz fasık bir topluluksunuz.\"'),
(1520, 1289, 9, 54, 'Tevbe ', 'Medeni', 'Verdiklerinin kabul olunmasına engel olan, Allah\'ı ve Peygamberini inkar etmeleri, namaza tembel tembel gelmeleri, istemeye istemeye vermeleridir.'),
(1521, 1290, 9, 55, 'Tevbe ', 'Medeni', 'Artık onların malları ve çocukları seni imrendirmesin. Allah bunlarla onlara dünya hayatında azabetmek ve canlarının inkarcı olarak çıkmasını ister.'),
(1522, 1291, 9, 56, 'Tevbe ', 'Medeni', 'Sizden olmadıkları halde, sizinle beraber olduklarına Allah\'a yemin ederler. Oysa onlar korkak bir topluluktur.'),
(1523, 1292, 9, 57, 'Tevbe ', 'Medeni', 'Bir sığınak veya mağara yahut girecek bir yer bulmuş olsalardı, çarçabuk oraya yönelirlerdi.'),
(1524, 1293, 9, 58, 'Tevbe ', 'Medeni', 'Sadakalar hakkında sana dil uzatanlar vardır. Onlara verilirse hoşnut olurlar, verilmezse, hemen öfkeleniverirler.'),
(1525, 1294, 9, 59, 'Tevbe ', 'Medeni', 'Eğer onlar, Allah ve Peygamberinin kendilerine vermiş oldukları şeylere razı olsalar ve \"Allah bize yeter, O ve Peygamberi bol nimetinden bize verecektir; doğrusu biz Allah\'a gönül bağlayanlardanız\" deselerdi daha hayırlı olurdu.'),
(1526, 1295, 9, 60, 'Tevbe ', 'Medeni', 'Zekatlar; Allah\'tan bir farz olarak yoksullara, düşkünlere, onu toplayan memurlara, kalbleri Müslümanlığa ısındırılacaklara verilir; kölelerin, borçluların, Allah yolunda olanların ve yolda kalanların uğrunda sarfedilir. Allah bilendir, hakimdir.'),
(1527, 1296, 9, 61, 'Tevbe ', 'Medeni', 'İkiyüzlülerin içinde \"O her şeye kulak kesiliyor\" diyerek Peygamberi incitenler vardır. De ki: \"O kulak, Allah\'a inanan ve müminlere inanan, sizin için hayırlı olan, içinizden inanan kimselere rahmet olan bir kulaktır.\" Allah\'ın Peygamberini incitenlere can yakıcı azab vardır.'),
(1528, 1297, 9, 62, 'Tevbe ', 'Medeni', 'Sizi hoşnut etmek için Allah\'a yemin ederler. Eğer inanıyorlarsa Allah\'ı ve Peygamberini hoşnut etmeleri daha gereklidir.'),
(1529, 1298, 9, 63, 'Tevbe ', 'Medeni', 'Allah\'a ve Peygamberine karşı koymağa kalkışana, ebedi kalacağı cehennem ateşi bulunduğunu bilmezler mi? Büyük rezillik budur.'),
(1530, 1299, 9, 64, 'Tevbe ', 'Medeni', 'İkiyüzlüler, kalblerinde olanı haber verecek bir surenin inmesinden çekiniyorlar. De ki: \"Alay edin bakalım, Allah çekindiğiniz şeyi ortaya koyacaktır.\"'),
(1531, 1300, 9, 65, 'Tevbe ', 'Medeni', 'Onlara soracak olursan, \"Biz and olsun ki, eğlenip oynuyorduk\" diyecekler; De ki: \"Allah\'la, ayetleriyle, Peygamberiyle mi alay ediyordunuz?\"'),
(1532, 1301, 9, 66, 'Tevbe ', 'Medeni', 'Özür beyan etmeyin, inandıktan sonra inkar ettiniz. İçinizden bir topluluğu affetsek bile, suçlarından ötürü bir topluluğa da azab ederiz.'),
(1533, 1302, 9, 67, 'Tevbe ', 'Medeni', 'İkiyüzlü erkek ve kadınlar da birbirlerindendir: Kötülüğü emreder, iyiliğe engel olurlar; elleri de sıkıdır; Allah\'ı unuttular, bu yüzden Allah da onları unuttu. Doğrusu ikiyüzlüler fasıktırlar.'),
(1534, 1303, 9, 68, 'Tevbe ', 'Medeni', 'Allah, ikiyüzlü erkek ve kadınlara ve inkarcılara, ebedi kalacakları cehennem ateşini hazırlamıştır. O, onlara yeter. Allah onları lanetlemiştir (rahmetinden uzak kılmıştır). Onlara devamlı azab vardır.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(1535, 1304, 9, 69, 'Tevbe ', 'Medeni', 'Ey ikiyüzlüler! Siz, sizden önce daha kuvvetli, malları ve çocukları daha çok olup, hisselerince bunlardan faydalanan kimseler gibisiniz. Sizden öncekiler, hisselerince faydalandıkları gibi siz de hissenizce faydalandınız ve onların batıla daldıklarıgibi siz de daldınız. İşte bunlar dünyada ve ahirette işleri boşa çıkanlardır, işte bunlar mahvolanlardır.'),
(1536, 1305, 9, 70, 'Tevbe ', 'Medeni', 'Kendilerinden önce olan Nuh, Ad, Semud milletlerinin, İbrahim milletinin, Medyen ve altüst olmuş şehirler halkının haberleri onlara gelmedi mi? Peygamberleri onlara belgeler getirmişlerdi. Allah onlara zulmetmemiş, onlar kendilerine yazık etmişlerdir.'),
(1537, 1306, 9, 71, 'Tevbe ', 'Medeni', 'Mümin erkekler ve mümin kadınlar birbirlerinin velileridir; iyiyi emreder kötülükten alıkorlar; namaz kılarlar, zekat verirler, Allah\'a ve Peygamberine itaat ederler. İşte Allah bunlara rahmet edecektir. Allah şüphesiz güçlüdür, hakimdir.'),
(1538, 1307, 9, 72, 'Tevbe ', 'Medeni', 'Allah mümin erkeklere ve mümin kadınlara, temelli kalacakları, içlerinden ırmaklar akan cennetler, Adn cennetlerinde hoş meskenler vadetmiştir. Allah\'ın hoşnut olması en büyük şeydir. İşte büyük kurtuluş budur.'),
(1539, 1308, 9, 73, 'Tevbe ', 'Medeni', 'Ey Peygamber! İnkarcılarla, ikiyüzlülerle savaş; onlara karşı sert davran. Varacakları yer cehennemdir, ne kötü dönüştür.'),
(1540, 1309, 9, 74, 'Tevbe ', 'Medeni', 'And olsun ki, müslüman olduktan sonra inkar edip küfür sözünü söylemişler iken, söylemedik diye Allah\'a yemin ettiler, başaramayacakları bir şeye giriştiler; Allah ve Peygamberi bol nimetinden onları zenginleştirdi ve öç almaya kalktılar. Eğer tevbe ederlerse iyiliklerine olur; şayet yüz çevirirlerse, Allah onları dünya ve ahirette can yakıcı azaba uğratır. Yeryüzünde bir dost ve yardımcıları yoktur.'),
(1541, 1310, 9, 75, 'Tevbe ', 'Medeni', 'Aralarında: \"Allah bize bol nimetinden verecek olursa, and olsun ki sadaka vereceğiz ve iyilerden olacağız\" diye O\'na and verenler vardır.'),
(1542, 1311, 9, 76, 'Tevbe ', 'Medeni', 'Allah onlara bol nimetinden verince, cimrilik ettiler, yüz çevirdiler. Zaten dönektirler.'),
(1543, 1312, 9, 77, 'Tevbe ', 'Medeni', 'Allah\'a verdikleri sözden caydıkları ve yalancı oldukları için O\'nunla karşılaşacakları güne kadar Allah kalblerine nifak soktu.'),
(1544, 1313, 9, 78, 'Tevbe ', 'Medeni', 'İkiyüzlüler, Allah\'ın onların sırlarını ve gizli toplantılarını bildiğini, Allah\'ın görünmeyenleri bilen olduğunu bilmiyorlar mıydı?'),
(1545, 1314, 9, 79, 'Tevbe ', 'Medeni', 'Sadaka vermekte gönülden davranan müminlere dil uzatan ve ancak ellerinden geldiği kadar verebilenlerle alay eden kimselere bu davranışlarının cezasını Allah verir; onlara can yakıcı azab vardır.'),
(1546, 1315, 9, 80, 'Tevbe ', 'Medeni', 'Onların ister bağışlanmasını dile, ister dileme, birdir. Onlara yetmiş defa bağışlanma dilesen Allah onları bağışlamayacaktır. Bu, Allah\'ı ve Peygamberini inkar etmelerinden ötürüdür. Allah fasık topluluğu doğru yola eriştirmez.'),
(1547, 1316, 9, 81, 'Tevbe ', 'Medeni', 'Allah\'ın Peygamberinin hilafına geri kalanlar, oturup kalmalarına sevindiler. Allah yolunda mallariyle ve canlariyle cihat hoşlarına gitmedi. \"Sıcakta savaşa çıkmayın\" dediler. De ki: \"Cehennem ateşi daha sıcaktır.\" Keşke bilseydiler!'),
(1548, 1317, 9, 82, 'Tevbe ', 'Medeni', 'Yaptıklarının cezası olarak, bundan böyle az gülsünler, çok ağlasınlar.'),
(1549, 1318, 9, 83, 'Tevbe ', 'Medeni', 'Allah seni geri döndürüp, onlardan bir toplulukla karşılaştırdığı zaman, senden savaşa çıkmak için izin isterlerse, de ki: \"Benimle asla çıkamayacaksınız, benim yanımda hiçbir düşmanla savaşmıyacaksınız; çünkü baştan, oturup kalmaya razı oldunuz. Artık geri kalanlarla beraber oturun.\"'),
(1550, 1319, 9, 84, 'Tevbe ', 'Medeni', 'Onlardan ölen kimsenin namazını sakın kılma, mezarı başında da durma! Çünkü onlar Allah\'ı ve peygamberini inkar ettiler, fasık olarak öldüler.'),
(1551, 1320, 9, 85, 'Tevbe ', 'Medeni', 'Malları ve çocukları seni hayrete düşürmesin; Allah bunlarla onlara dünyada azabetmek ve canlarının inkarcı olarak çıkmasını ister.'),
(1552, 1321, 9, 86, 'Tevbe ', 'Medeni', '\"Allah\'a inanın ve Peygamberinin yanında savaşın\" diye bir sure inmiş olsa, onların gücü yetenleri sizden izin isterler ve \"Bizi bırak oturanlarla beraber kalalım\" derler.'),
(1553, 1322, 9, 87, 'Tevbe ', 'Medeni', 'Geri kalan kadınlarla beraber bulunmaya razı oldular. Kalbleri kapanmıştır, bu yüzden anlamazlar.'),
(1554, 1323, 9, 88, 'Tevbe ', 'Medeni', 'Ama Peygamber ve onunla beraber bulunan müminler, mallariyle ve canlariyle savaştılar. İşte iyilikler onlaradır, saadete erişenler de onlardır.'),
(1555, 1324, 9, 89, 'Tevbe ', 'Medeni', 'Allah onlara temelli kalacakları, içlerinden ırmaklar akan cennetler hazırlamıştır. Büyük kurtuluş budur.'),
(1556, 1325, 9, 90, 'Tevbe ', 'Medeni', 'Bedevilerden, izin almak üzere, özür beyan eden kimseler geldiler. Allah\'a ve Peygamberine yalan söyleyenler ise, özür bile beyan etmeksizin geri kaldılar. Onlardan kafir olanlar can yakıcı azaba uğrayacaktır.'),
(1557, 1326, 9, 91, 'Tevbe ', 'Medeni', 'Güçsüzlere, hastalara ve sarfedecek bir şeyi bulunmayanlara, Allah ve Peygamberine bağlı kaldıkları müddetçe sorumluluk yoktur. İyi davrananlara sorumluluk olmaz. Allah bağışlayandır, merhamet edendir.'),
(1558, 1327, 9, 92, 'Tevbe ', 'Medeni', 'Binek vermen için sana geldiklerinde, \"Size binek bulamıyorum\" dediğin zaman, sarfedecek bir şey bulamadıkları için üzüntüden gözyaşı dökerek geri dönenlere de sorumluluk yoktur.'),
(1559, 1328, 9, 93, 'Tevbe ', 'Medeni', 'Sorumluluk ancak, zengin oldukları halde senden izin isteyen, geride kalan kadınlarla bulunmaya razı olanlara ve Allah kalblerini mühürlemiş olduğu için bilmeyenleredir.'),
(1560, 1329, 9, 94, 'Tevbe ', 'Medeni', 'Savaştan döndüğünüzde size özür beyan ederler. Onlara de ki: \"özür beyan etmeyin, size inanmayacağız, Allah haberlerinizi bize bildirmiştir. Allah da, Peygamberi de işleyeceklerinizi görecektir. Sonunda, görülmeyeni ve görüneni bilen Allah\'a geri çevrileceksiniz. O, işlediklerinizi size haber verecektir.\"'),
(1561, 1330, 9, 95, 'Tevbe ', 'Medeni', 'Döndüğünüzde kendilerine çıkışmamanız için, Allah\'a yemin edeceklerdir. Siz onlardan yüz çevirin; çünkü pistirler. Yaptıklarının karşılığı olarak varacakları yer cehennemdir.'),
(1562, 1331, 9, 96, 'Tevbe ', 'Medeni', 'Kendilerinden hoşnut olasınız diye, size and verirler. Siz onlardan hoşnut olsanız bile, Allah, yoldan çıkmış kimselerden razı olmaz.'),
(1563, 1332, 9, 97, 'Tevbe ', 'Medeni', 'Bedevilerin küfür ve nifakları her yönden, daha ileridir. Allah\'ın, Peygamberine indirdiğinin sınırlarını bilmemek, onlara daha layıktır. Allah bilendir, hakimdir.'),
(1564, 1333, 9, 98, 'Tevbe ', 'Medeni', 'Bedevilerden, Allah yolunda sarfettiklerini angarya sayanlar ve sizin başınıza belalar gelmesini bekleyenler vardır. Belalar onlara olsun; Allah işitir ve bilir.'),
(1565, 1334, 9, 99, 'Tevbe ', 'Medeni', 'Bedevilerden, Allah\'a ve ahiret gününe inanan, sarfettiğini, Allah katında ibadet ve Peygamberin dualarına nail olmağa vesile sayanlar da vardır. Bilin ki, verdikleri onlar için ibadettir. Allah, onlara rahmet edecektir. Allah şüphesiz bağışlar ve merhamet eder.'),
(1566, 1335, 9, 100, 'Tevbe ', 'Medeni', 'İyilik yarışında önceliği kazanan Muhacirler ve Ensar ile, onlara güzelce uyanlardan Allah hoşnut olmuştur, onlar da Allah\'tan hoşnuddurlar. Allah onlara, içinde temelli ve ebedi kalacakları, içlerinden ırmaklar akan cennetler hazırlamıştır; işte büyük kurtuluş budur.'),
(1567, 1336, 9, 101, 'Tevbe ', 'Medeni', 'Çevrenizdeki Bedeviler içinde ikiyüzlüler ve Medine\'liler içinde de ikiyüzlülükte direnenler vardır. Onları siz değil, ancak Biz biliriz. Kendilerine iki defa azabedeceğiz; onlar sonra da büyük bir azaba uğratılırlar.'),
(1568, 1337, 9, 102, 'Tevbe ', 'Medeni', 'Savaştan geri kalanların bir kısmı da, suçlarını itiraf ettiler. Onlar iyi işi kötüyle karıştırmışlardı. Allah\'ın onların tevbesini kabul etmesi umulur; çünkü O bağışlayandır, merhamet edendir.'),
(1569, 1338, 9, 103, 'Tevbe ', 'Medeni', 'Mallarının bir kısmını, kendilerini temizleyip arıtacak sadaka olarak al, onlara dua et; senin duan onlar için bir güvendir. Allah işitir ve bilir.'),
(1570, 1339, 9, 104, 'Tevbe ', 'Medeni', 'Allah\'ın, kullarının tevbesini kabul ettiğini, sadakalar aldığını, Allah\'ın tevbeleri kabul ve merhamet eden olduğunu bilmiyorlar mı?'),
(1571, 1340, 9, 105, 'Tevbe ', 'Medeni', 'De ki: \"İstediğinizi işleyin; Allah, Peygamberi ve müminler işlediklerinizi görecektir. Hepiniz, görülmeyeni ve görüleni bilen Allah\'a döndürüleceksiniz. O size, işlediklerinizi bildirecektir.\"'),
(1572, 1341, 9, 106, 'Tevbe ', 'Medeni', 'Savaştan geri kalanların bir kısmının işi de Allah\'ın buyruğuna kalmıştır. Allah onlara ya azabeder, ya da tevbelerini kabul eder. O bilendir, hakimdir.'),
(1573, 1342, 9, 107, 'Tevbe ', 'Medeni', 'Zarar vermek, inkar etmek, müminlerin arasını ayırmak, Allah ve Peygamber\'ine karşı savaşanlara daha önceden gözcülük yapmak üzere bir mescid kurup: \"Biz sadece iyilik yapmak istedik\" diye yemin edenlerin yalancı olduklarına şüphesiz ki Allah şahiddir.'),
(1574, 1343, 9, 108, 'Tevbe ', 'Medeni', 'O mescide hiç girme! İlk gününden beri Allah\'a karşı gelmekten sakınmak için kurulan mescidde bulunman daha uygundur. Orada, arınmak isteyen insanlar vardır. Allah, arınmak isteyenleri sever.'),
(1575, 1344, 9, 109, 'Tevbe ', 'Medeni', 'Yapısını, Allah\'tan sakınmak ve Onun hoşnudluğuna ermek için yapan kimse mi daha hayırlıdır; yoksa, yapısını kayacak bir yar kıyısına yapıp da onunla beraber cehennem ateşine yuvarlanan kimse mi? Allah, zulmeden kimselere doğru yolu göstermez.'),
(1576, 1345, 9, 110, 'Tevbe ', 'Medeni', 'Yaptıkları bina, kalblerinde şüphe ve ızdırap kaynağı olmakta kalbleri paralanana kadar devam edecektir. Allah bilendir, hakimdir.'),
(1577, 1346, 9, 111, 'Tevbe ', 'Medeni', 'Allah şüphesiz, Allah yolunda savaşıp, öldüren ve öldürülen müminlerin canlarını ve mallarını Tevrat, İncil ve Kuran\'da söz verilmiş bir hak olarak cennete karşılık satın almıştır. Verdiği sözü Allah\'tan daha çok tutan kim vardır? Öyleyse, yaptığınız alışverişe sevinin; bu büyük başarıdır.'),
(1578, 1347, 9, 112, 'Tevbe ', 'Medeni', 'Allah\'a tevbe eden, kullukta bulunan, O\'nu öven, O\'nun uğrunda gezen, rüku ve secde eden, uygun olanı buyurup fenalığı yasak eden ve Allah\'ın yasalarını koruyan müminlere de müjdele.'),
(1579, 1348, 9, 113, 'Tevbe ', 'Medeni', 'Cehennemlik oldukları anlaşıldıktan sonra, akraba bile olsalar, puta tapanlar için mağfiret dilemek Peygamber\'e ve müminlere yaraşmaz.'),
(1580, 1349, 9, 114, 'Tevbe ', 'Medeni', 'İbrahim\'in, babası için mağfiret dilemesi, sadece ona verdiği bir sözden ötürü idi.'),
(1581, 1350, 9, 115, 'Tevbe ', 'Medeni', 'Allah, bir milleti doğru yola eriştirdikten sonra, sakınacakları şeyleri onlara açıklamadıkça, sapıklığa düşürmez. Allah şüphesiz her şeyi bilir.'),
(1582, 1351, 9, 116, 'Tevbe ', 'Medeni', 'Göklerin ve yerin hükümranlığı elbette Allah\'ındır; dirilten ve öldüren O\'dur. Allah\'tan başka dost ve yardımcınız yoktur.'),
(1583, 1352, 9, 117, 'Tevbe ', 'Medeni', 'And olsun ki, Allah, sıkıntılı bir zamanda bir kısmının kalbleri kaymak üzere iken Peygamber\'e uyan Muhacirlerle Ensarın ve Peygamberin tevbelerini kabul etti. Tevbelerini, onlara karşı şefkatli ve merhametli olduğu için kabul etmiştir.'),
(1584, 1353, 9, 118, 'Tevbe ', 'Medeni', 'Bütün genişliğine rağmen yer onlara dar gelerek nefisleri kendilerini sıkıştırıp, Allah\'tan başka sığınacak kimse olmadığını anlayan, savaştan geri kalmış üç kişinin tevbesini de kabul etti. Allah, tevbe ettikleri için onların tevbesini kabul etmiştir. Çünkü O tevbeleri kabul eden, merhametli olandır.'),
(1585, 1354, 9, 119, 'Tevbe ', 'Medeni', 'Ey inananlar! Allah\'tan sakının ve doğrularla beraber olun.'),
(1586, 1355, 9, 120, 'Tevbe ', 'Medeni', 'Medinelilere ve çevrelerinde bulunan Bedevilere, savaşta Allah\'ın Peygamberinden geri kalmak, kendilerini ona tercih etmek yaraşmaz. Çünkü Allah yolunda susuzluğa, yorgunluğa, açlığa uğramak, kafirleri kızdıracak bir yeri işgal etmek ve düşmana başarı kazanmak karşılığında, onların yararlı bir iş yaptıkları mutlaka yazılır. Doğrusu Allah iyilik yapanların ecrini zayi etmez.'),
(1587, 1356, 9, 121, 'Tevbe ', 'Medeni', 'Allah, yaptıklarının karşılığını en güzel şekilde kendilerine vermek üzere, az veya çok sarfettikleri her şey, yürüdükleri her yol, onlar için yazılır.'),
(1588, 1357, 9, 122, 'Tevbe ', 'Medeni', 'İnananlar toptan savaşa çıkmamalıdır. Her topluluktan bir taifenin dini iyi öğrenmek ve milletlerini geri döndüklerinde uyarmak üzere geri kalmaları gerekli olmaz mı? Ki böylece belki yanlış hareketlerden çekinirler.'),
(1589, 1358, 9, 123, 'Tevbe ', 'Medeni', 'Ey inananlar! Yakınınızda bulunan inkarcılarla savaşın; sizi kendilerine karşı sert bulsunlar. Bilin ki Allah, kendisine karşı gelmekten sakınanlarla beraberdir.'),
(1590, 1359, 9, 124, 'Tevbe ', 'Medeni', 'Bir sure inince, aralarında \"Bu, hanginizin imanını artırdı?\" diyen ikiyüzlüler vardır. İnananların ise imanını artırmıştır; onlar birbirlerine bunu müjdelemek isterler.'),
(1591, 1360, 9, 125, 'Tevbe ', 'Medeni', 'Kalblerinde hastalık olanların ise pisliklerine pislik katmıştır; onlar kafir olarak ölmüşlerdir.'),
(1592, 1361, 9, 126, 'Tevbe ', 'Medeni', 'Onlar, yılda bir iki defa belaya uğratılıp imtihana çekildiklerini görmüyorlar mı? Böyleyken yine tevbe etmiyorlar, ibret de almıyorlar.'),
(1593, 1362, 9, 127, 'Tevbe ', 'Medeni', 'Bir sure inince, \"Sizi bir kimse görüyor mu?\" diye birbirlerine bakarlar, sonra dönüp giderler. Anlamaz bir güruh olmalarına karşılık Allah onların kalblerini imandan döndürmüştür.'),
(1594, 1363, 9, 128, 'Tevbe ', 'Medeni', 'Ey inananlar! And olsun ki, içinizden size, sıkıntıya uğramanız kendisine ağır gelen, size düşkün, inananlara şefkatli ve merhametli bir peygamber gelmiştir.'),
(1595, 1364, 9, 129, 'Tevbe ', 'Medeni', 'Eğer yüz çevirirlerse de ki: \"Allah bana yeter; O\'ndan başka tanrı yoktur, yalnız O\'na güveniyorum; O büyük arşın Rabbidir.\"'),
(1596, 1365, 10, 1, 'Yûnus ', 'Mekki', 'Elif, Lam, Ra. İşte bunlar hikmetli Kitap\'ın ayetleridir.'),
(1597, 1366, 10, 2, 'Yûnus ', 'Mekki', 'İçlerinden birine, \"İnsanları uyar ve inananlara, Rableri katında yüksek makamlar olduğunu müjdele\" diye vahyetmemiz, insanların tuhafına mı gitti ki, kafirler: \"Bu apaçık bir büyücüdür\" dediler?'),
(1598, 1367, 10, 3, 'Yûnus ', 'Mekki', 'Doğrusu sizin Rabbiniz gökleri ve yeri altı günde yaratıp sonra arşa hükmeden, işi düzenleyen Allah\'tır, izni olmadan kimse şefaat edemez. İşte Rabbiniz olan Allah budur. O\'na kulluk edin. Nasihat dinlemez misiniz?'),
(1599, 1368, 10, 4, 'Yûnus ', 'Mekki', 'Hepinizin dönüşü, O\'nadır. Allah\'ın vadi haktır. O, önce yaratır, sonra inanıp yararlı işler yapanların ve inkar edenlerin hareketlerinin karşılığını adaletle vermek için tekrar diriltir. İnkarcılara, inkarlarından ötürü kızgın bir içecek ve can yakıcı azab vardır.'),
(1600, 1369, 10, 5, 'Yûnus ', 'Mekki', 'Güneşi ışıklı ve ayı nurlu yapan; yılların sayısını ve hesabı bilmeniz için, aya konak yerleri düzenleyen O\'dur. Allah bunları ancak gerçeğe göre yaratmıştır; bilen millete ayetleri uzun uzadıya açıklıyor.'),
(1601, 1370, 10, 6, 'Yûnus ', 'Mekki', 'Gece ile gündüzün birbiri ardınca gelmesinde, Allah\'ın göklerde ve yerde yarattıklarında, O\'na karşı gelmekten sakınan kimseler için ayetler vardır.'),
(1602, 1371, 10, 7, 'Yûnus ', 'Mekki', 'Bizimle karşılaşmayı ummayan ve dünya hayatından hoşnut olup ona bağlananların ve ayetlerimizden habersiz bulunanların, işte bunların kazandıklarına karşılık varacakları yer cehennemdir.'),
(1603, 1372, 10, 8, 'Yûnus ', 'Mekki', 'Bizimle karşılaşmayı ummayan ve dünya hayatından hoşnut olup ona bağlananların ve ayetlerimizden habersiz bulunanların, işte bunların kazandıklarına karşılık varacakları yer cehennemdir.'),
(1604, 1373, 10, 9, 'Yûnus ', 'Mekki', 'İnananlar ve yararlı iş yapanları, imanlarına karşılık Rableri doğru yola eriştirir; nimet cennetlerinde onların altlarından ırmaklar akar.'),
(1605, 1374, 10, 10, 'Yûnus ', 'Mekki', 'Oradaki duaları: \"Münezzehsin ey Allah\'ım\", dirlik temennileri: \"Selam size\" ve dualarının sonu da: \"Alemlerin Rabbi Allah\'a hamd olsun\"dur.'),
(1606, 1375, 10, 11, 'Yûnus ', 'Mekki', 'İyiliği acele isteyen kimselere Allah fenalığı da çarçabuk verseydi, süreleri hemen bitmiş olurdu. Bizimle karşılaşmayı ummayanları, azgınlıkları içinde bocalayıp dururlarken bırakırız.'),
(1607, 1376, 10, 12, 'Yûnus ', 'Mekki', 'İnsana bir darlık gelince, yan yatarken, oturur veya ayakta iken bize yalvarıp yakarır; biz darlığını giderince, başına gelen darlıktan ötürü bize hiç yalvarmamışa döner. İşlerinde tutumsuz olanlara, yaptıkları böylece güzel görünür.'),
(1608, 1377, 10, 13, 'Yûnus ', 'Mekki', 'And olsun ki, sizden önce nice nesilleri, peygamberleri onlara belgeler getirmişken, haksızlık ederek inanmadıkları zaman yok etmiştik. İşte biz suçlu milleti böyle cezalandırırız.'),
(1609, 1378, 10, 14, 'Yûnus ', 'Mekki', 'Sonra onların ardından, nasıl davranacağınıza bakmak için sizi yeryüzünde onların yerine geçirdik.'),
(1610, 1379, 10, 15, 'Yûnus ', 'Mekki', 'Ayetlerimiz onlara açık açık okununca, bizimle karşılaşmayı ummayanlar, \"Bundan başka bir Kuran getir veya bunu değiştir\" dediler. De ki: \"Onu kendiliğimden değiştiremem, ben ancak, bana vahyolunana uyarım. Ben Rabbime karşı gelirsem, büyük günün azabına uğramaktan korkarım.\"'),
(1611, 1380, 10, 16, 'Yûnus ', 'Mekki', 'De ki: \"Allah dileseydi ben onu size okumazdım, size de bildirmemiş olurdu. Daha önce yıllarca aranızda bulundum, hiç düşünmüyor musunuz?\"'),
(1612, 1381, 10, 17, 'Yûnus ', 'Mekki', 'Allah\'a karşı yalan uyduran veya ayetlerini yalan sayandan daha zalim kim olabilir? Suçlular elbette saadete erişemezler.'),
(1613, 1382, 10, 18, 'Yûnus ', 'Mekki', 'Onlar, Allah\'ı bırakarak, kendilerine fayda da zarar da veremeyen putlara taparlar: \"Bunlar, Allah katında bizim şefaatçılarımızdır\" derler. De ki: \"Göklerde ve yerde, Allah\'ın bilmediği bir şeyi mi O\'na haber veriyorsunuz?\" Allah, onların ortak koşmalarından münezzeh ve yücedir.'),
(1614, 1383, 10, 19, 'Yûnus ', 'Mekki', 'İnsanlar bir tek ümmettiler, sonra ayrılığa düştüler; şayet Rabbinden, daha önce bir takdir geçmemiş olsaydı, aralarında ihtilafa düştükleri şeyler hakkında hüküm çoktan verilmiş olurdu.'),
(1615, 1384, 10, 20, 'Yûnus ', 'Mekki', '\"Rabbinden ona (Muhammed\'e) bir mucize indirilse ne olur!\" derler. Onlara de ki: \"Gaybı bilmek Allah\'a mahsustur; bekleyin, doğrusu ben de sizinle birlikte beklemekteyim.\"'),
(1616, 1385, 10, 21, 'Yûnus ', 'Mekki', 'İnsanlara darlık geldikten sonra onlara bolluğu taddırdığımızda, hemen ayetlerimize dil uzatmağa kalkışırlar; onlara de ki: \"Hile yapanın cezasını vermekte Allah daha çabuktur.\" Elçi meleklerimiz kurduğunuz tuzakları hiç şüphesiz yazmaktadırlar.'),
(1617, 1386, 10, 22, 'Yûnus ', 'Mekki', 'Sizi karada ve denizde yürüten Allah\'tır. Bulunduğunuz gemi, içindekileri güzel bir rüzgarla götürürken yolcular neşelenirler; bir fırtına çıkıp da onları her taraftan dalgaların sardığı ve çepeçevre kuşatıldıklarını sandıkları anda ise Allah\'ın dinine sarılarak, \"Bizi bu tehlikeden kurtarırsan and olsun ki şükredenlerden oluruz\" diye O\'na yalvarırlar.'),
(1618, 1387, 10, 23, 'Yûnus ', 'Mekki', 'Allah onları kurtarınca, hemen yeryüzünde haksız yere taşkınlıklara başlarlar. Ey insanlar! Geçici dünya hayatında yaptığınız taşkınlık aleyhinizedir. Sonra dönüşünüz Bizedir. Yaptıklarınızı size bildiririz.'),
(1619, 1388, 10, 24, 'Yûnus ', 'Mekki', 'Dünya hayatı gökten indirdiğimiz su gibidir ki, onunla insan ve hayvanların yiyeceği bitkiler yetişip birbirine karışmıştır. Yeryüzünün süslenip bezendiği ve yerin sahiplerinin bütün bunlara malik olduklarını sandıkları sırada, gece veya gündüz buyruğumuz o yere gelmiş ve orayı hiçbir şey bitirmemişe çevirmişiz; bir gün önce birşey yokmuş gibi olmuştur. Düşünen millet için ayetleri böylece uzun açıklıyoruz.'),
(1620, 1389, 10, 25, 'Yûnus ', 'Mekki', 'Allah, cennete çağırır ve dilediğini doğru yola eriştirir.'),
(1621, 1390, 10, 26, 'Yûnus ', 'Mekki', 'İyi davrananlara; daima daha iyisi ve üstünü verilir. Onların yüzlerine ne bir karalık, ne de zillet bulaşır. İşte onlar cennetliklerdir, orada temelli kalırlar.'),
(1622, 1391, 10, 27, 'Yûnus ', 'Mekki', 'Kötülük işleyenlere kötülükleri kadar ceza verilir; onların yüzlerini zillet bürür; Allah\'a karşı onları savunacak yoktur; yüzleri, geceden kara bir parçayla örtülmüş gibidir. Bunlar cehennemliklerdir, orada temelli kalırlar.'),
(1623, 1392, 10, 28, 'Yûnus ', 'Mekki', 'Onların hepsini bir gün toplarız, sonra, puta tapanlara, \"Siz ve putlarınız yerlerinize! deyip onları birbirlerinden ayırırız. Putları ise: \"Bize tapmıyordunuz ki. Allah, sizinle bizim aramızda şahit olarak yeter. Sizin tapınmanızdan bizim haberimiz yoktu\" derler.'),
(1624, 1393, 10, 29, 'Yûnus ', 'Mekki', 'Onların hepsini bir gün toplarız, sonra, puta tapanlara, \"Siz ve putlarınız yerlerinize! deyip onları birbirlerinden ayırırız. Putları ise: \"Bize tapmıyordunuz ki. Allah, sizinle bizim aramızda şahit olarak yeter. Sizin tapınmanızdan bizim haberimiz yoktu\" derler.'),
(1625, 1394, 10, 30, 'Yûnus ', 'Mekki', 'İşte orada herkes dünyada yapmış olduğuyla imtihan verir ve gerçek Mevlaları olan Allah\'a döndürülür. Uydurdukları putlar da ortadan kaybolmuştur.'),
(1626, 1395, 10, 31, 'Yûnus ', 'Mekki', 'De ki: \"Gökten ve yerden size rızık veren kimdir? Kulak ve gözlerin sahibi kimdir? Diriyi ölüden çıkaran, ölüyü de diriden çıkaran kimdir? Her işi düzenleyen kimdir?\" Onlar: \"Allah\'tır! \" diyecekler. \"O halde O\'na karşı gelmekten sakınmaz mısınız?\" de.'),
(1627, 1396, 10, 32, 'Yûnus ', 'Mekki', 'İşte gerçek Rabbiniz Allah budur. Gerçeğin dışında sadece sapıklık vardır. Öyleyse nasıl olup da döndürülüyorsunuz?'),
(1628, 1397, 10, 33, 'Yûnus ', 'Mekki', 'Böylece, fasık olanların inanmayacaklarına dair Rabbinin söylediği söz gerçekleşti.'),
(1629, 1398, 10, 34, 'Yûnus ', 'Mekki', 'De ki: \"Koştuğunuz ortaklardan, önce yaratan, sonra bunu tekrar eden var mıdır?\" De ki: \"Allah önce yaratır, sonra bunu tekrar eder. Nasıl da döndürülürsünüz! \"'),
(1630, 1399, 10, 35, 'Yûnus ', 'Mekki', 'De ki: \"Koştuğunuz ortaklardan gerçeğe eriştiren var mıdır?\" De ki: \"Ama Allah gerçeğe eriştirir. Gerçeğe eriştiren mi, yoksa, birisi götürmezse gidemeyen mi uyulmağa daha layıktır? Ne biçim hüküm veriyorsunuz?\"'),
(1631, 1400, 10, 36, 'Yûnus ', 'Mekki', 'Onların çoğu zanna uyarlar; gerçekte ise zan, hakikat karşısında bir şey ifade etmez. Allah, yaptıklarını şüphesiz bilir.'),
(1632, 1401, 10, 37, 'Yûnus ', 'Mekki', 'Bu Kuran, Allah\'tandır, başkası tarafından uydurulmuş değildir. Ancak kendinden öncekini doğrular ve O Kitap\'ı açıklar. Alemlerin Rabbinden geldiğinden şüphe yoktur.'),
(1633, 1402, 10, 38, 'Yûnus ', 'Mekki', 'Senin için, \"Onu uydurdu mu?\" diyorlar. De ki: \"Onun surelerine benzer bir sure meydana getirin, iddianızda samimi iseniz, Allah\'tan başka çağırabileceklerinizi de çağırın.\"'),
(1634, 1403, 10, 39, 'Yûnus ', 'Mekki', 'Onlar, ilmini kavrayamadıkları ve henüz yorumu da kendilerine bildirilmemiş olan şeyi yalanladılar. Onlardan öncekiler de böylece yalanlamışlardı. Zalimlerin sonunun nasıl olduğuna bir bak.'),
(1635, 1404, 10, 40, 'Yûnus ', 'Mekki', 'Aralarında ona inanan ve inanmayan vardır. Rabbin, bozguncuları daha iyi bilir.'),
(1636, 1405, 10, 41, 'Yûnus ', 'Mekki', 'Seni yalanlarlarsa, \"Benim yaptığım bana, sizin yaptığınız sizedir; siz benim yaptığımdan sorumlu değilsiniz, ben de sizin yaptığınızdan sorumlu değilim\" de.'),
(1637, 1406, 10, 42, 'Yûnus ', 'Mekki', 'Aralarında sana kulak veren vardır. Sen, sağırlara, üstelik akılları da almazsa, işittirebilir misin?'),
(1638, 1407, 10, 43, 'Yûnus ', 'Mekki', 'Aralarında sana bakan vardır. Sen körleri, görmezlerken doğru yola iletebilir misin?'),
(1639, 1408, 10, 44, 'Yûnus ', 'Mekki', 'Allah insanlara hiç zulmetmez, fakat insanlar kendilerine zulmederler.'),
(1640, 1409, 10, 45, 'Yûnus ', 'Mekki', 'Onları toplayacağı kıyamet günü, sanki gündüz, birbirleriyle sadece tanışacakları bir saat kadar kalmış gibidirler. Allah\'ın karşısına çıkmayı yalan sayanlar kaybetmişlerdir.'),
(1641, 1410, 10, 46, 'Yûnus ', 'Mekki', 'Onlara, söz verdiğimiz azabın bir kısmını ya dünyada sana gösteririz, veya senin ruhunu alırız da nasıl olsa onların dönüşü Bizedir; (ahirette görürsün) Allah onların yaptıklarına şahiddir.'),
(1642, 1411, 10, 47, 'Yûnus ', 'Mekki', 'Her ümmetin bir peygamberi vardır. Onlara peygamberleri geldiğinde aralarında adaletle hüküm verilmiş olur. Onların hakları yenmez.'),
(1643, 1412, 10, 48, 'Yûnus ', 'Mekki', '\"Bu iddiada samimi iseniz, bu azabın gerçekleşmesi ne zamandır? söyle\" derler.'),
(1644, 1413, 10, 49, 'Yûnus ', 'Mekki', 'De ki: \"Allah\'ın dilemesi dışında ben kendime bir fayda ve zarar verecek durumda değilim. Her ümmet için bir süre vardır; süreleri sona erince bir saat bile geciktirilmezler ve öne de alınmazlar.\"'),
(1645, 1414, 10, 50, 'Yûnus ', 'Mekki', 'De ki: \"Allah\'ın azabı size gece veya gündüz gelirse, ne yaparsınız? Suçlular neye bunda acele ediyorlar?\"'),
(1646, 1415, 10, 51, 'Yûnus ', 'Mekki', 'Vuku bulduktan sonra mı O\'na inanacaksınız? İnanmayanlar azabı görünce, \"şimdi miydi?\" derler. \"Elbette, siz onu acele istiyordunuz\" denir.'),
(1647, 1416, 10, 52, 'Yûnus ', 'Mekki', 'Haksızlık edenlere de: \"Sürekli azabı tadın, ancak yaptığınıza karşılık ceza çekiyorsunuz\" denir.'),
(1648, 1417, 10, 53, 'Yûnus ', 'Mekki', '\"O gerçek midir?\" diye senden sorarlar. De ki: \"Evet, Rabbim hakkı için o gerçektir, siz aciz kılamazsınız (önleyemezsiniz).\"'),
(1649, 1418, 10, 54, 'Yûnus ', 'Mekki', 'Haksızlık etmiş olan her kişi, yeryüzünde olan her şeye sahip olsa, onu azabın fidyesi olarak verirdi. Azabı görünce pişmanlık gösterdiler. Haksızlığa uğratılmadan aralarında adaletle hükmolunmuştur.'),
(1650, 1419, 10, 55, 'Yûnus ', 'Mekki', 'İyi bilin ki, Allah\'ın verdiği söz gerçektir, ama çoğu bunu bilmez.'),
(1651, 1420, 10, 56, 'Yûnus ', 'Mekki', 'Dirilten ve öldüren O\'dur. O\'na döneceksiniz.'),
(1652, 1421, 10, 57, 'Yûnus ', 'Mekki', 'Ey insanlar! Rabbinizden size bir öğüt ve kalblerde olana şifa, inananlara doğruyu gösteren bir rehber ve rahmet gelmiştir.'),
(1653, 1422, 10, 58, 'Yûnus ', 'Mekki', 'De ki: \"Bunlar, Allah\'ın bol nimeti ve rahmetiyledir.\" Buna sevinsinler. O, onların topladıklarından daha hayırlıdır.'),
(1654, 1423, 10, 59, 'Yûnus ', 'Mekki', 'De ki: \"Allah\'ın size indirdiği rızkın bir kısmını haram, bir kısmını helal kıldığınızı görmüyor musunuz?\" De ki: \"Size Allah mı izin verdi, yoksa Allah\'a karşı yalan mı uyduruyorsunuz?\"'),
(1655, 1424, 10, 60, 'Yûnus ', 'Mekki', 'Allah\'a karşı yalan uyduranlar kıyamet gününü ne zannederler? Doğrusu Allah\'ın insanlara olan nimeti boldur, fakat çoğu şükretmezler.'),
(1656, 1425, 10, 61, 'Yûnus ', 'Mekki', 'Ne iş yaparsan yap ve sizler ona dair Kuran\'dan ne okursanız okuyun; ne yaparsanız yapın; yaptıklarınıza daldığınız anda, mutlaka Biz sizi görürüz. Yerde ve gökte hiçbir zerre Rabbinden gizli değildir. Bundan daha küçüğü veya daha büyüğü şüphesiz apaçık bir Kitap\'dadır.'),
(1657, 1426, 10, 62, 'Yûnus ', 'Mekki', 'İyi bilin ki, Allah\'ın dostlarına korku yoktur, onlar üzülmeyeceklerdir.'),
(1658, 1427, 10, 63, 'Yûnus ', 'Mekki', 'Onlar Allah\'a inanmış ve O\'na karşı gelmekten sakınmışlardır.'),
(1659, 1428, 10, 64, 'Yûnus ', 'Mekki', 'Dünya hayatında da, ahirette de müjde onlaradır. Allah\'ın sözlerinde hiçbir değişme yoktur. Bu büyük başarıdır.'),
(1660, 1429, 10, 65, 'Yûnus ', 'Mekki', 'İnkarcıların sözleri seni üzmesin, çünkü bütün kudret Allah\'ındır. O, işitir ve bilir.'),
(1661, 1430, 10, 66, 'Yûnus ', 'Mekki', 'İyi bilin ki, göklerde ve yerde kim varsa hepsi Allah\'ındır. Allah\'ı bırakıp ortak koşanlar sadece zanna uyanlardır. Onlar ancak tahminde bulunuyorlar.'),
(1662, 1431, 10, 67, 'Yûnus ', 'Mekki', 'Size geceyi dinlenesiniz diye karanlık ve gündüzü çalışasınız diye aydınlık olarak yaratan Allah\'tır. Kulak veren millet için bunlarda ayetler vardır.'),
(1663, 1432, 10, 68, 'Yûnus ', 'Mekki', '\"Allah çocuk edindi\" dediler; haşa; O müstağnidir; göklerde ve yerde olanlara sahiptir. Elinizde, onun çocuk edindiğine dair bir delil yoktur, bilmediğiniz şeyi Allah\'a karşı nasıl söylüyorsunuz?'),
(1664, 1433, 10, 69, 'Yûnus ', 'Mekki', 'De ki: \"Allah\'a karşı yalan uyduranlar, kurtuluşa erişemezler.\"'),
(1665, 1434, 10, 70, 'Yûnus ', 'Mekki', 'Onlar için dünyada bir müddet geçinme vardır, sonra dönüşleri Bizedir. İnkarlarına karşılık onlara çetin azab taddıracağız.'),
(1666, 1435, 10, 71, 'Yûnus ', 'Mekki', 'Onlara Nuh\'un başından geçenleri anlat: Milletine, \"Ey milletim! Eğer durumum, Allah\'ın ayetlerini hatırlatmam size ağır geliyorsa ki ben Allah\'a güvenmişimdir siz ve koştuğunuz ortaklar elbirliği edin; yapacağınız iş sonra size bir tasa vermesin. Sonra onu bana uygulayın ve beni ertelemeyin\" demişti.'),
(1667, 1436, 10, 72, 'Yûnus ', 'Mekki', '\"Eğer yüz çevirirseniz bilin ki, ben sizden bir ücret istemiyorum. Benim ecrim Allah\'a aiddir. Müslimlerden olmakla emrolundum.\"'),
(1668, 1437, 10, 73, 'Yûnus ', 'Mekki', 'Onu yalancı saydılar; ama Biz onu ve gemide beraberinde bulunanları kurtardık. Onları ötekilerin yerine geçirdik, ayetlerimizi yalanlayanları suda boğduk. Uyarılanlardan söz dinlemeyenlerin sonlarının nasıl olduğuna bir bak.'),
(1669, 1438, 10, 74, 'Yûnus ', 'Mekki', 'Sonra onun ardından milletlere peygamberler gönderdik, onlara belgeler getirdiler. Diğerlerinin daha önce yalan saymış olduklarına bunlar da inanmadılar. Aşırı gidenlerin kalblerini işte böylece mühürleriz.'),
(1670, 1439, 10, 75, 'Yûnus ', 'Mekki', 'Onların ardından da Firavun ve erkanına ayetlerimizle Musa ve Harun\'u gönderdik. Ama büyüklük tasladılar ve suçlu bir millet oldular.'),
(1671, 1440, 10, 76, 'Yûnus ', 'Mekki', 'Gerçek, katımızdan onlara gelince: \"Doğrusu bu apaçık bir büyüdür\" dediler.'),
(1672, 1441, 10, 77, 'Yûnus ', 'Mekki', 'Musa: \"Size gelen gerçeğe dil mi uzatırsınız? Bu sihir midir? Sihirbazlar zaten başarı kazanamazlar\" dedi.'),
(1673, 1442, 10, 78, 'Yûnus ', 'Mekki', '\"Siz ikiniz, bizi babalarımızı üzerinde bulduğumuz yoldan çevirmek ve yeryüzünün büyükleri olasınız diye mi geldiniz? Biz size inanmıyoruz\" dediler.'),
(1674, 1443, 10, 79, 'Yûnus ', 'Mekki', 'Firavun: \"Bütün bilgin sihirbazları bana getirin\" dedi.'),
(1675, 1444, 10, 80, 'Yûnus ', 'Mekki', 'Sihirbazlar gelince Musa onlara: \"Atacağınızı atın\" dedi.'),
(1676, 1445, 10, 81, 'Yûnus ', 'Mekki', 'Attıklarında, Musa: \"Yaptığınız sihirdir, fakat Allah onu boşa çıkaracaktır. Allah bozguncuların işini elbette düzeltmez. Suçlular istemese de Allah sözleriyle hakkı gerçekleştirecektir\" dedi.'),
(1677, 1446, 10, 82, 'Yûnus ', 'Mekki', 'Attıklarında, Musa: \"Yaptığınız sihirdir, fakat Allah onu boşa çıkaracaktır. Allah bozguncuların işini elbette düzeltmez. Suçlular istemese de Allah sözleriyle hakkı gerçekleştirecektir\" dedi.'),
(1678, 1447, 10, 83, 'Yûnus ', 'Mekki', 'Firavun ve erkanının kendilerine fenalık yapmasından korktuklarından, milletinin bir kısım gençleri dışında, kimse Musa\'ya inanmamıştı, çünkü Firavun o yerde hakimdi. O, gerçekten aşırı gidenlerdendi.'),
(1679, 1448, 10, 84, 'Yûnus ', 'Mekki', 'Musa: \"Ey milletim! Allah\'a inanıyorsanız ve teslim olmuşsanız O\'na güvenin\" dedi.'),
(1680, 1449, 10, 85, 'Yûnus ', 'Mekki', '\"Allah\'a güvendik; Ey Rabbimiz! Zalim bir millet ile bizi sınama, rahmetinle bizi kafirlerden kurtar\" dediler.'),
(1681, 1450, 10, 86, 'Yûnus ', 'Mekki', '\"Allah\'a güvendik; Ey Rabbimiz! Zalim bir millet ile bizi sınama, rahmetinle bizi kafirlerden kurtar\" dediler.'),
(1682, 1451, 10, 87, 'Yûnus ', 'Mekki', 'Musa ve kardeşine: \"Mısır\'da milletinize evler hazırlayın; evlerinizi namazgah edinin, namaz kılın\" diye vahyettik, \"İnananlara müjde et.\"'),
(1683, 1452, 10, 88, 'Yûnus ', 'Mekki', 'Musa: \"Rabbimiz! Doğrusu sen Firavun\'a ve erkanına ziynetler ve dünya hayatında mallar verdin. Rabbimiz! Senin yolundan şaşırtmaları için mi? Rabbimiz! Mallarını yok et, kalblerini sık; çünkü onlar can yakıcı azabı görmedikçe inanmazlar\" dedi.'),
(1684, 1453, 10, 89, 'Yûnus ', 'Mekki', 'Allah: \"İkinizin duası kabul olundu. Dürüst hareket edin; bilmeyenlerin yoluna asla uymayın\" dedi.'),
(1685, 1454, 10, 90, 'Yûnus ', 'Mekki', 'İsrailoğullarını denizden geçirdik, Firavun ve askerleri haksızlık ve düşmanlıkla ardlarına düştüler. Firavun boğulacağı anda: \"İsrailoğullarının inandığından başka tanrı olmadığına inandım, artık ben O\'na teslim olanlardanım\" dedi.'),
(1686, 1455, 10, 91, 'Yûnus ', 'Mekki', 'O\'na: \"Şimdi mi inandın? Daha önce baş kaldırmış ve bozgunculuk etmiştin\" dendi.'),
(1687, 1456, 10, 92, 'Yûnus ', 'Mekki', '\"Senden sonrakilere bir ibret teşkil etmesi için bugün sadece senin cesedini çıkarıp (sahile) atacağız\" dedik. Doğrusu insanların çoğu ayetlerimizden habersizdir.'),
(1688, 1457, 10, 93, 'Yûnus ', 'Mekki', 'And olsun ki, İsrailoğullarını iyi bir yere yerleştirdik, onlara temiz rızıklar verdik, kendilerine bir bilgi gelene kadar ayrılığa düşmediler.'),
(1689, 1458, 10, 94, 'Yûnus ', 'Mekki', 'Sana indirdiğimizden şüphede isen, senden önce indirdiğimiz Kitap\'ları okuyanlara sor. And olsun ki, sana Rabbinden gerçek gelmiştir, sakın şüphelenenlerden olma.'),
(1690, 1459, 10, 95, 'Yûnus ', 'Mekki', 'Allah\'ın ayetlerini yalanlayanlardan da olma, yoksa kaybedenlerden olursun.'),
(1691, 1460, 10, 96, 'Yûnus ', 'Mekki', 'Doğrusu Rabbinin söz verdiği azabı hak edenler, can yakıcı azabı görene kadar kendilerine her türlü belge gelse bile inanmazlar.'),
(1692, 1461, 10, 97, 'Yûnus ', 'Mekki', 'Doğrusu Rabbinin söz verdiği azabı hak edenler, can yakıcı azabı görene kadar kendilerine her türlü belge gelse bile inanmazlar.'),
(1693, 1462, 10, 98, 'Yûnus ', 'Mekki', 'Bir kent halkı inanmalı değil miydi ki, imanları kendilerine fayda versin! İşte Yunus\'un milleti, inandığı zaman, dünya hayatında rezilliği gerektiren azabı onlardan kaldırdık ve onları bir süre daha bu dünyada geçindirdik.'),
(1694, 1463, 10, 99, 'Yûnus ', 'Mekki', 'Rabbin dileseydi, yeryüzünde bulunanların hepsi inanırdı. Öyle iken insanları inanmaya sen mi zorlayacaksın?'),
(1695, 1464, 10, 100, 'Yûnus ', 'Mekki', 'Allah\'ın izni olmadıkça hiç kimse inanamaz. O, aklını kullanmayanlara kötü bir azab verir.'),
(1696, 1465, 10, 101, 'Yûnus ', 'Mekki', '\"Göklerde ve yerde neler var, bir bakın\" de. İnanmayacak bir millete ayetler ve uyarmalar fayda vermez.'),
(1697, 1466, 10, 102, 'Yûnus ', 'Mekki', 'Kendilerinden önce geçenlerin başlarına gelen olaylardan başka bir şey mi bekliyorlar? \"Bekleyin, ben de sizinle beraber beklemekteyim\" de.'),
(1698, 1467, 10, 103, 'Yûnus ', 'Mekki', 'Sonra Biz, peygamberlerimizi ve inananları böylece kurtarırız, inananları (verdiğimiz söz gereğince) kurtarmamız Bize haktır.'),
(1699, 1468, 10, 104, 'Yûnus ', 'Mekki', 'De ki: \"Ey insanlar! Benim dinimden şüphede iseniz bilin ki ben Allah\'tan başka taptıklarınıza tapmam. Ancak, sizi öldürecek olan Allah\'a kulluk ederim. İnananlardan olmakla emrolundum.\"'),
(1700, 1469, 10, 105, 'Yûnus ', 'Mekki', '(Muhammed\'e) \"Yüzünü, doğruya yönelmiş olarak dine çevir, sakın ortak koşanlardan olma; sana fayda da zarar da veremeyecek, Allah\'tan başkasına yalvarma; öyle yaparsan şüphesiz, zalimlerden olursun\" denildi.'),
(1701, 1470, 10, 106, 'Yûnus ', 'Mekki', '(Muhammed\'e) \"Yüzünü, doğruya yönelmiş olarak dine çevir, sakın ortak koşanlardan olma; sana fayda da zarar da veremeyecek, Allah\'tan başkasına yalvarma; öyle yaparsan şüphesiz, zalimlerden olursun\" denildi.'),
(1702, 1471, 10, 107, 'Yûnus ', 'Mekki', 'Allah sana bir sıkıntı verirse, onu O\'ndan başkası gideremez. Sana bir iyilik dilerse O\'nun nimetini engelleyecek yoktur. O\'nu kullarından dilediğine verir. O, bağışlayandır, merhametlidir.'),
(1703, 1472, 10, 108, 'Yûnus ', 'Mekki', 'De ki: \"Ey insanlar! Rabbinizden size gerçek gelmiştir. Doğru yola giren ancak kendisi için girmiş ve sapıtan da kendi zararına olarak sapıtmıştır. Ben sizin üzerinize vekil değilim.\"'),
(1704, 1473, 10, 109, 'Yûnus ', 'Mekki', 'Sana vahyedilene uy; Allah hükmünü verene kadar sabret. O, hüküm verenlerin en iyisidir.'),
(1705, 1474, 11, 1, 'Hûd ', 'Mekki', 'Elif, Lam, Ra. Bu Kitap, hakim ve haberdar olan Allah tarafından, Allah\'tan başkasına kulluk etmeyesiniz diye ayetleri kesin kılınmış, sonra da uzun uzadıya açıklanmış bir Kitap\'dır. Ben size, O\'nun tarafından gönderilmiş bir uyarıcı ve müjdeciyim. Rabbinizden mağfiret dileyin ve O\'na tevbe edin ki, belli bir süreye kadar sizi güzelce geçindirsin ve her fazilet sahibine faziletinin karşılığını versin. Eğer yüz çevirirseniz o zaman ben doğrusu hakkınızda büyük günün azabından korkarım.'),
(1706, 1475, 11, 2, 'Hûd ', 'Mekki', 'Elif, Lam, Ra. Bu Kitap, hakim ve haberdar olan Allah tarafından, Allah\'tan başkasına kulluk etmeyesiniz diye ayetleri kesin kılınmış, sonra da uzun uzadıya açıklanmış bir Kitap\'dır. Ben size, O\'nun tarafından gönderilmiş bir uyarıcı ve müjdeciyim. Rabbinizden mağfiret dileyin ve O\'na tevbe edin ki, belli bir süreye kadar sizi güzelce geçindirsin ve her fazilet sahibine faziletinin karşılığını versin. Eğer yüz çevirirseniz o zaman ben doğrusu hakkınızda büyük günün azabından korkarım.'),
(1707, 1476, 11, 3, 'Hûd ', 'Mekki', 'Elif, Lam, Ra. Bu Kitap, hakim ve haberdar olan Allah tarafından, Allah\'tan başkasına kulluk etmeyesiniz diye ayetleri kesin kılınmış, sonra da uzun uzadıya açıklanmış bir Kitap\'dır. Ben size, O\'nun tarafından gönderilmiş bir uyarıcı ve müjdeciyim. Rabbinizden mağfiret dileyin ve O\'na tevbe edin ki, belli bir süreye kadar sizi güzelce geçindirsin ve her fazilet sahibine faziletinin karşılığını versin. Eğer yüz çevirirseniz o zaman ben doğrusu hakkınızda büyük günün azabından korkarım.'),
(1708, 1477, 11, 4, 'Hûd ', 'Mekki', 'Dönüşünüz ancak Allah\'adır. O her şeye Kadir\'dir.'),
(1709, 1478, 11, 5, 'Hûd ', 'Mekki', 'Bilin ki, onlar Kuran okunurken gizlenmek için iki büklüm olurlar. Bilin ki, elbiselerine büründüklerinde bile Allah onların gizlediklerini ve açığa vurduklarını bilir. Çünkü O, kalblerde olanı bilendir.'),
(1710, 1479, 11, 6, 'Hûd ', 'Mekki', 'Yeryüzünde yaşayan bütün canlıların rızkı ancak Allah\'a aittir. O, canlıları babaların sulbünde kararlaşmış ve anaların rahminde kararlaşmakta iken de bilir. Her şey apaçık bir Kitaptadır.'),
(1711, 1480, 11, 7, 'Hûd ', 'Mekki', 'Arş\'ı su üzerinde iken, hanginizin daha güzel işi işleyeceğini ortaya koymak için, gökleri ve yeri altı günde yaratan O\'dur. And olsun ki, \"Siz gerçekten, ölümden sonra dirileceksiniz\" desen, inkar edenler: \"Bu, apaçık bir sihirden başka bir şey değildir\" derler.'),
(1712, 1481, 11, 8, 'Hûd ', 'Mekki', 'And olsun ki, onların azabını sayılı bir süreye kadar ertelesek, \"Onu alıkoyan nedir?\" derler. Bilin ki, onlara azab geldiği gün, artık geri çevrilmez; alaya aldıkları şey onları mahvedecektir.'),
(1713, 1482, 11, 9, 'Hûd ', 'Mekki', 'And olsun ki, insana nimetimizi tattırır sonra onu ondan çekip alırsak, o şüphesiz umutsuz bir nanköre döner.'),
(1714, 1483, 11, 10, 'Hûd ', 'Mekki', 'Başına gelen sıkıntıdan sonra, ona bir nimet tattırırsak, \"Musibetler başımdan gitti\" der; doğrusu o, şımarıp böbürlenen biridir.'),
(1715, 1484, 11, 11, 'Hûd ', 'Mekki', 'Bunların dışında, sabredip iyi işler işleyen kimseler, işte onlara mağfiret ve büyük ecir vardır.'),
(1716, 1485, 11, 12, 'Hûd ', 'Mekki', 'Putperestlerin: \"Ona bir hazine indirilmeli veya yanında bir melek gelmeli değil miydi?\" demelerinden senin kalbin daralır ve belki de sana vahyolunanın bir kısmını terkedecek olursun. Sen ancak bir uyarıcısın, Allah her şeye vekildir.'),
(1717, 1486, 11, 13, 'Hûd ', 'Mekki', 'Senin için: \"Onu uydurdu\" diyorlar, öyle mi? De ki: \"Öyleyse onun surelerine benzer uydurma on sure meydana getirin, iddianızda samimi iseniz, Allah\'tan başka çağırabileceklerinizi de çağırın.\"'),
(1718, 1487, 11, 14, 'Hûd ', 'Mekki', 'Söylediğinizi yapamazlarsa, bilin ki o, ancak Allah\'ın ilmiyle indirilmiştir. O\'ndan başka tanrı yoktur, artık müslümansınız değil mi?'),
(1719, 1488, 11, 15, 'Hûd ', 'Mekki', 'Dünya hayatını ve güzelliklerini isteyenlere, orada işlediklerinin karşılığını tastamam veririz; onlar orada bir eksikliğe de uğratılmazlar.'),
(1720, 1489, 11, 16, 'Hûd ', 'Mekki', 'İşte ahirette onlara ateşten başka bir şey yoktur. İşledikleri şeyler orada boşa gitmiştir. Zaten yapmakta oldukları da batıldır.'),
(1721, 1490, 11, 17, 'Hûd ', 'Mekki', 'Rabbinin katından bir belgesi ve onun arkasından da bir şahidi olanlar, önlerinde de Musa\'nın Kitap\'ı önder ve rahmet olarak bulunanlardır ki, işte onlar Kuran\'a inanırlar. Hangi topluluk onu inkar ederse yeri ateştir; senin de bundan şüphen olmasın. Doğrusu o, Rabbinden bir gerçektir, fakat insanların çoğu inanmazlar.'),
(1722, 1491, 11, 18, 'Hûd ', 'Mekki', 'Yalan söyleyerek Allah\'a iftira edenden daha zalim kim vardır? İşte bunlar Rablerine götürülürler ve şahidler: \"Rablerine yalan söyleyenler bunlardır\" derler. Bilin ki Allah\'ın laneti haksızlık yapanlaradır.'),
(1723, 1492, 11, 19, 'Hûd ', 'Mekki', 'Bunlar Allah\'ın yolundan alıkorlar ve o yolu eğriltmeğe çalışırlar; işte onlar ahireti inkar edenlerdir.'),
(1724, 1493, 11, 20, 'Hûd ', 'Mekki', 'Bunlar yeryüzünde Allah\'ı aciz bırakamazlar. Allah\'dan başka kendilerini kurtaracak dostları da yoktur. Azab onlara kat kat verilir; işitemezler ve göremezlerdi.'),
(1725, 1494, 11, 21, 'Hûd ', 'Mekki', 'İşte bunlar kendilerine yazık edenlerdir. Uydurdukları putlar da onlardan uzaklaşıp kaybolmuştur.'),
(1726, 1495, 11, 22, 'Hûd ', 'Mekki', 'Ahirette en çok kayba uğrayacaklar şüphesiz bunlardır.'),
(1727, 1496, 11, 23, 'Hûd ', 'Mekki', 'Doğrusu inanan ve yararlı iş yapanlar ve Rablerine boyun eğenler, işte onlar cennetliklerdir; orada temellidirler.'),
(1728, 1497, 11, 24, 'Hûd ', 'Mekki', 'Bu iki zümrenin durumu, kör ve sağır kimse ile gören ve işiten kimsenin durumuna benzer. Durumları hiç eşit olabilir mi? İbret almıyor musunuz?'),
(1729, 1498, 11, 25, 'Hûd ', 'Mekki', 'And olsun ki biz Nuh\'u kendi milletine gönderdik; \"Ben sizin için apaçık bir uyarıcıyım; Allah\'tan başkasına kulluk etmeyin; doğrusu ben hakkınızda can yakıcı bir günün azabından korkuyorum\" dedi.'),
(1730, 1499, 11, 26, 'Hûd ', 'Mekki', 'And olsun ki biz Nuh\'u kendi milletine gönderdik; \"Ben sizin için apaçık bir uyarıcıyım; Allah\'tan başkasına kulluk etmeyin; doğrusu ben hakkınızda can yakıcı bir günün azabından korkuyorum\" dedi.'),
(1731, 1500, 11, 27, 'Hûd ', 'Mekki', 'Milletinin inkarcı ileri gelenleri: \"Senin ancak kendimiz gibi bir insan olduğunu görüyoruz. Daha başlangıçta, sana bizim ayak takımı dışında kimsenin uyduğunu görmüyoruz. Sizin bizden bir üstünlüğünüz yoktur; biz sizi yalancı sanıyoruz\" dediler.'),
(1732, 1501, 11, 28, 'Hûd ', 'Mekki', 'Nuh: \"Ey milletim! Rabbimin katından bir delilim bulunsa ve bana yine katından bir rahmet vermiş de bunlar sizden gizlenmiş olsa, söyleyin bana, hoşlanmadığınız halde zorla sizi bunlara mecbur mu ederiz?\" dedi.'),
(1733, 1502, 11, 29, 'Hûd ', 'Mekki', '\"Ey milletim! Buna karşılık ben sizden bir mal da istemiyorum. Benim ücretim Allah\'a aittir; inananları da kovacak değilim; çünkü onlar Rableriyle karşılaşacaklar; fakat ben sizi cahil bir millet olarak görüyorum.\"'),
(1734, 1503, 11, 30, 'Hûd ', 'Mekki', '\"Ey milletim! Onları kovarsam, Allah\'a karşı beni kim savunur? Düşünmez misiniz?\"'),
(1735, 1504, 11, 31, 'Hûd ', 'Mekki', '\"Size, Allah\'ın hazineleri yanımdadır demiyorum; gaybı da bilmem; doğrusu melek olduğumu da söylemiyorum; küçük gördüklerinize Allah iyilik vermeyecektir diyemem; içlerinde olanı Allah daha iyi bilir. Yoksa şüphesiz haksızlık edenlerden olurum.\"'),
(1736, 1505, 11, 32, 'Hûd ', 'Mekki', '\"Ey Nuh! Bizimle cidden tartıştın; hem de çok tartıştın. Doğru sözlülerden isen tehdit ettiğin azabı başımıza getir\" dediler.'),
(1737, 1506, 11, 33, 'Hûd ', 'Mekki', '\"Ancak Allah dilerse onu başınıza getirir, siz O\'nu aciz bırakamazsınız. Allah sizi azdırmak isterse, ben size öğüt vermek istesem de faydası olmaz. O, sizin Rabbinizdir, O\'na döndürüleceksiniz\" dedi.'),
(1738, 1507, 11, 34, 'Hûd ', 'Mekki', '\"Ancak Allah dilerse onu başınıza getirir, siz O\'nu aciz bırakamazsınız. Allah sizi azdırmak isterse, ben size öğüt vermek istesem de faydası olmaz. O, sizin Rabbinizdir, O\'na döndürüleceksiniz\" dedi.'),
(1739, 1508, 11, 35, 'Hûd ', 'Mekki', 'Sana \"Kuran\'ı kendiliğinden uydurdu\" derler, de ki: \"Uydurdumsa suçu bana aittir; oysa ben sizin işlediğiniz günahlardan uzağım.\"'),
(1740, 1509, 11, 36, 'Hûd ', 'Mekki', 'Nuh\'a, \"Senin milletinden, inanmış olanlardan başkası inanmayacaktır; onların yapageldiklerine üzülme; nezaretimiz altında, sana bildirdiğimiz gibi gemiyi yap. Haksızlık yapanlar için Bana baş vurma, çünkü onlar suda boğulacaklardır\" diye Allah tarafından vahyolundu.'),
(1741, 1510, 11, 37, 'Hûd ', 'Mekki', 'Nuh\'a, \"Senin milletinden, inanmış olanlardan başkası inanmayacaktır; onların yapageldiklerine üzülme; nezaretimiz altında, sana bildirdiğimiz gibi gemiyi yap. Haksızlık yapanlar için Bana baş vurma, çünkü onlar suda boğulacaklardır\" diye Allah tarafından vahyolundu.'),
(1742, 1511, 11, 38, 'Hûd ', 'Mekki', 'Gemiyi yaparken, milletinin inkarcı ileri gelenleri yanına uğradıkça onunla alay ederlerdi. O da: \"Bizimle alay ediyorsunuz ama, alay ettiğiniz gibi biz de sizinle alay edeceğiz; rezil edecek olan azabın kime geleceğini ve kime sürekli azabın ineceğini göreceksiniz\" dedi.'),
(1743, 1512, 11, 39, 'Hûd ', 'Mekki', 'Gemiyi yaparken, milletinin inkarcı ileri gelenleri yanına uğradıkça onunla alay ederlerdi. O da: \"Bizimle alay ediyorsunuz ama, alay ettiğiniz gibi biz de sizinle alay edeceğiz; rezil edecek olan azabın kime geleceğini ve kime sürekli azabın ineceğini göreceksiniz\" dedi.'),
(1744, 1513, 11, 40, 'Hûd ', 'Mekki', 'Buyruğumuz gelip tandırdan sular kaynamağa başlayınca, \"Her cinsten birer çifti ve aleyhine hüküm verilmiş olanın dışında kalan çoluk çocuğunu ve inananları gemiye bindir\" dedik. Pek az kimse onunla beraber inanmıştı.'),
(1745, 1514, 11, 41, 'Hûd ', 'Mekki', 'Allah \"Oraya binin; yürümesi ve durması Allah\'ın ismiyledir, Rabbin bağışlar ve merhamet eder\" dedi.'),
(1746, 1515, 11, 42, 'Hûd ', 'Mekki', 'Gemi, dağlar gibi dalgalar içinde onları götürürken, Nuh, bir kenarda ayrı kalmış olan oğluna \"Ey oğulcuğum! Bizimle beraber gel, kafirlerle birlik olma\" diye seslendi.'),
(1747, 1516, 11, 43, 'Hûd ', 'Mekki', 'Oğlu: \"Dağa sığınırım, beni sudan kurtarır\" deyince, Nuh: \"Bugün Allah\'ın buyruğundan O\'nun acıdıkları dışında kurtulacak yoktur\" dedi. Aralarına dalga girdi, oğlu da boğulanlara karıştı.'),
(1748, 1517, 11, 44, 'Hûd ', 'Mekki', 'Yere, \"Suyunu çek!\", göğe, \"Ey gök sen de tut!\" denildi. Su çekildi, iş de bitti; gemi Cudi\'ye oturdu. \"Haksızlık yapan millet Allah\'ın rahmetinden uzak olsun\" denildi.'),
(1749, 1518, 11, 45, 'Hûd ', 'Mekki', 'Nuh Rabbine seslendi: \"Rabbim! Oğlum benim ailemdendi. Doğrusu Senin vadin haktır. Sen hükmedenlerin en iyi hükmedenisin\" dedi.'),
(1750, 1519, 11, 46, 'Hûd ', 'Mekki', 'Allah: \"Ey Nuh! O senin ailenden sayılmaz; çünkü kötü bir iş işlemiştir; öyleyse bilmediğin şeyi Benden isteme. İşte sana öğüt, bilgisizlerden olma\" dedi.'),
(1751, 1520, 11, 47, 'Hûd ', 'Mekki', '\"Rabbim! Bilmediğim şeyi Senden istemekten Sana sığınırım. Beni bağışlamaz ve bana merhamet etmezsen kaybedenlerden olurum\" dedi.'),
(1752, 1521, 11, 48, 'Hûd ', 'Mekki', '\"Ey Nuh! Sana ve seninle beraber olan topluluklara bizden bir selamet ve bereketle gemiden in. Ama birçok toplulukları da geçindireceğiz, sonra onlara can yakıcı bir azab vereceğiz\" denildi.'),
(1753, 1522, 11, 49, 'Hûd ', 'Mekki', 'Bunlar sana vahyettiğimiz bilinmeyen olaylardır. Sen de, milletin de daha önce bunları bilmezdiniz. Sabret, sonuç, Allah\'tan sakınanlarındır.'),
(1754, 1523, 11, 50, 'Hûd ', 'Mekki', 'Ad milletine kardeşleri Hud\'u gönderdik. Şöyle dedi: \"Ey milletim! Allah\'a kulluk edin. O\'ndan başka tanrınız yoktur; yoksa sadece yalan uyduran kimseler olursunuz.\"'),
(1755, 1524, 11, 51, 'Hûd ', 'Mekki', '\"Ey milletim! Buna karşılık sizden bir ücret istemiyorum. Benim ücretim ancak beni yaratana aittir. Akletmez misiniz?\"'),
(1756, 1525, 11, 52, 'Hûd ', 'Mekki', '\"Ey milletim! Rabbinizden mağfiret dileyin, sonra O\'na tevbe edin ki size gökten bol bol yağmur göndersin, kuvvetinize kuvvet katsın; suçlular olarak yüz çevirmeyin.\"'),
(1757, 1526, 11, 53, 'Hûd ', 'Mekki', '\"Ey Hud! Sen bize bir belge getirmeden, senin sözünden ötürü tanrılarımızı terketmeyiz ve sana inanmayız.'),
(1758, 1527, 11, 54, 'Hûd ', 'Mekki', 'Bir kısım tanrılarımız seni çarpmıştır, demekten başka birşey demeyiz\" dediler. Hud: \"Doğrusu ben Allah\'ı şahit tutuyorum; siz de şahit olun ki ben O\'nu bırakıp koştuğunuz ortaklardan uzağım. Hepiniz bana tuzak kurun sonra da ertelemeyin. Ben, ancak benim de sizin de Rabbiniz olan Allah\'a güvenirim. Hiçbir canlı yoktur ki Allah ona el koymamış bulunsun. Rabbim elbette doğru yoldadır. Eğer yüz çevirirseniz, şüphesiz ben size benimle gönderileni bildirdim. Rabbim sizden başka bir milleti yerinize getirebilir, O\'na bir şey de yapamazsınız. Doğrusu Rabbim herşeyi koruyandır\" dedi.'),
(1759, 1528, 11, 55, 'Hûd ', 'Mekki', 'Bir kısım tanrılarımız seni çarpmıştır, demekten başka birşey demeyiz\" dediler. Hud: \"Doğrusu ben Allah\'ı şahit tutuyorum; siz de şahit olun ki ben O\'nu bırakıp koştuğunuz ortaklardan uzağım. Hepiniz bana tuzak kurun sonra da ertelemeyin. Ben, ancak benim de sizin de Rabbiniz olan Allah\'a güvenirim. Hiçbir canlı yoktur ki Allah ona el koymamış bulunsun. Rabbim elbette doğru yoldadır. Eğer yüz çevirirseniz, şüphesiz ben size benimle gönderileni bildirdim. Rabbim sizden başka bir milleti yerinize getirebilir, O\'na bir şey de yapamazsınız. Doğrusu Rabbim herşeyi koruyandır\" dedi.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(1760, 1529, 11, 56, 'Hûd ', 'Mekki', 'Bir kısım tanrılarımız seni çarpmıştır, demekten başka birşey demeyiz\" dediler. Hud: \"Doğrusu ben Allah\'ı şahit tutuyorum; siz de şahit olun ki ben O\'nu bırakıp koştuğunuz ortaklardan uzağım. Hepiniz bana tuzak kurun sonra da ertelemeyin. Ben, ancak benim de sizin de Rabbiniz olan Allah\'a güvenirim. Hiçbir canlı yoktur ki Allah ona el koymamış bulunsun. Rabbim elbette doğru yoldadır. Eğer yüz çevirirseniz, şüphesiz ben size benimle gönderileni bildirdim. Rabbim sizden başka bir milleti yerinize getirebilir, O\'na bir şey de yapamazsınız. Doğrusu Rabbim herşeyi koruyandır\" dedi.'),
(1761, 1530, 11, 57, 'Hûd ', 'Mekki', 'Bir kısım tanrılarımız seni çarpmıştır, demekten başka birşey demeyiz\" dediler. Hud: \"Doğrusu ben Allah\'ı şahit tutuyorum; siz de şahit olun ki ben O\'nu bırakıp koştuğunuz ortaklardan uzağım. Hepiniz bana tuzak kurun sonra da ertelemeyin. Ben, ancak benim de sizin de Rabbiniz olan Allah\'a güvenirim. Hiçbir canlı yoktur ki Allah ona el koymamış bulunsun. Rabbim elbette doğru yoldadır. Eğer yüz çevirirseniz, şüphesiz ben size benimle gönderileni bildirdim. Rabbim sizden başka bir milleti yerinize getirebilir, O\'na bir şey de yapamazsınız. Doğrusu Rabbim herşeyi koruyandır\" dedi.'),
(1762, 1531, 11, 58, 'Hûd ', 'Mekki', 'Buyruğumuz gelince, Hud\'u ve beraberindeki inananları, rahmetimizle kurtardık. Onları çetin bir azabdan koruduk.'),
(1763, 1532, 11, 59, 'Hûd ', 'Mekki', 'İşte bu, Rablerinin ayetlerini bile bile inkar eden, peygamberlerine kafa tutan ve her inatçı zorbanın emrine uyan Ad milletidir.'),
(1764, 1533, 11, 60, 'Hûd ', 'Mekki', 'Bu dünyada da, kıyamet gününde de lanete uğradılar. Bilin ki Ad milleti Rablerini inkar etti ve yine bilin ki Hud\'un milleti Ad Allah\'ın rahmetinden uzaklaştı.'),
(1765, 1534, 11, 61, 'Hûd ', 'Mekki', 'Semud milletine kardeşleri Salih\'i gönderdik. \"Ey milletim! Allah\'a kulluk edin; O\'ndan başka tanrınız yoktur; sizi yeryüzünde yaratıp orayı imar etmenizi dileyen O\'dur. Öyleyse O\'ndan mağfiret dileyin, sonra da O\'na tevbe edin. Doğrusu Rabbim size yakın ve duaları kabul edendir\" dedi.'),
(1766, 1535, 11, 62, 'Hûd ', 'Mekki', '\"Ey Salih! Sen bundan önce, aramızda kendisinden iyilik beklenir bir kimseydin; şimdi babalarımızın taptıklarına bizi tapmaktan men mi ediyorsun? Doğrusu bizi çağırdığın şeyden şüphe ve endişedeyiz\" dediler.'),
(1767, 1536, 11, 63, 'Hûd ', 'Mekki', '\"Ey milletim! Eğer Rabbimden bir belgem olur ve bana rahmet eder de ben O\'na baş kaldırırsam, söyleyin, Allah\'a karşı beni kim savunur? Bana zararımı artırmaktan başka birşey yapamazsınız\" dedi.'),
(1768, 1537, 11, 64, 'Hûd ', 'Mekki', '\"Ey milletim! Bu, size bir ayet olarak, Allah\'ın devesidir. Bırakın onu, Allah\'ın toprağında otlasın; ona fenalık etmeyin, yoksa siz hemen azaba uğrarsınız\"'),
(1769, 1538, 11, 65, 'Hûd ', 'Mekki', 'Buna rağmen onu kesip devirdiler. O zaman Salih: \"Yurdunuzda üç gün daha kalın. Bu, yalanlanmayacak bir sözdür\" dedi.'),
(1770, 1539, 11, 66, 'Hûd ', 'Mekki', 'Buyruğumuz gelince, Salih\'i ve beraberindeki inananları katımızdan bir rahmet olarak o günün rezilliğinden kurtardık. Doğrusu Rabbin pek kuvvetli ve güçlüdür.'),
(1771, 1540, 11, 67, 'Hûd ', 'Mekki', 'Haksızlık yapanları bir çığlık tuttu, oldukları yerde diz üstü çöküverdiler.'),
(1772, 1541, 11, 68, 'Hûd ', 'Mekki', 'Sanki orada hiç yaşamamışlardı. Bilin ki, Semud milleti Rabbini inkar etmişti. Bilin ki, Semud milleti Allah\'ın rahmetinden uzaklaştı.'),
(1773, 1542, 11, 69, 'Hûd ', 'Mekki', 'And olsun ki, elçilerimiz müjde ile İbrahim\'e geldiler. \"Selam sana\" dediler, \"Size de selam\" dedi, hemen kızartılmış bir buzağı getirdi.'),
(1774, 1543, 11, 70, 'Hûd ', 'Mekki', 'Ellerini ona uzatmadıklarını görünce, durumlarını beğenmedi ve içine korku düştü. Onlar, \"Korkma, biz Lut milletine gönderildik\" dediler.'),
(1775, 1544, 11, 71, 'Hûd ', 'Mekki', 'Bu arada, İbrahim\'in ayakta duran karısı gülünce, \"Ona İshak\'ı ardından Yakub\'u müjdeleriz\" dediler.'),
(1776, 1545, 11, 72, 'Hûd ', 'Mekki', '\"Vay başıma gelenler! Ben bir kocakarı, kocam da ihtiyar olmuşken nasıl doğurabilirim? Doğrusu bu şaşılacak bir şey\" dedi.'),
(1777, 1546, 11, 73, 'Hûd ', 'Mekki', '\"Ey evin hanımı! Allah\'ın rahmeti ve bereketleri üzerinize olmuşken, nasıl Allah\'ın işine şaşarsın? O, övülmeye layıktır, yücelerin yücesidir\" dediler.'),
(1778, 1547, 11, 74, 'Hûd ', 'Mekki', 'İbrahim\'in korkusu gidip de müjde kendisine ulaşınca, Lut milleti hakkında elçilerimizle tartışmaya girişti.'),
(1779, 1548, 11, 75, 'Hûd ', 'Mekki', 'Doğrusu İbrahim çok içli, yumuşak huylu ve kendini Allah\'a vermiş bir kimse idi.'),
(1780, 1549, 11, 76, 'Hûd ', 'Mekki', 'Elçilerimiz, \"Ey İbrahim! Bundan vazgeç, doğrusu Rabbinin emri gelmiştir. Onlara, şüphesiz, geri çevrilemeyecek bir azab gelmektedir\" dediler.'),
(1781, 1550, 11, 77, 'Hûd ', 'Mekki', 'Elçilerimiz Lut\'a gelince, onun fenasına gitti; çok sıkıldı, \"Bu çetin bir gündür\" dedi.'),
(1782, 1551, 11, 78, 'Hûd ', 'Mekki', 'Milleti ona koşarak geldiler. Daha önce kötü işler işliyorlardı. \"Ey milletim! İşte bunlar benim kızlarım, onlar sizin için daha temizdir. (size nikahlıyabilirim!) Allah\'tan sakının, konuklarımın önünde beni rezil etmeyin. İçinizde aklı başında kimse yok mudur?\" dedi.'),
(1783, 1552, 11, 79, 'Hûd ', 'Mekki', '\"And olsun ki, senin kızlarınla bir işimiz olmadığını biliyorsun; doğrusu, ne istediğimizin farkındasın\" dediler.'),
(1784, 1553, 11, 80, 'Hûd ', 'Mekki', '\"Keşke size yetecek bir kuvvetim olsa veya sağlam bir yere sığınsam\" dedi.'),
(1785, 1554, 11, 81, 'Hûd ', 'Mekki', '\"Ey Lut! Biz Rabbinin elçileriyiz, onlar sana ilişemiyecekler; geceleyin bir ara, ailenle beraber yola çık; karının dışında kimse geri kalmasın. Doğrusu onların başına gelen onun başına da gelecektir. Vadeleri gün doğana kadardır. Gün doğması yakın değil mi?\" dediler.'),
(1786, 1555, 11, 82, 'Hûd ', 'Mekki', 'Buyruğumuz gelince oraların altını üstüne getirdik; üzerine Rabbinin katından, işaretli olarak yığın yığın sert taş yağdırdık. Bunlar zalimlerden hiçbir zaman uzak olmayacaktır.'),
(1787, 1556, 11, 83, 'Hûd ', 'Mekki', 'Buyruğumuz gelince oraların altını üstüne getirdik; üzerine Rabbinin katından, işaretli olarak yığın yığın sert taş yağdırdık. Bunlar zalimlerden hiçbir zaman uzak olmayacaktır.'),
(1788, 1557, 11, 84, 'Hûd ', 'Mekki', 'Medyen halkına kardeşleri Şuayb\'ı gönderdik. Şöyle dedi: \"Ey milletim! Allah\'a kulluk edin; O\'ndan başka tanrınız yoktur. Ölçüyü tartıyı eksik tutmayın. Doğrusu ben sizi bolluk içinde görüyorum ve hakkınızda kuşatıcı bir günün azabından korkuyorum.\"'),
(1789, 1558, 11, 85, 'Hûd ', 'Mekki', '\"Ey milletim! Ölçüyü ve tartıyı tamamı tamamına yapın; insanlara eşyalarını eksik vermeyin; yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın.\"'),
(1790, 1559, 11, 86, 'Hûd ', 'Mekki', '\"İnanıyorsanız, Allah\'ın geri bıraktığı helal kar sizin için daha hayırlıdır. Ben size bekçi değilim.\"'),
(1791, 1560, 11, 87, 'Hûd ', 'Mekki', '\"Ey Şuayb! Babalarımızın taptığını bırakmamızı emreden veya mallarımızı istediğimiz gibi kullanmamızı meneden senin namazın mıdır? Sen doğrusu aklı başında, yumuşak huylu birisin\" dediler.'),
(1792, 1561, 11, 88, 'Hûd ', 'Mekki', '\"Ey Milletim! Rabbimden benim bir belgem olduğu ve bana güzel bir rızık da verdiği halde, O\'na karşı gelebilir miyim? Söylesenize! Size yasak ettiğim şeylerde, aykırı hareket etmek istemem; gücümün yettiği kadar ıslah etmekten başka bir dileğim yoktur. Başarım ancak Allah\'tandır, O\'na güvendim; O\'na yöneliyorum\" dedi.'),
(1793, 1562, 11, 89, 'Hûd ', 'Mekki', '\"Ey Milletim! Bana karşı gelmeniz, Nuh milletine veya Hud milletine yahut da Salih milletine gelen felaketin bir benzerini, sakın başınıza getirmesin. Lut milleti sizden uzak değildir.\"'),
(1794, 1563, 11, 90, 'Hûd ', 'Mekki', '\"Rabbinizden mağfiret dileyin; O\'na tevbe edin; doğrusu Rabbim merhamet eder ve çok sever.\"'),
(1795, 1564, 11, 91, 'Hûd ', 'Mekki', '\"Ey Şuayb! Söylediklerinin çoğunu anlamıyor ve doğrusu seni aramızda güçsüz görüyoruz. Eğer taraftarların olmasaydı seni taşlardık. Esasen bizim gözümüzde pek itibarın da yoktur\" dediler.'),
(1796, 1565, 11, 92, 'Hûd ', 'Mekki', '\"Ey Milletim! Benim taraftarlarım size göre Allah\'tan daha mı değerlidir ki Allah\'a sırt çevirdiniz? Doğrusu Rabbim yaptıklarınızı bilgisiyle kuşatmıştır\" dedi.'),
(1797, 1566, 11, 93, 'Hûd ', 'Mekki', '\"Ey Milletim! Durumunuzun gerektirdiğini yapın, doğrusu ben de yapacağım. Kime rezil edici bir azabın geleceğini, kimin yalancı olduğunu bileceksiniz. Gözleyin, doğrusu ben de sizinle beraber gözlüyorum.\"'),
(1798, 1567, 11, 94, 'Hûd ', 'Mekki', 'Buyruğumuz gelince, Şuayb\'ı ve beraberindeki inananları katımızdan bir rahmet olarak kurtardık. Haksızlık yapanları bir çığlık yakaladı, oldukları yerde diz üstü çöküverdiler.'),
(1799, 1568, 11, 95, 'Hûd ', 'Mekki', 'Sanki orada hiç yaşamamışlardı. Bilin ki Semud milleti Allah\'ın rahmetinden uzaklaştığı gibi Medyen halkı da uzaklaştı.'),
(1800, 1569, 11, 96, 'Hûd ', 'Mekki', 'And olsun ki Musa\'yı Firavun ve erkanına mucizelerimizle, apaçık bir delil ile gönderdik. Firavun\'un buyruğuna uydular, oysa Firavun\'un buyurduğu sağduyuya uygun değildi.'),
(1801, 1570, 11, 97, 'Hûd ', 'Mekki', 'And olsun ki Musa\'yı Firavun ve erkanına mucizelerimizle, apaçık bir delil ile gönderdik. Firavun\'un buyruğuna uydular, oysa Firavun\'un buyurduğu sağduyuya uygun değildi.'),
(1802, 1571, 11, 98, 'Hûd ', 'Mekki', 'Firavun, kıyamet gününde milletine öncülük eder, onları cehenneme götürür. Gittikleri yer ne kötü yerdir!'),
(1803, 1572, 11, 99, 'Hûd ', 'Mekki', 'Hem burada ve hem kıyamet gününde lanete uğratılırlar. Bu ne kötü bir bağıştır!'),
(1804, 1573, 11, 100, 'Hûd ', 'Mekki', 'Bu sana anlattıklarımız, kasabaların başından geçenlerdir. Onların bir kısmı hala duruyor, bir kısmı ise silinip gitmiştir.'),
(1805, 1574, 11, 101, 'Hûd ', 'Mekki', 'Onlara Biz zulmetmedik, fakat onlar kendilerine yazık ettiler. Rabbinin buyruğu gelince, Allah\'ı bırakıp taptıkları tanrılar kendilerine bir fayda vermedi, kayıplarını artırmaktan başka bir şeye yaramadı.'),
(1806, 1575, 11, 102, 'Hûd ', 'Mekki', 'Allah, kasabaların zalim halkını yakalayınca, böyle yakalar; yakalaması da şiddetli ve elimdir.'),
(1807, 1576, 11, 103, 'Hûd ', 'Mekki', 'Ahiretin azabından korkanlara, bunda, hiç şüphesiz ibret vardır. Bu, insanların toplanacağı gündür; bu, görülecek bir gündür.'),
(1808, 1577, 11, 104, 'Hûd ', 'Mekki', 'Biz, o günü, ancak belli bir süreye kadar geciktiririz.'),
(1809, 1578, 11, 105, 'Hûd ', 'Mekki', 'O gün gelince, Allah\'ın izni olmaksızın hiç kimse konuşamaz: İçlerinde bedbaht olanlar da, mesut olanlar da vardır.'),
(1810, 1579, 11, 106, 'Hûd ', 'Mekki', 'Bedbaht olanlar cehennemdedirler. Onlar orada ah edip inlerler.'),
(1811, 1580, 11, 107, 'Hûd ', 'Mekki', 'Rabbinin dilemesi bir yana, gökler ve yer durdukça, orada temelli kalacaklardır. Rabbin, şüphesiz, her istediğini yapar.'),
(1812, 1581, 11, 108, 'Hûd ', 'Mekki', 'Mesud olanlar ise cennettedirler. Rabbinin dilemesi bir yana, sonsuz bir lütuf olarak, gökler ve yer durdukça, orada temelli kalacaklardır.'),
(1813, 1582, 11, 109, 'Hûd ', 'Mekki', 'Bu putperestlerin taptıklarının batıl olduğunda şüphen olmasın; daha önce babalarının tapmış oldukları gibi onlar da taparlar. Onlara paylarını şüphesiz eksiksiz olarak ödeyeceğiz.'),
(1814, 1583, 11, 110, 'Hûd ', 'Mekki', 'And olsun ki, Musa\'ya Kitap verdik; onda ayrılığa düştüler. Eğer Rabbinin verilmiş bir sözü olmasaydı, aralarında çoktan hükmedilmiş olurdu. Doğrusu onlar, Kitap\'ın Allah katından olduğunda şüphe ve endişe içindedirler.'),
(1815, 1584, 11, 111, 'Hûd ', 'Mekki', 'Rabbin, onların işlerinin karşılığını elbette tamamen verecektir. O, şüphesiz, onların yaptıklarını bilir.'),
(1816, 1585, 11, 112, 'Hûd ', 'Mekki', 'Sen, beraberindeki tevbe edenlerle birlikte emrolunduğun gibi dosdoğru ol. Aşırı gitmeyin, doğrusu Allah yaptıklarınızı görür.'),
(1817, 1586, 11, 113, 'Hûd ', 'Mekki', 'Haksızlık yapanlara yönelmeyin, yoksa ateş size de dokunur. Sizin Allah\'tan başka dostunuz yoktur; sonra, yardım da göremezsiniz.'),
(1818, 1587, 11, 114, 'Hûd ', 'Mekki', 'Gündüzün iki ucunda ve gecenin gündüze yakın zamanlarında namaz kıl. Doğrusu iyilikler kötülükleri giderir. Bu, öğüt kabul edenlere bir öğüttür.'),
(1819, 1588, 11, 115, 'Hûd ', 'Mekki', 'Sabret, Allah iyi davrananların ecrini elbette zayi etmez.'),
(1820, 1589, 11, 116, 'Hûd ', 'Mekki', 'Sizden önceki nesillerin ileri gelenleri, yeryüzünde bozgunculuğa engel olmalı değil miydiler? Onlardan kurtardıklarımız pek azdır. Kendilerine verilen nimete karşı haksızlık edenlere uyanlar ise suçlu oldular.'),
(1821, 1590, 11, 117, 'Hûd ', 'Mekki', 'Rabbin, kasabaların halkı ıslah olmuşken, haksız yere onları yok etmez.'),
(1822, 1591, 11, 118, 'Hûd ', 'Mekki', 'Eğer Rabbin dileseydi insanları tek bir ümmet kılardı. Fakat, Rabbinin merhamet ettikleri bir yana, hala ayrılıktadırlar, esasen onları bunun için yaratmıştır. Rabbinin \"And olsun ki cehennemi hep insan ve cin ile dolduracağım\" sözü yerine gelmiştir.'),
(1823, 1592, 11, 119, 'Hûd ', 'Mekki', 'Eğer Rabbin dileseydi insanları tek bir ümmet kılardı. Fakat, Rabbinin merhamet ettikleri bir yana, hala ayrılıktadırlar, esasen onları bunun için yaratmıştır. Rabbinin \"And olsun ki cehennemi hep insan ve cin ile dolduracağım\" sözü yerine gelmiştir.'),
(1824, 1593, 11, 120, 'Hûd ', 'Mekki', 'Peygamberlerin başlarından geçenlerden, sana anlattığımız her şey, senin gönlünü pekiştirmemizi sağlar; sana bu belgelerle gerçek; inananlara da öğüt ve hatırlatma gelmiştir.'),
(1825, 1594, 11, 121, 'Hûd ', 'Mekki', 'İnanmayanlara: \"Durumunuzun gerektirdiğini yapın, doğrusu biz de yapıyoruz; bekleyin, biz de bekliyoruz\" de.'),
(1826, 1595, 11, 122, 'Hûd ', 'Mekki', 'İnanmayanlara: \"Durumunuzun gerektirdiğini yapın, doğrusu biz de yapıyoruz; bekleyin, biz de bekliyoruz\" de.'),
(1827, 1596, 11, 123, 'Hûd ', 'Mekki', 'Göklerin ve yerin gaybı Allah\'a aittir. Bütün işler O\'na döndürülür. Öyleyse O\'na kulluk et, O\'na güven. Rabbin, yaptıklarınızdan habersiz değildir.'),
(1828, 1597, 12, 1, 'Yûsuf ', 'Mekki', 'Elif, Lam, Ra. Bunlar, gerçeği açıklayan Kitap\'ın ayetleridir.'),
(1829, 1598, 12, 2, 'Yûsuf ', 'Mekki', 'Biz onu, anlayasınız diye, Arapça bir Kuran olarak indirdik.'),
(1830, 1599, 12, 3, 'Yûsuf ', 'Mekki', 'Biz bu Kuran\'ı vahyederek, sana en güzel kıssaları anlatıyoruz.. Oysa daha önce sen bunlardan habersizdin.'),
(1831, 1600, 12, 4, 'Yûsuf ', 'Mekki', 'Yusuf babasına: \"Babacığım! \"Rüyamda onbir yıldız, güneş ve ayın bana secde ettiklerini gördüm\" demişti.'),
(1832, 1601, 12, 5, 'Yûsuf ', 'Mekki', 'Babası şunları söyledi: \"Oğulcuğum! Rüyanı kardeşlerine anlatma, yoksa sana tuzak kurarlar; zira şeytan insanın apaçık düşmanıdır\".'),
(1833, 1602, 12, 6, 'Yûsuf ', 'Mekki', '\"Rabbin seni böylece rüyandaki gibi seçecek, sana rüyaları yorumlamayı öğretecek; daha önce, ataların İbrahim ve İshak\'a nimetlerini tamamladığı gibi, sana ve Yakub soyuna da tamamlayacaktır. Doğrusu Rabbin bilir, hakimdir.\"'),
(1834, 1603, 12, 7, 'Yûsuf ', 'Mekki', 'And olsun ki, Yusuf ve kardeşlerinin olayında, soranlara nice ibretler vardır.'),
(1835, 1604, 12, 8, 'Yûsuf ', 'Mekki', 'Kardeşleri demişlerdi ki: \"Yusuf ve özkardeşi babamıza bizden daha sevgilidir. Oysa biz bir cemaatiz. Babamız açık bir yanlışlık içindedir.\"'),
(1836, 1605, 12, 9, 'Yûsuf ', 'Mekki', '\"Yusuf\'u öldürün veya onu ıssız bir yere bırakıverin ki babanız size kalsın; ondan sonra da iyi kimseler olursunuz\"'),
(1837, 1606, 12, 10, 'Yûsuf ', 'Mekki', 'İçlerinden biri: \"Yusuf\'u öldürmeyin, onu bir kuyunun derinliklerine bırakın. Böyle yaparsanız yolculardan onu bulup alan olur\" dedi.'),
(1838, 1607, 12, 11, 'Yûsuf ', 'Mekki', 'Bunun üzerine \"Ey babamız! Yusuf\'un iyiliğini istediğimiz halde, onu niçin bize emniyet etmiyorsun? Yarın onu bizimle beraber gönder de gezsin oynasın, biz onu herhalde koruruz\" dediler.'),
(1839, 1608, 12, 12, 'Yûsuf ', 'Mekki', 'Bunun üzerine \"Ey babamız! Yusuf\'un iyiliğini istediğimiz halde, onu niçin bize emniyet etmiyorsun? Yarın onu bizimle beraber gönder de gezsin oynasın, biz onu herhalde koruruz\" dediler.'),
(1840, 1609, 12, 13, 'Yûsuf ', 'Mekki', 'Babaları, \"Onu götürmeniz beni üzüyor; siz farkına varmadan onu kurdun yemesinden korkarım\" dedi.'),
(1841, 1610, 12, 14, 'Yûsuf ', 'Mekki', '\"And olsun ki, biz kuvvetli bir toplulukken kurt onu yerse, biz aciz sayılırız\" dediler.'),
(1842, 1611, 12, 15, 'Yûsuf ', 'Mekki', 'Yusuf\'u oturup bir kuyunun derinliklerine bırakmayı kararlaştırdılar. Biz ona, kardeşlerinin bu işlerini kendileri farkına varmadan haber vereceksin, diye vahyettik.'),
(1843, 1612, 12, 16, 'Yûsuf ', 'Mekki', 'Akşam üstü ağlayarak babalarına geldiklerinde: \"Ey babamız! İnan olsun biz yarış yapıyorduk; Yusuf\'u eşyamızın yanına bırakmıştık; bir kurt onu yedi. Her ne kadar doğru söylüyorsak da sen bize inanmazsın\" dediler.'),
(1844, 1613, 12, 17, 'Yûsuf ', 'Mekki', 'Akşam üstü ağlayarak babalarına geldiklerinde: \"Ey babamız! İnan olsun biz yarış yapıyorduk; Yusuf\'u eşyamızın yanına bırakmıştık; bir kurt onu yedi. Her ne kadar doğru söylüyorsak da sen bize inanmazsın\" dediler.'),
(1845, 1614, 12, 18, 'Yûsuf ', 'Mekki', 'Üzerine başka bir kan bulaşmış olarak Yusuf\'un gömleğini de getirmişlerdi. Babaları: \"Sizi nefsiniz bir iş yapmaya sürükledi; artık bana güzelce sabır gerekir. Anlattıklarınıza ancak Allah\'tan yardım istenir\" dedi.'),
(1846, 1615, 12, 19, 'Yûsuf ', 'Mekki', 'Bir kervan geldi, sucularını gönderdiler; sucu kovasını kuyuya saldı, \"Müjde! İşte bir oğlan\" dedi. Yusuf\'u alıp onu ticari bir mal olarak sakladılar. Oysa Allah yaptıklarını bilir.'),
(1847, 1616, 12, 20, 'Yûsuf ', 'Mekki', 'Onu yanlarında alıkoymak istemedikleri için ucuz bir fiyata, birkaç dirheme sattılar.'),
(1848, 1617, 12, 21, 'Yûsuf ', 'Mekki', 'Mısır\'da onu satın alan kimse karısına: \"Ona güzel bak, belki bize faydası olur yahut ta onu evlat ediniriz\" dedi. Biz işte böylece Yusuf\'u o yere yerleştirdik; ona, rüyaların nasıl yorumlanacağını öğrettik. Allah, işinde hakimdir, fakat insanların çoğu bunu bilmezler.'),
(1849, 1618, 12, 22, 'Yûsuf ', 'Mekki', 'Erginlik çağına erince ona hikmet ve bilgi verdik. İyi davrananları böyle mükafatlandırırız.'),
(1850, 1619, 12, 23, 'Yûsuf ', 'Mekki', 'Evinde bulunduğu kadın onu kendine çağırdı, kapıları sıkı sıkı kapadı ve \"gelsene\" dedi. Yusuf: \"Günah işlemekten Allah\'a sığınırım, doğrusu senin kocan benim efendimdir; bana iyi baktı. Haksızlık yapanlar şüphesiz başarıya ulaşamazlar.\" dedi.'),
(1851, 1620, 12, 24, 'Yûsuf ', 'Mekki', 'And olsun ki kadın Yusuf\'a karşı istekli idi; Rabbin\'den bir işaret görmeseydi Yusuf da onu isteyecekti. İşte ondan kötülüğü ve fenalığı böylece engelledik. Doğrusu o bizim çok samimi kullarımızdandır.'),
(1852, 1621, 12, 25, 'Yûsuf ', 'Mekki', 'İkisi de kapıya koştu, kadın arkadan Yusuf\'un gömleğini yırttı; kapının önünde kocasına rastladılar. Kadın kocasına \"Ailene fenalık etmek isteyen bir kimsenin cezası ya hapis ya da can yakıcı bir azab olmalıdır\" dedi.'),
(1853, 1622, 12, 26, 'Yûsuf ', 'Mekki', 'Yusuf: \"Beni kendine o çağırdı\" dedi. Kadın tarafından bir şahit, \"Eğer gömleği önden yırtılmışsa kadın doğru söylemiş, erkek yalancılardandır; şayet gömleği arkadan yırtılmışsa kadın yalan söylemiştir, erkek doğrulardandır\" diye şahidlik etti.'),
(1854, 1623, 12, 27, 'Yûsuf ', 'Mekki', 'Yusuf: \"Beni kendine o çağırdı\" dedi. Kadın tarafından bir şahit, \"Eğer gömleği önden yırtılmışsa kadın doğru söylemiş, erkek yalancılardandır; şayet gömleği arkadan yırtılmışsa kadın yalan söylemiştir, erkek doğrulardandır\" diye şahidlik etti.'),
(1855, 1624, 12, 28, 'Yûsuf ', 'Mekki', 'Kocası gömleğin arkadan yırtılmış olduğunu görünce, karısına hitaben \"Doğrusu bu sizin hilenizdir, siz kadınların fendi büyüktür\" dedi. Yusuf\'a dönerek: \"Yusuf! Sen bundan kimseye bahsetme\"; kadına dönerek: \"Sen de günahının bağışlanmasını dile, çünkü suçlulardansın\" dedi.'),
(1856, 1625, 12, 29, 'Yûsuf ', 'Mekki', 'Kocası gömleğin arkadan yırtılmış olduğunu görünce, karısına hitaben \"Doğrusu bu sizin hilenizdir, siz kadınların fendi büyüktür\" dedi. Yusuf\'a dönerek: \"Yusuf! Sen bundan kimseye bahsetme\"; kadına dönerek: \"Sen de günahının bağışlanmasını dile, çünkü suçlulardansın\" dedi.'),
(1857, 1626, 12, 30, 'Yûsuf ', 'Mekki', 'Şehirde bir takım kadınlar: \"Vezirin karısı kölesinin olmak istiyormuş; sevgisi bağrını yakmış; doğrusu onun besbelli sapıtmış olduğunu görüyoruz.\" dediler.'),
(1858, 1627, 12, 31, 'Yûsuf ', 'Mekki', 'Kadınların kendisini yermesini işitince onları davet etti; koltuklar hazırladı; geldiklerinde her birine birer bıçak verdi. Yusuf\'a: \"Yanlarına çık\" dedi. Kadınlar Yusuf\'u görünce şaşıp ellerini kestiler ve \"Allah\'ı tenzih ederiz ama, bu insan değil ancak çok güzel bir melektir\" dediler.'),
(1859, 1628, 12, 32, 'Yûsuf ', 'Mekki', 'Vezirin karısı: \"İşte sözünü edip beni yerdiğiniz budur. And olsun ki onun olmak istedim, fakat o iffetinden dolayı çekindi. Emrimi yine yapmazsa, and olsun ki hapse tıkılacak ve kahre uğrayanlardan olacak.\"'),
(1860, 1629, 12, 33, 'Yûsuf ', 'Mekki', 'Yusuf: \"Rabbim! Hapis benim için, bunların istediklerini yapmaktan daha iyidir. Eğer tuzaklarını benden uzaklaştırmazsan onlara meyleder ve bilmeyenlerden olurum.\" dedi.'),
(1861, 1630, 12, 34, 'Yûsuf ', 'Mekki', 'Rabbi onun duasını kabul etti ve kadınların tuzağına engel oldu. Zira O, işitir ve bilir.'),
(1862, 1631, 12, 35, 'Yûsuf ', 'Mekki', 'Sonra, kadının ailesi delilleri Yusuf\'un lehinde gördüğü halde, onu bir süre için hapsetmeyi uygun buldu.'),
(1863, 1632, 12, 36, 'Yûsuf ', 'Mekki', 'Hapse, onunla beraber, iki genç daha girdi. Biri, \"Rüyamda şaraplık üzüm sıktığımı gördüm\" dedi; diğeri \"Başımın üzerinde, kuşların yediği bir ekmek taşıdığımı gördüm\" dedi. \"Bize bunu yorumla; senin iyi bir kimse olduğunu görüyoruz\"'),
(1864, 1633, 12, 37, 'Yûsuf ', 'Mekki', 'Yusuf: \"Rabbimin bana öğrettiği bilgi ile, daha yiyeceğiniz yemek gelmeden size onu yorumlarım. Doğrusu ben, Allah\'a inanmayan ve ahireti inkar eden, bir milletin dinini bırakmışımdır.'),
(1865, 1634, 12, 38, 'Yûsuf ', 'Mekki', 'Atalarım İbrahim, İshak ve Yakub\'un dinine uydum. Allah\'a herhangi bir ortak koşmak bize yaraşmaz; bu, Allah\'ın bize ve insanlara olan lütfudur; fakat insanların çoğu şükretmez\" dedi.'),
(1866, 1635, 12, 39, 'Yûsuf ', 'Mekki', '\"Ey mahpus arkadaşlarım! Ayrı ayrı bir sürü uydurma rabler mi daha iyidir, yoksa her şeyden üstün tek Allah mı?\"'),
(1867, 1636, 12, 40, 'Yûsuf ', 'Mekki', '\"Allah\'ı bırakıp taptığınız, sizin ve babalarınızın adlandırdığı putlardan başka bir şey değildir. Allah onların doğru olduğuna dair bir delil indirmemiştir. Hüküm vermek ancak Allah\'a aittir; kendisinden başkasına değil, O\'na tapmanızı emretmiştir. Bu, dosdoğru dindir, fakat insanların çoğu bilmezler\".'),
(1868, 1637, 12, 41, 'Yûsuf ', 'Mekki', '\"Ey mahpus arkadaşlarım! Biriniz efendinize şarap sunacak, diğeri asılacak ve kuşlar başından yiyecektir. Sorduğunuz iş işte böylece kesinleşmiştir.\"'),
(1869, 1638, 12, 42, 'Yûsuf ', 'Mekki', 'İkisinden, kurtulacağını sandığı kimseye Yusuf: \"Efendinin yanında beni an\" dedi. Ama şeytan efendisine onu hatırlatmayı unutturdu ve Yusuf bu yüzden daha birkaç yıl hapiste kaldı.'),
(1870, 1639, 12, 43, 'Yûsuf ', 'Mekki', 'Hükümdar: \"Ben, yedi semiz ineği yedi zayıf ineğin yediğini; yedi yeşil başak ve bir o kadar da kurumuş başak görüyorum. Ey ileri gelenler! Eğer rüya yormasını biliyorsanız rüyamı söyleyiniz.\" dedi.'),
(1871, 1640, 12, 44, 'Yûsuf ', 'Mekki', 'Etrafındakiler: \"Bir takım karışık rüyalar; biz böyle rüyaların yorumunu bilmeyiz\" dediler.'),
(1872, 1641, 12, 45, 'Yûsuf ', 'Mekki', 'Hapisteki iki kişiden kurtulmuş olanı, nice zaman sonra Yusuf\'u hatırladı ve: \"Ben size bunu yorumlayacağım, hele beni gönderin\" dedi.'),
(1873, 1642, 12, 46, 'Yûsuf ', 'Mekki', 'Hapishaneye varıp: \"Ey doğru sözlü Yusuf! Rüyada görülen yedi semiz ineği yedi zayıf ineğin yemesi; yedi yeşil başak ve bir o kadar kuru başak nedir? Bize yorumla, ben de insanlara ulaştırayım da bilsinler\" dedi.'),
(1874, 1643, 12, 47, 'Yûsuf ', 'Mekki', 'Yusuf: \"Devamlı yedi sene ekin ekip, biçtiğiniz ekinin yediğinizden artanını başağında bırakın.\"'),
(1875, 1644, 12, 48, 'Yûsuf ', 'Mekki', '\"Sonra bunun ardından yedi kurak yıl gelir, bütün biriktirdiğinizi yer, yalnız az bir miktar saklarsınız.\"'),
(1876, 1645, 12, 49, 'Yûsuf ', 'Mekki', '\"Sonra, halkın yağmur göreceği bir yıl gelir, o zaman sıkıp sağarlar\" dedi.'),
(1877, 1646, 12, 50, 'Yûsuf ', 'Mekki', 'Hükümdar: \"Onu bana getirin\" dedi. Yusuf\'a elçi gelince, \"Efendine dön, kadınlar niçin ellerini kesmişlerdi bir sor; doğrusu Rabbim onların hilesini bilir\" dedi.'),
(1878, 1647, 12, 51, 'Yûsuf ', 'Mekki', 'Hükümdar kadınlara: \"Yusuf\'un olmak istediğiniz zaman durumunuz neydi?\" dedi. Kadınlar, \"Haşa! Onun bir fenalığını görmedik\" dediler. Vezirin karısı: \"Şimdi gerçek ortaya çıktı; onun olmak isteyen bendim; doğrusu Yusuf doğrulardandır\" dedi.'),
(1879, 1648, 12, 52, 'Yûsuf ', 'Mekki', 'Yusuf, \"Maksadım, vezire, gıyabında ihanet etmediğimi, hainlerin tuzaklarını Allah\'ın başarıya erdirmediğini bilmesini sağlamaktı\" dedi.'),
(1880, 1649, 12, 53, 'Yûsuf ', 'Mekki', '\"Ben nefsimi temize çıkarmam; çünkü nefs, Rabbimin merhameti olmadıkça, kötülüğü emreder. Doğrusu Rabbim bağışlayandır, merhamet edendir.\"'),
(1881, 1650, 12, 54, 'Yûsuf ', 'Mekki', 'Hükümdar: \"Onu bana getirin, yanıma alayım\" dedi. Onunla konuşunca: \"Bugün senin yanımızda önemli bir yerin ve güvenilir bir durumun vardır.\" dedi.'),
(1882, 1651, 12, 55, 'Yûsuf ', 'Mekki', 'Yusuf: \"Beni memleketin hazinelerine memur et, çünkü ben korumasını ve yönetmesini bilirim\" dedi.'),
(1883, 1652, 12, 56, 'Yûsuf ', 'Mekki', 'Yusuf\'u böylece o memlekete yerleştirdik; istediği yerlerde oturabilirdi. Rahmetimizi tıpkı bu misalde olduğu gibi istediğimize veririz; iyi davrananların ecrini zayi etmeyiz.'),
(1884, 1653, 12, 57, 'Yûsuf ', 'Mekki', 'Ama ahiret ecri, inananlar ve Allah\'a karşı gelmekten sakınanlar için daha iyidir.'),
(1885, 1654, 12, 58, 'Yûsuf ', 'Mekki', 'Yusuf\'un kardeşleri gelip yanına girdiler. Kendisini tanımadıkları halde o onları tanıdı.'),
(1886, 1655, 12, 59, 'Yûsuf ', 'Mekki', 'Onların yüklerini hazırlatınca şöyle dedi: \"Baba bir kardeşinizi bana getirin. Sizlere ölçüyü bol tuttuğumu ve benim misafir konuklayanların en iyisi olduğumu görmüyor musunuz?\"'),
(1887, 1656, 12, 60, 'Yûsuf ', 'Mekki', '\"Eğer onu bana getirmezseniz bundan böyle benden bir ölçek bile alamazsınız ve bana artık yaklaşmayın da.\"'),
(1888, 1657, 12, 61, 'Yûsuf ', 'Mekki', 'Kardeşleri: \"Babasını ikna etmeye çalışacağız ve her halde bunu yaparız\" dediler.'),
(1889, 1658, 12, 62, 'Yûsuf ', 'Mekki', 'Yusuf adamlarına: \"Karşılık olarak getirdiklerini de yüklerine koyun. Belki ailelerine varınca, onu anlarlar da bir daha dönerler\" dedi.'),
(1890, 1659, 12, 63, 'Yûsuf ', 'Mekki', 'Babalarına döndüklerinde, \"Ey babamız! Bize yiyecek yasak edildi, kardeşimizi bizimle beraber gönder de yiyecek alalım. Onu elbette koruruz\" dediler.'),
(1891, 1660, 12, 64, 'Yûsuf ', 'Mekki', '\"Daha önce kardeşini size emanet ettiğim gibi, şimdi onu emanet eder miyim? Ama Allah en iyi koruyandır, O merhametlilerin merhametlisidir\" dedi.'),
(1892, 1661, 12, 65, 'Yûsuf ', 'Mekki', 'Yüklerini açınca karşılık olarak götürdükleri mallarının kendilerine iade edilmiş olduğunu gördüler. \"Ey babamız! Daha ne isteriz; işte mallarımız da bize iade edilmiş; ailemize onunla yine yiyecek getirir, kardeşimizi de korur ve bir deve yükü de artırmış oluruz; esasen bu az bir şeydir\" dediler.'),
(1893, 1662, 12, 66, 'Yûsuf ', 'Mekki', 'Babaları: \"Hepiniz helak olmadıkça onu bana geri getireceğinize dair Allah\'a karşı sağlam bir söz vermezseniz, sizinle göndermeyeceğim\" dedi. Söz verdiklerinde: \"Sözümüze Allah vekildir\" dedi.'),
(1894, 1663, 12, 67, 'Yûsuf ', 'Mekki', 'Babaları: \"Oğullarım! Tek bir kapıdan değil, ayrı ayrı kapılardan girin. Ama Allah katında size bir faydam olmaz, hüküm ancak Allah\'ındır, O\'na güvendim, güvenenler de O\'na güvensinler\" dedi.'),
(1895, 1664, 12, 68, 'Yûsuf ', 'Mekki', 'Babalarının emrettiği gibi girdiler. Esasen bu, Allah katında onlara bir fayda sağlamazdı, ancak Yakub içindeki arzuyu ortaya koymuş oldu. O, şüphesiz kendisine öğrettiğimizi bilir fakat insanların çoğu bilmezler.'),
(1896, 1665, 12, 69, 'Yûsuf ', 'Mekki', 'Yusuf\'un yanına girdiklerinde, kardeşini bağrına bastı ve: \"Ben senin kardeşinim, onların yaptıklarına artık üzülme\" dedi.'),
(1897, 1666, 12, 70, 'Yûsuf ', 'Mekki', 'Yusuf onların yüklerini yükletirken, bir su kabını kardeşinin yüküne koydurdu. Sonra bir münadi şöyle bağırdı: \"Ey kervancılar, siz hırsızsınız!\"'),
(1898, 1667, 12, 71, 'Yûsuf ', 'Mekki', 'Geri dönerek, \"Ne kaybettiniz?\" dediler.'),
(1899, 1668, 12, 72, 'Yûsuf ', 'Mekki', '\"Hükümdarın su kabını kaybettik, onu getirene bir deve yükü mükafat verilecek, buna ben kefil oluyorum\" dediler.'),
(1900, 1669, 12, 73, 'Yûsuf ', 'Mekki', '\"Allah\'a yemin ederiz ki memleketi ifsat etmeğe gelmediğimizi ve hırsız da olmadığımızı biliyorsunuz\" dediler.'),
(1901, 1670, 12, 74, 'Yûsuf ', 'Mekki', '\"Yalancı iseniz, hırsızlığın cezası nedir?\" dediler.'),
(1902, 1671, 12, 75, 'Yûsuf ', 'Mekki', '\"Cezası, kimin yükünde bulunursa, ceza olarak ona el konulur; biz zalimleri böyle cezalandırırız\" dediler.'),
(1903, 1672, 12, 76, 'Yûsuf ', 'Mekki', 'Yusuf kardeşinin yükünden önce onlarınkini aramaya başladı; sonra kardeşinin yükünden su kabını çıkardı. İşte biz Yusuf\'a böyle bir plan kullanmasını vahyettik. Çünkü hükümdarın kanunlarına göre kardeşini alıkoyamazdı, meğer ki Allah dileye. Dilediğimizi derecelerle yükseltiriz. Her ilim sahibinden üstün bir bilen bulunur.'),
(1904, 1673, 12, 77, 'Yûsuf ', 'Mekki', '\"Çalmışsa, daha önce kardeşi de çalmıştı\" dediler. Yusuf bunu içinde sakladı, onlara açmadı. İçinden, \"Durumunuz pek kötüdür; anlattığınızı Allah daha iyi bilir\" dedi.'),
(1905, 1674, 12, 78, 'Yûsuf ', 'Mekki', 'Kardeşleri: \"Ey Vezir! Onun yaşlanmış, kocamış bir babası vardır. Bizden birini onun yerine al. Doğrusu biz senin iyi davrananlardan olduğunu görüyoruz\" dediler.'),
(1906, 1675, 12, 79, 'Yûsuf ', 'Mekki', '\"Maazallah! Biz, malımızı kimde bulmuşsak ancak onu alıkoruz, yoksa haksızlık etmiş oluruz\" dedi.'),
(1907, 1676, 12, 80, 'Yûsuf ', 'Mekki', 'Ümidsizliğe düşünce, konuşmak üzere bir kenara çekildiler. Büyükleri şöyle dedi: \"Babanızın Allah\'a karşı sizden bir söz aldığını, daha önce Yusuf meselesinde de ileri gittiğinizi bilmiyor musunuz? Artık babam bana izin verene veya Allah hakkımda hüküm verene kadar ki O, hükmedenlerin en iyisidir bu yerden ayrılmayacağım. Siz dönün, babanıza gidin ve deyin ki: Ey Babamız! Senin oğlun hırsızlık yaptı, bu bildiğimizden başka bir şey görmedik; görülmeyeni de bilmeyiz; bulunduğumuz kasabanın halkına ve beraberinde olduğumuz kervana da sorabilirsin; biz şüphesiz doğru söylüyoruz.\"'),
(1908, 1677, 12, 81, 'Yûsuf ', 'Mekki', 'Yakup: \"Sizi nefsiniz bir iş yapmaya sürükledi, artık bana güzelce sabır gerekir; belki Allah hepsini birden bana getirecektir, çünkü O bilendir, hakimdir\" dedi.'),
(1909, 1678, 12, 82, 'Yûsuf ', 'Mekki', 'Onlara sırt çevirdi, \"Vah, Yusuf\'a yazık oldu!\" dedi ve üzüntüden gözlerine ak düştü. Artık acısını içinde saklıyordu.'),
(1910, 1679, 12, 83, 'Yûsuf ', 'Mekki', 'Onlara sırt çevirdi, \"Vah, Yusuf\'a yazık oldu!\" dedi ve üzüntüden gözlerine ak düştü. Artık acısını içinde saklıyordu.'),
(1911, 1680, 12, 84, 'Yûsuf ', 'Mekki', 'Onlara sırt çevirdi, \"Vah, Yusuf\'a yazık oldu!\" dedi ve üzüntüden gözlerine ak düştü. Artık acısını içinde saklıyordu.'),
(1912, 1681, 12, 85, 'Yûsuf ', 'Mekki', '\"Allah\'a yemin ederiz ki, Yusuf\'u anıp durman seni bitkin düşürecek veya helak olacaksın\" dediler.'),
(1913, 1682, 12, 86, 'Yûsuf ', 'Mekki', 'Yakup: \"Ben üzüntü ve tasamı yalnız Allah\'a açarım. Allah katından, sizin bilmediklerinizi bilirim\" dedi.'),
(1914, 1683, 12, 87, 'Yûsuf ', 'Mekki', '\"Ey Oğullarım! Gidin, Yusuf\'u ve kardeşini arayın. Allah\'ın rahmetinden ümidinizi kesmeyin; doğrusu kafirlerden başkası Allah\'ın rahmetinden ümidini kesmez.\"'),
(1915, 1684, 12, 88, 'Yûsuf ', 'Mekki', 'Kardeşleri vezirin yanına vardıklarında: \"Ey Vezir! Biz ve çoluk çocuğumuz darlığa uğradık; pek değersiz bir malla geldik; ölçeği bize tam yap ve sadaka ver; Allah sadaka verenleri şüphesiz mükafatlandırır\" dediler.'),
(1916, 1685, 12, 89, 'Yûsuf ', 'Mekki', '\"Siz, Yusuf ve kardeşine bilmeden neler yaptığınızın farkında mısınız?\" dedi.'),
(1917, 1686, 12, 90, 'Yûsuf ', 'Mekki', '\"Yoksa sen Yusuf musun?\" dediler. \"Ben Yusuf\'um, bu da kardeşim. Allah bize iyilikte bulundu; doğrusu kim kötülükten sakınır ve sabrederse bilsin ki Allah iyi davrananların ecrini katiyen zayi etmez\" dedi..'),
(1918, 1687, 12, 91, 'Yûsuf ', 'Mekki', '\"Allah\'a yemin ederiz ki, Allah seni bizden üstün tutmuştur; doğrusu biz suç işlemiştik\" dediler.'),
(1919, 1688, 12, 92, 'Yûsuf ', 'Mekki', 'Yusuf: \"Bugün azarlanacak değilsiniz, Allah sizi bağışlar. O, merhametlilerin merhametlisidir. Bu gömleğimi götürün, babamın yüzüne sürün, görmeğe başlar; bütün çoluk çocuğunuzla bana gelin\" dedi.'),
(1920, 1689, 12, 93, 'Yûsuf ', 'Mekki', 'Yusuf: \"Bugün azarlanacak değilsiniz, Allah sizi bağışlar. O, merhametlilerin merhametlisidir. Bu gömleğimi götürün, babamın yüzüne sürün, görmeğe başlar; bütün çoluk çocuğunuzla bana gelin\" dedi.'),
(1921, 1690, 12, 94, 'Yûsuf ', 'Mekki', 'Kervan, memleketlerine dönmek üzere ayrıldığında, babaları: \"Doğrusu ben Yusuf\'un kokusunu duyuyorum; ne olur bana bunak demeyin\" dedi.'),
(1922, 1691, 12, 95, 'Yûsuf ', 'Mekki', 'Çevresindekiler: \"Allah\'a yemin ederiz ki sen, hala eski şaşkınlığındasın\" dediler.'),
(1923, 1692, 12, 96, 'Yûsuf ', 'Mekki', 'Müjdeci gelip, gömleği Yakub\'un yüzüne bırakınca, hemen gözleri açıldı. Bunun üzerine Yakub \"Ben size, Allah katından sizin bilmediğinizi biliyorum dememiş miydim?\" dedi.'),
(1924, 1693, 12, 97, 'Yûsuf ', 'Mekki', 'Oğulları: \"Ey Babamız! Suçlarımızın bağışlanmasını dile, bizler hiç şüphesiz suçluyuz\" dediler.'),
(1925, 1694, 12, 98, 'Yûsuf ', 'Mekki', 'Yakub: \"Rabbim\'den bağışlanmanızı dileyeceğim; O şüphesiz bağışlar ve merhamet eder\" dedi.'),
(1926, 1695, 12, 99, 'Yûsuf ', 'Mekki', 'Yusuf\'un yanına geldiklerinde, o, anasını babasını bağrına bastı, \"Allah\'ın dileğince, güven içinde Mısır\'da yerleşin\" dedi.'),
(1927, 1696, 12, 100, 'Yûsuf ', 'Mekki', 'Ana babasını tahtın üzerine oturttu, hepsi onun önünde (Allah\'a secde edip) eğildiler. O zaman Yusuf: \"Babacığım! İşte bu, vaktiyle gördüğüm rüyanın çıkışıdır; Rabbim onu gerçekleştirdi. Şeytan, benimle kardeşlerimin arasını bozduktan sonra, beni hapisten çıkaran, sizi çölden getiren Rabbim bana pek çok iyilikte bulundu. Doğrusu Rabbim dilediğine lütufkardır, O şüphesiz bilendir, Hakim\'dir\" dedi.'),
(1928, 1697, 12, 101, 'Yûsuf ', 'Mekki', '\"Rabbim! Bana hükümranlık verdin, rüyaların yorumunu öğrettin. Ey göklerin ve yerin yaradanı! Dünya ve ahirette işlerimi yoluna koyan sensin; benim canımı müslüman olarak al ve beni iyilere kat.\"'),
(1929, 1698, 12, 102, 'Yûsuf ', 'Mekki', 'Sana böylece vahyettiklerimiz, gaybe ait haberlerdir. Onlar elbirliği edip düzen kurdukları zaman yanlarında değildin; sen ne kadar yürekten istersen iste, insanların çoğu inanmazlar.'),
(1930, 1699, 12, 103, 'Yûsuf ', 'Mekki', 'Sana böylece vahyettiklerimiz, gaybe ait haberlerdir. Onlar elbirliği edip düzen kurdukları zaman yanlarında değildin; sen ne kadar yürekten istersen iste, insanların çoğu inanmazlar.'),
(1931, 1700, 12, 104, 'Yûsuf ', 'Mekki', 'Oysa sen buna karşılık onlardan bir ücret de istemiyorsun. Kuran, alemler için sadece bir öğüttür.'),
(1932, 1701, 12, 105, 'Yûsuf ', 'Mekki', 'Göklerde ve yerde nice belgeler vardır ki, yanlarından yüzlerini çevirerek geçerler.'),
(1933, 1702, 12, 106, 'Yûsuf ', 'Mekki', 'Onların çoğu, ortak koşmadan Allah\'a inanmazlar.'),
(1934, 1703, 12, 107, 'Yûsuf ', 'Mekki', 'Allah tarafından, onları kuşatacak bir azaba uğramalarından veya farkına varmadan, kıyamet saatinin ansızın gelmesinden güvende midirler?'),
(1935, 1704, 12, 108, 'Yûsuf ', 'Mekki', 'De ki: \"Benim yolum budur; ben ve bana uyanlar bilerek insanları Allah\'a çağırırız. Allah\'ı noksan sıfatlardan tenzih ederim. Ben asla Allah\'a eş koşanlardan değilim.\"'),
(1936, 1705, 12, 109, 'Yûsuf ', 'Mekki', 'Senden önce kasabalar halkından şüphesiz, kendilerine vahyettiğimiz bir takım insanlar gönderdik. Yeryüzünde dolaşmıyorlar mı ki, kendilerinden önce geçenlerin sonlarının ne olduğunu görsünler? Ahiret yurdu Allah\'a karşı gelmekten sakınanlar için hayırlıdır. Akletmez misiniz?'),
(1937, 1706, 12, 110, 'Yûsuf ', 'Mekki', 'Öyle ki, peygamberler ümitsizliğe düşüp, yalanlandıklarını sandıkları bir sırada onlara yardımımız gelmiştir. Böylece, istediğimizi kurtarırız. Azabımız suçlu milletten geri çevrilemeyecektir.'),
(1938, 1707, 12, 111, 'Yûsuf ', 'Mekki', 'And olsun ki, peygamberlerin kıssalarında, aklı olanlar için ibretler vardır. Kuran uydurulabilen bir söz değildir. Fakat kendinden önceki Kitapları tasdik eden, inanan millete her şeyi açıklayan, doğru yolu gösteren bir rehber ve rahmettir.'),
(1939, 1708, 13, 1, 'Ra’d ', 'Mekki', 'Elif, Lam, Mim, Ra. Bunlar Kitap\'ın ayetleridir. Sana Rabbinden indirilen Kitap haktır; fakat insanların çoğu inanmazlar.'),
(1940, 1709, 13, 2, 'Ra’d ', 'Mekki', 'Gökleri, gördüğünüz gibi, direksiz yükselten, sonra arşa hükmeden, her biri belli bir süreye kadar hareket edecek olan Güneş ve Ay\'ı buyruğu altına alan, işleri yürüten, ayetleri uzun uzun açıklayan Allah\'tır; ola ki Rabbinize kavuşacağınıza kesin olarak inanırsınız.'),
(1941, 1710, 13, 3, 'Ra’d ', 'Mekki', 'Yeri düzleyen, orada dağlar, nehirler var eden, her türlü üründen çift çift yetiştiren, gündüzü geceyle bürüyen de O\'dur. Doğrusu bunlarda, düşünen kimseler için ibretler vardır.'),
(1942, 1711, 13, 4, 'Ra’d ', 'Mekki', 'Yeryüzünde, hepsi de aynı su ile sulanan, birbirine komşu toprak parçaları, tek ve çok köklü üzüm bağları, ekinler, hurma ağaçları vardır. Fakat onları şekil ve lezzetçe birbirinden farklı kılmışızdır. Düşünen kimseler için bunda ibretler vardır.'),
(1943, 1712, 13, 5, 'Ra’d ', 'Mekki', 'Şaşacaksan, onların: \"Biz toprak olunca mı yeniden yaratılacağız?\" demelerine şaşmak gerekir. İşte onlar Rablerini inkar edenlerdir. İşte onlar boyunlarına demir halkalar vurulanlardır. İşte onlar cehennemliklerdir, orada temelli kalacaklardır.'),
(1944, 1713, 13, 6, 'Ra’d ', 'Mekki', 'Puta tapanlar senden, iyilikten önce kötülük isterler, oysa onlardan önce nice ibret alınacak cezalar verilmiştir. Doğrusu Rabbinin, insanların zulümlerine rağmen onlara mağfireti vardır. Rabbinin cezalandırması çetindir.'),
(1945, 1714, 13, 7, 'Ra’d ', 'Mekki', 'İnkar edenler: \"Rabbinden ona bir mucize indirilmeli değil miydi?\" derler. Sen ancak bir uyarıcısın. Her milletin bir yol göstereni vardır.'),
(1946, 1715, 13, 8, 'Ra’d ', 'Mekki', 'Allah her dişinin rahminde taşıdığını, rahimlerin düşürdüğünü ve alıkoyduğunu bilir. O\'nun katında her şey bir ölçüye göredir.'),
(1947, 1716, 13, 9, 'Ra’d ', 'Mekki', 'Görüleni de görülmeyeni de bilen, yücelerin yücesi büyük Allah\'a göre, aranızdan sözü gizleyen ile, açığa vuran ve geceye bürünerek gizlenip gündüzün ortaya çıkan arasında fark yoktur.'),
(1948, 1717, 13, 10, 'Ra’d ', 'Mekki', 'Görüleni de görülmeyeni de bilen, yücelerin yücesi büyük Allah\'a göre, aranızdan sözü gizleyen ile, açığa vuran ve geceye bürünerek gizlenip gündüzün ortaya çıkan arasında fark yoktur.'),
(1949, 1718, 13, 11, 'Ra’d ', 'Mekki', 'Ardında ve önünde insanoğlunu takip edenler vardır; Allah\'ın emriyle onu gözetirler. Bir millet kendini bozmadıkça Allah onların durumunu değiştirmez. Allah bir milletin fenalığını dileyince artık onun önüne geçilmez. Onlar için Allah\'tan başka hamide bulunmaz.'),
(1950, 1719, 13, 12, 'Ra’d ', 'Mekki', 'Korku ve ümide düşürmek için size şimşeği gösteren, yağmurla yüklü bulutları meydana getiren O\'dur.'),
(1951, 1720, 13, 13, 'Ra’d ', 'Mekki', 'O\'nu, gök gürlemesi hamd ile, melekler de korkularından tesbih ederler. Onlar pek kuvvetli olan Allah hakkında çekişirken, O, yıldırımları gönderir de onlarla dilediğini çarpar.'),
(1952, 1721, 13, 14, 'Ra’d ', 'Mekki', 'Gerçek dua ve ibadet ancak O\'nadır. O\'ndan başka çağırdıkları putlar kendilerine hiçbir cevap vermezler. Durumları, suyun ağzına gelmesi için avuçlarını ona açmış bekleyen adamın durumu gibidir. Hiçbir zaman suya kavuşamaz. İşte kafirlerin yalvarışıda böyle, boşunadır.'),
(1953, 1722, 13, 15, 'Ra’d ', 'Mekki', 'Yerde ve göklerdeki kimseler de, gölgeleri de, sabah akşam, ister istemez Allah\'a secde ederler.'),
(1954, 1723, 13, 16, 'Ra’d ', 'Mekki', 'De ki: \"Göklerin ve yerin Rabbi kimdir?\", \"Allah\'tır\" de. \"Onu bırakıp, kendilerine bir fayda ve zararı olmayan dostlar mı edindiniz?\" de. \"Kör ile gören bir olur mu? Veya karanlıkla aydınlık bir midir?\" de. Yoksa Allah\'a, Allah gibi yaratması olan ortaklar buldular da, yaratmaları birbirine mi benzettiler? De ki: \"Her şeyi yaratan Allah\'tır. O, her şeye üstün gelen tek Tanrı\'dır.\"'),
(1955, 1724, 13, 17, 'Ra’d ', 'Mekki', 'Allah gökten su indirir, dereler onunla dolar taşar. Sel, üste çıkan köpüğü alır götürür. Süslenmek veya faydalanmak için ateşte erittiklerinizin üzerinde de buna benzer bir köpük vardır. Allah, hak ve batıl için şöyle misal verir: Köpük uçup gider, insanlara fayda veren ise yerde kalır. Allah bunun gibi daha nice misaller verir.'),
(1956, 1725, 13, 18, 'Ra’d ', 'Mekki', 'Rablerinin çağrısına gelenlere en güzel karşılık vardır. O\'nun çağrısına uymayanlar ise, yeryüzünde olan her şey ve daha bir katı onların olsa, kurtulmak için fidye verirlerdi. İşte hesapları kötü olanlar bunlardır. Varacakları yer cehennemdir; ne kötü konaktır!'),
(1957, 1726, 13, 19, 'Ra’d ', 'Mekki', 'Sana Rabbinden indirilenin gerçek olduğunu bilen kimse, onu bilmeyen köre benzer mi? Ancak akıl sahipleri ibret alırlar.'),
(1958, 1727, 13, 20, 'Ra’d ', 'Mekki', 'Onlar, Allah\'ın ahdini yerine getirirler, anlaşmayı bozmazlar.'),
(1959, 1728, 13, 21, 'Ra’d ', 'Mekki', 'Onlar, Allah\'ın birleştirilmesini emrettiği şeyi birleştirirler, Rablerinden korkarlar; kötü hesaptan ürkerler.'),
(1960, 1729, 13, 22, 'Ra’d ', 'Mekki', 'Onlar, Rablerinin rızasını dileyerek sabrederler, namazı kılarlar; kendilerine verdiğimiz rızıktan, gizlice ve açıkça sarfederler; iyilik yaparak kötülüğü ortadan kaldırırlar; işte onlara bu dünyanın iyi sonucu, girecekleri Adn cennetleri vardır; babalarının, eşlerinin, çocuklarının iyi olanları da oraya girerler. Melekler her kapıdan yanlarına girip: \"Sabretmenize karşılık size selam olsun; burası dünyanın ne güzel bir sonucudur!\" derler.'),
(1961, 1730, 13, 23, 'Ra’d ', 'Mekki', 'Onlar, Rablerinin rızasını dileyerek sabrederler, namazı kılarlar; kendilerine verdiğimiz rızıktan, gizlice ve açıkça sarfederler; iyilik yaparak kötülüğü ortadan kaldırırlar; işte onlara bu dünyanın iyi sonucu, girecekleri Adn cennetleri vardır; babalarının, eşlerinin, çocuklarının iyi olanları da oraya girerler. Melekler her kapıdan yanlarına girip: \"Sabretmenize karşılık size selam olsun; burası dünyanın ne güzel bir sonucudur!\" derler.'),
(1962, 1731, 13, 24, 'Ra’d ', 'Mekki', 'Onlar, Rablerinin rızasını dileyerek sabrederler, namazı kılarlar; kendilerine verdiğimiz rızıktan, gizlice ve açıkça sarfederler; iyilik yaparak kötülüğü ortadan kaldırırlar; işte onlara bu dünyanın iyi sonucu, girecekleri Adn cennetleri vardır; babalarının, eşlerinin, çocuklarının iyi olanları da oraya girerler. Melekler her kapıdan yanlarına girip: \"Sabretmenize karşılık size selam olsun; burası dünyanın ne güzel bir sonucudur!\" derler.'),
(1963, 1732, 13, 25, 'Ra’d ', 'Mekki', 'Sağlam söz verdikten sonra Allah\'ın ahdini bozanlar ve Allah\'ın birleştirilmesini emrettiğini ayıranlar ve yeryüzünde bozgunculuk yapanlar, işte lanet onlara ve kötü yurt, cehennem, onlaradır.'),
(1964, 1733, 13, 26, 'Ra’d ', 'Mekki', 'Allah dilediği kimsenin rızkını genişletir ve bir ölçüye göre verir. Dünya hayatıyla övünenler bilsinler ki dünyadaki hayat ahiret yanında sadece bir geçimlikten ibarettir.'),
(1965, 1734, 13, 27, 'Ra’d ', 'Mekki', 'İnkar edenler: \"Rabbinden ona bir mucize indirilmeli değil miydi?\" derler. De ki: \"Doğrusu Allah dileyeni saptırır ve Kendisine yöneleni doğru yola eriştirir.\"'),
(1966, 1735, 13, 28, 'Ra’d ', 'Mekki', 'Onlar inanmışlar, kalbleri Allah\'ı anmakla huzura kavuşmuştur. Dikkat edin, kalbler ancak Allah\'ı anmakla huzura kavuşur.'),
(1967, 1736, 13, 29, 'Ra’d ', 'Mekki', 'İnanan ve yararlı iş işleyen kimseler için hoş bir hayat ve dönülecek güzel bir yer vardır.'),
(1968, 1737, 13, 30, 'Ra’d ', 'Mekki', 'Sana vahyettiğimizi okuman için, seni de onlardan önce nice ümmetlerin gelip geçtiği bir ümmete gönderdik; o ümmet merhametli olan Allah\'ı inkar eder; de ki: \"O benim Rabbim\'dir, O\'ndan başka Tanrı yoktur, yalnız O\'na güvenirim, dönüşüm de O\'nadır.\"'),
(1969, 1853, 15, 51, 'Hicr ', 'Mekki', 'Onlara İbrahim\'in konuklarını da anlat:'),
(1970, 1854, 15, 52, 'Hicr ', 'Mekki', 'İbrahim\'in yanına girdiklerinde selam vermişlerdi. O: \"Doğrusu biz sizden korkuyoruz\" demişti de: \"Korkma, biz sana, bilgin bir oğlun olacağını müjdelemeye geldik\" demişlerdi.'),
(1971, 1855, 15, 53, 'Hicr ', 'Mekki', 'İbrahim\'in yanına girdiklerinde selam vermişlerdi. O: \"Doğrusu biz sizden korkuyoruz\" demişti de: \"Korkma, biz sana, bilgin bir oğlun olacağını müjdelemeye geldik\" demişlerdi.'),
(1972, 1856, 15, 54, 'Hicr ', 'Mekki', '\"Ben kocamışken bana müjde mi veriyorsunuz? Neye dayanarak müjdeliyorsunuz?\" deyince:'),
(1973, 1857, 15, 55, 'Hicr ', 'Mekki', '\"Seni gerçekten müjdeliyoruz, umutsuzlardan olma\" demişlerdi.'),
(1974, 1858, 15, 56, 'Hicr ', 'Mekki', '\"Zaten sapıklardan başka kim Rabbinin rahmetinden umudunu keser!\" diyerek sormuştu: \"Ey elçiler! İşiniz nedir?\"'),
(1975, 1859, 15, 57, 'Hicr ', 'Mekki', '\"Zaten sapıklardan başka kim Rabbinin rahmetinden umudunu keser!\" diyerek sormuştu: \"Ey elçiler! İşiniz nedir?\"'),
(1976, 1860, 15, 58, 'Hicr ', 'Mekki', 'Şöyle cevap vermişlerdi: \"Biz şüphesiz suçlu bir millete gönderildik. Lut\'un ailesi bunun dışındadır. Karısı hariç hepsini kurtaracağız. Karısının geride kalanlardan olmasını gerekli bulduk.\"'),
(1977, 1861, 15, 59, 'Hicr ', 'Mekki', 'Şöyle cevap vermişlerdi: \"Biz şüphesiz suçlu bir millete gönderildik. Lut\'un ailesi bunun dışındadır. Karısı hariç hepsini kurtaracağız. Karısının geride kalanlardan olmasını gerekli bulduk.\"'),
(1978, 1862, 15, 60, 'Hicr ', 'Mekki', 'Şöyle cevap vermişlerdi: \"Biz şüphesiz suçlu bir millete gönderildik. Lut\'un ailesi bunun dışındadır. Karısı hariç hepsini kurtaracağız. Karısının geride kalanlardan olmasını gerekli bulduk.\"'),
(1979, 1863, 15, 61, 'Hicr ', 'Mekki', 'Elçiler Lut\'un ailesine gelince, Lut: \"Doğrusu siz tanınmayan kimselersiniz\" dedi.'),
(1980, 1864, 15, 62, 'Hicr ', 'Mekki', 'Elçiler Lut\'un ailesine gelince, Lut: \"Doğrusu siz tanınmayan kimselersiniz\" dedi.'),
(1981, 1865, 15, 63, 'Hicr ', 'Mekki', '\"Biz sana sadece şüphe edip durdukları azabı getirdik. Sana gerçekle geldik. Şüphesiz biz doğru söyleyenleriz. Artık, geceleyin bir ara, aileni yola çıkar, sen de arkalarından git; hiçbiriniz arkaya bakmasın; emrolunduğunuz yere doğru yürüyün\" dediler.'),
(1982, 1866, 15, 64, 'Hicr ', 'Mekki', '\"Biz sana sadece şüphe edip durdukları azabı getirdik. Sana gerçekle geldik. Şüphesiz biz doğru söyleyenleriz. Artık, geceleyin bir ara, aileni yola çıkar, sen de arkalarından git; hiçbiriniz arkaya bakmasın; emrolunduğunuz yere doğru yürüyün\" dediler.'),
(1983, 1867, 15, 65, 'Hicr ', 'Mekki', '\"Biz sana sadece şüphe edip durdukları azabı getirdik. Sana gerçekle geldik. Şüphesiz biz doğru söyleyenleriz. Artık, geceleyin bir ara, aileni yola çıkar, sen de arkalarından git; hiçbiriniz arkaya bakmasın; emrolunduğunuz yere doğru yürüyün\" dediler.'),
(1984, 1868, 15, 66, 'Hicr ', 'Mekki', 'Böylece Lut\'a bunların sonlarının kesilmiş olarak sabahlıyacaklarını bildirdik.'),
(1985, 1869, 15, 67, 'Hicr ', 'Mekki', 'Şehir halkı, sevinerek geldiler.'),
(1986, 1870, 15, 68, 'Hicr ', 'Mekki', 'Lut: \"Bunlar benim konuklarımdır, onlara karşı beni rüsvay etmeyin, Allah\'tan korkun, beni utandırmayın\" dedi.'),
(1987, 1871, 15, 69, 'Hicr ', 'Mekki', 'Lut: \"Bunlar benim konuklarımdır, onlara karşı beni rüsvay etmeyin, Allah\'tan korkun, beni utandırmayın\" dedi.'),
(1988, 1872, 15, 70, 'Hicr ', 'Mekki', '\"Biz sana kimseyi misafir kabul etmeyi yasak etmemiş miydik?\" dediler.'),
(1989, 1873, 15, 71, 'Hicr ', 'Mekki', 'Lut: \"Alacaksanız, işte benim kızlarım\" dedi.'),
(1990, 1874, 15, 72, 'Hicr ', 'Mekki', 'Senin hayatına and olsun ki, onlar sarhoşlukları içinde bocalayıp duruyorlardı.'),
(1991, 1875, 15, 73, 'Hicr ', 'Mekki', 'Tanyeri ağarırken, çığlık onları yakalayıverdi.'),
(1992, 1876, 15, 74, 'Hicr ', 'Mekki', 'Memleketlerini alt üst ettik, üzerlerine sert taş yağdırdık.'),
(1993, 1877, 15, 75, 'Hicr ', 'Mekki', 'Bunda, görebilen insanlar için ibretler vardır.'),
(1994, 1878, 15, 76, 'Hicr ', 'Mekki', 'O şehrin kalıntıları işlek yollar üzerinde hala durmaktadır.'),
(1995, 1879, 15, 77, 'Hicr ', 'Mekki', 'Bunda inananlar için ibret vardır.'),
(1996, 1880, 15, 78, 'Hicr ', 'Mekki', 'Eykeliler de, şüphesiz zalim kimselerdi.'),
(1997, 1881, 15, 79, 'Hicr ', 'Mekki', 'Bunun için onlardan da öç aldık. Hala her iki memleket de işlek bir yol üzerindedirler.'),
(1998, 1882, 15, 80, 'Hicr ', 'Mekki', 'And olsun ki, Hicr halkı peygamberi yalanlamışlardı.'),
(1999, 1883, 15, 81, 'Hicr ', 'Mekki', 'Onlara ayetlerimizi verdiğimiz halde, yüz çevirmişlerdi.'),
(2000, 1884, 15, 82, 'Hicr ', 'Mekki', 'Dağlarda, güven içinde olarak evler yontuyorlardı.'),
(2001, 1885, 15, 83, 'Hicr ', 'Mekki', 'Sabaha karşı çığlık onları yakalayıverdi.'),
(2002, 1886, 15, 84, 'Hicr ', 'Mekki', 'Yaptıkları kendilerine bir fayda sağlamadı.'),
(2003, 1887, 15, 85, 'Hicr ', 'Mekki', 'Biz, gökleri, yeri ve her ikisi arasında bulunanları gereğince yarattık. Kıyamet günü şüphesiz gelecektir. O halde yumuşak ve iyi davran.'),
(2004, 1888, 15, 86, 'Hicr ', 'Mekki', 'Doğrusu yaratan ve bilen ancak Rabbindir.'),
(2005, 1889, 15, 87, 'Hicr ', 'Mekki', 'And olsun ki, sana daima tekrarlanan yedi ayetli Fatiha\'yı ve Kuran-ı Azim\'i verdik.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(2006, 1890, 15, 88, 'Hicr ', 'Mekki', 'Kafirler içinde bazı kimselere verdiğimiz kat kat servete gözünü dikme, onlara üzülme; inananları kanatların altına al.'),
(2007, 1891, 15, 89, 'Hicr ', 'Mekki', 'De ki: \"Doğrusu ben apaçık bir uyarıcıyım.\"'),
(2008, 1892, 15, 90, 'Hicr ', 'Mekki', 'Kuran\'ı işlerine geldiği gibi bölenlere de, kendi Kitablarının bir kısmına inanıp bir kısmını kabul etmeyen yahudi ve hıristiyanlara da nitekim Kitap indirmiştik; Rabbine and olsun ki hepsini, yaptıklarından sorumlu tutacağız.'),
(2009, 1893, 15, 91, 'Hicr ', 'Mekki', 'Kuran\'ı işlerine geldiği gibi bölenlere de, kendi Kitablarının bir kısmına inanıp bir kısmını kabul etmeyen yahudi ve hıristiyanlara da nitekim Kitap indirmiştik; Rabbine and olsun ki hepsini, yaptıklarından sorumlu tutacağız.'),
(2010, 1894, 15, 92, 'Hicr ', 'Mekki', 'Kuran\'ı işlerine geldiği gibi bölenlere de, kendi Kitablarının bir kısmına inanıp bir kısmını kabul etmeyen yahudi ve hıristiyanlara da nitekim Kitap indirmiştik; Rabbine and olsun ki hepsini, yaptıklarından sorumlu tutacağız.'),
(2011, 1895, 15, 93, 'Hicr ', 'Mekki', 'Kuran\'ı işlerine geldiği gibi bölenlere de, kendi Kitablarının bir kısmına inanıp bir kısmını kabul etmeyen yahudi ve hıristiyanlara da nitekim Kitap indirmiştik; Rabbine and olsun ki hepsini, yaptıklarından sorumlu tutacağız.'),
(2012, 1896, 15, 94, 'Hicr ', 'Mekki', 'Artık buyrulanı açıkça ortaya koy, puta tapanlara aldırış etme.'),
(2013, 1897, 15, 95, 'Hicr ', 'Mekki', 'Allah\'la beraber başka bir tanrının bulunduğunu kabul eden alaycılara karşı şüphesiz Biz sana kafiyiz. Yakında ne olduğunu öğreneceklerdir.'),
(2014, 1898, 15, 96, 'Hicr ', 'Mekki', 'Allah\'la beraber başka bir tanrının bulunduğunu kabul eden alaycılara karşı şüphesiz Biz sana kafiyiz. Yakında ne olduğunu öğreneceklerdir.'),
(2015, 1899, 15, 97, 'Hicr ', 'Mekki', 'And olsun ki, söyledikleri şeylerden senin gönlünün daraldığını biliyoruz.'),
(2016, 1900, 15, 98, 'Hicr ', 'Mekki', 'Rabbini hamd ile an, secde edenlerden ol ve ölünceye kadar Rabbine kulluk et.'),
(2017, 1901, 15, 99, 'Hicr ', 'Mekki', 'Rabbini hamd ile an, secde edenlerden ol ve ölünceye kadar Rabbine kulluk et.'),
(2018, 1902, 16, 1, 'Nahl ', 'Mekki', 'Allah\'ın buyruğu gelecektir; acele gelmesini istemeyin, Allah, ortak koştukları şeylerden münezzehtir, yücedir.'),
(2019, 1903, 16, 2, 'Nahl ', 'Mekki', 'Allah kullarından dilediğine buyruğunu bildirmek için meleklerini vahiyle indirerek şöyle der: \"İnsanları uyarın ki, Benden başka tanrı yoktur. Benden sakının.\"'),
(2020, 1904, 16, 3, 'Nahl ', 'Mekki', 'Gökleri ve yeri gereğince yaratmıştır. Onların eş koştukları şeylerden yücedir.'),
(2021, 1905, 16, 4, 'Nahl ', 'Mekki', 'İnsanı nutfeden yaratmıştır. Öyleyken o nasıl da açıkça karşı koymaktadır!'),
(2022, 1906, 16, 5, 'Nahl ', 'Mekki', 'Hayvanları da yaratmıştır. Onlarda sizi ısıtacak şeyler ve birçok faydalar vardır. Onların etlerini de yersiniz.'),
(2023, 1907, 16, 6, 'Nahl ', 'Mekki', 'Onları getirirken de, gönderirken de zevk alırsınız.'),
(2024, 1908, 16, 7, 'Nahl ', 'Mekki', 'Kendi kendinize zor varacağınız memleketlere, yüklerinizi taşırlar. Doğrusu Rabbiniz şefkatlidir, merhametlidir.'),
(2025, 1909, 16, 8, 'Nahl ', 'Mekki', 'Sizin için atları, katırları ve merkebleri binek ve süs hayvanı olarak yaratmıştır. Bilmediğiniz daha nice şeyleri de yaratır.'),
(2026, 1910, 16, 9, 'Nahl ', 'Mekki', 'Yolun eğri olanı da vardır. Allah dileseydi hepinizi doğru yola iletirdi.'),
(2027, 1911, 16, 10, 'Nahl ', 'Mekki', 'Yukarıdan size su indiren O\'dur. Ondan içersiniz; hayvanları otlattığınız bitkiler de onunla biter.'),
(2028, 1912, 16, 11, 'Nahl ', 'Mekki', 'Allah onunla size ekinler, zeytin ve hurma ağaçları, üzümler ve her türlü ürünü yetiştirir. Düşünen kimseler için bunda ders vardır.'),
(2029, 1913, 16, 12, 'Nahl ', 'Mekki', 'Geceyi gündüzü, güneşi ayı sizin istifadenize vermiştir. Yıldızlar da O\'nun buyruğuna boyun eğmiştir. Bunlarda, akleden kimseler için dersler vardır.'),
(2030, 1914, 16, 13, 'Nahl ', 'Mekki', 'Yeryüzünde rengarenk şeyleri de sizin için yaratmıştır. Bunda, öğüt alan kimseler için ibret vardır.'),
(2031, 1915, 16, 14, 'Nahl ', 'Mekki', 'Taze et yemeniz, takındığınız süsleri edinmeniz ve Allah\'ın bol nimetinden faydalanmanız için denize -ki gemilerin onu yara yara gittiğini görürsün- boyun eğdiren de O\'dur. Artık belki şükredersiniz.'),
(2032, 1916, 16, 15, 'Nahl ', 'Mekki', 'Yeryüzünde, sarsılmayasınız diye, sabit dağlar, nehirler ve belki yolunuzu bulursunuz diye yollar ve işaretler meydana getirmiştir. Onlar yıldızla da yollarını bulurlar.'),
(2033, 1917, 16, 16, 'Nahl ', 'Mekki', 'Yeryüzünde, sarsılmayasınız diye, sabit dağlar, nehirler ve belki yolunuzu bulursunuz diye yollar ve işaretler meydana getirmiştir. Onlar yıldızla da yollarını bulurlar.'),
(2034, 1918, 16, 17, 'Nahl ', 'Mekki', 'Hiç yaratan yaratamayana benzer mi? İbret almaz mısınız?'),
(2035, 1919, 16, 18, 'Nahl ', 'Mekki', 'Allah\'ın verdiği nimetleri sayacak olsanız bitiremezsiniz; doğrusu Allah bağışlar, merhamet eder.'),
(2036, 1920, 16, 19, 'Nahl ', 'Mekki', 'Allah, gizlediklerinizi de, açığa vurduklarınızı da bilir.'),
(2037, 1921, 16, 20, 'Nahl ', 'Mekki', 'Allah\'ı bırakıp taptıkları şeyler, hiçbir şey yaratmazlar; esasen kendileri yaratıktır.'),
(2038, 1922, 16, 21, 'Nahl ', 'Mekki', 'Onlar cansız, ölüdürler. Ne zaman dirileceklerini de bilemezler.'),
(2039, 1923, 16, 22, 'Nahl ', 'Mekki', 'Tanrınız tek bir Tanrıdır. Ahirete inanmayanların kalbleri bunu inkar eder; onlar büyüklük taslarlar.'),
(2040, 1924, 16, 23, 'Nahl ', 'Mekki', 'Onların gizlediklerini de, açığa vurduklarını da Allah\'ın bildiğinde şüphe yoktur. O, büyüklük taslayanları sevmez.'),
(2041, 1925, 16, 24, 'Nahl ', 'Mekki', 'Onlara: \"Rabbiniz ne indirdi?\" diye sorulsa: \"öncekilerin masalları\" derler.'),
(2042, 1926, 16, 25, 'Nahl ', 'Mekki', 'Böylece kıyamet günü kendi günahlarını tam olarak, bilmeden saptırdıkları kimselerin günahlarını kısmen yüklenirler. Dikkat edin, yüklendikleri yük ne kötüdür!'),
(2043, 1927, 16, 26, 'Nahl ', 'Mekki', 'Onlardan öncekiler düzen kurmuşlardı. Bunun üzerine Allah, binalarının temelini çökertti de tavanları başlarına yıkıldı. Azap, onlara farketmedikleri yerden geldi.'),
(2044, 1928, 16, 27, 'Nahl ', 'Mekki', 'Sonra kıyamet günü onları rezil eder ve: \"Haklarında tartıştığınız Benim ortaklarım nerede?\" der. İlim sahibleri şöyle derler: \"Doğrusu bugün inkarcılara rezillik ve iğrençlik vardır.\"'),
(2045, 1929, 16, 28, 'Nahl ', 'Mekki', 'Melekler kendilerine yazık etmiş kimselerin canlarını alırken: \"Biz hiçbir kötülük yapmıyorduk\" diyerek teslim olurlar. Hayır; öyle değil; doğrusu Allah onların yaptıklarını bilmektedir.'),
(2046, 1930, 16, 29, 'Nahl ', 'Mekki', 'Temelli kalacağınız cehennemin kapılarından girin. Büyüklenenlerin durağı ne kötüdür!'),
(2047, 1931, 16, 30, 'Nahl ', 'Mekki', 'Sakınan kimselere: \"Rabbiniz ne indirdi?\" denince, \"İyilik\" derler. Bu dünyada iyi davrananlara iyilik vardır. Ahiret yurdu ise daha iyidir. Sakınanların yurdu ne güzeldir!'),
(2048, 1932, 16, 31, 'Nahl ', 'Mekki', 'İçlerinden ırmaklar akan Adn cennetlerine girerler. Orada, diledikleri kendilerine verilir. Allah sakınanları böylece mükafatlandırır.'),
(2049, 1933, 16, 32, 'Nahl ', 'Mekki', 'Melekler onların canını temizlenmiş olarak alırken: \"Selam size; yaptıklarınıza karşılık haydi cennete girin\" derler.'),
(2050, 1934, 16, 33, 'Nahl ', 'Mekki', 'Onlar kendilerine yalnız meleklerin veya senin Rabbinin buyruğunun gelmesini mi bekliyorlar? Onlardan öncekiler de böyle yapmışlardı. Allah onlara zulmetmemişti, ama onlar kendilerine yazık ediyorlardı.'),
(2051, 1935, 16, 34, 'Nahl ', 'Mekki', 'Bu yüzden, işledikleri kötülüklere uğradılar ve alay ettikleri şey onları kuşattı.'),
(2052, 1936, 16, 35, 'Nahl ', 'Mekki', 'Allah\'a eş koşanlar: \"Allah dileseydi O\'ndan başka hiçbir şeye ne biz ve ne de babalarımız tapardı. O\'nun buyruğu olmaksızın hiçbir şeyi haram kılmazdık\" dediler. Kendilerinden öncekiler de böyle yapmıştı. Peygamberlere apaçık tebliğden başka ne vazife düşer?'),
(2053, 1937, 16, 36, 'Nahl ', 'Mekki', 'And olsun ki, her ümmete: \"Allah\'a kulluk edin, azdırıcılardan kaçının\" diyen peygamber göndermişizdir. Allah içlerinden kimini doğru yola eriştirdi, kimi de sapıklığı haketti. Yeryüzünde gezin; peygamberleri yalanlayanların sonlarının nasıl olduğunugörün.'),
(2054, 1938, 16, 37, 'Nahl ', 'Mekki', 'Onların doğru yolda olmalarına ne kadar özensen, yine de Allah, saptırdığını doğru yola iletmez. Onların yardımcıları da olmaz.'),
(2055, 1939, 16, 38, 'Nahl ', 'Mekki', 'Ölen kimseyi Allah\'ın diriltmeyeceği üzerine bütün güçleriyle Allah\'a yemin ederler. Hayır; öyle değil, ayrılığa düştükleri şeyi onlara açıklamayı, inkar edenlerin kendilerinin yalancı olduklarını bileceklerini, Allah gerçekten vadetmiştir, fakat insanların çoğu bilmezler.'),
(2056, 1940, 16, 39, 'Nahl ', 'Mekki', 'Ölen kimseyi Allah\'ın diriltmeyeceği üzerine bütün güçleriyle Allah\'a yemin ederler. Hayır; öyle değil, ayrılığa düştükleri şeyi onlara açıklamayı, inkar edenlerin kendilerinin yalancı olduklarını bileceklerini, Allah gerçekten vadetmiştir, fakat insanların çoğu bilmezler.'),
(2057, 1941, 16, 40, 'Nahl ', 'Mekki', 'Bir şeyin olmasını istediğimiz zaman sözümüz sadece ona \"Ol\" dememizdir ve hemen olur.'),
(2058, 1942, 16, 41, 'Nahl ', 'Mekki', 'Haksızlığa uğratıldıktan sonra, Allah yolunda hicret eden kimseleri, and olsun ki, dünyada güzel bir yerde yerleştiririz. Ahiret ecri ise daha büyüktür, keşki bilseler!'),
(2059, 1943, 16, 42, 'Nahl ', 'Mekki', 'Onlar sabreden ve yalnız Rablerine güvenen kimselerdir.'),
(2060, 1944, 16, 43, 'Nahl ', 'Mekki', 'Doğrusu senden önce de kendilerine kitablar ve belgelerle vahyettiğimiz bir takım adamlar gönderdik. Bilmiyorsanız kitablılara sorun. Sana da, insanlara gönderileni açıklayasın diye Kuran\'ı indirdik. Belki düşünürler.'),
(2061, 1945, 16, 44, 'Nahl ', 'Mekki', 'Doğrusu senden önce de kendilerine kitablar ve belgelerle vahyettiğimiz bir takım adamlar gönderdik. Bilmiyorsanız kitablılara sorun. Sana da, insanlara gönderileni açıklayasın diye Kuran\'ı indirdik. Belki düşünürler.'),
(2062, 1946, 16, 45, 'Nahl ', 'Mekki', 'Kötü işler düzenleyenler Allah\'ın kendilerini yere batırmasından yahut farketmedikleri bir yerden onlara azabın gelmesinden güvende midirler?'),
(2063, 1947, 16, 46, 'Nahl ', 'Mekki', 'Veya hareket halindelerken -ki Allah\'ı aciz bırakamazlar- ya da yok olmak endişesindeyken onlara azabın gelmesinden güvende midirler? Doğrusu Rabbin şefkatlidir, merhametlidir.'),
(2064, 1948, 16, 47, 'Nahl ', 'Mekki', 'Veya hareket halindelerken -ki Allah\'ı aciz bırakamazlar- ya da yok olmak endişesindeyken onlara azabın gelmesinden güvende midirler? Doğrusu Rabbin şefkatlidir, merhametlidir.'),
(2065, 1949, 16, 48, 'Nahl ', 'Mekki', 'Allah\'ın yarattığı şeylerin, gölgeleri sağa sola vurarak, Allah\'a boyun eğerek secde etmekte olduklarını görmüyorlar mı?'),
(2066, 1950, 16, 49, 'Nahl ', 'Mekki', 'Göklerde ve yerde bulunan her canlı ve melekler, büyüklük taslamaksızın Allah\'a secde ederler.'),
(2067, 1951, 16, 50, 'Nahl ', 'Mekki', 'Üstün olan Rablerinden korkarlar ve emrolundukları şeyleri yaparlar.'),
(2068, 1952, 16, 51, 'Nahl ', 'Mekki', 'Allah, \"İki tanrı edinmeyin, O ancak bir tek Tanrı\'dır. Yalnız Ben\'den korkun\" dedi.'),
(2069, 1953, 16, 52, 'Nahl ', 'Mekki', 'Göklerde ve yerde olan O\'nundur. Kulluk da daima O\'nadır. Allah\'tan başkasından mı sakınıyorsunuz?'),
(2070, 1954, 16, 53, 'Nahl ', 'Mekki', 'Size gelen her nimet Allah\'tandır. Sonra, bir sıkıntıya uğradığınızda yalnız O\'na sığınırsınız.'),
(2071, 1955, 16, 54, 'Nahl ', 'Mekki', 'Sıkıntılarınızı giderince de, içinizden bazıları kendilerine verdiğimize nankörlük ederek Rablerine eş koşarlar. Geçinin bakalım, yakında öğreneceksiniz.'),
(2072, 1956, 16, 55, 'Nahl ', 'Mekki', 'Sıkıntılarınızı giderince de, içinizden bazıları kendilerine verdiğimize nankörlük ederek Rablerine eş koşarlar. Geçinin bakalım, yakında öğreneceksiniz.'),
(2073, 1957, 16, 56, 'Nahl ', 'Mekki', 'Kendilerine verdiğimiz rızıktan, onların ne olduğunu bilmeyen putlara pay ayırırlar. Allah\'a and olsun ki, uydurup durduğunuz şeylerden elbette sorguya çekileceksiniz.'),
(2074, 1958, 16, 57, 'Nahl ', 'Mekki', 'Beğendikleri erkek çocukları kendilerine; kızları da Allah\'a malediyorlar. O bundan münezzehtir.'),
(2075, 1959, 16, 58, 'Nahl ', 'Mekki', 'Aralarından birine bir kızı olduğu müjdelendiği zaman içi gamla dolarak yüzü simsiyah kesilir.'),
(2076, 1960, 16, 59, 'Nahl ', 'Mekki', 'Kendisine verilen kötü müjde yüzünden, halktan gizlenmeye çalışır; onu utana utana tutsun mu, yoksa toprağa mı gömsün? Ne kötü hükmediyorlar!'),
(2077, 1961, 16, 60, 'Nahl ', 'Mekki', 'Ahirete inanmayanlar kötülük misalidirler. En üstün misali ise Allah verir. O Güçlü\'dür, Hakim\'dir.'),
(2078, 1962, 16, 61, 'Nahl ', 'Mekki', 'Allah insanları haksızlıklarından ötürü yakalayacak olsaydı, yeryüzünde canlı bırakmazdı. Fakat onları belirli bir süreye kadar erteler. Süreleri dolunca onu ne bir saat geciktirebilirler ne de öne alabilirler.'),
(2079, 1963, 16, 62, 'Nahl ', 'Mekki', 'Beğenmediklerini Allah\'a malederler. Dilleri, güzel şeylerin kendilerine ait olduğunu yalan yere söyler durur. Cehennemin onların olduğunda ve önceden oraya gideceklerinde şüphe yoktur.'),
(2080, 1964, 16, 63, 'Nahl ', 'Mekki', 'Allah\'a and olsun ki, senden önceki ümmetlere peygamberler gönderdik. Şeytan yaptıklarını onlara hep güzel gösterdi. Bugün de dostları odur. Onlara can yakıcı azap vardır.'),
(2081, 1965, 16, 64, 'Nahl ', 'Mekki', 'Sana Kitap\'ı, ayrılığa düştükleri şeyleri onlara açıklaman için, inanan kimselere de doğru yol rehberi ve rahmet olarak indirdik.'),
(2082, 1966, 16, 65, 'Nahl ', 'Mekki', 'Allah gökten su indirir ve ölümünden sonra yeryüzünü diriltir. Kulak veren kimseler için bunda ibret vardır.'),
(2083, 1967, 16, 66, 'Nahl ', 'Mekki', 'Hayvanlarda da size ibretler vardır. Bağırsaklarındakiler ile kan arasından, içenlere halis ve içimi kolay süt içiririz.'),
(2084, 1968, 16, 67, 'Nahl ', 'Mekki', 'Hurma ağaçlarının meyvelerinden ve üzümlerden şerbet, şıra (içecek) ve güzel rızık elde edersiniz. Düşünen millet için bunda ibret vardır.'),
(2085, 1969, 16, 68, 'Nahl ', 'Mekki', 'Rabbin bal arısına: \"Dağlarda, ağaçlarda ve hazırlanmış kovanlarda yuva edin; sonra her çeşit üründen ye; sonra da Rabbinin işlemen için gösterdiği yollardan yürü\" diye öğretti. Karınlarından insanlara şifa olan çeşitli renklerde bal çıkar. Düşünen bir millet için bunda ibret vardır.'),
(2086, 1970, 16, 69, 'Nahl ', 'Mekki', 'Rabbin bal arısına: \"Dağlarda, ağaçlarda ve hazırlanmış kovanlarda yuva edin; sonra her çeşit üründen ye; sonra da Rabbinin işlemen için gösterdiği yollardan yürü\" diye öğretti. Karınlarından insanlara şifa olan çeşitli renklerde bal çıkar. Düşünen bir millet için bunda ibret vardır.'),
(2087, 1971, 16, 70, 'Nahl ', 'Mekki', 'Allah sizi yaratmıştır, sonra öldürecektir, içinizden bir kısmı da ömrünün en fena zamanına ulaştırılır ki, bilirken bilmez olurlar. Doğrusu Allah bilendir, her şeye Kadir\'dir.'),
(2088, 1972, 16, 71, 'Nahl ', 'Mekki', 'Allah rızıkda kiminizi diğerlerine üstün tutmuştur. Üstün kılınanlar, emirleri altında bulunanların rızıklarını vermezler. Oysa rızıkta hepsi eşittir. Allah\'ın nimetini bile bile inkar mı ediyorlar?'),
(2089, 1973, 16, 72, 'Nahl ', 'Mekki', 'Allah size kendinizden eşler var eder. Eşlerinizden de oğullar ve torunlar var eder. Size temiz şeylerden rızık verir. Öyleyken batıla inanıyorlar ve Allah\'ın nimetini inkar mı ediyorlar?'),
(2090, 1974, 16, 73, 'Nahl ', 'Mekki', 'Allah\'ı bırakıp, göklerden ve yerden kendilerine verecek rızıkları olmayan ve vermeye güç yetiremeyen şeylere mi tapıyorlar?'),
(2091, 1975, 16, 74, 'Nahl ', 'Mekki', 'Allah\'a benzerler koşmaya kalkmayın. Şüphesiz Allah bilir, siz bilmezsiniz.'),
(2092, 1976, 16, 75, 'Nahl ', 'Mekki', 'Allah, hiçbir şeye gücü yetmeyen ve başkasının malı olan bir köle ile, kendisine verdiğimiz güzel nimetlerden gizlice ve açıkça sarfeden kimseyi misal gösterir: Hiç bunlar eşit olur mu? Övülmeğe layık olan Allah\'tır, fakat çoğu bilmezler.'),
(2093, 1977, 16, 76, 'Nahl ', 'Mekki', 'Allah iki adamı misal veriyor: Biri hiçbir şeye gücü yetmeyen bir dilsiz ki efendisine yüktür, nereye gönderse bir hayır çıkmaz; bu, doğru yolda olan, adaletle emreden kimse ile bir olabilir mi?'),
(2094, 1978, 16, 77, 'Nahl ', 'Mekki', 'Göklerin ve yerin gaybı Allah\'a aittir, kıyamet saatinin kopuşu bir göz kırpması kadar veya daha çabuk bir zaman içinde olur. Şüphesiz Allah her şeye Kadir\'dir.'),
(2095, 1979, 16, 78, 'Nahl ', 'Mekki', 'Allah sizi annelerinizin karnından bir şey bilmez halde çıkarmıştır. Belki şükredersiniz diye size kulak, göz ve kalp vermiştir.'),
(2096, 1980, 16, 79, 'Nahl ', 'Mekki', 'Göğün boşluğunda Allah\'ın buyruğuna boyun eğerek uçan kuşlara bakmıyorlar mı? Onları Allah\'tan başka tutan kimse yoktur. İnanan millet için bunda dersler vardır.'),
(2097, 1981, 16, 80, 'Nahl ', 'Mekki', 'Allah size evlerinizi dinlenme yeri kıldı. Hayvanların derilerinden, yolculukta ve ikamet zamanlarınızda kolayca taşıyacağınız evler; yün, tüy ve kıllarından bir süre kullanacağınız giyimlikler ve geçimlikler var etmiştir.'),
(2098, 1982, 16, 81, 'Nahl ', 'Mekki', 'Allah yarattıklarından size gölgeler yapmış; dağlarda sığınacağınız barınaklar var etmiş, sizi sıcaktan koruyacak elbiseler, harpte sizi koruyacak zırhlar vermiştir. Size olan nimetini müslüman olasınız diye işte bu şekilde tamamlamaktadır.'),
(2099, 1983, 16, 82, 'Nahl ', 'Mekki', 'Eğer yüz çevirirlerse, sana düşenin sadece açıkça tebliğ olduğunu bil.'),
(2100, 1984, 16, 83, 'Nahl ', 'Mekki', 'Allah\'ın nimetini hem bilirler hem de inkar ederler. Zaten çoğu kafir kimselerdir.'),
(2101, 1985, 16, 84, 'Nahl ', 'Mekki', 'Kıyamet günü her ümmetten bir şahit getiririz; inkar edenlere itiraz için izin de verilmez, onların özürleri de dinlenmez.'),
(2102, 1986, 16, 85, 'Nahl ', 'Mekki', 'Zulmedenler, azap görürlerken azabları hafifletilmez de geciktirilmez de.'),
(2103, 1987, 16, 86, 'Nahl ', 'Mekki', 'Allah\'a ortak koşanlar, koştukları ortakları gördüklerinde: \"Rabbimiz! Seni bırakıp yalvardığımız ortaklarımız bunlardır\" derler. Koştukları ortaklar: \"Doğrusu siz yalancısınız\" diye söz atarlar.'),
(2104, 1988, 16, 87, 'Nahl ', 'Mekki', 'Puta tapanlar o gün Allah\'ın hükmüne teslim olurlar; uydurdukları şeyler onlardan uzaklaşırlar.'),
(2105, 1989, 16, 88, 'Nahl ', 'Mekki', 'İnkar eden, Allah\'ın yolundan alıkoyanlara, bozgunculuklarına karşılık azap üstüne azap veririz.'),
(2106, 1990, 16, 89, 'Nahl ', 'Mekki', 'O gün her ümmetten bir kişiyi onlara şahit tutarız. Seni de ümmetine şahit getiririz. Sana her şeyi açıklayan ve Müslümanlara doğruyu gösteren bir rehber, rahmet ve müjde olarak Kuran\'ı indirdik.'),
(2107, 1991, 16, 90, 'Nahl ', 'Mekki', 'Allah şüphesiz adaleti, iyilik yapmayı, yakınlara bakmayı emreder; hayasızlığı, fenalığı ve haddi aşmayı yasak eder. Tutasınız diye size öğüt verir.'),
(2108, 1992, 16, 91, 'Nahl ', 'Mekki', 'Ahitleştiğiniz zaman Allah\'ın ahdini yerine getirin. Allah\'ı kendinize kefil kılarak sağlama bağladığınız yeminleri bozmayın. Allah yaptıklarınızı şüphesiz bilir.'),
(2109, 1993, 16, 92, 'Nahl ', 'Mekki', 'Bir ümmetin diğerinden daha çok olmasından ötürü, aranızdaki yeminleri bozarak, ipliğini iyice eğirip katladıktan sonra bozan kadın gibi olmayın. Allah onunla sizi dener. And olsun ki, ayrılığa düştüğünüz şeyleri size kıyamet günü açıklar.'),
(2110, 1994, 16, 93, 'Nahl ', 'Mekki', 'Allah dileseydi sizi tek bir ümmet yapardı. Ama O, istediğini saptırır, istediğini doğru yola eriştirir. İşlediklerinizden, and olsun ki, sorumlu tutulacaksınız.'),
(2111, 1995, 16, 94, 'Nahl ', 'Mekki', 'Birbirinizi aldatmak için yemin etmeyin ki, bu yüzden sağlamca yere basmakta olan ayak sürçebilir; Allah yolundan alıkoymanıza karşılık kötü bir azap tadarsınız ve (ahirette de) büyük bir azaba uğrarsınız.'),
(2112, 1996, 16, 95, 'Nahl ', 'Mekki', 'Allah\'ın ahdini hiçbir değere değişmeyin. Eğer bilirseniz, Allah katında olan sizin için daha iyidir.'),
(2113, 1997, 16, 96, 'Nahl ', 'Mekki', 'Sizde olanlar tükenir ama, Allah katında olanlar sonsuzdur, tükenmez. Sabredenlere ecirlerini, yaptıklarından daha güzeli ile ödeyeceğiz.'),
(2114, 1998, 16, 97, 'Nahl ', 'Mekki', 'Kadın, erkek, inanmış olarak kim iyi iş işlerse, ona hoş bir hayat yaşatacağız. Ecirlerini yaptıklarından daha güzeli ile ödeyeceğiz.'),
(2115, 1999, 16, 98, 'Nahl ', 'Mekki', 'Kuran okuyacağın zaman, kovulmuş şeytandan Allah\'a sığın.'),
(2116, 2000, 16, 99, 'Nahl ', 'Mekki', 'Doğrusu şeytanın, inananlar ve yalnız Rablerine güvenenler üzerinde bir nüfuzu yoktur.'),
(2117, 2001, 16, 100, 'Nahl ', 'Mekki', 'O\'nun nüfuzu sadece, O\'nu dost edinenler ve Allah\'a ortak koşanlar üzerindedir.'),
(2118, 2002, 16, 101, 'Nahl ', 'Mekki', 'Bir ayetin yerini başka bir ayetle değiştirdiğimizde, ki Allah ne indirdiğini gayet iyi bilir onlar, \"Sen sadece uyduruyorsun\" derler. Hayır, öyle değildir, ama onların çoğu bunu bilmezler.'),
(2119, 2003, 16, 102, 'Nahl ', 'Mekki', 'De ki: \"Kuran\'ı; Ruhul Kudüs (Cebrail) Rabbinin katından, inananların inançlarını pekiştirmek, Müslümanlara doğruluk rehberi ve müjde olmak üzere gerçekle indirmiştir.\"'),
(2120, 2004, 16, 103, 'Nahl ', 'Mekki', 'And olsun ki: \"Ona elbette bir insan öğretiyor\" dediklerini biliyoruz. Kast ettikleri kimsenin dili yabancıdır, Kuran ise fasih Arapça\'dır.'),
(2121, 2005, 16, 104, 'Nahl ', 'Mekki', 'Allah\'ın ayetlerine inanmayanları Allah doğru yola eriştirmez. Onlara can yakıcı azap vardır.'),
(2122, 2006, 16, 105, 'Nahl ', 'Mekki', 'Yalan uyduranlar ancak Allah\'ın ayetlerine inanmayanlardır. Yalancılar işte onlardır.'),
(2123, 2007, 16, 106, 'Nahl ', 'Mekki', 'Gönlü imanla dolu olduğu halde, zor altında olan kimse müstesna, inandıktan sonra Allah\'ı inkar edip, gönlünü kafirliğe açanlara Allah katından bir gazap vardır; büyük azap da onlar içindir.'),
(2124, 2008, 16, 107, 'Nahl ', 'Mekki', 'Bu, dünya hayatını ahirete tercih etmelerinden ve Allah\'ın da, inkarcı milleti doğru yola eriştirmemesinden ötürü böyledir.'),
(2125, 2009, 16, 108, 'Nahl ', 'Mekki', 'İşte Allah\'ın kalblerini, kulaklarını ve gözlerini mühürlediği kimseler bunlardır. Gafiller de işte bunlardır.'),
(2126, 2010, 16, 109, 'Nahl ', 'Mekki', 'Ahirette zarara uğrayacakların bunlar olduğunda şüphe yoktur.'),
(2127, 2011, 16, 110, 'Nahl ', 'Mekki', 'Rabbin, türlü eziyete uğratıldıktan sonra hicret eden, sonra Allah uğrunda savaşan ve sabreden kimselerden yanadır. Rabbin şüphesiz bundan sonra da bağışlar ve merhamet eder.'),
(2128, 2012, 16, 111, 'Nahl ', 'Mekki', 'O gün, herkesin kendi derdine düşüp çabalayacağı ve herkesin işlediğinin haksızlığa uğratılmadan kendisine ödeneceği bir gündür.'),
(2129, 2013, 16, 112, 'Nahl ', 'Mekki', 'Allah size güven ve huzur içinde olan bir kasabayı misal verir: Her taraftan oraya bolca rızık geliyordu. Ama Allah\'ın nimetlerine nankörlük ettiler; bu yüzden Allah onlara yaptıklarına karşılık açlık ve korku belasını tattırdı.'),
(2130, 2014, 16, 113, 'Nahl ', 'Mekki', 'And olsun ki, aralarından kendilerine bir peygamber gelmişti, onu yalancı saydılar. Haksızlık ederlerken azaba uğradılar.'),
(2131, 2015, 16, 114, 'Nahl ', 'Mekki', 'Yalnız Allah\'a kulluk ediyorsanız, Allah\'ın size helal ve temiz olarak verdiği rızıklardan yiyin, O\'nun nimetine şükredin.'),
(2132, 2016, 16, 115, 'Nahl ', 'Mekki', 'Allah size ancak leşi, kanı, domuz etini ve Allah\'tan başkasının adına kesilenleri haram etmiştir. Darda kalan, aşırı gitmemek ve başkasının hakkına el uzatmamak şartiyle bunun dışındadır. Allah şüphesiz bağışlar, merhamet eder.'),
(2133, 2017, 16, 116, 'Nahl ', 'Mekki', 'Diliniz yalana alışmış olduğu için, \"şu haram, bu helaldir\" demeyin, zira Allah\'a karşı yalan uydurmuş olursunuz. Allah\'a karşı yalan uyduranlar ise, saadete şüphesiz erişemezler.'),
(2134, 2018, 16, 117, 'Nahl ', 'Mekki', 'Az bir geçim ama ardından can yakıcı bir azap onlaradır.'),
(2135, 2019, 16, 118, 'Nahl ', 'Mekki', 'Sana anlattıklarımızı, daha önce, yahudi olanlara da haram kılmıştık; biz onlara zulmetmedik, onlar kendilerine zulmediyorlardı.'),
(2136, 2020, 16, 119, 'Nahl ', 'Mekki', 'Sonra doğrusu Rabbin, bilmeyerek kötülük işleyip ardından tevbe eden ve ıslah olanlardan yanadır. Rabbin bundan sonra da bağışlar ve merhamet eder.'),
(2137, 2021, 16, 120, 'Nahl ', 'Mekki', 'İbrahim, şüphesiz Allah\'a boyun eğen ve O\'na yönelen bir önderdi; puta tapanlardan değildi.'),
(2138, 2022, 16, 121, 'Nahl ', 'Mekki', 'Rabbinin nimetlerine şükrederdi; Rabbi de onu seçti ve doğru yola eriştirdi.'),
(2139, 2023, 16, 122, 'Nahl ', 'Mekki', 'Dünyada ona güzellik verdik, ahirette de o mutlaka barışsever iyiler arasında yer alacaktır.'),
(2140, 2024, 16, 123, 'Nahl ', 'Mekki', 'Şimdi sana, \"Doğruya yönelen, puta tapanlardan olmayan İbrahim\'in dinine uy\" diye vahyettik.'),
(2141, 2025, 16, 124, 'Nahl ', 'Mekki', 'Cumartesi ibadeti, ancak o gün üzerinde çekişenlere farz kılındı. Rabbin, ayrılığa düştükleri şeylerde, kıyamet günü aralarında hükmedecektir.'),
(2142, 2026, 16, 125, 'Nahl ', 'Mekki', 'Rabbinin yoluna, hikmetle, güzel öğütle çağır; onlarla en güzel şekilde tartış; doğrusu Rabbin, kendi yolundan sapanları daha iyi bilir. O, doğru yolda olanları da en iyi bilir.'),
(2143, 2027, 16, 126, 'Nahl ', 'Mekki', 'Eğer ceza vermek isterseniz size yapılanın aynıyla mukabele edin. Sabrederseniz and olsun ki bu, sabredenler için daha iyidir.'),
(2144, 2028, 16, 127, 'Nahl ', 'Mekki', 'Sabret, senin sabrın ancak Allah\'ın yardımıyladır; onlara üzülme, kurdukları düzenlerden de endişe etme.'),
(2145, 2029, 16, 128, 'Nahl ', 'Mekki', 'Allah şüphesiz sakınanlarla ve iyilik yapanlarla beraberdir.'),
(2146, 2030, 17, 1, 'İsrâ ', 'Mekki', 'Kulunu (Muhammed\'i) bir gece Mescidi Haram\'dan (Mekke\'den), kendisine bir kısım ayetlerimizi göstermek için, çevresini mübarek kıldığımız Mescidi Aksa\'ya (Kudüs\'e) götüren Allah\'ın şanı yücedir. Doğrusu O, işitir ve görür.'),
(2147, 2031, 17, 2, 'İsrâ ', 'Mekki', 'Musa\'ya kitap verdik. Ey Nuh\'la beraber taşıyarak kurtardığımız kimselerin soyundan olanlar! Beni bırakıp başkasını vekil edinmeyesiniz diye onu İsrailoğullarına doğruluk rehberi kıldık. Doğrusu Nuh, çok şükreden bir kuldu.'),
(2148, 2032, 17, 3, 'İsrâ ', 'Mekki', 'Musa\'ya kitap verdik. Ey Nuh\'la beraber taşıyarak kurtardığımız kimselerin soyundan olanlar! Beni bırakıp başkasını vekil edinmeyesiniz diye onu İsrailoğullarına doğruluk rehberi kıldık. Doğrusu Nuh, çok şükreden bir kuldu.'),
(2149, 2033, 17, 4, 'İsrâ ', 'Mekki', 'İsrailoğullarına Kitap\'da: \"Doğrusu yeryüzünde iki defa bozgunculuk yapacak ve kibirlendikçe kibirleneceksiniz\" diye bildirdik.'),
(2150, 2034, 17, 5, 'İsrâ ', 'Mekki', '\"Bu ikiden birincisinin vakti gelince, üzerinize pek güçlü olan kullarımızı salacağız. Onlar memleketlerinizde her köşeyi kontrollerine alacaklar. Bu, yerine gelecek bir vaaddir.\"'),
(2151, 2035, 17, 6, 'İsrâ ', 'Mekki', '\"Bunun ardından sizi onlara galip getireceğiz; mallar ve oğullarla size yardım edecek ve sizin sayınızı artıracağız.\"'),
(2152, 2036, 17, 7, 'İsrâ ', 'Mekki', 'İyilik ederseniz kendinize iyilik etmiş olursunuz. Kötülük ederseniz o da kendinizedir. İki vaadden ikincisinin vakti gelince, yüzünüzü üzüntüye sokmaları, kötülük yapmaları, önceden Mescid\'e girdikleri gibi girmeleri, ele geçirdikleri yerleri harap etmeleri için onları tekrar göndereceğiz.'),
(2153, 2037, 17, 8, 'İsrâ ', 'Mekki', 'Umulur ki Rabbiniz size acır; ama siz dönerseniz Biz de döneriz. Cehennemi, inkarcılara bir zindan kılmışızdır.'),
(2154, 2038, 17, 9, 'İsrâ ', 'Mekki', 'Doğrusu bu Kuran en doğru yola götürür ve yararlı iş yapan müminlere büyük ecir olduğunu, ahirete inanmayanlara can yakıcı bir azap hazırladığımızı müjdeler.'),
(2155, 2039, 17, 10, 'İsrâ ', 'Mekki', 'Doğrusu bu Kuran en doğru yola götürür ve yararlı iş yapan müminlere büyük ecir olduğunu, ahirete inanmayanlara can yakıcı bir azap hazırladığımızı müjdeler.'),
(2156, 2040, 17, 11, 'İsrâ ', 'Mekki', 'İnsan iyiliğin gelmesine dua ettiği gibi, kötülüğün gelmesine de dua eder. Esasen insanoğlu acelecidir.'),
(2157, 2041, 17, 12, 'İsrâ ', 'Mekki', 'Gece ve gündüzü varlığımıza birer delil kıldık. Bir delil olan geceyi kaldırıp yine bir delil olan gündüzü Rabbinizin bol nimetini aramanız, yılların sayısını ve hesabını bilmeniz için aydınlık kıldık. Her şeyi uzun uzadıya açıkladık.'),
(2158, 2042, 17, 13, 'İsrâ ', 'Mekki', 'Her insanın boynuna işlediklerini dolarız ve kıyamet günü açılmış bulacağı Kitap\'ı önüne çıkarırız.'),
(2159, 2043, 17, 14, 'İsrâ ', 'Mekki', '\"Kitabını oku, bugün, hesap görücü olarak sen kendine yetersin.\"'),
(2160, 2044, 17, 15, 'İsrâ ', 'Mekki', 'Kim doğru yola gelirse ancak kendi lehine yola gelmiş ve kim de saparsa ancak kendi aleyhine sapmıştır. Kimse kimsenin günahını çekmez. Biz peygamber göndermedikçe kimseye azabetmeyiz.'),
(2161, 2045, 17, 16, 'İsrâ ', 'Mekki', 'Bir şehri yok etmek istediğimiz zaman, şımarık varlıklarına yola gelmelerini emrederiz, ama onlar yoldan çıkarlar. Artık o şehir yok olmayı hakeder. Biz de onu yerle bir ederiz.'),
(2162, 2046, 17, 17, 'İsrâ ', 'Mekki', 'Nuh\'dan sonra nice nesilleri yok etmişizdir. Kullarının günahlarından haberdar ve onları gören olarak Rabbin yeter.'),
(2163, 2047, 17, 18, 'İsrâ ', 'Mekki', 'Dünyayı isteyene istediğimiz kimseye dilediğimiz kadar hemen veririz. Sonra ona cehennemi hazırlarız; yerilmiş ve kovulmuş olarak oraya girer.'),
(2164, 2048, 17, 19, 'İsrâ ', 'Mekki', 'Ahireti isteyip, inanmış olarak onun için gerekli çalışmada bulunan kimselerin, işte onların çalışmaları şükre değer.'),
(2165, 2049, 17, 20, 'İsrâ ', 'Mekki', 'Onların ve bunların her birine Rabbinin nimetinden ulaştırırız. Esasen Rabbinin nimeti kimseye yasak kılınmış değildir.'),
(2166, 2050, 17, 21, 'İsrâ ', 'Mekki', 'Onları birbirlerinden nasıl üstün kıldığımıza bir bak! Doğrusu ahirette daha büyük dereceler ve daha büyük üstünlükler vardır.'),
(2167, 2051, 17, 22, 'İsrâ ', 'Mekki', 'Allah\'la beraber başka bir tanrı edinme, yoksa yerilmiş ve tek başına kalmış olursun.'),
(2168, 2052, 17, 23, 'İsrâ ', 'Mekki', 'Rabbin, yalnız Kendisine tapmanızı ve ana babaya iyilik etmeyi buyurmuştur. Eğer ikisinden biri veya her ikisi, senin yanında iken ihtiyarlayacak olursa, onlara karşı \"Öf\" bile demeyesin, onları azarlamayasın. İkisine de hep tatlı söz söyleyesin.'),
(2169, 2053, 17, 24, 'İsrâ ', 'Mekki', 'Onlara acıyarak alçak gönüllülük kanatlarını ger ve: \"Rabbim! Küçükken beni yetiştirdikleri gibi sen de onlara merhamet et!\" de.'),
(2170, 2054, 17, 25, 'İsrâ ', 'Mekki', 'İçinizde olanı en iyi Rabbiniz bilir. İyi kimselerseniz bilin ki O şüphesiz, Kendine baş vuranları bağışlar.'),
(2171, 2055, 17, 26, 'İsrâ ', 'Mekki', 'Yakınına, düşküne, yolcuya hakkını ver; elindekileri saçıp savurma.'),
(2172, 2056, 17, 27, 'İsrâ ', 'Mekki', 'Saçıp savuranlar, şüphesiz şeytanlarla kardeş olmuş olurlar; şeytan ise Rabbine karşı pek nankördür.'),
(2173, 2057, 17, 28, 'İsrâ ', 'Mekki', 'Rabbin\'den umduğun rahmeti elde etmek için, hak sahiblerinden yüz çevirmek zorunda kalırsan, onlara hiç değilse tatlı bir söz söyle.'),
(2174, 2058, 17, 29, 'İsrâ ', 'Mekki', 'Elini boynuna bağlayıp cimri kesilme, büsbütün de açıp tutumsuz olma, yoksa pişman olur, açıkta kalırsın.'),
(2175, 2059, 17, 30, 'İsrâ ', 'Mekki', 'Doğrusu senin Rabbin dilediği kimsenin rızkını genişletir ve bir ölçüye göre verir. O kullarını gören ve haberdar olandır.'),
(2176, 2060, 17, 31, 'İsrâ ', 'Mekki', 'Çocuklarınızı yoksulluk korkusuyla öldürmeyin. Biz onlara da size de rızık veririz. Onları öldürmek, şüphesiz büyük bir günahtır.'),
(2177, 2061, 17, 32, 'İsrâ ', 'Mekki', 'Sakın zinaya yaklaşmayın; doğrusu bu çirkindir, kötü bir yoldur.'),
(2178, 2062, 17, 33, 'İsrâ ', 'Mekki', 'Allah\'ın haram kıldığı cana haksız yere kıymayın. Haksız yere öldürülenin velisine bir yetki tanımışızdır. Artık o da öldürmekte aşırı gitmesin. Zira kendisi ne de olsa yardım görmüştür.'),
(2179, 2063, 17, 34, 'İsrâ ', 'Mekki', 'Yetimin malına ergin çağa ulaşana kadar en güzel şeklin dışında yaklaşmayın. Ahdi de yerine getirin, doğrusu verilen ahidde sorumluluk vardır.'),
(2180, 2064, 17, 35, 'İsrâ ', 'Mekki', 'Bir şeyi ölçtüğünüz zaman, ölçüyü tam tutun, doğru teraziyle tartın. Böyle yapmak, sonuç itibariyle daha güzel ve daha iyidir.'),
(2181, 2065, 17, 36, 'İsrâ ', 'Mekki', 'Bilmediğin şeyin ardına düşme; doğrusu kulak, göz ve kalp, bunların hepsi o şeyden sorumlu olur.'),
(2182, 2066, 17, 37, 'İsrâ ', 'Mekki', 'Yeryüzünde böbürlenerek yürüme, çünkü sen ne yeri delebilir ve ne de boyca dağlara ulaşabilirsin.'),
(2183, 2067, 17, 38, 'İsrâ ', 'Mekki', 'Rabbinin katında bunların hepsi beğenilmeyen kötü şeylerdir.'),
(2184, 2068, 17, 39, 'İsrâ ', 'Mekki', 'Bunlar Rabbinin sana bildirdiği hikmetlerdir. Sakın Allah\'la beraber başka tanrı edinme. Yoksa yerilmiş ve kovulmuş olarak cehenneme atılırsın.'),
(2185, 2069, 17, 40, 'İsrâ ', 'Mekki', 'Rabbiniz oğulları size ayırdı, seçti de kendisi için kız olarak melekleri mi edindi? Doğrusu siz büyük söz söylüyorsunuz.'),
(2186, 2070, 17, 41, 'İsrâ ', 'Mekki', 'Biz, and olsun ki öğüt almaları için bu Kuran\'da bunları türlü türlü açıkladık. Fakat bu açıklamalar ancak onların nefretini artırmıştır.'),
(2187, 2071, 17, 42, 'İsrâ ', 'Mekki', 'De ki: \"Eğer dedikleri gibi Allah\'la beraber tanrılar bulunsaydı, o takdirde hepsi arşın sahibiyle savaşmaya bir yol ararlardı.\"'),
(2188, 2072, 17, 43, 'İsrâ ', 'Mekki', 'O, onların söylediklerinden Münezzeh\'tir, Yüce\'dir, Ulu\'dur.'),
(2189, 2073, 17, 44, 'İsrâ ', 'Mekki', 'Yedi gök, yer ve bunlarda bulunanlar O\'nu tesbih eder; O\'nu hamd ile tesbih etmeyen hiçbir şey yoktur; fakat siz onların tesbihlerini anlamazsınız. Doğrusu O Halim olandır, Bağışlayan\'dır.'),
(2190, 2074, 17, 45, 'İsrâ ', 'Mekki', 'Kuran okuduğun zaman senin ile ahirete inanmayan kimseler arasına görünmeyen bir perde çekeriz.'),
(2191, 2075, 17, 46, 'İsrâ ', 'Mekki', 'Kuran\'ı anlarlar diye kalblerine örtüler ve kulaklarına da ağırlık koyduk. Kuran\'da Rabbini bir tek olarak andığın zaman, onlar ürkerek ardlarına dönerler.'),
(2192, 2076, 17, 47, 'İsrâ ', 'Mekki', 'Seni dinledikleri zaman neye kulak verdiklerini ve gizli toplantılarında zalimlerin: \"Siz sadece büyülenmiş bir adama uyuyorsunuz\" dediklerini Biz çok iyi biliriz.'),
(2193, 2077, 17, 48, 'İsrâ ', 'Mekki', 'Sana nasıl misaller verdiklerine bir bak! Bu yüzden sapmışlardır, artık bir yol da bulamamaktadırlar.'),
(2194, 2078, 17, 49, 'İsrâ ', 'Mekki', '\"Biz kemik ve ufalanmış toprak olduğumuz zaman, yeniden mutlaka dirilecek miyiz? derler.'),
(2195, 2079, 17, 50, 'İsrâ ', 'Mekki', 'De ki: \"İster taş veya demir ya da kalbinizde büyüttüğünüz başka bir yaratık olun, yine de dirileceksiniz.\" \"Bizi tekrar kim diriltir?\" derler; de ki: \"Sizi ilk defa yaratan.\" Sana başlarını sallayarak: \"Ne zamandır bu?\" derler. \"Yakında olması mümkündür\" de.'),
(2196, 2080, 17, 51, 'İsrâ ', 'Mekki', 'De ki: \"İster taş veya demir ya da kalbinizde büyüttüğünüz başka bir yaratık olun, yine de dirileceksiniz.\" \"Bizi tekrar kim diriltir?\" derler; de ki: \"Sizi ilk defa yaratan.\" Sana başlarını sallayarak: \"Ne zamandır bu?\" derler. \"Yakında olması mümkündür\" de.'),
(2197, 2081, 17, 52, 'İsrâ ', 'Mekki', 'Sizi çağırdığı gün, O\'na hamdederek davetine uyarsınız ve kabirlerinizde pek az bir müddet kaldığınızı sanırsınız.'),
(2198, 2082, 17, 53, 'İsrâ ', 'Mekki', 'İnanan kullarıma söyle, en güzel şekilde konuşsunlar. Doğrusu şeytan aralarını bozmak ister. Şeytan şüphesiz insanın apaçık düşmanıdır.'),
(2199, 2083, 17, 54, 'İsrâ ', 'Mekki', 'Rabbiniz sizi daha iyi bilir. Dilerse size merhamet eder veya dilerse size azabeder. Biz seni onlara vekil olarak göndermedik.'),
(2200, 2084, 17, 55, 'İsrâ ', 'Mekki', 'Göklerde ve yerde olan kimseleri Rabbin daha iyi bilir. And olsun ki peygamberleri birbirinden üstün kılmış ve Davud\'a Zebur vermişizdir.'),
(2201, 2085, 17, 56, 'İsrâ ', 'Mekki', 'De ki: \"Allah\'tan başka tanrı olduğunu sandıklarınızı çağırın; sizin bir sıkıntınızı gidermeye ve onu değiştirmeye güçleri yetmez.\"'),
(2202, 2086, 17, 57, 'İsrâ ', 'Mekki', 'Taptıkları putlar Rablerine daha yakın olmak için vesile ararlar. O\'nun rahmetini umar, azabından korkarlar. Zira Rabbinin azabı korkmağa değer.'),
(2203, 2087, 17, 58, 'İsrâ ', 'Mekki', 'Kıyamet gününden önce ortadan kaldırmayacağımız veya çetin azaba uğratmayacağımız bir şehir yoktur. Bu, Kitap\'da yazılıdır.'),
(2204, 2088, 17, 59, 'İsrâ ', 'Mekki', 'Bizi mucize göndermekten alıkoyan, ancak, öncekilerin onları yalanlamış olmalarıdır. Semud milletine gözle görülebilen bir mucize, bir dişi deve vermiştik de ona zulmetmişlerdi. Oysa Biz mucizeleri yalnız korkutmak için göndeririz.'),
(2205, 2089, 17, 60, 'İsrâ ', 'Mekki', 'Sana: \"Rabbin şüphesiz insanları kuşatmıştır\" demiştik; sana gösterdiğimiz rüya ile ve Kuran\'da lanetlenmiş ağaçla, sadece insanları denedik. Biz onları korkutuyoruz, fakat bu onlara büyük taşkınlık vermekten başka birşeye yaramıyor.'),
(2206, 2090, 17, 61, 'İsrâ ', 'Mekki', 'Meleklere: \"Adem\'e secde edin\" demiştik, İblis\'ten başka hepsi secde etmiş, o ise: \"çamurdan yarattığına mı secde edeceğim?\" demişti.'),
(2207, 2091, 17, 62, 'İsrâ ', 'Mekki', '\"Benden üstün kıldığını görüyor musun? Kıyamet gününe kadar beni ertelersen, and olsun ki, azı bir yana, onun soyunu kendi buyruğum altına alacağım\" demişti.'),
(2208, 2092, 17, 63, 'İsrâ ', 'Mekki', 'Allah: \"Haydi git! Onlardan sana kim uyarsa bil ki, cehennem hepinizin cezası olur, hem de tam bir ceza\" dedi.'),
(2209, 2093, 17, 64, 'İsrâ ', 'Mekki', '\"Sesinle, gücünün yettiğini yerinden oynat, onlara karşı yaya ve atlılarınla haykırarak yürü, mallarına ve çocuklarına ortak ol, onlara vaadlerde bulun ama şeytan sadece onları aldatmak için vaadeder.'),
(2210, 2094, 17, 65, 'İsrâ ', 'Mekki', 'Doğrusu Benim mümin kullarım üzerinde senin bir hakimiyetin olamaz. Rabbin vekil olarak yeter.\"'),
(2211, 2095, 17, 66, 'İsrâ ', 'Mekki', 'Rabbiniz, bol nimetinden elde edesiniz diye, denizde gemileri sizin için yüzdürür. O, size merhamet eder.'),
(2212, 2096, 17, 67, 'İsrâ ', 'Mekki', 'Denizde bir sıkıntıya düştüğünüz zaman, Allah\'tan başka yalvardıklarınız kaybolup gider, fakat O sizi karaya çıkararak kurtarınca yüz çevirirsiniz. Zaten insan pek nankördür.'),
(2213, 2097, 17, 68, 'İsrâ ', 'Mekki', 'Onun karada da, sizi yere batırmasından veya başınıza taş yağdırmasından güvende misiniz? Sonra kendinize bir koruyucu da bulamazsınız.'),
(2214, 2098, 17, 69, 'İsrâ ', 'Mekki', 'Yoksa sizi tekrar denize döndürüp, üzerinize ortalığı yıkan bir fırtına gönderip, inkarlarınızdan ötürü sizi suda boğmasından güvende misiniz? O zaman bize soru soracak bir yardımcı da bulamazsınız.'),
(2215, 2099, 17, 70, 'İsrâ ', 'Mekki', 'And olsun ki, biz insanoğullarını şerefli kıldık, onların karada ve denizde gezmesini sağladık, temiz şeylerle onları rızıklandırdık, yaratıklarımızın pek çoğundan üstün kıldık.'),
(2216, 2100, 17, 71, 'İsrâ ', 'Mekki', 'Bir gün bütün insanları önderleriyle beraber çağırırız. O gün kitabı sağından verilenler, işte onlar kitablarını okurlar. Onlara kıl kadar haksizlik edilmez.'),
(2217, 2101, 17, 72, 'İsrâ ', 'Mekki', 'Bu dünyada kalbi kör olan, ahirette de kör ve daha şaşkındır.'),
(2218, 2102, 17, 73, 'İsrâ ', 'Mekki', 'Seni, sana vahyettiğimizden ayırıp başka bir şeyi Bize karşı uydurman için uğraşırlar. O zaman seni dost edinirler.'),
(2219, 2103, 17, 74, 'İsrâ ', 'Mekki', 'Sana sebat vermemiş olsaydık, and olsun ki, az da olsa onlara meyledecektin.'),
(2220, 2104, 17, 75, 'İsrâ ', 'Mekki', 'O takdirde sana, hayatın da ölümün de, kat kat azabını tattırırdık. Sonra bize karşı bir yardımcı da bulamazdın.'),
(2221, 2105, 17, 76, 'İsrâ ', 'Mekki', 'Memleketinden çıkarmak için seni nerdeyse zorlayacaklardı. O takdirde senin ardından onlar da pek az kalabilirlerdi.'),
(2222, 2106, 17, 77, 'İsrâ ', 'Mekki', 'Bu, senden önce gönderdiğimiz peygamberlerimize de uyguladığımız yasadır. Sen bizim yasamızda değişiklik bulamazsın.'),
(2223, 2107, 17, 78, 'İsrâ ', 'Mekki', 'Güneşin batıya yönelmesinden gecenin kararmasına kadar namaz kıl; sabah vakti de namaz kıl, zira sabah namazına melekler şahit olur.'),
(2224, 2108, 17, 79, 'İsrâ ', 'Mekki', 'Geceleyin uyanıp, yalnız sana mahsus olarak fazladan namaz kıl. Belki de Rabbin seni övülecek makama yükseltir.'),
(2225, 2109, 17, 80, 'İsrâ ', 'Mekki', 'De ki: \"Rabbim! Beni dahil edeceğin yere hoşnutluk ve esenlikle dahil et; çıkaracağın yerden de hoşnutluk ve esenlikle çıkar. Katından beni destekleyecek bir kuvvet ver.\"'),
(2226, 2110, 17, 81, 'İsrâ ', 'Mekki', 'De ki: \"Hak geldi, batıl ortadan kalkmaya mahkumdur.\"'),
(2227, 2111, 17, 82, 'İsrâ ', 'Mekki', 'Kuran\'dan inananlara rahmet ve şifa olan şeyler indiriyoruz. O, zalimlerin ise sadece kaybını artırır.'),
(2228, 2112, 17, 83, 'İsrâ ', 'Mekki', 'İnsana nimet verdiğimiz zaman yüz çevirerek yan çizer; başına bir kötülük gelince de yese düşer.'),
(2229, 2113, 17, 84, 'İsrâ ', 'Mekki', 'De ki: \"Herkes yaradılışına göre davranır. Rabbiniz kimin en doğru yolda olduğunu bilir.\"'),
(2230, 2114, 17, 85, 'İsrâ ', 'Mekki', 'Sana ruhun ne olduğunu soruyorlar, de ki: \"Ruh, Rabbimin emrinden ibarettir. Bu hususta size pek az bilgi verilmiştir.\"'),
(2231, 2115, 17, 86, 'İsrâ ', 'Mekki', 'Dileseydik and olsun ki, sana vahyettiğimizi alıp götürürdük. Sonra bize karşı duracak bir vekil de bulamazdın.'),
(2232, 2116, 17, 87, 'İsrâ ', 'Mekki', 'Bunu yapmayışı ancak Rabbinin sana merhamet etmesindendir. Çünkü O\'nun sana olan nimeti büyüktür.'),
(2233, 2117, 17, 88, 'İsrâ ', 'Mekki', 'De ki: \"İnsanlar ve cinler, birbirine yardımcı olarak bu Kuran\'ın bir benzerini ortaya koymak için bir araya gelseler, and olsun ki, yine de benzerini ortaya koyamazlar.\"'),
(2234, 2118, 17, 89, 'İsrâ ', 'Mekki', 'And olsun ki, biz Kuran\'da insanlara türlü türlü misal gösterip açıkladık. Öyleyken insanların çoğu nankör olmakta direndiler.'),
(2235, 2119, 17, 90, 'İsrâ ', 'Mekki', 'Şöyle söylediler: \"Bize, yerden kaynaklar fışkırtmadıkça sana inanmayacağız\",'),
(2236, 2120, 17, 91, 'İsrâ ', 'Mekki', '\"Veya hurmalıkların, bağların olup, aralarında ırmaklar akıtmalısın.\"'),
(2237, 2121, 17, 92, 'İsrâ ', 'Mekki', '\"Yahut da iddia ettiğin gibi, göğü tepemize parça parça düşürmeli, ya da Allah\'ı ve melekleri karşımıza getirmelisin.\"'),
(2238, 2122, 17, 93, 'İsrâ ', 'Mekki', '\"Veya altın bir evin olmalı, yahut göğe yükselmelisin ama oradan okuyacağımız bir kitap indirmezsen yine o yükselmene inanmayacağız.\" De ki: \"Fesubhanallah! Ben peygamber olan bir insandan başka bir şey miyim? \"'),
(2239, 2123, 17, 94, 'İsrâ ', 'Mekki', 'İnsanlara doğruluk rehberi geldiği zaman, inanmalarına engel olan, sadece: \"Allah peygamber olarak bir insan mı gönderdi?\" demiş olmalarıdır.'),
(2240, 2124, 17, 95, 'İsrâ ', 'Mekki', 'De ki: \"Yeryüzünde yerleşip dolaşanlar melek olsalardı, biz de onlara gökten peygamber olarak bir melek gönderirdik.\"'),
(2241, 2125, 17, 96, 'İsrâ ', 'Mekki', 'De ki: \"Benimle sizin aranızda şahit olarak Allah yeter. Doğrusu O, kullarını görür, haberdardır.\"'),
(2242, 2126, 17, 97, 'İsrâ ', 'Mekki', 'Allah\'ın doğru yola eriştirdiği kimse hak yoldadır. Kimleri de saptırırsa, artık onlar için Allah\'dan başka dostlar bulamazsın. Biz onları kıyamet günü yüzükoyun, körler, dilsizler ve sağırlar olarak haşrederiz. Varacakları yer cehennemdir. Onun ateşi ne zaman sönmeye yüz tutsa hemen alevini artırırız.'),
(2243, 2127, 17, 98, 'İsrâ ', 'Mekki', 'Bu, ayetlerimizi inkar etmelerinin ve: \"Kemik ve ufalanmış toprak olduğumuzda mı yeniden dirileceğiz?\" demelerinin cezasıdır.'),
(2244, 2128, 17, 99, 'İsrâ ', 'Mekki', 'Gökleri ve yeri yaratan Allah\'ın, onların benzerlerini de tekrar yaratmaya Kadir olduğunu görmezler mi? Onlar için şüphe götürmeyen bir süre tayin etmiştir. Öyleyken, zalimler, inkarcılıkta hala direnirler.'),
(2245, 2129, 17, 100, 'İsrâ ', 'Mekki', 'De ki: \"Rabbimin rahmet hazinelerine siz sahip olsaydınız, tükenir korkusuyla yine de cimrilik ederdiniz. Zaten insanlar pek cimridir.\"'),
(2246, 2130, 17, 101, 'İsrâ ', 'Mekki', 'And olsun ki, Musa\'ya dokuz tane apaçık mucize verdik. İsrailoğullarına sor, Musa onlara geldiğinde, Firavun kendisine: \"Ey Musa! Ben seni büyülenmiş sanıyorum\" demişti.'),
(2247, 2131, 17, 102, 'İsrâ ', 'Mekki', 'Musa da: \"And olsun ki, bunları göklerin ve yerin Rabbinin açık belgeler olarak indirdiğini biliyorsun. Ey Firavun! Doğrusu senin mahvolacağını sanıyorum\" demişti.'),
(2248, 2132, 17, 103, 'İsrâ ', 'Mekki', 'Firavun bunun üzerine onları memleketten sürmek istedi. Biz de onu ve beraberindekilerin hepsini suda boğduk.'),
(2249, 2133, 17, 104, 'İsrâ ', 'Mekki', 'Sonra İsrailoğullarına: \"Bu memlekette siz oturun, kıyamet koptuğunda hepinizi bir araya getiririz.\" dedik.'),
(2250, 2134, 17, 105, 'İsrâ ', 'Mekki', 'Kuran\'ı ancak hak olarak indirdik ve o da indiği gibi hak olarak kaldı. Seni de yalnız müjdeci ve uyarıcı olarak gönderdik.'),
(2251, 2135, 17, 106, 'İsrâ ', 'Mekki', 'Kuran\'ı, insanlara ağır ağır okuman için, bölüm bölüm indirdik ve onu gerektikçe indirdik.'),
(2252, 2136, 17, 107, 'İsrâ ', 'Mekki', 'De ki: \"Kuran\'a ister inanın, isten inanmayın, O\'ndan önceki bilginlere o okunduğu zaman, yüzleri üzerine secdeye varırlar\" ve \"Rabbimiz münezzehtir. Rabbimiz\'in sözü şüphesiz yerine gelecektir\" derler.'),
(2253, 2137, 17, 108, 'İsrâ ', 'Mekki', 'De ki: \"Kuran\'a ister inanın, isten inanmayın, O\'ndan önceki bilginlere o okunduğu zaman, yüzleri üzerine secdeye varırlar\" ve \"Rabbimiz münezzehtir. Rabbimiz\'in sözü şüphesiz yerine gelecektir\" derler.'),
(2254, 2138, 17, 109, 'İsrâ ', 'Mekki', 'Ağlayarak yüz üstü yere kapanırlar; bu, onların gönüllerindeki saygıyı artırır.'),
(2255, 2139, 17, 110, 'İsrâ ', 'Mekki', 'De ki: \"İster Allah deyin, ister Rahman deyin, hangisini derseniz deyin, en güzel isimler O\'nundur.\" Namaz kılarken sesini yükseltme, gizli de okuma, ikisi ortasında bir yol tut.'),
(2256, 2140, 17, 111, 'İsrâ ', 'Mekki', 'De ki: \"Hamd, çocuk edinmemiş olan, hükümranlığında ortağı bulunmayan, düşkün olmayıp yardımcıya da ihtiyaç göstermeyen Allah\'a mahsustur.\" O\'nu gereği gibi büyükle.'),
(2257, 2141, 18, 1, 'Kehf ', 'Mekki', 'Hamd Allah\'a mahsustur ki, kendi katından şiddetli bir baskını haber vermek ve yararlı iş yapan müminlere, içinde temelli kalacakları güzel bir mükafatı müjdelemek ve: \"Allah çocuk edindi\" diyenleri uyarmak için kuluna eğri bir taraf bırakmadığı dosdoğru Kitap\'ı indirmiştir.'),
(2258, 2142, 18, 2, 'Kehf ', 'Mekki', 'Hamd Allah\'a mahsustur ki, kendi katından şiddetli bir baskını haber vermek ve yararlı iş yapan müminlere, içinde temelli kalacakları güzel bir mükafatı müjdelemek ve: \"Allah çocuk edindi\" diyenleri uyarmak için kuluna eğri bir taraf bırakmadığı dosdoğru Kitap\'ı indirmiştir.'),
(2259, 2143, 18, 3, 'Kehf ', 'Mekki', 'Hamd Allah\'a mahsustur ki, kendi katından şiddetli bir baskını haber vermek ve yararlı iş yapan müminlere, içinde temelli kalacakları güzel bir mükafatı müjdelemek ve: \"Allah çocuk edindi\" diyenleri uyarmak için kuluna eğri bir taraf bırakmadığı dosdoğru Kitap\'ı indirmiştir.'),
(2260, 2144, 18, 4, 'Kehf ', 'Mekki', 'Hamd Allah\'a mahsustur ki, kendi katından şiddetli bir baskını haber vermek ve yararlı iş yapan müminlere, içinde temelli kalacakları güzel bir mükafatı müjdelemek ve: \"Allah çocuk edindi\" diyenleri uyarmak için kuluna eğri bir taraf bırakmadığı dosdoğru Kitap\'ı indirmiştir.'),
(2261, 2145, 18, 5, 'Kehf ', 'Mekki', 'Allah\'ın çocuk edindiğine dair ne kendilerinin ve ne de babalarının bir bilgisi vardır. Ağızlarından çıkan söz ne büyük iftiradır. Onlar yalnız ve yalnız yalan söylerler.'),
(2262, 2146, 18, 6, 'Kehf ', 'Mekki', 'Bu söze inanmayanların ardından üzülerek nerdeyse kendini mahvedeceksin!'),
(2263, 2147, 18, 7, 'Kehf ', 'Mekki', 'İnsanların hangisinin daha iyi iş işlediğini ortaya koyalım diye, yeryüzünde olan şeyleri, yeryüzünün süsü yaptık.'),
(2264, 2148, 18, 8, 'Kehf ', 'Mekki', 'Şüphesiz Biz, yeryüzünde olanları kupkuru bir toprak haline getirebiliriz.'),
(2265, 2149, 18, 9, 'Kehf ', 'Mekki', 'Yoksa sen Mağara ve Kitap ehlini şaşılacak ayetlerimizden mi zannettin?'),
(2266, 2150, 18, 10, 'Kehf ', 'Mekki', 'Birkaç genç mağaraya sığınmış: \"Rabbimiz! Katından bize rahmet ver ve işimizde doğruyu göster, bizi başarılı kıl\" demişlerdi.'),
(2267, 2151, 18, 11, 'Kehf ', 'Mekki', 'Mağaranın içinde onları yıllarca uyuttuk; sonra, iki taraftan hangisinin bekledikleri sonucu iyi hesaplamış olduğunu belirtmek için onları uyandırdık.'),
(2268, 2152, 18, 12, 'Kehf ', 'Mekki', 'Mağaranın içinde onları yıllarca uyuttuk; sonra, iki taraftan hangisinin bekledikleri sonucu iyi hesaplamış olduğunu belirtmek için onları uyandırdık.'),
(2269, 2153, 18, 13, 'Kehf ', 'Mekki', 'Onların olayını sana Biz gerçek olarak anlatıyoruz: Onlar Rablerine inanmış birkaç gençti. Onların hidayetlerini artırmış ve kalblerini pekiştirmiştik. Durup, şöyle demişlerdi: \"Rabbimiz göklerin ve yerin Rabbidir, O\'nu bırakıp başka bir tanrıya yalvarmayız, yoksa and olsun ki, batıl söz söylemiş oluruz. Şu bizim milletimiz, Allah\'ı bırakıp O\'ndan başka tanrılar edindiler. Onların gerçek olduğuna apaçık delil getirmeleri gerekmez mi? Allah\'a karşı yalan uydurandan daha zalim kimdir?\"'),
(2270, 2154, 18, 14, 'Kehf ', 'Mekki', 'Onların olayını sana Biz gerçek olarak anlatıyoruz: Onlar Rablerine inanmış birkaç gençti. Onların hidayetlerini artırmış ve kalblerini pekiştirmiştik. Durup, şöyle demişlerdi: \"Rabbimiz göklerin ve yerin Rabbidir, O\'nu bırakıp başka bir tanrıya yalvarmayız, yoksa and olsun ki, batıl söz söylemiş oluruz. Şu bizim milletimiz, Allah\'ı bırakıp O\'ndan başka tanrılar edindiler. Onların gerçek olduğuna apaçık delil getirmeleri gerekmez mi? Allah\'a karşı yalan uydurandan daha zalim kimdir?\"'),
(2271, 2155, 18, 15, 'Kehf ', 'Mekki', 'Onların olayını sana Biz gerçek olarak anlatıyoruz: Onlar Rablerine inanmış birkaç gençti. Onların hidayetlerini artırmış ve kalblerini pekiştirmiştik. Durup, şöyle demişlerdi: \"Rabbimiz göklerin ve yerin Rabbidir, O\'nu bırakıp başka bir tanrıya yalvarmayız, yoksa and olsun ki, batıl söz söylemiş oluruz. Şu bizim milletimiz, Allah\'ı bırakıp O\'ndan başka tanrılar edindiler. Onların gerçek olduğuna apaçık delil getirmeleri gerekmez mi? Allah\'a karşı yalan uydurandan daha zalim kimdir?\"');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(2272, 2156, 18, 16, 'Kehf ', 'Mekki', 'Onlara: \"Siz onlardan ve Allah\'tan başka taptıklarından ayrıldınız, bunun için mağaraya girin ki, Rabbiniz size rahmetini yaysın ve size işinizde kolaylık göstersin\" denildi.'),
(2273, 2157, 18, 17, 'Kehf ', 'Mekki', 'Baksaydın, güneşin mağaralarının sağ tarafından doğup meylettiğini, sol tarafından onlara dokunmadan battığını, onların da mağaranın genişçe bir yerinde bulunduğunu görürdün. Bu, Allah\'ın mucizelerindendir; Allah\'ın doğru yola eriştirdiği kimse hak yoldadır. Kimi de saptırırsa artık ona, doğru yola götürecek bir rehber bulamazsın.'),
(2274, 2158, 18, 18, 'Kehf ', 'Mekki', 'Mağara ehli uykuda iken sen onları uyanık sanırdın. Biz onları sağa ve sola döndürürdük. Köpekleri dirseklerini eşiğe uzatmıştı. Onları görsen, için korkuyla dolar, geri dönüp kaçardın.'),
(2275, 2159, 18, 19, 'Kehf ', 'Mekki', 'Birbirlerine sorsunlar diye onları uyandırdık. İçlerinden biri: \"Ne kadar kaldınız?\" dedi. \"Bir gün veya daha az bir müddet kaldık\" dediler. \"Ne kadar kaldığınızı Rabbiniz daha iyi bilir. Paranızla birinizi şehre gönderin, sakın sizi kimseye duyurmasın\" dediler.'),
(2276, 2160, 18, 20, 'Kehf ', 'Mekki', '\"Zira onların sizden haberi olacak olursa, ya taşlayarak öldürürler veya dinlerine döndürürler ve bu takdirde asla kurtulamazsınız.\"'),
(2277, 2161, 18, 21, 'Kehf ', 'Mekki', 'Böylece, Allah\'ın sözünün gerçek olduğunu ve kıyametin kopmasından şüphe edilemeyeceğini bilmeleri için, insanların onları bulmalarını sağladık. Nitekim halk, bunların hakkında çekişip duruyor: \"Onların mağaralarının çevresine bir bina kurun\" diyorlardı. Oysa, Rableri onları çok iyi bilir. Tartışmayı kazananlar: \"Onların mağaralarının çevresinde mutlaka bir mescid kuracağız\" dediler.'),
(2278, 2162, 18, 22, 'Kehf ', 'Mekki', 'Karanlığa taş atar gibi, \"Mağara ehli üçtür, dördüncüleri köpekleridir\" derler, yahut, \"Beştir, altıncıları köpekleridir\" derler, yahut \"Yedidir, sekizincileri köpekleridir\" derler. De ki: \"Onların sayısını en iyi bilen Rabbim\'dir. Onları pek az kimseden başkası bilmez.\" Bunun için, onlar hakkında, bu kısaca anlatılanın dışında, kimseyle tartışma ve onlar hakkında kimseden bir şey sorma.'),
(2279, 2163, 18, 23, 'Kehf ', 'Mekki', 'Herhangi bir şey için, Allah\'ın dilemesi dışında: \"Ben yarın onu yapacağım\" deme. Unuttuğun zaman Rabbini an ve şöyle de: \"Umulur ki, Rabbim beni doğruya daha yakın olana eriştirir.\"'),
(2280, 2164, 18, 24, 'Kehf ', 'Mekki', 'Herhangi bir şey için, Allah\'ın dilemesi dışında: \"Ben yarın onu yapacağım\" deme. Unuttuğun zaman Rabbini an ve şöyle de: \"Umulur ki, Rabbim beni doğruya daha yakın olana eriştirir.\"'),
(2281, 2165, 18, 25, 'Kehf ', 'Mekki', 'Onlar mağaralarında üçyüz dokuz yıl kaldılar.'),
(2282, 2166, 18, 26, 'Kehf ', 'Mekki', 'De ki: \"Onların ne kadar kaldıklarını en iyi Allah bilir. Göklerin ve yerin gaybı O\'na aittir. O, ne mükemmel görendir! O ne mükemmel işitendir! İnsanların O\'ndan başka dostu yoktur. O, hiç kimseyi hükümranlığa ortak kılmaz.\"'),
(2283, 2167, 18, 27, 'Kehf ', 'Mekki', 'Rabbinin Kitap\'ından sana vahyolunanı oku; O\'nun sözlerini değiştirecek yoktur. O\'ndan başka bir sığınılacak da bulamazsın.'),
(2284, 2168, 18, 28, 'Kehf ', 'Mekki', 'Sabah akşam Rablerinin rızasını dileyerek O\'na yalvaranlarla beraber sen de sabret. Dünya hayatının güzelliklerini isteyerek gözlerini o kimselerden ayırma. Bizi anmasını kendisine unutturduğumuz ve işinde aşırı giderek hevesine uyan kimseye uyma.'),
(2285, 2169, 18, 29, 'Kehf ', 'Mekki', 'De ki: \"Gerçek Rabbinizdendir.\" Dileyen inansın, dileyen inkar etsin. Şüphesiz zalimler için, duvarları çepeçevre onları içine alacak bir ateş hazırlamışızdır. Onlar yardım istediklerinde, erimiş maden gibi yüzleri kavuran bir su kendilerine sunulur. Bu ne kötü bir içecek ve cehennem ne kötü bir duraktır!'),
(2286, 2170, 18, 30, 'Kehf ', 'Mekki', 'İyi hareket edenin ecrini zayi etmeyiz. Doğrusu, inanıp yararlı iş yapanlara, işte onlara, içlerinden ırmaklar akan Adn cennetleri vardır. Orada altın bilezikler takınırlar, ince ve kalın ipekliden yeşil elbiseler giyerek tahtları üzerinde otururlar. Ne güzel bir mükafat ve ne güzel yaslanacak yer!'),
(2287, 2171, 18, 31, 'Kehf ', 'Mekki', 'İyi hareket edenin ecrini zayi etmeyiz. Doğrusu, inanıp yararlı iş yapanlara, işte onlara, içlerinden ırmaklar akan Adn cennetleri vardır. Orada altın bilezikler takınırlar, ince ve kalın ipekliden yeşil elbiseler giyerek tahtları üzerinde otururlar. Ne güzel bir mükafat ve ne güzel yaslanacak yer!'),
(2288, 2172, 18, 32, 'Kehf ', 'Mekki', 'Onlara iki adamı misal olarak göster: Birine iki üzüm bağı verip, etrafını hurmalıklarla çevirmiş ve aralarında ekinler bitirmiştik.'),
(2289, 2173, 18, 33, 'Kehf ', 'Mekki', 'Her iki bahçe de ürünlerini vermişlerdi, hiçbir şeyi de eksik bırakmamışlardı. İkisinin arasından bir de ırmak akıtmıştık.'),
(2290, 2174, 18, 34, 'Kehf ', 'Mekki', 'Onun gelirleri de vardı. Bu yüzden, arkadaşiyle konuşurken: \"Ben malca senden zengin, nüfusça da senden daha itibarlıyım\" dedi.'),
(2291, 2175, 18, 35, 'Kehf ', 'Mekki', 'Kendisine böylece yazık ederek bahçesine girerken: \"Bu bahçenin batacağını hiç zannetmem. Kıyametin kopacağını da sanmıyorum. Eğer Rabbime döndürülürsem, and olsun ki orada bundan daha iyisini bulurum\" dedi.'),
(2292, 2176, 18, 36, 'Kehf ', 'Mekki', 'Kendisine böylece yazık ederek bahçesine girerken: \"Bu bahçenin batacağını hiç zannetmem. Kıyametin kopacağını da sanmıyorum. Eğer Rabbime döndürülürsem, and olsun ki orada bundan daha iyisini bulurum\" dedi.'),
(2293, 2177, 18, 37, 'Kehf ', 'Mekki', 'Kendisiyle konuştuğu arkadaşı ona: \"Seni topraktan, sonra nutfeden yaratanı, sonunda de seni insan kılığına koyanı mı inkar ediyorsun? İşte O benim Rabbim olan Allah\'tır. Rabbime kimseyi ortak koşmam. Bahçene girdiğin zaman, her ne kadar beni kendinden mal ve nüfus bakımından daha az buluyorsan da: \"Maşallah! Kuvvet ancak Allah\'a mahsustur!\" demen gerekmez mi? Rabbim, senin bahçenden daha iyisini bana verebilir ve seninkinin üzerine gökten bir felaket gönderir de bahçen yerle bir olabilir. Yahut suyu çekilir bir daha da bulamazsın\" dedi.'),
(2294, 2178, 18, 38, 'Kehf ', 'Mekki', 'Kendisiyle konuştuğu arkadaşı ona: \"Seni topraktan, sonra nutfeden yaratanı, sonunda de seni insan kılığına koyanı mı inkar ediyorsun? İşte O benim Rabbim olan Allah\'tır. Rabbime kimseyi ortak koşmam. Bahçene girdiğin zaman, her ne kadar beni kendinden mal ve nüfus bakımından daha az buluyorsan da: \"Maşallah! Kuvvet ancak Allah\'a mahsustur!\" demen gerekmez mi? Rabbim, senin bahçenden daha iyisini bana verebilir ve seninkinin üzerine gökten bir felaket gönderir de bahçen yerle bir olabilir. Yahut suyu çekilir bir daha da bulamazsın\" dedi.'),
(2295, 2179, 18, 39, 'Kehf ', 'Mekki', 'Kendisiyle konuştuğu arkadaşı ona: \"Seni topraktan, sonra nutfeden yaratanı, sonunda de seni insan kılığına koyanı mı inkar ediyorsun? İşte O benim Rabbim olan Allah\'tır. Rabbime kimseyi ortak koşmam. Bahçene girdiğin zaman, her ne kadar beni kendinden mal ve nüfus bakımından daha az buluyorsan da: \"Maşallah! Kuvvet ancak Allah\'a mahsustur!\" demen gerekmez mi? Rabbim, senin bahçenden daha iyisini bana verebilir ve seninkinin üzerine gökten bir felaket gönderir de bahçen yerle bir olabilir. Yahut suyu çekilir bir daha da bulamazsın\" dedi.'),
(2296, 2180, 18, 40, 'Kehf ', 'Mekki', 'Kendisiyle konuştuğu arkadaşı ona: \"Seni topraktan, sonra nutfeden yaratanı, sonunda de seni insan kılığına koyanı mı inkar ediyorsun? İşte O benim Rabbim olan Allah\'tır. Rabbime kimseyi ortak koşmam. Bahçene girdiğin zaman, her ne kadar beni kendinden mal ve nüfus bakımından daha az buluyorsan da: \"Maşallah! Kuvvet ancak Allah\'a mahsustur!\" demen gerekmez mi? Rabbim, senin bahçenden daha iyisini bana verebilir ve seninkinin üzerine gökten bir felaket gönderir de bahçen yerle bir olabilir. Yahut suyu çekilir bir daha da bulamazsın\" dedi.'),
(2297, 2181, 18, 41, 'Kehf ', 'Mekki', 'Kendisiyle konuştuğu arkadaşı ona: \"Seni topraktan, sonra nutfeden yaratanı, sonunda de seni insan kılığına koyanı mı inkar ediyorsun? İşte O benim Rabbim olan Allah\'tır. Rabbime kimseyi ortak koşmam. Bahçene girdiğin zaman, her ne kadar beni kendinden mal ve nüfus bakımından daha az buluyorsan da: \"Maşallah! Kuvvet ancak Allah\'a mahsustur!\" demen gerekmez mi? Rabbim, senin bahçenden daha iyisini bana verebilir ve seninkinin üzerine gökten bir felaket gönderir de bahçen yerle bir olabilir. Yahut suyu çekilir bir daha da bulamazsın\" dedi.'),
(2298, 2182, 18, 42, 'Kehf ', 'Mekki', 'Nitekim, ürünleri yok edildi; bağın altüst olmuş çardakları karşısında, sarfettiği emeğe içi yanarak ellerini oğuşturup \"Keşke Rabbime kimseyi ortak koşmasaydım\" diyordu.'),
(2299, 2183, 18, 43, 'Kehf ', 'Mekki', 'Ona, Allah\'tan başka yardım edebilecek adamları da yoktu, kendi kendini de kurtaramadı.'),
(2300, 2184, 18, 44, 'Kehf ', 'Mekki', 'İşte burada kudret ve hakimiyet, varlığı gerçek olan Allah\'ındır. Mükafatlandırma bakımından hayırlı olan da, sonuçlandırma yönünden hayırlı olan da O\'dur.'),
(2301, 2185, 18, 45, 'Kehf ', 'Mekki', 'Onlara, dünya hayatı misalinin tıpkı şöyle olduğunu anlat: Gökten indirdiğimiz su ile yeryüzünde yetişen bitkiler birbirine karışır, ama sonunda rüzgarın savuracağı çerçöpe döner. Allah her şeyin üstünde bir kudrete sahip olandır.'),
(2302, 2186, 18, 46, 'Kehf ', 'Mekki', 'Mal ve oğullar, dünya hayatının süsüdür. Ama baki kalacak yararlı işler, sevab olarak da, emel olarak da, Rabbinin katında daha hayırlıdır.'),
(2303, 2187, 18, 47, 'Kehf ', 'Mekki', 'Bir gün dağları yürütürüz de yeri dümdüz görürsün. Hiçbirini bırakmaksızın diriltip bir araya toplarız.'),
(2304, 2188, 18, 48, 'Kehf ', 'Mekki', 'Dizi dizi Rabbine sunulduklarında onlara: \"And olsun ki, sizi ilk defa yarattığımız gibi Bize geldiniz. Sizi bir yere toplamak için söz vermediğimizi iddia etmiştiniz değil mi?\" denir.'),
(2305, 2189, 18, 49, 'Kehf ', 'Mekki', 'Amel defteri ortaya konunca, suçluların, onda yazılı olanlardan korktuklarını görürsün, \"Vah bize, eyvah bize! Bu defter nasıl olmuş da küçük büyük bir şey bırakmadan hepsini saymış!\" derler. İşlediklerini hazır bulurlar. Rabbin kimseye haksızlık etmez.'),
(2306, 2190, 18, 50, 'Kehf ', 'Mekki', 'Meleklere: \"Adem\'e secde edin\" demiştik. İblis\'ten başka hepsi secde etmişti. O, cinlerden idi. Rabbinin buyruğu dışına çıktı. Ey insanoğulları! Siz Beni bırakıp onu ve soyunu dost mu ediniyorsunuz? Halbuki onlar size düşmandır. Kendilerine yazık edenler için bu ne kötü değişmedir!'),
(2307, 2191, 18, 51, 'Kehf ', 'Mekki', 'Oysa Ben onları ne göklerin ve yerin yaratılmasında ve ne de kendilerinin yaratılmasında hazır bulundurdum. Saptıranları hiçbir işte asla yardımcı da edinmedim.'),
(2308, 2192, 18, 52, 'Kehf ', 'Mekki', 'O gün Allah: \"Bana ortak olduklarını iddia ettiklerinize seslenin\" der. Onları çağırırlar, fakat hiçbirisi onların çağrılarına gelmez. Aralarına bir cehennem deresi koyarız.'),
(2309, 2193, 18, 53, 'Kehf ', 'Mekki', 'Suçlular ateşi görürler ve ona düşeceklerini anlarlar, fakat ondan kaçacak yer bulamazlar.'),
(2310, 2194, 18, 54, 'Kehf ', 'Mekki', 'And olsun ki, Biz bu Kuran\'da insanlara türlü türlü misali gösterip açıkladık. İnsanın en çok yaptığı iş tartışmadır.'),
(2311, 2195, 18, 55, 'Kehf ', 'Mekki', 'İnsanlara doğruluk rehberi gelmişken, onları inanmaktan, Rablerinden mağfiret dilemekten alıkoyan öncekilere uygulananın kendilerine de uygulanmasını veya gözleri göre göre azaba uğramayı beklemeleridir.'),
(2312, 2196, 18, 56, 'Kehf ', 'Mekki', 'Biz peygamberleri ancak müjdeci ve uyarıcı olarak göndeririz. Oysa inkarcılar hakkı batılla ortadan kaldırmak için çekişirler. Ayetlerimizi ve kendilerine yapılan uyarmaları alaya alırlar.'),
(2313, 2197, 18, 57, 'Kehf ', 'Mekki', 'Rabbinin ayetleri kendisine hatırlatılmışken onlardan yüz çeviren ve önceden yaptıklarını unutan kimseden daha zalim var mıdır? Kuran\'ı anlarlar diye kalblerine örtüler, kulaklarına da ağırlık koyduk. Sen onları doğru yola çağırsan da asla doğru yolagelmezler.'),
(2314, 2198, 18, 58, 'Kehf ', 'Mekki', 'Bununla beraber, Rabbin mağfiret ve merhamet sahibidir. Eğer onları, yaptıklarından dolayı hemen hesaba çekmek isteseydi, azaba uğratmakta acele ederdi. Ama onların bir vadesi vardır. Ondan kaçıp sığınacak yer bulamazlar.'),
(2315, 2199, 18, 59, 'Kehf ', 'Mekki', 'Haksızlıklarından ötürü işte yok ettiğimiz şehirler! Onları yok etmek için bir süre tayin etmiştik.'),
(2316, 2200, 18, 60, 'Kehf ', 'Mekki', 'Musa, genç arkadaşına: \"Ben iki denizin birleştiği yere ulaşmağa, yahut yıllarca yürümeye kararlıyım\" demişti.'),
(2317, 2201, 18, 61, 'Kehf ', 'Mekki', 'İkisi, iki denizin birleştiği yere ulaşınca, balıklarını unutmuşlardı, balık bir delikten kayıp denizi boyladı.'),
(2318, 2202, 18, 62, 'Kehf ', 'Mekki', 'Oradan uzaklaştıklarında Musa, yanındaki gence: \"Azığımızı çıkar, and olsun bu yolculuğumuzda yorgun düştük\" dedi.'),
(2319, 2203, 18, 63, 'Kehf ', 'Mekki', 'O da: \"Bak sen! Kayalığa vardığımızda balığı unutmuştum. Bana onu hatırlamamı unutturan ancak şeytandır. Balık şaşılacak şekilde denizde yolunu tutup gitmiş\" dedi.'),
(2320, 2204, 18, 64, 'Kehf ', 'Mekki', 'Musa: \"İstediğimiz zaten buydu\" dedi. Hemen geldikleri yoldan izleri üzerinde geri döndüler.'),
(2321, 2205, 18, 65, 'Kehf ', 'Mekki', 'Bu arada ikisi katımızdan kendisine bir rahmet verdiğimiz ve kendisine ilim öğrettiğimiz kullarımızdan birini buldular.'),
(2322, 2206, 18, 66, 'Kehf ', 'Mekki', 'Musa ona: \"Sana öğretileni bana hayra götüren bir bilgi olarak öğretmen için peşinden gelebilir miyim?\" dedi.'),
(2323, 2207, 18, 67, 'Kehf ', 'Mekki', 'O: \"Sen doğrusu benim yaptıklarıma dayanamazsın, bilgice kavrayamadığın bir şeye nasıl dayanabilirsin?\" dedi.'),
(2324, 2208, 18, 68, 'Kehf ', 'Mekki', 'Musa: \"İnşallah sabrettiğimi göreceksin, sana hiçbir işte baş kaldırmayacağım\" dedi.'),
(2325, 2209, 18, 69, 'Kehf ', 'Mekki', 'O da: \"O halde, bana uyacaksan, ben sana anlatmadıkça herhangi bir şey hakkında bana soru sormayacaksın\" dedi.'),
(2326, 2210, 18, 70, 'Kehf ', 'Mekki', 'O da: \"O halde, bana uyacaksan, ben sana anlatmadıkça herhangi bir şey hakkında bana soru sormayacaksın\" dedi.'),
(2327, 2211, 18, 71, 'Kehf ', 'Mekki', 'Bunun üzerine kalkıp gittiler; sonunda bir gemiye bindiklerinde, o gemiyi deliverdi; Musa: \"Gemiyi içindekileri boğmak için mi deldin? Doğrusu şaşılacak bir şey yaptın\" dedi.'),
(2328, 2212, 18, 72, 'Kehf ', 'Mekki', 'Musa\'ya: \"Ben sana yaptığım işlere dayanamazsın demedim mi?\" dedi.'),
(2329, 2213, 18, 73, 'Kehf ', 'Mekki', 'Musa: \"Unuttuğum için bana çıkışma, gücümün yetmediği şeyden beni sorumlu tutma\" dedi.'),
(2330, 2214, 18, 74, 'Kehf ', 'Mekki', 'Yine gittiler; sonunda bir erkek çocuğa rastladılar, o hemen onu öldürdü. Musa: \"Bir cana karşılık olmaksızın masum bir cana mı kıydın? Doğrusu pek kötü bir şey yaptın\" dedi.'),
(2331, 2215, 18, 75, 'Kehf ', 'Mekki', 'O: \"Ben sana, yaptığım işlere dayanamazsın demedim mi?\" dedi.'),
(2332, 2216, 18, 76, 'Kehf ', 'Mekki', 'Musa: \"Bundan sonra sana bir şey sorarsam bana arkadaş olma, o zaman benim tarafımdan mazur sayılırsın\" dedi.'),
(2333, 2217, 18, 77, 'Kehf ', 'Mekki', 'Yine yola koyuldular; sonunda vardıkları bir kasaba halkından yiyecek istediler. Kasaba halkı, bu ikisini misafir etmek istemedi. İkisi, şehrin içinde yıkılmağa yüz tutan bir duvar gördüler, Musa\'nın arkadaşı onu doğrultuverdi; Musa: \"Dileseydin buna karşı bir ücret alabilirdin\" dedi.'),
(2334, 2218, 18, 78, 'Kehf ', 'Mekki', 'O şöyle söyledi: \"İşte bu, seninle benim ayrılmamızı gerektiriyor; dayanamadığın işlerin yorumunu sana anlatacağım\"'),
(2335, 2219, 18, 79, 'Kehf ', 'Mekki', '\"Gemi, denizde çalışan birkaç yoksula aitti; onu kusurlu kılmak istedim, çünkü peşlerinde her sağlam gemiye zorla el koyan bir hükümdar vardı.\"'),
(2336, 2220, 18, 80, 'Kehf ', 'Mekki', '\"Oğlana gelince; onun ana babası inanmış kimselerdi. Çocuğun onları azdırmasından ve inkara sürüklemesinden korkmuştuk.'),
(2337, 2221, 18, 81, 'Kehf ', 'Mekki', 'Rablerinin o çocuktan daha temiz ve onlara daha çok merhamet eden birini vermesini istedik.\"'),
(2338, 2222, 18, 82, 'Kehf ', 'Mekki', '\"Duvar ise, şehirde iki yetim erkek çocuğa aitti. Duvarın altında onların bir hazinesi vardı; babaları da iyi bir kimseydi. Rabbin onların erginlik çağına ulaşmasını ve Rabbinden bir rahmet olarak hazinelerini çıkarmalarını istedi. Ben bunları kendiliğimden yapmadım. İşte dayanamadığın işlerin içyüzleri budur.\"'),
(2339, 2223, 18, 83, 'Kehf ', 'Mekki', 'Sana Zülkarneyn\'i sorarlar, \"Onu size anlatacağım\" de.'),
(2340, 2224, 18, 84, 'Kehf ', 'Mekki', 'Doğrusu biz onu yeryüzüne yerleştirmiş ve her şeyin yolunu ona öğretmiştik.'),
(2341, 2225, 18, 85, 'Kehf ', 'Mekki', 'O da bir yol tuttu.'),
(2342, 2226, 18, 86, 'Kehf ', 'Mekki', 'Sonunda güneşin battığı yere ulaşınca onu, kara balçıklı bir suda batıyor gördü. Orada bir millete rastladı. \"Zülkarneyn! Onlara azap da edebilirsin, iyi muamelede de bulunabilirsin\" dedik.'),
(2343, 2227, 18, 87, 'Kehf ', 'Mekki', '\"Haksızlık yapana azap edeceğiz, sonra Rabbine döndürülür, onu görülmemiş bir azaba uğratır; ama inanıp yararlı iş işleyene, mükafat olarak güzel şeyler vardır, ona buyruğumuzdan kolay olanı söyleriz\" dedi.'),
(2344, 2228, 18, 88, 'Kehf ', 'Mekki', '\"Haksızlık yapana azap edeceğiz, sonra Rabbine döndürülür, onu görülmemiş bir azaba uğratır; ama inanıp yararlı iş işleyene, mükafat olarak güzel şeyler vardır, ona buyruğumuzdan kolay olanı söyleriz\" dedi.'),
(2345, 2229, 18, 89, 'Kehf ', 'Mekki', 'Sonra yine bir yol tuttu.'),
(2346, 2230, 18, 90, 'Kehf ', 'Mekki', 'Sonunda güneşin doğduğu yere ulaşınca, güneşi, kendilerini elbise, bina gibi şeylerle örtmediğimiz bir millet üzerine doğuyor buldu.'),
(2347, 2231, 18, 91, 'Kehf ', 'Mekki', 'İşte bunun gibi, onun yaptıklarının hepsini baştanbaşa biliyorduk.'),
(2348, 2232, 18, 92, 'Kehf ', 'Mekki', 'Sonra yine bir yol tuttu.'),
(2349, 2233, 18, 93, 'Kehf ', 'Mekki', 'Sonunda, iki dağın arasına varınca, orada nerdeyse hiç laf anlamayan bir millete rastladı.'),
(2350, 2234, 18, 94, 'Kehf ', 'Mekki', 'Dediler ki: Zülkarneyn! Doğrusu Yecüc ve Mecüc bu ülkede bozgunculuk yapıyorlar. Bizimle onların arasına bir sed yapman için sana bir vergi verelim mi?'),
(2351, 2235, 18, 95, 'Kehf ', 'Mekki', '\"Rabbimin bana verdikleri sizinkinden daha iyidir. Bana gücünüzle yardım edin de sizinle onların arasına sağlam bir sed yapayım. Bana demir kütleleri getirin\" dedi. Bunlar iki dağın arasını doldurunca: \"Körükleyin\" dedi. Demirler akkor haline gelince; \"Bana erimiş bakır getirin de üzerine dökeyim\" dedi.'),
(2352, 2236, 18, 96, 'Kehf ', 'Mekki', '\"Rabbimin bana verdikleri sizinkinden daha iyidir. Bana gücünüzle yardım edin de sizinle onların arasına sağlam bir sed yapayım. Bana demir kütleleri getirin\" dedi. Bunlar iki dağın arasını doldurunca: \"Körükleyin\" dedi. Demirler akkor haline gelince; \"Bana erimiş bakır getirin de üzerine dökeyim\" dedi.'),
(2353, 2237, 18, 97, 'Kehf ', 'Mekki', 'Artık Yecüc ve Mecüc onu ne aşabildiler ve ne de delip geçebildiler.'),
(2354, 2238, 18, 98, 'Kehf ', 'Mekki', 'Zülkarneyn: \"İşte bu, Rabbimin bir rahmetidir. Rabbimin tayin ettiği zaman gelince onu yerle bir eder; Rabbimin verdiği söz gerçektir\" dedi.'),
(2355, 2239, 18, 99, 'Kehf ', 'Mekki', 'Biz o gün onları bırakırız, dalgalar halinde birbirlerine girerler. Sura üflenince hepsini bir araya toplarız.'),
(2356, 2240, 18, 100, 'Kehf ', 'Mekki', 'Gözleri bizim öğüdümüze karşı kapalı olan ve öfkelerinden onu dinlemeye tahammül edemeyen kafirlere o gün cehennemi öyle bir gösteririz ki!'),
(2357, 2241, 18, 101, 'Kehf ', 'Mekki', 'Gözleri bizim öğüdümüze karşı kapalı olan ve öfkelerinden onu dinlemeye tahammül edemeyen kafirlere o gün cehennemi öyle bir gösteririz ki!'),
(2358, 2242, 18, 102, 'Kehf ', 'Mekki', 'İnkar edenler, Beni bırakıp da kullarımı dost edinmelerini yeterli mi sandılar? Doğrusu biz cehennemi inkarcılara konak olarak hazırladık.'),
(2359, 2243, 18, 103, 'Kehf ', 'Mekki', '\"Size, amelce en çok kayıpta bulunanları haber verelim mi?\" de.'),
(2360, 2244, 18, 104, 'Kehf ', 'Mekki', 'Dünya hayatında, çalışmaları boşa gitmiştir, oysa onlar güzel iş yaptıklarını sanıyorlardı.'),
(2361, 2245, 18, 105, 'Kehf ', 'Mekki', 'Bunlar, Rablerinin ayetlerini ve O\'na kavuşmayı inkar edenlerdir. Bu yüzden işleri boşa gitmiştir. Kıyamet günü Biz onlara değer vermeyeceğiz.'),
(2362, 2246, 18, 106, 'Kehf ', 'Mekki', 'İşte onların cezası; inkarlarına, peygamberlerimi ve ayetlerimi alaya almalarına karşılık olarak, cehennemdir.'),
(2363, 2247, 18, 107, 'Kehf ', 'Mekki', 'Ama inanıp yararlı iş işleyenlerin konakları Firdevs cennetleridir.'),
(2364, 2248, 18, 108, 'Kehf ', 'Mekki', 'Orada temelli kalırlar, başka bir yere gitmek istemezler.'),
(2365, 2249, 18, 109, 'Kehf ', 'Mekki', 'De ki: \"Rabbimin sözlerini yazmak için denizler mürekkep olsa ve bir o kadarını da katsak, Rabbimin sözleri tükenmeden denizler tükenirdi.\"'),
(2366, 2250, 18, 110, 'Kehf ', 'Mekki', 'De ki: \"Ben de ancak sizin gibi bir insanım; ancak bana tanrınızın tek bir Tanrı olduğu vahyolunuyor. Rabbine kavuşmayı uman kimse yararlı iş işleşin ve Rabbine kullukta hiç ortak koşmasın.\"'),
(2367, 2251, 19, 1, 'Meryem ', 'Mekki', 'Kaf, Ha, Ya, Ayn, Sad.'),
(2368, 2252, 19, 2, 'Meryem ', 'Mekki', 'Bu, Rabbinin kulu Zekeriya\'ya olan rahmetini anmadır.'),
(2369, 2253, 19, 3, 'Meryem ', 'Mekki', 'O Rabbine içinden yalvarmıştı.'),
(2370, 2254, 19, 4, 'Meryem ', 'Mekki', 'Şöyle demişti: \"Rabbim! Gerçekten kemiklerim zayıfladı, saçlarım ağardı. Rabbim! Sana yalvarmakla şimdiye kadar bedbaht olup bir şeyden mahrum kalmadım.\"'),
(2371, 2255, 19, 5, 'Meryem ', 'Mekki', 'Doğrusu, benden sonra yerime geçecek yakınlarımın iyi hareket etmeyeceklerinden korkuyorum. Karım da kısırdır. Katından bana bir oğul bağışla ki, bana ve Yakub oğullarına mirasçı olsun. Rabbim! Onun, rızanı kazanmasını da sağla.\"'),
(2372, 2256, 19, 6, 'Meryem ', 'Mekki', 'Doğrusu, benden sonra yerime geçecek yakınlarımın iyi hareket etmeyeceklerinden korkuyorum. Karım da kısırdır. Katından bana bir oğul bağışla ki, bana ve Yakub oğullarına mirasçı olsun. Rabbim! Onun, rızanı kazanmasını da sağla.\"'),
(2373, 2257, 19, 7, 'Meryem ', 'Mekki', 'Allah: \"Ey Zekeriya! Sana, Yahya isminde bir oğlanı müjdeliyoruz. Bu adı daha önce kimseye vermemiştik\" buyurdu.'),
(2374, 2258, 19, 8, 'Meryem ', 'Mekki', 'Zekeriya: \"Rabbim! Karım kısır, ben de son derece kocamışken nasıl oğlum olabilir?\" dedi.'),
(2375, 2259, 19, 9, 'Meryem ', 'Mekki', 'Allah: \"Rabbin böyle buyurdu; Çünkü bu bana kolaydır, nitekim sen yokken daha önce seni yaratmıştım\" dedi.'),
(2376, 2260, 19, 10, 'Meryem ', 'Mekki', 'Zekeriya \"Rabbim! Öyleyse bana bir alamet ver\" dedi. Allah: \"Senin alametin, sağlam ve sıhhatli olduğun halde üç gün üç gece insanlarla konuşamamandır\" buyurdu.'),
(2377, 2261, 19, 11, 'Meryem ', 'Mekki', 'Zekeriya bunun üzerine mabedden çıkıp milletine: \"Sabah akşam Allah\'ı tesbih edin\" diye işarette bulundu.'),
(2378, 2262, 19, 12, 'Meryem ', 'Mekki', '\"Ey Yahya! Kitaba kuvvetle sarıl\" deyip daha çocukken ona hikmet, katımızdan kalp yumuşaklığı ve safiyet verdik. O, Allah\'tan sakınan ve anasına babasına karşı iyi davranan bir kimse idi, baş kaldıran bir zorba değildi.'),
(2379, 2263, 19, 13, 'Meryem ', 'Mekki', '\"Ey Yahya! Kitaba kuvvetle sarıl\" deyip daha çocukken ona hikmet, katımızdan kalp yumuşaklığı ve safiyet verdik. O, Allah\'tan sakınan ve anasına babasına karşı iyi davranan bir kimse idi, baş kaldıran bir zorba değildi.'),
(2380, 2264, 19, 14, 'Meryem ', 'Mekki', '\"Ey Yahya! Kitaba kuvvetle sarıl\" deyip daha çocukken ona hikmet, katımızdan kalp yumuşaklığı ve safiyet verdik. O, Allah\'tan sakınan ve anasına babasına karşı iyi davranan bir kimse idi, baş kaldıran bir zorba değildi.'),
(2381, 2265, 19, 15, 'Meryem ', 'Mekki', 'Doğduğu günde, öleceği günde ve dirileceği günde ona selam olsun.'),
(2382, 2266, 19, 16, 'Meryem ', 'Mekki', 'Kitabda Meryem\'i de an. O, ailesinden ayrılarak, doğu yönünde bir yere çekilmişti.'),
(2383, 2267, 19, 17, 'Meryem ', 'Mekki', 'Sonra, insanlardan gizlenmek için bir perde germişti. Cebrail\'i göndermiştik de ona tam bir insan olarak görünmüştü.'),
(2384, 2268, 19, 18, 'Meryem ', 'Mekki', 'Meryem: \"Eğer Allah\'tan sakınan bir kimse isen, senden Rahman\'a sığınırım\" dedi.'),
(2385, 2269, 19, 19, 'Meryem ', 'Mekki', 'Cebrail: \"Ben temiz bir oğlan bağışlamak için Rabbinin sana gönderdiği elçiden başkası değilim\" dedi.'),
(2386, 2270, 19, 20, 'Meryem ', 'Mekki', 'Meryem: \"Bana bir insan temas etmemişken, ben kötü kadın da olmadığım halde nasıl oğlum olabilir?\" dedi.'),
(2387, 2271, 19, 21, 'Meryem ', 'Mekki', 'Cebrail: \"Bu böyledir, çünkü Rabbin, \'Bu bana kolaydır, onu insanlar için bir mucize ve katımızdan da bir rahmet kılacağız; hem bu önceden kararlaştırılmış bir iştir\' diyor\" dedi.'),
(2388, 2272, 19, 22, 'Meryem ', 'Mekki', 'Meryem oğlana gebe kaldı, o haliyle uzak bir yere çekildi.'),
(2389, 2273, 19, 23, 'Meryem ', 'Mekki', 'Doğum sancısı onu bir hurma ağacının dibine gitmeğe mecbur etti. \"Keşke ben bundan önce ölmüş olsaydım da unutulup gitseydim\" dedi.'),
(2390, 2274, 19, 24, 'Meryem ', 'Mekki', 'Onun altından bir ses kendisine şöyle seslendi: \"Sakın üzülme, Rabbin içinde bulunanı şerefli kılmıştır. Hurma ağacını kendine doğru silkele, üstüne taze hurma dökülsün.'),
(2391, 2275, 19, 25, 'Meryem ', 'Mekki', 'Onun altından bir ses kendisine şöyle seslendi: \"Sakın üzülme, Rabbin içinde bulunanı şerefli kılmıştır. Hurma ağacını kendine doğru silkele, üstüne taze hurma dökülsün.'),
(2392, 2276, 19, 26, 'Meryem ', 'Mekki', 'Ye iç, gözün aydın olsun. İnsanlardan birini görecek olursan \'Ben Rahman için oruç adadım, bugün hiçbir insanla konuşmayacağım\' de.\"'),
(2393, 2277, 19, 27, 'Meryem ', 'Mekki', 'Çocuğu alıp kavmine getirdi, onlar: \"Meryem! Utanılacak bir şey yaptın. Ey Harun\'un kızkardeşi! Baban kötü bir kimse değildi, annen de iffetsiz değildi\" dediler.'),
(2394, 2278, 19, 28, 'Meryem ', 'Mekki', 'Çocuğu alıp kavmine getirdi, onlar: \"Meryem! Utanılacak bir şey yaptın. Ey Harun\'un kızkardeşi! Baban kötü bir kimse değildi, annen de iffetsiz değildi\" dediler.'),
(2395, 2279, 19, 29, 'Meryem ', 'Mekki', 'Meryem çocuğu gösterdi. \"Biz beşikteki çocukla nasıl konuşabiliriz?\" dediler.'),
(2396, 2280, 19, 30, 'Meryem ', 'Mekki', 'Çocuk: \"Ben şüphesiz Allah\'ın kuluyum. Bana kitap verdi ve beni peygamber yaptı, nerede olursam olayım beni mübarek kıldı. Yaşadığım müddetçe namaz kılmamı, zekat vermemi ve anneme iyi davranmamı emretti. Beni bedbaht bir zorba kılmadı. Doğduğum günde, öleceğim günde, dirileceğim günde bana selam olsun\" dedi.'),
(2397, 2281, 19, 31, 'Meryem ', 'Mekki', 'Çocuk: \"Ben şüphesiz Allah\'ın kuluyum. Bana kitap verdi ve beni peygamber yaptı, nerede olursam olayım beni mübarek kıldı. Yaşadığım müddetçe namaz kılmamı, zekat vermemi ve anneme iyi davranmamı emretti. Beni bedbaht bir zorba kılmadı. Doğduğum günde, öleceğim günde, dirileceğim günde bana selam olsun\" dedi.'),
(2398, 2282, 19, 32, 'Meryem ', 'Mekki', 'Çocuk: \"Ben şüphesiz Allah\'ın kuluyum. Bana kitap verdi ve beni peygamber yaptı, nerede olursam olayım beni mübarek kıldı. Yaşadığım müddetçe namaz kılmamı, zekat vermemi ve anneme iyi davranmamı emretti. Beni bedbaht bir zorba kılmadı. Doğduğum günde, öleceğim günde, dirileceğim günde bana selam olsun\" dedi.'),
(2399, 2283, 19, 33, 'Meryem ', 'Mekki', 'Çocuk: \"Ben şüphesiz Allah\'ın kuluyum. Bana kitap verdi ve beni peygamber yaptı, nerede olursam olayım beni mübarek kıldı. Yaşadığım müddetçe namaz kılmamı, zekat vermemi ve anneme iyi davranmamı emretti. Beni bedbaht bir zorba kılmadı. Doğduğum günde, öleceğim günde, dirileceğim günde bana selam olsun\" dedi.'),
(2400, 2284, 19, 34, 'Meryem ', 'Mekki', 'İşte hakkında şüpheye düştükleri Meryem oğlu İsa gerçek söze göre budur.'),
(2401, 2285, 19, 35, 'Meryem ', 'Mekki', 'Allah çocuk edinmez, O münezzehtir. Bir işin olmasına hükmederse ona ancak \"Ol\" der, o da olur.'),
(2402, 2286, 19, 36, 'Meryem ', 'Mekki', '\"Doğrusu Allah benim de sizin de Rabbinizdir. O\'na kulluk edin, bu doğru yoldur.\"'),
(2403, 2287, 19, 37, 'Meryem ', 'Mekki', 'Fırkalar, kendi aralarında anlaşmazlığa düştüler. Vay o büyük günü görecek kafirlerin haline!'),
(2404, 2288, 19, 38, 'Meryem ', 'Mekki', 'Bize geldikleri gün neler görüp neler işitecekler! Ama zalimler bugün apaçık bir sapıklık içindedirler.'),
(2405, 2289, 19, 39, 'Meryem ', 'Mekki', 'Hala gaflet içinde bulunanları ve hala inanmayanları işin bitmiş olacağı o hasret günü ile uyar.'),
(2406, 2290, 19, 40, 'Meryem ', 'Mekki', 'Şüphesiz Biz bütün yeryüzüne ve üzerinde bulunanlara varis olacağız. Onlar Bize döneceklerdir.'),
(2407, 2291, 19, 41, 'Meryem ', 'Mekki', 'Kitap\'da İbrahim\'e dair anlattıklarımızı da an, o şüphesiz dosdoğru bir peygamberdi.'),
(2408, 2292, 19, 42, 'Meryem ', 'Mekki', 'Babasına şöyle demişti: \"Babacığım! İşitmeyen, görmeyen ve sana bir faydası olmayan şeylere niçin tapıyorsun?\"'),
(2409, 2293, 19, 43, 'Meryem ', 'Mekki', '\"Babacığım! Doğrusu sana gelmeyen bir ilim bana geldi. Bana uy, seni doğru yola eriştireyim.\"'),
(2410, 2294, 19, 44, 'Meryem ', 'Mekki', '\"Babacığım! Şeytana tapma, çünkü şeytan Rahman\'a baş kaldırmıştır\"'),
(2411, 2295, 19, 45, 'Meryem ', 'Mekki', '\"Babacığım! Doğrusu sana Rahman katından bir azabın gelmesinden korkuyorum ki böylece şeytanın dostu olarak kalırsın.\"'),
(2412, 2296, 19, 46, 'Meryem ', 'Mekki', 'Babası: \"Ey İbrahim! Sen benim tanrılarımdan yüz çevirmek mi istiyorsun? Bundan vazgeçmezsen mutlaka seni taşlarım; uzun bir süre benden uzaklaş git.\" dedi.'),
(2413, 2297, 19, 47, 'Meryem ', 'Mekki', 'İbrahim şöyle cevap verdi: \"Sana selam olsun. Senin için Rabbim\'den mağfiret dileyeceğim, çünkü O, bana karşı çok lütufkardır.\"'),
(2414, 2298, 19, 48, 'Meryem ', 'Mekki', '\"Sizi Allah\'tan başka taptıklarınızla bırakıp çekilir, Rabbime yalvarırım. Rabbime yalvarışımda mahrum kalmayacağımı umarım.\"'),
(2415, 2299, 19, 49, 'Meryem ', 'Mekki', 'İbrahim onları Allah\'tan başka taptıklarıyla başbaşa bırakıp çekilince ona İshak ve Yakub\'u bahşettik ve her birini peygamber yaptık.'),
(2416, 2300, 19, 50, 'Meryem ', 'Mekki', 'Onlara rahmetimizden bağışta bulunduk. Onların her dilde üstün şekilde anılmalarını sağladık.'),
(2417, 2301, 19, 51, 'Meryem ', 'Mekki', 'Kitap\'da Musa\'ya dair anlattıklarımızı da an. O seçkin kılınmış bir insan, tarafımızdan gönderilmiş bir peygamberdi.'),
(2418, 2302, 19, 52, 'Meryem ', 'Mekki', 'Ona Tur\'un sağ yanından seslenmiş ve konuşmak için onu yaklaştırmıştık.'),
(2419, 2303, 19, 53, 'Meryem ', 'Mekki', 'Rahmetimizden, kardeşi Harun\'u bir peygamber olarak ona bağışladık.'),
(2420, 2304, 19, 54, 'Meryem ', 'Mekki', 'Kitap\'da İsmail\'e dair anlattıklarımızı da an. Çünkü o sözünde doğru bir kimse idi, tarafımızdan gönderilmiş bir peygamberdi.'),
(2421, 2305, 19, 55, 'Meryem ', 'Mekki', 'Çevresinde bulunanlara namaz kılmalarını, zekat vermelerini emrederdi. Rabbinin katında hoşnutluğa ermişti.'),
(2422, 2306, 19, 56, 'Meryem ', 'Mekki', 'Kitap\'da İdris\'i de zikret, çünkü o dosdoğru bir peygamberdi.'),
(2423, 2307, 19, 57, 'Meryem ', 'Mekki', 'Biz onu yüce bir yere yükselttik.'),
(2424, 2308, 19, 58, 'Meryem ', 'Mekki', 'İşte bunlar Allah\'ın kendilerine nimetler sunduğu peygamberler; Adem\'in soyundan, Nuh ile beraber taşıdıklarımızdan; İbrahim ve İsmail\'in neslinden ve doğru yola erdirdiğimizden, seçip beğendiklerimizdendirler. Rahman\'ın ayetleri onlara okunduğu zaman ağlayarak secdeye kapanırlardı.'),
(2425, 2309, 19, 59, 'Meryem ', 'Mekki', 'Onların ardından, namazı bırakan, şehvetlerine uyan bir nesil geldi. İşte bunlar azgınlıklarının karşılığını göreceklerdir.'),
(2426, 2310, 19, 60, 'Meryem ', 'Mekki', 'Ancak tevbe eden, inanıp yararlı iş yapanlar bunun dışındadır. Bunlar hiçbir haksızlığa uğratılmadan, Rahman\'ın kullarına gaybde vadettiği cennete, Adn cennetlerine gireceklerdir. Şüphesiz, O\'nun sözü yerini bulacaktır.'),
(2427, 2311, 19, 61, 'Meryem ', 'Mekki', 'Ancak tevbe eden, inanıp yararlı iş yapanlar bunun dışındadır. Bunlar hiçbir haksızlığa uğratılmadan, Rahman\'ın kullarına gaybde vadettiği cennete, Adn cennetlerine gireceklerdir. Şüphesiz, O\'nun sözü yerini bulacaktır.'),
(2428, 2312, 19, 62, 'Meryem ', 'Mekki', 'Orada boş sözler değil sadece esenlik veren sözler işitirler. Orada rızıklarını sabah akşam hazır bulurlar.'),
(2429, 2313, 19, 63, 'Meryem ', 'Mekki', 'Kullarımızdan Allah\'a karşı gelmekten sakınanları mirasçı kılacağımız Cennet işte budur.'),
(2430, 2314, 19, 64, 'Meryem ', 'Mekki', 'Cebrail: \"Biz ancak Rabbinin buyruğu ile ineriz, geçmişimizi geleceğimizi ve ikisinin arasındakileri bilmek O\'na mahsustur. Rabbin unutkan değildir.\"'),
(2431, 2315, 19, 65, 'Meryem ', 'Mekki', 'O, göklerin, yerin ve ikisi arasında bulunanların Rabbidir. Öyleyse Ona ibadette sabırlı ol. Hiç O\'na benzeyen bir şey bilir misin?'),
(2432, 2316, 19, 66, 'Meryem ', 'Mekki', 'İnsan: \"Ben öldüğümde mi diriltileceğim?\" der.'),
(2433, 2317, 19, 67, 'Meryem ', 'Mekki', 'Bu insan kendisi önceden bir şey değilken onu yaratmış olduğumuzu hatırlamaz mi?'),
(2434, 2318, 19, 68, 'Meryem ', 'Mekki', 'Rabbine and olsun ki Biz onları mutlaka uydukları şeytanlarla beraber haşredeceğiz. Sonra cehennemin yanında diz çöktürerek hazır bulunduracağız.'),
(2435, 2319, 19, 69, 'Meryem ', 'Mekki', 'Sonra her toplumdan Rahman\'a en çok kimin baş kaldırdığını ortaya koyacağız.'),
(2436, 2320, 19, 70, 'Meryem ', 'Mekki', 'Cehenneme girmeye en layık olanları Biz biliriz.'),
(2437, 2321, 19, 71, 'Meryem ', 'Mekki', 'Sizden cehenneme uğramayacak yoktur. Bu, Rabbinin yapmayı üzerine aldığı kesinleşmiş bir hükümdür.'),
(2438, 2322, 19, 72, 'Meryem ', 'Mekki', 'Sonra Biz Allah\'a karşı gelmekten sakınmış olanları kurtarır, zalimleri de orada diz üstü çökmüş olarak bırakırız.'),
(2439, 2323, 19, 73, 'Meryem ', 'Mekki', 'Ayetlerimiz kendilerine apaçık okunduğu zaman inkar edenler inananlara: \"Bu iki takımın hangisinin makamı daha iyi ve yeri daha güzeldir?\" derler.'),
(2440, 2324, 19, 74, 'Meryem ', 'Mekki', 'Onlardan önce nice nesilleri yok ettik ki, onlar varlıkça ve gösterişçe bunlardan daha üstündüler.'),
(2441, 2325, 19, 75, 'Meryem ', 'Mekki', 'De ki: \"Sapıklıkta olanı Rahman ne kadar ertelese bile, sonunda tehdit edildikleri azabı ya da kıyamet gününü gördükleri zaman onlar kimin yerinin daha kötü ve taraftarlarının daha güçsüz olduğunu bilecektir.\"'),
(2442, 2326, 19, 76, 'Meryem ', 'Mekki', 'Allah doğru yolda olanların doğruluğunu artırır. Baki kalacak yararlı işler Rabbinin katında sevap olarak da daha iyidir, sonuç olarak da daha iyidir.'),
(2443, 2327, 19, 77, 'Meryem ', 'Mekki', 'Ayetlerimizi inkar eden ve \"bana elbette mal ve çocuk verilecektir\" diyeni gördün mu?'),
(2444, 2328, 19, 78, 'Meryem ', 'Mekki', 'O görülmeyeni mi biliyor, yoksa Rahman katından bir söz mü almıştır?'),
(2445, 2329, 19, 79, 'Meryem ', 'Mekki', 'Hayır, söylediğini yazacağız ve onun azabını uzattıkça uzatacağız.'),
(2446, 2330, 19, 80, 'Meryem ', 'Mekki', 'Bahsettikleri şeyler Bize kalacaktır, kendisi Bize tek olarak gelecektir.'),
(2447, 2331, 19, 81, 'Meryem ', 'Mekki', 'Onlar kendilerine kuvvet ve şeref kazandırsın diye, Allah\'ı bırakarak tanrılar edindiler.'),
(2448, 2332, 19, 82, 'Meryem ', 'Mekki', 'Hayır, tanrıları kendilerinin ibadetlerini inkar edecekler ve onlara düşman olacaklardır.'),
(2449, 2333, 19, 83, 'Meryem ', 'Mekki', 'Kafirlerin üzerine onları kışkırtan şeytanlar gönderdiğimizi bilmiyor musun?'),
(2450, 2334, 19, 84, 'Meryem ', 'Mekki', 'Öyleyse onların acele yok olmalarını isteme. Biz onların günlerini saydıkça sayıyoruz.'),
(2451, 2335, 19, 85, 'Meryem ', 'Mekki', 'sakınanları o gün Rahman\'ın huzurunda O\'na gelmiş konuklar olarak toplarız, suçluları suya götürür gibi cehenneme süreriz.'),
(2452, 2336, 19, 86, 'Meryem ', 'Mekki', 'sakınanları o gün Rahman\'ın huzurunda O\'na gelmiş konuklar olarak toplarız, suçluları suya götürür gibi cehenneme süreriz.'),
(2453, 2337, 19, 87, 'Meryem ', 'Mekki', 'Rahman\'ın katında bir ahd almış olandan başkası asla şefaatte bulunamıyacaktır.'),
(2454, 2338, 19, 88, 'Meryem ', 'Mekki', 'Bazı kimseler: \"Rahman çocuk edindi\" dediler'),
(2455, 2339, 19, 89, 'Meryem ', 'Mekki', 'And olsun ki, ortaya pek kötü bir şey attınız.'),
(2456, 2340, 19, 90, 'Meryem ', 'Mekki', 'Rahman\'a çocuk isnat etmelerinden ötürü neredeyse gökler parçalanacak, yer yarılacak, dağlar göçecekti.'),
(2457, 2341, 19, 91, 'Meryem ', 'Mekki', 'Rahman\'a çocuk isnat etmelerinden ötürü neredeyse gökler parçalanacak, yer yarılacak, dağlar göçecekti.'),
(2458, 2342, 19, 92, 'Meryem ', 'Mekki', 'Oysa Rahman\'a çocuk edinmek yaraşmaz, çünkü göklerde ve yerde olan her şey Rahman\'a baş eğmiş kul olarak gelecektir.'),
(2459, 2343, 19, 93, 'Meryem ', 'Mekki', 'Oysa Rahman\'a çocuk edinmek yaraşmaz, çünkü göklerde ve yerde olan her şey Rahman\'a baş eğmiş kul olarak gelecektir.'),
(2460, 2344, 19, 94, 'Meryem ', 'Mekki', 'And olsun ki onların adedini bilmiş ve teker teker saymıştır.'),
(2461, 2345, 19, 95, 'Meryem ', 'Mekki', 'Kıyamet günü hepsi O\'na tek olarak gelecektir.'),
(2462, 2346, 19, 96, 'Meryem ', 'Mekki', 'İnanıp yararlı iş işleyenleri Rahman sevgili kılacaktır.'),
(2463, 2347, 19, 97, 'Meryem ', 'Mekki', 'Biz Kuran\'ı Allah\'a karşı gelmekten sakınanları müjdelemen ve inatçı milleti uyarman için senin dilinde indirerek kolaylaştırdık.'),
(2464, 2348, 19, 98, 'Meryem ', 'Mekki', 'Onlardan önce nice nesilleri yok ettik, şimdi onlardan hiçbirini duyuyor veya bir ses işitiyor musun?'),
(2465, 2349, 20, 1, 'Tâ-Hâ ', 'Mekki', 'Ta, Ha.'),
(2466, 2350, 20, 2, 'Tâ-Hâ ', 'Mekki', 'Kuran\'ı sana, sıkıntıya düşeşin diye değil, ancak Allah\'tan korkanlara bir öğüt ve yeri ve yüce gökleri yaratanın katından bir Kitap olarak indirdik.'),
(2467, 2351, 20, 3, 'Tâ-Hâ ', 'Mekki', 'Kuran\'ı sana, sıkıntıya düşeşin diye değil, ancak Allah\'tan korkanlara bir öğüt ve yeri ve yüce gökleri yaratanın katından bir Kitap olarak indirdik.'),
(2468, 2352, 20, 4, 'Tâ-Hâ ', 'Mekki', 'Kuran\'ı sana, sıkıntıya düşeşin diye değil, ancak Allah\'tan korkanlara bir öğüt ve yeri ve yüce gökleri yaratanın katından bir Kitap olarak indirdik.'),
(2469, 2353, 20, 5, 'Tâ-Hâ ', 'Mekki', 'Rahman arşa hükmetmektedir.'),
(2470, 2354, 20, 6, 'Tâ-Hâ ', 'Mekki', 'Göklerde ve yerde, her ikisi arasında ve toprağın altında bulunanlar O\'nundur.'),
(2471, 2355, 20, 7, 'Tâ-Hâ ', 'Mekki', 'Sen sözü istersen açığa vur, şüphesiz O gizliyi de, gizlinin gizlisini de bilir.'),
(2472, 2356, 20, 8, 'Tâ-Hâ ', 'Mekki', 'Allah\'tan başka tanrı yoktur, en güzel isimler O\'nundur.'),
(2473, 2357, 20, 9, 'Tâ-Hâ ', 'Mekki', 'Musa\'nın başından geçen olay sana geldi mi?'),
(2474, 2358, 20, 10, 'Tâ-Hâ ', 'Mekki', 'O, bir ateş görmüştü de, ailesine: \"Durun, ben bir ateş gördüm, ya ondan size bir kor getirir, ya da ateşin yanında bir yol gösteren bulurum\" demişti.'),
(2475, 2359, 20, 11, 'Tâ-Hâ ', 'Mekki', 'Musa ateşin yanına gelince: \"Ey Musa!\" diye seslenildi:'),
(2476, 2360, 20, 12, 'Tâ-Hâ ', 'Mekki', '\"Ben şüphesiz senin Rabbinim; ayağındakileri çıkar; çünkü sen, kutsal bir vadi olan Tuva\'dasın.\"'),
(2477, 2361, 20, 13, 'Tâ-Hâ ', 'Mekki', '\"Ben seni seçtim; artık vahyolunanları dinle.\"'),
(2478, 2362, 20, 14, 'Tâ-Hâ ', 'Mekki', '\"Şüphesiz Ben Allah\'ım, Benden başka tanrı yoktur; Bana kulluk et; Beni anmak için namaz kıl.\"'),
(2479, 2363, 20, 15, 'Tâ-Hâ ', 'Mekki', 'Herkes işlediğinin karşılığını görsün diye, zamanını gizli tuttuğum kıyamet mutlaka gelecektir.'),
(2480, 2364, 20, 16, 'Tâ-Hâ ', 'Mekki', '\"Buna inanmayan ve hevesine uyan kimse seni ondan alıkoymasın, yoksa helak olursun.\"'),
(2481, 2365, 20, 17, 'Tâ-Hâ ', 'Mekki', '\"Ey Musa! Sağ elindeki nedir?\"'),
(2482, 2366, 20, 18, 'Tâ-Hâ ', 'Mekki', 'Musa: \"O benim değneğimdir, ona dayanırım, onunla davarıma yaprak silkerim, ondan daha birçok işlerde faydalanırım\" dedi.'),
(2483, 2367, 20, 19, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Ey Musa! Bırak onu\" dedi.'),
(2484, 2368, 20, 20, 'Tâ-Hâ ', 'Mekki', 'Bırakınca, değnek hemen, koşan bir yılan oluverdi.'),
(2485, 2369, 20, 21, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Onu al, korkma; biz onu yine eski durumuna çevireceğiz. Daha büyük mucizelerimizi sana göstermemiz için elini koltuğunun altına koy da, diğer bir mucize olarak, kusursuz, bembeyaz çıksın\" dedi.'),
(2486, 2370, 20, 22, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Onu al, korkma; biz onu yine eski durumuna çevireceğiz. Daha büyük mucizelerimizi sana göstermemiz için elini koltuğunun altına koy da, diğer bir mucize olarak, kusursuz, bembeyaz çıksın\" dedi.'),
(2487, 2371, 20, 23, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Onu al, korkma; biz onu yine eski durumuna çevireceğiz. Daha büyük mucizelerimizi sana göstermemiz için elini koltuğunun altına koy da, diğer bir mucize olarak, kusursuz, bembeyaz çıksın\" dedi.'),
(2488, 2372, 20, 24, 'Tâ-Hâ ', 'Mekki', '\"Firavun\'a git, doğrusu o azmıştır.\"'),
(2489, 2373, 20, 25, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2490, 2374, 20, 26, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2491, 2375, 20, 27, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2492, 2376, 20, 28, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2493, 2377, 20, 29, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2494, 2378, 20, 30, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2495, 2379, 20, 31, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2496, 2380, 20, 32, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2497, 2381, 20, 33, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2498, 2382, 20, 34, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2499, 2383, 20, 35, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbim! Göğsümü genişlet, işimi kolaylaştır, dilimin düğümünü çöz ki sözümü iyi anlasınlar. Ailemden kardeşim Harun\'u bana vezir yap, beni onunla destekle, onu görevimde ortak kıl ki Seni daha çok tesbih edelim ve çokça analım. Şüphesiz Sen bizi görmektesin\" dedi.'),
(2500, 2384, 20, 36, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Ey Musa! İstediğin sana verildi\" dedi, \"Zaten sana başka bir defa da iyilikte bulunmuş ve annene vahyedilmesi gerekeni vahyetmiştik: Musa\'yı bir sandığa koy da suya bırak; su onu kıyıya atar, Bana da, ona da düşman olan biri onu alır. Ey Musa! Gözümün önünde yetişesin diye seni sevimli kıldım.\"'),
(2501, 2385, 20, 37, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Ey Musa! İstediğin sana verildi\" dedi, \"Zaten sana başka bir defa da iyilikte bulunmuş ve annene vahyedilmesi gerekeni vahyetmiştik: Musa\'yı bir sandığa koy da suya bırak; su onu kıyıya atar, Bana da, ona da düşman olan biri onu alır. Ey Musa! Gözümün önünde yetişesin diye seni sevimli kıldım.\"'),
(2502, 2386, 20, 38, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Ey Musa! İstediğin sana verildi\" dedi, \"Zaten sana başka bir defa da iyilikte bulunmuş ve annene vahyedilmesi gerekeni vahyetmiştik: Musa\'yı bir sandığa koy da suya bırak; su onu kıyıya atar, Bana da, ona da düşman olan biri onu alır. Ey Musa! Gözümün önünde yetişesin diye seni sevimli kıldım.\"'),
(2503, 2387, 20, 39, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Ey Musa! İstediğin sana verildi\" dedi, \"Zaten sana başka bir defa da iyilikte bulunmuş ve annene vahyedilmesi gerekeni vahyetmiştik: Musa\'yı bir sandığa koy da suya bırak; su onu kıyıya atar, Bana da, ona da düşman olan biri onu alır. Ey Musa! Gözümün önünde yetişesin diye seni sevimli kıldım.\"'),
(2504, 2388, 20, 40, 'Tâ-Hâ ', 'Mekki', 'Kızkardeşin Firavun\'un sarayına giderek: \"Ona bakacak birini size göstereyim mi?\" diyordu. Böylece, annen üzülmesin, sevinsin diye, seni ona iade etmiştik. Sen bir cana kıymıştın, seni üzüntüden kurtarmış ve seni birçok musibetlerle denemiştik. Bunun için, Medyen halkı arasında yıllarca kalmıştın. Sonra, ey Musa, peygamberlik görevini yüklenecek bir yaşa gelince dönüp geldin.'),
(2505, 2389, 20, 41, 'Tâ-Hâ ', 'Mekki', 'Seni kendim için ayırdım.'),
(2506, 2390, 20, 42, 'Tâ-Hâ ', 'Mekki', 'Sen ve kardeşin, ayetlerimle gidin; beni anmakta gevşek davranmayın.'),
(2507, 2391, 20, 43, 'Tâ-Hâ ', 'Mekki', 'Firavun\'a gidin, doğrusu o azmıştır.'),
(2508, 2392, 20, 44, 'Tâ-Hâ ', 'Mekki', 'Ona yumuşak söz söyleyin, belki öğüt dinler veya korkar.'),
(2509, 2393, 20, 45, 'Tâ-Hâ ', 'Mekki', 'Musa ve kardeşi: \"Rabbimiz! Onun bize kötülük etmesinden veya azgınlığının artmasından korkarız\" dediler.'),
(2510, 2394, 20, 46, 'Tâ-Hâ ', 'Mekki', 'Allah: Korkmayın, dedi; Ben sizinle beraberim; görür ve işitirim. Ona gidin şöyle söyleyin: \"Doğrusu biz senin Rabbinin elçileriyiz. İsrailoğullarını bizimle beraber gönder, onlara azabetme; Rabbinden sana bir mucize getirdik; selam, doğru yolda gidene olsun! Doğrusu bize, yalanlayıp sırt çevirene azap edileceği vahyolundu.\"'),
(2511, 2395, 20, 47, 'Tâ-Hâ ', 'Mekki', 'Allah: Korkmayın, dedi; Ben sizinle beraberim; görür ve işitirim. Ona gidin şöyle söyleyin: \"Doğrusu biz senin Rabbinin elçileriyiz. İsrailoğullarını bizimle beraber gönder, onlara azabetme; Rabbinden sana bir mucize getirdik; selam, doğru yolda gidene olsun! Doğrusu bize, yalanlayıp sırt çevirene azap edileceği vahyolundu.\"'),
(2512, 2396, 20, 48, 'Tâ-Hâ ', 'Mekki', 'Allah: Korkmayın, dedi; Ben sizinle beraberim; görür ve işitirim. Ona gidin şöyle söyleyin: \"Doğrusu biz senin Rabbinin elçileriyiz. İsrailoğullarını bizimle beraber gönder, onlara azabetme; Rabbinden sana bir mucize getirdik; selam, doğru yolda gidene olsun! Doğrusu bize, yalanlayıp sırt çevirene azap edileceği vahyolundu.\"'),
(2513, 2397, 20, 49, 'Tâ-Hâ ', 'Mekki', 'Firavun: \"Musa! Rabbiniz kimdir?\" dedi.'),
(2514, 2398, 20, 50, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Rabbimiz, her şeye ayrı bir özellik veren, sonra doğru yola eriştirendir\" dedi.'),
(2515, 2399, 20, 51, 'Tâ-Hâ ', 'Mekki', 'Firavun: \"Öyleyse önceki nesillerin durumu ne oluyor?\" dedi.'),
(2516, 2400, 20, 52, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Onların bilgisi Rabbimin katında yazılıdır. Rabbim şaşırmaz ve unutmaz.\" dedi.'),
(2517, 2401, 20, 53, 'Tâ-Hâ ', 'Mekki', 'Sizin için yeryüzünü döşeyen, yollar açan, gökten su indiren O\'dur. Biz o su ile türlü türlü, çift çift bitkiler yetiştirdik.'),
(2518, 2402, 20, 54, 'Tâ-Hâ ', 'Mekki', 'İster yiyin, ister hayvanlarınızı otlatın, onlarda akıl sahipleri için şüphesiz dersler vardır.'),
(2519, 2403, 20, 55, 'Tâ-Hâ ', 'Mekki', 'Sizi yerden yarattık, oraya döndüreceğiz, sizi tekrar oradan çıkaracağız.'),
(2520, 2404, 20, 56, 'Tâ-Hâ ', 'Mekki', 'And olsun ki Firavun\'a bütün delillerimizi gösterdik de yalan sayıp kabulden çekindi ve: \"Ey Musa! Sihirbazlığınla bizi yurdumuzdan çıkarmaya mı geldin? Şimdi biz de seninkinin benzeri bir sihri sana göstereceğiz. Bizimle senin aranda bir vakit tayinet ki sen de biz de düz bir yerde bulunalım da caymayalım\" dedi.'),
(2521, 2405, 20, 57, 'Tâ-Hâ ', 'Mekki', 'And olsun ki Firavun\'a bütün delillerimizi gösterdik de yalan sayıp kabulden çekindi ve: \"Ey Musa! Sihirbazlığınla bizi yurdumuzdan çıkarmaya mı geldin? Şimdi biz de seninkinin benzeri bir sihri sana göstereceğiz. Bizimle senin aranda bir vakit tayinet ki sen de biz de düz bir yerde bulunalım da caymayalım\" dedi.'),
(2522, 2406, 20, 58, 'Tâ-Hâ ', 'Mekki', 'And olsun ki Firavun\'a bütün delillerimizi gösterdik de yalan sayıp kabulden çekindi ve: \"Ey Musa! Sihirbazlığınla bizi yurdumuzdan çıkarmaya mı geldin? Şimdi biz de seninkinin benzeri bir sihri sana göstereceğiz. Bizimle senin aranda bir vakit tayinet ki sen de biz de düz bir yerde bulunalım da caymayalım\" dedi.'),
(2523, 2407, 20, 59, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Buluşma zamanımız sizin bayram gününüzde, insanların toplandığı kuşluk vaktidir\" dedi.'),
(2524, 2408, 20, 60, 'Tâ-Hâ ', 'Mekki', 'Firavun döndü, tuzaklarını toplayıp o gün geldi.'),
(2525, 2409, 20, 61, 'Tâ-Hâ ', 'Mekki', 'Musa onlara: \"Size yazıklar olsun! Allah\'a karşı yalan uydurmayın, yoksa sizi azabla yok eder. Allah\'a iftira eden hüsrana uğrar\" dedi.'),
(2526, 2410, 20, 62, 'Tâ-Hâ ', 'Mekki', 'Sihirbazlar işi aralarında tartıştılar ve konuşmalarını gizli tuttular.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(2527, 2411, 20, 63, 'Tâ-Hâ ', 'Mekki', 'Musa ile Harun\'u göstererek: \"Bu iki sihirbaz, sihirleriyle sizi yurdunuzdan çıkarmak, sizin en üstün dininizi ortadan kaldırmak istiyorlar; onun için tuzaklarınızı bir araya getirin, sonra sırayla gelin. Bugün üstün gelen başarıya erecektir\" dediler.'),
(2528, 2412, 20, 64, 'Tâ-Hâ ', 'Mekki', 'Musa ile Harun\'u göstererek: \"Bu iki sihirbaz, sihirleriyle sizi yurdunuzdan çıkarmak, sizin en üstün dininizi ortadan kaldırmak istiyorlar; onun için tuzaklarınızı bir araya getirin, sonra sırayla gelin. Bugün üstün gelen başarıya erecektir\" dediler.'),
(2529, 2413, 20, 65, 'Tâ-Hâ ', 'Mekki', '\"Ey Musa! Marifetini ya sen ortaya koy, ya da önce biz koyalım\" dediler.'),
(2530, 2414, 20, 66, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Siz koyun\" dedi. Hemen, değnekleri ve ipleri, sihirleri yüzünden, Musa\'ya sanki yürüyorlarmış gibi geldi.'),
(2531, 2415, 20, 67, 'Tâ-Hâ ', 'Mekki', 'Bu yüzden Musa içinde bir korku hissetti.'),
(2532, 2416, 20, 68, 'Tâ-Hâ ', 'Mekki', '\"Korkma, sen muhakkak daha üstünsün\" dedik.'),
(2533, 2417, 20, 69, 'Tâ-Hâ ', 'Mekki', '\"Sağ elindekini at da onların yaptıklarını yutsun, yaptıkları sadece sihirbaz düzenidir. Sihirbaz nereden gelirse gelsin başarı kazanamaz.\"'),
(2534, 2418, 20, 70, 'Tâ-Hâ ', 'Mekki', 'Sonunda sihirbazlar: \"Biz Musa ve Harun\'un Rabbine inandık\" deyip secdeye kapandılar.'),
(2535, 2419, 20, 71, 'Tâ-Hâ ', 'Mekki', 'Firavun \"Ben size izin vermeden mi O\'na inandınız? Doğrusu size sihri öğreten, büyüğünüz odur. And olsun ki, ellerinizi ve ayaklarınızı çaprazlama keseceğim, sizi hurma kütüklerine asacağım. Hangimizin azabının daha çetin ve daha devamlı olduğunu bileceksiniz\" dedi.'),
(2536, 2420, 20, 72, 'Tâ-Hâ ', 'Mekki', 'İman eden sihirbazlar: \"Seni, gelen apaçık mucizelere ve bizi yaratana üstün tutmayacağız. Ne hüküm vereceksen ver. Sen, ancak bu dünya hayatına hükmedebilirsin. Doğrusu biz, yanılmalarımızı ve bize zorla yaptırdığın sihri bağışlaması için Rabbimize iman ettik. Allah\'ın vereceği mükafat daha iyi ve daha devamlıdır\" dediler.'),
(2537, 2421, 20, 73, 'Tâ-Hâ ', 'Mekki', 'İman eden sihirbazlar: \"Seni, gelen apaçık mucizelere ve bizi yaratana üstün tutmayacağız. Ne hüküm vereceksen ver. Sen, ancak bu dünya hayatına hükmedebilirsin. Doğrusu biz, yanılmalarımızı ve bize zorla yaptırdığın sihri bağışlaması için Rabbimize iman ettik. Allah\'ın vereceği mükafat daha iyi ve daha devamlıdır\" dediler.'),
(2538, 2422, 20, 74, 'Tâ-Hâ ', 'Mekki', 'Rabbine suçlu olarak gelen bilsin ki, cehennem onun içindir. Orada ne ölür, ne yaşar.'),
(2539, 2423, 20, 75, 'Tâ-Hâ ', 'Mekki', 'Rabbine inanmış ve yararlı iş yaparak gelenlere, işte onlara, en üstün dereceler, içlerinden ırmaklar akan, içinde temelli kalacakları Adn cennetleri vardır. Bu, arınanların mükafatıdır.'),
(2540, 2424, 20, 76, 'Tâ-Hâ ', 'Mekki', 'Rabbine inanmış ve yararlı iş yaparak gelenlere, işte onlara, en üstün dereceler, içlerinden ırmaklar akan, içinde temelli kalacakları Adn cennetleri vardır. Bu, arınanların mükafatıdır.'),
(2541, 2425, 20, 77, 'Tâ-Hâ ', 'Mekki', 'And olsun ki Musa\'ya: \"Kullarımı geceleyin yürüt, denizde onlara kuru bir yol aç, batmaktan ve düşmanların yetişmesinden korkma, endişe etme\" diye vahyettik.'),
(2542, 2426, 20, 78, 'Tâ-Hâ ', 'Mekki', 'Firavun, ordusuyla onları takip etti, deniz de onları içine alıverdi, hem de ne alış!'),
(2543, 2427, 20, 79, 'Tâ-Hâ ', 'Mekki', 'Firavun, milletini saptırdı, onlara doğru yolu göstermedi.'),
(2544, 2428, 20, 80, 'Tâ-Hâ ', 'Mekki', 'Ey İsrailoğulları! Sizleri düşmanınızdan kurtardık, Tur\'un sağ yanını size vadettik ve üzerinize kudret helvasıyla bıldırcın indirdik.'),
(2545, 2429, 20, 81, 'Tâ-Hâ ', 'Mekki', 'Size verdiğimiz rızıkların temizlerinden yiyin, bunda aşırı gitmeyin ki gazabımı haketmeyesiniz. Gazabımı hakeden kimse muhakkak mahvolur.'),
(2546, 2430, 20, 82, 'Tâ-Hâ ', 'Mekki', 'Doğrusu Ben, tevbe edeni, inanıp yararlı iş işleyerek doğru yola gireni bağışlarım.'),
(2547, 2431, 20, 83, 'Tâ-Hâ ', 'Mekki', '\"Musa! Seni milletinden daha çabuk gelmeye sevkeden nedir?\" dedik.'),
(2548, 2432, 20, 84, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Onlar ardımdadır, Rabbim! Hoşnut olman için Sana acele geldim\" dedi.'),
(2549, 2433, 20, 85, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Doğrusu Biz, senden sonra milletini sınadık; Samiri onları saptırdı\" dedi.'),
(2550, 2434, 20, 86, 'Tâ-Hâ ', 'Mekki', 'Musa, milletine kızgın ve üzgün olarak döndü. \"Ey milletim! Rabbiniz size güzel bir vaadde bulunmadı mı? Uzun bir zaman mı geçti, yoksa Rabbinizin gazabına mı uğramak istediniz de bana verdiğiniz sözden caydınız?\" dedi.'),
(2551, 2435, 20, 87, 'Tâ-Hâ ', 'Mekki', 'Onlar: \"Sana verdiğimiz sözden kendi başımıza caymadık. O milletin ziynet eşyasından bize yükler dolusu taşıtıldı. Biz onları ateşe attık, aynı şekilde Samiri de attı\" dediler.'),
(2552, 2436, 20, 88, 'Tâ-Hâ ', 'Mekki', 'Bunun üzerine Samiri onlara böğüren bir buzağı heykeli ortaya koydu. O ve adamları: \"Bu sizin de Musa\'nın da tanrısıdır, ama o unuttu\" dediler.'),
(2553, 2437, 20, 89, 'Tâ-Hâ ', 'Mekki', 'Görmüyorlar mıydı ki, o heykel onlara ne söz söyleyebilir, ne zarar ve ne de fayda verebilirdi?'),
(2554, 2438, 20, 90, 'Tâ-Hâ ', 'Mekki', 'And olsun ki, Harun da onlara önceden: \"Ey milletim! Siz bu buzağı ile sınanıyorsunuz. Sizin gerçek Rabbiniz Rahman\'dır. Bana uyun, emrime itaat edin\" demişti.'),
(2555, 2439, 20, 91, 'Tâ-Hâ ', 'Mekki', '\"Musa bize dönene kadar buna sarılmaktan vazgeçmeyeceğiz\" demişlerdi.'),
(2556, 2440, 20, 92, 'Tâ-Hâ ', 'Mekki', 'Musa gelince: \"Harun! Onların sapıttığını görünce seni benim yolumdan gitmekten alıkoyan nedir? Benim emrime karşı mı geldin?\" dedi.'),
(2557, 2441, 20, 93, 'Tâ-Hâ ', 'Mekki', 'Musa gelince: \"Harun! Onların sapıttığını görünce seni benim yolumdan gitmekten alıkoyan nedir? Benim emrime karşı mı geldin?\" dedi.'),
(2558, 2442, 20, 94, 'Tâ-Hâ ', 'Mekki', 'Harun: \"Ey Annemoğlu! Saçımdan sakalımdan tutma; doğrusu İsrailoğulları arasına ayrılık koydun, sözüme bakmadın demenden korktum\" dedi.'),
(2559, 2443, 20, 95, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Ey Samiri! Ya senin yaptığın nedir?\" dedi.'),
(2560, 2444, 20, 96, 'Tâ-Hâ ', 'Mekki', 'Samiri: \"Onların görmedikleri bir şey gördüm ve o sana gelen elçinin bastığı yerden bir avuç avuçladım. Bunu ziynet eşyasının eritildiği potaya attım. Nefsim böyle yaptırdı\" dedi.'),
(2561, 2445, 20, 97, 'Tâ-Hâ ', 'Mekki', 'Musa: \"Defol! Doğrusu artık hayatta, \"Bana dokunmayın!\" demenden başka yapacağın yoktur. Senin için asla kaçamayacağın bir ceza daha vardır. Durup üzerinde titrediğin tanrına bak, onu yakacağız, sonra denize dökeceğiz\" dedi.'),
(2562, 2446, 20, 98, 'Tâ-Hâ ', 'Mekki', 'Sizin Tanrınız, ancak, O\'ndan başka tanrı olmayan Allah\'tır. İlmi her şeyi içine almıştır.'),
(2563, 2447, 20, 99, 'Tâ-Hâ ', 'Mekki', 'Geçmiş olayları sana böyle anlatırız. Katımızdan sana da bir Kitap verdik; kim ondan yüz çevirirse bilsin ki kıyamet günü bir günah yükü yüklenecektir.'),
(2564, 2448, 20, 100, 'Tâ-Hâ ', 'Mekki', 'Geçmiş olayları sana böyle anlatırız. Katımızdan sana da bir Kitap verdik; kim ondan yüz çevirirse bilsin ki kıyamet günü bir günah yükü yüklenecektir.'),
(2565, 2449, 20, 101, 'Tâ-Hâ ', 'Mekki', 'Devamlı bu günahın azabında kalacaklar. Kıyamet günü onlar için ne kötüdür bu yük!'),
(2566, 2450, 20, 102, 'Tâ-Hâ ', 'Mekki', 'Sura üflendiği gün, işte o gün, suçluları gözleri korkudan göğermiş olarak toplarız.'),
(2567, 2451, 20, 103, 'Tâ-Hâ ', 'Mekki', '\"Siz dünyada sadece on gün eğleştiniz\" diye, aralarında saklı saklı konuşurlar.'),
(2568, 2452, 20, 104, 'Tâ-Hâ ', 'Mekki', 'Aralarında konuştuklarını Biz daha iyi biliriz. En akıllıları: \"Sadece bir gün eğleştiniz\" der.'),
(2569, 2453, 20, 105, 'Tâ-Hâ ', 'Mekki', 'Sana dağları sorarlar; de ki: \"Rabbim onları ufalayıp savuracak, yerlerini düz, kuru bir toprak haline getirecek; orada ne çukur, ne tümsek göreceksin. O gün, hiçbir tarafa sapmadan bir davetçiye uyarlar. Sesler Rahman\'ın heybetinden kısılmıştır; ancak bir fısıltı işitirsin.\"'),
(2570, 2454, 20, 106, 'Tâ-Hâ ', 'Mekki', 'Sana dağları sorarlar; de ki: \"Rabbim onları ufalayıp savuracak, yerlerini düz, kuru bir toprak haline getirecek; orada ne çukur, ne tümsek göreceksin. O gün, hiçbir tarafa sapmadan bir davetçiye uyarlar. Sesler Rahman\'ın heybetinden kısılmıştır; ancak bir fısıltı işitirsin.\"'),
(2571, 2455, 20, 107, 'Tâ-Hâ ', 'Mekki', 'Sana dağları sorarlar; de ki: \"Rabbim onları ufalayıp savuracak, yerlerini düz, kuru bir toprak haline getirecek; orada ne çukur, ne tümsek göreceksin. O gün, hiçbir tarafa sapmadan bir davetçiye uyarlar. Sesler Rahman\'ın heybetinden kısılmıştır; ancak bir fısıltı işitirsin.\"'),
(2572, 2456, 20, 108, 'Tâ-Hâ ', 'Mekki', 'Sana dağları sorarlar; de ki: \"Rabbim onları ufalayıp savuracak, yerlerini düz, kuru bir toprak haline getirecek; orada ne çukur, ne tümsek göreceksin. O gün, hiçbir tarafa sapmadan bir davetçiye uyarlar. Sesler Rahman\'ın heybetinden kısılmıştır; ancak bir fısıltı işitirsin.\"'),
(2573, 2457, 20, 109, 'Tâ-Hâ ', 'Mekki', 'O gün Rahman\'ın izin verdiği ve sözünden hoşnut olduğu kimseden başkasının şefaati fayda vermez.'),
(2574, 2458, 20, 110, 'Tâ-Hâ ', 'Mekki', 'Allah onların geçmişlerini de, geleceklerini de bilir. Onların hiçbirinin ilmi ise O\'nu kuşatamaz.'),
(2575, 2459, 20, 111, 'Tâ-Hâ ', 'Mekki', 'İnsanlar, diri ve her an yaratıklarını gözetip duran Allah\'a boyun eğmiştir. Yükü zulüm olan kimse ise hüsrana uğramıştır.'),
(2576, 2460, 20, 112, 'Tâ-Hâ ', 'Mekki', 'İnanmış olarak, yararlı işler işleyen kimse, haksızlıktan ve hakkının yeneceğinden korkmaz.'),
(2577, 2461, 20, 113, 'Tâ-Hâ ', 'Mekki', 'İşte Kuran\'ı, Arapça okunmak üzere indirdik, onda tehditleri türlü türlü açıkladık ki belki sakınırlar yahut onlara ibret verir.'),
(2578, 2462, 20, 114, 'Tâ-Hâ ', 'Mekki', 'Gerçek hükümdar olan Allah Yüce\'dir. Kuran sana vahyedilirken, vahy bitmezden önce, unutmamak için, tekrarda acele edip durma, \"Rabbim! ilmimi artır\" de.'),
(2579, 2463, 20, 115, 'Tâ-Hâ ', 'Mekki', 'And olsun ki daha önce \"Adem\'e secde edin\" demiştik; İblis\'ten başka hepsi secde etmiş, o çekinmişti.'),
(2580, 2464, 20, 116, 'Tâ-Hâ ', 'Mekki', '\"Ey Adem! Doğrusu bu, senin ve eşinin düşmanıdır. Sakın sizi cennetten çıkarmasın, yoksa bedbaht olursun. Doğrusu cennette ne acıkırsın, ne de çıplak kalırsın; orada ne susarsın ne de güneşin sıcağında kalırsın\" dedik.'),
(2581, 2465, 20, 117, 'Tâ-Hâ ', 'Mekki', '\"Ey Adem! Doğrusu bu, senin ve eşinin düşmanıdır. Sakın sizi cennetten çıkarmasın, yoksa bedbaht olursun. Doğrusu cennette ne acıkırsın, ne de çıplak kalırsın; orada ne susarsın ne de güneşin sıcağında kalırsın\" dedik.'),
(2582, 2466, 20, 118, 'Tâ-Hâ ', 'Mekki', '\"Ey Adem! Doğrusu bu, senin ve eşinin düşmanıdır. Sakın sizi cennetten çıkarmasın, yoksa bedbaht olursun. Doğrusu cennette ne acıkırsın, ne de çıplak kalırsın; orada ne susarsın ne de güneşin sıcağında kalırsın\" dedik.'),
(2583, 2467, 20, 119, 'Tâ-Hâ ', 'Mekki', '\"Ey Adem! Doğrusu bu, senin ve eşinin düşmanıdır. Sakın sizi cennetten çıkarmasın, yoksa bedbaht olursun. Doğrusu cennette ne acıkırsın, ne de çıplak kalırsın; orada ne susarsın ne de güneşin sıcağında kalırsın\" dedik.'),
(2584, 2468, 20, 120, 'Tâ-Hâ ', 'Mekki', 'Ama şeytan ona vesvese verip: \"Ey Adem! Sana sonsuzluk ağacını ve çökmesi olmayan bir saltanatı göstereyim mi?\" dedi.'),
(2585, 2469, 20, 121, 'Tâ-Hâ ', 'Mekki', 'Bunun üzerine ikisi de o ağacın meyvesinden yedi, ayıp yerleri görünüverdi. Cennet yapraklarıyla örtünmeye koyuldular. Adem, Rabbine baş kaldırdı ve yolunu şaşırdı.'),
(2586, 2470, 20, 122, 'Tâ-Hâ ', 'Mekki', 'Rabbi yine de onu seçip tevbesini kabul etti, ona doğru yolu gösterdi.'),
(2587, 2471, 20, 123, 'Tâ-Hâ ', 'Mekki', 'Onlara şöyle dedi: \"Birbirinize düşman olarak hepiniz oradan inin. Elbet size Benden bir yol gösteren gelir; Benim yoluma uyan ne sapar ve ne de bedbaht olur.\"'),
(2588, 2472, 20, 124, 'Tâ-Hâ ', 'Mekki', 'Benim Kitap\'ımdan yüz çeviren bilsin ki onun dar bir geçimi olur ve kıyamet günü de onu kör olarak haşrederiz.'),
(2589, 2473, 20, 125, 'Tâ-Hâ ', 'Mekki', 'O zaman: \"Rabbim! Beni niçin kör olarak haşrettin, oysa ben gören bir kimseydim\" der.'),
(2590, 2474, 20, 126, 'Tâ-Hâ ', 'Mekki', 'Allah: \"Böyledir, ayetlerimiz sana gelmişti de sen onları unutmuştun, bugün de öylece unutulursun\" der.'),
(2591, 2475, 20, 127, 'Tâ-Hâ ', 'Mekki', 'İşte haddi aşanları, Rabbinin ayetlerine inanmayanları böylece cezalandıracağız. Hem, ahiretin azabı bu dünya azabından daha şiddetli ve daha devamlıdır.'),
(2592, 2476, 20, 128, 'Tâ-Hâ ', 'Mekki', 'Onları yerlerinde gezdikleri, kendilerinden önce yok etmiş olduğumuz bunca nesiller doğru yola sevketmedi mi? Doğrusu bunlarda akıl sahipleri için ibretler vardır.'),
(2593, 2477, 20, 129, 'Tâ-Hâ ', 'Mekki', 'Eğer Rabbinin verilmiş bir sözü ve tayin ettiği bir süre olmasaydı, hemen azaba uğrarlardı.'),
(2594, 2478, 20, 130, 'Tâ-Hâ ', 'Mekki', 'Onların dediklerine sabret; güneşin doğmasından ve batmasından önce Rabbini hamd ile tesbih et; gece saatlerinde ve gündüzleri de tesbih et ki Rabbinin rızasına eresin.'),
(2595, 2479, 20, 131, 'Tâ-Hâ ', 'Mekki', 'Kendilerini sınamak için, dünya hayatının süsü olarak bol bol geçimlik verdiğimiz kimselere sakın göz dikme, Rabbinin rızkı daha iyi ve daha devamlıdır.'),
(2596, 2480, 20, 132, 'Tâ-Hâ ', 'Mekki', 'Ehline namaz kılmalarını emret, kendin de onda devamlı ol. Biz senden rızık istemiyoruz, sana rızık veren Biziz. Sonuç Allah\'a karşı gelmekten sakınanındır.'),
(2597, 2481, 20, 133, 'Tâ-Hâ ', 'Mekki', '\"Rabbinden bize bir mucize getirseydi ya\" derler. Onlara, önceki Kitablarda bulunan belgeler gelmedi mi?'),
(2598, 2482, 20, 134, 'Tâ-Hâ ', 'Mekki', 'Eğer onları ondan önce bir azaba uğratarak yok etseydik: \"Rabbimiz! Bize bir peygamber gönderseydin de, alçak ve rezil olmazdan önce ayetlerine uysaydık, olmaz mıydı?\" diyeceklerdi.'),
(2599, 2483, 20, 135, 'Tâ-Hâ ', 'Mekki', 'De ki: \"Herkes gözlemektedir, siz de gözleyin. Şüphesiz düz yolun sahiplerinin kimler olduğunu ve kimlerin doğru yolda bulunduğunu bileceksiniz.\"'),
(2600, 2484, 21, 1, 'Enbiyâ ', 'Mekki', 'İnsanların hesap görme zamanı yaklaştı, fakat onlar hala habersiz, hakdan yüz çeviriyorlar.'),
(2601, 2485, 21, 2, 'Enbiyâ ', 'Mekki', 'Rablerinden kendilerine gelen her yeni ihtarı mutlaka, gönülleri gaflet içinde eğlenerek dinlerler. Zulmedenler, gizli toplantılarında: \"Bu zat, sizin gibi bir insandan başka bir şey midir? Siz, göz göre göre sihre mi uyarsınız?\" diye konuşurlar.'),
(2602, 2486, 21, 3, 'Enbiyâ ', 'Mekki', 'Rablerinden kendilerine gelen her yeni ihtarı mutlaka, gönülleri gaflet içinde eğlenerek dinlerler. Zulmedenler, gizli toplantılarında: \"Bu zat, sizin gibi bir insandan başka bir şey midir? Siz, göz göre göre sihre mi uyarsınız?\" diye konuşurlar.'),
(2603, 2487, 21, 4, 'Enbiyâ ', 'Mekki', 'Peygamber: \"Benim Rabbim gökte ve yerde söyleneni bilir. O, işitendir, bilendir\" dedi.'),
(2604, 2488, 21, 5, 'Enbiyâ ', 'Mekki', 'Onlar: \"Hayır; bunlar karışık rüyalardır\", \"Hayır; onu uydurmuştur\", \"Hayır; o şairdir\", \"Haydi önceki peygamberler gibi o da bize bir mucize getirsin\" dediler.'),
(2605, 2489, 21, 6, 'Enbiyâ ', 'Mekki', 'Onlardan önce yoketmiş olduğumuz kasabalar halkı inanmadılar, bunlar mı inanacaklar?'),
(2606, 2490, 21, 7, 'Enbiyâ ', 'Mekki', 'Senden önce de, kendilerine vahyettiğimiz adamlar gönderdik. Bilmiyorsanız kitablılara sorun.'),
(2607, 2491, 21, 8, 'Enbiyâ ', 'Mekki', 'Biz onları yemek yemez birer ceset kılmadık ve onlar ölümsüz de değillerdi.'),
(2608, 2492, 21, 9, 'Enbiyâ ', 'Mekki', 'Sonra Biz onlara verdiğimiz sözü yerine getirdik, kendilerini ve dilediklerimizi kurtardık; aşırı gidenleri ise yok ettik.'),
(2609, 2493, 21, 10, 'Enbiyâ ', 'Mekki', 'And olsun ki, size şerefiniz ve öğüt veren bir Kitap indirdik; akletmiyor musunuz?'),
(2610, 2494, 21, 11, 'Enbiyâ ', 'Mekki', 'Halkı zalim olan nice kasabaları kırıp geçirdik ve onlardan sonra başka milletler varettik.'),
(2611, 2495, 21, 12, 'Enbiyâ ', 'Mekki', 'Onlar bizim baskınımızı hissettiklerinde, oradan kaçmağa koyuluyorlardı.'),
(2612, 2496, 21, 13, 'Enbiyâ ', 'Mekki', '\"Koşup kaçmayın; size nimet verilen yere, yurdlarınıza dönün, elbette sorguya çekileceksiniz\" dedik.'),
(2613, 2497, 21, 14, 'Enbiyâ ', 'Mekki', '\"Vay başımıza gelenlere! Doğrusu biz haksızlık yapmış kimseleriz\" dediler.'),
(2614, 2498, 21, 15, 'Enbiyâ ', 'Mekki', 'Biz onları biçilmiş ot ve bir yığın kül haline getirinceye kadar haykırmaları devam etti.'),
(2615, 2499, 21, 16, 'Enbiyâ ', 'Mekki', 'Biz gökleri, yeri ve ikisinin arasındakileri oyun olsun diye yaratmadık.'),
(2616, 2500, 21, 17, 'Enbiyâ ', 'Mekki', 'Eğlenme dileseydik, bunu yapacak olsaydık, şanımıza uygun şekilde yapardık; ama yapmayız.'),
(2617, 2501, 21, 18, 'Enbiyâ ', 'Mekki', 'Gerçeği batılın başına çarparız ve onun beynini parçalar; böylece batıl ortadan kalkar. Allah\'a yakıştırdığınız vasıflardan ötürü yazıklar olsun size!'),
(2618, 2502, 21, 19, 'Enbiyâ ', 'Mekki', 'Göklerde ve yerde ne varsa O\'nundur. Katında olanlar O\'na kulluk etmekten çekinmezler ve usanmazlar.'),
(2619, 2503, 21, 20, 'Enbiyâ ', 'Mekki', 'Gece ve gündüz, bıkmadan tesbih ederler.'),
(2620, 2504, 21, 21, 'Enbiyâ ', 'Mekki', 'Yeryüzünde edindikleri tanrılar mı, onlar mı ölüleri diriltecekler?'),
(2621, 2505, 21, 22, 'Enbiyâ ', 'Mekki', 'Eğer yerle gökte Allah\'tan başka tanrılar olsaydı, ikisi de bozulurdu. Arşın Rabbi olan Allah, onların vasıflandırdıklarından münezzehtir.'),
(2622, 2506, 21, 23, 'Enbiyâ ', 'Mekki', 'O, yaptığından sorumlu değildir, onlar ise sorumlu tutulacaklardır.'),
(2623, 2507, 21, 24, 'Enbiyâ ', 'Mekki', 'O\'nu bırakıp tanrılar mı edindiler? De ki: \"Kesin delilinizi getirin. İşte benim ve ümmetimin Kitap\'ı ve senden öncekilerin kitapları.\" Hayır; onların çoğu gerçeği bilmez de yüz çevirirler.'),
(2624, 2508, 21, 25, 'Enbiyâ ', 'Mekki', 'Senden önce gönderdiğimiz her peygambere: \"Benden başka tanrı yoktur, Bana kulluk edin\" diye vahyetmişizdir.'),
(2625, 2509, 21, 26, 'Enbiyâ ', 'Mekki', '\"Rahman çocuk edindi\" dediler. Haşa; hayır, melekler şerefli kılınmış kullardır.'),
(2626, 2510, 21, 27, 'Enbiyâ ', 'Mekki', 'Allah\'tan önce söz söyleyemezler; ancak O\'nun emri üzerine iş işlerler.'),
(2627, 2511, 21, 28, 'Enbiyâ ', 'Mekki', 'Allah, onların yaptıklarını ve yapmakta olduklarını bilir. Onlar Allah\'ın hoşnut olduğu kimseden başkasına şefaat edemezler; O\'nun korkusundan titrerler.'),
(2628, 2512, 21, 29, 'Enbiyâ ', 'Mekki', 'Bunlar içinde kim \"Ben, Allah\'tan başka bir tanrıyım\" derse, işte onu cehennemle cezalandırırız. Zulmedenlerin cezasını böyle veririz.'),
(2629, 2513, 21, 30, 'Enbiyâ ', 'Mekki', 'İnkar edenler, gökler ve yer yapışıkken onları ayırdığımızı ve bütün canlıları sudan meydana getirdiğimizi bilmezler mi? İnanmıyorlar mı?'),
(2630, 2514, 21, 31, 'Enbiyâ ', 'Mekki', 'Yeryüzüne, insanlar sarsılmasın diye sabit dağlar yerleştirdik; rahat gidebilsinler diye aralarında geniş yollar varettik.'),
(2631, 2515, 21, 32, 'Enbiyâ ', 'Mekki', 'Göğü karışıklıktan korunmuş bir tavan kıldık; oysa onlar bundaki delillerden yüz çeviriyorlar.'),
(2632, 2516, 21, 33, 'Enbiyâ ', 'Mekki', 'Geceyi ve gündüzü, güneşi ve ayı yaratan O\'dur. Her biri bir yörüngede yürür.'),
(2633, 2517, 21, 34, 'Enbiyâ ', 'Mekki', 'Senden önce de hiçbir insanı ölümsüz kılmadık, sen ölürsün de onlar baki kalır mı?'),
(2634, 2518, 21, 35, 'Enbiyâ ', 'Mekki', 'Her can ölümü tadacaktır. Bir imtihan olarak size iyilik ve kötülük veririz. Sonunda Bize dönersiniz.'),
(2635, 2519, 21, 36, 'Enbiyâ ', 'Mekki', 'İnkarcılar seni gördükleri zaman, şüphesiz, seni alaya almaktan başka bir şey yapmazlar. \"Sizin tanrılarınızı diline dolayan bu mudur?\" derler ve Rahman\'ın Kitabını işte onlar inkar ederler.'),
(2636, 2520, 21, 37, 'Enbiyâ ', 'Mekki', 'İnsan aceleci olarak yaratılmıştır. Size ayetlerimi göstereceğim, bunu Benden acele istemeyin.'),
(2637, 2521, 21, 38, 'Enbiyâ ', 'Mekki', '\"Doğru sözlü iseniz bildirin bu tehdit ne zamandır?\" derler.'),
(2638, 2522, 21, 39, 'Enbiyâ ', 'Mekki', 'Bu kafirler, ateşi yüzlerinden ve sırtlarından menedemeyecekleri ve yardım da göremiyecekleri zamanı keşke bilseler.'),
(2639, 2523, 21, 40, 'Enbiyâ ', 'Mekki', 'Belki aniden gelecek de onları şaşırtacaktır. Artık onu geri çeviremezler; kendileri de ertelenmez.'),
(2640, 2524, 21, 41, 'Enbiyâ ', 'Mekki', 'And olsun ki, senden önce birçok peygamber alaya alınmıştı da, alaya alanları, eğlendikleri şey mahvetmişti.'),
(2641, 2525, 21, 42, 'Enbiyâ ', 'Mekki', 'De ki: \"Geceleyin ve gündüzün sizi Rahman\'dan kim koruyabilir?\" Ama onlar Rablerinin Kitabından yüz çevirmektedirler.'),
(2642, 2526, 21, 43, 'Enbiyâ ', 'Mekki', 'Yoksa kendilerini bize karşı savunacak tanrıları mı var? O tanrılar kendilerine bile yardım edemezler. Katımızdan da dostluk görmezler.'),
(2643, 2527, 21, 44, 'Enbiyâ ', 'Mekki', 'Biz bunlara ve babalarına geçimlikler verdik de ömürleri uzadı; şimdi memleketlerini her yandan eksilttiğimizi görmüyorlar mı? Üstün gelen onlar mıdır?'),
(2644, 2528, 21, 45, 'Enbiyâ ', 'Mekki', 'De ki: \"Ben ancak sizi vahy ile uyarıyorum\" Uyarıldıkları zaman, sağırlar çağrıyı duymazlar.'),
(2645, 2529, 21, 46, 'Enbiyâ ', 'Mekki', 'Rabbinin azabından onlara bir esinti dokunsa: \"Vah bize! Doğrusu biz haksızdık\" derler.'),
(2646, 2530, 21, 47, 'Enbiyâ ', 'Mekki', 'Kıyamet günü doğru teraziler kurarız; hiçbir kimse hiçbir haksızlığa uğratılmaz. Hardal tanesi kadar olsa bile yapılanı ortaya koyarız. Hesap gören olarak Biz yeteriz.'),
(2647, 2531, 21, 48, 'Enbiyâ ', 'Mekki', 'And olsun ki, Musa ve Harun\'a eğriyi doğrudan ayıran Kitap\'ı sakınanlar için ışık ve öğüt olarak verdik.'),
(2648, 2532, 21, 49, 'Enbiyâ ', 'Mekki', 'Onlar görmedikleri halde Rablerinden korkarlar; kıyamet saatinden de titrerler.'),
(2649, 2533, 21, 50, 'Enbiyâ ', 'Mekki', 'İşte bu, indirdiğimiz kutsal bir Kitap\'dır. Siz mi onu inkar ediyorsunuz?'),
(2650, 2534, 21, 51, 'Enbiyâ ', 'Mekki', 'And olsun ki, daha önce İbrahim\'e de akla uygun olanı göstermiştik. Biz onu biliyorduk.'),
(2651, 2535, 21, 52, 'Enbiyâ ', 'Mekki', 'İbrahim, babasına ve milletine: \"Bu tapınıp durduğunuz heykeller nedir?\" demişti.'),
(2652, 2536, 21, 53, 'Enbiyâ ', 'Mekki', '\"Babalarımızı onlara tapar bulduk\" demişlerdi.'),
(2653, 2537, 21, 54, 'Enbiyâ ', 'Mekki', 'İbrahim: \"And olsun ki sizler de babalarınız da apaçık bir sapıklık içindesiniz\" deyince:'),
(2654, 2538, 21, 55, 'Enbiyâ ', 'Mekki', '\"Sen bize gerçeği mi getirdin yoksa şaka mı ediyorsun?\" dediler.'),
(2655, 2539, 21, 56, 'Enbiyâ ', 'Mekki', 'O şöyle dedi: \"Hayır; Rabbiniz, göklerin ve yerin Rabbidir ki onları O yaratmıştır. Ben de buna şahidlik edenlerdenim.\"'),
(2656, 2540, 21, 57, 'Enbiyâ ', 'Mekki', '\"Allah\'a yemin ederim ki, siz ayrıldıktan sonra, putlarınıza bir tuzak kuracağım!\"'),
(2657, 2541, 21, 58, 'Enbiyâ ', 'Mekki', 'Hepsini paramparça edip, içlerinden büyüğünü ona başvursunlar diye, sağlam bıraktı.'),
(2658, 2542, 21, 59, 'Enbiyâ ', 'Mekki', 'Milleti: \"Tanrılarımıza bunu kim yaptı? Doğrusu o zalimlerden biridir\" dediler.'),
(2659, 2543, 21, 60, 'Enbiyâ ', 'Mekki', 'Bazıları: \"İbrahim denen bir gencin onları diline doladığını duymuştuk\" deyince, \"O halde bunların şahidlik edebilmeleri için onu halkın gözü önüne getirin\" dediler.'),
(2660, 2544, 21, 61, 'Enbiyâ ', 'Mekki', 'Bazıları: \"İbrahim denen bir gencin onları diline doladığını duymuştuk\" deyince, \"O halde bunların şahidlik edebilmeleri için onu halkın gözü önüne getirin\" dediler.'),
(2661, 2545, 21, 62, 'Enbiyâ ', 'Mekki', 'İbrahim gelince, ona: \"Ey İbrahim, bunu tanrılarımıza sen mi yaptın?\" dediler.'),
(2662, 2546, 21, 63, 'Enbiyâ ', 'Mekki', 'İbrahim: \"Belki onu şu büyükleri yapmıştır, konuşabiliyorlarsa onlara sorun\" dedi.'),
(2663, 2547, 21, 64, 'Enbiyâ ', 'Mekki', 'Kendi kendilerine: \"Doğrusu siz haksızsınız\", sonra kafalarında olan eski inançlarına dönerek: \"Ey İbrahim! bunların konuşmayacağını, and olsun ki, bilirsin\" dediler.'),
(2664, 2548, 21, 65, 'Enbiyâ ', 'Mekki', 'Kendi kendilerine: \"Doğrusu siz haksızsınız\", sonra kafalarında olan eski inançlarına dönerek: \"Ey İbrahim! bunların konuşmayacağını, and olsun ki, bilirsin\" dediler.'),
(2665, 2549, 21, 66, 'Enbiyâ ', 'Mekki', 'İbrahim: \"O halde, Allah\'ı bırakıp da size hiçbir fayda ve zarar veremeyecek olan putlara ne diye taparsınız? Size de, Allah\'ı bırakıp taptıklarınıza da yazıklar olsun! Akletmiyor musunuz?\" dedi.'),
(2666, 2550, 21, 67, 'Enbiyâ ', 'Mekki', 'İbrahim: \"O halde, Allah\'ı bırakıp da size hiçbir fayda ve zarar veremeyecek olan putlara ne diye taparsınız? Size de, Allah\'ı bırakıp taptıklarınıza da yazıklar olsun! Akletmiyor musunuz?\" dedi.'),
(2667, 2551, 21, 68, 'Enbiyâ ', 'Mekki', 'Onlar: \"Bir şey yapacaksanız, şunu yakın da tanrılarınıza yardım edin\" dediler.'),
(2668, 2552, 21, 69, 'Enbiyâ ', 'Mekki', 'Biz: \"Ey ateş! İbrahim\'e karşı serin ve zararsız ol\" dedik.'),
(2669, 2553, 21, 70, 'Enbiyâ ', 'Mekki', 'Ona düzen kurmak istediler, fakat Biz onları hüsrana uğrattık.'),
(2670, 2554, 21, 71, 'Enbiyâ ', 'Mekki', 'Onu da, Lut\'u da, alemler için kutsal kıldığımız yere ulaştırıp kurtardık.'),
(2671, 2555, 21, 72, 'Enbiyâ ', 'Mekki', 'İbrahim\'e, buna ilaveten İshak ve Yakub\'u da verdik, her birini iyi kimseler kıldık.'),
(2672, 2556, 21, 73, 'Enbiyâ ', 'Mekki', 'Onları, buyruğumuz altında insanları doğru yola götüren önderler yaptık; onlara, iyi işler yapmayı, namaz kılmayı, zekat vermeyi vahyettik. Onlar, bize kulluk eden kimselerdi.'),
(2673, 2557, 21, 74, 'Enbiyâ ', 'Mekki', 'Lut\'a da hüküm ve ilim verdik; onu, çirkin işler işleyen kasabadan kurtardık. Doğrusu onlar yoldan çıkmış kötü bir milletti.'),
(2674, 2558, 21, 75, 'Enbiyâ ', 'Mekki', 'Lut\'u rahmetimizin içine aldık; doğrusu o iyilerdendi.'),
(2675, 2559, 21, 76, 'Enbiyâ ', 'Mekki', 'Nuh da daha önceleri Bize yalvarmıştı, onun duasını kabul edip, kendisini ve ailesini büyük sıkıntıdan kurtardık.'),
(2676, 2560, 21, 77, 'Enbiyâ ', 'Mekki', 'Ayetlerimizi yalanlayan millete karşı ona yardım ettik. Doğrusu onlar fena bir milletti, hepsini suda boğduk.'),
(2677, 2561, 21, 78, 'Enbiyâ ', 'Mekki', 'Davud ve Süleyman da milletin koyunlarının yayıldığı bir ekin hakkında hüküm veriyorlarken, Biz onların hükmüne şahiddik.'),
(2678, 2562, 21, 79, 'Enbiyâ ', 'Mekki', 'Süleyman\'a bu meselenin hükmünü bildirmiştik; her birine hüküm ve ilim verdik. Davud ile beraber tesbih etsinler diye dağları ve kuşları buyruk altına aldık. Bunları Biz yapmıştık.'),
(2679, 2563, 21, 80, 'Enbiyâ ', 'Mekki', 'Ona, sizi savaşta korumak için zırh yapma sanatını öğrettik, artık şükreder misiniz?'),
(2680, 2564, 21, 81, 'Enbiyâ ', 'Mekki', 'Bereketli kıldığımız yere doğru, Süleyman\'ın emriyle yürüyen şiddetli rüzgarı, onun buyruğuna verdik. Biz herşeyi biliyorduk.'),
(2681, 2565, 21, 82, 'Enbiyâ ', 'Mekki', 'Dalgıçlık yapan ve bundan başka işler de gören şeytanlardan da onun buyruğu altına verdik. Onların hepsini gözetiyorduk.'),
(2682, 2566, 21, 83, 'Enbiyâ ', 'Mekki', 'Eyyub da: \"Başıma bir bela geldi, (Sana sığındım), Sen merhametlilerin merhametlisisin\" diye Rabbine nida etmişti.'),
(2683, 2567, 21, 84, 'Enbiyâ ', 'Mekki', 'Biz de onun duasını kabul etmiş ve başına gelenleri kaldırmıştık. Katımızdan bir rahmet ve kulluk edenlere bir hatıra olmak üzere ona tekrar ailesini ve kaybettikleriyle bir mislini daha vermiştik.'),
(2684, 2568, 21, 85, 'Enbiyâ ', 'Mekki', 'İsmail, İdris ve Zülkifl hakkında anlattığımızı da an; onların her biri sabredenlerdendi.'),
(2685, 2569, 21, 86, 'Enbiyâ ', 'Mekki', 'Onları rahmetimizin içine aldık; doğrusu onlar iyilerdendi.'),
(2686, 2570, 21, 87, 'Enbiyâ ', 'Mekki', 'Zünnun (Balık Sahibi; Yunus) hakkında söylediğimizi de an. O, öfkelenerek giderken, kendisini sıkıntıya sokmayacağımızı sanmıştı; fakat sonunda karanlıklar içinde: \"Senden başka tanrı yoktur, Sen münezzehsin, doğrusu ben haksızlık edenlerdenim\" diye seslenmişti.'),
(2687, 2571, 21, 88, 'Enbiyâ ', 'Mekki', 'Biz de ona cevap verip, onu üzüntüden kurtarmıştık. inananları böyle kurtarırız.'),
(2688, 2572, 21, 89, 'Enbiyâ ', 'Mekki', 'Zekeriya da: \"Rabbim! Beni tek Başıma bırakma, Sen varislerin en hayırlısısın\" diye nida etmişti.'),
(2689, 2573, 21, 90, 'Enbiyâ ', 'Mekki', 'Biz de ona icabet ederek, Yahya\'yı bahşetmiş, eşini de doğum yapacak hale getirmiştik. Doğrusu onlar iyi işlerde yarışıyorlar, korkarak ve umarak Bize yalvarıyorlardı. Bize karşı gönülden saygı duyuyorlardı.'),
(2690, 2574, 21, 91, 'Enbiyâ ', 'Mekki', 'Mahrem yerini koruyan Meryem\'e ruhumuzdan üflemiş, onu ve oğlunu, alemler için bir mucize kılmıştık.'),
(2691, 2575, 21, 92, 'Enbiyâ ', 'Mekki', 'Doğrusu tevhid dini olan Müslümanlık, bir tek din olarak sizin dininizdir ve Ben de Rabbinizim, artık Bana kulluk edin.'),
(2692, 2576, 21, 93, 'Enbiyâ ', 'Mekki', 'Ama insanlar, din konusunda aralarında bölüklere ayrıldılar, hepsi Bize döneceklerdir.'),
(2693, 2577, 21, 94, 'Enbiyâ ', 'Mekki', 'İnanmış olarak yararlı iş işleyenin ameli inkar edilmeyecektir. Biz onu yazmaktayız.'),
(2694, 2578, 21, 95, 'Enbiyâ ', 'Mekki', 'Yok ettiğimiz kasaba halkının ahirette ceza görmek üzere Bize dönmemesi imkansızdır.'),
(2695, 2579, 21, 96, 'Enbiyâ ', 'Mekki', 'Yecüc ve Mecüc\'ün seddi yıkıldığı zaman her dere ve tepeden boşanırlar.'),
(2696, 2580, 21, 97, 'Enbiyâ ', 'Mekki', 'Gerçek vaad yaklaştığında, inkar edenlerin gözleri beleriverir: \"Vah bize! Bundan önce gaflet içindeydik, hem de zalimdik\" derler.'),
(2697, 2581, 21, 98, 'Enbiyâ ', 'Mekki', 'Siz ve Allah\'tan başka taptıklarınız, cehennemin yakıtısınız; oraya gireceksiniz.'),
(2698, 2582, 21, 99, 'Enbiyâ ', 'Mekki', 'Eğer bunlar tanrı olsaydı cehenneme girmezlerdi; hepsi orada temelli kalacaktır.'),
(2699, 2583, 21, 100, 'Enbiyâ ', 'Mekki', 'Orada onlara ah etmek vardır; birşey de işitemezler.'),
(2700, 2584, 21, 101, 'Enbiyâ ', 'Mekki', 'Yaptıklarına karşılık katımızdan kendileri için iyi şeyler yazılmış olanlar, işte onlar cehennemden uzak tutulanlardır.'),
(2701, 2585, 21, 102, 'Enbiyâ ', 'Mekki', 'Cehennemin uğultusunu duymazlar. Canlarının istediği şeyler içinde temelli kalırlar.'),
(2702, 2586, 21, 103, 'Enbiyâ ', 'Mekki', 'En büyük korku bile onları üzmez; kendilerini melekler: \"Size söz verilen gün işte bugündür\" diye karşılarlar.'),
(2703, 2587, 21, 104, 'Enbiyâ ', 'Mekki', 'Göğü, kitap dürer gibi dürdüğümüz zaman, yaratmaya ilk başladığımız gibi katımızdan verilmiş bir söz olarak onu tekrar var edeceğiz. Doğrusu Biz yaparız.'),
(2704, 2588, 21, 105, 'Enbiyâ ', 'Mekki', 'And olsun ki, Tevrat\'tan sonra Zebur\'da da yeryüzüne ancak iyi kullarımın mirasçı olduğunu yazmıştık.'),
(2705, 2589, 21, 106, 'Enbiyâ ', 'Mekki', 'Doğrusu bu Kuran\'da, kulluk eden kimselere bildiri vardır.'),
(2706, 2590, 21, 107, 'Enbiyâ ', 'Mekki', 'Biz seni ancak alemlere rahmet olarak gönderdik.'),
(2707, 2591, 21, 108, 'Enbiyâ ', 'Mekki', 'De ki: \"Doğrusu tanrınızın tek bir Tanrı olduğu bana şüphesiz vahyolundu. Artık müslüman olacak mısınız?\"'),
(2708, 2592, 21, 109, 'Enbiyâ ', 'Mekki', 'Eğer yüz çevirirlerse, de ki: \"Size düpedüz açıkladım; tehdit olunduğunuz şeyin yakın mı uzak mı olduğunu bilmem.\"'),
(2709, 2593, 21, 110, 'Enbiyâ ', 'Mekki', '\"Doğrusu O, açığa vurulan sözü de bilir, gizlediklerinizi de bilir.\"'),
(2710, 2594, 21, 111, 'Enbiyâ ', 'Mekki', '\"Bilmem; belki bu gecikme sizi denemek ve bir süreye kadar geçindirmek içindir.\"'),
(2711, 2595, 21, 112, 'Enbiyâ ', 'Mekki', 'Peygamber: \"Rabbim! Aramızda gerçekle hükmet, anlattıklarınıza karşı ancak Rahman olan Rabbimizden yardım istenir\" dedi.'),
(2712, 2596, 22, 1, 'Hac ', 'Medeni', 'Ey insanlar! Rabbinizden sakının; doğrusu kıyamet gününün sarsıntısı büyük şeydir.'),
(2713, 2597, 22, 2, 'Hac ', 'Medeni', 'Kıyameti gören her emzikli kadın emzirdiğini unutur, her hamile kadın çocuğunu düşürür. İnsanları sarhoş gibi görürsün oysa sarhoş değildirler, fakat bu sadece Allah\'ın azabının çetin olmasındandır.'),
(2714, 2598, 22, 3, 'Hac ', 'Medeni', 'Allah hakkında bilmeden taşıyan ve her azılı şeytana uyan insanlar vardır.'),
(2715, 2599, 22, 4, 'Hac ', 'Medeni', 'Onun hakkında şöyle yazılmıştır: O kendisini dost edinen kimseyi saptırır ve alevli azaba götürür.'),
(2716, 2600, 22, 5, 'Hac ', 'Medeni', 'Ey insanlar! Öldükten sonra tekrar dirilmekten şüphede iseniz bilin ki, ne olduğunuzu size açıklamak için, Biz sizi topraktan sonra nutfeden, sonra pıhtılaşmış kandan, sonra da yapısı belli belirsiz bir çiğnem etten yaratmışızdır. Dilediğimizi belli bir süreye kadar rahimlerde tutarız; sonra sizi çocuk olarak çıkartırız, böylece yetişip erginlik çağına varırsınız. Kiminiz öldürülür, kiminiz de ömrünün en fena zamanına ulaştırılır ki, bilirken birşey bilmez olur. Yeryüzünü görürsün ki kupkurudur; fakat Biz ona su indirdiğimiz zaman harekete geçer, kabarır, her güzel bitkiden çift çift yetiştirir.'),
(2717, 2601, 22, 6, 'Hac ', 'Medeni', 'Bunlar, yalnız Allah\'ın gerçek olduğunu, ölüleri dirilttiğini, gücünün herşeye yettiğini, şüphe götürmeyen kıyamet saatinin geleceğini, Allah\'ın kabirlerde olanı dirilteceğini gösterir.'),
(2718, 2602, 22, 7, 'Hac ', 'Medeni', 'Bunlar, yalnız Allah\'ın gerçek olduğunu, ölüleri dirilttiğini, gücünün herşeye yettiğini, şüphe götürmeyen kıyamet saatinin geleceğini, Allah\'ın kabirlerde olanı dirilteceğini gösterir.'),
(2719, 2603, 22, 8, 'Hac ', 'Medeni', 'Bilmeden, doğruya götüren bir rehberi olmadan, aydınlatıcı bir kitabı da bulunmadan Allah yolundan saptırmak için büyüklük taslayarak Allah hakkında tartışan insan vardır. Dünyada rezillik onadır; ona kıyamet günü yakıcı azabı tattırırız.'),
(2720, 2604, 22, 9, 'Hac ', 'Medeni', 'Bilmeden, doğruya götüren bir rehberi olmadan, aydınlatıcı bir kitabı da bulunmadan Allah yolundan saptırmak için büyüklük taslayarak Allah hakkında tartışan insan vardır. Dünyada rezillik onadır; ona kıyamet günü yakıcı azabı tattırırız.'),
(2721, 2605, 22, 10, 'Hac ', 'Medeni', 'Ona: \"Bunlar senin yaptıklarından ötürüdür\" denir, yoksa Allah, kullarına karşı hiç de zalim değildir.'),
(2722, 2606, 22, 11, 'Hac ', 'Medeni', 'İnsanlar içinde Allah\'a, bir yar kenarındaymış gibi kulluk eden vardır. Ona bir iyilik gelirse yatışır, başına bir bela gelirse yüz üstü döner. Dünyayı da ahireti de kaybeder. İşte apaçık kayıp budur.'),
(2723, 2607, 22, 12, 'Hac ', 'Medeni', 'Allah\'ı bırakıp, kendisine fayda da zarar da veremeyen şeylere yalvarır. İşte derin sapıklık budur.'),
(2724, 2608, 22, 13, 'Hac ', 'Medeni', 'Kendisine zararı faydasından daha yakın olana yalvarır. Yalvardığı şey ne kötü yardımcı ve ne kötü yoldaştır!'),
(2725, 2609, 22, 14, 'Hac ', 'Medeni', 'Doğrusu Allah, inananları ve yararlı işler işleyenleri, içlerinden ırmaklar akan cennetlere koyar. Allah, şüphesiz, istediğini yapar.'),
(2726, 2610, 22, 15, 'Hac ', 'Medeni', 'Allah\'ın peygamber\'e dünyada ve ahirette yardım etmeyeceğini sanan kimse, yukarı bağladığı bir ipe kendini asıp, boğsun; bir düşünsün bakalım, bu hilesi kendisini öfkelendiren şeye engel olabilir mi?'),
(2727, 2611, 22, 16, 'Hac ', 'Medeni', 'İşte böylece Kuran\'ı apaçık ayetler olarak indirdik. Allah, şüphesiz, dilediğini doğru yola eriştirir.'),
(2728, 2612, 22, 17, 'Hac ', 'Medeni', 'Doğrusu, inananlar ve yahudiler, sabiiler, hıristiyanlar, mecusiler, ortak koşanlar arasında, kıyamet günü Allah kesin hüküm verecektir. Doğrusu Allah herşeye şahiddir.'),
(2729, 2613, 22, 18, 'Hac ', 'Medeni', 'Göklerde ve yerde olanların, güneş, ay, yıldızlar, dağlar, ağaçlar, hayvanların ve insanların birçoğunun Allah\'a secde ettiklerini görmüyor musun? İnsanların birçoğu da azabı hak etmiştir. Allah\'ın alçalttığı kimseyi yükseltebilecek yoktur. Doğrusu Allah ne dilerse yapar.'),
(2730, 2614, 22, 19, 'Hac ', 'Medeni', 'İşte Rableri hakkında tartışmaya giren iki taraf: O\'nu inkar edenlere, ateşten elbiseler kesilmiştir, başlarına da kaynar su dökülür de bununla karınlarındakiler ve deriler eritilir. Demir topuzlar da onlar içindir.'),
(2731, 2615, 22, 20, 'Hac ', 'Medeni', 'İşte Rableri hakkında tartışmaya giren iki taraf: O\'nu inkar edenlere, ateşten elbiseler kesilmiştir, başlarına da kaynar su dökülür de bununla karınlarındakiler ve deriler eritilir. Demir topuzlar da onlar içindir.'),
(2732, 2616, 22, 21, 'Hac ', 'Medeni', 'İşte Rableri hakkında tartışmaya giren iki taraf: O\'nu inkar edenlere, ateşten elbiseler kesilmiştir, başlarına da kaynar su dökülür de bununla karınlarındakiler ve deriler eritilir. Demir topuzlar da onlar içindir.'),
(2733, 2617, 22, 22, 'Hac ', 'Medeni', 'Orada, uğradıkları gamdan ne zaman çıkmak isteseler her defasında oraya geri çevrilirler: \"Yakıcı azabı tadın\" denir.'),
(2734, 2618, 22, 23, 'Hac ', 'Medeni', 'Doğrusu Allah, inanıp yararlı iş işleyenleri, içlerinden ırmaklar akan cennetlere koyar. Orada altın bilezikler ve inciler takınırlar. Oradaki elbiseleri de ipektendir.'),
(2735, 2619, 22, 24, 'Hac ', 'Medeni', 'Bu kimseler, sözün güzelini işitecek duruma ulaştırılmışlar, övülmeğe layık olan Allah\'ın yoluna eriştirilmişlerdir.'),
(2736, 2620, 22, 25, 'Hac ', 'Medeni', 'Doğrusu inkar edenleri, Allah\'ın yolundan, yerli ve yolcu bütün insanlar için eşit kılınan Mescidi Haram\'dan alıkoyanları ve orada zulm ile yanlış yola saptırmak isteyeni, can yakıcı bir azaba uğratırız.'),
(2737, 2621, 22, 26, 'Hac ', 'Medeni', '\"Bana hiçbir şeyi ortak koşma; tavaf edenler, orada kıyama duranlar, rüku edenler ve secdeye varanlar için Evimi temiz tut\" diye İbrahim\'i Kabe\'nin yerine yerleştirmiştik.'),
(2738, 2622, 22, 27, 'Hac ', 'Medeni', 'İnsanları hacca çağır; yürüyerek veya binekler üstünde uzak yollardan sana gelsinler.'),
(2739, 2623, 22, 28, 'Hac ', 'Medeni', 'Taki kendi menfaatlerine şahid olsunlar; Allah\'ın onlara rızık olarak verdiği hayvanları belli günlerde kurban ederken O\'nun adını ansınlar. Siz de bunlardan yiyin, çaresiz kalmış yoksulu da doyurun.'),
(2740, 2624, 22, 29, 'Hac ', 'Medeni', 'Sonra kirlerini giderip temizlensinler. Adaklarını yerine getirsinler. Kabe\'yi tavaf etsinler.'),
(2741, 2625, 22, 30, 'Hac ', 'Medeni', 'İşte böyle. Kim Allah\'ın yasaklarına saygı gösterirse, bu Rabbinin katında kendi iyiliğinedir. (Haram olduğu) size okunanlar dışında kalan hayvanlar, size helal kılındı. O halde pis putlardan sakının; yalan sözden kaçının.'),
(2742, 2626, 22, 31, 'Hac ', 'Medeni', 'Allah\'a ortak koşmaksızın O\'na yönelerek pis putlardan kaçının, yalan sözden çekinin. Allah\'a ortak koşan kimse, gökten düşüp de kuşların kaptığı veya rüzgarın bir uçuruma attığı şeye benzer.'),
(2743, 2627, 22, 32, 'Hac ', 'Medeni', 'Bu böyledir; kişinin Allah\'ın nişanelerine hürmet göstermesi, kalblerin Allah\'a karşı gelmekten sakınmasındandır.'),
(2744, 2628, 22, 33, 'Hac ', 'Medeni', 'Bu nişanelerde sizin için belli bir süreye kadar faydalar vardır. Sonra bunlar Beyti Atik\'de, Kabe\'de son bulurlar.'),
(2745, 2629, 22, 34, 'Hac ', 'Medeni', 'Her ümmet için, Allah\'ın kendilerine rızk olarak verdiği kurbanlık hayvanların üzerlerine O\'nun adını anarak kurban kesmeyi meşru kıldık. Sizin Tanrınız tek bir Tanrı\'dır, O\'na teslim olun. Allah anıldığı zaman kalbleri titreyen, başlarına gelene sabreden, namaz kılan, kendilerine verdiğimiz rızıktan sarfeden ve Allah\'a gönül vermiş olan kimselere müjde et.'),
(2746, 2630, 22, 35, 'Hac ', 'Medeni', 'Her ümmet için, Allah\'ın kendilerine rızk olarak verdiği kurbanlık hayvanların üzerlerine O\'nun adını anarak kurban kesmeyi meşru kıldık. Sizin Tanrınız tek bir Tanrı\'dır, O\'na teslim olun. Allah anıldığı zaman kalbleri titreyen, başlarına gelene sabreden, namaz kılan, kendilerine verdiğimiz rızıktan sarfeden ve Allah\'a gönül vermiş olan kimselere müjde et.'),
(2747, 2631, 22, 36, 'Hac ', 'Medeni', 'İşte kurbanlık deve ve sığırları Allah\'ın size olan nişanelerinden kıldık. Onlarda sizin için hayır vardır. Bağlı halde keserken üzerlerine Allah\'ın adını anın. Yan üstü düşüp ölünce onlardan yiyin, isteyene de istemeyene de verin. Şükredersiniz diye onları böylece sizin buyruğunuza verdik.'),
(2748, 2632, 22, 37, 'Hac ', 'Medeni', 'Bu hayvanların ne etleri ve ne de kanları Allah\'a ulaşacaktır. Allah\'a ulaşacak olan ancak sizin O\'nun için yaptığınız gösterişten uzak amel ve ibadettir. Size doğru yolu gösterdiğinden, Allah\'ı yüceltmeniz için onları böylece sizin buyruğunuza vermiştir. İyilik yapanlara müjde et.'),
(2749, 2633, 22, 38, 'Hac ', 'Medeni', 'Allah şüphesiz inananları savunur, çünkü hainleri ve nankörleri hiç sevmez.'),
(2750, 2634, 22, 39, 'Hac ', 'Medeni', 'Haksızlığa uğratılarak kendilerine savaş açılan kimselerin karşı koyup savaşmasına izin verilmiştir. Allah onlara yardım etmeğe elbette Kadir\'dir.'),
(2751, 2635, 22, 40, 'Hac ', 'Medeni', 'Onlar haksız yere ve \"Rabbimiz Allah\'tır\" dediler diye yurtlarından çıkarılmışlardır. Allah insanların bir kısmını diğeriyle savmasaydı, manastırlar, kiliseler, havralar ve içinde Allah\'ın adı çok anılan camiler yıkılıp giderdi. And olsun ki, Allah\'a yardım edenlere O da yardım eder. Doğrusu Allah kuvvetlidir, güçlüdür.'),
(2752, 2636, 22, 41, 'Hac ', 'Medeni', 'Onları biz yeryüzüne yerleştirirsek namaz kılarlar, zekat verirler, uygun olanı emrederler, fenalığı yasak ederler. İşlerin sonucu Allah\'a aittir.'),
(2753, 2637, 22, 42, 'Hac ', 'Medeni', 'Seni yalancı sayıyorlarsa bil ki, onlardan önce Nuh milleti, Ad, Semud, İbrahim milleti, Lut milleti ve Medyen halkı da peygamberlerini yalancı saymış ve Musa da yalanlanmıştı. Ama Ben, kafirlere önce mehil verdim, sonra da onları yakalayıverdim; Beni tanımamak nasılmış görsünler.'),
(2754, 2638, 22, 43, 'Hac ', 'Medeni', 'Seni yalancı sayıyorlarsa bil ki, onlardan önce Nuh milleti, Ad, Semud, İbrahim milleti, Lut milleti ve Medyen halkı da peygamberlerini yalancı saymış ve Musa da yalanlanmıştı. Ama Ben, kafirlere önce mehil verdim, sonra da onları yakalayıverdim; Beni tanımamak nasılmış görsünler.'),
(2755, 2639, 22, 44, 'Hac ', 'Medeni', 'Seni yalancı sayıyorlarsa bil ki, onlardan önce Nuh milleti, Ad, Semud, İbrahim milleti, Lut milleti ve Medyen halkı da peygamberlerini yalancı saymış ve Musa da yalanlanmıştı. Ama Ben, kafirlere önce mehil verdim, sonra da onları yakalayıverdim; Beni tanımamak nasılmış görsünler.'),
(2756, 2640, 22, 45, 'Hac ', 'Medeni', 'Nice kasabaların halkını haksızlık yaparken yok ettik. Artık çatıları çökmüş, kuyuları metruk, sarayları bomboş kalmıştır.'),
(2757, 2641, 22, 46, 'Hac ', 'Medeni', 'Yeryüzünde dolaşmıyorlar mı ki, orada olanları akledecek kalbleri, işitecek kulakları olsun. Ama yalnız gözler kör olmaz, fakat göğüslerde olan kalbler de körleşir.'),
(2758, 2642, 22, 47, 'Hac ', 'Medeni', 'Senden, başlarına acele azap getirmeni istiyorlar. Allah sözünden asla caymayacaktır. Rabbinin katında bir gün, saydıklarınızdan bin yıl gibidir.'),
(2759, 2643, 22, 48, 'Hac ', 'Medeni', 'Nice kasabalara, haksız oldukları halde, mehil vermiştim; sonunda onları yakalayıverdim. Dönüş ancak Bana\'dır.'),
(2760, 2644, 22, 49, 'Hac ', 'Medeni', '\"Ey insanlar! Ben sizin için ancak apaçık bir uyarıcıyım\" de.'),
(2761, 2645, 22, 50, 'Hac ', 'Medeni', 'Cömertçe verilmiş rızık ve mağfiret, inanan ve yararlı iş işleyenleredir.'),
(2762, 2646, 22, 51, 'Hac ', 'Medeni', 'Ayetlerimizi tartışarak bozmağa uğraşanlar, işte onlar cehennemliklerdir.'),
(2763, 2647, 22, 52, 'Hac ', 'Medeni', 'Senden önce gönderdiğimiz hiçbir resul ve nebi yoktur ki, birşeyi arzuladığı zaman, şeytan onun arzusuna vesvese karıştırmamış olsun. Fakat Allah, şeytanın attığını derhal iptal eder, sonra kendi ayetlerini sağlamlaştırır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2764, 2648, 22, 53, 'Hac ', 'Medeni', '/. Allah şeytanın karıştırdığını, kalblerinde hastalık bulunan ve kalbleri kaskatı olan kimseleri sınamayı vesile kılar. Zalimler şüphesiz derin bir ayrılık içindedirler.'),
(2765, 2649, 22, 54, 'Hac ', 'Medeni', 'Bu, kendilerine ilim verilenlerin Kuran\'ın, senin Rabbin\'den bir gerçek olduğunu bilip de ona inanmaları ve gönüllerini bağlamaları içindir. Allah inananları şüphesiz doğru yola eriştirir.'),
(2766, 2650, 22, 55, 'Hac ', 'Medeni', 'İnkar edenler, ceza saati kendilerine ansızın gelene veya gecesi olmayan günün azabı çatana kadar Kuran\'dan şüphe etmekte devam ederler.'),
(2767, 2651, 22, 56, 'Hac ', 'Medeni', 'İşte o gün hükümranlık Allah\'ındır. O aralarında hükmeder. İnanıp yararlı iş işleyenler nimet cennetlerindedirler.'),
(2768, 2652, 22, 57, 'Hac ', 'Medeni', 'İnkar edenler, ayetlerimizi yalan sayan kimseler, işte onlar için hakir düşüren azap vardır.'),
(2769, 2653, 22, 58, 'Hac ', 'Medeni', 'Allah yolunda hicret edenlere, sonra öldürülen veya ölenlere Allah, elbette onlara güzel bir rızık verecektir. Rızık verenlerin en hayırlısı yalnız Allah\'tır.'),
(2770, 2654, 22, 59, 'Hac ', 'Medeni', 'And olsun ki, onları hoşnut olacakları bir yere koyar. Şüphesiz Allah bilendir, Halim\'dir.'),
(2771, 2655, 22, 60, 'Hac ', 'Medeni', 'Bu böyledir; kim kendisine verilen kadar ceza verirse ve kendisine yine de saldırılırsa, Allah ona, and olsun ki yardım edecektir. Allah şüphesiz, affeder ve bağışlar.'),
(2772, 2656, 22, 61, 'Hac ', 'Medeni', 'Böyledir; Allah geceyi gündüze katar, gündüzü de geceye katar ve Allah şüphesiz işitir ve görür.'),
(2773, 2657, 22, 62, 'Hac ', 'Medeni', 'Keza Hak yalnız Allah\'tır; O\'nu bırakıp taptıkları sadece batıldır. Doğrusu Allah yücedir büyüktür.'),
(2774, 2658, 22, 63, 'Hac ', 'Medeni', 'Allah\'ın gökten indirdiği su ile yerin yemyeşil olduğunu görmez misin? Doğrusu Allah Latif\'dir, haberdardır.'),
(2775, 2659, 22, 64, 'Hac ', 'Medeni', 'Göklerde olanlar, yerde olanlar O\'nundur. Doğrusu Allah müstağnidir, övülmeğe layık olandır.'),
(2776, 2660, 22, 65, 'Hac ', 'Medeni', 'Allah\'ın yerde olanları ve emriyle denizlerde yürüyen gemileri buyruğunuz altına vermiş olduğunu; buyruğu olmaksızın yere düşmemesi için göğü O\'nun tuttuğunu görmez misin? Doğrusu Allah insanlara karşı şefkatli ve merhametli olandır.'),
(2777, 2661, 22, 66, 'Hac ', 'Medeni', 'Sizi dirilten, sonra öldürecek sonra yine diriltecek olan O\'dur. İnsan gerçekten pek nankördür.'),
(2778, 2662, 22, 67, 'Hac ', 'Medeni', 'Her ümmete, yerine getirmeleri gerekli ibadetler koyduk. Öyleyse, bu konuda seninle çekişmelerine fırsat verme; Rabbine davet et, sen şüphesiz doğru yol üzerindesin. Seninle tartışırlarsa: \"Allah yaptığınızı çok iyi bilir; ayrılığa düştüğünüz şeyler hakkında, kıyamet günü aranızda Allah hükmedecektir\" de.'),
(2779, 2663, 22, 68, 'Hac ', 'Medeni', 'Her ümmete, yerine getirmeleri gerekli ibadetler koyduk. Öyleyse, bu konuda seninle çekişmelerine fırsat verme; Rabbine davet et, sen şüphesiz doğru yol üzerindesin. Seninle tartışırlarsa: \"Allah yaptığınızı çok iyi bilir; ayrılığa düştüğünüz şeyler hakkında, kıyamet günü aranızda Allah hükmedecektir\" de.'),
(2780, 2664, 22, 69, 'Hac ', 'Medeni', 'Her ümmete, yerine getirmeleri gerekli ibadetler koyduk. Öyleyse, bu konuda seninle çekişmelerine fırsat verme; Rabbine davet et, sen şüphesiz doğru yol üzerindesin. Seninle tartışırlarsa: \"Allah yaptığınızı çok iyi bilir; ayrılığa düştüğünüz şeyler hakkında, kıyamet günü aranızda Allah hükmedecektir\" de.'),
(2781, 2665, 22, 70, 'Hac ', 'Medeni', 'Gökte ve yerde olanı Allah\'ın bildiğini bilmez misin? Bunlar hiç şüphesiz Kitap\'dadır ve şüphesiz bunlar Allah\'a kolaydır.'),
(2782, 2666, 22, 71, 'Hac ', 'Medeni', 'Onlar Allah\'ı bırakıp da O\'nun, haklarında hiçbir delil indirmediği, kendilerinde de bir bilgi olmayan şeylere taparlar. Zulmedenlerin yardımcısı olmaz.'),
(2783, 2667, 22, 72, 'Hac ', 'Medeni', 'Onlara ayetlerimiz apaçık olarak okunduğu zaman, inkar edenlerin yüzlerinden inkarlarını anlarsın. Nerdeyse, kendilerine ayetlerimizi okuyanlara saldıracaklar. De ki: \"Size bundan daha fenasını haber vereyim mi? Allah\'ın inkarcılara vadettiği ateş! Ne kötü bir dönüştür!..'),
(2784, 2668, 22, 73, 'Hac ', 'Medeni', 'Ey insanlar! Bir misal verilmektedir, şimdi onu dinleyin: Sizlerin Allah\'ı bırakıp taptıklarınız bir araya gelseler, bir sinek bile yaratamıyacaklardır. Sinek onlardan bir şey kapsa, onu kurtaramazlar; isteyen de, istenen de aciz!'),
(2785, 2669, 22, 74, 'Hac ', 'Medeni', 'Allah\'ı gereği gibi değerlendiremediler. Doğrusu Allah kuvvetlidir, güçlüdür.'),
(2786, 2670, 22, 75, 'Hac ', 'Medeni', 'Allah meleklerden ve insanlardan peygamberler seçer. Doğrusu Allah işitir ve görür.'),
(2787, 2671, 22, 76, 'Hac ', 'Medeni', 'O, geçmişlerini geleceklerini bilir. Bütün işler Allah\'a döner.'),
(2788, 2672, 22, 77, 'Hac ', 'Medeni', 'Ey inananlar! Rüku edin, secdeye varın, Rabbiniz\'e kulluk edin, iyilik yapın ki saadete erişesiniz.'),
(2789, 2673, 22, 78, 'Hac ', 'Medeni', 'Allah uğrunda gereği gibi cihat edin. O, sizi seçmiş, babanız İbrahim\'in yolu olan dinde sizin için bir zorluk kılmamıştır. Daha önce ve Kuran\'da, peygamberin size şahit olması, sizin de insanlara şahit olmanız için size müslüman adını veren O\'dur. Artık, namaz kılın, zekat verin, Allah\'a sarılın. O sizin sahibinizdir. Ne güzel sahip ve ne güzel yardımcıdır!'),
(2790, 2674, 23, 1, 'Mü’minûn ', 'Mekki', 'Müminler saadete ermişlerdir.'),
(2791, 2675, 23, 2, 'Mü’minûn ', 'Mekki', 'Onlar namazda huşu içindedirler.'),
(2792, 2676, 23, 3, 'Mü’minûn ', 'Mekki', 'Onlar boş şeylerden yüz çevirirler.'),
(2793, 2677, 23, 4, 'Mü’minûn ', 'Mekki', 'Onlar zekatlarını verirler.'),
(2794, 2678, 23, 5, 'Mü’minûn ', 'Mekki', 'Onlar, eşleri ve cariyeleri dışında, mahrem yerlerini herkesten korurlar. Doğrusu bunlar yerilemezler.'),
(2795, 2679, 23, 6, 'Mü’minûn ', 'Mekki', 'Onlar, eşleri ve cariyeleri dışında, mahrem yerlerini herkesten korurlar. Doğrusu bunlar yerilemezler.'),
(2796, 2680, 23, 7, 'Mü’minûn ', 'Mekki', 'Bu sınırları aşmak isteyenler, işte bunlar aşırı gidenlerdir.'),
(2797, 2681, 23, 8, 'Mü’minûn ', 'Mekki', 'Onlar emanetlerini ve sözlerini yerine getirirler.'),
(2798, 2682, 23, 9, 'Mü’minûn ', 'Mekki', 'Namazlarına riayet ederler.'),
(2799, 2683, 23, 10, 'Mü’minûn ', 'Mekki', 'İşte onlar, temelli kalacakları Firdevs cennetine varis olanlardır.'),
(2800, 2684, 23, 11, 'Mü’minûn ', 'Mekki', 'İşte onlar, temelli kalacakları Firdevs cennetine varis olanlardır.'),
(2801, 2685, 23, 12, 'Mü’minûn ', 'Mekki', 'And olsun ki, insanı süzme çamurdan yarattık.'),
(2802, 2686, 23, 13, 'Mü’minûn ', 'Mekki', 'Sonra onu nutfe halinde sağlam bir yere yerleştirdik.'),
(2803, 2687, 23, 14, 'Mü’minûn ', 'Mekki', 'Sonra nutfeyi kan pıhtısına çevirdik, kan pıhtısını bir çiğnemlik et yaptık, bir çiğnemlik etten kemikler yarattık, kemiklere de et giydirdik. Sonra onu başka bir yaratık yaptık: Biçim verenlerin en güzeli olan Allah ne uludur!'),
(2804, 2688, 23, 15, 'Mü’minûn ', 'Mekki', 'Sizler, bütün bunlardan sonra ölürsünüz.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(2805, 2689, 23, 16, 'Mü’minûn ', 'Mekki', 'Şüphesiz kıyamet günü tekrar diriltilirsiniz.'),
(2806, 2690, 23, 17, 'Mü’minûn ', 'Mekki', 'And olsun ki, üstünüzde yedi tabaka yarattık. Biz, yarattığımızdan habersiz değiliz.'),
(2807, 2691, 23, 18, 'Mü’minûn ', 'Mekki', 'Gökten suyu ölçülü indirdik de, onu yerde durdurduk. Şüphesiz onu gidermeye de kadiriz.'),
(2808, 2692, 23, 19, 'Mü’minûn ', 'Mekki', 'Onunla, içinde, yediğiniz birçok meyvalar bulunan hurmalık ve üzüm bağları, Tur-i Sina\'da yetişen, yiyenlere, yağ ve katık veren zeytin ağacını var ettik.'),
(2809, 2693, 23, 20, 'Mü’minûn ', 'Mekki', 'Onunla, içinde, yediğiniz birçok meyvalar bulunan hurmalık ve üzüm bağları, Tur-i Sina\'da yetişen, yiyenlere, yağ ve katık veren zeytin ağacını var ettik.'),
(2810, 2694, 23, 21, 'Mü’minûn ', 'Mekki', 'Ehli hayvanlarda size ders vardır; onlardan çıkan sütten size içiririz; onlarda daha birçok menfaatiniz vardır. Onlardan yersiniz.'),
(2811, 2695, 23, 22, 'Mü’minûn ', 'Mekki', 'Hem onların ve hem de gemilerin üzerinde taşınırsınız.'),
(2812, 2696, 23, 23, 'Mü’minûn ', 'Mekki', 'And olsun ki Nuh\'u milletine gönderdik; onlara: \"Ey milletim! Allah\'a kulluk edin; O\'ndan başka tanrınız yoktur; sakınmaz mısınız?\" dedi.'),
(2813, 2697, 23, 24, 'Mü’minûn ', 'Mekki', 'Milletinin inkarcı ileri gelenleri: \"Bu, sizin gibi bir insandan başka birşey değildir. Sizden üstün olmak istiyor. Allah dilemiş olsaydı melekler indirirdi. İlk atalarımızdan beri böyle birşey işitmedik. Bu adamda nedense biraz delilik var, bir süreye kadar onu gözetleyin\" dediler.'),
(2814, 2698, 23, 25, 'Mü’minûn ', 'Mekki', 'Milletinin inkarcı ileri gelenleri: \"Bu, sizin gibi bir insandan başka birşey değildir. Sizden üstün olmak istiyor. Allah dilemiş olsaydı melekler indirirdi. İlk atalarımızdan beri böyle birşey işitmedik. Bu adamda nedense biraz delilik var, bir süreye kadar onu gözetleyin\" dediler.'),
(2815, 2699, 23, 26, 'Mü’minûn ', 'Mekki', 'Nuh: \"Rabbim! Beni yalanlamalarına karşılık bana yardım et\" dedi.'),
(2816, 2700, 23, 27, 'Mü’minûn ', 'Mekki', 'Bunun üzerine ona şöyle vahyettik: \"Nezaretimiz altında, sana bildirdiğimiz gibi gemiyi yap; buyruğumuz gelip tandırdan sular kaynayınca her cinsten birer çifti ve aleyhine hüküm verilmiş olanın dışında kalan çoluk çocuğunu alıp gemiye bindir. Haksızlık yapanlar için Bana baş vurma, çünkü onlar suda boğulacaklardır.\"'),
(2817, 2701, 23, 28, 'Mü’minûn ', 'Mekki', 'Ey Nuh! Sen ve beraberindekiler gemiye yerleşince: \"Bizi zalim milletten kurtaran Allah\'a hamdolsun\" de.'),
(2818, 2702, 23, 29, 'Mü’minûn ', 'Mekki', '\"Rabbim! Beni mübarek bir yere indir. Sen indirenlerin en iyisisin\" de.'),
(2819, 2703, 23, 30, 'Mü’minûn ', 'Mekki', 'Doğrusu bunlarda dersler vardır. Biz şüphesiz insanları denemekteyiz.'),
(2820, 2704, 23, 31, 'Mü’minûn ', 'Mekki', 'Bunların ardından başka nesiller varettik.'),
(2821, 2705, 23, 32, 'Mü’minûn ', 'Mekki', 'Onlara aralarından: \"Allah\"a kulluk edin, O\'ndan başka tanrınız yoktur, sakınmaz mısınız?\" diyen bir elçi gönderdik.'),
(2822, 2706, 23, 33, 'Mü’minûn ', 'Mekki', 'Onun, inkarcı ve ahirete kavuşmayı yalanlayan milletinin ileri gelenleri ki Biz onlara bu dünya hayatında nimet vermiştik şöyle dediler: \"Bu, yediğinizden yiyen, içtiğinizden içen sizin gibi bir insandan başka birşey değildir.\"'),
(2823, 2707, 23, 34, 'Mü’minûn ', 'Mekki', '\"Kendiniz gibi bir insana itaat ederseniz hüsrana uğrayacağınızda hiç şüphe yoktur.\"'),
(2824, 2708, 23, 35, 'Mü’minûn ', 'Mekki', '\"Öldüğünüz, toprak ve kemik yığını olduğunuz zaman tekrar dirilmenizle sizi tehdit mi ediyor?\"'),
(2825, 2709, 23, 36, 'Mü’minûn ', 'Mekki', '\"Oysa tehdit edildiğiniz şey ne kadar, hem de ne kadar uzak!\"'),
(2826, 2710, 23, 37, 'Mü’minûn ', 'Mekki', '\"Hayat ancak bu dünyadakidir. Ölürüz ve yaşarız (kimimiz ölür kimimiz doğar); tekrar diriltilmeyiz.\"'),
(2827, 2711, 23, 38, 'Mü’minûn ', 'Mekki', '\"Bu, sadece Allah\'a karşı yalan uyduranın biridir. Biz ona inanmayız.\"'),
(2828, 2712, 23, 39, 'Mü’minûn ', 'Mekki', 'O peygamber: \"Rabbim! Beni yalancı saymalarına karşılık bana yardım et\" dedi.'),
(2829, 2713, 23, 40, 'Mü’minûn ', 'Mekki', 'Allah da: \"Az sonra pişman olacaklar\" buyurdu.'),
(2830, 2714, 23, 41, 'Mü’minûn ', 'Mekki', 'Gerçekten, onları bir çığlık yakaladı ve onları süprüntü yığını haline getirdik. Haksızlık eden millet, rahmetden ırak olsun!'),
(2831, 2715, 23, 42, 'Mü’minûn ', 'Mekki', 'Ardlarından başka nesiller varettik.'),
(2832, 2716, 23, 43, 'Mü’minûn ', 'Mekki', 'Hiçbir ümmet, kendi süresini ne çabuklaştırabilir ve ne de geciktirebilir.'),
(2833, 2717, 23, 44, 'Mü’minûn ', 'Mekki', 'Sonra birbiri peşinden peygamberlerimizi gönderdik. Her ümmete peygamberi geldikçe onu yalancı saydılar. Onları birbiri peşinden yok edip hepsini birer efsane yaptık. İnanmayan millet, rahmetden ırak olsun!'),
(2834, 2718, 23, 45, 'Mü’minûn ', 'Mekki', 'Sonra Musa ve kardeşi Harun\'u, Firavun ve erkanına mucizelerimiz ve apaçık delille gönderdik. Büyüklük tasladılar. Zaten mağrur bir topluluktular.'),
(2835, 2719, 23, 46, 'Mü’minûn ', 'Mekki', 'Sonra Musa ve kardeşi Harun\'u, Firavun ve erkanına mucizelerimiz ve apaçık delille gönderdik. Büyüklük tasladılar. Zaten mağrur bir topluluktular.'),
(2836, 2720, 23, 47, 'Mü’minûn ', 'Mekki', 'Bu yüzden: \"Milletleri bize kul iken, bizim gibi iki insana mı inanacağız?\" deyip onları yalancı saydılar. Bu yüzden yok edildiler.'),
(2837, 2721, 23, 48, 'Mü’minûn ', 'Mekki', 'Bu yüzden: \"Milletleri bize kul iken, bizim gibi iki insana mı inanacağız?\" deyip onları yalancı saydılar. Bu yüzden yok edildiler.'),
(2838, 2722, 23, 49, 'Mü’minûn ', 'Mekki', 'And olsun ki Musa\'ya, doğru yola girsinler diye Kitap verdik.'),
(2839, 2723, 23, 50, 'Mü’minûn ', 'Mekki', 'Meryem oğlunu da, annesini de mucize kıldık. Her ikisini de, pınarı bulunan, oturmaya elverişli yüksek bir yere yerleştirdik.'),
(2840, 2724, 23, 51, 'Mü’minûn ', 'Mekki', 'Ey Peygamberler! Temiz şeylerden yiyin, yararlı iş işleyin; doğrusu Ben, yaptığınızı bilirim.'),
(2841, 2725, 23, 52, 'Mü’minûn ', 'Mekki', 'Şüphesiz bu Müslümanlık, bir tek din olarak sizin dininizdir ve Ben de Rabbinizim; öyleyse Benden sakının.'),
(2842, 2726, 23, 53, 'Mü’minûn ', 'Mekki', 'Ama insanlar din konusunda aralarında bölük bölük oldular. Her bölük kendi tuttuğu yoldan memnundur.'),
(2843, 2727, 23, 54, 'Mü’minûn ', 'Mekki', 'Onları bir süreye kadar sapıklıklarıyla başbaşa bırak.'),
(2844, 2728, 23, 55, 'Mü’minûn ', 'Mekki', 'Kendilerine mal ve oğullar vermekle, iyiliklerde onlar için acele ettiğimizi mi zannederler? Hayır; farkında değiller.'),
(2845, 2729, 23, 56, 'Mü’minûn ', 'Mekki', 'Kendilerine mal ve oğullar vermekle, iyiliklerde onlar için acele ettiğimizi mi zannederler? Hayır; farkında değiller.'),
(2846, 2730, 23, 57, 'Mü’minûn ', 'Mekki', 'Rablerinden korkarak titreyenler, Rablerinin ayetlerine inananlar, Rablerine eş koşmayanlar, Rablerine dönecekleri için kalbleri ürpererek vermeleri gerekeni verenler, işte onlar iyi işlerde yarış ederler, o uğurda ileri geçerler.'),
(2847, 2731, 23, 58, 'Mü’minûn ', 'Mekki', 'Rablerinden korkarak titreyenler, Rablerinin ayetlerine inananlar, Rablerine eş koşmayanlar, Rablerine dönecekleri için kalbleri ürpererek vermeleri gerekeni verenler, işte onlar iyi işlerde yarış ederler, o uğurda ileri geçerler.'),
(2848, 2732, 23, 59, 'Mü’minûn ', 'Mekki', 'Rablerinden korkarak titreyenler, Rablerinin ayetlerine inananlar, Rablerine eş koşmayanlar, Rablerine dönecekleri için kalbleri ürpererek vermeleri gerekeni verenler, işte onlar iyi işlerde yarış ederler, o uğurda ileri geçerler.'),
(2849, 2733, 23, 60, 'Mü’minûn ', 'Mekki', 'Rablerinden korkarak titreyenler, Rablerinin ayetlerine inananlar, Rablerine eş koşmayanlar, Rablerine dönecekleri için kalbleri ürpererek vermeleri gerekeni verenler, işte onlar iyi işlerde yarış ederler, o uğurda ileri geçerler.'),
(2850, 2734, 23, 61, 'Mü’minûn ', 'Mekki', 'Rablerinden korkarak titreyenler, Rablerinin ayetlerine inananlar, Rablerine eş koşmayanlar, Rablerine dönecekleri için kalbleri ürpererek vermeleri gerekeni verenler, işte onlar iyi işlerde yarış ederler, o uğurda ileri geçerler.'),
(2851, 2735, 23, 62, 'Mü’minûn ', 'Mekki', 'Biz herkese ancak gücünün yeteceği kadar yükleriz. Katımızda gerçeği söyleyen bir kitap vardır; onlar haksızlığa uğratılmazlar.'),
(2852, 2736, 23, 63, 'Mü’minûn ', 'Mekki', 'Ama, kafirlerin kalbleri bundan habersizdir. Bundan başka da onların yapageldikleri işler de vardır.'),
(2853, 2737, 23, 64, 'Mü’minûn ', 'Mekki', 'Sonunda şımarık varlıklılarını azabla yakaladığımız zaman feryat ederler.'),
(2854, 2738, 23, 65, 'Mü’minûn ', 'Mekki', 'Onlara şöyle deriz: \"Bugün feryat etmeyin, doğrusu katımızdan bir yardım görmezsiniz.\"'),
(2855, 2739, 23, 66, 'Mü’minûn ', 'Mekki', '\"Ayetlerim size okunduğunda büyüklük taslayıp, gece ağzınıza geleni söyleyerek ardınıza dönüyordunuz.\"'),
(2856, 2740, 23, 67, 'Mü’minûn ', 'Mekki', '\"Ayetlerim size okunduğunda büyüklük taslayıp, gece ağzınıza geleni söyleyerek ardınıza dönüyordunuz.\"'),
(2857, 2741, 23, 68, 'Mü’minûn ', 'Mekki', 'Söyleneni hiç düşünmezler mi? Yoksa onlara, ilk atalarına gelmeyen bir şey mi geldi?'),
(2858, 2742, 23, 69, 'Mü’minûn ', 'Mekki', 'Veya peygamberlerini tanımadılar da; bu yüzden mi onu inkar ediyorlar?'),
(2859, 2743, 23, 70, 'Mü’minûn ', 'Mekki', 'Ya da: \"Onda delilik var\" diyorlar öyle mi? Hayır; onlara gerçeği getirmiştir, ama çoğu ondan hoşlanmamaktadır.'),
(2860, 2744, 23, 71, 'Mü’minûn ', 'Mekki', 'Eğer gerçek onların heveslerine uysaydı, gökler, yer ve onlarda bulananlar bozulup giderdi. Onlara, kendilerine öğüt veren bir şey getirdik; onlar ise öğütlerinden yüz çevirirler.'),
(2861, 2745, 23, 72, 'Mü’minûn ', 'Mekki', 'Yoksa sen onlardan bir ücret mi istiyorsun? Rabbinin ecri daha iyidir. O, rızık verenlerin en hayırlısıdır.'),
(2862, 2746, 23, 73, 'Mü’minûn ', 'Mekki', 'Aslında sen onları doğru yola çağırıyorsun ama, ahirete inanmayanlar bu yoldan sapmaktadırlar.'),
(2863, 2747, 23, 74, 'Mü’minûn ', 'Mekki', 'Aslında sen onları doğru yola çağırıyorsun ama, ahirete inanmayanlar bu yoldan sapmaktadırlar.'),
(2864, 2748, 23, 75, 'Mü’minûn ', 'Mekki', 'Biz onlara acısak ve başlarındaki sıkıntıyı gidersek bile, azgınlıkları içinde bocalayıp kalırlar.'),
(2865, 2749, 23, 76, 'Mü’minûn ', 'Mekki', 'And olsun ki, Biz onları azabla yakalamıştık, yine de Rablerine boyun eğmemiş ve yakarmamışlardı.'),
(2866, 2750, 23, 77, 'Mü’minûn ', 'Mekki', 'Sonunda onlara şiddetli bir azap kapısı açtığımız zaman ümitsiz kalıverdiler.'),
(2867, 2751, 23, 78, 'Mü’minûn ', 'Mekki', 'Oysa, sizin için kulaklar, gözler ve kalbler vareden O\'dur. Pek az şükrediyorsunuz.'),
(2868, 2752, 23, 79, 'Mü’minûn ', 'Mekki', 'Sizi yerde yaratıp yayan O\'dur ve O\'nun huzurunda toplanacaksınız.'),
(2869, 2753, 23, 80, 'Mü’minûn ', 'Mekki', 'Dirilten de, öldüren de O\'dur. Gece ile gündüzün birbiri ardından gitmesi de O\'nun emrine bağlıdır. Düşünmez misiniz?'),
(2870, 2754, 23, 81, 'Mü’minûn ', 'Mekki', 'Hayır; yine de öncekilerin dediklerini derler.'),
(2871, 2755, 23, 82, 'Mü’minûn ', 'Mekki', 'Öncekiler: \"Ölüp toprak ve bir yığın kemik olduğumuzda mı diriltileceğiz? And olsun ki biz ve daha önce de babalarımız tehdit edilmişti; bu, öncekilerin masallarından başka birşey değildir\" demişlerdi.'),
(2872, 2756, 23, 83, 'Mü’minûn ', 'Mekki', 'Öncekiler: \"Ölüp toprak ve bir yığın kemik olduğumuzda mı diriltileceğiz? And olsun ki biz ve daha önce de babalarımız tehdit edilmişti; bu, öncekilerin masallarından başka birşey değildir\" demişlerdi.'),
(2873, 2757, 23, 84, 'Mü’minûn ', 'Mekki', 'De ki: \"Biliyorsanız söyleyin, yer ve onda bulunanlar kimindir?\"'),
(2874, 2758, 23, 85, 'Mü’minûn ', 'Mekki', '\"Allah\'ındır\" diyecekler, \"Öyleyse ders almaz mısınız?\" de.'),
(2875, 2759, 23, 86, 'Mü’minûn ', 'Mekki', '\"Yedi göğün de Rabbi, yüce arşın da Rabbi kimdir?\" de.'),
(2876, 2760, 23, 87, 'Mü’minûn ', 'Mekki', '\"Allah\'tır\" diyecekler! \"Öyleyse O\'na karşı gelmekten sakınmaz mısınız?\" de.'),
(2877, 2761, 23, 88, 'Mü’minûn ', 'Mekki', '\"Biliyorsanız söyleyin her şeyin hükümranlığı elinde olan, barındıran fakat himayeye muhtaç olmayan kimdir?\"'),
(2878, 2762, 23, 89, 'Mü’minûn ', 'Mekki', '\"Allah\'tır\" diyecekler; \"Öyleyse nasıl aldanıyorsunuz\" de.'),
(2879, 2763, 23, 90, 'Mü’minûn ', 'Mekki', 'Hayır; Biz onlara gerçeği getirdik ama, onlar yalancıdırlar.'),
(2880, 2764, 23, 91, 'Mü’minûn ', 'Mekki', 'Allah çocuk edinmemiştir; O\'nun yanında hiçbir tanrı yoktur, olsaydı, her tanrı kendi yarattığı ile beraber gider ve birbirinden üstün olmağa çalışırlardı. Allah onların vasıflandırdıklarından münezzehtir.'),
(2881, 2765, 23, 92, 'Mü’minûn ', 'Mekki', 'O, görülmeyeni de, görüleni de bilir. Koştukları ortaklardan yücedir.'),
(2882, 2766, 23, 93, 'Mü’minûn ', 'Mekki', 'De ki: \"Rabbim! Onların tehdit olundukları şeyi bana mutlaka göstereceksen, o zaman beni zalim milletin içinde bulundurma Yarabbi.\"'),
(2883, 2767, 23, 94, 'Mü’minûn ', 'Mekki', 'De ki: \"Rabbim! Onların tehdit olundukları şeyi bana mutlaka göstereceksen, o zaman beni zalim milletin içinde bulundurma Yarabbi.\"'),
(2884, 2768, 23, 95, 'Mü’minûn ', 'Mekki', 'Biz onlara vadettiğimizi sana elbette gösterebiliriz.'),
(2885, 2769, 23, 96, 'Mü’minûn ', 'Mekki', 'Kötülüğü en iyi ile sav. Onların vasıflandırmalarını Biz daha iyi biliriz.'),
(2886, 2770, 23, 97, 'Mü’minûn ', 'Mekki', 'De ki: \"Rabbim! Şeytanların kışkırtmalarından Sana sığınırım.\"'),
(2887, 2771, 23, 98, 'Mü’minûn ', 'Mekki', '\"Rabbim! Yanımda bulunmalarından da Sana sığınırım.\"'),
(2888, 2772, 23, 99, 'Mü’minûn ', 'Mekki', 'Onlardan birine ölüm gelince: \"Rabbim! Beni geri çevir, belki, yapmadan bıraktığımı tamamlar, iyi iş işlerim\" der. Hayır; bu söylediği sadece kendi lafıdır. Tekrar diriltilecekleri güne kadar arkalarında geriye dönmekten onları alıkoyan bir engel vardır.'),
(2889, 2773, 23, 100, 'Mü’minûn ', 'Mekki', 'Onlardan birine ölüm gelince: \"Rabbim! Beni geri çevir, belki, yapmadan bıraktığımı tamamlar, iyi iş işlerim\" der. Hayır; bu söylediği sadece kendi lafıdır. Tekrar diriltilecekleri güne kadar arkalarında geriye dönmekten onları alıkoyan bir engel vardır.'),
(2890, 2774, 23, 101, 'Mü’minûn ', 'Mekki', 'Sura üflendiği zaman, o gün, aralarındaki soy yakınlığı fayda vermez ve birbirlerine de birşey soramazlar.'),
(2891, 2775, 23, 102, 'Mü’minûn ', 'Mekki', 'Tartıları ağır gelenler, işte onlar kurtuluşa ermiş olanlardır.'),
(2892, 2776, 23, 103, 'Mü’minûn ', 'Mekki', 'Tartıları hafif gelenler, işte onlar, kendilerine yazık edendir, cehennemde temellidirler.'),
(2893, 2777, 23, 104, 'Mü’minûn ', 'Mekki', 'Ateş onların yüzlerini yalar, dişleri sırıtıp kalır.'),
(2894, 2778, 23, 105, 'Mü’minûn ', 'Mekki', 'Allah: \"Ayetlerim size okunurken onları yalanlıyordunuz değil mi?\" der.'),
(2895, 2779, 23, 106, 'Mü’minûn ', 'Mekki', 'Şöyle derler: \"Rabbimiz! Bizi bedbahtlığımız yenmişti; sapık bir millet olmuştuk.\"'),
(2896, 2780, 23, 107, 'Mü’minûn ', 'Mekki', '\"Rabbimiz! Bizi buradan çıkar, tekrar günaha dönersek, doğrusu zulmetmiş oluruz.\"'),
(2897, 2781, 23, 108, 'Mü’minûn ', 'Mekki', 'Allah: \"Sinin orada! Benimle konuşmayın. Kullarımdan bir topluluk: \"Rabbimiz! inandık, artık bizi bağışla, bize acı. Sen acıyanların en iyisisin\" diyordu. Siz ise, onları alaya alıyordunuz. Bu yaptıklarınız size Beni anmayı unutturuyordu. Onlara hep gülüyordunuz. Sabretmelerine karşılık bugün onları mükafatlandırdım. Doğrusu onlar kurtulanlardır\" der.'),
(2898, 2782, 23, 109, 'Mü’minûn ', 'Mekki', 'Allah: \"Sinin orada! Benimle konuşmayın. Kullarımdan bir topluluk: \"Rabbimiz! inandık, artık bizi bağışla, bize acı. Sen acıyanların en iyisisin\" diyordu. Siz ise, onları alaya alıyordunuz. Bu yaptıklarınız size Beni anmayı unutturuyordu. Onlara hep gülüyordunuz. Sabretmelerine karşılık bugün onları mükafatlandırdım. Doğrusu onlar kurtulanlardır\" der.'),
(2899, 2783, 23, 110, 'Mü’minûn ', 'Mekki', 'Allah: \"Sinin orada! Benimle konuşmayın. Kullarımdan bir topluluk: \"Rabbimiz! inandık, artık bizi bağışla, bize acı. Sen acıyanların en iyisisin\" diyordu. Siz ise, onları alaya alıyordunuz. Bu yaptıklarınız size Beni anmayı unutturuyordu. Onlara hep gülüyordunuz. Sabretmelerine karşılık bugün onları mükafatlandırdım. Doğrusu onlar kurtulanlardır\" der.'),
(2900, 2784, 23, 111, 'Mü’minûn ', 'Mekki', 'Allah: \"Sinin orada! Benimle konuşmayın. Kullarımdan bir topluluk: \"Rabbimiz! inandık, artık bizi bağışla, bize acı. Sen acıyanların en iyisisin\" diyordu. Siz ise, onları alaya alıyordunuz. Bu yaptıklarınız size Beni anmayı unutturuyordu. Onlara hep gülüyordunuz. Sabretmelerine karşılık bugün onları mükafatlandırdım. Doğrusu onlar kurtulanlardır\" der.'),
(2901, 2785, 23, 112, 'Mü’minûn ', 'Mekki', 'Allah onlara yine: \"Yeryüzünde kaç yıl kaldınız\" der.'),
(2902, 2786, 23, 113, 'Mü’minûn ', 'Mekki', '\"Bir gün veya daha az bir süre kaldık, sayanlara sor\" derler.'),
(2903, 2787, 23, 114, 'Mü’minûn ', 'Mekki', 'Allah\' \"Pek az kaldınız, keşke bilseydiniz! Sizi boşuna yarattığımızı ve Bize döndürülmeyeceğinizi mi sandınız?\" der.'),
(2904, 2788, 23, 115, 'Mü’minûn ', 'Mekki', 'Allah\' \"Pek az kaldınız, keşke bilseydiniz! Sizi boşuna yarattığımızı ve Bize döndürülmeyeceğinizi mi sandınız?\" der.'),
(2905, 2789, 23, 116, 'Mü’minûn ', 'Mekki', 'Gerçek hükümdar olan Allah yücedir. O\'ndan başka tanrı yoktur. O, yüce arşın Rabbidir.'),
(2906, 2790, 23, 117, 'Mü’minûn ', 'Mekki', 'Allah\'la beraber, varlığına hiçbir delili olmadığı halde başka tanrıya tapanın hesabını Rabbi görecektir. İnkarcılar elbette kurtulamazlar.'),
(2907, 2791, 23, 118, 'Mü’minûn ', 'Mekki', 'De ki: \"Rabbim! Bağışla, merhamet et, Sen merhamet edenlerin en hayırlısısın.\"'),
(2908, 2792, 24, 1, 'Nûr ', 'Medeni', 'Bu, indirip, hükümlerini kesinleştirdiğimiz suredir. Öğüt alasınız diye onda apaçık ayetler indirdik.'),
(2909, 2793, 24, 2, 'Nûr ', 'Medeni', 'Zina eden kadın ve erkeğin her birine yüzer değnek vurun. Allah\'a ve ahiret gününe inanıyorsanız, Allah\'ın dini konusunda o ikisine acımayın. Onların ceza görmesine, inananlardan bir topluluk da şahit olsun.'),
(2910, 2794, 24, 3, 'Nûr ', 'Medeni', 'Zina eden erkek, ancak zina eden veya putperest bir kadınla evlenebilir. Zina eden kadınla da, ancak zina eden veya putperest olan bir erkek evlenebilir. Bu, müminlere yasak edilmiştir.'),
(2911, 2795, 24, 4, 'Nûr ', 'Medeni', 'İffetli kadınlara zina isnat edip de, sonra dört şahit getiremeyenlere seksen değnek vurun; ebediyen onların şahidliğini kabul etmeyin. İşte onlar yoldan çıkmış kimselerdir.'),
(2912, 2796, 24, 5, 'Nûr ', 'Medeni', 'Ama bundan sonra, tevbe edip düzelenler bunun dışındadır. Şüphesiz Allah bağışlar ve merhamet eder.'),
(2913, 2797, 24, 6, 'Nûr ', 'Medeni', 'Karılarına zina isnat edip de kendilerinden başka şahidleri olmayanların şahidliği, kendisinin doğru sözlülerden olduğuna Allah\'ı dört defa şahit tutmasıyla olur. Beşincisinde, eğer yalancılardan ise Allah\'ın lanetinin kendisine olmasını diler.'),
(2914, 2798, 24, 7, 'Nûr ', 'Medeni', 'Karılarına zina isnat edip de kendilerinden başka şahidleri olmayanların şahidliği, kendisinin doğru sözlülerden olduğuna Allah\'ı dört defa şahit tutmasıyla olur. Beşincisinde, eğer yalancılardan ise Allah\'ın lanetinin kendisine olmasını diler.'),
(2915, 2799, 24, 8, 'Nûr ', 'Medeni', 'Kocasının yalancılardan olduğuna Allah\'ı dört defa şahit tutması, cezayı kadından savar. Beşincisinde, kocası doğrulardan ise kendisinin Allah\'ın gazabına uğramasını diler.'),
(2916, 2800, 24, 9, 'Nûr ', 'Medeni', 'Kocasının yalancılardan olduğuna Allah\'ı dört defa şahit tutması, cezayı kadından savar. Beşincisinde, kocası doğrulardan ise kendisinin Allah\'ın gazabına uğramasını diler.'),
(2917, 2801, 24, 10, 'Nûr ', 'Medeni', 'Allah\'ın size nimet ve rahmeti bulunmasa ve Allah tevbeleri kabul eden ve Hakim olmasaydı suçlunun hemen cezasını verirdi.'),
(2918, 2802, 24, 11, 'Nûr ', 'Medeni', '(Peygamber\'in eşi hakkında) o yalanı uyduranlar içinizden bir güruhtur. Bunu kendiniz için kötü sanmayın, o sizin için hayırlı olmuştur. O kimselerden her birine kazandığı günah karşılığı ceza vardır; içlerinden elebaşılık yapana ise büyük azap vardır.'),
(2919, 2803, 24, 12, 'Nûr ', 'Medeni', 'Onu işittiğiniz zaman, erkek kadın müminlerin, kendiliklerinden hüsnü zanda bulunup da: \"Bu apaçık bir iftiradır\" demeleri gerekmez miydi?'),
(2920, 2804, 24, 13, 'Nûr ', 'Medeni', 'Dört şahit getirmeleri gerekmez miydi? İşte bunlar, şahit getirmedikçe Allah katında yalancı olanlardır.'),
(2921, 2805, 24, 14, 'Nûr ', 'Medeni', 'Allah\'ın dünya ve ahirette size lütuf ve merhameti olmasaydı, o kötü sözü yaymanızdan ötürü büyük bir azaba uğrardınız.'),
(2922, 2806, 24, 15, 'Nûr ', 'Medeni', 'Onu dilinize dolamıştınız. Bilmediğiniz şeyleri ağzınıza alıyordunuz. Onu önemsiz bir şey sanıyordunuz, oysa Allah katında önemi büyüktü.'),
(2923, 2807, 24, 16, 'Nûr ', 'Medeni', 'O\'nu işittiğinizde: \"Bu konuda konuşmamız yakışık almaz; haşa, bu büyük bir iftiradır\" demeniz gerekmez miydi?'),
(2924, 2808, 24, 17, 'Nûr ', 'Medeni', 'Eğer mümin kişilerdenseniz, Allah buna benzer bir şeye bir daha dönmemenizi tavsiye eder.'),
(2925, 2809, 24, 18, 'Nûr ', 'Medeni', 'Allah size ayetleri açıkça bildirir. Allah bilendir, Hakim\'dir.'),
(2926, 2810, 24, 19, 'Nûr ', 'Medeni', 'Müminler arasından hayasızlığın yayılmasını arzu edenlere, işte onlara, dünya ve ahirette can yakıcı azap vardır. Allah bilir, siz ise bilmezsiniz.'),
(2927, 2811, 24, 20, 'Nûr ', 'Medeni', 'Allah\'ın size lütuf ve merhameti bulunmasaydı, Allah şefkatli ve merhametli olmasaydı hemen cezanızı verirdi.'),
(2928, 2812, 24, 21, 'Nûr ', 'Medeni', 'Ey İnananlar! Şeytana ayak uydurmayın. Kim şeytanın ardına takılırsa, bilsin ki, o, hayasızlığı ve fenalığı emreder. Allah\'ın size lütuf ve merhameti bulunmasaydı, hiçbiriniz ebediyen temize çıkamazdı. Fakat Allah dilediğini temize çıkarır. Allah işitir ve bilir.'),
(2929, 2813, 24, 22, 'Nûr ', 'Medeni', 'İçinizde lütuf ve servet sahibi olanlar, yakınlarına, düşkünlere ve Allah yolunda hicret edenlere, vermemek için yemin etmesinler, affetsinler, geçsinler. Allah\'ın sizi bağışlamasından hoşlanmaz mısınız? Allah bağışlayandır, merhametli olandır.'),
(2930, 2814, 24, 23, 'Nûr ', 'Medeni', 'İffetli, habersiz, mümin kadınlara zina isnat edenler dünya ve ahirette lanetlenmişlerdir. Kendi dilleri, elleri ve ayakları, yapmış olduklarına şahidlik ettikleri gün onlar büyük azaba uğrayacaklardır.'),
(2931, 2815, 24, 24, 'Nûr ', 'Medeni', 'İffetli, habersiz, mümin kadınlara zina isnat edenler dünya ve ahirette lanetlenmişlerdir. Kendi dilleri, elleri ve ayakları, yapmış olduklarına şahidlik ettikleri gün onlar büyük azaba uğrayacaklardır.'),
(2932, 2816, 24, 25, 'Nûr ', 'Medeni', 'O gün, Allah onlara kesinleşmiş cezalarını verecektir. Allah\'ın apaçık hak olduğunu bileceklerdir.'),
(2933, 2817, 24, 26, 'Nûr ', 'Medeni', 'Kötü kadınlar kötü erkeklere, kötü erkekler kötü kadınlara yakışırlar. İyi kadınlar iyi erkeklere, iyi erkekler de iyi kadınlara yakışırlar. Bunlar, onların söylediklerinden uzaktırlar. İşte bunlara mağfiret ve cömertçe verilmiş rızık vardır.'),
(2934, 2818, 24, 27, 'Nûr ', 'Medeni', 'Ey inananlar! Evlerinizden başka evlere, izin almadan, seslenip sahiplerine selam vermeden girmeyiniz. Eğer düşünürseniz bu sizin için daha iyidir.'),
(2935, 2819, 24, 28, 'Nûr ', 'Medeni', 'Eğer evde kimseyi bulamazsanız, yine de size izin verilmedikçe içeriye girmeyiniz. Size \"Dönün\" denirse dönün. Bu, sizi daha çok temize çıkarır. Allah yaptıklarınızı bilir.'),
(2936, 2820, 24, 29, 'Nûr ', 'Medeni', 'İçinde malınız bulunan boş evlere girmenizde bir sorumluluk yoktur. Allah, açığa vurduğunuzu da, gizlediğinizi de bilir.'),
(2937, 2821, 24, 30, 'Nûr ', 'Medeni', 'Mümin erkeklere söyle: Gözlerini bakılması yasak olandan çevirsinler, mahrem yerlerini, korusunlar. Bu, onların arınmasını daha iyi sağlar. Allah yaptıklarından şüphesiz haberdardır.'),
(2938, 2822, 24, 31, 'Nûr ', 'Medeni', 'Mümin kadınlara da söyle: Gözlerini bakılması yasak olandan çevirsinler, iffetlerini korusunlar. Süslerini, kendiliğinden görünen kısmı müstesna, açmasınlar. Baş örtülerini yakalarının üzerine salsınlar. Süslerini kocaları veya babaları ve kayınpederleri veya oğulları veya kocalarının oğulları veya kardeşleri veya erkek kardeşlerinin oğulları veya kızkardeşlerinin oğulları veya müslüman kadınları veya cariyeleri veya erkekliği kalmamış hizmetçiler, ya da kadınların mahrem yerlerini henüz anlamayan çocuklardan başkasına göstermesinler. Gizledikleri süslerin bilinmesi için ayaklarını yere vurmasınlar. Ey inananlar! Saadete ermeniz için hepiniz tevbe ederek Allah\'ın hükmüne dönün.'),
(2939, 2823, 24, 32, 'Nûr ', 'Medeni', 'İçinizdeki bekarları, kölelerinizden ve cariyelerinizden iyi olanları evlendirin. Eğer yoksul iseler, Allah onları lütfü ile zenginleştirir. Allah lütfü bol olandır, bilendir.'),
(2940, 2824, 24, 33, 'Nûr ', 'Medeni', 'Evlenemeyenler, Allah kendilerini lütfü ile zenginleştirene kadar iffetli davransınlar. Kölelerinizden hür olmak için bedel vermek isteyenlerin, onlarda bir iyilik görürseniz, bedel vermelerini kabul edin. Onlara Allah\'ın size verdiği maldan verin. Dünya hayatının geçici menfaatini elde etmek için, iffetli olmak isteyen cariyelerinizi fuhşa zorlamayın. Kim onları buna zorlarsa bilsin ki Allah hiç şüphesiz onu değil zorlanan kadınları bağışlar ve merhamet eder.'),
(2941, 2825, 24, 34, 'Nûr ', 'Medeni', 'And olsun ki, size apaçık ayetler, sizden önce geçenlerden misal ve sakınanlara öğüt indirdik.'),
(2942, 2826, 24, 35, 'Nûr ', 'Medeni', 'Allah göklerin ve yerin Nur\'udur. O\'nun nuru, içinde ışık bulunan bir kandil yuvasına benzer. O ışık bir cam içindedir, cam ise, sanki inci gibi parlayan bir yıldızdır; bu ne yalnız doğuda ve ne de yalnız batıda bulunan bereketli zeytin ağacından yakılır. Ateş değmese bile, nerdeyse yağın kendisi aydınlatacak! Nur üstüne nurdur. Allah dilediğini nuruna kavuşturur. Allah insanlara misaller verir. O, herşeyi bilir.'),
(2943, 2827, 24, 36, 'Nûr ', 'Medeni', 'Allah\'ın yüksek tutulmasına ve içlerinde adının anılmasına izin verdiği evlerde, insanlar sabah akşam O\'nu tesbih ederler.'),
(2944, 2828, 24, 37, 'Nûr ', 'Medeni', 'Bunları ne ticaret ve ne de alışveriş Allah\'ı anmaktan, namaz kılmaktan, zekat vermekten alıkoyar. Bunlar, gönüllerin ve gözlerin döneceği günden korkarlar.'),
(2945, 2829, 24, 38, 'Nûr ', 'Medeni', 'Allah, onları işlediklerinin en güzeliyle mükafatlandırır ve lütfundan onlara fazlasıyla verir. Allah dilediğini hesapsız şekilde rızıklandırır.'),
(2946, 2830, 24, 39, 'Nûr ', 'Medeni', 'İnkar edenlerin işleri engin çöllerdeki serap gibidir. Susayan kimse onu su zanneder, fakat oraya geldiğinde hiçbir şey bulamaz. Orada Allah\'ı bulur ve O da hesabını görür. Allah hesabı çabuk görendir.'),
(2947, 2831, 24, 40, 'Nûr ', 'Medeni', 'Veya derin denizin karanlıklarına benzer. Onu üstüste dalgalar ve dalgaların üstünde de bulutlar örter; karanlıklar üstünde karanlıklar; insan elini uzattığı zaman, nerdeyse onu bile göremez. Allah\'ın nur vermediği kimsenin nuru olmaz.'),
(2948, 2832, 24, 41, 'Nûr ', 'Medeni', 'Göklerde ve yerde olan kimselerin, sıra sıra uçan kuşların Allah\'ı tesbih ettiğini görmez misin? Her biri kendi niyaz ve tesbihini bilir. Allah, onların yaptıklarını bilendir.'),
(2949, 2833, 24, 42, 'Nûr ', 'Medeni', 'Göklerin ve yerin hükümranlığı Allah\'ındır. Dönüş Allah\'adır.'),
(2950, 2834, 24, 43, 'Nûr ', 'Medeni', 'Bilmez misiniz ki, Allah bulutları sürer, sonra onları bir araya getirir; üstüste yığar, sen de onların arasından yağmur yağdığını görürsün. Gökten içinde dolu bulunan dağlar gibi bulutlar indirir, dilediğini ona uğratır, dilediğinden de uzak tutar. Bu bulutların şimşeğinin parıltısı nerdeyse gözleri alır!'),
(2951, 2835, 24, 44, 'Nûr ', 'Medeni', 'Allah geceyi gündüze, gündüzü geceye çevirir. Doğrusu, görebilenler için bunda ibretler vardır.'),
(2952, 2836, 24, 45, 'Nûr ', 'Medeni', 'Allah bütün canlıları sudan yaratmıştır. Kimi karnı üzerinde sürünür, kimi iki ayakla yürür, kimi dört ayakla yürür. Allah dilediğini yaratır, Allah şüphesiz herşeye Kadir\'dir.'),
(2953, 2837, 24, 46, 'Nûr ', 'Medeni', 'And olsun ki, açıklayıcı ayetler indirmişizdir. Allah dilediğini doğru yola eriştirir.'),
(2954, 2838, 24, 47, 'Nûr ', 'Medeni', 'Münafıklar: \"Allah\'a ve Peygamber\'e inandık, itaat ettik\" derler; sonra da bir takımı yüz çevirirler. İşte bunlar inanmış değillerdir.'),
(2955, 2839, 24, 48, 'Nûr ', 'Medeni', 'Aralarında hüküm vermek üzere Allah\'a ve Peygamberine çağırıldıkları zaman, bir takımı hemen yüz çevirirler. İşte bunlar inanmış değillerdir.'),
(2956, 2840, 24, 49, 'Nûr ', 'Medeni', 'Ama hak kendilerinden tarafa ise, itaatle koşa koşa gelirler.'),
(2957, 2841, 24, 50, 'Nûr ', 'Medeni', 'Kalplerinde hastalık mı var, yoksa şüphelenmişler midir, yahut Allah\'ın ve Peygamberinin onlara haksızlık yapacağından mı korkmaktadırlar? Hayır; onlar sadece zalimdirler.'),
(2958, 2842, 24, 51, 'Nûr ', 'Medeni', 'Aralarında hüküm verilmek üzere Allah\'a ve Peygambere çağırıldıkları vakit: \"İşittik, itaat ettik\" demek, ancak müminlerin sözüdür, işte saadete erenler onlardır.'),
(2959, 2843, 24, 52, 'Nûr ', 'Medeni', 'Allah\'a ve Peygambere itaat eden, Allah\'tan korkan ve O\'ndan sakınan kimseler, işte onlar kurtulanlardır.'),
(2960, 2844, 24, 53, 'Nûr ', 'Medeni', 'Eğer kendilerine emredersen, o iki yüzlüler, savaşa çıkacaklarına bütün güçleriyle yemin ederler. De ki: \"Yemin etmeyin; itaatiniz malumdur. Allah yaptıklarınızdan şüphesiz haberdardır.\"'),
(2961, 2845, 24, 54, 'Nûr ', 'Medeni', 'De ki: \"Allah\'a itaat edin; Peygambere itaat edin.\" Eğer yüz çevirirseniz, bilin ki o Peygamber, kendisine yükletilenden ve siz de kendinize yükletilenden sorumlusunuz. Eğer O\'na itaat ederseniz doğru yolu bulursunuz, Peygambere düşen sadece, apaçık tebliğdir.'),
(2962, 2846, 24, 55, 'Nûr ', 'Medeni', 'Allah, içinizden inanıp yararlı iş işleyenlere, onlardan öncekileri halef kıldığı gibi, onları da yeryüzüne halef kılacağına, onlar için beğendiği dini temelli yerleştireceğine, korkularını güvene çevireceğine dair söz vermiştir. Çünkü onlar Bana kulluk eder, hiçbir şeyi Bana ortak koşmazlar. Bundan sonra inkar eden kimseler, işte onlar artık yoldan çıkmış olanlardır.'),
(2963, 2847, 24, 56, 'Nûr ', 'Medeni', 'Namaz kılın, zekat verin, Peygambere itaat edin ki size merhamet edilsin.'),
(2964, 2848, 24, 57, 'Nûr ', 'Medeni', 'İnkar edenlerin, Bizi yeryüzünde aciz bırakacaklarını sanmayasın. Varacakları yer ateştir. Ne kötü dönüştür!'),
(2965, 2849, 24, 58, 'Nûr ', 'Medeni', 'Ey inananlar! Ellerinizin altında olan köle ve cariyeler ve sizden henüz erginliğe ermemiş olanlar, sabah namazından önce, öğle sıcağında soyunduğunuzda ve yatsı namazından sonra yanınıza gireceklerinde üç defa izin istesinler. Bunlar, sizin açık bulunabileceğiniz üç vakittir. Bu vakitlerin dışında birbirinizin yanına girip çıkmakta size de, onlara da bir sorumluluk yoktur. Allah size ayetlerini böylece açıklar. Allah bilendir, Hakim\'dir.'),
(2966, 2850, 24, 59, 'Nûr ', 'Medeni', 'Çocuklarınız erginlik çağına gelince, büyüklerinin izin istediği gibi, onlar da her defasında izin istesinler. Allah size ayetlerini böylece açıklar. Allah bilendir, Hakim\'dir.'),
(2967, 2851, 24, 60, 'Nûr ', 'Medeni', 'Evlenme ümidi kalmayan, ihtiyarlayıp oturmuş kadınlara, süslerini açığa vurmamak şartiyle, dış esvaplarını çıkarmaktan ötürü sorumluluk yoktur; ama sakınmaları kendileri için daha iyi olur. Allah işitir ve bilir.'),
(2968, 2852, 24, 61, 'Nûr ', 'Medeni', 'Kör için bir sorumluluk yoktur. Topal için bir sorumluluk yoktur. Hastaya da bir sorumluluk yoktur. Evlerinizde veya babalarınızın evlerinde veya annelerinizin evlerinde veya erkek kardeşlerinizin evlerinde veya kız kardeşlerinizin evlerinde veya amcalarınızın evlerinde veya halalarınızın evlerinde veya dayılarınızın evlerinde veya teyzelerinizin evlerinde veya kahyası olup anahtarları elinde olan evlerde, ya da dostlarınızın evlerinde izinsiz yemek yemenizde bir sorumluluk yoktur. Bir arada veya ayrı ayrı yemenizde de bir sorumluluk yoktur. Evlere girdiğiniz zaman, kendinize ehlinize Allah katından bereket, esenlik ve güzellik dileyerek selam verin. Allah size ayetleri, düşünesiniz diye böylece açıklar.'),
(2969, 2853, 24, 62, 'Nûr ', 'Medeni', 'Doğrusu Allah\'a ve Peygamberine inanan Müminler, Peygamberle beraber bir işe karar vermek için toplandıklarında, ondan izin almaksızın gitmezler. Senden izin isteyenler, işte onlar, Allah\'a ve Peygamberine inananlardır. Bazı işleri için senden izin isterlerse, içlerinden dilediğine izin ver, Allah\'tan, onların bağışlanmalarını dile. Allah şüphesiz bağışlar, merhamet eder.'),
(2970, 2854, 24, 63, 'Nûr ', 'Medeni', 'Peygamberin çağrısını, kendi aranızda birbirinizi çağırmanız gibi tutmayın. Allah, içinizden sıvışıp gidenleri şüphesiz bilir. O\'nun buyruğuna aykırı hareket edenler, başlarına bir belanın gelmesinden veya can yakıcı bir azaba uğramaktan sakınsınlar.'),
(2971, 2855, 24, 64, 'Nûr ', 'Medeni', 'Dikkat edin; göklerde ve yerde olanlar Allah\'ındır. O, içinde bulunduğunuz durumu da, kendisine döndürüleceğiniz günü de gerçekten bilir. Onlara işlediklerini haber verir. Allah herşeyi bilir.'),
(2972, 2856, 25, 1, 'Furkân ', 'Mekki', 'Göklerin ve yerin hükümranlığı kendisinin olan, çocuk edinmeyen, hükümranlıkta ortağı bulunmayan, herşeyi yaratıp bir ölçüye göre düzenleyen ve dünyaları uyarmak üzere kuluna hakkı batıldan ayırdeden Kuran\'ı indiren Allah yücelerin yücesidir.'),
(2973, 2857, 25, 2, 'Furkân ', 'Mekki', 'Göklerin ve yerin hükümranlığı kendisinin olan, çocuk edinmeyen, hükümranlıkta ortağı bulunmayan, herşeyi yaratıp bir ölçüye göre düzenleyen ve dünyaları uyarmak üzere kuluna hakkı batıldan ayırdeden Kuran\'ı indiren Allah yücelerin yücesidir.'),
(2974, 2858, 25, 3, 'Furkân ', 'Mekki', 'Kafirler, O\'nu bırakıp, birşey yaratamayan, bilakis kendileri yaratılmış olan, kendilerine ne zarar ve ne de fayda verebilen; öldürmeye, diriltmeye ve ölümden sonra tekrar canlandırmaya güçleri yetmeyen tanrılar edindiler.'),
(2975, 2859, 25, 4, 'Furkân ', 'Mekki', 'İnkar edenler: \"Bu Kuran uydurmadır, ona başka bir topluluk yardım etmiştir\" diyerek haksız ve asılsız bir söz uydurdular.'),
(2976, 2860, 25, 5, 'Furkân ', 'Mekki', '\"Kuran öncekilerin masallarıdır; başkalarına yazdırıp sabah akşam kendisine okunmaktadır\" dediler.'),
(2977, 2861, 25, 6, 'Furkân ', 'Mekki', 'De ki: \"Onu, göklerin ve yerin sırrını bilen indirmiştir. Şüphesiz O, bağışlayandır, merhamet edendir.\"'),
(2978, 2862, 25, 7, 'Furkân ', 'Mekki', 'Şöyle dediler: \"Bu ne biçim peygamber ki yemek yer, sokaklarda gezer? Ona, beraberinde bulunup uyaran bir melek indirilseydi ya! Yahut, kendisine bir hazine verilseydi, veya besleneceği bir bahçe olsaydı ya!\" Bu zalimler, inananlara: \"Siz sadece büyülenmiş bir adama uyuyorsunuz\" dediler.'),
(2979, 2863, 25, 8, 'Furkân ', 'Mekki', 'Şöyle dediler: \"Bu ne biçim peygamber ki yemek yer, sokaklarda gezer? Ona, beraberinde bulunup uyaran bir melek indirilseydi ya! Yahut, kendisine bir hazine verilseydi, veya besleneceği bir bahçe olsaydı ya!\" Bu zalimler, inananlara: \"Siz sadece büyülenmiş bir adama uyuyorsunuz\" dediler.'),
(2980, 2864, 25, 9, 'Furkân ', 'Mekki', 'Sana nasıl misaller getirdiklerine bir bak! Onlar sapmışlardır, yol bulamazlar.'),
(2981, 2865, 25, 10, 'Furkân ', 'Mekki', 'Dilerse sana, bunlardan daha iyi olan, içlerinden ırmaklar akan cennetler verebilen ve köşkler kurabilen Allah yücelerin yücesidir.'),
(2982, 2866, 25, 11, 'Furkân ', 'Mekki', 'Zaten onlar, kıyamet saatini de yalanladılar. O saatin geleceğini yalanlayanlara çılgın alevli bir ateş hazırlamışızdır.'),
(2983, 2867, 25, 12, 'Furkân ', 'Mekki', 'Bu ateş, onlara uzak bir yerden gözükünce, onun kaynamasını ve uğultusunu işitirler.'),
(2984, 2868, 25, 13, 'Furkân ', 'Mekki', 'Elleri boyunlarına bağlanarak, dar bir yerden atıldıkları zaman, orada, yok olup gitmeyi isterler.'),
(2985, 2869, 25, 14, 'Furkân ', 'Mekki', '\"Bir kere yok olmayı değil, birçok defa yok olmayı isteyin\" denir.'),
(2986, 2870, 25, 15, 'Furkân ', 'Mekki', 'De ki: \"Bu mu iyidir, yoksa Allah\'a karşı gelmekten sakınanlara mükafat ve gidilecek yer olarak söz verilen ebedi cennet mi daha iyidir?\"'),
(2987, 2871, 25, 16, 'Furkân ', 'Mekki', 'Temelli kalacakları cennette diledikleri şeyleri bulurlar. Bu, Rabbinin yerine getirilmesi istenen bir vaadidir.'),
(2988, 2872, 25, 17, 'Furkân ', 'Mekki', 'O gün Rabbin onları ve Allah\'ı bırakıp da taptıkları şeyleri toplar ve: \"Bu kullarımı siz mi saptırdınız, yoksa kendi kendilerine mi yoldan saptılar?\" der.'),
(2989, 2873, 25, 18, 'Furkân ', 'Mekki', 'Onlar: \"Haşa; Seni bırakıp başka dostlar edinmek bize yaraşmaz; fakat Sen onlara ve babalarına nimetler verdin de sonunda Seni anmayı unuttular ve helaki hak eden bir millet oldular\" derler.'),
(2990, 2874, 25, 19, 'Furkân ', 'Mekki', '\"Söylediklerinizde sizi yalancı çıkardılar, artık kendinizden azabı çeviremez, yardım da göremezsiniz. Zulmedenlerinize büyük bir azap tattıracağız\" denir.'),
(2991, 2875, 25, 20, 'Furkân ', 'Mekki', 'Senden önce gönderdiğimiz bütün peygamberler de, şüphesiz, yemek yerler, sokaklarda gezerlerdi. Ey insanlar! Sabreder misiniz diye sizi birbirinizle sınarız. Rabbin her şeyi görür.'),
(2992, 2876, 25, 21, 'Furkân ', 'Mekki', 'Bizimle karşılaşmayı ummayanlar: \"Bize ya melekler indirilmeli, ya da Rabbimiz\'i görmeliyiz\" derler. And olsun ki kendi kendilerine büyüklenmişler, azgınlıkta pek ileri gitmişlerdir.'),
(2993, 2877, 25, 22, 'Furkân ', 'Mekki', 'Melekleri gördükleri gün, işte o gün, suçlulara iyi haber yoktur. Melekler: \"İyi haber size yasaktır, yasak!\" derler.'),
(2994, 2878, 25, 23, 'Furkân ', 'Mekki', 'Yaptıkları her işi ele alır, onu toz duman ederiz.'),
(2995, 2879, 25, 24, 'Furkân ', 'Mekki', 'O gün, cennetliklerin kalacağı yer çok iyi, dinlenecekleri yer çok güzeldir.'),
(2996, 2880, 25, 25, 'Furkân ', 'Mekki', 'O gün, gök beyaz bulutlar halinde parçalanacak ve melekler bölük bölük indirilecektir.'),
(2997, 2881, 25, 26, 'Furkân ', 'Mekki', 'O gün gerçek hükümdarlık Rahman\'ındır. İnkarcılar için yaman bir gündür.'),
(2998, 2882, 25, 27, 'Furkân ', 'Mekki', 'O gün, zalim kimse ellerini ısırıp: \"Keşke Peygamberle beraber bir yol tutsaydım, vay başıma gelene; keşke falancayı dost edinmeseydim. And olsun ki beni, bana gelen Kuran\'dan o saptırdı. Şeytan insanı yalnız ve yardımcısız bırakıyor\" der.'),
(2999, 2883, 25, 28, 'Furkân ', 'Mekki', 'O gün, zalim kimse ellerini ısırıp: \"Keşke Peygamberle beraber bir yol tutsaydım, vay başıma gelene; keşke falancayı dost edinmeseydim. And olsun ki beni, bana gelen Kuran\'dan o saptırdı. Şeytan insanı yalnız ve yardımcısız bırakıyor\" der.'),
(3000, 2884, 25, 29, 'Furkân ', 'Mekki', 'O gün, zalim kimse ellerini ısırıp: \"Keşke Peygamberle beraber bir yol tutsaydım, vay başıma gelene; keşke falancayı dost edinmeseydim. And olsun ki beni, bana gelen Kuran\'dan o saptırdı. Şeytan insanı yalnız ve yardımcısız bırakıyor\" der.'),
(3001, 2885, 25, 30, 'Furkân ', 'Mekki', 'Peygamber: \"Ey Rabbim! Doğrusu milletim bu Kuran\'ı terketmişti\" der.'),
(3002, 2886, 25, 31, 'Furkân ', 'Mekki', 'Her peygamber için, böylece suçlulardan bir düşman ortaya koyarız. Doğruyu gösterici ve yardımcı olarak, Rabbin yeter.'),
(3003, 2887, 25, 32, 'Furkân ', 'Mekki', 'İnkar edenler: \"Kuran ona bir defada indirilmeliydi\" derler. Oysa Biz onu böylece senin kalbine yerleştirmek için azar azar indirir ve onu ağır ağır okuruz.'),
(3004, 2888, 25, 33, 'Furkân ', 'Mekki', 'Sana bir misal vermezler ki, Biz onun gerçeğini ve en iyi anlaşılanını sana vermemiş olalım.'),
(3005, 2889, 25, 34, 'Furkân ', 'Mekki', 'Cehennemde yüzü koyun toplanacak olanlar, işte onların yerleri en kötü ve yolları da en sapıktır.'),
(3006, 2890, 25, 35, 'Furkân ', 'Mekki', 'And olsun ki Musa\'ya Kitap verdik, kardeşi Harun\'u da kendisine vezir yaptık.'),
(3007, 2891, 25, 36, 'Furkân ', 'Mekki', '\"Ayetlerimizi yalanlayan millete gidin\" dedik. Sonunda o milleti yerle bir ettik.'),
(3008, 2892, 25, 37, 'Furkân ', 'Mekki', 'Nuh milletini de, peygamberleri yalanladıkları zaman suda boğduk ve kendilerini insanlar için bir ibret kıldık. Zalimlere can yakıcı azap hazırlamışızdır.'),
(3009, 2893, 25, 38, 'Furkân ', 'Mekki', 'Ad, Semud milletleri ile Ress\'lileri ve bunların arasında birçok nesilleri de yerle bir ettik.'),
(3010, 2894, 25, 39, 'Furkân ', 'Mekki', 'Her birine misaller vermiştik ama, dinlemedikleri için hepsini kırdık geçirdik.'),
(3011, 2895, 25, 40, 'Furkân ', 'Mekki', 'Bu putperestler and olsun ki, bela yağmuruna tutulmuş olan kasabaya uğramışlardı. Onu görmediler mi? Hayır; tekrar dirilmeyi ummuyorlardı.'),
(3012, 2896, 25, 41, 'Furkân ', 'Mekki', 'Seni gördükleri zaman, \"Allah\'ın gönderdiği elçi bu mudur?\" diye alaya almaktan başka birşey yapmazlar.'),
(3013, 2897, 25, 42, 'Furkân ', 'Mekki', '\"Tanrılarımız üzerinde direnmeseydik, doğrusu neredeyse bizi onlardan uzaklaştıracaktı\" derler. Azabı gördükleri zaman, kimin yolunun sapık olduğunu bileceklerdir.'),
(3014, 2898, 25, 43, 'Furkân ', 'Mekki', 'Hevesini kendine tanrı edineni gördün mü? Ona sen mi vekil olacaksın?'),
(3015, 2899, 25, 44, 'Furkân ', 'Mekki', 'Yoksa çoklarının söz dinlediklerini veya aklettiklerini mi sanırsın? Onlar şüphesiz davarlar gibidir, belki daha da sapık yolludurlar.'),
(3016, 2900, 25, 45, 'Furkân ', 'Mekki', 'Rabbinin gölgeyi nasıl uzattığını görmez misin? İsteseydi onu durdururdu. Sonra Biz güneşi, ona delil kılıp yavaş yavaş kendimize çekmişizdir.'),
(3017, 2901, 25, 46, 'Furkân ', 'Mekki', 'Rabbinin gölgeyi nasıl uzattığını görmez misin? İsteseydi onu durdururdu. Sonra Biz güneşi, ona delil kılıp yavaş yavaş kendimize çekmişizdir.'),
(3018, 2902, 25, 47, 'Furkân ', 'Mekki', 'Size geceyi örtü, uykuyu rahatlık kılan, gündüzü çalışma zamanı yapan Allah\'tır.'),
(3019, 2903, 25, 48, 'Furkân ', 'Mekki', 'Rüzgarları rahmetinin önünde müjdeci gönderen O\'dur. Ölü bir yeri diriltmek ve yarattığımız nice hayvan ve insanları sulamak için gökten tertemiz su indirmişizdir.'),
(3020, 2904, 25, 49, 'Furkân ', 'Mekki', 'Rüzgarları rahmetinin önünde müjdeci gönderen O\'dur. Ölü bir yeri diriltmek ve yarattığımız nice hayvan ve insanları sulamak için gökten tertemiz su indirmişizdir.'),
(3021, 2905, 25, 50, 'Furkân ', 'Mekki', 'And olsun ki öğüt almaları için ülkeler arasında yer yer türlü türlü yağmur yağdırmışızdır. Buna rağmen insanların çoğu nankörlükte direnmiştir.'),
(3022, 2906, 25, 51, 'Furkân ', 'Mekki', 'Dileseydik, her kente bir uyarıcı gönderirdik.'),
(3023, 2907, 25, 52, 'Furkân ', 'Mekki', 'Sen, inkarcılara uyma, onlara karşı olanca gücünle mücadele et.'),
(3024, 2908, 25, 53, 'Furkân ', 'Mekki', 'Birinin suyu tatlı ve kolay içimli, diğerininki tuzlu ve acı olan iki denizi salıverip aralarına da, karışmalarına engel olan bir sınır koyan Allah\'tır.'),
(3025, 2909, 25, 54, 'Furkân ', 'Mekki', 'İnsanı sudan yaratarak, ona soy sop veren O\'dur. Rabbin herşeye Kadir\'dir.'),
(3026, 2910, 25, 55, 'Furkân ', 'Mekki', 'Allah\'ı bırakıp, kendilerine fayda da zarar da veremeyen şeylere kulluk ederler. İnkar eden, Rabbine karşı gelenin (şeytanın) yardımcısıdır.'),
(3027, 2911, 25, 56, 'Furkân ', 'Mekki', 'Biz seni sadece müjdeci ve uyarıcı olarak gönderdik.'),
(3028, 2912, 25, 57, 'Furkân ', 'Mekki', 'De ki: \"Ben buna karşı sizden bir ücret değil, ancak, Rabbine doğru bir yol tutmak dileyen kimseler olmanızı istiyorum.\"'),
(3029, 2913, 25, 58, 'Furkân ', 'Mekki', 'Ölümsüz, diri olan Allah\'a güven, O\'nu överek tesbih et. Kullarının günahlarından haberdar olarak kendisi yeter.'),
(3030, 2914, 25, 59, 'Furkân ', 'Mekki', 'Gökleri, yeri ve ikisinin arasındakileri altı günde yaratan sonra da arşa hükmeden Rahman\'dır. Bunu bir bilene sor.'),
(3031, 2915, 25, 60, 'Furkân ', 'Mekki', 'Onlara: \"Rahman\'a secdeye varın\" dendiği zaman \"Rahman da nedir? Emrettiğine mi secdeye varacağız?\" derler. Bu, onların nefretini artırır.'),
(3032, 2916, 25, 61, 'Furkân ', 'Mekki', 'Gökte burçlar vareden, orada ışık saçan güneş ve aydınlatan ayı yaratan Allah, yücelerin yücesidir.'),
(3033, 2917, 25, 62, 'Furkân ', 'Mekki', 'İbret almak veya şükretmek dileyen kimseler için gece ile gündüzü birbiri ardınca getiren O\'dur.'),
(3034, 2918, 25, 63, 'Furkân ', 'Mekki', 'Rahman\'ın kulları yeryüzünde mütevazı yürürler. Bilgisizler kendilerine takıldıkları zaman onlara güzel ve yumuşak söz söylerler.'),
(3035, 2919, 25, 64, 'Furkân ', 'Mekki', 'Onlar, gecelerini Rableri için kıyama durarak ve secdeye vararak geçirirler.'),
(3036, 2920, 25, 65, 'Furkân ', 'Mekki', 'Onlar, \"Rabbimiz! Bizden cehennem azabını uzaklaştır; doğrusu onun azabı sürekli ve acıdır. Orası şüphesiz kötü bir yer ve kötü bir duraktır\" derler.'),
(3037, 2921, 25, 66, 'Furkân ', 'Mekki', 'Onlar, \"Rabbimiz! Bizden cehennem azabını uzaklaştır; doğrusu onun azabı sürekli ve acıdır. Orası şüphesiz kötü bir yer ve kötü bir duraktır\" derler.'),
(3038, 2922, 25, 67, 'Furkân ', 'Mekki', 'Onlar, sarfettikleri zaman ne israf ederler ne de cimrilik, ikisi arasında orta bir yol tutarlar.'),
(3039, 2923, 25, 68, 'Furkân ', 'Mekki', 'Onlar, Allah\'ın yanında başka tanrı tutup ona yalvarmazlar. Allah\'ın haram kıldığı cana haksız yere kıymazlar. Zina etmezler. Bunları yapan günaha girmiş olur.'),
(3040, 2924, 25, 69, 'Furkân ', 'Mekki', 'Kıyamet günü azabı kat kat olur, orada, alçaltılarak temelli kalır.'),
(3041, 2925, 25, 70, 'Furkân ', 'Mekki', 'Ancak tevbe eden, inanıp yararlı iş işleyenlerin, işte Allah onların kötülüklerini iyiliklere çevirir. Allah bağışlar ve merhamet eder.'),
(3042, 2926, 25, 71, 'Furkân ', 'Mekki', 'Kim tevbe edip yararlı iş işlerse, şüphesiz o, Allah\'a gereği gibi yönelmiş olur.'),
(3043, 2927, 25, 72, 'Furkân ', 'Mekki', 'Onlar yalan yere şehadet etmezler; faydasız birşeye rastladıkları zaman yüz çevirip vakarla geçerler.'),
(3044, 2928, 25, 73, 'Furkân ', 'Mekki', 'Kendilerine Rablerinin ayetleri hatırlatıldığı zaman, onlara karşı kör ve sağır davranmazlar.'),
(3045, 2929, 25, 74, 'Furkân ', 'Mekki', 'Onlar: \"Rabbimiz! Bize eşlerimizden ve çocuklarımızdan gözümüzün aydınlığı olacak insanlar ihsan et ve bizi, Allah\'a karşı gelmekten sakınanlara önder yap\" derler.'),
(3046, 2930, 25, 75, 'Furkân ', 'Mekki', 'İşte onlar, sabrettiklerinden ötürü cennetin en yüksek dereceleriyle mükafatlandırılırlar. Orada esenlik ve dirlik dilekleriyle karşılanırlar.'),
(3047, 2931, 25, 76, 'Furkân ', 'Mekki', 'Orada temellidirler. Orası ne güzel bir yer ve ne güzel duraktır!'),
(3048, 2932, 25, 77, 'Furkân ', 'Mekki', 'De ki: \"İbadetiniz (duanız) olmasa Rabbim size ne diye değer versin?\" Ey inkarcılar! Yalanladığınız için, azap yakanızı bırakmayacaktır.'),
(3049, 2933, 26, 1, 'Şu’arâ ', 'Mekki', 'Ta, Sin, Mim.'),
(3050, 2934, 26, 2, 'Şu’arâ ', 'Mekki', 'Bunlar apaçık Kitap\'ın ayetleridir.'),
(3051, 2935, 26, 3, 'Şu’arâ ', 'Mekki', 'İnanmıyorlar diye nerdeyse kendini mahvedeceksin.'),
(3052, 2936, 26, 4, 'Şu’arâ ', 'Mekki', 'Biz dilesek onlara gökten bir mucize indiririz de ona boyun eğip kalırlar.'),
(3053, 2937, 26, 5, 'Şu’arâ ', 'Mekki', 'Rahman\'dan kendilerine gelen her yeni öğütten mutlaka yüz çevirirler.'),
(3054, 2938, 26, 6, 'Şu’arâ ', 'Mekki', 'Evet, yalanladılar; alay edip durdukları şeylerin haberleri kendilerine ulaşacaktır.'),
(3055, 2939, 26, 7, 'Şu’arâ ', 'Mekki', 'Yeryüzüne bakmazlar mı? Orada, bitkilerden nice güzel çiftler yetiştirmişizdir.'),
(3056, 2940, 26, 8, 'Şu’arâ ', 'Mekki', 'Şüphesiz bunlarda Allah\'ın kudretine işaret vardır, ama çoğu inanmazlar.'),
(3057, 2941, 26, 9, 'Şu’arâ ', 'Mekki', 'Rabbin şüphesiz güçlüdür, merhametlidir.'),
(3058, 2942, 26, 10, 'Şu’arâ ', 'Mekki', 'Rabbin Musa\'ya: \"Haksızlık eden millete, Firavun\'un milletine git\" diye nida etmişti. \"Haksızlıktan sakınmazlar mı?\"'),
(3059, 2943, 26, 11, 'Şu’arâ ', 'Mekki', 'Rabbin Musa\'ya: \"Haksızlık eden millete, Firavun\'un milletine git\" diye nida etmişti. \"Haksızlıktan sakınmazlar mı?\"'),
(3060, 2944, 26, 12, 'Şu’arâ ', 'Mekki', 'Musa: \"Rabbim! Doğrusu beni yalanlamalarından korkuyorum; göğsüm daralıyor, dilim açılmıyor. Onun için Harun\'a da elçilik ver. Onların bana isnat ettikleri bir suç da vardır. Beni öldürmelerinden korkuyorum\" demişti.'),
(3061, 2945, 26, 13, 'Şu’arâ ', 'Mekki', 'Musa: \"Rabbim! Doğrusu beni yalanlamalarından korkuyorum; göğsüm daralıyor, dilim açılmıyor. Onun için Harun\'a da elçilik ver. Onların bana isnat ettikleri bir suç da vardır. Beni öldürmelerinden korkuyorum\" demişti.'),
(3062, 2946, 26, 14, 'Şu’arâ ', 'Mekki', 'Musa: \"Rabbim! Doğrusu beni yalanlamalarından korkuyorum; göğsüm daralıyor, dilim açılmıyor. Onun için Harun\'a da elçilik ver. Onların bana isnat ettikleri bir suç da vardır. Beni öldürmelerinden korkuyorum\" demişti.'),
(3063, 2947, 26, 15, 'Şu’arâ ', 'Mekki', 'Allah: \"Hayır; ikiniz mucizelerimizle gidiniz. Doğrusu Biz sizinle beraber dinlemekteyiz. Firavun\'a varınız: \"Biz şüphesiz alemlerin Rabbinin elçisiyiz; İsrailoğullarını bizimle beraber gönder, deyiniz\" demişti.'),
(3064, 2948, 26, 16, 'Şu’arâ ', 'Mekki', 'Allah: \"Hayır; ikiniz mucizelerimizle gidiniz. Doğrusu Biz sizinle beraber dinlemekteyiz. Firavun\'a varınız: \"Biz şüphesiz alemlerin Rabbinin elçisiyiz; İsrailoğullarını bizimle beraber gönder, deyiniz\" demişti.'),
(3065, 2949, 26, 17, 'Şu’arâ ', 'Mekki', 'Allah: \"Hayır; ikiniz mucizelerimizle gidiniz. Doğrusu Biz sizinle beraber dinlemekteyiz. Firavun\'a varınız: \"Biz şüphesiz alemlerin Rabbinin elçisiyiz; İsrailoğullarını bizimle beraber gönder, deyiniz\" demişti.'),
(3066, 2950, 26, 18, 'Şu’arâ ', 'Mekki', 'Firavun Musa\'ya: \"Biz seni çocukken yanımıza alıp büyütmedik mi? Hayatının birçok yıllarını aramızda geçirmedin mi? Sonunda yapacağını da yaptın. Sen nankörün birisin\" dedi.'),
(3067, 2951, 26, 19, 'Şu’arâ ', 'Mekki', 'Firavun Musa\'ya: \"Biz seni çocukken yanımıza alıp büyütmedik mi? Hayatının birçok yıllarını aramızda geçirmedin mi? Sonunda yapacağını da yaptın. Sen nankörün birisin\" dedi.'),
(3068, 2952, 26, 20, 'Şu’arâ ', 'Mekki', 'Musa: \"O işi kasden yaptımsa sapıklardan biri sayılırım. Bu yüzden sizden korkunca aranızdan kaçtım. Sonra, Rabbim bana hikmet verip, beni peygamber yaptı. Başıma kaktığın bu nimet, İsrailoğullarını kendine köle ettiğinden ötürüdür\" dedi.'),
(3069, 2953, 26, 21, 'Şu’arâ ', 'Mekki', 'Musa: \"O işi kasden yaptımsa sapıklardan biri sayılırım. Bu yüzden sizden korkunca aranızdan kaçtım. Sonra, Rabbim bana hikmet verip, beni peygamber yaptı. Başıma kaktığın bu nimet, İsrailoğullarını kendine köle ettiğinden ötürüdür\" dedi.');
INSERT INTO `tr_diyanet` (`id`, `index`, `sura`, `aya`, `sure_adi`, `sure_yer`, `text`) VALUES
(3070, 2954, 26, 22, 'Şu’arâ ', 'Mekki', 'Musa: \"O işi kasden yaptımsa sapıklardan biri sayılırım. Bu yüzden sizden korkunca aranızdan kaçtım. Sonra, Rabbim bana hikmet verip, beni peygamber yaptı. Başıma kaktığın bu nimet, İsrailoğullarını kendine köle ettiğinden ötürüdür\" dedi.'),
(3071, 2955, 26, 23, 'Şu’arâ ', 'Mekki', 'Firavun: \"Alemlerin Rabbi de nedir?\" dedi.'),
(3072, 2956, 26, 24, 'Şu’arâ ', 'Mekki', 'Musa: \"Kesin olarak inanacaksanız, bilin ki O göklerin, yerin ve ikisinin arasında bulunanların Rabbidir\" dedi.'),
(3073, 2957, 26, 25, 'Şu’arâ ', 'Mekki', 'Yanında bulunanlara: \"İşitmiyor musunuz?\" dedi.'),
(3074, 2958, 26, 26, 'Şu’arâ ', 'Mekki', '\"O sizin de Rabbiniz, önce geçmiş atalarınızın da Rabbidir\" dedi.'),
(3075, 2959, 26, 27, 'Şu’arâ ', 'Mekki', 'Firavun, çevresindekilere: \"Size gönderilen peygamberiniz şüphesiz delidir\" dedi.'),
(3076, 2960, 26, 28, 'Şu’arâ ', 'Mekki', 'Musa: \"Eğer akledebilen kimselerseniz bilin ki O, doğunun, batının ve ikisinin arasında bulunanların Rabbidir\" dedi.'),
(3077, 2961, 26, 29, 'Şu’arâ ', 'Mekki', 'Firavun: \"Benden başkasını tanrı edinirsen, and olsun ki seni zindanlık ederim\" dedi.'),
(3078, 2962, 26, 30, 'Şu’arâ ', 'Mekki', 'Musa: \"Sana apaçık bir şey getirmiş isem de mi?\" dedi.'),
(3079, 2963, 26, 31, 'Şu’arâ ', 'Mekki', 'Firavun: \"Doğru sözlülerden isen haydi getir\" dedi.'),
(3080, 2964, 26, 32, 'Şu’arâ ', 'Mekki', 'Bunun üzerine Musa değneğini attı, besbelli bir yılan oluverdi.'),
(3081, 2965, 26, 33, 'Şu’arâ ', 'Mekki', 'Elini çıkardı, bakanlara bembeyaz göründü.'),
(3082, 2966, 26, 34, 'Şu’arâ ', 'Mekki', 'Firavun çevresinde bulunan ileri gelenlere: \"Doğrusu bu bilgin bir sihirbaz; sizi sihirle yurdunuzdan çıkarmak istiyor; ne buyurursunuz?\" dedi.'),
(3083, 2967, 26, 35, 'Şu’arâ ', 'Mekki', 'Firavun çevresinde bulunan ileri gelenlere: \"Doğrusu bu bilgin bir sihirbaz; sizi sihirle yurdunuzdan çıkarmak istiyor; ne buyurursunuz?\" dedi.'),
(3084, 2968, 26, 36, 'Şu’arâ ', 'Mekki', '\"Onu ve kardeşini alıkoy, şehirlere, sana bütün bilgin sihirbazları getirecek toplayıcılar gönder\" dediler.'),
(3085, 2969, 26, 37, 'Şu’arâ ', 'Mekki', '\"Onu ve kardeşini alıkoy, şehirlere, sana bütün bilgin sihirbazları getirecek toplayıcılar gönder\" dediler.'),
(3086, 2970, 26, 38, 'Şu’arâ ', 'Mekki', 'Sihirbazlar, belirli bir günün bildirilen vaktinde toplandılar.'),
(3087, 2971, 26, 39, 'Şu’arâ ', 'Mekki', 'İnsanlara: \"Siz de toplanır mısınız?\" denildi.'),
(3088, 2972, 26, 40, 'Şu’arâ ', 'Mekki', '\"Sihirbazlar üstün gelirlerse biz de onlara uyarız\" dediler.'),
(3089, 2973, 26, 41, 'Şu’arâ ', 'Mekki', 'Sihirbazlar geldiklerinde, Firavun\'a; \"Biz üstün gelirsek, şüphesiz bize bir ücret vardır değil mi?\" dediler.'),
(3090, 2974, 26, 42, 'Şu’arâ ', 'Mekki', 'Firavun: \"Evet; o takdirde siz gözde kimselerden olacaksınız\" dedi.'),
(3091, 2975, 26, 43, 'Şu’arâ ', 'Mekki', 'Musa onlara: \"Ne atacaksanız atın\" dedi.'),
(3092, 2976, 26, 44, 'Şu’arâ ', 'Mekki', 'Onlar da iplerini ve değneklerini attılar ve: \"Firavun hakkı için, şüphesiz, biz üstün geleceğiz\" dediler.'),
(3093, 2977, 26, 45, 'Şu’arâ ', 'Mekki', 'Bunun üzerine Musa değneğini attı; onların uydurduklarını yutmağa başlayıverdi.'),
(3094, 2978, 26, 46, 'Şu’arâ ', 'Mekki', 'Bunu gören sihirbazlar secdeye kapanarak: \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(3095, 2979, 26, 47, 'Şu’arâ ', 'Mekki', 'Bunu gören sihirbazlar secdeye kapanarak: \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(3096, 2980, 26, 48, 'Şu’arâ ', 'Mekki', 'Bunu gören sihirbazlar secdeye kapanarak: \"Alemlerin Rabbine, Musa ve Harun\'un Rabbine inandık\" dediler.'),
(3097, 2981, 26, 49, 'Şu’arâ ', 'Mekki', 'Firavun: \"Ben size izin vermeden ona iman mı ettiniz? Muhakkak ki o, size sihri öğreten büyüğünüzdür. Şimdi bileceksiniz; ellerinizi ayaklarınızı, and olsun, çaprazlama kestireceğim, hepinizi astıracağım\" dedi.'),
(3098, 2982, 26, 50, 'Şu’arâ ', 'Mekki', 'İman eden sihirbazlar: \"Zararı yok, biz şüphesiz Rabbimize doneceğiz; inananların ilki olmamızdan ötürü, Rabbimizin kusurlarımızı bize bağışlayacağını umarız\" dediler.'),
(3099, 2983, 26, 51, 'Şu’arâ ', 'Mekki', 'İman eden sihirbazlar: \"Zararı yok, biz şüphesiz Rabbimize doneceğiz; inananların ilki olmamızdan ötürü, Rabbimizin kusurlarımızı bize bağışlayacağını umarız\" dediler.'),
(3100, 2984, 26, 52, 'Şu’arâ ', 'Mekki', 'Biz Musa\'ya: \"Kullarımı geceleyin yola çıkar; şüphesiz takip edileceksiniz\" diye vahyettik.'),
(3101, 2985, 26, 53, 'Şu’arâ ', 'Mekki', 'Bu arada Firavun şehirlere, \"Doğrusu bunlar bizi öfkelendiren döküntü azınlıklardır; hepimiz tedbirli olmalıyız\" diyen münadiler gönderdi.'),
(3102, 2986, 26, 54, 'Şu’arâ ', 'Mekki', 'Bu arada Firavun şehirlere, \"Doğrusu bunlar bizi öfkelendiren döküntü azınlıklardır; hepimiz tedbirli olmalıyız\" diyen münadiler gönderdi.'),
(3103, 2987, 26, 55, 'Şu’arâ ', 'Mekki', 'Bu arada Firavun şehirlere, \"Doğrusu bunlar bizi öfkelendiren döküntü azınlıklardır; hepimiz tedbirli olmalıyız\" diyen münadiler gönderdi.'),
(3104, 2988, 26, 56, 'Şu’arâ ', 'Mekki', 'Bu arada Firavun şehirlere, \"Doğrusu bunlar bizi öfkelendiren döküntü azınlıklardır; hepimiz tedbirli olmalıyız\" diyen münadiler gönderdi.'),
(3105, 2989, 26, 57, 'Şu’arâ ', 'Mekki', 'Ama biz Firavun ve adamlarını bahçelerden, pınar başlarından, hazinelerden ve şerefli makamlardan çıkardık. Böylece oralara İsrailoğullarını mirasçı kıldık.'),
(3106, 2990, 26, 58, 'Şu’arâ ', 'Mekki', 'Ama biz Firavun ve adamlarını bahçelerden, pınar başlarından, hazinelerden ve şerefli makamlardan çıkardık. Böylece oralara İsrailoğullarını mirasçı kıldık.'),
(3107, 2991, 26, 59, 'Şu’arâ ', 'Mekki', 'Ama biz Firavun ve adamlarını bahçelerden, pınar başlarından, hazinelerden ve şerefli makamlardan çıkardık. Böylece oralara İsrailoğullarını mirasçı kıldık.'),
(3108, 2992, 26, 60, 'Şu’arâ ', 'Mekki', 'Firavun ve adamları güneş üzerlerine doğarken onların ardına düştüler.'),
(3109, 2993, 26, 61, 'Şu’arâ ', 'Mekki', 'İki topluluk birbirini gördüğünde, Musa\'nın adamları: \"İşte yakalandık\" dediler.'),
(3110, 2994, 26, 62, 'Şu’arâ ', 'Mekki', 'Musa: \"Hayır; Rabbim benimle beraberdir, bana elbette yol gösterecektir\" dedi.'),
(3111, 2995, 26, 63, 'Şu’arâ ', 'Mekki', 'Bunun üzerine Biz Musa\'ya: \"Değneğinle denize vur\" diye vahyettik. Hemen deniz ikiye ayrıldı, her parçası yüce bir dağ gibiydi.'),
(3112, 2996, 26, 64, 'Şu’arâ ', 'Mekki', 'İşte oraya, geridekileri de yaklaştırdık.'),
(3113, 2997, 26, 65, 'Şu’arâ ', 'Mekki', 'Musa ve beraberinde bulunanların hepsini kurtardık.'),
(3114, 2998, 26, 66, 'Şu’arâ ', 'Mekki', 'Öbürlerini suda boğduk.'),
(3115, 2999, 26, 67, 'Şu’arâ ', 'Mekki', 'Bunda şüphesiz ders vardır, ama çoğu inanmamıştır.'),
(3116, 3000, 26, 68, 'Şu’arâ ', 'Mekki', 'Doğrusu Rabbin, güçlü olandır, merhamet edendir.'),
(3117, 3001, 26, 69, 'Şu’arâ ', 'Mekki', 'Onlara İbrahim\'in kıssasını anlat.'),
(3118, 3002, 26, 70, 'Şu’arâ ', 'Mekki', 'İbrahim, babasına ve milletine: \"Nelere tapıyorsunuz?\" demişti.'),
(3119, 3003, 26, 71, 'Şu’arâ ', 'Mekki', '\"Putlara tapıyoruz, onlara bağlanıp duruyoruz\" demişlerdi.'),
(3120, 3004, 26, 72, 'Şu’arâ ', 'Mekki', 'İbrahim: \"Çağırdığınız zaman sizi duyarlar veya size bir fayda ve zarar verirler mi?\" demişti.'),
(3121, 3005, 26, 73, 'Şu’arâ ', 'Mekki', 'İbrahim: \"Çağırdığınız zaman sizi duyarlar veya size bir fayda ve zarar verirler mi?\" demişti.'),
(3122, 3006, 26, 74, 'Şu’arâ ', 'Mekki', '\"Hayır ama, babalarımızı da bu şekilde ibadet ederken bulduk\" demişlerdi.'),
(3123, 3007, 26, 75, 'Şu’arâ ', 'Mekki', 'İbrahim: \"Eski atalarınızın ve sizin nelere taptıklarınızı görüyor musunuz? Doğrusu onlar benim düşmanımdır. Dostum ancak Alemlerin Rabbidir. Beni yaratan da, doğru yola eriştiren de O\'dur. Beni yediren de, içiren de O\'dur. Hasta olduğumda bana O şifa verir. Beni öldürecek, sonra da diriltecek O\'dur. Ahiret gününde yanılmalarımı bana bağışlamasını umduğum O\'dur. Rabbim! Bana hikmet ver ve beni iyiler arasına kat.'),
(3124, 3008, 26, 76, 'Şu’arâ ', 'Mekki', 'İbrahim: \"Eski atalarınızın ve sizin nelere taptıklarınızı görüyor musunuz? Doğrusu onlar benim düşmanımdır. Dostum ancak Alemlerin Rabbidir. Beni yaratan da, doğru yola eriştiren de O\'dur. Beni yediren de, içiren de O\'dur. Hasta olduğumda bana O şifa verir. Beni öldürecek, sonra da diriltecek O\'dur. Ahiret gününde yanılmalarımı bana bağışlamasını umduğum O\'dur. Rabbim! Bana hikmet ver ve beni iyiler arasına kat.'),
