(5630, 5532, 74, 37, 'Müddessir ', 'Mekki', 'Hayır, hayır öğüt almazlar. Aya, dönüp gelen geceye, ağarmakta olan sabaha and olsun ki, içinizden öne geçmek veya geri kalmak isteyen kimseye, insanoğlunu uyarıcı olarak anlatılan cehennem büyük olaylardan biridir.'),
(5631, 5533, 74, 38, 'Müddessir ', 'Mekki', 'Herkes kazancına bağlı bir rehindir;'),
(5632, 5534, 74, 39, 'Müddessir ', 'Mekki', 'Ancak, defteri sağdan verilenler böyle değildir; onlar cennettedirler. Suçlulara: \"Sizi bu yakıcı ateşe sürükleyen nedir?\" diye sorarlar.'),
(5633, 5535, 74, 40, 'Müddessir ', 'Mekki', 'Ancak, defteri sağdan verilenler böyle değildir; onlar cennettedirler. Suçlulara: \"Sizi bu yakıcı ateşe sürükleyen nedir?\" diye sorarlar.'),
(5634, 5536, 74, 41, 'Müddessir ', 'Mekki', 'Ancak, defteri sağdan verilenler böyle değildir; onlar cennettedirler. Suçlulara: \"Sizi bu yakıcı ateşe sürükleyen nedir?\" diye sorarlar.'),
(5635, 5537, 74, 42, 'Müddessir ', 'Mekki', 'Ancak, defteri sağdan verilenler böyle değildir; onlar cennettedirler. Suçlulara: \"Sizi bu yakıcı ateşe sürükleyen nedir?\" diye sorarlar.'),
(5636, 5538, 74, 43, 'Müddessir ', 'Mekki', 'Onlar derler ki: \"Namaz kılanlardan değildik.\"'),
(5637, 5539, 74, 44, 'Müddessir ', 'Mekki', '\"Düşkün kimseyi doyurmuyorduk.\"'),
(5638, 5540, 74, 45, 'Müddessir ', 'Mekki', '\"Batıla dalanlarla biz de dalardık.\"'),
(5639, 5541, 74, 46, 'Müddessir ', 'Mekki', '\"Ceza gününü yalanlardık.\"'),
(5640, 5542, 74, 47, 'Müddessir ', 'Mekki', '\"Ölüm bize o haldeyken geldi.\"'),
(5641, 5543, 74, 48, 'Müddessir ', 'Mekki', 'Artık onlara, şefaatçilerin şefaati fayda vermez.'),
(5642, 5544, 74, 49, 'Müddessir ', 'Mekki', 'Öyleyken, bunlara ne oluyor ki öğütten yüz çeviriyorlar?'),
(5643, 5545, 74, 50, 'Müddessir ', 'Mekki', 'Aslandan ürkerek kaçan yabani merkeplere benzerler.'),
(5644, 5546, 74, 51, 'Müddessir ', 'Mekki', 'Aslandan ürkerek kaçan yabani merkeplere benzerler.'),
(5645, 5547, 74, 52, 'Müddessir ', 'Mekki', 'Hayır; her biri önüne açılıvermiş sahifeler verilmesini ister.'),
(5646, 5548, 74, 53, 'Müddessir ', 'Mekki', 'Hayır; daha doğrusu ahiretten korkmazlar.'),
(5647, 5549, 74, 54, 'Müddessir ', 'Mekki', 'Hayır; şüphesiz bu Kuran bir öğüttür.'),
(5648, 5550, 74, 55, 'Müddessir ', 'Mekki', 'Dileyen kimse öğüt alır.'),
(5649, 5551, 74, 56, 'Müddessir ', 'Mekki', 'Allah dilemeksizin öğüt alamazlar. O, kendisinden korkulmaya daha layıktır ve bağışlamaya daha ehildir.'),
(5650, 5552, 75, 1, 'Kıyâme ', 'Mekki', 'Kıyamet gününe yemin ederim.'),
(5651, 5553, 75, 2, 'Kıyâme ', 'Mekki', 'Ve nedamet çeken nefse yemin ederim.'),
(5652, 5554, 75, 3, 'Kıyâme ', 'Mekki', 'İnsan, kemiklerini bir araya toplayamayız mı sanıyor?'),
(5653, 5555, 75, 4, 'Kıyâme ', 'Mekki', 'Evet, Biz onu, parmak uçlarına varıncaya kadar bütün incelikleriyle yeniden yapmaya kadiriz.'),
(5654, 5556, 75, 5, 'Kıyâme ', 'Mekki', 'Ama, insanoğlu gelecekte de suç işlemek ister de: \"Kıyamet günü ne zamanmış! \" der.'),
(5655, 5557, 75, 6, 'Kıyâme ', 'Mekki', 'Ama, insanoğlu gelecekte de suç işlemek ister de: \"Kıyamet günü ne zamanmış! \" der.'),
(5656, 5558, 75, 7, 'Kıyâme ', 'Mekki', 'Gözün kamaştığı, ayın tutulduğu, güneş ve ayın bir araya getirildiği zaman, işte o gün insan: \"kaçacak yer nerede?\" der.'),
(5657, 5559, 75, 8, 'Kıyâme ', 'Mekki', 'Gözün kamaştığı, ayın tutulduğu, güneş ve ayın bir araya getirildiği zaman, işte o gün insan: \"kaçacak yer nerede?\" der.'),
(5658, 5560, 75, 9, 'Kıyâme ', 'Mekki', 'Gözün kamaştığı, ayın tutulduğu, güneş ve ayın bir araya getirildiği zaman, işte o gün insan: \"kaçacak yer nerede?\" der.'),
(5659, 5561, 75, 10, 'Kıyâme ', 'Mekki', 'Gözün kamaştığı, ayın tutulduğu, güneş ve ayın bir araya getirildiği zaman, işte o gün insan: \"kaçacak yer nerede?\" der.'),
(5660, 5562, 75, 11, 'Kıyâme ', 'Mekki', 'Hayır; hayır; bir sığınak yoktur.'),
(5661, 5563, 75, 12, 'Kıyâme ', 'Mekki', 'O gün, sen, Rabbinin huzuruna varıp durursun.'),
(5662, 5564, 75, 13, 'Kıyâme ', 'Mekki', 'O gün, insanoğluna önde ve sonda yaptığı ne varsa bildirilir.'),
(5663, 5565, 75, 14, 'Kıyâme ', 'Mekki', 'Özürlerini sayıp dökse de, insanoğlu, artık kendi kendinin şahididir.'),
(5664, 5566, 75, 15, 'Kıyâme ', 'Mekki', 'Özürlerini sayıp dökse de, insanoğlu, artık kendi kendinin şahididir.'),
(5665, 5567, 75, 16, 'Kıyâme ', 'Mekki', 'Cebrail sana Kuran okurken, unutmamak için acele edip onunla beraber söyleme, yalnız dinle.'),
(5666, 5568, 75, 17, 'Kıyâme ', 'Mekki', 'Doğrusu o vahyolunanı kalbine yerleştirmek ve onu sana okutturmak Bize düşer.'),
(5667, 5569, 75, 18, 'Kıyâme ', 'Mekki', 'Biz onu Cebrail\'e okuttuğumuz zaman, onun okumasını dinle.'),
(5668, 5570, 75, 19, 'Kıyâme ', 'Mekki', 'Sonra onu sana açıklamak Bize düşer.'),
(5669, 5571, 75, 20, 'Kıyâme ', 'Mekki', 'Hayır, hayır! Sizler, çabuk elde edeceğiniz dünya nimetlerini seversiniz.'),
(5670, 5572, 75, 21, 'Kıyâme ', 'Mekki', 'Ahireti bırakırsınız.'),
(5671, 5573, 75, 22, 'Kıyâme ', 'Mekki', 'O gün bir takım yüzler Rablerine bakıp parlayacaktır.'),
(5672, 5574, 75, 23, 'Kıyâme ', 'Mekki', 'O gün bir takım yüzler Rablerine bakıp parlayacaktır.'),
(5673, 5575, 75, 24, 'Kıyâme ', 'Mekki', 'O gün bir takım yüzler de asıktır.'),
(5674, 5576, 75, 25, 'Kıyâme ', 'Mekki', 'Kendisinin belkemiğinin kırılacağını sanır.'),
(5675, 5577, 75, 26, 'Kıyâme ', 'Mekki', 'Dikkat edin; can boğaza gelip köprücük kemiklerine dayandığı zaman: \"Çare bulan yok mudur?\" denir.'),
(5676, 5578, 75, 27, 'Kıyâme ', 'Mekki', 'Dikkat edin; can boğaza gelip köprücük kemiklerine dayandığı zaman: \"Çare bulan yok mudur?\" denir.'),
(5677, 5579, 75, 28, 'Kıyâme ', 'Mekki', 'Artık ayrılık vaktinin geldiğini sanır.'),
(5678, 5580, 75, 29, 'Kıyâme ', 'Mekki', 'Bacaklar birbirine dolaşır.'),
(5679, 5581, 75, 30, 'Kıyâme ', 'Mekki', 'O gün sevk Rabbin huzurunadır.'),
(5680, 5582, 75, 31, 'Kıyâme ', 'Mekki', 'O, Peygamberi doğrulamamış, namaz kılmamış, ama yalanlayıp yüz çevirmiş, sonra da salına salına kendinden yana olanlara gitmişti.'),
(5681, 5583, 75, 32, 'Kıyâme ', 'Mekki', 'O, Peygamberi doğrulamamış, namaz kılmamış, ama yalanlayıp yüz çevirmiş, sonra da salına salına kendinden yana olanlara gitmişti.'),
(5682, 5584, 75, 33, 'Kıyâme ', 'Mekki', 'O, Peygamberi doğrulamamış, namaz kılmamış, ama yalanlayıp yüz çevirmiş, sonra da salına salına kendinden yana olanlara gitmişti.'),
(5683, 5585, 75, 34, 'Kıyâme ', 'Mekki', 'Sana yazıklar olsun, yazıklar!'),
(5684, 5586, 75, 35, 'Kıyâme ', 'Mekki', 'Daha ne olsun, sana yazıklar olsun, yazıklar!'),
(5685, 5587, 75, 36, 'Kıyâme ', 'Mekki', 'İnsanoğlu kendisinin başıboş bırakılacağını mı sanır?'),
(5686, 5588, 75, 37, 'Kıyâme ', 'Mekki', 'O, katılan bir meni damlası değil miydi?'),
(5687, 5589, 75, 38, 'Kıyâme ', 'Mekki', 'Sonra kan pıhtısı olmuş, sonra Allah onu yaratıp şekil vermişti.'),
(5688, 5590, 75, 39, 'Kıyâme ', 'Mekki', 'Ondan, erkek, dişi iki cins yaratmıştı.'),
(5689, 5591, 75, 40, 'Kıyâme ', 'Mekki', 'Bunları yapan Allah\'ın ölüleri diriltmeye gücü yetmez mi? Elbette yeter.'),
(5690, 5592, 76, 1, 'İnsan ', 'Mekki', 'İnsanoğlu, var edilip bahse değer bir şey olana kadar, şüphesiz, uzun bir zaman geçmemiş midir?'),
(5691, 5593, 76, 2, 'İnsan ', 'Mekki', 'Biz insanı katışık bir nutfeden yaratmışızdır; onu deneriz; bu yüzden, onun işitmesini ve görmesini sağlamışızdır.'),
(5692, 5594, 76, 3, 'İnsan ', 'Mekki', 'Şüphesiz ona yol gösterdik; buna kimi şükreder, kimi de nankörlük.'),
(5693, 5595, 76, 4, 'İnsan ', 'Mekki', 'Doğrusu, inkarcılar için zincirler, demir halkalar ve çılgın alevli cehennem hazırladık.'),
(5694, 5596, 76, 5, 'İnsan ', 'Mekki', 'Şüphesiz iyiler kafur katılmış bir tastan içerler.'),
(5695, 5597, 76, 6, 'İnsan ', 'Mekki', 'Bu ancak Allah\'ın kullarının taşıra taşıra içebileceği bir pınardır.'),
(5696, 5598, 76, 7, 'İnsan ', 'Mekki', 'Onlar verdikleri sözleri yerine getirirler, fenalığı yaygın olan bir günden korkarlar.'),
(5697, 5599, 76, 8, 'İnsan ', 'Mekki', 'Onlar içleri çektiği halde, yiyeceği yoksula, öksüze ve esire yedirirler.'),
(5698, 5600, 76, 9, 'İnsan ', 'Mekki', '\"Biz sizi ancak Allah rızası için doyuruyoruz, bir karşılık ve teşekkür beklemiyoruz. Doğrusu biz çok asık suratların bulunacağı bir günde Rabbimizden korkarız\" derler.'),
(5699, 5601, 76, 10, 'İnsan ', 'Mekki', '\"Biz sizi ancak Allah rızası için doyuruyoruz, bir karşılık ve teşekkür beklemiyoruz. Doğrusu biz çok asık suratların bulunacağı bir günde Rabbimizden korkarız\" derler.'),
(5700, 5602, 76, 11, 'İnsan ', 'Mekki', 'Allah da onları bu yüzden o günün fenalığından korur; onların yüzüne parlaklık ve neşe verir.'),
(5701, 5603, 76, 12, 'İnsan ', 'Mekki', 'Sabırlarının karşılığı, cennet ve oradaki ipeklerdir.'),
(5702, 5604, 76, 13, 'İnsan ', 'Mekki', 'Orada tahtlara yaslanırlar; orada yakıcı sıcak ve dondurucu soğuk görmezler.'),
(5703, 5605, 76, 14, 'İnsan ', 'Mekki', 'Meyve ağaçlarının gölgeleri üzerlerine sarkmış ve onların koparılması kolaylaştırılmıştır.'),
(5704, 5606, 76, 15, 'İnsan ', 'Mekki', 'Çevrelerinde gümüş kaplar ve billur kaseler dolaştırılır.'),
(5705, 5607, 76, 16, 'İnsan ', 'Mekki', 'Billurları gümüş gibi parlaktır, onları ölçüp ölçüp dağıtırlar.'),
(5706, 5608, 76, 17, 'İnsan ', 'Mekki', 'Orada, zencefil karışık bir tasla içirilirler.'),
(5707, 5609, 76, 18, 'İnsan ', 'Mekki', 'O pınara \"Selsebil\" denir.'),
(5708, 5610, 76, 19, 'İnsan ', 'Mekki', 'Yanlarında ölümsüz gençler dolaşır; onları gördüğünde saçılmış birer inci sanırsın.'),
(5709, 5611, 76, 20, 'İnsan ', 'Mekki', 'Oranın neresine baksan, nimet ve büyük bir saltanat görürsün.'),
(5710, 5612, 76, 21, 'İnsan ', 'Mekki', 'Üzerlerinde ince yeşil ipekli, parlak atlastan elbiseler vardır; gümüş bileziklerle süslenmişlerdir Rableri onlara tertemiz içecekler içirir.'),
(5711, 5613, 76, 22, 'İnsan ', 'Mekki', '\"İşte bu sizin işlediklerinizin karşılığıdır, çalışmalarınız şükre değer\" denir.'),
(5712, 5614, 76, 23, 'İnsan ', 'Mekki', 'Kuran\'ı sana indiren şüphesiz Biziz.'),
(5713, 5615, 76, 24, 'İnsan ', 'Mekki', 'Rabbinin hükmüne kadar sabret; onların günah işleyen ve inkarcı olanlarına uyma.'),
(5714, 5616, 76, 25, 'İnsan ', 'Mekki', 'Rabbinin adını sabah akşam an.'),
(5715, 5617, 76, 26, 'İnsan ', 'Mekki', 'Geceleyin O\'na secde et; O\'nu geceleri uzun uzun tesbih et.'),
(5716, 5618, 76, 27, 'İnsan ', 'Mekki', 'Doğrusu insanlar, çabuk elde edilen dünya nimetlerini severler de ağırlığı çekilmez günü arkalarında bırakırlar.'),
(5717, 5619, 76, 28, 'İnsan ', 'Mekki', 'Onları yaratan, mafsallarını pekiştiren Biziz; dilersek onları benzerleri ile değiştiriveririz.'),
(5718, 5620, 76, 29, 'İnsan ', 'Mekki', 'Bu sadece bir öğüttür; dileyen, Rabbine giden yolu tutar.'),
(5719, 5621, 76, 30, 'İnsan ', 'Mekki', 'Allah dilemedikçe siz dileyemezsiniz. Doğrusu Allah, bilendir, Hakim\'dir.'),
(5720, 5622, 76, 31, 'İnsan ', 'Mekki', 'Dilediğine rahmet eder. Zalimlere, işte onlara, can yakıcı bir azap hazırlamıştır.'),
(5721, 5623, 77, 1, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5722, 5624, 77, 2, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5723, 5625, 77, 3, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5724, 5626, 77, 4, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5725, 5627, 77, 5, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5726, 5628, 77, 6, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5727, 5629, 77, 7, 'Mürselât ', 'Mekki', 'Birbiri ardından gönderilenlere ve görevlerine koştukça koşanlara, Allah\'ın buyruklarını yaydıkça yayanlara ve hak ile batılın arasını ayırdıkça ayıranlara, kötülüğü önlemek veya uyarmak için vahiy getiren meleklere and olsun ki, size söz verilen kıyamet şüphesiz kopacaktır.'),
(5728, 5630, 77, 8, 'Mürselât ', 'Mekki', 'Yıldızların ışığı giderildiği zaman,'),
(5729, 5631, 77, 9, 'Mürselât ', 'Mekki', 'Gök yarıldığı zaman,'),
(5730, 5632, 77, 10, 'Mürselât ', 'Mekki', 'Dağlar pamuk gibi atıldığı zaman,'),
(5731, 5633, 77, 11, 'Mürselât ', 'Mekki', 'Peygamberlere ümmetleri hakkında şahidlik vakitleri bildirildiği zaman;'),
(5732, 5634, 77, 12, 'Mürselât ', 'Mekki', 'Bu, hangi güne bırakılmıştı?'),
(5733, 5635, 77, 13, 'Mürselât ', 'Mekki', 'Hüküm gününe bırakılmıştı.'),
(5734, 5636, 77, 14, 'Mürselât ', 'Mekki', 'Hüküm gününün ne olduğunu sen nerden bilirsin?'),
(5735, 5637, 77, 15, 'Mürselât ', 'Mekki', 'O gün yalanlamış olanların vay haline!'),
(5736, 5638, 77, 16, 'Mürselât ', 'Mekki', 'Öncekileri yok etmedik mi? Ardından, sonrakileri de onlara katarız.'),
(5737, 5639, 77, 17, 'Mürselât ', 'Mekki', 'Öncekileri yok etmedik mi? Ardından, sonrakileri de onlara katarız.'),
(5738, 5640, 77, 18, 'Mürselât ', 'Mekki', 'Suçlulara böyle yaparız.'),
(5739, 5641, 77, 19, 'Mürselât ', 'Mekki', 'O gün, yalanlamış olanların vay haline!.'),
(5740, 5642, 77, 20, 'Mürselât ', 'Mekki', 'Sizi bayağı bir sudan yaratıp onu belli bir süreye kadar sağlam bir yere yerleştirmedik mi?'),
(5741, 5643, 77, 21, 'Mürselât ', 'Mekki', 'Sizi bayağı bir sudan yaratıp onu belli bir süreye kadar sağlam bir yere yerleştirmedik mi?'),
(5742, 5644, 77, 22, 'Mürselât ', 'Mekki', 'Sizi bayağı bir sudan yaratıp onu belli bir süreye kadar sağlam bir yere yerleştirmedik mi?'),
(5743, 5645, 77, 23, 'Mürselât ', 'Mekki', 'Buna gücümüz yeter; Biz ne güzel güç yetireniz!'),
(5744, 5646, 77, 24, 'Mürselât ', 'Mekki', 'O gün yalanlamış olanların vay haline!'),
(5745, 5647, 77, 25, 'Mürselât ', 'Mekki', 'Biz yeryüzünü, dirilerin ve ölülerin toplantı yeri yapmadık mı?'),
(5746, 5648, 77, 26, 'Mürselât ', 'Mekki', 'Biz yeryüzünü, dirilerin ve ölülerin toplantı yeri yapmadık mı?'),
(5747, 5649, 77, 27, 'Mürselât ', 'Mekki', 'Orada yüksek yüksek sabit dağlar var edip size tatlı sular içirmedik mi?'),
(5748, 5650, 77, 28, 'Mürselât ', 'Mekki', 'Yalanlamış olanların vay o gün haline!'),
(5749, 5651, 77, 29, 'Mürselât ', 'Mekki', 'İnkarcılara o gün şöyle denir: \"yalanlayıp durduğunuz şeye gidin;\"'),
(5750, 5652, 77, 30, 'Mürselât ', 'Mekki', '\"gölge yapmayan ve ateşten de korumayan cehennem dumanının üç kollu gölgesine gidin.\"'),
(5751, 5653, 77, 31, 'Mürselât ', 'Mekki', '\"gölge yapmayan ve ateşten de korumayan cehennem dumanının üç kollu gölgesine gidin.\"'),
(5752, 5654, 77, 32, 'Mürselât ', 'Mekki', 'O gölgenin saçtığı her bir kıvılcım sanki birer sarı devedir, konak gibi de büyüktür.'),
(5753, 5655, 77, 33, 'Mürselât ', 'Mekki', 'O gölgenin saçtığı her bir kıvılcım sanki birer sarı devedir, konak gibi de büyüktür.'),
(5754, 5656, 77, 34, 'Mürselât ', 'Mekki', 'Yalanlamış olanların o gün vay haline!'),
(5755, 5657, 77, 35, 'Mürselât ', 'Mekki', 'Bu, onların konuşamayacakları gündür.'),
(5756, 5658, 77, 36, 'Mürselât ', 'Mekki', 'Onlara izin de verilmez ki özür beyan etsinler.'),
(5757, 5659, 77, 37, 'Mürselât ', 'Mekki', 'Yalanlamış olanların o gün vay haline!'),
(5758, 5660, 77, 38, 'Mürselât ', 'Mekki', '\"Bu, sizleri ve öncekileri topladığımız hüküm günüdür.\"'),
(5759, 5661, 77, 39, 'Mürselât ', 'Mekki', '\"Eğer bir düzeniniz varsa Bana kurun.\"'),
(5760, 5662, 77, 40, 'Mürselât ', 'Mekki', 'Yalanlamış olanların o gün vay haline!.'),
(5761, 5663, 77, 41, 'Mürselât ', 'Mekki', 'Allah\'a karşı gelmekten sakınmış olanlar, elbette gölgeliklerde ve pınar başlarındadırlar.'),
(5762, 5664, 77, 42, 'Mürselât ', 'Mekki', 'Canlarının istediği meyveler arasındadırlar.'),
(5763, 5665, 77, 43, 'Mürselât ', 'Mekki', 'Onlara denir ki: \"İşlediklerinize karşılık afiyetle yiyiniz, içiniz.\"'),
(5764, 5666, 77, 44, 'Mürselât ', 'Mekki', 'Biz, iyi davrananlara işte böyle karşılık veririz.'),
(5765, 5667, 77, 45, 'Mürselât ', 'Mekki', 'O gün yalanlamış olanların vay haline'),
(5766, 5668, 77, 46, 'Mürselât ', 'Mekki', 'Yiyiniz, biraz zevkleniniz bakalım, doğrusu sizler suçlularsınız.'),
(5767, 5669, 77, 47, 'Mürselât ', 'Mekki', 'O gün yalanlamış olanların vay haline!'),
(5768, 5670, 77, 48, 'Mürselât ', 'Mekki', 'Onlara \"Rüku edin\" denildiğinde rükua varmazlar.'),
